INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(1, 1, 'Chương 1-1: Mở đầu (1)', 'Chương 1-1: Mở đầu (1)', '<h2>Lần đầu ngẫu nhi&ecirc;n gặp, hắn nhếch m&ocirc;i, n&eacute;t mặt tinh xảo như b&ocirc;ng hoa tươi đẹp, như ph&aacute;o hoa đang độ đẹp nhất, diễm lệ như hồ y&ecirc;u ng&agrave;n năm, hơi thở của hắn như hương lan ở b&ecirc;n tai n&agrave;ng: &ldquo;Ta tưởng l&agrave; ai, th&igrave; ra l&agrave; một mỹ nh&acirc;n lạc đường xinh đẹp như hoa!&rdquo; C&oacute; lẽ v&igrave; &aacute;nh trăng thật đẹp, trong khoảnh khắc n&agrave;y, n&agrave;ng bỗng động t&acirc;m.<br />\r\n<br />\r\nĐ&ecirc;m đ&oacute;, hắn lại th&ocirc; bạo lăng nhục, trong thời khắc đau đớn cực độ kia, m&agrave;n lụa hoa bay bổng l&ecirc;n rồi rơi xuống, hương thơm tụ lại th&agrave;nh cụm xoay tr&ograve;n h&iacute;t v&agrave;o lại thở ra, yếu ớt, đ&ocirc;i m&ocirc;i lạnh như băng của người nọ d&aacute;n b&ecirc;n tai n&agrave;ng hiện ra một nụ cười khẽ như c&oacute; như kh&ocirc;ng:<br />\r\n<br />\r\n&ldquo;Mạn Nhi ngươi chỉ l&agrave; một con chim ho&agrave;ng yến cho người giải buồn.&rdquo;<br />\r\n<br />\r\nTrong r&egrave;m gấm đỏ một m&agrave;n hỗn độn, vết m&aacute;u đỏ của xử nữ hiện l&ecirc;n ch&oacute;i lọi tr&ecirc;n đệm, tựa như đo&aacute; hoa bị sương lạnh đọng lại trong ng&agrave;y m&ugrave;a thu nở bung ra. &Aacute;nh mắt kiều diễm hiện ra &aacute;nh s&aacute;ng lạnh như băng, &aacute;nh mắt vẫn tiếp tục quẩn quanh như cơn &aacute;c mộng b&aacute;m riết b&ecirc;n người, đọng m&atilde;i trong l&ograve;ng, l&agrave;m thế n&agrave;o cũng kh&ocirc;ng biến mất được. N&agrave;ng nhịn kh&ocirc;ng được quay đầu, k&eacute;o vạt &aacute;o d&agrave;i, bước đi kh&ocirc;ng tiếng động, kh&ocirc;ng c&ograve;n ch&uacute;t sức lực n&agrave;o đi qua căn ph&ograve;ng, sau lưng c&oacute; người cho&agrave;ng &aacute;o cho&agrave;ng l&ecirc;n người n&agrave;ng. N&agrave;ng l&agrave;m như cảm thấy lạnh, hoảng sợ nắm chặt lấy cổ &aacute;o. Thị nữ đ&atilde; bưng thuốc chờ ở một b&ecirc;n, mặc d&ugrave; n&agrave;ng sớm đ&atilde; ngờ tới, trong l&ograve;ng vẫn giống bị kim đ&acirc;m. Kh&ocirc;ng phải n&agrave;ng muốn uống nhưng cảm gi&aacute;c giống như th&ecirc;m một phần chần chờ l&agrave; th&ecirc;m một phần nhận thua, n&agrave;ng quật cường bưng l&ecirc;n uống một hơi cạn sạch. Chất lỏng m&aacute;t m&agrave; đặc chậm r&atilde;i lướt qua cổ họng, nặng nề chảy xuống, giống như một tiếng nức nở nghẹn ng&agrave;o r&ecirc;n rỉ. Tiện tay cầm ch&eacute;n thuốc n&eacute;m xuống khay, n&agrave;ng chậm r&atilde;i đưa tay l&ecirc;n d&ugrave;ng sức lau thuốc c&ograve;n đọng lại b&ecirc;n m&ocirc;i, sửa lại c&acirc;y tr&acirc;m bị lệch tr&ecirc;n t&oacute;c, ngo&aacute;i đầu nh&igrave;n lại cười như c&oacute; như kh&ocirc;ng: &ldquo;Đại nh&acirc;n, từ h&ocirc;m nay, ch&uacute;ng ta rốt cục kh&ocirc;ng c&ograve;n li&ecirc;n quan.&rdquo;<br />\r\n<br />\r\nNụ cười kia như hoa l&ecirc; s&aacute;ng ch&oacute;i nở ra trong đ&ecirc;m, chiếu s&aacute;ng đ&igrave;nh viện, thật đẹp l&agrave;m cho hắn c&oacute; ch&uacute;t thất thần, chỉ tho&aacute;ng giật m&igrave;nh, n&agrave;ng đ&atilde; quyết tuyệt xoay người đầu lẫn v&agrave;o b&oacute;ng tối, giống như một con chim phượng giương c&aacute;nh bay. Hắn nh&igrave;n b&oacute;ng lưng của n&agrave;ng, sau nửa ng&agrave;y mới nhếch l&ecirc;n một nụ cười vui vẻ &yacute; vị s&acirc;u xa.<br />\r\n<br />\r\nC&oacute; lẽ Mạn Nhi kh&ocirc;ng biết, l&uacute;c n&agrave;y mới chỉ l&agrave; bắt đầu.</h2>', 0, 'chuong-1-1-mỏ-dàu-1'),
(2, 1, 'Phượng Nghịch - Chương 1-2: Mở đầu (2)', 'Phượng Nghịch - Chương 1-2: Mở đầu (2)', '<h2>Tết Nguy&ecirc;n Ti&ecirc;u năm đ&oacute;, n&agrave;ng nữ giả nam trang trộm theo th&aacute;nh gi&aacute; đi xem ph&aacute;o hoa.<br />\r\n<br />\r\nĐ&ecirc;m xu&acirc;n gi&oacute; thổi ng&agrave;n hoa nở, c&aacute;nh hoa rụng như mưa, &aacute;nh sao rực rỡ. Lầu c&aacute;c đẹp đẽ, m&aacute;i đỏ cong cong. Vua quan ở tr&ecirc;n lầu c&aacute;c, đứng tr&ecirc;n cao để nh&igrave;n được xa, chỉ thấy những chiếc xe ngựa sang qu&yacute;, chạm gỗ trầm hương đỗ đầy đường. &ldquo;Phụng ti&ecirc;u uyển chuyển, &aacute;nh trăng lay động, suốt đ&ecirc;m rồng c&aacute; rộn&rdquo;.[1]<br />\r\n<br />\r\nĐo&agrave;n người nhộn nhịp, nhiều tiếng động lớn, xa hoa đồi trụy. N&agrave;ng tr&aacute;nh ở trong đ&aacute;m người vụng trộm mong chờ, chỉ v&igrave; đế vương cao cao tại thượng l&agrave; phụ ho&agrave;ng của n&agrave;ng nhưng từ nhỏ chưa từng gặp mặt. N&agrave;ng kh&ocirc;ng nghe theo mẫu th&acirc;n khuy&ecirc;n can, cuối c&ugrave;ng vụng trộm chạy đến t&igrave;m gặp, gặp được, nhưng lại trăm mối cảm x&uacute;c ngổn ngang. C&ograve;n n&agrave;ng giả trang th&agrave;nh thiếu ni&ecirc;n, tư th&aacute;i xinh đẹp, ưu nh&atilde; như vẽ, khiến b&aacute;ch quan li&ecirc;n tiếp ch&uacute; &yacute;, muốn t&igrave;m c&aacute;ch hỏi thăm xem l&agrave; c&ocirc;ng tử nh&agrave; ai, mong gả nữ nhi nh&agrave; m&igrave;nh cho n&agrave;ng.<br />\r\n<br />\r\nN&agrave;ng n&eacute; tr&aacute;nh mọi &aacute;nh nh&igrave;n, chuy&ecirc;n t&acirc;m đ&aacute;nh cờ với một l&atilde;o giả xảo tr&aacute;. L&atilde;o giả b&agrave;y trận huyền diệu, chiến phong sắc b&eacute;n, v&agrave;i người đ&atilde; bại trận. T&iacute;nh t&igrave;nh thiếu ni&ecirc;n dễ d&agrave;ng t&acirc;m cường h&aacute;o thắng, l&agrave;m g&igrave; chắc đ&oacute;, lại thận trọng, cuối c&ugrave;ng qua hai canh giờ, cờ đen rốt cuộc bao tr&ugrave;m nửa b&ecirc;n, chiến thắng được cả sảnh đường ủng hộ. L&atilde;o giả k&eacute;o n&agrave;ng đ&aacute;nh tiếp v&aacute;n kh&aacute;c, chợt nghe mọi người huy&ecirc;n n&aacute;o, h&igrave;nh như c&oacute; người l&ecirc;n c&aacute;c, rốt cục nhịn kh&ocirc;ng được tr&ocirc;ng qua, chỉ thấy ngọn đ&egrave;n dầu lay động, đ&aacute;m người t&uacute;m tụm quanh một người mặc &aacute;o trắng hơn tuyết, trong trẻo nhưng lạnh l&ugrave;ng xuất trần giống như ti&ecirc;n lẫn trong đ&aacute;m ph&agrave;m nh&acirc;n. Hắn ngước đ&ocirc;i mắt lười biếng nh&igrave;n qua, m&ocirc;i mỏng nổi l&ecirc;n m&agrave;u s&aacute;ng b&oacute;ng nhạt, cặp mắt m&agrave;u lam như hồ nước, mị hoặc phong lưu như y&ecirc;u tinh.<br />\r\n<br />\r\nNghe n&oacute;i trong triều thần, chỉ c&oacute; người đ&oacute; được th&aacute;nh sủng, tuổi c&ograve;n trẻ đ&atilde; l&agrave; phụ t&aacute; đắc lực của Th&aacute;nh Thượng, th&igrave; ra ch&iacute;nh l&agrave; hắn.<br />\r\n<br />\r\nL&agrave;m như biết được sự ch&uacute; &yacute; của n&agrave;ng, b&ecirc;n m&ocirc;i người nọ hơi hiện một v&ograve;ng vui vẻ mập mờ kh&ocirc;ng r&otilde;, quăng đến tr&ecirc;n người n&agrave;ng rất nhanh mất đi, như sao xẹt tr&ecirc;n trời, tho&aacute;ng qua tức th&igrave;. Tim n&agrave;ng tự dưng đập như trống, sau khi đảo mắt che dấu, l&ocirc;ng mi d&agrave;i phủ xuống, tho&aacute;ng qua b&oacute;ng mờ nh&agrave;n nhạt trong đ&aacute;y mắt như nước. Đ&aacute;nh cờ kh&ocirc;ng c&ograve;n theo luật, l&atilde;o giả dựng r&acirc;u trừng mắt, n&agrave;ng sau nửa ng&agrave;y từ chối chơi tiếp, c&oacute; phần chật vật chạy thục mạng ra.<br />\r\n<br />\r\nTr&ecirc;n thềm đ&aacute; gạch ngọc trong vườn hoa nhỏ, từng cụm mai v&agrave;ng toả hương, n&agrave;ng một th&acirc;n một m&igrave;nh chống cằm ngồi ở tr&ecirc;n bậc thềm ngọc, với b&oacute;ng nhạt th&agrave;nh đ&ocirc;i, h&igrave;nh độc ảnh đơn.<br />\r\n<br />\r\nTh&iacute;ch th&uacute; nhịn kh&ocirc;ng được ng&acirc;m khẽ:<br />\r\n<br />\r\n&ldquo;Thềm lạnh lẻ loi ngắm &aacute;nh trăng, sương đậm hoa thơm quanh đ&igrave;nh nhỏ&rdquo; Đằng sau ch&iacute;nh l&agrave; &quot;Từ khi ph&acirc;n t&aacute;n sau &acirc;n &aacute;i, nh&acirc;n gian kh&ocirc;ng đường đến nh&agrave; ti&ecirc;n, mặc cho hồn mộng đến ch&acirc;n trời.&rdquo; Thiếu ni&ecirc;n chẳng biết đến cảm gi&aacute;c tương tư, chỉ y&ecirc;u mến hai c&acirc;u thơ tr&ecirc;n, d&ugrave;ng trong l&uacute;c n&agrave;y đ&uacute;ng l&agrave; c&oacute; ch&uacute;t hợp với ho&agrave;n cảnh.<br />\r\n<br />\r\nSau lưng lại đột nhi&ecirc;n vang l&ecirc;n một hồi cười khẽ, tiếng n&oacute;i thuần khiết, kh&ocirc;ng trung mơ hồ c&oacute; tiếng ngọc va chạm v&agrave;o nhau, như tiếng trời trầm thấp truyền đến: &ldquo;Kh&ocirc;ng biết l&agrave; đang tưởng niệm ai đ&acirc;y?&rdquo;<br />\r\n<br />\r\nN&agrave;ng kinh h&atilde;i cứng người, ngo&aacute;i đầu nh&igrave;n lại, đứng dậy.<br />\r\n<br />\r\nĐ&atilde; thấy người nọ cong m&ocirc;i m&agrave; đứng đ&oacute;, mặt m&agrave;y đẹp đẽ ẩn b&ecirc;n trong b&oacute;ng c&acirc;y mờ ảo, diện mạo như ngọc, m&ocirc;i giống như hoa đỏ, chắc l&agrave; v&igrave; uống rượu, &aacute;nh mắt lại c&oacute; ch&uacute;t m&ecirc; ly.<br />\r\n<br />\r\nN&agrave;ng kh&ocirc;ng lộ th&acirc;n phận, chỉ d&ugrave;ng lễ thăm viếng: &ldquo;Thảo d&acirc;n tham kiến Thượng Thư Đại nh&acirc;n.&rdquo;<br />\r\n<br />\r\nNgười nọ kh&ocirc;ng n&oacute;i, c&oacute; ch&uacute;t nh&iacute;u m&agrave;y: &ldquo;Thảo d&acirc;n?&rdquo;<br />\r\n<br />\r\nN&agrave;ng đ&aacute;p kh&ocirc;ng ki&ecirc;u ngạo kh&ocirc;ng nịnh bợ, trong trẻo nhưng lạnh l&ugrave;ng: &ldquo;Phải&rdquo;<br />\r\n<br />\r\nHắn nh&agrave;n rỗi mở miệng: &ldquo;Chơi cờ kh&ocirc;ng tồi.&rdquo;<br />\r\n<br />\r\nNghe n&oacute;i người n&agrave;y thường c&ugrave;ng Ho&agrave;ng đế đ&aacute;nh cờ, trong nội cung thường thắng, liền c&oacute; t&acirc;m muốn quyết cao thấp, nhịn kh&ocirc;ng được mở miệng: &ldquo;Kh&ocirc;ng biết c&oacute; may mắn c&ugrave;ng Đại nh&acirc;n chơi v&aacute;n kế tiếp hay kh&ocirc;ng.&rdquo;<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng n&oacute;i g&igrave;, chỉ nheo mắt nh&igrave;n n&agrave;ng, &aacute;nh s&aacute;ng nhạt chớp động trong đ&ocirc;i mắt xanh, kh&ocirc;ng cảm thấy n&agrave;ng mạo muội hay l&agrave; kh&ocirc;ng biết tự lượng sức m&igrave;nh. N&agrave;ng đột nhi&ecirc;n ph&aacute;t gi&aacute;c m&igrave;nh đường đột, m&iacute;m m&ocirc;i kh&ocirc;ng n&oacute;i. Hắn lại tr&igrave; ho&atilde;n bước ch&acirc;n đi đến trước mặt n&agrave;ng, c&oacute; ch&uacute;t c&uacute;i người, t&oacute;c đen chảy xuống, lo&aacute;ng tho&aacute;ng chạm l&ecirc;n vai của n&agrave;ng, chỉ cảm thấy tươi m&aacute;t dị thường, ẩn ẩn thấm hương lạnh, nhất thời cũng thất thần. Hắn lại duỗi ra ng&oacute;n tay thon d&agrave;i trắng n&otilde;n nghịch hoa rơi tr&ecirc;n vai n&agrave;ng, c&aacute;nh hoa mềm mại dừng một l&aacute;t ở đầu ng&oacute;n tay, sau đ&oacute; theo gi&oacute; m&agrave; đi, nhưng đầu ng&oacute;n tay lại lưu hương, bị &aacute;nh trăng chiếu, mang theo qu&yacute; kh&iacute; sạch sẽ trắng n&otilde;n.<br />\r\n<br />\r\nN&agrave;ng nh&igrave;n đến ngốc, nh&igrave;n sang đ&ocirc;i mắt th&acirc;m th&uacute;y của hắn, đỏ mặt mỉm cười: &ldquo;Đ&ecirc;m khuya sương lạnh, Đại nh&acirc;n th&acirc;n thể qu&yacute; gi&aacute;, hay l&agrave; nhanh ch&oacute;ng trở về đi.&rdquo;<br />\r\n<br />\r\nVừa n&oacute;i như vậy, sau lưng quả thật truyền đến tiếng kinh ngạc của nội thị: &ldquo;Đại nh&acirc;n, th&igrave; ra ng&agrave;i trong n&agrave;y!&rdquo;<br />\r\n<br />\r\nMột v&ograve;ng &aacute;nh s&aacute;ng như nước dồn dập chảy qua trong đ&ocirc;i mắt hắn, lại giống như hoa tươi đẹp, như ph&aacute;o hoa rơi, diễm lệ như hồ y&ecirc;u ng&agrave;n năm. Hắn cong m&ocirc;i, hơi thở như lan giống như cười m&agrave; kh&ocirc;ng phải cười ở b&ecirc;n tai n&agrave;ng: &ldquo;Ta tưởng l&agrave; ai, h&oacute;a ra l&agrave; một mỹ nh&acirc;n xinh đẹp như hoa!&rdquo;<br />\r\n<br />\r\nHơi thở ấm &aacute;p mơ hồ đụng chạm v&agrave;nh tai tuyết trắng, n&oacute;ng bỏng, mặt n&agrave;ng tho&aacute;ng chốc đỏ ửng, ngu ngơ ngẩng đầu. Hắn cũng đ&atilde; xoay người, b&oacute;ng lưng thon d&agrave;i thẳng tắp dần dần đi xa trong b&oacute;ng đ&ecirc;m.<br />\r\n<br />\r\n~~~~~~~~~~~~~~~~~~~~~~~~~~~~~<br />\r\n<br />\r\n[1]Đ&acirc;y l&agrave; b&agrave;i thơ Thanh ngọc &aacute;n của t&aacute;c giả T&acirc;n Kh&iacute; Tật. Ở tr&ecirc;n l&agrave; bảng dịch thơ của Điệp luyến hoa.&nbsp;<br />\r\n<br />\r\nBản gốc<br />\r\n<br />\r\nĐ&ocirc;ng phong dạ ph&oacute;ng hoa thi&ecirc;n thụ,&nbsp;<br />\r\n<br />\r\nC&aacute;nh xuy lạc, tinh như vũ.&nbsp;<br />\r\n<br />\r\nBảo m&atilde; đi&ecirc;u xa hương m&atilde;n lộ.&nbsp;<br />\r\n<br />\r\nPhụng ti&ecirc;u thanh động,&nbsp;<br />\r\n<br />\r\nNgọc hồ quang chuyển,&nbsp;<br />\r\n<br />\r\nNhất dạ ngư long vũ.&nbsp;<br />\r\n<br />\r\nNga nhi tuyết liễu ho&agrave;ng kim lũ,&nbsp;<br />\r\n<br />\r\nTiếu ngữ doanh doanh &aacute;m hương khứ.&nbsp;<br />\r\n<br />\r\nCh&uacute;ng l&yacute; tầm tha thi&ecirc;n b&aacute;ch độ,&nbsp;<br />\r\n<br />\r\nMạch nhi&ecirc;n hồi thủ,&nbsp;<br />\r\n<br />\r\nNa nh&acirc;n khước tại,&nbsp;<br />\r\n<br />\r\nĐăng hoả lan san xứ.<br />\r\n<br />\r\nDịch thơ:<br />\r\n<br />\r\nĐ&ecirc;m xu&acirc;n gi&oacute; thổi ng&agrave;n hoa nở,&nbsp;<br />\r\n<br />\r\nRụng như mưa, sao rực rỡ.&nbsp;<br />\r\n<br />\r\nNgựa qu&yacute;, hương đưa, xe trạm trổ.&nbsp;<br />\r\n<br />\r\nPhụng ti&ecirc;u uyển chuyển,&nbsp;<br />\r\n<br />\r\n&Aacute;nh trăng lay động,&nbsp;<br />\r\n<br />\r\nSuốt đ&ecirc;m rồng c&aacute; rộn.&nbsp;<br />\r\n<br />\r\nNg&agrave;i tằm, liễu tuyết, tơ v&agrave;ng rủ,&nbsp;<br />\r\n<br />\r\nPhảng phất hương bay, cười n&oacute;i rộ.&nbsp;<br />\r\n<br />\r\nGiữa đ&aacute;m t&igrave;m người trăm ng&agrave;n độ,&nbsp;<br />\r\n<br />\r\nBỗng quay đầu lại,&nbsp;<br />\r\n<br />\r\nNgười ngay trước mắt,&nbsp;<br />\r\n<br />\r\nDưới lửa t&agrave;n đứng đ&oacute;.</h2>', 0, 'phuong-nghich-chuong-1-2-mỏ-dàu-2'),
(3, 2, 'Buông Gian Thần Của Trẫm Ra - Chương 1: Nhất thời kích động, hậu quả của một cuộc cưỡng dâm không thành!(1)', 'Buông Gian Thần Của Trẫm Ra - Chương 1: Nhất thời kích động, hậu quả của một cuộc cưỡng dâm không thành!(1)', '<h2>Ngự thư ph&ograve;ng.<br />\r\n<br />\r\nHo&agrave;ng thượng n&eacute;m đống tấu sớ tr&ecirc;n tay xuống đất, hướng về ph&iacute;a Đổng Khanh mặc một bộ quan phục m&agrave;u xanh lam, bực tức n&oacute;i: &quot;&Aacute;i khanh, Đậu thừa tướng g&oacute;p nhặt kh&ocirc;ng &iacute;t chứng cớ bất lợi với ngươi, cứ n&oacute;i rằng Đổng gia thường xuy&ecirc;n lung lạc triều thần, kết đảng kết ph&aacute;i, mưu lợi ri&ecirc;ng l&agrave;m rối kỉ cương. . . Thừa tướng c&ograve;n k&egrave;m theo bằng cớ, chứng nhận r&otilde; r&agrave;ng. C&oacute; phải l&agrave; ngươi nh&igrave;n tr&uacute;ng ng&ocirc;i vị ho&agrave;ng đế của trẫm rồi hả ? T&iacute;nh to&aacute;n mưu đồ gian tr&aacute;, cướp lấy giang sơn sao ?&quot;<br />\r\n<br />\r\nĐối mặt với lời l&ecirc;n &aacute;n li&ecirc;n tiếp, Đổng Khanh m&atilde;nh liệt lau mồ h&ocirc;i lạnh tr&ecirc;n tr&aacute;n, len l&eacute;n liếc mắt một c&aacute;i nh&igrave;n người bạn chơi từ thời nối khố c&agrave;ng ng&agrave;y c&agrave;ng anh tuấn oai h&ugrave;ng thần kh&iacute; r&otilde; r&agrave;ng, tiến l&ecirc;n một bước, v&aacute;i một lạy thật s&acirc;u, nghi&ecirc;m trang n&oacute;i: &quot;Ho&agrave;ng thượng anh minh, vi thần tuyệt đối kh&ocirc;ng nh&ograve;m ng&oacute; ng&ocirc;i vị ho&agrave;ng đế của ng&agrave;i, vi thần tuyệt kh&ocirc;ng c&oacute; l&ograve;ng tạo phản kia, trời đất chứng gi&aacute;m!&quot;<br />\r\n<br />\r\nHo&agrave;ng thượng r&otilde; r&agrave;ng l&agrave; hiểu lầm, r&otilde; r&agrave;ng c&aacute;i m&agrave; n&agrave;ng nh&igrave;n tr&uacute;ng ch&iacute;nh l&agrave;. . . . . . Vẻ đẹp của hắn, coi trọng ch&iacute;nh l&agrave; hậu vị a, n&agrave;o c&oacute; để &yacute; tới giang sơn ng&agrave;n dặm của hắn chứ?<br />\r\n<br />\r\nHuống chi, đ&oacute; cũng l&agrave; chuyện thật l&acirc;u trước đ&acirc;y rồi.<br />\r\n<br />\r\n&quot;Hả ?&quot; -Ho&agrave;ng thượng h&iacute;p mắt nh&igrave;n n&agrave;ng, vẻ mặt ho&agrave;i nghi, lạnh l&ugrave;ng n&oacute;i: &quot;Nghe Tiểu An Tử n&oacute;i, ngươi từng vứt trẫm v&agrave;o trong hồ nước, &yacute; đồ d&igrave;m trẫm chết đuối hả ? Ngươi c&ograve;n d&aacute;m n&oacute;i Đổng Khanh ngươi kh&ocirc;ng c&oacute; &yacute; đồ tạo phản sao ?&quot;<br />\r\n<br />\r\nD&igrave;m chết hắn ? Kh&ocirc;ng đ&acirc;u! Năm đ&oacute; n&agrave;ng năm đ&atilde; 17, m&agrave; hắn th&acirc;n l&agrave; th&aacute;i tử mới 15, n&agrave;ng ước chừng lớn hắn hai tuổi a, nữ lớn nam nhỏ, tuổi kết h&ocirc;n rất l&agrave; cấp b&aacute;ch, mắt thấy tuổi được gả rất nhanh sắp đến rồi, n&agrave;ng liền n&ocirc;n n&oacute;ng.<br />\r\n<br />\r\nChỉ l&agrave; thủ đoạn c&oacute; kịch liệt một ch&uacute;t &iacute;t, cưỡng gian chưa th&agrave;nh, cướp sắc thất bại, nhất thời kh&ocirc;ng cẩn thận thất thủ th&ocirc;i m&agrave;. . . . . . .<br />\r\n<br />\r\nNgay cả Thần Ti&ecirc;n đều c&oacute; thời điểm đ&aacute;nh trống sai m&agrave;.<br />\r\n<br />\r\nN&agrave;o ai biết được th&aacute;i tử quả thật l&agrave; Long thể, th&acirc;n thể vốn được chiều chuộng đến hỏng, ho&agrave;n to&agrave;n kh&ocirc;ng chịu được sự t&agrave;n ph&aacute;.<br />\r\n<br />\r\nTh&aacute;i tử l&uacute;c đ&oacute;, cũng ch&iacute;nh l&agrave; đương kim ho&agrave;ng thượng, sau khi t&eacute; xuống ao nước, tr&ecirc;n đầu nổi một cục u to, dẫn đến bệnh nặng một hồi, trong cung lập tức s&ocirc;i s&ugrave;ng sục truyền ra tin Đổng tiểu thư &acirc;m mưu giết hại ho&agrave;ng tự th&aacute;i tử Lưu Lăng. . . . . .<br />\r\n<br />\r\nViệc n&agrave;y, cưỡng gian chưa th&agrave;nh, t&eacute; xuống ao nước, chỉ l&agrave; việc nhỏ nha, th&aacute;i tử cũng kh&ocirc;ng bị n&agrave;ng l&agrave;m cho chết đi ? Chẳng qua chỉ nằm ở tr&ecirc;n giường mấy ng&agrave;y, &agrave;, kh&ocirc;ng! Ch&iacute;nh l&agrave; mấy th&aacute;ng m&agrave; th&ocirc;i. . . Song, những người kh&aacute;c xem việc n&agrave;y thấy c&oacute; ch&uacute;t nghi&ecirc;m trọng, chuyện b&eacute; x&eacute; ra to, d&aacute;m bịa đặt, ho&agrave;n to&agrave;n d&iacute;nh d&aacute;ng đến tội danh &ldquo;mưu hại&rdquo; c&aacute;c loại, chỉ tr&iacute;ch n&agrave;ng c&oacute; &yacute; đồ mưu hại th&aacute;i tử, cũng may hậu thuẫn của n&agrave;ng cứng c&aacute;p, c&oacute; một l&atilde;o cha l&agrave; đại quan ch&iacute;nh nhất phẩm của triều đ&igrave;nh Luyện Lương chống đỡ, thật vất vả mới bảo vệ được c&aacute;i mạng nhỏ của n&agrave;ng. Nhưng n&agrave;ng vốn c&oacute; cơ hội được sắc lập l&agrave;m th&aacute;i tử phi, lập tức được nhận định v&igrave; b&aacute;t tự c&oacute; điềm xấu, v&igrave; c&aacute;i ngo&agrave;i &yacute; muốn chết tiệt n&agrave;y, khiến từ nay về sau sẽ c&aacute;ch xa con đường l&agrave;m phi, sau đ&oacute; kh&ocirc;ng thể l&agrave;m g&igrave; kh&aacute;c hơn l&agrave; bất đắc dĩ thừa kế y b&aacute;t của phụ th&acirc;n, l&agrave;m quan tại triều, từ đ&oacute; v&ocirc; duy&ecirc;n với địa vị ho&agrave;ng hậu.<br />\r\n<br />\r\nBởi v&igrave; c&aacute;i gọi l&agrave; gần vua như gần cọp, kết bạn với mỹ nam lại c&agrave;ng đau khổ trong l&ograve;ng a.<br />\r\n<br />\r\nMấy năm qua đ&oacute;, n&agrave;ng sống dễ d&agrave;ng sao.<br />\r\n<br />\r\nN&agrave;ng ảo n&atilde;o ch&aacute;n nản nh&igrave;n sắc đẹp của ho&agrave;ng thượng, nuốt một nuốt nước miếng, chỉ l&ecirc;n trời ph&aacute;t ra lời thề son sắt: &quot;Thần đối với bệ hạ tuyệt đối kh&ocirc;ng c&oacute; hai l&ograve;ng !&quot;<br />\r\n<br />\r\n&quot;Như vậy, v&igrave; sao ngươi vẫn lu&ocirc;n ngăn cản trẫm sắc lập ho&agrave;ng hậu ? Chớ n&ecirc;n bảo l&agrave; v&igrave; sợ trẫm c&oacute; con nối d&ograve;ng sẽ được thừa kế đế vị? Đổng gia ngươi lo sợ nhất kh&ocirc;ng phải như thế sao?&quot; - Ho&agrave;ng thượng vẫn ho&agrave;i nghi n&agrave;ng.<br />\r\n<br />\r\nĐổng khanh kh&ocirc;ng nhanh kh&ocirc;ng chậm n&oacute;i: &quot;Vi thần chưa từng ngăn cản ho&agrave;ng thượng sắc lập ho&agrave;ng hậu, sau khi chọn trong danh s&aacute;ch, con g&aacute;i L&yacute; Thị lang c&oacute; bệnh kh&ocirc;ng tiện n&oacute;i ra, l&agrave; một người n&oacute;i ngọng, con g&aacute;i An Nam Quận vương qu&aacute; lẳng lơ, h&agrave;nh vi rất kh&ocirc;ng c&oacute; tiết chế, con g&aacute;i Trần thượng thư t&iacute;nh t&igrave;nh dữ dằn, c&ograve;n c&oacute; thể ngược đ&atilde;i động vật nhỏ, con g&aacute;i Ngụy thừa dung mạo xấu, con g&aacute;i Vương tướng qu&acirc;n qu&aacute; gầy, con g&aacute;i L&acirc;m ngự sử c&oacute; b&aacute;t tự kh&ocirc;ng tốt. . . . . . . .&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y kh&ocirc;ng tốt, c&aacute;i kia kh&ocirc;ng tốt, tất cả nữ nh&acirc;n của triều ta đều kh&ocirc;ng lọt v&agrave;o trong mắt Đổng Khanh ngươi hả ?&quot; - Hai tr&ograve;ng mắt sắc b&eacute;n của Ho&agrave;ng thượng nh&igrave;n chằm chằm n&agrave;ng, chậm r&atilde;i mở miệng hỏi: &quot;Đổng Khanh thật sự l&agrave; đ&iacute;ch th&acirc;n đi phỏng vấn qua tất cả những người được chọn l&agrave;m ho&agrave;ng hậu? Cẩn thận điều tra qua rồi hả ?&quot;<br />\r\n<br />\r\n&quot;Ho&agrave;ng hậu l&agrave; bậc mẫu nghi của một nước, thần kh&ocirc;ng d&aacute;m xem nhẹ ch&uacute;t n&agrave;o! Trong qu&aacute; tr&igrave;nh vi thần li&ecirc;n tục chọn lựa, cẩn thận gi&aacute;m định. . . quả thật kh&ocirc;ng chọn được người th&iacute;ch hợp tr&ecirc;n danh s&aacute;ch!&quot; - Đổng Khanh cất giọng đ&aacute;p lại. N&agrave;ng đ&acirc;u chỉ kh&ocirc;ng d&aacute;m xem nhẹ, n&oacute;i ngắn gọn ch&iacute;nh l&agrave; xoi m&oacute;i, bới l&ocirc;ng t&igrave;m vết đ&oacute;.<br />\r\n<br />\r\n&quot;Bởi v&igrave; &aacute;i khanh kh&ocirc;ng d&aacute;m khinh thường, qu&aacute; nghi&ecirc;m cẩn, trẫm cứ v&igrave; thế đến nay chưa lập được hậu, vốn tưởng rằng ngươi một l&ograve;ng suy nghĩ v&igrave; trẫm, l&acirc;u ng&agrave;y, ngay cả trẫm cũng kh&ocirc;ng khỏi kh&ocirc;ng ho&agrave;i nghi Đổng Khanh ngươi c&oacute; rắp t&acirc;m kh&aacute;c đấy!&quot;<br />\r\n<br />\r\nHo&agrave;ng thượng đ&atilde; qu&aacute; mười t&aacute;m tuổi, đến nay chưa lập hậu, chắc l&agrave; bị Th&aacute;i hậu l&agrave;m cho n&oacute;ng nảy, ho&agrave;ng thượng chịu &aacute;p lực, kh&ocirc;ng khỏi cũng bắt đầu ho&agrave;i nghi n&agrave;ng chịu tr&aacute;ch nhiệm thay mặt hắn nắm giữ lựa chọn danh s&aacute;ch lập hậu.Tr&ecirc;n tr&aacute;n Đổng Khanh lập tức t&uacute;a ra mồ h&ocirc;i lạnh . . . . . . .<br />\r\n<br />\r\nL&uacute;c n&agrave;y, lại thấy người hầu nhỏ của ho&agrave;ng thượng, nội gi&aacute;m Tiểu An Tử đang ở ngo&agrave;i cửa, m&atilde;nh liệt nh&aacute;y mắt.<br />\r\n<br />\r\nHo&agrave;ng thượng Lưu Lăng liếc g&atilde; một c&aacute;i, thấp giọng n&oacute;i: &quot;Ở b&ecirc;n ngo&agrave;i l&eacute;n l&uacute;t l&agrave;m c&aacute;i g&igrave; thế ? V&agrave;o đi !&quot;<br />\r\n<br />\r\nTiểu An Tử ở ngo&agrave;i cửa nghe thấy, lập tức cười h&igrave; h&igrave; tiến v&agrave;o, khom khom n&oacute;i: &quot;Th&aacute;i hậu muốn mời con g&aacute;i của Đậu thừa tướng v&agrave;o cung b&aacute;i yết, Người sai tiểu nh&acirc;n đến xem ho&agrave;ng thượng đ&atilde; l&agrave;m xong chuyện tr&ecirc;n triều chưa, nếu kh&ocirc;ng c&oacute; chuyện g&igrave; xin mời ho&agrave;ng thượng di gi&aacute; đến cung Từ Ninh ngồi một ch&uacute;t !&quot;<br />\r\n<br />\r\nTh&aacute;i hậu quả thật cho đ&ograve;i Đậu tiểu thư tiến cung.<br />\r\n<br />\r\n&quot;Nghe n&oacute;i con g&aacute;i Đậu thừa tướng dung mạo tuyệt mỹ, thi từ ca ph&uacute; kh&ocirc;ng g&igrave; kh&ocirc;ng biết, bản t&iacute;nh hiền thục, c&ograve;n l&agrave; một t&agrave;i nữ nổi danh xinh đẹp !&quot; - Lưu Lăng ngồi ngay ngắn ở tr&ecirc;n ghế rồng, kh&ocirc;ng ch&uacute;t lưu t&acirc;m d&ugrave;ng ng&oacute;n tay g&otilde; c&aacute;i b&agrave;n, vẫn l&agrave; nh&igrave;n Đổng Khanh m&agrave; n&oacute;i: &quot;N&agrave;ng ấy chẳng lẽ cũng kh&ocirc;ng đủ điều kiện l&agrave;m hậu sao?&quot;<br />\r\n<br />\r\nViệc n&agrave;y Đổng Khanh thật oan ức, n&agrave;ng lập tức chắp tay thi lễ, n&oacute;i: &quot;Khởi bẩm ho&agrave;ng thượng, trong danh s&aacute;ch lập hậu kh&ocirc;ng c&oacute; tiểu thư Đậu gia!&quot;<br />\r\n<br />\r\n&quot;Ồ !&quot; - Ho&agrave;ng thượng nh&iacute;u m&agrave;y, đ&uacute;ng l&agrave; c&oacute; ch&uacute;t kh&ocirc;ng tin. &quot;Danh m&ocirc;n khu&ecirc; t&uacute; điều kiện tốt như thế lại kh&ocirc;ng c&oacute; ở trong danh s&aacute;ch lập hậu sao ?&quot;<br />\r\n<br />\r\nĐổng Khanh bất đắc dĩ nh&uacute;n nh&uacute;n vai.<br />\r\n<br />\r\nTiểu An Tử nhạy b&eacute;n tin tức lập tức tiến l&ecirc;n bổ sung n&oacute;i r&otilde;: &quot;Bẩm ho&agrave;ng thượng, chuyện l&agrave; như vậy, Th&aacute;i hậu n&oacute;i Đổng Khanh rắp t&acirc;m hại người, cả ng&agrave;y đi theo b&ecirc;n cạnh ho&agrave;ng thượng, r&otilde; r&agrave;ng l&agrave; mưu đồ kh&ocirc;ng tốt, &yacute; đồ biển thủ, muốn một m&igrave;nh nuốt ho&agrave;ng thượng, thế l&agrave; Th&aacute;i hậu xuất ra kỳ chi&ecirc;u, lần n&agrave;y kh&ocirc;ng chọn theo danh s&aacute;ch, kh&ocirc;ng d&ugrave;ng quan gi&aacute;m định của triều đ&igrave;nh, tự Người chọn lựa con d&acirc;u, l&atilde;o nh&acirc;n gia Người đối với tiểu thư Đậu gia vừa gặp liền h&agrave;i l&ograve;ng ngay, muốn ho&agrave;ng thượng đi qua nh&igrave;n một c&aacute;i, nếu nh&igrave;n ưng &yacute; rồi, nhanh ch&oacute;ng t&igrave;m ng&agrave;y sắc phong l&agrave;m ho&agrave;ng hậu !&quot;<br />\r\n<br />\r\n&quot;Theo s&aacute;t ở b&ecirc;n cạnh trẫm, mưu đồ kh&ocirc;ng tốt, &yacute; đồ biển thủ ?&quot; - Lưu Lăng lạnh l&ugrave;ng ng&oacute; chừng Đổng Khanh, vạn phần căm tức, đột nhi&ecirc;n h&eacute;t lớn một tiếng n&oacute;i: &quot;Đổng Khanh to gan, ngươi d&aacute;m cả gan nh&igrave;n tr&uacute;ng nữ nh&acirc;n của trẫm? Kh&oacute; tr&aacute;ch ngươi x&eacute;t duyệt cung nữ trong danh s&aacute;ch, kh&ocirc;ng c&oacute; được một người hợp c&aacute;ch, tất cả đều bị ngươi đ&aacute;nh trượt !&quot;<br />\r\n<br />\r\nĐổng Khanh nghe xong, th&acirc;n thể cứng đờ, ngơ ng&aacute;c nh&igrave;n ho&agrave;ng thượng Lưu Lăng, h&aacute; miệng, rồi lại kh&ocirc;ng biết n&ecirc;n giải th&iacute;ch từ đ&acirc;u.<br />\r\n<br />\r\nTiểu An Tử lập tức thay n&agrave;ng giải th&iacute;ch, hắn nhướng m&agrave;y n&oacute;i: &quot;Ho&agrave;ng thượng, ng&agrave;i xảy ra chuyện g&igrave; vậy, ng&agrave;i vừa đ&atilde; qu&ecirc;n. . . . . . Đổng đại nh&acirc;n l&agrave; nữ sao ? C&aacute;c người c&ograve;n c&ugrave;ng nhau lớn l&ecirc;n nha, n&agrave;ng sẽ kh&ocirc;ng đoạt nữ nh&acirc;n với ng&agrave;i!&quot;<br />\r\n<br />\r\nN&agrave;ng l&agrave; nữ nh&acirc;n?<br />\r\n<br />\r\nLưu Lăng ngưng mắt nh&igrave;n người trước mắt mặc &aacute;o b&agrave;o quan phục m&agrave;u xanh lam, người trước mắt một th&acirc;n trang phục nam tử nhưng d&aacute;ng vẻ thanh lệ, bối rối một l&uacute;c l&acirc;u, ấp &uacute;ng n&oacute;i: &quot;Nữ nh&acirc;n. . .,Đổng Khanh l&agrave; một phụ nữ ? Nữ nh&acirc;n ch&acirc;n ch&iacute;nh, kh&ocirc;ng phải l&agrave; giả g&aacute;i hả ? Trẫm r&otilde; r&agrave;ng lại kh&ocirc;ng để &yacute; Đổng Khanh l&agrave; nữ đấy!&quot;<br />\r\n<br />\r\nNăm đ&oacute; hắn rơi xuống nước, t&eacute; đầu, mất tr&iacute; nhớ, đồng thời vừa bệnh nặng một trận, sau khi hết bệnh, hắn lo&aacute;ng tho&aacute;ng chỉ nhớ r&otilde; m&igrave;nh l&agrave; ho&agrave;ng th&aacute;i tử Lưu Lăng, ở Đ&ocirc;ng cung từ từ nghỉ ngơi hơn năm, sau khi l&ecirc;n ng&ocirc;i, nh&igrave;n thấy Đổng Khanh đ&atilde; l&agrave; một th&acirc;n trang phục quan b&agrave;o của nam tử, d&aacute;ng vẻ n&agrave;y của n&agrave;ng l&agrave;m cho hắn kh&ocirc;ng lưu &yacute;. . . . . . N&agrave;ng l&agrave; nữ nh&acirc;n.<br />\r\n<br />\r\nTr&ecirc;n triều đ&igrave;nh, hắn cảm thấy Đổng Khanh l&agrave; kẻ giả g&aacute;i, hắn sao lại nh&igrave;n như vậy, sao m&agrave; kh&ocirc;ng được tự nhi&ecirc;n.<br />\r\n<br />\r\nĐương triều theo đuổi tr&agrave;o lưu danh sĩ, y&ecirc;u th&iacute;ch thoa phấn son, da thịt trơn b&oacute;ng được che giấu bảo dưỡng cẩn thận tỉ mỉ kh&ocirc;ng bỏ s&oacute;t, l&agrave;m cho da dẻ trắng s&aacute;ng, c&oacute; thể so với nữ nh&acirc;n, hiện tại giả g&aacute;i nhiều lắm, Đổng Khanh mặc d&ugrave; kh&ocirc;ng thoa phấn, m&agrave; vẫn giống như l&agrave; kẻ giả g&aacute;i.<br />\r\n<br />\r\nNhư Tiểu An Tử n&oacute;i, Đổng Khanh theo s&aacute;t ở b&ecirc;n cạnh &lsquo;gi&aacute;m s&aacute;t&rsquo; hắn, vẫn c&ograve;n nghĩ muốn bản th&acirc;n nuốt hắn, vậy . . . . . . Chẳng lẽ, Đổng Khanh đ&uacute;ng l&agrave; ngấp ngh&eacute; hắn sao ?!<br />\r\n<br />\r\nBuồn cười, nhưng hắn l&agrave; đ&agrave;n &ocirc;ng m&agrave; ! Lại l&agrave; Đương Kim Th&aacute;nh Thượng, c&oacute; thể n&agrave;o để cho một nam nh&acirc;n nam kh&ocirc;ng ra nam, nữ kh&ocirc;ng ra nữ. . ,&Agrave; Kh&ocirc;ng! L&agrave; nữ nh&acirc;n đến th&egrave;m thuồng ?<br />\r\n<br />\r\nThể diện của Lưu Lăng bị bẽ b&agrave;ng, lập tức vỗ l&ecirc;n tr&ecirc;n b&agrave;n thật mạnh, cả giận n&oacute;i: &quot;C&agrave;n rỡ!&quot;<br />\r\n<br />\r\nĐổng Khanh lập tức quỳ rạp xuống đất, &quot;Xin Ho&agrave;ng thượng bớt giận!&quot;<br />\r\n<br />\r\n&quot;Đổng khanh, ngươi thật to gan, lại d&aacute;m ngấp ngh&eacute; trẫm . . . Khụ, khụ, sắc đẹp. . . của trẫm!&quot; - Lưu Lăng vừa c&aacute;u vừa giận.<br />\r\n<br />\r\nNếu như n&agrave;ng muốn nh&uacute;ng ch&agrave;m giang sơn của hắn th&igrave; cũng coi như th&ocirc;i, coi như n&agrave;ng c&oacute; gan!<br />\r\n<br />\r\nNhưng m&agrave; n&agrave;ng, n&agrave;ng lại l&agrave; muốn nh&uacute;ng ch&agrave;m hắn sao ? Ho&agrave;ng thượng vậy m&agrave; bị đại thần mạnh tay bẻ hoa sao. Hừ, buồn cười! Để cho hắn biết đặt mặt mũi ở nơi n&agrave;o chứ?<br />\r\n<br />\r\nN&oacute;i thế n&agrave;o cũng n&ecirc;n l&agrave; hắn tới nh&uacute;ng ch&agrave;m n&agrave;ng mới đ&uacute;ng chứ.<br />\r\n<br />\r\nẶc, kh&ocirc;ng! Giới t&iacute;nh của hắn b&igrave;nh thường, kh&ocirc;ng phải l&agrave; đồng t&iacute;nh, đối với loại giả g&aacute;i thực sự kh&ocirc;ng c&oacute; hứng th&uacute;.<br />\r\n<br />\r\nHuống chi th&acirc;n l&agrave; vua một nước, sao c&oacute; thể n&agrave;o nh&uacute;ng ch&agrave;m đại thần, l&agrave;m tr&ograve; cười cho người trong thi&ecirc;n hạ ?<br />\r\n<br />\r\nĐổng Khanh lăn lộn đ&atilde; l&acirc;u tại quan trường, cũng ch&igrave;m ẩn, thấy ho&agrave;ng thượng giận dữ, kh&ocirc;ng nhanh kh&ocirc;ng chậm n&oacute;i: &quot;Ho&agrave;ng thượng oan uổng vi thần rồi, Th&aacute;i hậu đ&atilde; nghi&ecirc;m lệnh, điều kiện tuyển chọn ho&agrave;ng hậu l&agrave; cung nữ từ 14 tới 18 tuổi, thần năm nay đ&atilde; nhược qu&aacute;n, hai mươi tuổi rồi, đ&atilde; sớm qua tuổi h&ocirc;n phối, kh&ocirc;ng thể lập hậu, n&oacute;i tới nh&uacute;ng ch&agrave;m, l&agrave; n&oacute;i mơ sao?&quot;<br />\r\n<br />\r\n(nhược qu&aacute;n: ng&agrave;y xưa gọi thanh ni&ecirc;n khoảng 20 l&agrave; nhược qu&aacute;n)<br />\r\n<br />\r\nTiểu An Tử thấy ho&agrave;ng thượng giận dữ, lập tức giảng h&ograve;a, nhắc nhở: &quot;Ho&agrave;ng thượng, Đổng đại nh&acirc;n đ&atilde; qu&aacute; tuổi, kh&ocirc;ng ph&ugrave; hợp với điều kiện trứng cử ho&agrave;ng hậu m&agrave;. Huống chi n&agrave;ng nay l&agrave; ch&iacute;nh nhất phẩm đại quan rồi, thực tại n&agrave;ng kh&ocirc;ng cần thiết cản trở ho&agrave;ng thượng ng&agrave;i sắc lập ho&agrave;ng hậu, chuyện đ&atilde; qua nhắc lại cũng kh&ocirc;ng c&ograve;n &yacute; nghĩa, Th&aacute;i hậu c&ograve;n đang ở cung Từ Ninh chờ ho&agrave;ng thượng đi gặp Đậu tiểu thư nữa, cũng kh&ocirc;ng n&ecirc;n để cho Th&aacute;i hậu l&atilde;o nh&acirc;n gia đợi l&acirc;u !&quot;<br />\r\n<br />\r\nHo&agrave;ng thượng ngoắc ngoắc tay, cho triệu Tiểu An Tử bước l&ecirc;n bậc thềm, gh&eacute; v&agrave;o lỗ tai g&atilde;, nhỏ giọng hỏi: &quot;N&oacute;i một ch&uacute;t đi, đến tột c&ugrave;ng l&agrave; trẫm v&agrave; Đổng Khanh đ&atilde; xảy ra việc g&igrave; hả ?&quot;<br />\r\n<br />\r\nTiểu An Tử l&eacute;n l&uacute;t liếc Đổng Khanh ở dưới bậc một c&aacute;i, gh&eacute; v&agrave;o b&ecirc;n tai ho&agrave;ng thượng n&oacute;i: &quot;Ho&agrave;ng thượng v&agrave; Đổng Khanh l&agrave; thanh mai tr&uacute;c m&atilde;, c&ugrave;ng nhau lớn l&ecirc;n, t&igrave;nh cảm tốt v&ocirc; c&ugrave;ng, hai người thường xuy&ecirc;n kh&ocirc;ng t&aacute;ch ra, nhưng khi Đổng Khanh vừa được 17 tuổi lại đột nhi&ecirc;n phạm v&agrave;o d&acirc;m t&acirc;m, sắc t&acirc;m nổi l&ecirc;n, cả ng&agrave;y mơ ước sắc đẹp ho&agrave;ng thượng, ba năm trước đ&acirc;y, v&agrave;o một ng&agrave;y nọ, kh&ocirc;ng biết sự việc l&agrave; v&igrave; sao, n&agrave;ng bỗng nhi&ecirc;n giống như ăn phải thuốc d&acirc;m, rồi nổi l&ecirc;n sắc t&acirc;m với ho&agrave;ng thượng, n&agrave;ng bất ngờ nổi l&ecirc;n th&uacute; t&iacute;nh, sắc đẹp ph&iacute;a trước, n&agrave;ng chỉ v&igrave; th&egrave;m thuồng đ&atilde; l&acirc;u kh&ocirc;ng được thỏa m&atilde;n, l&uacute;c ấy ban đ&ecirc;m gi&oacute; lớn, chim quạ k&ecirc;u về đ&ecirc;m, gi&oacute; &acirc;m nổi l&ecirc;n bốn ph&iacute;a, đ&uacute;ng l&uacute;c bốn bề vắng lặng, mắt thấy cơ hội hiếm c&oacute;, thế l&agrave; n&agrave;ng liền mạnh mẽ d&acirc;ng l&ecirc;n sắc t&acirc;m, nhe ra h&agrave;m răng d&acirc;m đ&atilde;ng, lộ ra đ&ocirc;i m&oacute;ng vuốt d&acirc;m &ocirc;, cười he he hai tiếng, sau đ&oacute; l&ograve;ng dạ độc &aacute;c đ&aacute;nh về ph&iacute;a ho&agrave;ng thượng, thủ đoạn đ&acirc;u chỉ hung t&agrave;n khiến người ta giận s&ocirc;i tr&agrave;o, n&agrave;ng t&agrave;n nhẫn m&atilde;nh mẽ bẻ hoa, đem ho&agrave;ng thượng vẫn c&ograve;n trẻ dại đ&egrave; ở tr&ecirc;n vi&ecirc;n đ&aacute; to lớn, ch&agrave; ch&agrave; rồi lại đạp đạp, &yacute; đồ nh&uacute;ng ch&agrave;m. . . . . . .&quot;<br />\r\n<br />\r\nN&oacute;i tới đ&acirc;y, sắc mặt ho&agrave;ng thượng nhất thời một trận ửng đỏ, hơi c&oacute; phần căm tức n&oacute;i: &quot;Đủ rồi, đừng n&oacute;i nữa!&quot;<br />\r\n<br />\r\nHắn quả thật bị triều thần t&agrave;n ph&aacute; ch&agrave; đạp ? Xong rồi sao ? Quẫn b&aacute;ch nhất ch&iacute;nh l&agrave; cuối c&ugrave;ng c&ograve;n ng&atilde; v&agrave;o trong hồ, chật vật kh&ocirc;ng chịu nổi, quẫn b&aacute;ch như thế, mất hết mặt mũi.<br />\r\n<br />\r\nVậy c&ograve;n kh&ocirc;ng bằng cứ thất th&acirc;n cho sảng kho&aacute;i.<br />\r\n<br />\r\nNam nh&acirc;n c&oacute; cần thiết phải liều chết thủ th&acirc;n như ngọc kh&ocirc;ng ?<br />\r\n<br />\r\n&quot;Sau n&agrave;y, kh&ocirc;ng cho ph&eacute;p nhắc lại chuyện n&agrave;y nửa chữ !&quot; - Ho&agrave;ng thượng ra vẻ trấn định dặn d&ograve;, sau đ&oacute; l&uacute;c lắc tay &aacute;o, ra lệnh n&oacute;i: &quot;B&atilde;i gi&aacute; cung Từ Ninh !&quot;<br />\r\n<br />\r\nDứt lời, th&aacute;nh gi&aacute; đứng dậy, Đổng Khanh lập tức thối lui đến b&ecirc;n cạnh, c&uacute;i đầu, cung tiễn ho&agrave;ng thượng, trong kh&oacute;e mắt dư quang lại liếc thấy hắn đột nhi&ecirc;n dừng bước ở trước mặt n&agrave;ng. . . . . . . . .<br />\r\n<br />\r\nNh&igrave;n tư thế n&agrave;y, r&otilde; r&agrave;ng l&agrave; c&oacute; lời g&igrave; muốn dặn d&ograve;.<br />\r\n<br />\r\nTự xưng l&agrave; trung thần, Đổng Khanh thở d&agrave;i cung k&iacute;nh n&oacute;i: &quot;Xin ho&agrave;ng thượng căn dặn!&quot;<br />\r\n<br />\r\nLưu Lăng quan s&aacute;t n&agrave;ng thật l&acirc;u, thần sắc th&acirc;m trầm kh&oacute; d&ograve;, cuối c&ugrave;ng cũng l&agrave; cười như kh&ocirc;ng cười ph&acirc;n ph&oacute;, n&oacute;i: &quot;Đổng Khanh, theo trẫm đi gi&aacute;m định nữ nh&acirc;n, trẫm cũng muốn nh&igrave;n một ch&uacute;t &aacute;nh mắt lựa chọn nữ nh&acirc;n của ngươi sắc b&eacute;n như thế n&agrave;o!&quot;<br />\r\n<br />\r\nĐổng Khanh nghe xong, sắc mặt nhất thời trầm xuống.</h2>', 0, 'buong-gian-than-cua-tram-ra-chuong-1-nhat-thoi-kich-dong-hau-qua-cua-mot-cuoc-cuong-dam-khong-thanh1');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(4, 3, 'Xuyên Đến Mạt Thế Bảo Vệ Anh - Chương 1', 'Xuyên Đến Mạt Thế Bảo Vệ Anh - Chương 1', '<h2>Chương 1</h2>\r\n\r\n<h2>&ldquo;Đi nhanh! Đừng lo cho t&ocirc;i!&rdquo;<br />\r\n<br />\r\nTrong đ&agrave;n zombie, một người đ&agrave;n &ocirc;ng cao lớn, anh tuấn đang vừa đ&aacute;nh nhau với zombie, vừa kh&agrave;n giọng g&agrave;o về hướng chiếc m&aacute;y bay trực thăng. M&aacute;y bay trực thăng kh&ocirc;ng muốn rời đi, c&oacute; người đứng tr&ecirc;и m&aacute;y bay g&agrave;o l&ecirc;n với anh: &ldquo;Muốn đi th&igrave; c&ugrave;ng đi, nhanh l&ecirc;n đ&acirc;y!&rdquo;<br />\r\n<br />\r\nĐột nhi&ecirc;n người đ&agrave;n &ocirc;ng vung tay l&ecirc;n, một v&ograve;i rồng chuẩn x&aacute;c bay về ph&iacute;a m&aacute;y bay trực thăng, sức mạnh v&ocirc; c&ugrave;ng khổng lồ trực tiếp n&eacute;m bay chiếc trực thăng cứ chậm chạp kh&ocirc;ng chịu rời đi ra ngo&agrave;i.<br />\r\n<br />\r\nMắt thấy trực thăng c&agrave;ng l&uacute;c c&agrave;ng xa, người đ&agrave;n &ocirc;ng quay đầu nh&igrave;n tầng tầng lớp lớp zombie ở khắp bốn phương t&aacute;m hướng, anh cười, gương mặt bi tr&aacute;ng m&agrave; quyết tuyệt, lại giống như được giải tho&aacute;t, anh nhắm mắt lại, h&eacute;t to một tiếng.<br />\r\n<br />\r\nNgay sau đ&oacute;, từ tr&ecirc;и kh&ocirc;ng nh&igrave;n xuống, một l&agrave;n s&oacute;ng v&ocirc; h&igrave;nh lấy người đ&agrave;n &ocirc;ng l&agrave;m trung t&acirc;m lan ra tứ ph&iacute;a, tất cả zombie v&agrave; những kiến tr&uacute;c c&ograve;n s&oacute;t lại h&oacute;a th&agrave;nh bột mịn trong nh&aacute;y mắt, qu&eacute;t sạch cả khu vực.<br />\r\n<br />\r\nMột dị năng giả phong hệ cấp 9 tự bạo, hủy diệt đ&agrave;n zombie cuối c&ugrave;ng, từ đ&oacute; thi&ecirc;n địa đổi thay, mở ra một thế giới mới.<br />\r\n<br />\r\n&hellip;<br />\r\n<br />\r\n&ldquo;Hu hu hu&hellip;&rdquo; L&acirc;m Đ&agrave;m Đ&agrave;m r&uacute;t tờ khăn giấy cuối c&ugrave;ng, kh&oacute;c đến hai mắt đỏ bừng: &ldquo;Tại sao? Tại sao lại phải tự bạo? Tại sao lại phải chết? R&otilde; r&agrave;ng anh c&oacute; thể sống m&agrave;!&rdquo;<br />\r\n<br />\r\nC&ocirc; y t&aacute; b&ecirc;n cạnh rời mắt khỏi m&agrave;n h&igrave;nh, một lời kh&oacute; n&oacute;i hết nh&igrave;n c&ocirc;, giải th&iacute;ch lần thứ n: &ldquo;Anh ta bị zombie c&agrave;o rồi, vốn kh&ocirc;ng thể sống tiếp nữa.&rdquo;<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng, kh&ocirc;ng phải!&rdquo; L&acirc;m Đ&agrave;m Đ&agrave;m chợt đặt miếng khăn giấy xuống, mặt nghi&ecirc;m t&uacute;c: &ldquo;L&agrave; bởi v&igrave; suốt bảy năm qua, chiến hữu v&agrave; bạn b&egrave; của anh ấy đều chết hết, ngay cả người anh ấy y&ecirc;u cũng chết lu&ocirc;n, một m&igrave;nh anh kh&ocirc;ng c&ograve;n lưu luyến g&igrave; nữa, anh ấy kh&ocirc;ng muốn sống một m&igrave;nh&hellip; Hu hu hu, sao anh lại thảm như vậy, Diệp thần của em!&rdquo;<br />\r\n<br />\r\nC&ocirc; y t&aacute; cười khan: &ldquo;Đ&agrave;m Đ&agrave;m &agrave;, quan hệ giữa Diệp thần v&agrave; đội ph&oacute; Bạch l&agrave; do tiểu thuyết đồng nh&acirc;n v&agrave; d&atilde; sử (*) viết lung tung th&ocirc;i.&rdquo;<br />\r\n<br />\r\n(*) D&atilde; sử l&agrave; k&yacute; ức lịch sử được lưu truyền trong d&acirc;n gian. Từ điển Tiếng Việt 1992, định nghĩa d&atilde; sử l&agrave;: &ldquo;Lịch sử ghi ch&eacute;p những chuyện lưu truyền trong d&acirc;n gian, do tư nh&acirc;n viết, ph&acirc;n biệt với ch&iacute;nh sử&rdquo;. Nội dung c&oacute; li&ecirc;n quan đến c&aacute;c nh&acirc;n vật v&agrave; sự kiện lịch sử. V&iacute; dụ như những c&acirc;u chuyện về vua L&ecirc; Lợi, về Nguyễn Tr&atilde;i&hellip; (Nguồn: Linkhay)<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng thể n&agrave;o, Diệp thần c&oacute; rất nhiều chiến hữu nhưng đ&acirc;u thấy anh ấy th&acirc;n mật với người kh&aacute;c như vậy, gi&aacute; trị vũ lực của Diệp thần đứng đầu cả nước, đội ph&oacute; Bạch l&agrave; cố vấn cao cấp của anh ấy, bọn họ hỗ trợ lẫn nhau, l&agrave;m bạn nửa cuộc đời, đội ph&oacute; Bạch chết rồi, mấy th&aacute;ng sau Diệp thần cũng tự bạo, đ&oacute; ch&iacute;nh l&agrave; t&igrave;nh y&ecirc;u!&rdquo;<br />\r\n<br />\r\nC&ocirc; y t&aacute; cạn lời, được rồi, c&ocirc; vui l&agrave; được.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, m&agrave;n h&igrave;nh đ&atilde; đổi cảnh, rất nhiều người sống s&oacute;t tr&ecirc;и cả nước thương tiếc cho một thế hệ cường giả Diệp Ti&ecirc;u, một người đ&agrave;n &ocirc;ng đứng tr&ecirc;и bục, cảm x&uacute;c dạt d&agrave;o n&oacute;i: &ldquo;Tuy thủ lĩnh Diệp Ti&ecirc;u đ&atilde; rời xa ch&uacute;ng ta nhưng zombie cũng đ&atilde; bị diệt sạch, mở ra một thế giới mới, l&uacute;c n&agrave;y, ch&uacute;ng ta c&agrave;ng phải đo&agrave;n kết, x&acirc;y dựng lại qu&ecirc; hướng mới của ch&uacute;ng ta.&rdquo;<br />\r\n<br />\r\nNgười n&agrave;y t&ecirc;n l&agrave; Chu Nham, l&agrave; Tổng Thống đầu ti&ecirc;n sau thời mạt thế, dẫn dắt những người may mắn sống s&oacute;t trong nước kiến thiết qu&ecirc; hương mới, l&agrave; một nh&acirc;n vật vĩ đại tr&ecirc;и tấm bia lịch sử to lớn. L&acirc;m Đ&agrave;m Đ&agrave;m bĩu m&ocirc;i, cảm thấy t&ecirc;n &aacute;c &ocirc;n n&agrave;y kh&ocirc;ng phải l&agrave; người tốt.<br />\r\n<br />\r\nĐ&uacute;ng vậy, mạt thế k&eacute;o d&agrave;i bảy năm, &ocirc;ng ta chỉ biết theo sau những cường giả kia ăn h&ocirc;i, gi&aacute; trị vũ lực kh&ocirc;ng l&ecirc;n nổi top 20 cả nước nhưng sau một trận zombie c&ocirc;ng kϊƈɦ, cường giả từng người mất đi, cuối c&ugrave;ng ngay cả đệ nhất cường giả Diệp Ti&ecirc;u cũng tự bạo n&ecirc;n người n&agrave;y mới c&oacute; cơ hội ra mặt, nếu kh&ocirc;ng chừng n&agrave;o mới đến phi&ecirc;n &ocirc;ng ta l&ecirc;n l&agrave;m người l&atilde;nh đạo thế giới mới?<br />\r\n<br />\r\nNghe n&oacute;i &ocirc;ng ta v&igrave; cũng cố địa vị l&atilde;nh đạo của m&igrave;nh m&agrave; l&agrave;m kh&ocirc;ng &iacute;t việc l&ograve;ng dạ hiểm &aacute;c, căn cứ Ch&iacute;nh Dương của Diệp thần cũng bị &ocirc;ng ta &ldquo;tẩy sạch&rdquo; một lần, tất cả những người kh&ocirc;ng ủng hộ &ocirc;ng ta đều bị &ocirc;ng ta &acirc;m thầm trừ khử hết.<br />\r\n<br />\r\nNếu kh&ocirc;ng phải mạt thế đ&atilde; qua ba trăm năm, mọi người bắt đầu nghĩ lại về lịch sử, c&oacute; gan đ&agrave;o m&oacute;c chuyện cũ th&igrave; những việc &aacute;c m&agrave; vị n&agrave;y l&agrave;m ra kh&ocirc;ng biết c&ograve;n bị che giấu bao l&acirc;u nữa!<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m bỗng n&oacute;i: &ldquo; Nếu em c&oacute; thể gi&uacute;p Diệp thần một tay th&igrave; hay rồi, trong mạt thế, anh ấy l&agrave;m biết bao việc cho d&acirc;n, cứu được biết bao người thế m&agrave; lại kh&ocirc;ng thể chết gi&agrave;, anh em sinh tử cũng chết rất th&ecirc; thảm&hellip;&rdquo;<br />\r\n<br />\r\nC&ocirc; y t&aacute; nh&igrave;n c&ocirc;, hốc mắt đột nhi&ecirc;n ẩm ướt nhưng vẫn cố gắng nhịn xuống, n&oacute;i: &ldquo;Đ&agrave;m Đ&agrave;m, ba mẹ v&agrave; em trai em tới thăm rồi, ch&uacute;ng ta ra ngo&agrave;i đi.&rdquo;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m lập tức phấn chấn hẳn: &ldquo;Họ đến rồi sao? Ch&uacute;ng ta đi th&ocirc;i.&rdquo;<br />\r\n<br />\r\nC&ocirc; y t&aacute; gi&uacute;p L&acirc;m Đ&agrave;m Đ&agrave;m ra ngo&agrave;i, quả nhi&ecirc;n ba mẹ L&acirc;m gia v&agrave; em trai đ&atilde; đến, bọn họ th&acirc;n thiết n&oacute;i chuyện với L&acirc;m Đ&agrave;m Đ&agrave;m, c&ugrave;ng nhau ăn cơm, r&otilde; r&agrave;ng l&agrave; h&igrave;nh ảnh vui tươi n&aacute;o nhiệt nhưng ai ai cũng c&oacute; vẻ như đang cố n&eacute;n bi thương.<br />\r\n<br />\r\nChỉ c&oacute; L&acirc;m Đ&agrave;m Đ&agrave;m l&agrave; kh&ocirc;ng sao cả, c&ocirc; ăn cơm với ba mẹ v&agrave; em trai xong, để mọi người đẩy m&igrave;nh ra hoa vi&ecirc;n trong bệnh viện tản bộ, n&oacute;i n&oacute;i cười cười, bệnh nh&acirc;n gặp được dọc đường ai cũng nồng nhiệt k&iacute;nh nể ch&agrave;o hỏi L&acirc;m Đ&agrave;m Đ&agrave;m: &ldquo;Ch&agrave;o b&aacute;c sĩ L&acirc;m.&rdquo; &ldquo;B&aacute;c sĩ L&acirc;m tản bộ vui ạ.&rdquo;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m cũng gật đầu đ&aacute;p lại từng người, kh&iacute; thế ổn trọng của một vị b&aacute;c sĩ lừng danh tản ra, n&agrave;o c&ograve;n d&aacute;ng vẻ kh&oacute;c b&ugrave; lu b&ugrave; loa l&uacute;c xem phim khi n&atilde;y.<br />\r\n<br />\r\nCửa ph&ograve;ng, L&acirc;m Đ&agrave;m Đ&agrave;m tạm biệt người nh&agrave;: &ldquo;Ba mẹ, em &agrave;, mọi người về đi, con muốn đi ngủ.&rdquo;<br />\r\n<br />\r\nMẹ L&acirc;m rốt cuộc kh&ocirc;ng nhịn được nữa, &ograve;a kh&oacute;c: &ldquo;Đ&agrave;m Đ&agrave;m&hellip;&rdquo;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m vẫn cười, đưa tay lau nước mắt cho mẹ: &ldquo;Mẹ đừng đau buồn, cơ thể của con như vậy vốn kh&ocirc;ng sống được l&acirc;u, c&oacute; thể vui vẻ sống đến hai mươi tuổi đ&atilde; l&agrave; may mắn lắm rồi, con c&ograve;n tu luyện th&agrave;nh c&ocirc;ng, chữa trị cho rất nhiều người, cứu được rất nhiều người, đời n&agrave;y con đ&atilde; rất m&atilde;n nguyện.&rdquo;<br />\r\n<br />\r\nMẹ L&acirc;m vừa gật đầu vừa kh&ocirc;ng ngừng rơi nước mắt.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m dặn d&ograve; em trai họ L&acirc;m cũng đang lau nước mắt: &ldquo;Sau n&agrave;y nh&agrave; m&igrave;nh chỉ c&ograve;n em, em phải chăm s&oacute;c tốt cho ba mẹ đ&oacute;.&rdquo;<br />\r\n<br />\r\n&ldquo;Chị, em biết rồi!&rdquo;<br />\r\n<br />\r\n&ldquo;Ba, ba dẫn mẹ v&agrave; em đi đi, con kh&ocirc;ng muốn mọi người nh&igrave;n con rời khỏi, di thể của con sẽ quy&ecirc;n cho quốc gia, mọi người đừng quay lại nh&igrave;n.&rdquo; L&acirc;m Đ&agrave;m Đ&agrave;m n&oacute;i với L&acirc;m ba ba.<br />\r\n<br />\r\nL&acirc;m ba ba xoa đầu L&acirc;m Đ&agrave;m Đ&agrave;m, nghẹn ng&agrave;o n&oacute;i: &ldquo;Ngoan, ba biết rồi.&rdquo;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m mỉm cười về ph&ograve;ng, cửa ph&ograve;ng vừa đ&oacute;ng, ngăn c&aacute;ch tất cả tiếng động, tr&ecirc;и mặt c&ocirc; tho&aacute;ng hiện vẻ thương cảm.<br />\r\n<br />\r\nKể từ sau trận hạo kiếp mạt thế ba trăm năm về trước, cơ thể con người tr&ecirc;и cơ bản đ&atilde; được cải tạo, hiện nay dị năng giả chiếm một phần năm d&acirc;n số, đối với những người n&agrave;y, dị năng l&agrave; cơ sở sinh tồn của bọn họ.<br />\r\n<br />\r\nKhi một đứa trẻ được sinh ra, bệnh viện sẽ kiểm tra dị năng cho n&oacute;, đ&oacute; l&agrave; bước đầu ti&ecirc;n ph&acirc;n biệt người b&igrave;nh thường v&agrave; dị năng giả. Đối với dị năng giả, bệnh viện sẽ tiến h&agrave;nh th&ecirc;m một bước l&agrave; kiểm tra thuộc t&iacute;nh của năng nguy&ecirc;n hạch trong đầu đứa b&eacute;. ( Editor: năng nguy&ecirc;n hạch = hạch sinh lực, nguồn năng lượng duy tr&igrave; sự sống)<br />\r\n<br />\r\nNghe n&oacute;i thời mạt thế c&oacute; rất nhiều chủng loại dị năng, nhưng thời gian tr&ocirc;i qua những loại năng lượng nhỏ b&eacute; hoặc l&agrave; đ&atilde; ti&ecirc;u hao hết hoặc l&agrave; chuyển th&agrave;nh loại năng lượng kh&aacute;c, cuối c&ugrave;ng khi trời đất ổn định lại h&igrave;nh th&agrave;nh đầy đủ năm loại năng lượng tuần ho&agrave;n, tức l&agrave; năm hệ: kim, mộc, thủy, hỏa, thổ.<br />\r\n<br />\r\nNhững thứ n&agrave;y ứng với ngũ h&agrave;nh b&aacute;t qu&aacute;i trong trời đất tự nhi&ecirc;n g&igrave; đ&oacute; L&acirc;m Đ&agrave;m Đ&agrave;m kh&ocirc;ng hiểu, c&ocirc; chỉ biết nếu như trẻ em kiểm tra ra thuộc t&iacute;nh của năng nguy&ecirc;n hạch chỉ c&oacute; một hệ, tương lai đứa trẻ n&agrave;y nhất định sẽ c&oacute; năng lực hơn người, tr&iacute; lực si&ecirc;u ph&agrave;m, l&agrave; cường giả trời sinh.<br />\r\n<br />\r\nNếu l&agrave; hai hệ th&igrave; sẽ k&eacute;m một ch&uacute;t, ba hệ th&igrave; sống cuộc đời b&igrave;nh thường, bốn hệ tr&ecirc;и cơ bản l&agrave; t&agrave;n tật ngu si, năm hệ? &Agrave;&hellip; c&oacute; thể n&oacute;i l&agrave; sinh ra đ&atilde; ti&ecirc;u hết tất cả may mắn rồi.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m khi c&ograve;n trong bụng mẹ đ&atilde; ph&aacute;t triển chậm chạp, sinh ra th&igrave; y như một con m&egrave;o nhỏ, kh&ocirc;ng c&oacute; h&ocirc; hấp, may mắn được b&aacute;c sĩ y t&aacute; cấp cứu mới cứu được, sau đ&oacute; kiểm tra ra đủ cả năm hệ.<br />\r\n<br />\r\nBa mẹ c&ocirc; như bị s&eacute;t đ&aacute;nh nhưng bọn họ kh&ocirc;ng vứt bỏ c&ocirc;, c&ograve;n đặc biệt tạo ra rương năng lượng năm hệ cho c&ocirc;, đặt c&ocirc; nằm trong đ&oacute;.<br />\r\n<br />\r\nSau đ&oacute; kỳ t&iacute;ch xảy ra, L&acirc;m Đ&agrave;m Đ&agrave;m chẳng những kh&ocirc;ng chết m&agrave; c&ograve;n từ từ lớn l&ecirc;n, tuy bị liệt nửa người, yếu ớt nhiều bệnh nhưng đầu &oacute;c b&igrave;nh thường, thậm ch&iacute; c&ograve;n th&ocirc;ng minh hơn những đứa trẻ kh&aacute;c.<br />\r\n<br />\r\nBa mẹ L&acirc;m gia v&ocirc; c&ugrave;ng vui sướиɠ, nhưng nu&ocirc;i một đứa trẻ c&oacute; năm hệ thật sự qu&aacute; kh&oacute;, mỗi ng&agrave;y phải tốn một m&oacute;n tiền khổng lồ để duy tr&igrave; năng lượng thạch (năng lượng thạch= đ&aacute; năng lượng), coi như nh&agrave; gi&agrave;u cũng bị &eacute;p th&agrave;nh ngh&egrave;o t&uacute;ng.<br />\r\n<br />\r\nL&uacute;c đ&oacute;, &acirc;n sư của L&acirc;m Đ&agrave;m Đ&agrave;m xuất hiện, b&agrave; l&agrave; một dị năng giả mộc hệ rất mạnh, l&agrave; b&aacute;c sĩ điều dưỡng nổi tiếng, b&agrave; nghe chuyện về L&acirc;m Đ&agrave;m Đ&agrave;m, d&ugrave;ng dị năng gi&uacute;p c&ocirc; điều dưỡng cơ thể, trợ gi&uacute;p c&ocirc; chi ph&iacute; cho năng lượng thạch, c&ograve;n dạy c&ocirc; tu tuyện dị năng như thế n&agrave;o.<br />\r\n<br />\r\nTu luyện rất kh&oacute; khăn, mệt chết đi được, cảm gi&aacute;c năm loại năng lượng đ&aacute;nh nhau trong người, rất đau đớn, c&ocirc; đ&atilde; su&yacute;t chết nhiều lần rồi.<br />\r\n<br />\r\nNhưng tu luyện sẽ khống chế được những năng lượng kh&ocirc;ng c&oacute; trật tự đ&oacute;, khiến cho cơ thể khỏe mạnh hơn, gi&uacute;p L&acirc;m Đ&agrave;m Đ&agrave;m gắng gượng sống tiếp.<br />\r\n<br />\r\nNăng nguy&ecirc;n hạch c&oacute; mấy hệ th&igrave; phải đồng thời tu luyện mấy hệ nếu kh&ocirc;ng năng lượng sẽ mất c&acirc;n bằng, dẫn đến nguy hiểm đến t&iacute;nh mạng. Thi&ecirc;n ph&uacute; tu luyện của L&acirc;m Đ&agrave;m Đ&agrave;m kh&ocirc;ng ai s&aacute;nh bằng, d&ugrave; c&oacute; tới năm hệ, tốc độ tu luyện vẫn rất nhanh. Năm 14 tuổi, mỗi loại dị năng đều đạt cấp 6, do chịu sự ảnh hưởng của &acirc;n sư, c&ocirc; c&agrave;ng am hiểu dị năng mộc hệ, đến bệnh viện của &acirc;n sư l&agrave;m b&aacute;c sĩ điều dưỡng.<br />\r\n<br />\r\nTrong 6 năm, c&ocirc; từ một b&aacute;c sĩ b&igrave;nh thường thăng cấp th&agrave;nh chuy&ecirc;n gia, vang danh cả nước, c&ograve;n lấy được hu&acirc;n chương của quốc gia, số người được c&ocirc; chữa khỏi đếm kh&ocirc;ng hết, người nh&agrave; bị c&ocirc; l&agrave;m cho ngh&egrave;o t&uacute;ng cũng nhờ c&ocirc; m&agrave; gi&agrave;u c&oacute; l&ecirc;n, nhận được đ&atilde;i ngộ của ch&iacute;nh phủ.<br />\r\n<br />\r\nHiện nay, dị năng mộc hệ của c&ocirc; đ&atilde; gần đạt đến level max, những dị năng kh&aacute;c cũng kh&ocirc;ng k&eacute;m, c&ocirc; c&oacute; thể bước v&agrave;o h&agrave;ng ngũ dị năng giả mạnh nhất cả nước nhưng th&acirc;n thể c&ocirc; lại bắt đầu suy tho&aacute;i. dien. dan. Le Quy. Don<br />\r\n<br />\r\nC&oacute; lẽ đ&acirc;y l&agrave; vận mệnh của dị năng giả nhiều hệ, d&ugrave; c&ocirc; c&oacute; cố gắng thế n&agrave;o, vượt qua bao trắc trở, đứng tr&ecirc;и đỉnh cao m&agrave; đại đa số dị năng giả kh&aacute;c đều kh&ocirc;ng đạt được nhưng vẫn kh&ocirc;ng tho&aacute;t khỏi số mệnh phải chết yểu.<br />\r\n<br />\r\nMay m&agrave; cả cuộc đời ngắn ngủi c&ocirc; đ&atilde; l&agrave;m hết khả năng của m&igrave;nh, kh&ocirc;ng thẹn với ba mẹ, kh&ocirc;ng thẹn vơi &acirc;n sư, c&agrave;ng kh&ocirc;ng thẹn với ch&iacute;nh m&igrave;nh, vậy l&agrave; đủ rồi.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m than nhẹ, nếu c&oacute; kiếp sau, c&oacute; thể đứng l&ecirc;n chạy nhảy th&igrave; tốt rồi, c&ocirc; nh&igrave;n đ&ocirc;i ch&acirc;n kh&ocirc;ng c&oacute; cảm gi&aacute;c của m&igrave;nh, lại nghĩ, nếu c&oacute; thể gặp được Diệp thần của m&igrave;nh một lần lại c&agrave;ng hay.<br />\r\n<br />\r\nM&ocirc;i c&ocirc; khẽ cong l&ecirc;n &yacute; cười nh&agrave;n nhạt, đ&ocirc;i mắt từ từ nhắm lại.<br />\r\n<br />\r\n&Aacute;nh mặt trời v&agrave; gi&oacute; nhẹ xuy&ecirc;n qua khung cửa sổ, phủ l&ecirc;n người c&ocirc;, dụng cụ trong ph&ograve;ng ph&aacute;t ra tiếng cảnh b&aacute;o t&iacute;t t&iacute;t thể hiện đối tượng theo d&otilde;i đ&atilde; kh&ocirc;ng c&ograve;n dấu hiệu của sự sống.<br />\r\n<br />\r\nC&aacute;ch một c&aacute;nh của, ba mẹ L&acirc;m, em trai v&agrave; rất nhiều nh&acirc;n vi&ecirc;n c&ocirc;ng t&aacute;c trong bệnh viện vẫn chưa rời khỏi, những người đ&atilde; từng được L&acirc;m Đ&agrave;m Đ&agrave;m chữa trị sau khi nghe tin cũng k&eacute;o tới. Bọn họ đều nghe thấy tiếng dụng cụ k&ecirc;u to, ba người nh&agrave; họ L&acirc;m g&agrave;o kh&oacute;c, những người kh&aacute;c cũng kh&ocirc;ng n&eacute;n được nước mắt.<br />\r\n<br />\r\nH&ocirc;m sau, quốc gia ph&aacute;t c&aacute;o ph&oacute;: &ldquo;Nữ sĩ L&acirc;m Đ&agrave;m Đ&agrave;m, b&aacute;c sĩ điều dưỡng đặc cấp quốc gia, ph&oacute; hội trưởng danh dự của hiệp hội dị năng giả, người cải tiến phương ph&aacute;p tu luyện nhiều hệ dị năng, người đạt hu&acirc;n chương Tử V&acirc;n, cường giả dị năng cấp 9, dị năng giả năm hệ duy nhất to&agrave;n cầu,&hellip;. Ng&agrave;y 8 th&aacute;ng 7 năm 23X1, tại bệnh viện trung ương quốc gia, tuổi trẻ mất sớm, hưởng dương 20 tuổi&hellip;&rdquo;<br />\r\n<br />\r\n&hellip;<br />\r\n<br />\r\nNg&agrave;y 1 th&aacute;ng 1 năm 20X9, 13:59, trong một ph&ograve;ng ngủ thuộc đại học Dương Thị, c&ocirc; g&aacute;i nằm tr&ecirc;и giường cả người to&aacute;t mồ h&ocirc;i, khu&ocirc;n mặt nhăn nh&oacute; đỏ bừng, h&igrave;nh như mơ thấy &aacute;c mộng, giống như đang chịu sự đau đớn, l&ecirc;n cơn co giật li&ecirc;n tục.<br />\r\n<br />\r\nĐột nhi&ecirc;n, c&ocirc; mở bừng mắt, thở dốc từng hơi từng hơi, tầm mắt mơ hồ dần dần r&otilde; r&agrave;ng hơn.<br />\r\n<br />\r\nẤy, đ&acirc;y l&agrave; đ&acirc;u? Kh&ocirc;ng phải c&ocirc; đ&atilde; chết rồi sao? Chẳng lẽ c&ocirc; sống lại?<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m Đ&agrave;m ngồi dậy, hai mắt mở to, ngoại từ v&aacute;ch tường m&agrave;u trắng miễn cưỡng tương tự như căn ph&ograve;ng quen thuộc của c&ocirc; th&igrave; những vật kh&aacute;c, c&aacute;ch bố tr&iacute; đều v&ocirc; c&ugrave;ng xa lạ, sự xa lạ n&agrave;y kh&ocirc;ng chỉ v&igrave; chưa từng thấy qua m&agrave; c&ograve;n v&igrave;&hellip;. Đ&acirc;y đều l&agrave; những thứ lỗi thời!<br />\r\n<br />\r\nC&ocirc; nhớ đến những bộ phim, s&aacute;ch vở, ảnh chụp m&igrave;nh đ&atilde; từng xem, phong c&aacute;ch n&agrave;y giống y như ph&ograve;ng ốc trước mạt thế, chỗ n&agrave;y cũng kh&ocirc;ng phải nh&agrave; cửa b&igrave;nh thường m&agrave; l&agrave; k&yacute; t&uacute;c x&aacute; tập thể.<br />\r\n<br />\r\nC&ocirc; nh&igrave;n tay m&igrave;nh, c&aacute;i n&agrave;y cũng kh&ocirc;ng phải cơ thể của m&igrave;nh, nh&eacute;o một c&aacute;i, &aacute;, đau!<br />\r\n<br />\r\nChẳng lẽ sau khi chết c&ocirc; đ&atilde; sống lại? C&ograve;n đến thế giới trước khi hạo kiếp mạt thế tr&uacute;t xuống?<br />\r\n<br />\r\nTr&aacute;i tim của L&acirc;m Đ&agrave;m Đ&agrave;m đập bang bang, m&ograve; mẫm khắp nơi, cuối c&ugrave;ng m&ograve; được c&aacute;i &ldquo;điện thoại di động&rdquo; ở dưới gầm giường, thứ n&agrave;y đối với c&ocirc; l&agrave; một m&oacute;n đồ cổ, may m&agrave; c&ocirc; kh&ocirc;ng ngu dốt, sờ n&uacute;t nguồn, ấn mở, m&agrave;n h&igrave;nh s&aacute;ng l&ecirc;n, ng&agrave;y 1 th&aacute;ng 1 năm 20X9, &ecirc; &ecirc; &ecirc;, đ&acirc;y kh&ocirc;ng phải ng&agrave;y tận thế sao?!<br />\r\n<br />\r\nĐ&ocirc;i mắt L&acirc;m Đ&agrave;m Đ&agrave;m mở to, đầu &oacute;c trống rỗng, qua một hồi, c&ocirc; mới h&eacute;t ầm l&ecirc;n: &ldquo;A a a a a!!! Diệp thần, Diệp thần, em đến đ&acirc;y!&rdquo;<br />\r\n<br />\r\nC&ocirc; kϊƈɦ động, xốc c&aacute;i mền nặng đến mức kh&ocirc;ng khoa học tr&ecirc;и người ra, định xuống giường.<br />\r\n<br />\r\nNhưng L&acirc;m Đ&agrave;m Đ&agrave;m chưa từng ngủ giường tầng một gi&acirc;y sau đ&oacute; đ&atilde; gặp phải bi kịch.<br />\r\n<br />\r\nMột tiếng &ldquo;ầm&rdquo; vang l&ecirc;n, k&yacute; t&uacute;c x&aacute; nữ ph&ograve;ng 305 ph&aacute;t ra một tiếng h&eacute;t thảm thiết v&agrave; một tiếng vang rất lớn, cả s&agrave;n nh&agrave; cũng bị chấn động.</h2>', 0, 'xuyen-den-mat-the-bao-ve-anh-chuong-1'),
(5, 4, 'Thiết Xuất Nhân Sinh - Chương 1: Xuyên qua', 'Thiết Xuất Nhân Sinh - Chương 1: Xuyên qua', '<h2>&ldquo;H&aacute;n Tử, anh n&oacute;i đi&hellip; mọi chuyện kh&ocirc;ng phải l&agrave; anh l&agrave;m đ&uacute;ng kh&ocirc;ng?&rdquo; L&acirc;m Thiền Di nước mắt gi&agrave;n giụa, s&acirc;u trong t&acirc;m c&ocirc; kh&ocirc;ng muốn thừa nhận đ&acirc;y l&agrave; sự thật.<br />\r\n<br />\r\nNhưng đ&aacute;ng tiếc, l&agrave; c&ocirc; tự lừa gạt ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nDung H&aacute;n Tử liếc nh&igrave;n L&acirc;m Thiền Di, đ&ocirc;i mắt l&eacute;o ra tia s&aacute;ng lạnh, l&atilde;nh liệt n&oacute;i:<br />\r\n<br />\r\n&ldquo;L&acirc;m Thiền Di, đến b&acirc;y giờ c&ocirc; c&ograve;n chưa nhận ra sao? Tất cả chỉ l&agrave; một m&agrave;n kịch, t&ocirc;i tiếp cận c&ocirc; chỉ v&igrave; c&ocirc; l&agrave; ch&aacute;u g&aacute;i cưng của chủ tịch tập đo&agrave;n lớn mạnh L&acirc;m Thị. Cũng ch&iacute;nh l&agrave; t&ocirc;i đ&atilde; gi&aacute;n tiếp hại chết &ocirc;ng c&ocirc;, thật ra t&ocirc;i chẳng y&ecirc;u thương g&igrave; c&ocirc; cả, t&ocirc;i gh&eacute;t nhất l&agrave; thứ tiểu thư c&ocirc; g&aacute;i hoa hoa ướt &aacute;t chỉ biết dựa v&agrave;o người kh&aacute;c như c&ocirc;, t&ocirc;i chỉ cần tất cả t&agrave;i sản của &ocirc;ng c&ocirc; th&ocirc;i. C&ocirc; đ&uacute;ng l&agrave; đứa con g&aacute;i ngu ngốc nhất m&agrave; t&ocirc;i từng thấy.&rdquo;<br />\r\n<br />\r\nNhững lời ấy như lưỡi dao đ&acirc;m v&agrave;o tr&aacute;i tim c&ocirc; g&aacute;i nhỏ.<br />\r\n<br />\r\nTừ ng&agrave;y c&ograve;n thơ b&eacute;, c&ocirc; đ&atilde; kh&ocirc;ng c&oacute; cha mẹ, l&agrave; &ocirc;ng nội thu nhận c&ocirc;, chăm s&oacute;c c&ocirc; từng li từng t&iacute;. Thế m&agrave; chỉ v&igrave; sự xuất hiện của Dung H&aacute;n Tử liền đảo lộn hết cuộc sống của c&ocirc;. &Ocirc;ng nội phản đối c&ocirc; tiến đến với hắn, nhưng c&ocirc; lại kh&ocirc;ng nghe lời &ocirc;ng, ngược lại bỏ nh&agrave; ra đi trong khi &ocirc;ng đang bệnh nặng. Cứ tưởng rằng đ&acirc;y chỉ l&agrave; tạm thời chia xa, ngay ng&agrave;y h&ocirc;m sau c&ocirc; lại nghe tin &ocirc;ng qua đời, hỏi th&igrave; mới biết hơn năm mươi phần trăm cổ phần của L&acirc;m Thị bị tập đo&agrave;n t&agrave;i ch&iacute;nh Dung Thị thu mua, &ocirc;ng c&ocirc; v&igrave; qu&aacute; sốc n&ecirc;n bị đột quỵ m&agrave; chết.<br />\r\n<br />\r\nKh&ocirc;ng, đ&acirc;y hết thảy kh&ocirc;ng phải l&agrave; sự thật, mọi chuyện kh&ocirc;ng phải như vậy&hellip; Nếu c&ocirc; kh&ocirc;ng y&ecirc;u Dung H&aacute;n Tử, &ocirc;ng nội sẽ kh&ocirc;ng chết, nếu c&ocirc; mạnh mẽ hơn tập đo&agrave;n L&acirc;m thị m&agrave; &ocirc;ng d&agrave;y c&ocirc;ng x&acirc;y dựng sẽ kh&ocirc;ng bị rơi v&agrave;o tay Dung thị. Tại sao c&ocirc; lại ngu muội như vậy&hellip; tại sao???<br />\r\n<br />\r\nL&acirc;m Thiền Di như thấy lại cảnh tượng đ&ecirc;m đ&oacute;, giọt nước mắt lăn d&agrave;i tr&ecirc;n hai g&ograve; m&aacute;.<br />\r\n<br />\r\n&ldquo;Nữ nhi, con sao vậy!... Nữ nhi&rdquo; Giọng n&oacute;i kh&agrave;n kh&agrave;n của ai đ&oacute; vang l&ecirc;n, đ&aacute;nh thức L&acirc;m Thiền Di từ trong giấc mộng d&agrave;i.<br />\r\n<br />\r\nTr&ecirc;n giường h&agrave;n băng, L&acirc;m Thiền Di mở mắt ra, trước mặt l&agrave; một nam nh&acirc;n kh&ocirc;ng hề quen biết, khung cảnh xung quanh cũng v&ocirc; c&ugrave;ng xa lạ, c&ocirc; c&ograve;n cảm thấy to&agrave;n th&acirc;n buốt gi&aacute; như ở trong hầm băng.<br />\r\n<br />\r\n&ldquo;Đ&acirc;y l&agrave; đ&acirc;u, sao ta lại ở đ&acirc;y?&rdquo; L&acirc;m Thiền Di mơ hồ hỏi.<br />\r\n<br />\r\nTrợn to cặp mắt nh&igrave;n nữ nhi, kh&ocirc;ng thể kềm n&eacute;n được x&uacute;c động, Ho&agrave;nh B&acirc;n mừng rỡ k&ecirc;u l&ecirc;n:<br />\r\n<br />\r\n&ldquo;Nữ nhi, con kh&oacute;c được, c&ograve;n n&oacute;i được...&rdquo;.<br />\r\n<br />\r\n&ldquo;Th&uacute;c th&uacute;c, ngươi l&agrave;m sao vậy, ta đương nhi&ecirc;n kh&oacute;c được n&oacute;i được, vả lại ta c&ograve;n cử động được, ngươi xem&rdquo; L&acirc;m Thiền Di n&oacute;i xong, liền cố gắng bật người dậy, thế nhưng d&ugrave; c&ocirc; cố gắng đến nhường n&agrave;o, th&acirc;n thể nhấc l&ecirc;n được một ch&uacute;t cũng trở n&ecirc;n nặng trịch rớt xuống giường băng.<br />\r\n<br />\r\nHo&agrave;nh B&acirc;n thấy nữ nhi tr&ecirc;n tr&aacute;n lấm tấm mồ h&ocirc;i, trong l&ograve;ng gấp g&aacute;p liền n&oacute;i:<br />\r\n<br />\r\n&ldquo;Đừng vội, đừng vội, để ta gi&uacute;p con&rdquo; Hắn vừa n&oacute;i vừa gi&uacute;p đỡ n&agrave;ng n&acirc;ng người dậy dựa lưng v&agrave;o tường &ldquo;Con vừa mới tỉnh, kh&iacute; lực c&ograve;n chưa tốt, đừng tự h&agrave;nh hạ bản th&acirc;n, n&ecirc;n nghỉ ngơi cho tốt để mau hồi phục &hellip; B&acirc;y giờ ta đi nấu canh g&agrave; cho con tẩm bổ, tiện thể n&oacute;i với mẫu th&acirc;n con chuyện vui n&agrave;y.&rdquo;<br />\r\n<br />\r\nL&acirc;m Thiền Di như rơi v&agrave;o m&ecirc; cung thiếc, người n&agrave;y c&oacute; &yacute; g&igrave;, những điều hắn n&oacute;i n&agrave;ng kh&ocirc;ng hiểu lắm, mẹ của n&agrave;ng mất l&acirc;u rồi m&agrave;.<br />\r\n<br />\r\n&ldquo;Chuyện vui g&igrave; chứ?&rdquo; L&acirc;m Thiền Di ho&agrave;i nghi hỏi.<br />\r\n<br />\r\n&ldquo;L&agrave; chuyện n&agrave;y đ&acirc;y&rdquo; Ho&agrave;nh B&acirc;n cười n&oacute;i.<br />\r\n<br />\r\nL&acirc;m Thiền Di vẫn kh&ocirc;ng hiểu người n&agrave;y đang đề cập đến chuyện g&igrave;, đ&uacute;ng l&agrave; kỳ qu&aacute;i. N&agrave;ng ki&ecirc;n nhẫn hỏi lại lần nữa:<br />\r\n<br />\r\n&ldquo;Chuyện n&agrave;y l&agrave; chuyện g&igrave;?&rdquo;.<br />\r\n<br />\r\n&ldquo;L&agrave; chuyện ch&uacute;ng ta đ&atilde; th&agrave;nh c&ocirc;ng sinh được một đứa b&eacute; v&ocirc; c&ugrave;ng ngộ nghĩnh dễ thương l&agrave; con&rdquo; Ho&agrave;nh B&acirc;n hiện rất vui vẻ, tr&ecirc;n mặt mang theo &yacute; cười thao thao bất tuyệt n&oacute;i.<br />\r\n<br />\r\n&ldquo;N&agrave;y, chậm đ&atilde;&hellip;&rdquo; tựa hồ c&oacute; chuyện muốn hỏi tiếp, nhưng lời chưa thốt ra khỏi miệng L&acirc;m Thiền Di đ&atilde; chẳng thấy vị th&uacute;c th&uacute;c kia đ&acirc;u, hắn giống như gi&oacute; thổi qua kh&ocirc;ng thấy b&oacute;ng h&igrave;nh.<br />\r\n<br />\r\nN&agrave;ng v&ocirc; c&ugrave;ng rối bời, rốt cục n&agrave;ng đang ở chỗ qu&aacute;i quỷ n&agrave;o, v&igrave; sao c&aacute;i g&igrave; cũng lạ lẫm như vậy. Cả c&aacute;i giường băng lạnh thấu xương n&agrave;y nữa, hiện tại t&igrave;nh cảnh của n&agrave;ng tựa c&aacute; mắc cạn vậy.<br />\r\n<br />\r\nMột chốc sau, Ho&agrave;nh B&acirc;n cũng trở lại, hắn tới c&ugrave;ng một nữ nh&acirc;n, n&agrave;ng ta tr&ecirc;n tay cầm một b&aacute;t canh g&agrave; to, m&ugrave;i thơm dạt d&agrave;o. Nhưng điều L&acirc;m Thiền Di ch&uacute; &yacute; kh&ocirc;ng phải l&agrave; b&aacute;t canh m&agrave; l&agrave; dung mạo của nữ nh&acirc;n n&agrave;y.<br />\r\n<br />\r\nN&agrave;ng vận một bộ y phục đỏ c&oacute; th&ecirc;u h&igrave;nh hoa mẫu đơn v&ocirc; c&ugrave;ng rực rỡ, lại th&ecirc;m khu&ocirc;n mặt g&oacute;c cạnh, m&aacute;i t&oacute;c đen d&agrave;i tới g&oacute;t ch&acirc;n được cột giữa, đ&ocirc;i m&ocirc;i chum ch&iacute;m c&aacute;nh hoa đ&agrave;o c&agrave;ng tăng th&ecirc;m vẻ kiều mị thướt tha nhưng lại kh&ocirc;ng thiếu phần lạnh l&ugrave;ng cao ngạo, dung mạo của n&agrave;ng c&oacute; thể n&oacute;i l&agrave; hơn cả thi&ecirc;n ti&ecirc;n, ng&agrave;n năm vạn năm cũng kh&ocirc;ng c&oacute; kẻ thứ hai so s&aacute;nh được.<br />\r\n<br />\r\nTh&uacute;c th&uacute;c ban n&atilde;y tuy kh&ocirc;ng đẹp bằng n&agrave;ng nhưng cũng l&agrave; nhất phẩm nam tử, người người ngưỡm mộ, hắn một th&acirc;n bạch trường b&agrave;o, l&agrave;n da hơi ngăm, t&oacute;c đen b&uacute;i cao, cơ thể rắn chắc, kh&iacute; thế đế vương kh&ocirc;ng thể lung lay. Nhưng l&agrave; &hellip; đ&acirc;y kh&ocirc;ng phải l&agrave; bộ mặt thật của hắn.<br />\r\n<br />\r\nL&acirc;m Thiền Di nh&igrave;n hai người n&agrave;y, đ&uacute;ng l&agrave; tuyệt sắc mĩ nh&acirc;n, đỉnh phong nam tử.<br />\r\n<br />\r\nCho d&ugrave; xinh đẹp th&igrave; sao, l&ograve;ng người kh&oacute; đo&aacute;n, n&agrave;ng kh&ocirc;ng thể bị sắc đẹp của bọn họ m&ecirc; hoặc, phải cẩn thận d&ograve; x&eacute;t.<br />\r\n<br />\r\n&ldquo;Hai người l&agrave; ai? Nơi n&agrave;y l&agrave; nơi n&agrave;o? Ban n&atilde;y hắn gọi ta l&agrave; nữ nhi, vậy ta thật sự l&agrave; con của hai người sao?&rdquo; L&acirc;m Thiền Di một lượt n&oacute;i ra những c&acirc;u hỏi trong đầu m&igrave;nh, lại cảm thấy thiếu thiếu g&igrave; đ&oacute;, n&agrave;ng bổ sung &ldquo;C&ograve;n nữa, ngươi sao lại c&oacute; thể biến mất nhanh như vậy? Phải trả lời r&otilde; r&agrave;ng hết cho ta, kh&ocirc;ng được thiếu s&oacute;t&rdquo; Dường như qua một lần đại nạn đ&atilde; l&agrave;m cho n&agrave;ng trưởng th&agrave;nh, l&agrave;m g&igrave; cũng phải động n&atilde;o suy x&eacute;t cẩn thận.<br />\r\n<br />\r\nHo&agrave;nh B&acirc;n nghe nữ nhi đề cập đến: &quot;ban n&atilde;y hắn&quot; liền biết l&agrave; n&agrave;ng đề cập đến m&igrave;nh nhưng m&agrave; c&oacute; cần hỏi một l&egrave;o như vậy kh&ocirc;ng, tr&iacute; nhớ của hắn thật sự k&eacute;m lắm nha.<br />\r\n<br />\r\nThi&ecirc;n Hồng đứng b&ecirc;n cạnh Ho&agrave;nh B&acirc;n cũng nghe hiểu những g&igrave; L&acirc;m Thiền Di n&oacute;i, giọng n&oacute;i như tiếng suối r&oacute;c r&aacute;ch vang l&ecirc;n:<br />\r\n<br />\r\n&ldquo;Ta t&ecirc;n Thi&ecirc;n Hồng, c&ograve;n hắn l&agrave; Ho&agrave;nh B&acirc;n, đ&acirc;y l&agrave; nơi ch&uacute;ng ta ẩn cư thuộc thần giới, ngươi thật sự l&agrave; con g&aacute;i của bọn ta nhưng kh&ocirc;ng phải do ta sinh ra m&agrave; l&agrave; tạo ra. Ban n&atilde;y hắn biến mất nhanh như vậy l&agrave; bởi v&igrave; ch&uacute;ng ta c&oacute; thần lực, người b&igrave;nh thường kh&ocirc;ng thể bắt kịp được&rdquo;.</h2>', 0, 'thiet-xuat-nhan-sinh-chuong-1-xuyen-qua'),
(6, 5, 'Vọng Giang Nam - Chương 1', 'Vọng Giang Nam - Chương 1', '<h2>Quyển 1 &ndash; Chương 1-1: Tiết tử</h2>\r\n\r\n<h2>Lũng T&acirc;y[1]&nbsp;l&agrave; v&ugrave;ng bi&ecirc;n th&ugrave;y ph&iacute;a T&acirc;y Bắc, tiếp gi&aacute;p T&acirc;y Nhung, từ thuở sơ khai nh&agrave; H&aacute;n, nơi đ&acirc;y đ&atilde; trở th&agrave;nh v&ugrave;ng giao tranh kh&oacute;i lửa li&ecirc;n mi&ecirc;n.<br />\r\n<br />\r\nThi&ecirc;n Khải ho&agrave;ng triều, căn cứ theo tổ chế, Ho&agrave;ng Đế trực tiếp bổ nhiệm thứ sử c&aacute;c Ch&acirc;u, định kỳ c&oacute; c&aacute;c Truất tr&iacute; sứ[2]&nbsp;đến tuần tra r&agrave; so&aacute;t t&igrave;nh h&igrave;nh. C&ograve;n về h&agrave;ng Vương th&igrave; hoặc lớn hoặc nhỏ m&agrave; được phong đất tự trị, hưởng uy quyền ngang ngửa bậc Đế Vương, được thần d&acirc;n cung phụng, sử dụng th&agrave;nh quả lao động cần c&ugrave; của nh&acirc;n d&acirc;n để nu&ocirc;i dưỡng số lượng qu&acirc;n đội kinh người của m&igrave;nh.<br />\r\n<br />\r\nV&ograve;ng đi v&ograve;ng lại, đu&ocirc;i to kh&oacute; vẫy, phi&ecirc;n trấn[3] trở th&agrave;nh mối họa lớn của Thi&ecirc;n Khải.<br />\r\n<br />\r\nM&agrave; vị Tĩnh T&acirc;y vương sở tại Lương Ch&acirc;u[4] khi đ&oacute;, tay nắm binh lực h&ugrave;ng hậu, rục rịch ng&oacute;c đầu, hơn mười năm trước đ&atilde; trở th&agrave;nh miếng xương c&aacute; nghẹn ngang trong họng Ho&agrave;ng Đế m&agrave; kh&ocirc;ng biết r&uacute;t ra sao cho đặng.<br />\r\n<br />\r\n~<br />\r\n<br />\r\nGiữa biển c&aacute;t v&agrave;ng mụt m&ugrave;ng giăng ngợp trời đất, c&oacute; một con đường d&agrave;i hẹp uốn lượn quanh co, hướng về phương xa tưởng chừng như k&eacute;o d&agrave;i v&ocirc; tận.<br />\r\n<br />\r\nTr&ecirc;и đường c&oacute; độc một cỗ xe ngựa, trong xe c&oacute; một nam tử trẻ tuổi ngồi ngay ngắn chỉnh tề.<br />\r\n<br />\r\nC&oacute; lẽ v&igrave; xuất th&acirc;n trong một gia đ&igrave;nh quan lại, từ nhỏ đ&atilde; chịu sự gi&aacute;o ɖu͙ƈ h&agrave; khắc, m&agrave; d&ugrave; cỗ xe c&oacute; x&oacute;c nảy ch&ograve;ng ch&agrave;nh, y vẫn giữ một vẻ nghi&ecirc;m trang kh&ocirc;ng suy chuyển.<br />\r\n<br />\r\n&ldquo;Tam thiếu gia, c&ograve;n chừng năm mươi dặm nữa l&agrave; tới Lương Ch&acirc;u.&rdquo;<br />\r\n<br />\r\nNam tử khẽ ngẩng đầu, y c&oacute; một đ&ocirc;i mắt hoa đ&agrave;o lấp l&aacute;nh đẹp v&ocirc; ngần, kh&ocirc;ng cười tự tiếu, h&agrave;m ẩn sắc xu&acirc;n.<br />\r\n<br />\r\nCậu tiểu tư nhanh nhảu hỏi: &ldquo;Ch&uacute;ng ta t&igrave;m kh&aacute;ch điếm dừng ch&acirc;n nghỉ trước, hay trực tiếp tới b&aacute;i kiến Vương gia trước ạ?&rdquo;<br />\r\n<br />\r\nNam tử trầm ng&acirc;m trong chốc l&aacute;t, ph&acirc;n ph&oacute;: &ldquo;Qua cổng m&agrave; kh&ocirc;ng ch&agrave;o sẽ khiến người ta ch&ecirc; cười ch&uacute;ng ta kh&ocirc;ng biết lễ ph&eacute;p. Tố Huyền, ngươi để Trung th&uacute;c với xa phu tới kh&aacute;ch điểm nghỉ ch&acirc;n trước, c&ograve;n ch&uacute;ng ta tới thẳng Vương phủ.&rdquo;<br />\r\n<br />\r\n____________<br />\r\n<br />\r\n<em>1. Lũng T&acirc;y&nbsp;</em>(Lũng Hữu): t&ecirc;n cổ của tỉnh Cam T&uacute;c. Trong t&aacute;c phẩm t&aacute;c giả c&oacute; d&ugrave;ng qua lại 2 từ Lũng T&acirc;y v&agrave; Lũng Hữu. M&igrave;nh sẽ chuyển hết th&agrave;nh Lũng T&acirc;y cho đỡ g&acirc;y nhầm lẫn :3<br />\r\n<br />\r\n<em>2. Truất tr&iacute; sứ:</em>&nbsp;Ch&iacute;nh quyền trung ương (m&agrave; trong t&aacute;c phẩm l&agrave; Ho&agrave;ng Đế) ph&aacute;i quan vi&ecirc;n đi d&ograve; x&eacute;t t&igrave;nh h&igrave;nh trong khu vực th&igrave; gọi l&agrave; Truất tr&iacute; sứ. Chức quan n&agrave;y c&oacute; quyền hạn kh&aacute; lớn, c&oacute; thể trực tiếp xử tr&iacute; những quan vi&ecirc;n phạm ph&aacute;p, phạm h&uacute;y trong khu vực gi&aacute;m s&aacute;t, c&oacute; thể b&atilde;i quan hoặc thậm ch&iacute; l&agrave; bỏ t&ugrave; người đ&oacute; m&agrave; kh&ocirc;ng cần b&aacute;o c&aacute;o trước.<br />\r\n<br />\r\n<em>3. Phi&ecirc;n trấn:</em>&nbsp;v&ugrave;ng đất bi&ecirc;n giới, đặt trấn thủ bởi một tướng lĩnh c&oacute; nhiều quyền h&agrave;nh, phụ thuộc lỏng lẻo v&agrave;o Triều đ&igrave;nh. M&agrave; trong t&aacute;c phẩm l&agrave; Lũng T&acirc;y.<br />\r\n<br />\r\n<em>4. Lương Ch&acirc;u:</em>&nbsp;T&ecirc;n c&ocirc;̉, tức qu&acirc;̣n Vũ Uy trực thu&ocirc;̣c tỉnh Cam Túc hiện thời. Lương Ch&acirc;u với Lũng T&acirc;y cũng như c&aacute;c quận với thủ đ&ocirc; đ&oacute;:3</h2>', 0, 'vong-giang-nam-chuong-1'),
(7, 5, 'Vọng Giang Nam - Chương 2', 'Vọng Giang Nam - Chương 2', '<h2>Quyển 1 &ndash; Chương 1-2: Lần đầu gặp gỡ</h2>\r\n\r\n<h2>Phủ Tĩnh T&acirc;y Vương kh&ocirc;ng gi&ocirc;́ng những gì Chu Kỳ đã tưởng tượng, hay phải nói là r&acirc;́t khác mới phải.<br />\r\n<br />\r\nChu Kỳ chắp tay sau lưng, thản nhi&ecirc;n đánh giá Vương cung trước mắt.<br />\r\n<br />\r\nTường cao ngói xám, mái cong th&uacute; đ&aacute;, bắt mắt m&agrave; kh&ocirc;ng rườm rà. Từng vi&ecirc;n gạch h&agrave;ng ng&oacute;i đều đầy ắp hơi thở lạnh l&ugrave;ng cứng rắn, ngay cả đ&aacute;m hạ nh&acirc;n đi lại chuyện tr&ograve; cũng mang vẻ rập khu&ocirc;n cứng nhắc tựa như sĩ binh trước khi l&acirc;m trận.<br />\r\n<br />\r\n&ldquo;Chu đại nh&acirc;n.&rdquo;<br />\r\n<br />\r\nChu Kỳ quay đầu, thấy một vi&ecirc;n tướng qu&acirc;n trẻ tuổi đi ra, mũi cao mắt sắc, l&agrave;n da ngăm đen, y khom m&igrave;nh ch&agrave;o hỏi.<br />\r\n<br />\r\nNgười tới tuy lời lẽ kh&aacute;ch s&aacute;o, song vẻ mặt giọng điệu lại lồ lộ &yacute; khinh thường, &ldquo;Vương gia đợi đại nh&acirc;n l&acirc;u rồi đấy, Chu đại nh&acirc;n, mời.&rdquo; &ndash; dứt lời cũng chẳng th&egrave;m đợi Chu Kỳ đi theo, g&atilde; đ&atilde; quay ngoắt người đi thẳng v&agrave;o trong.<br />\r\n<br />\r\nChu Kỳ cau m&agrave;y, dằn xuống sự kh&oacute; chịu chực nhen nh&uacute;m, lẳng lặng đi theo g&atilde;.<br />\r\n<br />\r\nNh&agrave; binh vốn đi lại mau lẹ, ban đầu Chu Kỳ rảo nhanh c&ograve;n đuổi kịp, nhưng c&agrave;ng về sau c&agrave;ng phải ra sức chạy theo. Tuy kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, nhưng Chu Kỳ cũng lo&aacute;ng tho&aacute;ng nhận ra đối phương chẳng c&oacute; thiện &yacute;. L&ograve;ng kh&ocirc;ng khỏi cười khẩy, y dứt kho&aacute;t bước chậm lại, đủng đỉnh vừa đi vừa ngắm phong cảnh chung quanh.<br />\r\n<br />\r\nQua d&atilde;y h&agrave;nh lang thẳng tắp l&agrave; tới một mảnh s&acirc;n cực lớn, ch&iacute;nh giữa l&agrave; đại điện h&ugrave;ng vĩ, mặt quay hướng Bắc lưng tựa phương Nam, hai b&ecirc;n sườn đ&ocirc;ng t&acirc;y l&agrave; điện kề nhỏ hơn một ch&uacute;t, cấu tr&uacute;c tương đồng.<br />\r\n<br />\r\nTừ trong đại điện thấp tho&aacute;ng c&oacute; tiếng người vọng ra, Chu Kỳ đo&aacute;n c&oacute; lẽ l&agrave; Tĩnh T&acirc;y Vương. Chẳng qua, n&atilde;y đ&atilde; lạc mất người dẫn đường, giờ kh&ocirc;ng ai dẫn v&agrave;o diện kiến, nếu cứ t&ugrave;y tiện x&ocirc;ng v&agrave;o th&igrave; lỗ m&atilde;ng qu&aacute;, m&agrave; kh&ocirc;ng v&agrave;o, để Vương gia đợi l&acirc;u cũng thất lễ như nhau.<br />\r\n<br />\r\nChu Kỳ khoanh tay đăm chi&ecirc;u tại chỗ, bỗng mỉm cười.<br />\r\n<br />\r\nY theo đường cũ lộn trở ra s&acirc;n ngo&agrave;i, tiện tay k&eacute;o lại một tỳ nữ, thầm th&igrave; dặn d&ograve; mấy c&acirc;u, nữ tỳ lễ ph&eacute;p dạ v&acirc;ng đi l&agrave;m.<br />\r\n<br />\r\nChỉ chốc l&aacute;t sau, đ&atilde; thấy một vi&ecirc;n quản sự d&aacute;ng vẻ oai nghi&ecirc;m vội v&agrave;ng bước ra.<br />\r\n<br />\r\n&ldquo;Thi&ecirc;n tướng trẻ người non dạ, suy nghĩ chưa thấu đ&aacute;o, vả lại ng&agrave;y n&agrave;o cũng loanh quanh với đ&aacute;m m&agrave;y r&acirc;u th&ocirc; kệch, đ&acirc;u c&oacute; biết Chu đại nh&acirc;n lại liễu yếu thế n&agrave;y, d&ugrave; g&igrave; cũng l&agrave; người đọc s&aacute;ch m&agrave;.&rdquo; &ndash; sau một hồi giả lả v&aacute;i ch&agrave;o, quản sự mới cười cợt dẫn đường, &ldquo;Vương gia đang sốt ruột chờ ng&agrave;i ở Vũ Đức điện đấy, đại nh&acirc;n, xin mời.&rdquo;<br />\r\n<br />\r\nChu Kỳ cũng chẳng th&egrave;m để &yacute; c&aacute;i sự ch&ecirc; trong khen của l&atilde;o, chỉ b&acirc;ng quơ hỏi: &ldquo;Xin hỏi qu&yacute; t&iacute;nh đại nh&acirc;n?&rdquo;<br />\r\n<br />\r\nQuản sự vẻ mặt h&ograve;a nh&atilde; đ&aacute;p: &ldquo;Chỉ l&agrave; hạng n&ocirc; bộc th&ocirc;i, đ&acirc;u được cho l&agrave; đại nh&acirc;n g&igrave; chứ. Chu đại nh&acirc;n kh&ocirc;ng ch&ecirc; cứ gọi ta l&agrave; Trịnh tổng quản như người trong phủ l&agrave; được.&rdquo;<br />\r\n<br />\r\nChu Kỳ gật đầu, chậm r&atilde;i đi theo.<br />\r\n<br />\r\nKề c&agrave; một th&ocirc;i một hồi, hai người v&agrave;o đến nơi cũng ng&oacute;t ngh&eacute;t một n&eacute;n nhang.<br />\r\n<br />\r\nChu Kỳ bước l&ecirc;n bậc, từ từ tiến lại gần vị tr&iacute; trang nghi&ecirc;m nhất giữa Vương cung, kh&ocirc;ng gian vắng lặng kh&ocirc;ng một tiếng người, c&oacute; chăng chỉ l&agrave; những sĩ binh lu&acirc;n phi&ecirc;n c&ocirc;ng t&aacute;c. &Aacute;nh s&aacute;ng từ binh kh&iacute; nh&aacute;ng l&ecirc;n những khu&ocirc;n mặt trẻ trung nhưng lạnh ngắt, tr&ocirc;ng thật ma qu&aacute;i, l&agrave;m một người c&oacute; t&acirc;m l&yacute; vững v&agrave;ng như Chu Kỳ cũng sinh cảm gi&aacute;c sợ sệt, tuy rằng chỉ tho&aacute;ng qua trong t&iacute;ch tắc.<br />\r\n<br />\r\nChu Kỳ từ tốn quỳ xuống, sống lưng thẳng tắp: &ldquo;Hạ quan Chu Kỳ b&aacute;i kiến Vương gia.&rdquo;<br />\r\n<br />\r\nĐợi m&atilde;i vẫn kh&ocirc;ng thấy tiếng đ&aacute;p lời, chẳng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c, Chu Kỳ đ&agrave;nh phải tiếp tục quỳ tr&ecirc;и nền gạch đ&aacute; lạnh lẽo, những lời lẽ đồn thổi về vị Vương gia n&agrave;y trong Kinh th&agrave;nh cũng lần lượt hiện l&ecirc;n. Th&ocirc; bạo, t&agrave;n nhẫn, hiếu chiến, bất trung&hellip;<br />\r\n<br />\r\nNgười được khen tất nhi&ecirc;n kh&ocirc;ng dễ, nhưng c&oacute; thể khiến cả thi&ecirc;n hạ để mắt tới, chẳng phải cũng l&agrave; một thứ bản lĩnh đ&oacute; sao?<br />\r\n<br />\r\nL&ograve;ng Chu Kỳ vui vẻ mắng nhiếc, kh&oacute;e miệng khẽ cong l&ecirc;n.<br />\r\n<br />\r\n&ldquo;C&oacute; vẻ như cả qu&atilde;ng đường b&ocirc;n ba bụi c&aacute;t cũng kh&ocirc;ng ảnh hưởng tới t&acirc;m trạng của Chu đại nh&acirc;n nhỉ.&rdquo;<br />\r\n<br />\r\nKh&ocirc;ng r&otilde; c&oacute; phải được t&ocirc;i luyện trường kỳ giữa b&atilde;o c&aacute;t t&aacute;i ngoại hay kh&ocirc;ng m&agrave; giọng Vương gia nghe kh&agrave;n kh&agrave;n đặc sệt, lại k&eacute;o d&agrave;i giọng mũi khiến những lời lẽ to&aacute;t ra c&agrave;ng th&ecirc;m v&agrave;i phần lười nh&aacute;c mỉa mai.<br />\r\n<br />\r\nChu Kỳ cung kính trả lời: &ldquo;Cho dù đường sá xa x&ocirc;i m&ecirc;̣t nhọc, nhưng chỉ c&acirc;̀n nghĩ tới vi&ecirc;̣c có th&ecirc;̉ được tr&ocirc;ng th&acirc;́y phong thái Vương gia, hạ quan lại h&aacute;o hức ng&oacute;ng tr&ocirc;ng, nào còn cảm th&acirc;́y m&ecirc;̣t nhọc.&rdquo;<br />\r\n<br />\r\nPhía tr&ecirc;и loạt xoạt ti&ecirc;́ng l&acirc;̣t gi&acirc;́y, tựa h&ocirc;̀ có ti&ecirc;́ng Vương gia cười khẽ: &ldquo;Nga, l&acirc;̀n này Lại b&ocirc;̣[1]&nbsp;chi&ecirc;́u c&ocirc;́ Bản vương quá nhỉ. Cử hẳn c&ocirc;ng tử Chu gia xu&acirc;́t th&acirc;n d&ograve;ng d&otilde;i C&ocirc;ng Khanh[2]&nbsp;lặn lội từ tận Giang Nam xa x&ocirc;i tới phụ tá cho Bản vương, mà lục sự[3]&nbsp;b&acirc;́t quá cũng chỉ là chức quan bát ph&acirc;̉m, chẳng lẽ Chu c&ocirc;ng tử kh&ocirc;ng th&acirc;́y đại tài ti&ecirc;̉u dụng sao?&rdquo;<br />\r\n<br />\r\nTrước khi tới đ&acirc;y, Chu Kỳ đã giả thi&ecirc;́t g&atilde; sẽ đ&ecirc;̀ c&acirc;̣p tới v&acirc;́n đề này, tự nhi&ecirc;n giờ cũng dự tr&ugrave; c&acirc;u trả lời từ sẵn, kh&ocirc;ng mặn kh&ocirc;ng nhạt đáp: &ldquo;H&ocirc;̀i Vương gia, s&ocirc;́ng ở đời có ai sinh ra đã thăng quan ti&ecirc;́n chức, sao ngài bi&ecirc;́t trước khi gia t&ocirc;̉ đứng hàng C&ocirc;ng Khanh chưa từng đảm đương chức vụ m&ocirc;̣t ti&ecirc;̉u Lại cỏn con? Hu&ocirc;́ng chi, nam nhi đứng trong trời đ&acirc;́t cũng kh&ocirc;ng th&ecirc;̉ ỷ vào bóng mát của t&ocirc;̉ t&ocirc;ng mà ng&ocirc;̀i mát ăn bát vàng, phải kh&ocirc;ng ạ?&rdquo;<br />\r\n<br />\r\nChẳng chờ Tĩnh t&acirc;y vương kịp trả lời, Chu Kỳ thay đ&ocirc;̉i kh&acirc;̉u khí d&otilde;ng dạc ngay: &ldquo;Mặc dù hạ quan su&acirc;́t th&acirc;n sĩ t&ocirc;̣c nhưng ki&ecirc;́n thức n&ocirc;ng cạn, Vương gia cũng bi&ecirc;́t, trong hàng trăm thí sinh đứng hàng Tam giáp[4], Chu Kỳ đứng thứ chót, n&ecirc;́u kh&ocirc;ng phải do xu&acirc;́t th&acirc;n sĩ t&ocirc;̣c nào có được cơ h&ocirc;̣i vì Thi&ecirc;n Khải tới Lương Ch&acirc;u, d&ocirc;́c sức vì Vương gia? Mong Vương gia kh&ocirc;ng ch&ecirc; Chu Kỳ tư ch&acirc;́t t&acirc;̀m thường, cho phép Chu Kỳ được đ&acirc;̀u qu&acirc;n dưới trướng Vương gia ki&ecirc;́n c&ocirc;ng l&acirc;̣p nghi&ecirc;̣p, dĩ hi&ecirc;̣u khuy&ecirc;̉n mã!&rdquo;<br />\r\n<br />\r\nKh&acirc;̉u khí Tĩnh t&acirc;y vương có vài ph&acirc;̀n nghi&ecirc;̀n ngẫm: &ldquo;Kiến c&ocirc;ng lập nghiệp? Lẽ n&agrave;o lại đơn giản như nhập ba ng&agrave;n chiếc &aacute;o l&ocirc;ng chồn v&agrave;o Bắc cương như những Ho&agrave;ng tử Ho&agrave;ng t&ocirc;n c&aacute;c ngươi tưởng.&rdquo; &ndash; tựu chừng như rốt cuộc cũng nhớ rằng Chu kỳ c&ograve;n đang quỳ, g&atilde; như ban ơn: &ldquo;Đứng l&ecirc;n đi.&rdquo;<br />\r\n<br />\r\nChu Kỳ đứng dậy, kh&ocirc;ng ki&ecirc;u ngạo kh&ocirc;ng siểm nịnh ngẩng đầu nh&igrave;n, l&ograve;ng chợt cả kinh.<br />\r\n<br />\r\nNam tử tọa ph&iacute;a tr&ecirc;и kia anh tuấn tuấn l&atilde;ng v&ocirc; c&ugrave;ng, một th&acirc;n y b&agrave;o đen tuyền, ngay cả viền vạt &aacute;o cũng c&ugrave;ng một m&agrave;u đen kịt. Nhưng điều ấy c&ograve;n chưa đủ kinh ngạc, điều khiến Chu Kỳ phải tr&ograve;n mắt sững sờ chỉ c&oacute; hai. Thứ nhất, vị Tĩnh vương gia n&agrave;y mặc d&ugrave; l&agrave; đường đệ của th&aacute;nh thượng nhưng ni&ecirc;n kỷ lại r&otilde; trẻ trung, thứ hai l&agrave; ban n&atilde;y v&igrave; quỳ tr&ecirc;и mặt đất n&ecirc;n y chưa ch&uacute; &yacute;, hiện giờ mới linh mẫn nhận ra cả cung điện bắt đầu bốc l&ecirc;n thứ m&ugrave;i rỉ sắt thoảng qua, nguy&ecirc;n l&agrave; khi vấn thoại vừa rồi, c&oacute; một vị ph&oacute; tướng chưa kịp băng b&oacute; vết thương cho g&atilde;, vậy m&agrave; vị Tĩnh vương gia vẫn c&oacute; thể r&agrave;nh rọt từng chữ, kh&iacute; tức đều đều như thường.<br />\r\n<br />\r\nDường như ch&uacute; &yacute; tới vẻ ngạc nhi&ecirc;n của y, Tĩnh t&acirc;y vương nhếch kh&oacute;e m&ocirc;i: &ldquo;Chu lục sự c&oacute; g&igrave; chỉ gi&aacute;o?&rdquo;<br />\r\n<br />\r\n&ldquo;Xưa c&oacute; Quan C&ocirc;ng cạo xương đọc Xu&acirc;n Thu, Chu Kỳ vẫn thường thở d&agrave;i than thở v&igrave; ng&agrave;y nay kh&ocirc;ng c&ograve;n thấy bao trang v&otilde; th&aacute;nh kiệt xuất t&aacute;i thế nữa, nay gặp được Vương gia mới biết, ra tr&ecirc;и đời vẫn c&ograve;n tồn tại một anh h&ugrave;ng ch&acirc;n ch&iacute;nh.&rdquo;<br />\r\n<br />\r\nDọc sống lưng đột nhi&ecirc;n lạnh to&aacute;t, cảm nhận r&otilde; rệt &aacute;nh mắt đầy &aacute;p b&aacute;ch đang chụp l&ecirc;n m&igrave;nh, Chu Kỳ ngẩng đầu, ngh&ecirc;nh đ&oacute;n đ&ocirc;i mắt đen kịt kia, kh&ocirc;ng tr&aacute;nh, kh&ocirc;ng l&ugrave;i.<br />\r\n<br />\r\nChốc l&aacute;t sau, Vương gia bật cười: &ldquo;Thế nh&acirc;n vẫn n&oacute;i sĩ tử Trung Nguy&ecirc;n mồm miệng lanh lợi, quả kh&ocirc;ng sai. Trịnh Trung, thu thập một tiểu viện, sạch sẽ y&ecirc;n tĩnh một ch&uacute;t, đừng chậm trễ Chu lục sự.&rdquo;<br />\r\n<br />\r\nChu Kỳ khom người cảm tạ, sau ngẩng đầu ly khai.<br />\r\n<br />\r\n&ldquo;Vương gia.&rdquo; &ndash; Trịnh tổng quản mở miệng gọi, sắc mặt u &aacute;m.<br />\r\n<br />\r\n&ldquo;L&agrave;m sao?&rdquo; &ndash; Tĩnh t&acirc;y vương đứng dậy, qu&eacute;t mắt nh&igrave;n vết thương vừa được băng b&oacute;.<br />\r\n<br />\r\nTrịnh tổng quản c&acirc;n nhắc c&acirc;u từ: &ldquo;Chu lục sự kia, tuổi trẻ xuất th&acirc;n thế gia lại khinh cuồng, đối với Vương gia cũng thiếu k&iacute;nh nể tối thiểu, e rằng mai sau kh&oacute; khống chế.&rdquo;<br />\r\n<br />\r\nTĩnh t&acirc;y vương cười nhạt: &ldquo;Khống chế? Ngươi nghĩ Chu gia đưa y tới đ&acirc;y để Bản vương khống chế ư?&rdquo; &ndash; g&atilde; trầm tư một l&uacute;c, &ndash; &ldquo;Bắc cương lạnh lẽo khắc nghiệt, nu&ocirc;i một g&atilde; qu&yacute; c&ocirc;ng tử cẩm y ngọc thực th&igrave; c&oacute; &iacute;ch lợi g&igrave;, c&ograve;n l&atilde;ng ph&iacute; lương thảo nu&ocirc;i kh&ocirc;ng y ư? Xem ra, t&igrave;nh h&igrave;nh trong Kinh th&agrave;nh c&ograve;n phức tạp hơn Bản vương tưởng nhiều. Ph&acirc;n ph&oacute; xuống dưới, ph&aacute;i người canh giữ Chu Kỳ, đừng để y c&oacute; cơ hội tiếp x&uacute;c đến sự vụ cơ mật.&rdquo;<br />\r\n<br />\r\nTrịnh tổng quản khom lưng đ&aacute;p, nh&igrave;n theo vạt &aacute;o đen tuyền biến mất s&acirc;u b&ecirc;n trong cung thất.<br />\r\n<br />\r\n_____________<br />\r\n<br />\r\n<em>1. Lại bộ hay bộ Lại: l&agrave; t&ecirc;n gọi của một cơ quan h&agrave;nh ch&iacute;nh thời phong kiến tại c&aacute;c nước Đ&ocirc;ng &Aacute;, chủ quản c&aacute;c c&ocirc;ng việc của quan văn như bổ nhiệm, b&atilde;i miễn, khảo kh&oacute;a, thăng gi&aacute;ng, hu&acirc;n phong, điều động.</em><br />\r\n<br />\r\n<em>2. C&ocirc;ng Khanh: Vi&ecirc;́t tắt của tam c&ocirc;ng cửu khanh &ndash; thu&acirc;̣t ngữ chung chỉ m&ocirc;̣t s&ocirc;́ đại nh&acirc;n v&acirc;̣t có quy&ecirc;̀n lực lớn (thường chỉ 3, 4 người) trong tri&ecirc;̀u.</em><br />\r\n<br />\r\n<em>3. Lục sự: C&ocirc;ng vi&ecirc;̣c ghi chép, sao lục.</em><br />\r\n<br />\r\n<em>4. Tam Giáp: Thời đại khoa cử, trong khoa đ&igrave;nh th&iacute;, chia những người đậu l&agrave;m ba bực, gọi l&agrave; tam gi&aacute;p</em><em>: Trạng Nguy&ecirc;n, Bảng Nhãn, Thám Hoa là nhất gi&aacute;p, Ho&agrave;ng Gi&aacute;p l&agrave; đệ nhị gi&aacute;p, Tiến Sĩ l&agrave; đệ tam gi&aacute;p.</em></h2>', 0, 'vong-giang-nam-chuong-2');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(8, 6, 'Hoa Nắng - Chương 1', 'Hoa Nắng - Chương 1', '<h2>Chương 1</h2>\r\n\r\n<h2>Tr&ecirc;и đại sảnh của 1 t&ograve;a cao ốc lớn (thật ra l&agrave; cực kỳ lớn), một người đ&agrave;n &ocirc;ng khoảng 35t, mặc một bộ vest đen, khu&ocirc;n mặt chữ điền, ngũ quan h&agrave;i h&ograve;a c&oacute; n&eacute;t hiền hậu, thế nhưng giữa 2 h&agrave;ng l&ocirc;ng m&agrave;y lại lộ ra kh&iacute; chất cương nghị khiến người kh&aacute;c kh&ocirc;ng thể xem nhẹ. Những khu vực &ocirc;ng đi qua đều c&oacute; người cuối đầu ch&agrave;o hỏi, tr&ecirc;и mặt mỗi người đều c&oacute; thần sắc t&ocirc;n trọng v&agrave; một ch&uacute;t sợ h&atilde;i. &Ocirc;ng từng bước từng bước tiến dần đến cửa thang m&aacute;y&hellip;<br />\r\n<br />\r\n&ldquo;10&hellip; &hellip;2&hellip;1..ting&rdquo;<br />\r\n<br />\r\nCửa thang m&aacute;y mở ra, những nh&acirc;n vi&ecirc;n b&ecirc;n trong thấy &ocirc;ng liền lo lắng, kh&ocirc;ng khỏi n&eacute;p v&agrave;o 2 b&ecirc;n, chừa 1 khoảng rộng giữa thang m&aacute;y, c&ograve;n 1 số người vội bước ra khỏi thang m&aacute;y, trước khi đi cũng kh&ocirc;ng qu&ecirc;n c&uacute;i ch&agrave;o &ocirc;ng.<br />\r\n<br />\r\nThu tất cả những h&agrave;nh động n&agrave;y v&agrave;o mắt, người đ&agrave;n &ocirc;ng cũng chỉ c&oacute; thể bất đắc dĩ thở d&agrave;i..: Aizz, quả thật c&aacute;i chức tổng gi&aacute;m đốc n&agrave;y của hắn thật rắc rối a~ Ng&agrave;y ng&agrave;y đều phải đối mặt với th&aacute;i độ k&iacute;nh sợ quả thật nhiều năm rồi cũng c&oacute; ch&uacute;t kh&ocirc;ng quen. Chỉ l&agrave;&hellip;<br />\r\n<br />\r\n&ldquo;1&hellip;2&hellip;3&hellip; &hellip;116&hellip;117..ting&rdquo; mất khoảng 3 ph&uacute;t, &ocirc;ng bước ra khỏi thang m&aacute;y, tất nhi&ecirc;n giờ ph&uacute;t n&agrave;y th&igrave; kh&ocirc;ng c&ograve;n 1 b&oacute;ng d&aacute;ng ai trong thang m&aacute;y nữa, bởi v&igrave; tầng cao nhất cũng ch&iacute;nh l&agrave; nơi chỉ c&oacute; v&agrave;i người c&oacute; thể bước v&agrave;o, gồm Tổng gi&aacute;m đốc, Chủ tịch v&agrave; người nh&agrave; của Chủ tịch<br />\r\n<br />\r\n<strong><em>Ch&uacute; th&iacute;ch:</em></strong><em>&nbsp;(Goldin Finance 117, Thi&ecirc;n T&acirc;n, Trung Quốc. Chiều cao: 1.957ft. Ho&agrave;n th&agrave;nh: 2016. T&ograve;a nh&agrave; c&oacute; 117 tầng. Sau khi ho&agrave;n th&agrave;nh đ&acirc;y sẽ l&agrave; t&ograve;a nh&agrave; cao thứ 5 thế giới trong tương lai. &ndash; Ta sẽ lấy h&igrave;nh mẫu n&agrave;y cho trụ sở tập đo&agrave;n Thi&ecirc;n Lam của Băng tỷ X3 khửa khửa)</em><br />\r\n<br />\r\n&Ocirc;ng bước đến trước cửa một văn ph&ograve;ng trang trọng, trực tiếp đẩy cửa v&agrave;o m&agrave; kh&ocirc;ng hề g&otilde; cửa hay b&aacute;o trước. Thật ra d&ugrave; &ocirc;ng c&oacute; g&otilde; cửa th&igrave; &ldquo;người n&agrave;o đ&oacute;&rdquo; cũng sẽ kh&ocirc;ng nghe thấy đ&acirc;u~<br />\r\n<br />\r\nBước v&agrave;o ph&ograve;ng, đập v&agrave;o mắt &ocirc;ng ch&iacute;nh l&agrave; khung cảnh 1 thiếu nữ mi mục như họa đang ngồi tr&ecirc;и ghế chủ tịch, lấy trăng l&agrave;m thần, lấy ngọc l&agrave;m cốt, lấy tuyết l&agrave;m da, m&aacute;i t&oacute;c m&agrave;u bạch kim xinh đẹp bu&ocirc;ng x&otilde;a, đ&ocirc;i mắt đang nhắm nghiền, hai h&agrave;ng l&ocirc;ng m&agrave;y mỏng cong cong đen như mực vẽ, l&ocirc;ng mi d&agrave;i như c&aacute;nh bướm nh&egrave; nhẹ rung động, d&ugrave; đang nhắm mắt nhưng cũng c&oacute; thể tưởng tượng khi mở ra, đ&ocirc;i mắt ấy sẽ lấp l&aacute;nh, rực rỡ đến nhường n&agrave;o, c&aacute;i mũi cao nho nhỏ, dưới mũi l&agrave; đ&ocirc;i m&ocirc;i đỏ mọng như c&aacute;nh hoa anh đ&agrave;o mọng nước chọc người muốn cắn. n&agrave;ng mặc một chiếc &aacute;o sơmi trắng cổ trụ kết hợp với ch&acirc;n v&aacute;y x&ograve;e đen tr&ecirc;и gối, phong c&aacute;ch n&agrave;y c&oacute; ch&uacute;t đơn giản nhưng khi n&agrave;ng mặc n&oacute; lại to&aacute;t l&ecirc;n vẻ thanh lịch, hồn nhi&ecirc;n, ẩn ẩn trong đ&oacute; c&ograve;n to&aacute;t l&ecirc;n 1 ch&uacute;t quyến rũ&hellip; Thật ra th&igrave; b&igrave;nh thường đ&acirc;y nhất định l&agrave; cảnh đẹp &yacute; vui, thế nhưng c&oacute; một việc, kh&ocirc;ng biết do v&ocirc; t&igrave;nh hay cố &yacute; m&agrave; nh&acirc;n vật ch&iacute;nh của bức tranh b&acirc;y giờ ch&iacute;nh l&agrave; bộ d&aacute;ng tay dựa l&ecirc;n th&agrave;nh ghế chống cằm, ʍ&ocirc;иɠ ngồi tr&ecirc;и ghế, 2 ch&acirc;n th&igrave; hồn nhi&ecirc;n g&aacute;c l&ecirc;n b&agrave;n chủ tịch, bộ dạng rất chi l&agrave; thoải m&aacute;i Người đ&agrave;n &ocirc;ng ban n&atilde;y ch&iacute;nh l&agrave; chảy xuống 3 vạch hắc tuyến, bất khả tư nghị (kh&ocirc;ng c&oacute; g&igrave; để n&oacute;i &ndash; miễn b&igrave;nh luận a~) nh&igrave;n 1 m&agrave;n n&agrave;y&hellip;<br />\r\n<br />\r\n&ldquo;Khụ&hellip;&rdquo; &ndash; ho khan 1 tiếng, &ocirc;ng lặng lẽ đứng chờ &ldquo;người n&agrave;o đ&oacute;&rdquo; tỉnh dậy.<br />\r\n<br />\r\nNghe tiếng ho, thiếu nữ lẳng lặng mở mắt. tất nhi&ecirc;n kh&ocirc;ng phụ l&ograve;ng mọi người, mở ra l&agrave; một đ&ocirc;i tử đồng tuyệt đẹp, trong suốt như nước hồ m&ugrave;a thu, s&aacute;ng ngời như tinh t&uacute;, c&oacute; thể thấy được chủ nh&acirc;n đ&ocirc;i mắt n&agrave;y l&agrave; người th&ocirc;ng minh, sắc b&eacute;n đến nhường n&agrave;o&hellip;<br />\r\n<br />\r\nD&ugrave;ng 2s để ho&agrave;n to&agrave;n tỉnh t&aacute;o, thiếu nữ thấy hiện giờ tư thế của m&igrave;nh c&oacute; vẻ thất lễ, nhưng&hellip; mặc kệ đi, d&ugrave; sao nơi n&agrave;y chỉ c&oacute; mỗi m&igrave;nh n&agrave;ng v&agrave; một số người kh&aacute;c, m&agrave; những người đ&oacute; đ&atilde; qu&aacute; quen thuộc với n&agrave;ng đến mức n&agrave;ng c&oacute; thể tự do thể hiện t&iacute;nh c&aacute;ch của m&igrave;nh trước mặt họ th&igrave; cần g&igrave; lễ tiết với thục nữ g&igrave; ở đ&acirc;y nữa chứ ??. (Min: Ngượng ng&ugrave;ng-_-|| nữ ch&iacute;nh bị ta l&agrave;m mất gi&aacute; rồi). N&agrave;ng liền nhẹ nh&agrave;ng đặt ch&acirc;n xuống đất, đứng dậy, xoay người liền thấy người đ&agrave;n &ocirc;ng đang đứng b&ecirc;n cạnh kh&ocirc;ng khỏi giật m&igrave;nh một ch&uacute;t:<br />\r\n<br />\r\n&ldquo;A~ Lạc th&uacute;c, th&uacute;c đến rồi a! Sao th&uacute;c lại đứng đ&oacute;, mau ngồi mau ngồi a~&rdquo; &ndash; Giọng n&oacute;i trong trẻo, tuyệt đẹp như tiếng chim h&oacute;t cất l&ecirc;n, mang theo ch&uacute;t th&acirc;n thiết.<br />\r\n<br />\r\n&ldquo;Tiểu thư, người t&igrave;m ta c&oacute; vi&ecirc;c?&rdquo; &ndash; đ&uacute;ng! người đ&agrave;n &ocirc;ng được gọi l&agrave; Lạc th&uacute;c vừa gọi thiếu nữ khoảng 15t n&agrave;y l&agrave; tiểu thư, hơn nữa c&ograve;n l&agrave; th&aacute;i độ t&ocirc;n k&iacute;nh.<br />\r\n<br />\r\nThật dễ nhận ra, thiếu nữ thanh lệ tho&aacute;t tục n&agrave;y ch&iacute;nh l&agrave; c&ocirc; tiểu thư Thi&ecirc;n Băng &ndash; con g&aacute;i cưng của &ldquo;&ocirc;ng tr&ugrave;m&rdquo; tập đo&agrave;n Thi&ecirc;n Lam. &Ocirc;ng nghĩ: Chỉ l&agrave; người thật sự đ&aacute;ng k&iacute;nh sợ của cả tập đo&agrave;n n&agrave;y ch&iacute;nh l&agrave; vị tiểu thư xinh đẹp trước mắt &ocirc;ng đ&acirc;y&hellip;<br />\r\n<br />\r\nM&agrave; tất nhi&ecirc;n giờ n&agrave;y &ldquo;&ocirc;ng tr&ugrave;m&rdquo; Thi&ecirc;n H&agrave;n đ&atilde; dắt tay &rdquo; b&agrave; tr&ugrave;m phu nh&acirc;n&rdquo; Lam Ngọc đi đ&acirc;u mất rồi.<br />\r\n<br />\r\n&ldquo;Lạc th&uacute;c! th&uacute;c đừng gọi con l&agrave; tiểu thư n&agrave;y tiểu thư nọ nữa a~ th&uacute;c đ&atilde; gi&uacute;p bố con n&acirc;ng đỡ tập đo&agrave;n n&agrave;y bao nhi&ecirc;u năm rồi, th&uacute;c b&acirc;y giờ vốn đ&atilde; ch&iacute;nh l&agrave; người một nh&agrave;, sao cứ kh&aacute;ch kh&iacute; m&atilde;i như vậy chứ a?&rdquo; &ndash; Thi&ecirc;n Băng giọng điệu thập phần đ&aacute;ng y&ecirc;u, lại pha ch&uacute;t l&agrave;m nũng khiến Lạc th&uacute;c c&oacute; ch&uacute;t ấm &aacute;p lại c&oacute; ch&uacute;t buồn cười: C&ocirc; ch&aacute;u nhỏ của hắn a~, t&iacute;nh t&igrave;nh của n&agrave;ng nếu muốn thấy được cảnh n&agrave;y quả thật v&ocirc; c&ugrave;ng kh&oacute;, n&agrave;ng thường ng&agrave;y vốn lạnh nhạt kh&ocirc;ng quan t&acirc;m, nhưng khi b&ecirc;n cạnh người n&agrave;ng tin tưởng th&igrave; sẽ lộ ra t&iacute;nh c&aacute;ch thật sự của m&igrave;nh&hellip; nghĩ tới đ&acirc;y, Lạc th&uacute;c kh&ocirc;ng khỏi cảm thấy 1 d&ograve;ng nước ấm chảy qua tim hắn.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng n&oacute;i đến vấn đề n&agrave;y nữa!&rdquo; &ndash; giọng điệu Thi&ecirc;n Băng bỗng chốc thay đổi, ch&iacute;nh l&agrave; trở n&ecirc;n nghi&ecirc;m t&uacute;c &ndash; &lsquo;Lạc th&uacute;c, hiện tại con c&oacute; ch&uacute;t chuyện muốn n&oacute;i với người&hellip; Tuần sau, con sẽ tạm thời nghỉ ph&eacute;p, bao l&acirc;u th&igrave; con kh&ocirc;ng n&oacute;i trước được, ch&iacute;nh l&agrave; con b&aacute;o với th&uacute;c trước, tập đo&agrave;n khoảng thời gian n&agrave;y con nhờ th&uacute;c phải quản l&yacute; to&agrave;n bộ rồi!&rdquo;<br />\r\n<br />\r\nLạc th&uacute;c c&oacute; ch&uacute;t bất ngờ với lời th&ocirc;ng b&aacute;o của n&agrave;ng, nhưng rất nhanh liền b&igrave;nh ổn trở lại: tiểu thư n&agrave;ng từ nhỏ đ&atilde; v&ugrave;i đầu v&agrave;o đống s&aacute;ch vở, sau đ&oacute; lại đi&ecirc;n cuồng luyện tập v&otilde; thuật (Min: thật ra th&uacute;c ấy sai rồi, n&agrave;ng chỉ nhấc tay m&agrave; th&ocirc;i a~), bỏ qua nhiều thời gian v&ocirc; tư của đứa trẻ như vậy, giờ lại phải g&aacute;nh v&aacute;c c&ocirc;ng việc nặng nề của một tập đo&agrave;n lớn gần 1 năm trời th&igrave; nghỉ ph&eacute;p để n&agrave;ng thư gi&atilde;n l&agrave; 1 &yacute; kiến kh&ocirc;ng tồi. &ndash; &ldquo;Được, con cứ từ từ tận hưởng thời gian nghỉ của m&igrave;nh m&agrave; thư giản, mấy năm qua khổ cực con nhiều rồi. Con cứ y&ecirc;n t&acirc;m, ta sẽ quản l&yacute; tốt tập đo&agrave;n của Thi&ecirc;n thị (tập đo&agrave;n của nh&agrave; họ Thi&ecirc;n &aacute;)&rdquo;.<br />\r\n<br />\r\nTại s&acirc;n bay quốc tế Royal<br />\r\n<br />\r\nMột b&oacute;ng d&aacute;ng nhỏ nhắn xinh đẹp đang đứng trong đại sảnh s&acirc;n bay tr&ocirc;ng v&ocirc; c&ugrave;ng n&oacute;ng ruột, thiếu nữ cứ ng&oacute;ng tới ng&oacute;ng lui, nh&igrave;n tr&aacute;i nh&igrave;n phải như t&igrave;m k&iacute;m g&igrave; đ&oacute;, m&agrave; thiếu nữ n&agrave;y đang thu h&uacute;t &aacute;nh mắt của kh&ocirc;ng biết bao nhi&ecirc;u ch&agrave;ng trai, những c&ocirc; g&aacute;i xung quanh d&ugrave; c&oacute; ghen tỵ như thế n&agrave;o th&igrave; cũng kh&ocirc;ng nhịn được m&agrave; quay lại nh&igrave;n th&ecirc;m v&agrave;i lần. Nh&igrave;n kĩ, thiếu nữ quả thật v&ocirc; c&ugrave;ng xinh đẹp, m&aacute;i t&oacute;c đen &oacute;ng mượt hơi uốn ở phần đu&ocirc;i được buộc cao bằng một sợi ruy băng m&agrave;u đen, l&agrave;n da trắng n&otilde;n mịn m&agrave;ng như da em b&eacute;, đ&ocirc;i mắt m&agrave;u t&iacute;m trong trẻo, long lanh, c&aacute;i mũi nho nhỏ, đ&ocirc;i m&ocirc;i anh đ&agrave;o đỏ mọng, đ&aacute;ng người như ẩn như hiện dưới chiếc v&aacute;y liền m&agrave;u xanh lam, quả thật ch&iacute;nh l&agrave; mĩ nữ kh&oacute; gặp nha~<br />\r\n<br />\r\nThiếu nữ nh&igrave;n về một ph&iacute;a, bất ngờ cười tươi khiến c&aacute;c ch&agrave;ng xung quanh xịt m&aacute;u mũi (Min: hắc hắc, c&aacute;c n&agrave;ng đo&aacute;n được mĩ nữ n&agrave;y l&agrave; ai h&ocirc;n X3), n&agrave;ng bổ nh&agrave;o về ph&iacute;a một b&oacute;ng d&aacute;ng thon d&agrave;i, xinh đẹp kh&aacute;c, h&ocirc; to: &ldquo;Chị!!&rdquo;<br />\r\n<br />\r\nNghe lời của thiếu nữ n&oacute;i, v&ocirc; số &aacute;nh mắt liền bắn về ph&iacute;a b&oacute;ng d&aacute;ng kia, kh&ocirc;ng nh&igrave;n th&igrave; th&ocirc;i, vừa nh&igrave;n liền trực tiếp ngất xỉu v&igrave; mất m&aacute;u qu&aacute; nhiều: B&oacute;ng d&aacute;ng thon thả, nhẹ nh&agrave;ng, uyển chuyển (Min: cho ta bổ sung, Băng Băng nh&agrave; ta v&ograve;ng n&agrave;o ra v&ograve;ng nấy nha, cần lồi c&oacute; lồi, cần l&otilde;m liền l&otilde;m, l&agrave; d&aacute;ng người mẫu hẳn, m&aacute;i t&oacute;c bạch kim t&ugrave;y &yacute; bu&ocirc;ng x&otilde;a, đ&ocirc;i mắt m&agrave;u t&iacute;m xinh đẹp ẩn sau chiếc k&iacute;nh m&aacute;t m&agrave;u đen, c&aacute;i m&ocirc;i đỏ mọng khẽ nhếch, l&agrave;n da bạch ngọc giống như trong suốt, chiếc v&aacute;y c&uacute;p trắng tinh khiết d&agrave;i ngang gối, d&ugrave; bị chiếc k&iacute;nh r&acirc;m che gần nữa khu&ocirc;n mặt nhưng ai cũng c&oacute; thể nh&igrave;n ra người thiếu nữ v&aacute;y trắng n&agrave;y c&ograve;n xinh đẹp hơn thiếu nữ v&aacute;y xanh v&agrave;i phần (Min: tất nhi&ecirc;n a~ n.vật ch&iacute;nh của ta m&agrave; lị)<br />\r\n<br />\r\nThiếu nữ v&aacute;y trắng bị thiếu nữ v&aacute;y xanh nh&agrave;o v&agrave;o &ocirc;m m&igrave;nh th&igrave; kh&ocirc;ng khỏi hắc tuyến: con nh&oacute;c n&agrave;y c&oacute; cần th&aacute;i h&oacute;a như vậy kh&ocirc;ng a? &ndash; &ldquo;Tiểu H&acirc;n, mau bu&ocirc;ng chị ra a~&rdquo;- n&agrave;ng nhẹ nh&agrave;ng n&oacute;i. Thiếu nữ v&aacute;y xanh được gọi l&agrave; tiểu H&acirc;n đang ch&igrave;m trong vui sướиɠ v&igrave; l&acirc;u ng&agrave;y mới gặp được c&ocirc; chị họ m&agrave; m&igrave;nh thần tượng nghe vậy liền kh&ocirc;ng khỏi giật m&igrave;nh, vội t&aacute;ch người ra: Aaaaa~ quả thật n&agrave;ng v&ocirc; c&ugrave;ng kϊƈɦ động nha, chị của n&agrave;ng thực sự đ&atilde; đến rồi nha</h2>', 0, 'hoa-nang-chuong-1'),
(9, 7, 'Kiều Nữ Lâm Gia - Chương 1', 'Kiều Nữ Lâm Gia - Chương 1', '<h2>Mặt trời vừa l&ecirc;n, &aacute;nh s&aacute;ng nhẹ nh&agrave;ng dần dần lộ ra từ ph&iacute;a đ&ocirc;ng, dịu d&agrave;ng soi rọi nh&acirc;n gian, chiếu s&aacute;ng giọt sương l&oacute;ng l&aacute;nh tr&ecirc;n đầu c&agrave;nh, h&ograve;a với tiếng chim h&oacute;t vang trong rừng, tạo n&ecirc;n cảnh tượng v&ocirc; c&ugrave;ng h&agrave;i h&ograve;a.<br />\r\n<br />\r\nĐến giờ Mẹo, người trong L&acirc;m phủ đều lục tục dậy, nữ đầu bếp nấu cơm, n&ocirc; bộc qu&eacute;t dọn đ&igrave;nh viện, bọn thị nữ hoặc nu&ocirc;i chim tước, hoặc ủi quần &aacute;o, ai c&oacute; chức nấy, ai l&agrave;m chuyện đ&oacute;.<br />\r\n<br />\r\nKh&oacute;i bếp từ ph&ograve;ng bếp lượn lờ bay l&ecirc;n, h&ograve;a với &aacute;nh mặt trời y&ecirc;n tĩnh trang nh&atilde;, l&agrave;m cho người ta cảm thấy t&acirc;m b&igrave;nh kh&iacute; h&ograve;a, t&acirc;m thần sảng kho&aacute;i.<br />\r\n<br />\r\nCả L&acirc;m phủ giống như trẻ n&iacute;t thức tỉnh sau cả đ&ecirc;m ngủ say, khắp nơi đều hiện ra sức sống tốt đẹp, sự phấn chấn v&agrave; hăng h&aacute;i.<br />\r\n<br />\r\nTr&ecirc;n con đường m&ograve;n s&aacute;ng b&oacute;ng do đ&aacute; vụn trải l&ecirc;n, c&oacute; một con ngỗng trắng mập mạp đang đi ngh&ecirc;nh ngang, bộ l&ocirc;ng tuyết trắng, miệng v&agrave; l&ograve;ng b&agrave;n ch&acirc;n lại giống như hồng ngọc, rất l&agrave; đ&aacute;ng y&ecirc;u. N&oacute; nghễnh cao đầu, vẻ mặt ngạo mạn, bước đi thong dong, vừa đi, vừa ph&aacute;t ra tiếng k&ecirc;u qu&aacute;t qu&aacute;t, &acirc;m điệu trịnh trọng.<br />\r\n<br />\r\nB&ecirc;n cạnh ngỗng trắng l&agrave; một tiểu c&ocirc; nương khoảng ba bốn tuổi, tr&ecirc;n đầu c&oacute; hai b&iacute;m t&oacute;c nhỏ, gương mặt tươi cười, ng&acirc;y thơ v&agrave; suиɠ sướиɠ.<br />\r\n<br />\r\nTiểu c&ocirc; nương rất hiền ho&agrave;, ngỗng trắng th&igrave; c&oacute; d&aacute;ng điệu ng&ocirc;ng ngh&ecirc;nh, một b&eacute; g&aacute;i, v&agrave; một con ngỗng trắng lớn, thật l&agrave; th&uacute; vị.<br />\r\n<br />\r\n&quot;Nhị tiểu thư, nhị tiểu thư chậm một ch&uacute;t!&quot; Sau lưng tiểu c&ocirc; nương vang l&ecirc;n &acirc;m thanh năn nỉ, hai nha đầu đi như chạy, sắc mặt gấp g&aacute;p.<br />\r\n<br />\r\nTiểu c&ocirc; nương cười h&igrave; h&igrave; quay đầu lại, kho&aacute;t kho&aacute;t tay với hai n&agrave;ng ấy, &quot;Đại Bạch kh&ocirc;ng th&iacute;ch hai người, đừng tới đ&acirc;y.&quot;<br />\r\n<br />\r\n&Acirc;m thanh non nớt, mềm mại dịu d&agrave;ng, rất dễ nghe, rất &ecirc;m tai, nhưng hai nha đầu nghe b&eacute; n&oacute;i như vậy, lại thật kh&ocirc;ng d&aacute;m đuổi theo b&eacute;, chỉ d&aacute;m đi theo sau b&eacute; xa xa.<br />\r\n<br />\r\nPh&iacute;a trước l&agrave; một hồ nước gợn dập dềnh, tr&ecirc;n hồ c&oacute; c&acirc;y cầu cong bằng đ&aacute;, b&ecirc;n hồ c&oacute; tảng đ&aacute; xanh, lan can cẩm thạch, sạch sẽ nhẹ nh&agrave;ng khoan kho&aacute;i.<br />\r\n<br />\r\ndiễn-đ&agrave;n-(L&ecirc;)-Qu&yacute; -Đ&ocirc;n<br />\r\n<br />\r\nTr&ecirc;n cầu c&oacute; một người đi tới, t&oacute;c quấn khăn xanh, mặc &aacute;o d&agrave;i m&agrave;u xanh nhạt, xa xa nh&igrave;n thật giống một thiếu ni&ecirc;n lang đẹp trai. Nếu đến gần nh&igrave;n kỹ, sẽ ph&aacute;t hiện m&agrave;u da của n&agrave;ng v&ocirc; c&ugrave;ng trắng n&otilde;n nhẵn nhụi, ngũ quan v&ocirc; c&ugrave;ng tinh xảo mỹ lệ, thanh nh&atilde; tuấn dật, d&aacute;ng người cao cao, dung mạo phong độ, thật l&agrave; thiếu nữ tuyệt sắc.<br />\r\n<br />\r\n&quot;A Thấm, ch&agrave;o buổi s&aacute;ng!&quot; Người nọ cười d&agrave;i, mặt m&agrave;y hớn hở, &quot;Đại Bạch, cũng ch&agrave;o buổi s&aacute;ng!&quot;<br />\r\n<br />\r\nNgỗng trắng ngạo mạn kh&ocirc;ng ai b&igrave; nổi dừng bước lại nghi&ecirc;m nghị k&ecirc;u hai tiếng, giống như đang ch&agrave;o hỏi với người nọ, L&acirc;m Thấm lại xoay đầu nhỏ d&ograve; x&eacute;t n&agrave;ng cẩn thận, một l&aacute;t sau, lại lộ ra vẻ mặt như người lớn, c&ograve;n tr&aacute;ch: &quot;Tỷ tỷ lại bướng bỉnh.&quot;<br />\r\n<br />\r\nTrong đ&ocirc;i mắt xinh đẹp của L&acirc;m Đ&agrave;m chớp qua vẻ kinh ngạc, ngồi xổm người xuống, c&oacute; ch&uacute;t hăng h&aacute;i hỏi tiểu muội muội của m&igrave;nh, &quot;A Thấm, tỷ tỷ v&agrave; đại ca l&agrave; long phượng thai, d&aacute;ng dấp giống nhau, quần &aacute;o ăn mặc cũng giống đại ca như đ&uacute;c, muội l&agrave;m sao nh&igrave;n ra được? A Thấm rất lợi hại, thật tinh mắt.&quot;<br />\r\n<br />\r\nMặt L&acirc;m Thấm lộ vẻ đắc &yacute;, chỉ chỉ mặt của A Đ&agrave;m, &quot;Tỷ tỷ cười giống như m&ugrave;a xu&acirc;n, đại ca th&igrave; cười giống như......&quot; B&eacute; nghi&ecirc;m t&uacute;c suy nghĩ một l&aacute;t, hơi do dự n&oacute;i: &quot;Đại ca, th&igrave; cười giống như m&ugrave;a thu.....&quot; Giọng n&oacute;i v&ocirc; c&ugrave;ng kh&ocirc;ng x&aacute;c định.<br />\r\n<br />\r\nMặt m&agrave;y L&acirc;m Đ&agrave;m cong cong, &quot;Tỷ tỷ cười thật ấm &aacute;p, đại ca th&igrave; hơi lạnh l&ugrave;ng, c&oacute; đ&uacute;ng hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;T&oacute;m lại kh&ocirc;ng giống nhau.&quot; L&acirc;m Thấm l&agrave;m c&aacute;i mặt quỷ với tỷ tỷ.<br />\r\n<br />\r\n&quot;A Thấm nh&agrave; ta thật c&oacute; học vấn, c&aacute;i từ &lsquo;t&oacute;m lại&rsquo; n&agrave;y d&ugrave;ng cực đ&uacute;ng, cực kỳ s&aacute;t!&quot; L&acirc;m Đ&agrave;m đưa ra ng&oacute;n tay c&aacute;i, khen ngợi muội muội.<br />\r\n<br />\r\nL&acirc;m Thấm hả h&ecirc; ngẩng đầu nhỏ l&ecirc;n.<br />\r\n<br />\r\nL&uacute;c n&agrave;y vẻ mặt tư th&aacute;i của L&acirc;m Thấm v&agrave; Đại Bạch b&ecirc;n người b&eacute; rất c&oacute; v&agrave;i phần tương tự, đảo mắt nh&igrave;n quanh mọi nơi.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m cưng ch&igrave;u cười, đưa tay sờ sờ đầu nhỏ của muội muội, &quot;Sao h&ocirc;m nay A Thấm dậy sớm vậy? C&oacute; ngủ đủ kh&ocirc;ng?&quot; L&acirc;m Thấm cười h&igrave; h&igrave;, &quot;Ngủ đủ. Nương bảo h&ocirc;m nay c&oacute; th&acirc;n th&iacute;ch tới, muội chơi với Đại Bạch trước, chờ th&acirc;n th&iacute;ch tới, liền tới chơi với th&acirc;n th&iacute;ch.&quot; L&acirc;m Đ&agrave;m mỉm cười, &quot;Tiểu A Thấm c&aacute;i n&agrave;y nh&agrave; ta c&ograve;n chưa đến bốn tuổi, lại suy nghĩ chu đ&aacute;o qu&aacute;.&quot; L&acirc;m Thấm vui vẻ, &quot;Chứ sao.&quot;<br />\r\n<br />\r\nMặt trời mọc l&ecirc;n từ ph&iacute;a đ&ocirc;ng, tỏa s&aacute;ng khắp nơi, chiếu v&agrave;o khu&ocirc;n mặt nhỏ b&eacute; trắng như tuyết của L&acirc;m Thấm c&agrave;ng ph&aacute;t ra &oacute;ng &aacute;nh trong suốt.<br />\r\n<br />\r\ndiễn.đ&agrave;n-(L&ecirc;)-Qu&yacute;.Đ&ocirc;n<br />\r\n<br />\r\nHai nha đầu vẫn lu&ocirc;n đi xa xa đằng sau giống như xem đến cứu tinh, vội v&agrave;ng tới, &quot;Xin thỉnh an đại tiểu thư, thỉnh an nhị tiểu thư.&quot; L&acirc;m Đ&agrave;m đứng l&ecirc;n, khẽ cau m&agrave;y, &quot;Nhan Tại, Ứng Cai, sao hai ngươi lại c&aacute;ch xa nhị tiểu thư vậy? Ph&iacute;a trước ch&iacute;nh l&agrave; hồ nước, Đại Bạch th&iacute;ch bơi, nhị tiểu thư c&ograve;n nhỏ kh&ocirc;ng hiểu chuyện, nếu đi theo xuống nước, th&igrave; sao đ&acirc;y?&quot; N&oacute;i đến sau, liền nh&igrave;n hồ nước c&aacute;ch đ&oacute; kh&ocirc;ng xa một chốc, giọng n&oacute;i dần dần nghi&ecirc;m nghị.<br />\r\n<br />\r\nHai nha đầu vội quỳ xuống, nha đầu t&ecirc;n Nhan Tại hết sức giải th&iacute;ch, &quot;Nhị tiểu thư n&oacute;i Đại Bạch kh&ocirc;ng th&iacute;ch hai ta, kh&ocirc;ng cho hai ta &aacute;p s&aacute;t qu&aacute; gần, n&ocirc; tỳ kh&ocirc;ng d&aacute;m tr&aacute;i &yacute; nhị tiểu thư.&quot; Nha đầu t&ecirc;n Ứng Cai lại ngoan ngo&atilde;n c&uacute;i đầu, &quot;Tạ đại tiểu thư dạy dỗ, n&ocirc; tỳ biết sai, l&aacute;t nữa liền đến chỗ Tần ma ma nhận đ&ograve;n.&quot;<br />\r\n<br />\r\nNgỗng trắng thấy hai nha đầu tới đ&acirc;y, liền k&ecirc;u g&agrave;o qu&aacute;t qu&aacute;t, duỗi d&agrave;i cổ, muốn cắn ch&acirc;n Nhan Tại, Nhan Tại bị sợ đến sắc mặt trắng bệch, run run trốn về sau, &quot;Ngươi đừng cắn ta, ngươi đừng cắn ta....&quot; L&acirc;m Thấm mất hứng, khu&ocirc;n mặt nhỏ nhắn nhăn lại, khiển tr&aacute;ch, &quot;Đại Bạch, ngươi đừng cắn người t&ugrave;m lum!&quot; C&ograve;n tức giận đ&aacute;nh ngỗng trắng.<br />\r\n<br />\r\nNgỗng trắng lại rất kh&ocirc;ng nghe lời, cố chấp muốn cắn tiếp.<br />\r\n<br />\r\n&quot;Đại Bạch sao ngươi lại thế?&quot; L&acirc;m Thấm giận đến khu&ocirc;n mặt nhỏ b&eacute; đỏ bừng, &quot;Ngươi kh&ocirc;ng nghe lời nữa, ta liền kh&ocirc;ng cho ngươi bơi, biết kh&ocirc;ng?&quot;<br />\r\n<br />\r\nKh&oacute;e miệng L&acirc;m Đ&agrave;m hơi vểnh.<br />\r\n<br />\r\nĐược đ&oacute;, muội muội ta tuy mới chỉ lớn thế n&agrave;y, lại rất biết giảng đạo l&yacute;. Đại Bạch sợ nhất l&agrave; c&aacute;i g&igrave;? Một l&agrave; kh&ocirc;ng cho n&oacute; ăn cơm, hai ch&iacute;nh l&agrave; kh&ocirc;ng cho n&oacute; bơi. Khi A Thấm khiển tr&aacute;ch Đại Bạch, c&ograve;n rất biết nắm lấy yếu điểm.<br />\r\n<br />\r\n&quot;Cũng kh&ocirc;ng cho ngươi ăn cơm, kh&ocirc;ng cho ngươi ăn b&ugrave;n, kh&ocirc;ng cho ngươi ăn cỏ!&quot; L&acirc;m Thấm giận chỉ Đại Bạch, kh&iacute; thế mạnh mẽ, l&agrave;m liền một mạch.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m xem say sưa ngon l&agrave;nh.<br />\r\n<br />\r\nMuội muội ta rất biết g&acirc;y gổ, rất biết dạy dỗ nha!<br />\r\n<br />\r\nNgỗng trắng vừa trang trọng nghi&ecirc;m t&uacute;c vừa ngạo mạn cao ngạo vừa cố chấp v&ocirc; c&ugrave;ng cư nhi&ecirc;n bị L&acirc;m Thấm dạy dỗ chịu phục, rướn cổ k&ecirc;u l&ecirc;n v&agrave;i tiếng, kh&iacute; thế oai phong h&ugrave;ng dũng bừng bừng đứng ở một b&ecirc;n, nhưng lại kh&ocirc;ng cắn người nữa.<br />\r\n<br />\r\nNhan Tại đang chảy nước mắt, run run rẩy rẩy quỳ gối, nh&igrave;n cực kỳ đ&aacute;ng thương.<br />\r\n<br />\r\nỨng Cai th&igrave; tốt hơn n&agrave;ng ta nhiều, h&igrave;nh như chưa từng bị kinh h&atilde;i h&ugrave; dọa.<br />\r\n<br />\r\ndiễn&quot;đ&agrave;n-(L&ecirc;)-Qu&yacute;.Đ&ocirc;n<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m dịu d&agrave;ng n&oacute;i: &quot;Chuyện ng&agrave;y h&ocirc;m nay, mặc d&ugrave; c&oacute; nguy&ecirc;n nh&acirc;n, nhưng hai ngươi đều c&oacute; lỗi. Nhị tiểu thư n&oacute;i Đại Bạch kh&ocirc;ng th&iacute;ch hai ngươi, kh&ocirc;ng cho hai ngươi đến gần, c&aacute;c ngươi liền n&ecirc;n nghĩ c&aacute;ch, một người lặng lẽ đi theo sau lưng nhị tiểu thư, c&oacute; khả năng gần cỡ n&agrave;o liền gần cỡ đ&oacute;; một người kh&aacute;c nhanh đi về bẩm b&aacute;o phu nh&acirc;n, phu nh&acirc;n tự c&oacute; sắp xếp. Bằng kh&ocirc;ng, phu nh&acirc;n nghĩ hai ngươi đi theo hầu hạ nhị tiểu thư, nhưng thật ra th&igrave; hai ngươi c&aacute;ch nhị tiểu thư xa t&aacute;m trượng, c&ograve;n ở m&eacute;p nước, thế sao được?&quot; Hai nha đầu Nhan Tại v&agrave; Ứng Cai kh&ocirc;ng phản đối, dập đầu nhận lầm, &quot;Dạ, n&ocirc; tỳ biết, về sau kh&ocirc;ng d&aacute;m nữa, sẽ tự m&igrave;nh đi nhận đ&ograve;n.&quot;<br />\r\n<br />\r\nL&acirc;m Thấm k&eacute;o v&aacute;y của tỷ tỷ, ngửa khu&ocirc;n mặt nhỏ nhắn l&ecirc;n, nh&igrave;n n&agrave;ng năn nỉ.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m c&uacute;i đầu thấy đ&ocirc;i mắt nhỏ của muội muội, th&igrave; biết r&otilde; b&eacute; đang cầu cạnh cho Nhan Tại v&agrave; Ứng Cao, kh&ocirc;ng khỏi nh&igrave;n hai nha đầu n&agrave;y một c&aacute;i, sắc mặt trầm ng&acirc;m.<br />\r\n<br />\r\nNhan Tại v&agrave; Ứng Cai cũng đ&atilde; mười một mười hai tuổi, hầu hạ L&acirc;m Thấm cũng gần nửa năm, t&ecirc;n của hai người c&ograve;n do L&acirc;m Thấm đặt nữa. L&acirc;m Thấm mới học n&oacute;i chuyện kh&ocirc;ng l&acirc;u, người b&aacute;n mang người đến cho La phu nh&acirc;n mẫu th&acirc;n n&agrave;ng xem, th&igrave; n&oacute;i &quot;Phu nh&acirc;n, đứa nhỏ n&agrave;y t&ecirc;n Ứng....&quot; Lời kế tiếp c&ograve;n chưa n&oacute;i xong, L&acirc;m Thấm liền đưa b&agrave;n tay nhỏ b&eacute; chỉ v&agrave;o k&ecirc;u, &quot;Ứng Cai, Ứng Cai.&quot; Ph&aacute;t &acirc;m rất r&otilde; r&agrave;ng.<br />\r\n<br />\r\nLa phu nh&acirc;n mừng rỡ, &quot;A Thấm của ta k&ecirc;u Ứng Cai, n&agrave;ng ta liền t&ecirc;n Ứng Cai.&quot;<br />\r\n<br />\r\nMua v&agrave;o, để ma ma quản sự dạy hai năm xong, bởi v&igrave; L&acirc;m Thấm gọi Ứng Cai rất thuận miệng, liền cho L&acirc;m Thấm sai bảo.<br />\r\n<br />\r\nNhan Tại l&agrave; con của gia n&ocirc;, họ Chu, l&uacute;c đ&oacute; L&acirc;m Thấm đang được phụ th&acirc;n L&acirc;m Phong bế trong l&ograve;ng, L&acirc;m Phong chỉ v&agrave;o hoa Nhạn Lai Hồng đỏ tươi trong s&acirc;n dạy cho tiểu nữ nhi, &quot;Sương hoa tẩy tẫn chu nhan tại, bất học xu&acirc;n hoa xảo lộng nghi&ecirc;n&quot;, c&ograve;n La phu nh&acirc;n đang xem x&eacute;t từng nha đầu m&agrave; quản sự đưa tới, muốn t&igrave;m ra mấy người cơ tr&iacute; lại trung th&agrave;nh.<br />\r\n<br />\r\nTai L&acirc;m Thấm thật th&iacute;nh, r&otilde; r&agrave;ng l&agrave; phụ th&acirc;n đang dạy b&eacute; đọc thơ, c&ograve;n mẫu th&acirc;n đang hỏi chuyện nha đầu kia. Nha đầu vừa l&ecirc;n tiếng trả lời &quot;N&ocirc; tỳ họ Chu....&quot; B&eacute; liền vỗ b&agrave;n tay nhỏ cười ha ha, &quot;Chu Nhan, Chu Nhan.&quot; L&acirc;m Phong dạy n&agrave;ng đọc &quot;Sương hoa tẩy tẫn chu nhan tại, bất học xu&acirc;n hoa xảo lộng nghi&ecirc;n&quot;, b&eacute; học được r&otilde; r&agrave;ng, kh&ocirc;ng sai một chữ, L&acirc;m Phong vui v&ocirc; c&ugrave;ng, &quot;Tiểu A Thấm thật th&ocirc;ng minh! A Thấm, ch&uacute;ng ta đặt t&ecirc;n cho nha đầu n&agrave;y l&agrave; Chu Nhan Tại, con n&oacute;i c&oacute; được hay kh&ocirc;ng?&quot; L&acirc;m Thấm vui vui vẻ đồng &yacute;, &quot;Được!&quot;<br />\r\n<br />\r\nL&acirc;m gia liền c&oacute; nha đầu t&ecirc;n Chu Nhan Tại.<br />\r\n<br />\r\ndiễn-đ&agrave;n/L&ecirc;/Qu&yacute;/Đ&ocirc;n<br />\r\n<br />\r\nLa phu nh&acirc;n cảm thấy t&ecirc;n Nhan Tại do L&acirc;m Thấm đặt, nha đầu n&agrave;y coi như cơ tr&iacute;, liền lệnh n&agrave;ng hầu hạ L&acirc;m Thấm. Nha đầu n&agrave;y nửa năm qua, cũng chăm chỉ.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m suy nghĩ trong l&ograve;ng, &quot;Nha đầu sơ &yacute; lơ như vậy sao được? Nhưng A Thấm nhỏ vậy, trong l&ograve;ng lại r&otilde; r&agrave;ng, chuyện quản gi&aacute;o nha đầu, hơi lớn hơn ch&uacute;t dạy muội ấy cũng kh&ocirc;ng muộn, d&ugrave; sao ta cũng phải thương lượng với nương, đổi hai người ổn thỏa đi theo A Thấm, thật cũng kh&ocirc;ng phiền c&aacute;i g&igrave;.&quot; Sau khi tự suy nghĩ, n&agrave;ng dịu d&agrave;ng n&oacute;i: &quot;A Thấm, chuyện n&agrave;y kh&ocirc;ng li&ecirc;n quan tới muội, l&agrave; hai nha đầu n&agrave;y xử sự chưa tốt. Chuyện nh&agrave; do mẫu th&acirc;n l&agrave;m chủ, tỷ tỷ sẽ bẩm b&aacute;o chuyện n&agrave;y với nương, nương n&oacute;i xử tr&iacute; như thế n&agrave;o, liền xử tr&iacute; như thế ấy, c&oacute; được hay kh&ocirc;ng?&quot;<br />\r\n<br />\r\nL&acirc;m Thấm kh&eacute;o l&eacute;o gật đầu, &quot;Ừ, muội nghe tỷ.&quot; L&acirc;m Đ&agrave;m cười cười, ph&acirc;n ph&oacute;: &quot;Nhị tiểu thư c&oacute; ta, hai người c&aacute;c ngươi trở về đổi Khổng Dương v&agrave; tiểu Uyển tới đ&acirc;y.&quot; Hai nha đầu nghe lệnh, dập đầu, lui về ph&iacute;a sau mấy bước, rồi xoay người đi.<br />\r\n<br />\r\n&quot;Tỷ tỷ theo muội, thật tốt.&quot; L&acirc;m Thấm cười h&igrave; h&igrave;.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m dắt tay nhỏ mềm mại của b&eacute;, &quot;D&ugrave; sao b&acirc;y giờ tỷ tỷ cũng rảnh.&quot; Nh&agrave;n nh&atilde; đi tới m&eacute;p nước.<br />\r\n<br />\r\nNgỗng trắng kh&ocirc;ng k&ecirc;u kh&ocirc;ng gọi, nện bước ổn thỏa đến m&eacute;p nước, rồi bơi v&agrave;o nước.<br />\r\n<br />\r\nNghểnh đầu thật cao, ch&acirc;n hồng l&ugrave;a gạt s&oacute;ng, tự tự tại tại, tự nhi&ecirc;n ti&ecirc;u dao.<br />\r\n<br />\r\n&quot;Muội thật sự th&iacute;ch Đại Bạch.&quot; L&acirc;m Thấm nh&igrave;n ngỗng trắng bơi bơi, khu&ocirc;n mặt nhỏ b&eacute; lộ vẻ thưởng thức v&agrave; y&ecirc;u th&iacute;ch.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m cười n&oacute;i: &quot;A Thấm y&ecirc;u ngỗng, đam m&ecirc; n&agrave;y, giống với Vương Hi Chi. Vương Hi Chi y&ecirc;u ngỗng, Đ&agrave;o Uy&ecirc;n Minh y&ecirc;u c&uacute;c, Chu Mậu Th&uacute;c y&ecirc;u sen, Lam H&ograve;a Tĩnh y&ecirc;u hạc, được xưng &lsquo; tứ &aacute;i &rsquo;, c&oacute; thể thể hiện sự phong nh&atilde; thanh tĩnh, kh&aacute;c hẳn trần tục của văn nh&acirc;n ẩn sĩ.&quot;<br />\r\n<br />\r\nBờ hồ c&oacute; mấy con bướm, một l&aacute;t nhẹ nh&agrave;ng bay m&uacute;a tr&ecirc;n kh&ocirc;ng trung, một l&aacute;t lại từ từ bay v&agrave;o buội hoa, đ&ocirc;i c&aacute;nh xinh đẹp tung bay, c&oacute; vẻ đẹp tinh xảo m&agrave; mảnh khảnh.<br />\r\n<br />\r\nHai mắt L&acirc;m Thấm vụt s&aacute;ng l&ecirc;n, l&ocirc;ng mi thật d&agrave;i linh động đ&aacute;ng y&ecirc;u giống như c&aacute;nh bướm, &quot;Tứ &aacute;i &agrave;.&quot; B&eacute; cười h&igrave; h&igrave;, nhưng nụ cười lại c&oacute; ch&uacute;t chột dạ.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m l&agrave; trưởng tỷ của b&eacute;, n&ecirc;n rất hiểu r&otilde; b&eacute;, thấy bộ d&aacute;ng của b&eacute;, biết b&eacute; nghe kh&ocirc;ng hiểu lắm, liền dịu d&agrave;ng tỉ mỉ kể b&eacute; nghe: &quot;Vương Hi Chi l&agrave; danh gia thư ph&aacute;p, tỷ tỷ l&agrave; đang khen muội thanh nh&atilde; tho&aacute;t tục như &ocirc;ng ấy. Nh&atilde; v&agrave; tục đối lập nhau, nh&atilde; l&agrave; rất tốt, tục lại khiến người gh&eacute;t, l&agrave;m người ta khinh thường. A Thấm, tỷ tỷ đang khen muội, khen muội kh&ocirc;ng tầm thường, rất nh&atilde;, y&ecirc;u th&iacute;ch ngỗng trắng giống như danh nh&acirc;n Vương Hi Chi.&quot;<br />\r\n<br />\r\ndiễn,đ&agrave;n,L&ecirc;,Qu&yacute;,Đ&ocirc;n<br />\r\n<br />\r\n&quot;Muội rất l&agrave; nh&atilde; đấy.&quot; L&acirc;m Thấm nghe tỷ tỷ giải th&iacute;ch, vui vẻ ra mặt.<br />\r\n<br />\r\nB&eacute; đang ở tuổi l&agrave;m người kh&aacute;c ưa th&iacute;ch, cười l&ecirc;n mặt m&agrave;y cong cong, ngọt giống như mật, rất l&agrave; đ&aacute;ng y&ecirc;u.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m cưng chiều n&oacute;i: &quot;A Thấm nh&agrave; ta dĩ nhi&ecirc;n l&agrave; nh&atilde;, mỹ danh của nhị tiểu thư L&acirc;m gia, ở An Định ai kh&ocirc;ng biết, ai kh&ocirc;ng hiểu?&quot;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m n&oacute;i mặc d&ugrave; khoa trương ch&uacute;t, nhưng phụ th&acirc;n của c&aacute;c n&agrave;ng L&acirc;m Phong l&agrave; tri ch&acirc;u An Định ch&acirc;u, l&agrave;m chủ một ch&acirc;u, rất c&oacute; uy vọng, L&acirc;m Thấm l&agrave; &aacute;i nữ của hắn, ở trong th&agrave;nh An Định n&agrave;y, người biết L&acirc;m Thấm thật đ&uacute;ng l&agrave; rất kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\nMặt nước c&oacute; một chiếc thuyền con lay động tới, thiếu ni&ecirc;n mặc quần &aacute;o xanh nhạt đứng ở đu&ocirc;i thuyền, m&agrave;u da như ngọc, d&aacute;nh vẻ thanh nh&atilde;, m&aacute;i t&oacute;c đen như mực chỉ d&ugrave;ng một c&acirc;y tr&acirc;m tr&uacute;c c&agrave;i lỏng, c&agrave;ng hiện ra vẻ phong lưu, lỗi lạc bất ph&agrave;m.<br />\r\n<br />\r\n&quot;Đại ca đến rồi!&quot; L&acirc;m Thấm hoan h&ocirc;.<br />\r\n<br />\r\nKh&oacute;e miệng L&acirc;m Đ&agrave;m lộ ra nụ cười ranh m&atilde;nh, c&uacute;i người xuống, nhỏ giọng n&oacute;i mấy c&acirc;u với L&acirc;m Thấm.<br />\r\n<br />\r\nL&acirc;m Thấm li&ecirc;n tiếp gật c&aacute;i đầu nhỏ.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m n&acirc;ng người l&ecirc;n, mặt m&agrave;y mỉm cười.<br />\r\n<br />\r\nChiếc thuyền kia c&agrave;ng l&uacute;c c&agrave;ng gần, diện mạo của thiếu ni&ecirc;n tr&ecirc;n thuyền, cũng c&agrave;ng l&uacute;c c&agrave;ng r&otilde; r&agrave;ng.<br />\r\n<br />\r\nD&aacute;ng dấp v&ocirc; c&ugrave;ng giống L&acirc;m Đ&agrave;m.<br />\r\n<br />\r\nL&acirc;m Thấm d&ugrave;ng &aacute;nh mắt vừa thưởng thức vừa &aacute;i mộ nh&igrave;n hắn, vươn tay nhỏ b&eacute;, từ từ thở d&agrave;i n&oacute;i: &quot;Thiếu ni&ecirc;n xanh l&egrave;.&quot;<br />\r\n<br />\r\n&mdash;&ndash; giọng n&oacute;i r&otilde; r&agrave;ng, c&ograve;n rất l&agrave; lớn.</h2>', 0, 'kieu-nu-lam-gia-chuong-1');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(10, 7, 'Kiều Nữ Lâm Gia - Chương 2', 'Kiều Nữ Lâm Gia - Chương 2', '<h2>&quot;Bướng bỉnh.&quot; Thiếu ni&ecirc;n tr&ecirc;n thuyền nhếch m&ocirc;i, tr&ecirc;n khu&ocirc;n mặt thanh nh&atilde; hiện ra nụ cười nhạt nhẽo.<br />\r\n<br />\r\nL&acirc;m Thấm vẫn vươn tay nhỏ b&eacute;, than thở như thật, &quot;Thiếu ni&ecirc;n xanh l&egrave;, t&aacute;c phong nhanh nhẹn!&quot;<br />\r\n<br />\r\nB&eacute; mới ba bốn tuổi, v&oacute;c d&aacute;ng nho nhỏ, mặt ng&acirc;y thơ, cố t&igrave;nh phải học d&ugrave;ng giọng của đại nh&acirc;n để kh&iacute;ch lệ huynh trưởng của m&igrave;nh, nh&igrave;n qua rất l&agrave; buồn cười.<br />\r\n<br />\r\nL&acirc;m Khai bỏ thuyền l&ecirc;n bờ, khom lưng siết chặt c&aacute;i mũi nhỏ của muội muội, &quot;A Thấm thật bướng bỉnh.&quot; Vẻ mặt v&agrave; giọng n&oacute;i vừa thoải m&aacute;i vừa th&acirc;n mật.<br />\r\n<br />\r\nHắn cực kỳ tinh xảo mỹ lệ, phong c&aacute;ch lại thi&ecirc;n về lạnh l&ugrave;ng, vẻ th&acirc;n mật từ hắn lộ ra c&oacute; vẻ hết sức kh&oacute; được.<br />\r\n<br />\r\n&quot;Lu&ocirc;n b&oacute;p lỗ mũi của muội, b&oacute;p dẹp sẽ kh&ocirc;ng đẹp.&quot; L&acirc;m Thấm nhăn c&aacute;i mũi nhỏ, lộ vẻ mặt gh&eacute;t bỏ.<br />\r\n<br />\r\n&quot;Thật điệu.&quot; L&acirc;m Khai cưng ch&igrave;u cười khẽ.<br />\r\n<br />\r\n&quot;A Thấm n&oacute;i kh&ocirc;ng sai nha, lỗ mũi kh&ocirc;ng thể b&oacute;p ho&agrave;i, b&oacute;p dẹp rồi kh&ocirc;ng xinh đẹp.&quot; L&acirc;m Đ&agrave;m cười gi&uacute;p muội muội n&oacute;i chuyện.<br />\r\n<br />\r\n&quot;Đại tiểu thư v&agrave; nhị tiểu thư của ch&uacute;ng ta li&ecirc;n thủ, tất nhi&ecirc;n sẽ đ&aacute;nh đ&acirc;u thắng đ&oacute;, lu&ocirc;n l&agrave; người thắng.&quot; L&acirc;m Khai khoa trương chắp tay với L&acirc;m Đ&agrave;m v&agrave; L&acirc;m Thấm, &quot;Đại ca sai lầm rồi, đại ca nhận thua.&quot;<br />\r\n<br />\r\nL&acirc;m Thấm ngửa khu&ocirc;n mặt nhỏ nhắn l&ecirc;n, giọng th&agrave;nh khẩn, v&ocirc; c&ugrave;ng nghi&ecirc;m t&uacute;c, &quot;Đại ca, kh&ocirc;ng c&oacute; chuyện g&igrave;, cha đ&atilde; n&oacute;i, biết sai liền sửa, vẫn l&agrave; đứa b&eacute; ngoan.&quot;<br />\r\n<br />\r\nL&acirc;m Khai:.........<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m quay lưng lại, kh&ocirc;ng c&oacute; nghĩa kh&iacute; che miệng cười trộm.<br />\r\n<br />\r\nL&acirc;m Khai nhẹ nh&agrave;ng ho một tiếng, &quot;Đại ca phải về thư ph&ograve;ng chăm chỉ rồi, A Đ&agrave;m, A Thấm, tạm biệt.&quot; Thản nhi&ecirc;n đi về ph&iacute;a m&eacute;p nước.<br />\r\n<br />\r\nKhi hắn sắp tr&egrave;o l&ecirc;n thuyền, L&acirc;m Thấm liền nện ch&acirc;n ngắn đuổi theo, d&ograve; th&acirc;n thể nhỏ b&eacute; &acirc;n cần hỏi thăm: &quot;Đại ca, huynh trở về thư ph&ograve;ng chăm chỉ sao? Học c&aacute;i g&igrave;? Muội kh&ocirc;ng hiểu?&quot;<br />\r\n<br />\r\nL&acirc;m Khai ph&oacute;ng kho&aacute;ng l&ecirc;n thuyền, trong tay cầm một c&acirc;y gậy tr&uacute;c d&agrave;i khuấy v&agrave;o nước một c&aacute;i, con thuyền nhỏ liền bơi tr&ecirc;n mặt nước giống như mũi t&ecirc;n, người đ&atilde; đi xa, &acirc;m thanh lại du dương truyền trở lại, &quot;Đại ca trở về ch&eacute;p s&aacute;ch, t&ecirc;n s&aacute;ch l&agrave; Mỗi ng&agrave;y đều bị nhị tiểu thư L&acirc;m gia đ&aacute;nh bại.....&quot;<br />\r\n<br />\r\ndien-danlequy-don<br />\r\n<br />\r\n&quot;Ph&igrave;....&quot; L&acirc;m Đ&agrave;m cười.<br />\r\n<br />\r\nTr&ecirc;n khu&ocirc;n mặt nhỏ nhắn ng&acirc;y thơ của L&acirc;m Thấm đều l&agrave; vẻ m&ecirc; mang, &quot;Nhị tiểu thư L&acirc;m gia ch&iacute;nh l&agrave; muội, tỷ tỷ, mỗi ng&agrave;y muội đều l&agrave;m g&igrave;?&quot;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m dịu d&agrave;ng n&oacute;i: &quot;A Thấm nhỏ vậy, c&oacute; thể l&agrave;m c&aacute;i g&igrave;? Đương nhi&ecirc;n l&agrave; mỗi ng&agrave;y ăn ăn ăn, chơi chơi chơi, đ&acirc;y ch&iacute;nh l&agrave; chuyện đứng đắn của muội.&quot; L&acirc;m Thấm kh&ocirc;ng vui vẻ, chỉ chỉ ngỗng trắng đang nh&agrave;n nh&atilde; nghịch nước, lẽ thẳng kh&iacute; h&ugrave;ng, &quot;Muội cũng biết tr&ocirc;ng nom Đại Bạch.&quot;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m n&iacute;n cười, &quot;Đ&uacute;ng, A Thấm kh&ocirc;ng chỉ biết ăn v&agrave; chơi, c&ograve;n biết l&agrave;m việc đấy, thật l&agrave; bận rộn!&quot; A Thấm muội ngoại trừ sống ph&oacute;ng t&uacute;ng, l&agrave;m nũng, g&acirc;y cười ra th&igrave; c&ograve;n l&agrave;m g&igrave;, ừ, thật l&agrave; rất bận rộn.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, một b&eacute; trai chừng bảy t&aacute;m tuổi kh&ocirc;ng nhanh kh&ocirc;ng chậm đi dọc theo bờ, ph&iacute;a sau cậu c&oacute; một g&atilde; sai vặt cũng bằng tuổi đi theo sau, c&ograve;n thay cậu cầm một c&aacute;i giỏ tre.<br />\r\n<br />\r\n&quot;Nhị ca.&quot; L&acirc;m Thấm thấy cậu, mắt liền tỏa s&aacute;ng, chạy tung tăng tới, cười nịnh h&oacute;t, &quot;Nhị ca, muội rất nh&atilde; đấy, muội c&ograve;n rất bận!&quot;<br />\r\n<br />\r\nL&acirc;m H&agrave;n căn bản nghe kh&ocirc;ng hiểu tiểu muội của m&igrave;nh đang n&oacute;i c&aacute;i g&igrave;, c&aacute;i g&igrave; rất nh&atilde;, rất bận, A Thấm, đ&acirc;y l&agrave; c&aacute;i g&igrave; với c&aacute;i g&igrave; chứ. Nhưng cậu cũng biết tiểu muội của m&igrave;nh qu&aacute; nhỏ tuổi, rất hồ đồ, thường tự n&oacute;i một m&igrave;nh, lời n&oacute;i ra kh&ocirc;ng giải th&iacute;ch được cũng kh&ocirc;ng c&oacute; ch&uacute;t &yacute; nghĩa n&agrave;o, liền ki&ecirc;n nhẫn hỏi b&eacute;: &quot;A Thấm, tại sao n&oacute;i muội rất nh&atilde;?&quot;<br />\r\n<br />\r\nL&acirc;m Thấm hứng th&uacute; chỉ chỉ ngỗng trắng trong nước, &quot;N&oacute;, rất nh&atilde;, Vương Hi Chi!&quot; L&acirc;m H&agrave;n theo tay của muội muội nh&igrave;n sang, rất nhanh liền biết, &quot;A Thấm, muội y&ecirc;u ngỗng, n&oacute;i điều n&agrave;y rất nh&atilde;, giống như Vương Hi Chi, c&oacute; đ&uacute;ng hay kh&ocirc;ng?&quot; L&acirc;m Thấm liền gật đầu li&ecirc;n tục kh&ocirc;ng ngừng.<br />\r\n<br />\r\nL&acirc;m H&agrave;n chỉ chỉ ngỗng trắng dạy cho b&eacute;, &quot;Vương Hi Chi y&ecirc;u ngỗng kh&ocirc;ng chỉ l&agrave; chuyện tao nh&atilde; của văn nh&acirc;n, &ocirc;ng ấy c&ograve;n thể nghiệp ra c&aacute;ch viết thư ph&aacute;p ảo diệu từ tư thế của ngỗng. Muội xem, cổ ngỗng ngẩng cao cong l&ecirc;n, c&oacute; đ&uacute;ng hay kh&ocirc;ng? Chấp b&uacute;t th&igrave; cần như vậy. L&uacute;c vận b&uacute;t, lại phải giống như ch&acirc;n ngỗng t&aacute;t nước, mới c&oacute; thể khiến tinh thần chăm ch&uacute; v&agrave;o b&uacute;t ph&aacute;p.&quot;<br />\r\n<br />\r\nL&acirc;m Thấm nghe mơ mơ m&agrave;ng m&agrave;ng, nhưng vẫn cười lấy l&ograve;ng, li&ecirc;n tiếp gật đầu.<br />\r\n<br />\r\n&quot;A Thấm, qua hai năm nữa muội hiểu chuyện rồi, nhị ca sẽ dạy muội viết chữ.&quot; L&acirc;m H&agrave;n trịnh trọng cam kết.<br />\r\n<br />\r\ndien/danle/quydon<br />\r\n<br />\r\n&quot;Nh&oacute;c cổ hũ.&quot; L&acirc;m Đ&agrave;m thấy nhị đệ như thế, trong nội t&acirc;m vui l&ecirc;n.<br />\r\n<br />\r\n&quot;Nhị đệ, ch&agrave;o buổi s&aacute;ng.&quot; L&acirc;m Đ&agrave;m cười h&igrave; h&igrave; n&oacute;i.<br />\r\n<br />\r\nL&acirc;m H&agrave;n nh&igrave;n l&ecirc;n nh&igrave;n xuống đ&aacute;nh gi&aacute; n&agrave;ng mấy lần, khẽ nh&iacute;u m&agrave;y, c&oacute; ch&uacute;t miễn cưỡng khom người thi lễ, &quot;Đại ca, ch&agrave;o buổi s&aacute;ng.&quot;<br />\r\n<br />\r\n&quot;Nhị đệ, h&ocirc;m nay l&agrave; ai chuẩn bị quần &aacute;o cho đệ? &Aacute;nh mắt kh&ocirc;ng tệ, &aacute;o m&agrave;u xanh ngọc n&agrave;y rất xứng với đệ, khiến cho gương mặt của đệ c&agrave;ng tuyết trắng b&eacute;o mập, thật đ&aacute;ng y&ecirc;u.&quot; L&acirc;m Đ&agrave;m th&acirc;n thiết hỏi &quot;Đệ muốn đến trường sao? A H&agrave;n, thật ra th&igrave; đệ kh&ocirc;ng cần chăm chỉ vậy đ&acirc;u, h&ocirc;m nay trong nh&agrave; c&oacute; kh&aacute;ch tới, cha v&agrave; nương đ&atilde; xin Hứa gi&aacute;o dụ cho đệ nghỉ rồi. Đệ ở nh&agrave; nghỉ ngơi, &lsquo;trộm được ph&ugrave; sinh nửa ng&agrave;y nh&agrave;n&rsquo;, chẳng phải rất th&iacute;ch &yacute;?&quot;<br />\r\n<br />\r\nVẻ mặt L&acirc;m H&agrave;n nghi&ecirc;m t&uacute;c, &quot;Thời gian kh&ocirc;ng thể mua được bằng tiền, tỷ tỷ, h&ocirc;m nay đệ kh&ocirc;ng đi học, l&uacute;c n&agrave;y d&ugrave; sao nh&agrave;n rỗi v&ocirc; sự, liền đến chỗ Văn ti&ecirc;n sinh một l&aacute;t, thuận tiện thỉnh gi&aacute;o học vấn.&quot;<br />\r\n<br />\r\nVăn ti&ecirc;n sinh l&agrave; phụ t&aacute; L&acirc;m Phong mời về, bởi v&igrave; chỉ ở An Định một m&igrave;nh, kh&ocirc;ng c&oacute; gia quyến chăm s&oacute;c cuộc sống thường ng&agrave;y, L&acirc;m Phong liền mời hắn ở L&acirc;m phủ. Văn ti&ecirc;n sinh c&oacute; học vấn uy&ecirc;n b&aacute;c, L&acirc;m H&agrave;n rất ngưỡng mộ hắn, cho n&ecirc;n r&otilde; r&agrave;ng h&ocirc;m nay c&oacute; thể kh&ocirc;ng đi học, cậu cũng phải đến chỗ Văn ti&ecirc;n sinh cầu học.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m cười, &quot;Đệ đệ của ta thật chăm chỉ.&quot;<br />\r\n<br />\r\nGương mặt tuấn t&uacute; của L&acirc;m H&agrave;n nghi&ecirc;m lại, &quot;Kh&aacute;ch gần trưa mới tới, đến l&uacute;c đ&oacute; ta trở lại, cũng kh&ocirc;ng thiếu lễ độ, n&ecirc;n tiểu đệ c&aacute;o từ.&quot; Cẩn thận tỉ mỉ c&uacute;i người một c&aacute;i, c&aacute;o biệt với L&acirc;m Đ&agrave;m, L&acirc;m Thấm, liền đoan ch&iacute;nh bước đi l&ecirc;n cầu đ&aacute;.<br />\r\n<br />\r\n&quot;C&oacute; thể quang minh ch&aacute;nh đại lười biếng m&agrave; đệ cũng kh&ocirc;ng chịu, A H&agrave;n, đệ thật l&agrave; ngoại tộc trong những học sinh đ&oacute;.&quot; L&acirc;m Đ&agrave;m nh&igrave;n b&oacute;ng lưng của đệ đệ, lắc đầu thở d&agrave;i.<br />\r\n<br />\r\n&quot;Ngoại tộc nha.&quot; L&acirc;m Thấm bắt chước cảm kh&aacute;i giống tỷ tỷ.<br />\r\n<br />\r\n&quot;Qu&aacute;t, qu&aacute;t, qu&aacute;t.&quot; Đại Bạch từ trong nước l&ecirc;n bờ, vẫy vẫy nước tr&ecirc;n l&ocirc;ng vũ, ng&ocirc;ng ngh&ecirc;nh kh&ocirc;ng ch&uacute;t hoang mang đi tới b&ecirc;n cạnh L&acirc;m Thấm, đợi b&eacute; c&ugrave;ng nhau trở về.<br />\r\n<br />\r\n&quot;Đợi l&aacute;t nữa bơi tiếp.&quot; L&acirc;m Thấm nghi&ecirc;m t&uacute;c n&oacute;i cho n&oacute; biết.<br />\r\n<br />\r\nĐại Bạch bất m&atilde;n rướn cổ l&ecirc;n k&ecirc;u v&agrave;i tiếng, tiếng k&ecirc;u trịnh trọng, tựa như c&oacute; &yacute; khiển tr&aacute;ch.<br />\r\n<br />\r\n&quot;Ngươi bơi l&acirc;u như vậy, ta chỉ k&ecirc;u ngươi chờ ch&uacute;t th&ocirc;i nha.&quot; Trong đ&ocirc;i mắt vừa đen vừa s&aacute;ng của L&acirc;m Thấm tr&agrave;n đầy kinh ngạc, &quot;Ta mới n&oacute;i mấy c&acirc;u với tỷ tỷ, ngươi đ&atilde; chờ kh&ocirc;ng b&igrave;nh tĩnh rồi hả? Đại Bạch, ngươi như vậy thật kh&ocirc;ng tốt.&quot;<br />\r\n<br />\r\nTiếng k&ecirc;u của Đại Bạch c&agrave;ng to hơn.<br />\r\n<br />\r\n&quot;K&ecirc;u to kh&ocirc;ng c&oacute; nghĩa l&agrave; ngươi c&oacute; l&yacute;!&quot; L&acirc;m Thấm lộ vẻ mặt nghi&ecirc;m t&uacute;c, khiển tr&aacute;ch: &quot;Chỉ biết chơi! D&atilde; man kh&ocirc;ng hiểu chuyện! Tốt lắm, đừng k&ecirc;u nữa, ta dẫn ngươi trở về l&agrave; được.&quot; Ngỗng trắng nghe b&eacute; n&oacute;i trở về, mới kh&ocirc;ng k&ecirc;u nữa, ngẩng đầu đi trở về.<br />\r\n<br />\r\nDạy Đại Bạch xong, L&acirc;m Thấm phất phất tay nhỏ b&eacute; với L&acirc;m Đ&agrave;m, &quot;Tỷ tỷ, muội v&agrave; Đại Bạch đi trước nh&eacute;.&quot; Vừa hay đ&uacute;ng l&uacute;c n&agrave;y hai nha đầu kh&aacute;c l&agrave; Khổng Dương v&agrave; tiểu Uyển cũng tới, hai nha đầu n&agrave;y đều mười lăm mười s&aacute;u tuổi, nh&igrave;n liền thấy th&agrave;nh thục hơn Nhan Tại v&agrave; Ứng Cai, L&acirc;m Đ&agrave;m ph&acirc;n ph&oacute; n&oacute;i: &quot;Hai ngươi theo nhị tiểu thư đưa Đại Bạch trở về chuồng ngỗng, coi chừng nhị tiểu thư cho tốt, kh&ocirc;ng cho nhị tiểu thư chạy loạn.&quot;<br />\r\n<br />\r\nKhổng Dương v&agrave; tiểu Uyển đều xưng v&acirc;ng, L&acirc;m Đ&agrave;m vẫn kh&ocirc;ng y&ecirc;n l&ograve;ng, lại dặn d&ograve; muội muội, &quot;A Thấm, đi đường chậm ch&uacute;t.....&quot;<br />\r\n<br />\r\n&quot;Biết, biết.&quot; L&acirc;m Thấm kh&ocirc;ng đợi L&acirc;m Đ&agrave;m n&oacute;i xong đ&atilde; đồng &yacute;.<br />\r\n<br />\r\nB&eacute; ng&aacute;i mang theo ngỗng trắng, ngh&ecirc;nh ngang đi rồi.<br />\r\n<br />\r\nHai nha đầu kh&ocirc;ng gần kh&ocirc;ng xa đi theo ph&iacute;a sau b&eacute;.<br />\r\n<br />\r\ndien[dan]le[quy]don<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m nh&igrave;n b&oacute;ng lưng nho nhỏ của muội muội, bất gi&aacute;c mỉm cười.<br />\r\n<br />\r\n&quot;Đại tiểu thư, ng&agrave;i trở lại rồi, phu nh&acirc;n đang t&igrave;m ng&agrave;i đấy.&quot; Một thiếu nữ mặc &aacute;o ngo&agrave;i m&agrave;u xanh giống kiểu của thị nữ đi ra từ trong viện, vẻ mặt tươi cười, khom gối h&agrave;nh lễ.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m buồn bực c&uacute;i đầu, &quot;Thanh Tr&uacute;c, ta kh&ocirc;ng giống chỗ n&agrave;o, cả ngươi cũng c&oacute; thể nh&igrave;n ra sao?&quot;<br />\r\n<br />\r\nThị nữ t&ecirc;n Thanh Tr&uacute;c h&eacute; miệng cười, &quot;Đại tiểu thư, n&ocirc; tỳ vụng về, ng&agrave;i giả trang rất giống, ta thật kh&ocirc;ng nh&igrave;n ra. Nhưng s&aacute;ng nay ta đ&atilde; thấy đại c&ocirc;ng tử, ng&agrave;i ấy r&otilde; r&agrave;ng mặc &aacute;o m&agrave;u xanh l&aacute;.&quot;<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m kh&ocirc;ng khỏi cười, &quot;Dọa ta giật m&igrave;nh, c&ograve;n tưởng ngươi c&oacute; hỏa nh&atilde;n kim tinh đấy.&quot;<br />\r\n<br />\r\nThanh Tr&uacute;c đi theo L&acirc;m Đ&agrave;m v&agrave;o trong, trong miệng tr&ecirc;u ghẹo, &quot;Đại tiểu thư ng&agrave;i suy nghĩ xem, L&acirc;m gia c&oacute; huynh muội sanh đ&ocirc;i l&agrave; đại c&ocirc;ng tử v&agrave; ng&agrave;i, s&aacute;ng nay n&ocirc; tỳ đ&atilde; gặp đại c&ocirc;ng tử, l&uacute;c n&agrave;y nếu kh&ocirc;ng nhận ra ng&agrave;i, chẳng phải l&agrave; đứa ngốc sao? Thật ngốc vậy, n&ecirc;n bị phu nh&acirc;n đuổi ra ngo&agrave;i rồi!&quot;<br />\r\n<br />\r\nThanh Tr&uacute;c l&agrave; ch&aacute;u g&aacute;i của L&yacute; ma ma b&agrave; v&uacute; của La phu nh&acirc;n, từ nhỏ lớn l&ecirc;n ở L&acirc;m gia, với L&acirc;m Đ&agrave;m tuy l&agrave; chủ tớ, lại hết sức quen thuộc, n&oacute;i chuyện cũng kh&ocirc;ng g&ograve; b&oacute;, cười cười n&oacute;i n&oacute;i, hai người một trước một sau v&agrave;o căn ph&ograve;ng ph&iacute;a đ&ocirc;n m&agrave; La phu nh&acirc;n ở hằng ng&agrave;y.<br />\r\n<br />\r\nL&uacute;c n&agrave;y trời đ&atilde; s&aacute;ng l&ecirc;n, &aacute;nh mặt trời chiếu v&agrave;o trong ph&ograve;ng, s&aacute;ng rỡ m&agrave; ấm &aacute;p. La phu nh&acirc;n đang ngồi ở trước b&agrave;n uống tr&agrave;, thấy L&acirc;m Đ&agrave;m đi v&agrave;o, liền cười n&oacute;i: &quot;Mau tới đ&acirc;y, ngồi tr&ograve; chuyện với nương một l&aacute;t. Mới s&aacute;ng sớm, đại ca con đ&atilde; muốn thả thuyền tr&ecirc;n s&ocirc;ng, con phải tiễn T&ocirc; sư gia, A H&agrave;n th&igrave; cố &yacute; muốn đến chỗ Văn ti&ecirc;n sinh thỉnh gi&aacute;o học vấn, A Thấm lại vội vội v&agrave;ng v&agrave;ng ăn một ch&uacute;t liền chạy ra ngo&agrave;i, muốn đi chơi với Đại Bạch trước khi kh&aacute;ch đến, ai, mấy con thật l&agrave; mỗi người đều c&oacute; chuyện chơi.&quot;<br />\r\n<br />\r\nN&agrave;ng mặc d&ugrave; đ&atilde; l&agrave; nương của hai trai hai g&aacute;i, nhưng cũng chỉ hơn ba mươi tuổi, hai mắt s&aacute;ng ngời, l&ocirc;ng m&agrave;y cong d&agrave;i, tướng mạo diễm lệ lại kh&iacute; kh&aacute;i, lời n&oacute;i cũng hết sức lanh lẹ dứt kho&aacute;t, l&agrave;m cho người ta cảm thấy sảng kho&aacute;i.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m thỉnh an ch&agrave;o hỏi xong, liền theo lời ngồi xuống b&ecirc;n người n&agrave;ng, n&oacute;i cười r&iacute;u r&iacute;t, &quot;A H&agrave;n tuy hơi c&acirc;u nệ, nhưng rất biết l&yacute; hiểu chuyện; A Thấm th&igrave; thật th&uacute; vị, chỉ mới bao lớn, b&eacute; liền c&oacute; thể suy một ra ba rồi. L&uacute;c n&atilde;y b&eacute; c&ograve;n dạy dỗ Đại Bạch, &lsquo;chỉ biết chơi &rsquo; &lsquo;k&ecirc;u to kh&ocirc;ng c&oacute; nghĩa l&agrave; ngươi c&oacute; l&yacute;&rsquo;!&quot;<br />\r\n<br />\r\nLa phu nh&acirc;n bật cười, &quot;Đứa nhỏ n&agrave;y, h&ocirc;m qua n&oacute; v&agrave; A H&agrave;n g&acirc;y gổ kh&ocirc;ng lại, liền la h&eacute;t k&ecirc;u to, A H&agrave;n liền n&oacute;i với n&oacute; la to kh&ocirc;ng phải c&oacute; l&yacute;, n&oacute; liền nhớ rồi. Quay đầu c&ograve;n dạy dỗ Đại Bạch! Kh&ocirc;ng được, nhị tiểu thư của ch&uacute;ng ta thật gh&ecirc; gớm!&quot; L&acirc;m Đ&agrave;m hết sức chấp nhận, &quot;Trời sinh th&ocirc;ng minh, nghe một hiểu mười.&quot;<br />\r\n<br />\r\nLa phu nh&acirc;n hỏi &quot;T&ocirc; sư gia l&ecirc;n đường c&oacute; thuận lợi kh&ocirc;ng?&quot; L&acirc;m Đ&agrave;m h&eacute; miệng cười, &quot;Hắn xuất th&acirc;n nh&agrave; gi&agrave;u, quen sống ch&uacute; &yacute;, vật dụng hằng ng&agrave;y đều muốn mang theo đầy đủ hết, kh&ocirc;ng chịu s&oacute;t thứ g&igrave;, lại nhất định phải l&ecirc;n đường đ&uacute;ng canh ba giờ Dần, l&agrave;m cho bận rộn một phen.&quot;<br />\r\n<br />\r\nLa phu nh&acirc;n cũng biết T&ocirc; sư gia, kh&ocirc;ng khỏi cười một tiếng.<br />\r\n<br />\r\nT&ocirc; sư gia l&agrave; thứ tử của thương nh&acirc;n, rất nghĩa kh&iacute;, chỉ b&egrave;o nước gặp nhau với L&acirc;m Phong lại vừa gặp đ&atilde; th&acirc;n, n&ecirc;n liền l&agrave;m phụ t&aacute; cho L&acirc;m Phong, mặc d&ugrave; L&acirc;m Phong chỉ l&agrave;m huyện lệnh trong một th&agrave;nh nhỏ ở T&acirc;y Bắc cũng kh&ocirc;ng c&oacute; nửa c&acirc;u o&aacute;n hận, kh&ocirc;ng xa kh&ocirc;ng rời. Nhưng hắn ăn uống sinh hoạt từ trước đến giờ đều xa xỉ, d&ugrave; đi xa nh&agrave; cũng kh&ocirc;ng chịu qua loa, lại tự cho l&agrave; đại sư Chu Dịch, động ch&uacute;t th&igrave; muốn b&oacute;i một quẻ, đo&aacute;n một phen, tự cho l&agrave; coi rất ch&iacute;nh x&aacute;c. Giống như s&aacute;ng sớm h&ocirc;m nay, hắn b&oacute;i ra canh ba giờ Dần, liền phải đ&uacute;ng canh ba giờ Dần, nhất định phải khởi h&agrave;nh l&ecirc;n đường ở ng&agrave;y l&agrave;nh th&aacute;ng tốt n&agrave;y. H&ocirc;m nay L&acirc;m Phong lại c&oacute; c&ocirc;ng vụ quan trọng, li&ecirc;n tục thương lượng với hắn cho hắn đổi ng&agrave;y, hắn cũng kh&ocirc;ng chịu.<br />\r\n<br />\r\nL&uacute;c n&oacute;i chuyện, L&acirc;m Đ&agrave;m liền nhắc tới hai nha đầu Nhan Tại v&agrave; Ứng Cai, &quot;.... A Thấm cũng đ&atilde; đi tới m&eacute;p nước rồi, hai người đ&oacute; c&ograve;n sợ h&atilde;i rụt r&egrave;, c&aacute;ch xa t&aacute;m trượng. N&ecirc;n con cho hai người đ&oacute; trở về, đổi Khổng Dương v&agrave; tiểu Uyển theo A Thấm.&quot;<br />\r\n<br />\r\ndien;dan&quot;le&quot;quy,don<br />\r\n<br />\r\nLa phu nh&acirc;n giận t&aacute;i mặt, &quot;Thường ng&agrave;y thấy hai người n&agrave;y cũng tốt, ai ngờ v&ocirc; dụng như vậy! A Đ&agrave;m, con đổi người l&agrave; đ&uacute;ng, Khổng Dương v&agrave; tiểu Uyển l&agrave; nha đầu con dạy dỗ, l&agrave;m việc ổn thỏa, lại tỉ mỉ.&quot; L&acirc;m Đ&agrave;m thấy sắc mặt La phu nh&acirc;n t&aacute;i xanh, khuy&ecirc;n giải: &quot;Nương kh&ocirc;ng cần tức giận, Nhan Tại v&agrave; Ứng Cai kh&ocirc;ng lớn lắm, lo việc kh&ocirc;ng chu đ&aacute;o, việc n&agrave;y cũng kh&oacute; tr&aacute;nh khỏi, về sau dạy dỗ lại l&agrave; được.&quot;<br />\r\n<br />\r\nLa phu nh&acirc;n rốt cuộc c&ograve;n tức, sai người gọi Nhan Tại v&agrave; Ứng Cai tới la mắng.<br />\r\n<br />\r\nNhan Tại quỳ tr&ecirc;n mặt đất, chảy nước mắt thanh minh cho bản th&acirc;n, &quot;Phu nh&acirc;n, ng&agrave;y h&ocirc;m trước n&ocirc; tỳ bị Đại Bạch cắn một lần, trong l&ograve;ng liền sợ.... Ngỗng cắn người cũng rất đau..... Ứng Cai cũng bị ngỗng cắn, n&agrave;ng ấy nh&aacute;t gan, l&ocirc;i k&eacute;o ta kh&ocirc;ng cho ta qua..... Phu nh&acirc;n, thật kh&ocirc;ng phải.....&quot;<br />\r\n<br />\r\nTh&acirc;n thể Ứng Cai ph&aacute;t run, c&uacute;i đầu nhận tội, &quot;N&ocirc; tỳ biết sai, đ&atilde; đến chỗ Tần ma ma lĩnh năm h&egrave;o, cầu xin phu nh&acirc;n tr&aacute;ch phạt tiếp! Chuyện n&agrave;y kh&ocirc;ng li&ecirc;n quan tới Nhan Tại tỷ tỷ, tất cả đều l&agrave; lỗi của n&ocirc; tỳ, tất cả đều l&agrave;<br />\r\n<br />\r\nlỗi của n&ocirc; tỳ!&quot; Liền dập đầu bốp bốp, kh&ocirc;ng bao l&acirc;u c&aacute;i tr&aacute;n đ&atilde; sưng đỏ.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m ở b&ecirc;n thờ ơ lạnh nhạt, xem thần th&aacute;i v&agrave; ng&ocirc;n ngữ của hai nha đầu suy nghĩ một l&aacute;t, cũng đ&atilde; biết chuyện g&igrave; xảy ra.<br />\r\n<br />\r\nNhan Tại l&agrave; con của gia n&ocirc;, Chu Năng cha n&agrave;ng ta quản hai cửa h&agrave;ng, trong nh&agrave; chắc l&agrave; sung t&uacute;c, liền nu&ocirc;i n&agrave;ng ta hơi ngạo mạn ch&uacute;t, kh&ocirc;ng cho n&agrave;ng ta chịu khổ g&igrave;. Một nha đầu, bởi v&igrave; bị ngỗng cắn, liền d&aacute;m chậm trễ tiểu chủ nh&acirc;n, lại kh&ocirc;ng c&oacute; tr&aacute;ch nhiệm, gặp chuyện liền muốn dội nước đục l&ecirc;n người người kh&aacute;c. C&ograve;n Ứng Cai l&agrave; từ ngo&agrave;i mua về, vốn thấp hơn Nhan Tại một ch&uacute;t, n&agrave;ng ấy lại một th&acirc;n một m&igrave;nh ở L&acirc;m phủ, kh&ocirc;ng c&oacute; g&igrave; dựa v&agrave;o, b&igrave;nh thường nhất định l&agrave; bị Nhan Tại khi dễ quen rồi, mọi chuyện đều nghe Nhan Tại, d&ugrave; đến l&uacute;c n&agrave;y, Nhan Tại đẩy to&agrave;n bộ tr&aacute;ch nhiệm l&ecirc;n người n&agrave;ng ấy, n&agrave;ng ấy cũng kh&ocirc;ng d&aacute;m phản kh&aacute;ng đ&ocirc;i c&acirc;u v&agrave;i lời.<br />\r\n<br />\r\nNhan Tại vốn n&ecirc;n nghi&ecirc;m trị, nhưng Chu Năng cha n&agrave;ng ta l&agrave; người hầu do La phu nh&acirc;n mang tới từ mang tới nh&agrave; mẹ Tấn Giang hầu phủ, nếu phạt nặng Nhan Tại, La phu nh&acirc;n cũng kh&ocirc;ng khỏi mất mạt. Về phần Ứng Cai, n&agrave;ng ấy chỉ mềm yếu<br />\r\n<br />\r\nch&uacute;t, c&ograve;n c&oacute; thể dạy.<br />\r\n<br />\r\nL&acirc;m Đ&agrave;m liền nh&aacute;y mắt với La phu nh&acirc;n.<br />\r\n<br />\r\nLa phu nh&acirc;n hiểu &yacute;, lệnh Nhan Tại ra ngo&agrave;i quỳ, chờ xử l&yacute;.<br />\r\n<br />\r\nHai nha đầu nước mắt lưng tr&ograve;ng dập đầu, liền đi ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;C&oacute; lời g&igrave; muốn n&oacute;i với nương?&quot; La phu nh&acirc;n ngoắc k&ecirc;u L&acirc;m Đ&agrave;m, dịu d&agrave;ng hỏi.</h2>', 0, 'kieu-nu-lam-gia-chuong-2'),
(11, 10, 'Chương 1: Dreamland', 'Chương 1: Dreamland', '<h2>Mười s&aacute;u, mười ba<br />\r\n<br />\r\nĐ&atilde; mười ba ph&uacute;t tr&ocirc;i qua kể từ khi cậu rời ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp để mua coca.<br />\r\n<br />\r\nĐứng trong b&oacute;ng đ&ecirc;m, L&acirc;m Phong nh&igrave;n h&agrave;nh lang rỉ s&eacute;t v&agrave; bức tường loang lổ đốm v&agrave;ng trước mặt, d&ugrave;ng sức dụi mắt. Cậu cảm thấy chắc chắn m&igrave;nh xem m&aacute;y t&iacute;nh l&acirc;u qu&aacute; n&ecirc;n bị ảo gi&aacute;c rồi.<br />\r\n<br />\r\nNhưng sự thật cho L&acirc;m Phong biết rằng đ&acirc;y thực sự l&agrave; tiểu khu v&agrave; nh&agrave; m&igrave;nh.<br />\r\n<br />\r\nĐ&egrave;n đường xung quanh vừa tối vừa s&aacute;ng, tiếng điện thỉnh thoảng từng ch&uacute;t k&iacute;ƈɦ ŧɦ&iacute;ƈɦ thần kinh n&atilde;o L&acirc;m Phong. B&ecirc;n tai kh&ocirc;ng ngừng văng vẳng tiếng gi&oacute; thổi qua, cậu đột nhi&ecirc;n cảm thấy cảnh tượng n&agrave;y c&oacute; ch&uacute;t quen thuộc.<br />\r\n<br />\r\nNhưng hiện tại cậu kh&ocirc;ng thể nhớ ra n&oacute;. Loại cảm gi&aacute;c gần gũi khảm s&acirc;u v&agrave;o t&acirc;m can nhưng kh&ocirc;ng thể diễn đạt n&agrave;y khiến cậu c&agrave;ng cảm thấy hoảng sợ. Cũng kh&ocirc;ng biết tại sao, L&acirc;m Phong c&oacute; cảm gi&aacute;c m&igrave;nh nảy sinh &yacute; tưởng &quot;mau c&uacute;t khỏi đ&acirc;y&quot;.<br />\r\n<br />\r\nTuy nhi&ecirc;n, trận đấu trong ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp vẫn đang mở, vẫn phải l&agrave;m việc, kiếm tiền. Hơn nữa, đ&acirc;y vốn l&agrave; nh&agrave; của cậu, cậu c&ograve;n c&oacute; thể đi đ&acirc;u?<br />\r\n<br />\r\nL&acirc;m Phong h&iacute;t s&acirc;u một hơi, cuối c&ugrave;ng lựa chọn đ&egrave; xuống cảm gi&aacute;c kỳ qu&aacute;i n&agrave;y v&agrave;o đ&aacute;y l&ograve;ng, căng da đầu đi v&agrave;o thang lầu vừa quen thuộc vừa xa lạ.<br />\r\n<br />\r\n&quot;Diệp Kiều, nhanh l&ecirc;n, sao mới đ&acirc;y m&agrave; cậu đ&atilde; đi mất rồi? Tr&igrave; Hợp vẫn đang đợi cậu đ&oacute;!&quot; Mất cảnh gi&aacute;c, L&acirc;m Phong nh&igrave;n thấy một c&ocirc; g&aacute;i vội v&agrave;ng chạy xuống lầu. Vừa nh&igrave;n thấy L&acirc;m Phong, c&ocirc; liền h&eacute;t lớn.<br />\r\n<br />\r\nL&acirc;m Phong sửng sốt.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng phải t&ecirc;n của cậu, nhưng L&acirc;m Phong c&oacute; thể cảm nhận r&otilde; r&agrave;ng c&ocirc; g&aacute;i đang n&oacute;i chuyện với m&igrave;nh. Nơi n&agrave;y, dường như kh&ocirc;ng c&oacute; người n&agrave;o kh&aacute;c ngo&agrave;i cậu.<br />\r\n<br />\r\nL&acirc;m Phong kh&ocirc;ng khỏi kinh ngạc nh&igrave;n c&ocirc; g&aacute;i trước mặt. Kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi tuổi của c&ocirc; g&aacute;i tương đương với cậu. Chắc do chạy xuống cầu thang qu&aacute; gấp n&ecirc;n t&oacute;c c&ocirc; g&aacute;i tr&ocirc;ng hơi rối.<br />\r\n<br />\r\nL&acirc;m Phong nh&iacute;u m&agrave;y, cậu kh&ocirc;ng nhớ c&oacute; một c&ocirc; g&aacute;i như vậy sống trong t&ograve;a nh&agrave; n&agrave;y.<br />\r\n<br />\r\n&quot;C&ocirc; g&aacute;i, c&ocirc; đ&atilde; nhận nhầm người rồi.&quot; Mặc d&ugrave; L&acirc;m Phong cảm thấy bối rối trước h&agrave;nh vi của c&ocirc; g&aacute;i, nhưng cậu vẫn mỉm cười lịch sự.<br />\r\n<br />\r\n&quot;Đến khi n&agrave;o rồi m&agrave; cậu c&ograve;n t&acirc;m tư đ&ugrave;a giỡn!&quot; C&ocirc; g&aacute;i kh&ocirc;ng tự chủ nắm lấy tay L&acirc;m Phong, xoay người k&eacute;o cậu l&ecirc;n lầu.<br />\r\n<br />\r\nL&acirc;m Phong kh&ocirc;ng ngờ rằng c&ocirc; g&aacute;i n&agrave;y nh&igrave;n kh&ocirc;ng cao lắm nhưng lực tay lại mạnh mẽ đến bất ngờ. Cậu mất cảnh gi&aacute;c trước c&aacute;i giật mạnh đột ngột của c&ocirc; n&ecirc;n loạng choạng đi theo l&ecirc;n lầu.<br />\r\n<br />\r\nR&otilde; r&agrave;ng chỉ c&oacute; mấy tầng cầu thang, nhưng L&acirc;m Phong lại cảm gi&aacute;c bọn họ chạy rất l&acirc;u rồi. Kh&ocirc;ng biết từ l&uacute;c n&agrave;o, L&acirc;m Phong đột nhi&ecirc;n ph&aacute;t hiện m&igrave;nh mất phương hướng, cầu thang quen thuộc dường như bị một lực n&agrave;o đ&oacute; t&aacute;c động b&oacute;p m&eacute;o. Bởi v&igrave; xung quanh qu&aacute; tối, L&acirc;m Phong chỉ c&oacute; thể nh&igrave;n thấy b&oacute;ng lưng của c&ocirc; g&aacute;i, ngo&agrave;i ra kh&ocirc;ng thể nh&igrave;n thấy thứ g&igrave; xa hơn.<br />\r\n<br />\r\nNhưng bản năng của L&acirc;m Phong n&oacute;i với cậu rằng dường như cậu lu&ocirc;n đi l&ecirc;n lầu, nhưng cậu kh&ocirc;ng hề rẽ v&agrave;o một g&oacute;c - cầu thang dẫn thẳng đến một nơi cao!<br />\r\n<br />\r\nĐiều n&agrave;y kh&ocirc;ng ph&ugrave; hợp với cấu tr&uacute;c của một h&agrave;nh lang b&igrave;nh thường ch&uacute;t n&agrave;o!<br />\r\n<br />\r\nTuy nhi&ecirc;n, ch&iacute;nh một cảnh tượng qu&aacute;i gở v&agrave; dị thường như vậy lại khiến trong đầu L&acirc;m Phong l&oacute;e l&ecirc;n một h&igrave;nh ảnh quen thuộc giữa &aacute;nh điện v&agrave; đ&aacute; lửa.<br />\r\n<br />\r\nKhoan đ&atilde;!<br />\r\n<br />\r\nC&ocirc; g&aacute;i vừa n&oacute;i g&igrave;...<br />\r\n<br />\r\nDiệp Kiều....Tr&igrave; Hợp....?<br />\r\n<br />\r\nDiệp Kiều!<br />\r\n<br />\r\n&quot;Đừng n&oacute;i với t&ocirc;i l&agrave; bạn....&quot; L&acirc;m Phong c&ograve;n chưa n&oacute;i xong th&igrave; nghe thấy một &acirc;m thanh sột sột soạt soạt. Bước ch&acirc;n của c&ocirc; g&aacute;i cũng dừng lại, L&acirc;m Phong su&yacute;t nữa đ&atilde; đụng phải c&ocirc; m&agrave; kh&ocirc;ng kịp phanh lại. Nhưng trước khi L&acirc;m Phong c&oacute; thời gian để n&oacute;i điều g&igrave; đ&oacute;, cậu đột nhi&ecirc;n thấy tr&ecirc;n tường c&oacute; hai c&aacute;nh tay duỗi ra.<br />\r\n<br />\r\nHai c&aacute;nh tay bịt chặt miệng cậu trong gi&acirc;y tiếp theo.<br />\r\n<br />\r\nNgay sau đ&oacute;, đ&ocirc;i tay ấy kh&ocirc;ng cho L&acirc;m Phong c&oacute; cơ hội v&ugrave;ng vẫy, liền bịt miệng cậu k&eacute;o v&agrave;o trong ph&ograve;ng tối. Cuối c&ugrave;ng, khi cậu c&oacute; thể lấy lại được hơi thở, cậu nh&igrave;n thấy một ngọn nến thắp s&aacute;ng ở cuối ph&ograve;ng, trong khi &aacute;nh nến đang đung đưa trong căn ph&ograve;ng u &aacute;m.<br />\r\n<br />\r\nL&acirc;m Phong cảm thấy sợ h&atilde;i chưa từng c&oacute;. Nếu như l&uacute;c n&atilde;y, cậu chỉ nghĩ đến chuyện &quot;vượt ngục&quot;, th&igrave; b&acirc;y giờ cậu cực kỳ sẵn s&agrave;ng &aacute;p dụng n&oacute; v&agrave;o thực tế. Chỉ l&agrave; trước khi L&acirc;m Phong kịp h&eacute;t l&ecirc;n, một b&agrave;i h&aacute;t đột nhi&ecirc;n vang l&ecirc;n b&ecirc;n tai cậu:<br />\r\n<br />\r\n&quot;HAPPY BIRTHDAY TO YOU...&quot;<br />\r\n<br />\r\n&Aacute;nh mắt L&acirc;m Phong đột nhi&ecirc;n chấn động, đ&egrave;n trong ph&ograve;ng ph&uacute;t chốc bật s&aacute;ng. Sau khi L&acirc;m Phong lấy lại tầm nh&igrave;n, cậu nh&igrave;n thấy những khu&ocirc;n mặt kỳ lạ xuất hiện trước mặt m&igrave;nh, họ h&aacute;t &quot;Ch&uacute;c mừng sinh nhật&quot; trong miệng v&agrave; d&ugrave;ng tay g&otilde; nhịp. Khu&ocirc;n mặt của mọi người đều hướng về ph&iacute;a L&acirc;m Phong, bọn họ được bao quanh bởi một chiếc b&aacute;nh sinh nhật lớn, v&agrave; một v&agrave;i ngọn nến được thắp s&aacute;ng tr&ecirc;n b&aacute;nh sinh nhật.<br />\r\n<br />\r\n&ldquo;Anh&hellip; anh l&agrave; Cao&hellip; Cao Thanh Thanh&hellip;?&rdquo; Ngay cả khi đối mặt với những khu&ocirc;n mặt tươi cười, L&acirc;m Phong vẫn run l&ecirc;n kh&ocirc;ng ngừng khi n&oacute;i đến &acirc;m cuối c&ugrave;ng.<br />\r\n<br />\r\n&Aacute;nh s&aacute;ng của ngọn nến tạo n&ecirc;n sự tương phản r&otilde; rệt với b&oacute;ng tối của h&agrave;nh lang, to&agrave;n bộ căn ph&ograve;ng ch&igrave;m trong hắc &aacute;m ngoại trừ khoảng c&aacute;ch quanh ngọn nến chưa đầy nửa m&eacute;t.<br />\r\n<br />\r\n&Aacute;nh nến l&uacute;c n&agrave;y trong b&oacute;ng tối c&agrave;ng trở n&ecirc;n ch&oacute;i mắt.<br />\r\n<br />\r\n&quot;Hẳn l&agrave; đang nằm mơ... Hẳn l&agrave; ngủ thiếp đi khi chơi game... Đ&uacute;ng vậy... m&igrave;nh vẫn đang chơi game trực tiếp... Đ&uacute;ng... Kh&ocirc;ng thể... Kh&ocirc;ng thể...&quot;<br />\r\n<br />\r\nL&acirc;m Phong nhận ra rằng m&igrave;nh bắt đầu c&oacute; ch&uacute;t kh&ocirc;ng ổn,run rẩy lảo đảo l&ugrave;i lại về ph&iacute;a sau.<br />\r\n<br />\r\nTrong qu&aacute; tr&igrave;nh r&uacute;t lui, L&acirc;m Phong thậm ch&iacute; c&ograve;n v&ocirc; t&igrave;nh đạp phải một c&aacute;i lon tr&ecirc;n mặt đất, c&aacute;i lon &quot;h&eacute;t&quot; l&ecirc;n, ph&aacute;t ra tiếng động mạnh trong m&ocirc;i trường vốn đ&atilde; khiến đầu &oacute;c cậu căng thẳng.<br />\r\n<br />\r\nV&agrave; tại thời điểm L&acirc;m Phong l&ugrave;i lại, cảnh vật xung quanh bắt đầu dần thay đổi dưới &aacute;nh nến, mọi thứ như bị lo&atilde;ng ra, tất cả đều chảy theo một hướng nhất định trong kh&ocirc;ng gian. Kh&ocirc;ng phải l&agrave; trở n&ecirc;n kh&ocirc;ng r&otilde; r&agrave;ng, chỉ c&oacute; thể n&oacute;i l&agrave; cảm gi&aacute;c kh&ocirc;ng r&otilde;.<br />\r\n<br />\r\nTan chảy &mdash; đ&acirc;y l&agrave; t&iacute;nh từ đầu ti&ecirc;n L&acirc;m Phong nghĩ đến.<br />\r\n<br />\r\nMọi thứ xung quanh trở n&ecirc;n v&ocirc; c&ugrave;ng mờ ảo, điều duy nhất L&acirc;m Phong c&oacute; thể nh&igrave;n thấy l&agrave; nụ cười tr&ecirc;n gương mặt của những người trong ph&ograve;ng vẫn kh&ocirc;ng thay đổi, dường như họ kh&ocirc;ng hề nhận ra sự thay đổi của kh&ocirc;ng gian, họ vẫn mỉm cười v&agrave; nh&igrave;n chằm chằm v&agrave;o cậu.<br />\r\n<br />\r\nL&acirc;m Phong kh&ocirc;ng d&aacute;m do dự, xoay người bỏ chạy, khi t&ocirc;ng cửa x&ocirc;ng ra liền chạy theo cầu thang l&ecirc;n lầu.<br />\r\n<br />\r\nĐ&uacute;ng, cậu nhớ rằng n&oacute; hướng l&ecirc;n, tất cả c&aacute;c con đường đều đi l&ecirc;n.<br />\r\n<br />\r\nGiọng n&oacute;i của Cao Thanh Thanh vẫn vang l&ecirc;n sau lưng: &quot;Diệp Kiều, em đi đ&acirc;u vậy, ngọn nến vẫn chưa thổi...&quot;<br />\r\n<br />\r\n&quot;Em chưa thổi...&quot;<br />\r\n<br />\r\n&quot;Thổi...&quot;<br />\r\n<br />\r\nL&acirc;m Phong kh&ocirc;ng biết m&igrave;nh đ&atilde; chạy bao l&acirc;u rồi. Cậu kh&ocirc;ng d&aacute;m nh&igrave;n lại ch&uacute;t n&agrave;o. Cậu cảm gi&aacute;c được mồ h&ocirc;i lạnh tr&ecirc;n tr&aacute;n trượt xuống l&ocirc;ng m&agrave;y, đang l&agrave; m&ugrave;a h&egrave;, nhưng L&acirc;m Phong lại r&otilde; r&agrave;ng cảm thấy lỗ ch&acirc;n l&ocirc;ng to&agrave;n th&acirc;n đều đang run rẩy.<br />\r\n<br />\r\nMột cơn ớn lạnh từ sau lưng L&acirc;m Phong truyền đến đỉnh đầu.<br />\r\n<br />\r\nM&ugrave;i trong h&agrave;nh lang thật sự rất kh&oacute; chịu, m&ugrave;i tanh của oxit sắt v&agrave; m&ugrave;i chua của qu&aacute; tr&igrave;nh l&ecirc;n men, nhưng l&uacute;c n&agrave;y L&acirc;m Phong lại kh&ocirc;ng thể quan t&acirc;m nhiều như vậy. L&acirc;m Phong kh&ocirc;ng d&aacute;m dừng lại v&agrave; thở gấp cho đến khi cậu kh&ocirc;ng c&ograve;n nghe thấy giọng n&oacute;i r&ugrave;ng rợn của Cao Thanh Thanh. Cậu duỗi tay muốn giữ chặt bức tường, nhưng vừa chạm v&agrave;o bức tường, c&aacute;i chạm v&agrave;o mềm mại v&agrave; ẩm ướt của b&agrave;n tay đ&atilde; l&agrave;m tr&aacute;i tim cậu chấn động, v&ocirc; thức rụt tay lại.<br />\r\n<br />\r\nChết tiệt, điều n&agrave;y sẽ kh&ocirc;ng thực sự--<br />\r\n<br />\r\nMặc d&ugrave; &yacute; nghĩ n&agrave;y chỉ tho&aacute;ng qua, nhưng L&acirc;m Phong vẫn sợ đến to&aacute;t mồ h&ocirc;i lạnh. Cảm gi&aacute;c ngột ngạt v&agrave; thiếu dưỡng kh&iacute; khiến cậu thở gấp g&aacute;p, v&agrave; cậu cảm thấy cổ m&igrave;nh như bị siết chặt bởi một đ&ocirc;i tay v&ocirc; h&igrave;nh.<br />\r\n<br />\r\nV&igrave; vậy L&acirc;m Phong v&ocirc; thức đưa tay k&eacute;o cổ &aacute;o sơ mi. C&oacute; lẽ do d&ugrave;ng lực qu&aacute; mạnh, c&ugrave;ng với &acirc;m thanh &quot;bộp bộp&quot;, chiếc n&uacute;t &aacute;o tr&ecirc;n cổ &aacute;o sơ mi đ&atilde; bị x&eacute; toạc ra.<br />\r\n<br />\r\nNhưng L&acirc;m Phong cảm thấy hiện tại ngay cả sinh mệnh của m&igrave;nh cũng sắp mất rồi, c&ograve;n rảnh rỗi đ&acirc;u m&agrave; chăm s&oacute;c một c&aacute;i n&uacute;t &aacute;o nhỏ.<br />\r\n<br />\r\nCậu chỉ c&oacute; thể tiếp tục n&acirc;ng cao ch&acirc;n của m&igrave;nh v&agrave; chạy l&ecirc;n. D&ugrave; kh&ocirc;ng biết m&igrave;nh l&agrave; người đứng đầu từ l&uacute;c n&agrave;o.<br />\r\n<br />\r\nL&acirc;m Phong chưa kịp đi v&agrave;i bước, đột nhi&ecirc;n từ xa vang l&ecirc;n tiếng chu&ocirc;ng điện thoại di động gi&ograve;n gi&atilde;.<br />\r\n<br />\r\nL&acirc;m Phong sững sờ trong chốc l&aacute;t, sau đ&oacute; &yacute; thức dần dần mờ mịt, vặn vẹo giống như kh&ocirc;ng gian vừa rồi.<br />\r\n<br />\r\nNhận ra m&igrave;nh lại ngồi ở trước m&aacute;y t&iacute;nh trong ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp, L&acirc;m Phong trở n&ecirc;n hưng phấn, m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh vẫn l&agrave; tr&ograve; chơi tạm dừng trước khi rời đi.<br />\r\n<br />\r\n&quot;Đệt... Cũng may, thật sự l&agrave; một giấc mơ, h&ugrave; chết người...&quot; L&acirc;m Phong thở d&agrave;i một hơi ng&atilde; xuống lưng ghế ph&iacute;a sau. May mắn thay, cơn &aacute;c mộng n&agrave;y đ&atilde; qua đi.<br />\r\n<br />\r\nThở một hơi, L&acirc;m Phong nhớ tới vừa rồi c&oacute; tiếng chu&ocirc;ng điện thoại, liền ph&aacute;t hiện điện thoại vẫn đang đổ chu&ocirc;ng. Cậu nhanh ch&oacute;ng vươn tay cầm lấy điện thoại tr&ecirc;n b&agrave;n bấm n&uacute;t trả lời.<br />\r\n<br />\r\n&quot;T&ocirc;i n&oacute;i n&agrave;y Phong Tử, cậu nghe điện thoại l&acirc;u vậy? H&ocirc;m nay l&agrave; sinh nhật của cậu. Tối nay, anh em ch&uacute;ng ta sẽ c&ugrave;ng nhau ăn mừng.&quot; Ngay khi L&acirc;m Phong định trả lời điện thoại, một giọng n&oacute;i quen thuộc vang l&ecirc;n.<br />\r\n<br />\r\nĐ&oacute; l&agrave; anh em tốt của cậu Dương Quần.<br />\r\n<br />\r\nDương Quần:&quot;Theo thường lệ, ch&uacute;ng ta sẽ đến đ&oacute; v&agrave;o buổi tối sớm. Con kh&ocirc;ng cần chuẩn bị g&igrave; cả. C&aacute;c ba ba sẽ thu xếp cho con.&quot;<br />\r\n<br />\r\n&quot;Lăn lăn lăn, cậu mới l&agrave; con t&ocirc;i&quot; Giọng điệu quen thuộc tr&ecirc;n điện thoại khiến L&acirc;m Triệt Phong dễ chịu hơn rất nhiều, khụ khụ một tiếng, &quot;Đ&atilde; biết, đ&atilde; biết. T&ocirc;i chờ xem mấy cậu nghĩ ra c&aacute;i g&igrave; lừa gạt t&ocirc;i.&quot;<br />\r\n<br />\r\nL&acirc;m Phong cười cười, đặt điện thoại xuống.<br />\r\n<br />\r\nNhưng cậu nhận ra quần &aacute;o của m&igrave;nh ướt đẫm mồ h&ocirc;i.<br />\r\n<br />\r\n&quot;C&aacute;i qu&aacute;i g&igrave;, sao lại đổ mồ h&ocirc;i... Trong khoảng thời gian n&agrave;y c&oacute; mệt qu&aacute; kh&ocirc;ng? Sao chỉ ph&aacute;t trực tiếp lại đổ mồ h&ocirc;i.&quot; L&acirc;m Phong nh&iacute;u m&agrave;y, tr&ecirc;n lưng m&aacute;t lạnh khiến cậu cảm thấy c&oacute; ch&uacute;t kh&oacute; chịu, vội v&agrave;ng đứng dậy đi v&agrave;o ph&ograve;ng tắm để thay &aacute;o sơ mi.<br />\r\n<br />\r\nKhi vừa đứng dậy, cậu nghe thấy tiếng chai nước rơi. L&acirc;m Phong nh&igrave;n thấy một chai Coca lăn dưới ch&acirc;n m&igrave;nh.<br />\r\n<br />\r\nChờ đ&atilde;...<br />\r\n<br />\r\nsao lại c&oacute; chai coca n&agrave;y!<br />\r\n<br />\r\nL&acirc;m Phong lập tức nhận ra c&oacute; g&igrave; đ&oacute; kh&ocirc;ng đ&uacute;ng. Cảm gi&aacute;c bối rối đ&oacute; lại tr&agrave;n v&agrave;o t&acirc;m tr&iacute; cậu.<br />\r\n<br />\r\nVừa rồi kh&ocirc;ng phải l&agrave; mơ sao...<br />\r\n<br />\r\nL&acirc;m Phong vừa mới được thở ph&agrave;o nhẹ nh&otilde;m, b&acirc;y giờ thần kinh trong đại n&atilde;o lại căng thẳng.<br />\r\n<br />\r\nNhưng nếu l&agrave; mua coca thật, cậu trở lại ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp từ khi n&agrave;o?<br />\r\n<br />\r\nR&otilde; r&agrave;ng l&agrave; vừa rồi cậu đang ngồi trong ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp, v&agrave; tr&ograve; chơi vẫn đang diễn ra, nhưng cậu kh&ocirc;ng nhớ g&igrave; về qu&aacute; tr&igrave;nh quay lại ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp sau khi mua coca.<br />\r\n<br />\r\nNhưng nếu l&agrave; mơ th&igrave; l&agrave;m sao giải th&iacute;ch được chai coca n&agrave;y đ&acirc;y?<br />\r\n<br />\r\nTrong chốc l&aacute;t, L&acirc;m Phong cảm thấy sau đầu c&oacute; một tia r&acirc;m ran. Cậu c&oacute; thể cảm nhận r&otilde; những hạt mồ h&ocirc;i bắt đầu trượt xuống lưng.<br />\r\n<br />\r\nL&acirc;m Phong h&iacute;t s&acirc;u một hơi, cậu cố gắng hết sức để kh&ocirc;ng nghĩ đến những điều n&agrave;y - người ta thường n&oacute;i, tự h&ugrave; dọa m&igrave;nh cũng g&acirc;y chết người, v&agrave; cậu kh&ocirc;ng thể khiến bản th&acirc;n th&ecirc;m sợ h&atilde;i được.<br />\r\n<br />\r\nL&acirc;m Phong lấy lại b&igrave;nh tĩnh, cậu quyết định vẫn l&agrave; thay quần &aacute;o trước đi, nếu kh&ocirc;ng sẽ bị cảm lạnh, thật l&agrave; mất hứng.<br />\r\n<br />\r\nL&acirc;m Phong mở tủ lấy ra một chiếc &aacute;o ph&ocirc;ng. Nhưng m&agrave;, khi cậu giơ tay muốn cởi c&uacute;c &aacute;o tr&ecirc;n người, đồng tử của cậu lập tức co r&uacute;t lại, ngay cả tim cũng như ngừng đập.<br />\r\n<br />\r\n&ldquo;N&uacute;t, n&uacute;t tr&ecirc;n cổ &aacute;o của m&igrave;nh đ&acirc;u rồi?&rdquo;<br />\r\n<br />\r\nKhoảnh khắc L&acirc;m Phong nhận ra n&uacute;t tr&ecirc;n cổ &aacute;o bị thiếu, cậu thấy mồ h&ocirc;i lạnh cố gắng kiềm chế lại chảy ra tr&ecirc;n tr&aacute;n. cậu bước nhanh v&agrave;o ph&ograve;ng tắm, cố gắng trấn tĩnh lại.<br />\r\n<br />\r\nL&acirc;m Phong h&iacute;t s&acirc;u một hơi, ngẩng đầu nh&igrave;n ch&iacute;nh m&igrave;nh trong gương.<br />\r\n<br />\r\nTuy nhi&ecirc;n, c&acirc;u trả lời l&agrave; tất cả những điều n&agrave;y đều đ&atilde; xảy ra, v&agrave; n&uacute;t &aacute;o của cậu thực sự bị thiếu. L&acirc;m Phong thậm ch&iacute; c&oacute; thể nh&igrave;n qua gương, thấy quần &aacute;o tr&ecirc;n người vẫn c&ograve;n nguy&ecirc;n sợi chỉ sau khi n&uacute;t &aacute;o bị x&eacute; ra.</h2>', 0, 'chuong-1-dreamland'),
(12, 10, 'Chương 2: Cuốn sách kỳ lạ', 'Chương 2: Cuốn sách kỳ lạ', '<h2>L&acirc;m Phong thở d&agrave;i thườn thượt, cầm lấy khăn lau mặt.<br />\r\n<br />\r\n&ldquo;Chỉ l&agrave; mơ th&ocirc;i&rdquo; L&acirc;m Phong thấp giọng an ủi bản th&acirc;n.<br />\r\n<br />\r\n&quot;Hẳn l&agrave; l&uacute;c trước bị rơi m&agrave; qu&ecirc;n kh&acirc;u lại, c&oacute; thể do ban ng&agrave;y nghĩ ban đ&ecirc;m nằm mơ, ng&agrave;y nghĩ đ&ecirc;m mộng&hellip;&rdquo;<br />\r\n<br />\r\nNhưng L&acirc;m Phong vẫn cảm nhận được vạn vật xung quanh dường như đều lộ ra một luồng hơi thở t&ecirc; liệt cho d&ugrave; c&oacute; nh&igrave;n như thế n&agrave;o đi nữa. R&otilde; r&agrave;ng đ&acirc;y l&agrave; căn nh&agrave; m&agrave; cậu đ&atilde; sống mấy năm nay, nhưng hiện tại cậu cảm thấy c&oacute; điều g&igrave; đ&oacute; kh&ocirc;ng ổn.<br />\r\n<br />\r\nNhưng cho d&ugrave; L&acirc;m Phong c&oacute; cảnh gi&aacute;c quan s&aacute;t xung quanh như thế n&agrave;o, cho d&ugrave; c&oacute; n&iacute;n thở, cuối c&ugrave;ng cũng kh&ocirc;ng nh&igrave;n thấy g&igrave;.<br />\r\n<br />\r\nSau khi L&acirc;m Phong lẩm bẩm &quot;Thịnh vượng, D&acirc;n chủ, Thống nhất v&agrave; Hữu nghị&quot; trong l&ograve;ng thầm nghĩ, cậu kh&ocirc;ng muốn nghĩ th&ecirc;m nữa. Tất nhi&ecirc;n, cậu kh&ocirc;ng d&aacute;m.<br />\r\n<br />\r\n&ldquo;Chết tiệt!&rdquo;<br />\r\n<br />\r\nSau khi tắt v&ograve;i nước, L&acirc;m Phong mới nhớ ra h&igrave;nh như buổi ph&aacute;t s&oacute;ng trực tiếp của cậu vẫn chưa tắt. Nhạc nền của tr&ograve; chơi trong ph&ograve;ng truyền đến ph&ograve;ng tắm một c&aacute;ch yếu ớt, thậm ch&iacute; thỉnh thoảng c&ograve;n c&oacute; một hoặc hai lời nhắc của hệ thống &quot;t&iacute;ch...t&iacute;ch&quot;.<br />\r\n<br />\r\nL&acirc;m Phong nhanh ch&oacute;ng quay trở lại m&aacute;y t&iacute;nh, nh&igrave;n thấy m&agrave;n h&igrave;nh hiện l&ecirc;n đầy rẫy l&agrave;n đạn chữ đỏ đỏ xanh xanh đang cuộn l&ecirc;n lần lượt, kh&ocirc;ng biết tại sao, l&uacute;c L&acirc;m Phong nh&igrave;n những lời nhắc tr&ecirc;n thanh chắn quen thuộc n&agrave;y lại cảm thấy kh&ocirc;ng thể giải th&iacute;ch được.<br />\r\n<br />\r\nNhưng c&ocirc;ng việc vẫn phải ho&agrave;n th&agrave;nh. L&acirc;m Phong vừa xoa xoa th&aacute;i dương vừa đeo tai nghe.<br />\r\n<br />\r\n&quot;Buổi ph&aacute;t s&oacute;ng trực tiếp h&ocirc;m nay kết th&uacute;c tại đ&acirc;y, hẹn gặp lại c&aacute;c bạn v&agrave;o 11 giờ tối mai! Y&ecirc;u c&aacute;c bạn!&quot; Sau khi kết th&uacute;c tập ph&aacute;t s&oacute;ng vừa rồi, cậu đương nhi&ecirc;n kh&ocirc;ng hề c&ograve;n t&acirc;m trạng để tiếp tục ph&aacute;t s&oacute;ng, hơn nữa theo trạng th&aacute;i tinh thần hiện tại của cậu, ước t&iacute;nh rằng cậu sẽ kh&ocirc;ng bắt đầu tr&ograve; chơi nữa. Điều đ&oacute; kh&aacute; tốt.<br />\r\n<br />\r\nTạm biệt người h&acirc;m mộ, nhiệm vụ ph&aacute;t s&oacute;ng trực tiếp ng&agrave;y h&ocirc;m nay coi như miễn cưỡng ho&agrave;n th&agrave;nh. L&acirc;m Phong liếc nh&igrave;n điện thoại, cậu chợt nhớ tới cuộc gọi của Dương Quần.<br />\r\n<br />\r\nCậu t&iacute;nh đi tắm v&agrave; sẽ ch&agrave;o đ&oacute;n sự &ldquo;bất ngờ&rdquo; của đ&aacute;m bạn th&acirc;n v&agrave;o buổi tối. Cậu thực sự muốn xem mấy t&ecirc;n đ&oacute; b&agrave;y tr&ograve; g&igrave;.<br />\r\n<br />\r\nNhưng ngay l&uacute;c vừa ra khỏi ph&ograve;ng ph&aacute;t s&oacute;ng trực tiếp, một l&agrave;n đạn tr&ecirc;n m&agrave;n h&igrave;nh đập v&agrave;o mắt L&acirc;m Phong.<br />\r\n<br />\r\n&ldquo;Đại ca, cậu cũng xem &quot;613&quot; hả?&quot;<br />\r\n<br />\r\n&quot;S&aacute;u một ba&quot;<br />\r\n<br />\r\nTừ n&agrave;y l&agrave;m L&acirc;m Phong tự nhi&ecirc;n giật m&igrave;nh.<br />\r\n<br />\r\nL&acirc;m Phong vừa hồi phục sau cơn &aacute;c mộng vừa rồi, b&acirc;y giờ cậu nh&igrave;n một trận đạn kh&ocirc;ng đầu kh&ocirc;ng đu&ocirc;i n&agrave;y, tr&aacute;i tim lại nhảy dựng. Đến khi cậu hồi phục, cậu thấy m&igrave;nh đ&atilde; nhấn n&uacute;t đ&oacute;ng trang web.<br />\r\n<br />\r\nL&acirc;m Phong cau m&agrave;y.<br />\r\n<br />\r\nCậu kh&ocirc;ng nhớ l&agrave; m&igrave;nh đọc bất kỳ cuốn s&aacute;ch n&agrave;o c&oacute; t&ecirc;n &quot;613&quot; n&agrave;y, thậm ch&iacute; chưa bao giờ thấy một cuốn s&aacute;ch c&oacute; số như vậy.<br />\r\n<br />\r\nM&agrave;n h&igrave;nh điện thoại di động l&uacute;c n&agrave;y s&aacute;ng l&ecirc;n, thời gian hiển thị tr&ecirc;n m&agrave;n h&igrave;nh kh&oacute;a lại k&iacute;ƈɦ ŧɦ&iacute;ƈɦ cậu.<br />\r\n<br />\r\n- 6:13!<br />\r\n<br />\r\nL&acirc;m Phong ngẩng đầu, &aacute;nh mắt lại rơi v&agrave;o tr&ecirc;n m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh, trong nh&aacute;y mắt, dường như bất chợt nghĩ tới c&aacute;i g&igrave;, liền quay đầu lại.<br />\r\n<br />\r\nNh&igrave;n dọc theo vị tr&iacute; m&agrave; camera tr&ecirc;n m&aacute;y t&iacute;nh c&oacute; thể quay, cậu thấy tr&ecirc;n gi&aacute; s&aacute;ch ph&iacute;a sau m&igrave;nh, giữa truyện tranh Nhật Bản v&agrave; nhiều tiểu thuyết trực tuyến kh&aacute;c nhau, một cuốn s&aacute;ch d&agrave;y đột nhi&ecirc;n xuất hiện.<br />\r\n<br />\r\nBăng qua một lối đi nhỏ đi đến, L&acirc;m Phong c&oacute; thể nh&igrave;n thấy d&ograve;ng chữ &quot;613&quot; được viết tr&ecirc;n b&igrave;a của cuốn s&aacute;ch.<br />\r\n<br />\r\n&ldquo;613&hellip;!&rdquo;<br />\r\n<br />\r\nL&acirc;m Phong sửng sốt đứng l&ecirc;n. Cậu kh&ocirc;ng quan t&acirc;m đến c&aacute;i ghế bị ng&atilde; xuống đất, vội v&agrave;ng chạy đến gi&aacute; s&aacute;ch l&ocirc;i cuốn s&aacute;ch c&oacute; t&ecirc;n &ldquo;613&rdquo; ra khỏi kệ.<br />\r\n<br />\r\nBao b&igrave; của cuốn s&aacute;ch n&agrave;y ho&agrave;n to&agrave;n kh&ocirc;ng ph&ugrave; hợp với những cuốn s&aacute;ch xung quanh, b&igrave;a m&agrave;u đỏ sẫm đ&atilde; bị sờn, nh&igrave;n như được l&agrave;m bằng da b&ograve;, đ&atilde; rất cũ. Cuốn s&aacute;ch được buộc đơn giản v&agrave; th&ocirc; sơ bằng loại d&acirc;y cứng v&agrave; nhỏ, được đặt trong một đống s&aacute;ch viễn tưởng phương T&acirc;y. N&oacute; thực sự tr&ocirc;ng giống cuốn sổ ghi ch&eacute;p của một ph&aacute;p sư để ghi lại một số c&acirc;u thần ch&uacute;.<br />\r\n<br />\r\nL&acirc;m Phong cầm cuốn s&aacute;ch trong tay, cậu cảm thấy cuốn &quot;613&quot; n&agrave;y nhẹ như kh&ocirc;ng trọng lượng, m&ugrave;i ẩm mốc thoang thoảng tho&aacute;t ra từ c&aacute;c trang s&aacute;ch, l&agrave;m cậu nghẹn cả mũi.<br />\r\n<br />\r\nL&acirc;m Phong giơ tay l&ecirc;n, đầu ng&oacute;n tay vừa chạm v&agrave;o d&acirc;y buộc, h&igrave;nh như n&oacute; bị cột rất nhiều v&ograve;ng, g&atilde;y th&agrave;nh mấy đoạn liền rơi tr&ecirc;n mặt đất.<br />\r\n<br />\r\nTr&aacute;i tim L&acirc;m Phong nhảy dựng, cậu cẩn thận mở s&aacute;ch ra.<br />\r\n<br />\r\nQuả nhi&ecirc;n, trang s&aacute;ch b&ecirc;n trong cũng giống như b&igrave;a b&ecirc;n ngo&agrave;i của n&oacute;, đ&atilde; ố v&agrave;ng rất nhiều, nhưng L&acirc;m Phong ph&aacute;t hiện mực tr&ecirc;n trang vẫn chưa kh&ocirc;, dưới &aacute;nh đ&egrave;n vẫn c&ograve;n hơi phản chiếu.<br />\r\n<br />\r\nR&otilde; r&agrave;ng l&agrave; mới được viết ra.<br />\r\n<br />\r\nL&acirc;m Phong v&ocirc; thức c&uacute;i đầu nh&igrave;n lướt qua nội dung cụ thể, nhưng ch&iacute;nh c&aacute;i liếc mắt n&agrave;y khiến h&ocirc; hấp của cậu chốc l&aacute;t ngưng trệ.</h2>', 0, 'chuong-2-cuon-sach-ky-la');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(13, 11, 'Chương 1: Bầu Không Khí Kỳ Lạ', 'Chương 1: Bầu Không Khí Kỳ Lạ', '<h2>- Thỏ ơi, cậu biết g&igrave; chưa? Cuộc thi sẽ bị l&ugrave;i lại một ng&agrave;y đ&oacute;.<br />\r\n<br />\r\n- Vậy l&agrave; ng&agrave;y kia thi chứ kh&ocirc;ng phải l&agrave; ng&agrave;y mai sao?<br />\r\n<br />\r\n- Đ&uacute;ng vậy.<br />\r\n<br />\r\n- Nếu vậy, ch&uacute;ng ta h&atilde;y c&ugrave;ng nhau cố gắng nh&eacute;! Mong mọi người sẽ gi&uacute;p đỡ th&ecirc;m cho m&igrave;nh nh&eacute;!<br />\r\n<br />\r\n...<br />\r\n<br />\r\nNg&ocirc;i trường cấp ba Star được mệnh danh l&agrave; một trong những ng&ocirc;i trường h&agrave;ng đầu tại Việt Nam. Đ&acirc;y l&agrave; ng&ocirc;i trường do ch&iacute;nh phủ lập ra nhằm gi&uacute;p đỡ c&aacute;c học sinh c&oacute; điều kiện kinh tế kh&oacute; khăn c&oacute; cơ hội được đi học. Tuy nhi&ecirc;n điều m&agrave; họ kh&ocirc;ng thể ngờ rằng, nhờ v&agrave;o ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch học tập của m&igrave;nh m&agrave; c&oacute; rất nhiều học sinh học tập tại trường đ&atilde; c&oacute; th&agrave;nh t&iacute;ch rất cao, thậm ch&iacute;, đ&atilde; c&oacute; rất nhiều học sinh đ&atilde; tham gia v&agrave; đoạt giải trong c&aacute;c cuộc thi quốc tế. Tiếng l&agrave;nh đồn xa, Star trở th&agrave;nh một m&ocirc;i trường trong mơ của rất nhiều phụ huynh v&agrave; dần dần trở th&agrave;nh miếng mồi b&eacute;o bở của c&aacute;c nh&agrave; đầu tư.<br />\r\n<br />\r\nChỉ trong năm năm, từ một ng&ocirc;i trường chỉ c&oacute; hai t&ograve;a nh&agrave;, mỗi t&ograve;a nh&agrave; 4 tầng; đến nay, Star đ&atilde; được quy m&ocirc; v&agrave; mở rộng với diện t&iacute;ch tr&ecirc;n 1000m2 với đầy đủ những tiện nghi như căn-tin, thư viện, khu&ocirc;n vi&ecirc;n trường v&agrave; v&ocirc; số thứ kh&aacute;c nữa. Đương nhi&ecirc;n, đi đ&ocirc;i với c&ocirc;ng cuộc cải tạo n&agrave;y sẽ l&agrave; sự thay đổi rất lớn đến từ c&aacute;c ch&iacute;nh s&aacute;ch d&agrave;nh cho học vi&ecirc;n. Nếu như trước kia, Star chỉ l&agrave; ng&ocirc;i trường d&agrave;nh cho học sinh ngh&egrave;o với mức học ph&iacute; gần như l&agrave; miễn ph&iacute; th&igrave; giờ đ&acirc;y, học sinh ngh&egrave;o chỉ chiếm 20% học sinh to&agrave;n trường, 80% c&ograve;n lại thuộc về c&aacute;c học vi&ecirc;n c&oacute; gia thế gi&agrave;u c&oacute;.<br />\r\n<br />\r\nNhưng c&oacute; lẽ, đ&oacute; kh&ocirc;ng phải l&agrave; điều ngạc nhi&ecirc;n nhất tại ng&ocirc;i trường n&agrave;y. Ng&ocirc;i trường n&agrave;y, thực chất, c&ograve;n c&oacute; một b&iacute; mật v&ocirc; c&ugrave;ng kinh khủng kh&aacute;c li&ecirc;n quan đến 12 con gi&aacute;p.<br />\r\n<br />\r\n...<br />\r\n<br />\r\nTại lớp 10C,<br />\r\n<br />\r\nMột khung cảnh lớp học buổi s&aacute;ng v&ocirc; c&ugrave;ng lộn xộn. Học sinh đi ra đi v&agrave;o, cười n&oacute;i vui vẻ. Tiếng n&oacute;i chuyện nghe r&acirc;m ran ở khắp nơi trong lớp học. Tiếng cười đ&ugrave;a, chạy nhảy v&ocirc; c&ugrave;ng n&aacute;o nhiệt.<br />\r\n<br />\r\n<br />\r\n<br />\r\nReng... reng... reng...<br />\r\n<br />\r\nTiếng chu&ocirc;ng đ&uacute;ng 7h s&aacute;ng cất l&ecirc;n vang vọng cả kh&ocirc;ng gian. Học sinh nhốn nh&aacute;o ngồi v&agrave;o chỗ, chỉ khoảng v&agrave;i ph&uacute;t sau, to&agrave;n bộ học sinh đ&atilde; ngồi y&ecirc;n lặng, hai tay chắp l&ecirc;n mặt b&agrave;n, t&aacute;c phong v&ocirc; c&ugrave;ng nghi&ecirc;m chỉnh.<br />\r\n<br />\r\nTừ cửa lớp, b&oacute;ng d&aacute;ng một người phụ nữ cao, t&oacute;c buộc gọn sau g&aacute;y, gương mặt to&aacute;t l&ecirc;n vẻ th&acirc;n thiện dễ gần, bước v&agrave;o lớp<br />\r\n<br />\r\n- Cả lớp đứng!<br />\r\n<br />\r\nTiếng một học sinh vang l&ecirc;n lảnh l&oacute;t trong kh&ocirc;ng gian. Sau tiếng hiệu lệnh ấy, cả lớp đồng loạt đứng l&ecirc;n.<br />\r\n<br />\r\nC&ocirc; gi&aacute;o bước l&ecirc;n ch&iacute;nh giữa bục giục, nh&igrave;n lướt qua lớp một l&uacute;c rồi mỉm cười, ra hiệu cả lớp ngồi xuống. Khi tất cả học sinh đ&atilde; ngồi lại nghi&ecirc;m chỉnh, c&ocirc; gi&aacute;o n&oacute;i:<br />\r\n<br />\r\n- Ch&agrave;o mừng c&aacute;c em đ&atilde; đến với Star. C&ocirc; t&ecirc;n l&agrave; Minh Nguyệt, gi&aacute;o vi&ecirc;n chủ nhiệm trong ba năm tới của c&aacute;c em đồng thời l&agrave; gi&aacute;o vi&ecirc;n dạy To&aacute;n. Mong rằng qu&atilde;ng thời gian n&agrave;y ch&uacute;ng ta c&oacute; thể hợp t&aacute;c tốt với nhau.<br />\r\n<br />\r\nHọc sinh ngay lập tức trở n&ecirc;n cười n&oacute;i vui vẻ, bắt đầu lại xuất hiện một v&agrave;i tiếng n&oacute;i chuyện, khu&ocirc;n mặt ai cũng v&ocirc; c&ugrave;ng mong ng&oacute;ng v&agrave; hạnh ph&uacute;c.<br />\r\n<br />\r\n- H&ocirc;m nay, c&ocirc; sẽ giới thiệu một em học sinh mới, vừa c&oacute; danh s&aacute;ch v&agrave;o lớp m&igrave;nh n&ecirc;n kh&ocirc;ng thể gặp mặt c&aacute;c em v&agrave;o ng&agrave;y lễ khai giảng được. - C&ocirc; dừng lại rồi hướng mắt ra cửa - V&agrave;o đi em!<br />\r\n<br />\r\nTo&agrave;n bộ 36 đ&ocirc;i mắt đồng loạt hướng về ph&iacute;a cửa, xung quanh dường như im bặt. Phải đến một v&agrave;i ph&uacute;t sau, một b&oacute;ng d&aacute;ng của một nữ sinh mới từ từ tiến v&agrave;o trong lớp học.<br />\r\n<br />\r\nC&ocirc; bạn n&agrave;y c&oacute; một m&aacute;i t&oacute;c m&agrave;u n&acirc;u, d&agrave;i, để x&otilde;a tự nhi&ecirc;n; đ&ocirc;i mắt m&agrave;u n&acirc;u đen, đ&ocirc;i m&ocirc;i nhỏ, d&aacute;ng vẻ ngượng ngập lộ ra r&otilde; m&ograve;n một. Từng bước đi v&ocirc; c&ugrave;ng e d&egrave;, c&oacute; ch&uacute;t sợ h&atilde;i, một b&ecirc;n tay lu&ocirc;n để trước ngực, &aacute;nh mắt kh&ocirc;ng d&aacute;m nh&igrave;n thẳng, li&ecirc;n tục hướng xuống đất.<br />\r\n<br />\r\nSự xuất hiện của c&ocirc; g&aacute;i đ&atilde; khiến cả lớp ồ l&ecirc;n v&ocirc; c&ugrave;ng kinh ngạc, đặc biệt l&agrave; c&aacute;c bạn nam. Bởi lẽ, c&ocirc; ấy v&ocirc; c&ugrave;ng dễ thương, tạo cho người đối diện cảm gi&aacute;c muốn được che chở.<br />\r\n<br />\r\n<br />\r\n<br />\r\n- N&agrave;o, em h&atilde;y giới thiệu về bản th&acirc;n một ch&uacute;t đi. - C&ocirc; gi&aacute;o hướng về ph&iacute;a c&ocirc; n&oacute;i<br />\r\n<br />\r\nC&ocirc; g&aacute;i c&oacute; vẻ hơi hoảng loạn khi bắt gặp &aacute;nh mắt như hổ vồ của mọi người trong lớp. Một l&uacute;c sau, c&ocirc; h&iacute;t một hơi thật s&acirc;u rồi bất ngờ c&uacute;i gập người, h&eacute;t lớn:<br />\r\n<br />\r\n- Ch&agrave;o mọi người, m&igrave;nh l&agrave; H&acirc;n Nhi, mong được mọi người gi&uacute;p đỡ!<br />\r\n<br />\r\nThấy phản ứng c&oacute; phần hơi th&aacute;i qu&aacute; của c&ocirc;, một số người trong lớp từ ngạc nhi&ecirc;n đến bật cười v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;. Nghe thấy tiếng cười của mọi, H&acirc;n Nhi từ từ đứng thẳng l&ecirc;n, đ&ocirc;i mắt mở to tỏ vẻ v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;.<br />\r\n<br />\r\n- Được rồi, được rồi. - C&ocirc; gi&aacute;o vỗ hai tay v&agrave;o với nhau tỏ &yacute; y&ecirc;u cầu mọi người trật tự - Xem n&agrave;o, em ngồi đ&acirc;u được nhỉ?<br />\r\n<br />\r\nC&ocirc; gi&aacute;o đảo mắt xung quanh lớp một hồi, khi mắt chạm đến chỗ ngồi trống duy nhất, bất chợt &aacute;nh mắt c&oacute; ch&uacute;t hơi e d&egrave;. Tuy nhi&ecirc;n, khi c&ocirc; lại một lần nữa v&ocirc; t&igrave;nh nh&igrave;n thấy &aacute;nh mắt v&ocirc; c&ugrave;ng chờ đợi của c&ocirc;, b&egrave;n chậc lưỡi rồi n&oacute;i:<br />\r\n<br />\r\n- H&acirc;n Nhi, em xuống b&agrave;n cuối c&ugrave;ng ngồi nh&eacute;!<br />\r\n<br />\r\nC&ocirc; gật đầu mừng rỡ rồi nhanh ch&oacute;ng bước về ph&iacute;a cuối lớp. Bước ch&acirc;n c&ocirc; từ từ chậm lại khi bất ngờ nhận ra những tiếng x&igrave; x&agrave;o đang dần đổ về ph&iacute;a của m&igrave;nh. Mọi người đều l&eacute;n l&uacute;t nh&igrave;n c&ocirc;, &aacute;nh mắt v&ocirc; c&ugrave;ng kh&aacute;c lạ. Khi c&ocirc; định quay sang nh&igrave;n họ để ch&agrave;o hỏi th&igrave; họ đột ngột thu tầm mắt về, l&agrave;m bộ kh&ocirc;ng thấy g&igrave; cả.<br />\r\n<br />\r\nH&acirc;n Nhi nhanh ch&oacute;ng thu gọn s&aacute;ch vở ngăn nắp v&agrave;o ngăn b&agrave;n, chỉ giữ lại quyển s&aacute;ch gi&aacute;o khoa m&ocirc;n To&aacute;n sẽ học trong tiết n&agrave;y, ngoan ngo&atilde;n chờ c&ocirc; gi&aacute;o giảng b&agrave;i.<br />\r\n<br />\r\nBầu kh&ocirc;ng kh&iacute; dường như c&oacute; g&igrave; đ&oacute; rất lạ. C&ocirc; v&ocirc; t&igrave;nh quay lại chỗ ngồi b&ecirc;n cạnh m&igrave;nh c&ograve;n trống, chỉ c&oacute; duy nhất một chiếc cặp m&agrave;u đen cất gọn trong ngăn b&agrave;n.<br />\r\n<br />\r\nH&igrave;nh như b&ecirc;n cạnh c&ocirc; vẫn c&ograve;n c&oacute; ai đ&oacute;....</h2>', 0, 'chuong-1-bau-khong-khi-ky-la'),
(14, 11, 'Chương 2: Khởi Đầu Không Được Suôn Sẻ', 'Chương 2: Khởi Đầu Không Được Suôn Sẻ', '<h2>Reng... Reng...<br />\r\n<br />\r\nTiếng chu&ocirc;ng lớp học lại một lần nữa vang l&ecirc;n, sau 45p d&agrave;i đằng đẵng. Sau khi gi&aacute;o vi&ecirc;n bước khỏi lớp, c&ocirc; từ từ đ&oacute;ng s&aacute;ch lại, thở d&agrave;i một hơi. Đ&uacute;ng l&agrave; m&ocirc;n To&aacute;n vẫn l&agrave; một vấn đề kh&aacute; nan giải với c&ocirc;.<br />\r\n<br />\r\nKhi c&ocirc; vừa cất s&aacute;ch v&agrave;o cặp, ngước mắt l&ecirc;n để chạm phải ngay &aacute;nh mắt của một bạn nữ, khu&ocirc;n mặt đang d&iacute; s&aacute;t v&agrave;o mặt c&ocirc;, tr&ecirc;n m&ocirc;i mim cười rạng rỡ<br />\r\n<br />\r\n- A... - C&ocirc; bất ngờ thốt l&ecirc;n, người v&ocirc; thức l&ugrave;i về ph&iacute;a sau<br />\r\n<br />\r\n- Ch&agrave;o cậu. - C&ocirc; bạn đ&oacute; l&ecirc;n tiếng trước, sau đ&oacute; nhanh ch&oacute;ng đứng b&ecirc;n cạnh c&ocirc; - M&igrave;nh l&agrave; Mỹ Anh, người ngồi ngay tr&ecirc;n b&agrave;n của cậu. Rất vui được l&agrave;m quen với cậu.<br />\r\n<br />\r\nN&oacute;i rồi, Mỹ Anh đưa tay ra định bắt tay với c&ocirc;. C&ocirc; l&uacute;c đầu cảm thấy hơi hoảng hốt, sau khi b&igrave;nh tĩnh lại mới chậm r&atilde;i đưa tay ra, l&iacute; nh&iacute; n&oacute;i:<br />\r\n<br />\r\n- Ch&agrave;o cậu, m&igrave;nh l&agrave; H&acirc;n Nhi. M&igrave;nh cũng rất vui được l&agrave;m quen với cậu<br />\r\n<br />\r\n- Th&ocirc;i n&agrave;o, việc g&igrave; phải sợ h&atilde;i thế chứ. M&igrave;nh đ&acirc;u c&oacute; ăn thịt cậu. - Mỹ Anh bật cười, sau đ&oacute; d&ugrave;ng tay vỗ mạnh l&ecirc;n vai c&ocirc;<br />\r\n<br />\r\n- &Agrave;... kh&ocirc;ng phải đ&acirc;u... M&agrave; l&agrave;... - C&ocirc; khẽ c&uacute;i gằm mặt xuống n&oacute;i l&iacute; nh&iacute;<br />\r\n<br />\r\nMỹ Anh quan s&aacute;t cử chỉ từ đầu đến cuối của c&ocirc;, sau khi nhận ra c&oacute; vẻ m&igrave;nh đ&atilde; v&ocirc; t&igrave;nh khiến c&ocirc; cảm thấy sợ, b&egrave;n nhất quyết k&eacute;o tay c&ocirc; đứng l&ecirc;n.<br />\r\n<br />\r\n- Ngồi m&atilde;i trong lớp, ch&aacute;n lắm. Ra ngo&agrave;i h&agrave;nh lang với m&igrave;nh đi!<br />\r\n<br />\r\nN&oacute;i rồi, kh&ocirc;ng kịp để c&ocirc; kịp n&oacute;i lời từ chối, Mỹ Anh đ&atilde; k&eacute;o c&ocirc; th&agrave;nh c&ocirc;ng ra ngo&agrave;i h&agrave;nh lang. Sau đ&oacute; vươn vai, h&iacute;t thở ra vẻ v&ocirc; c&ugrave;ng thoải m&aacute;i.<br />\r\n<br />\r\n- Thế n&agrave;o? Ra ngo&agrave;i n&agrave;y thoải m&aacute;i hơn nhiều, phải kh&ocirc;ng? - Mỹ Anh quay sang nh&igrave;n c&ocirc; cười n&oacute;i<br />\r\n<br />\r\n- &Agrave;, ừ. - C&ocirc; chậm r&atilde;i l&ecirc;n tiếng<br />\r\n<br />\r\nLớp học của hai người nằm ở tầng ba to&agrave; nh&agrave; C. Khung cảnh nh&igrave;n từ tr&ecirc;n cao thực sự rất đẹp. Đặc biệt l&agrave; đối với Star - một ng&ocirc;i trường mỗi năm nhận được khoản đầu tư l&ecirc;n đến con số h&agrave;ng tỷ đồng.<br />\r\n<br />\r\n- Nhưng m&agrave;, sao tr&ocirc;ng cậu l&uacute;c n&agrave;o cũng c&oacute; vẻ e d&egrave; thế? - Mỹ Anh nh&igrave;n d&aacute;ng vẻ ngại ng&ugrave;ng của c&ocirc;, lại bật cười<br />\r\n<br />\r\n<br />\r\n<br />\r\n- Kh&ocirc;ng phải... L&agrave; do, h&ocirc;m nay c&oacute; nhiều người nh&igrave;n v&agrave;o m&igrave;nh qu&aacute;. M&igrave;nh kh&ocirc;ng quen lắm....<br />\r\n<br />\r\n- L&agrave; vậy sao? - Mỹ Anh đ&aacute;p, tỏ vẻ kh&aacute; ngạc nhi&ecirc;n sau đ&oacute; lại tặc lưỡi n&oacute;i - Do cậu xinh đẹp đấy, điều đấy cậu c&ograve;n kh&ocirc;ng hiểu sao? Thật l&agrave; l&atilde;ng ph&iacute; cả một khu&ocirc;n mặt<br />\r\n<br />\r\n- M&igrave;nh m&agrave; xinh đẹp sao? - C&ocirc; bất ngờ l&uacute;ng t&uacute;ng - Kh&ocirc;ng phải đ&acirc;u... M&igrave;nh trước khi v&agrave;o lớp đ&atilde; nh&igrave;n thấy rất nhiều c&ocirc; g&aacute;i xinh đẹp ở trong trường rồi<br />\r\n<br />\r\n- Đ&uacute;ng vậy nhỉ?<br />\r\n<br />\r\nN&eacute;t mặt Mỹ Anh dường như trầm xuống đ&ocirc;i ch&uacute;t, quay người về ph&iacute;a trước, tay nắm l&ecirc;n th&agrave;nh lan can<br />\r\n<br />\r\n- Họ đều l&agrave; những tiểu thư danh gi&aacute;, l&agrave;m sao c&oacute; thể kh&ocirc;ng xinh đẹp được?<br />\r\n<br />\r\nNh&igrave;n thấy t&acirc;m trạng c&oacute; ch&uacute;t biến đổi của Mỹ Anh, c&ocirc; đột nhi&ecirc;n trở n&ecirc;n hoảng hốt, l&uacute;ng t&uacute;ng n&oacute;i đỡ lời:<br />\r\n<br />\r\n- &Yacute; m&igrave;nh l&agrave; họ thực sự rất xinh đẹp, m&igrave;nh chỉ l&agrave; người b&igrave;nh thường th&ocirc;i... Cậu cũng rất xinh đẹp nữa.<br />\r\n<br />\r\n- Vậy sao? - Mỹ Anh quay lại - Cậu l&agrave; người đầu ti&ecirc;n n&oacute;i m&igrave;nh xinh đẹp đấy. M&igrave;nh xinh đẹp ở điểm n&agrave;o?<br />\r\n<br />\r\n- &Agrave; th&igrave;..... - C&ocirc; lại ngập ngừng v&igrave; c&acirc;u hỏi bất ngờ của Mỹ Anh - Cậu xinh đẹp...từ nội t&acirc;m... Đ&uacute;ng rồi, cậu l&agrave; một người rất tốt<br />\r\n<br />\r\nKhi n&oacute;i hết c&acirc;u, c&ocirc; bất chợt ngẩng mặt l&ecirc;n nh&igrave;n Mỹ Anh. Nhưng thay v&igrave; vẻ l&uacute;ng t&uacute;ng, ngại ng&ugrave;ng như trước kia, c&ocirc; đ&atilde; mỉm cười rất tươi, cực k&igrave; rạng rỡ.<br />\r\n<br />\r\nMỹ Anh nghe thấy c&acirc;u trả lời từ c&ocirc; th&igrave; v&ocirc; c&ugrave;ng bất ngờ, sau đ&oacute; mỉm cười nhẹ rồi quay đi, n&oacute;i:<br />\r\n<br />\r\n- Đ&ugrave;a cậu th&ocirc;i, đồ ngốc!<br />\r\n<br />\r\n- Kh&ocirc;ng phải đ&acirc;u - C&ocirc; sợ rằng Mỹ Anh sẽ hiểu nhầm &yacute; của m&igrave;nh - M&igrave;nh thấy cậu xinh đẹp thật m&agrave;!<br />\r\n<br />\r\nThật ra, c&ocirc; n&oacute;i cũng kh&ocirc;ng phải ho&agrave;n to&agrave;n l&agrave; n&oacute;i dối. Mỹ Anh c&oacute; bề ngo&agrave;i kh&aacute; ưa nh&igrave;n, nhưng kh&ocirc;ng phải kiểu dịu d&agrave;ng, e lệ giống c&aacute;c tiểu thư quyền qu&yacute; nh&agrave; gi&agrave;u. T&oacute;c buộc cao tr&ocirc;ng đầy khỏe khoắn, đ&ocirc;i mắt s&aacute;ng, nụ cười tươi, giọng n&oacute;i to, r&otilde; r&agrave;ng khiến Mỹ Anh giống như một nữ cường bước ra từ truyện vậy.<br />\r\n<br />\r\nNhưng c&acirc;u n&oacute;i trước đ&oacute; của Mỹ Anh khiến c&ocirc; cảm thấy hơi buồn. Thực chất, nếu nh&igrave;n bề ngo&agrave;i, Star l&agrave; một m&ocirc;i trường đ&aacute;ng ngưỡng mộ. Tuy nhi&ecirc;n, chỉ ri&ecirc;ng việc ph&acirc;n chia học sinh theo gia thế đ&atilde; phần n&agrave;o chứng tỏ sự bất c&ocirc;ng trong trường.<br />\r\n<br />\r\nNg&ocirc;i trường n&agrave;y c&oacute; ba t&ograve;a nh&agrave; v&agrave; học sinh tại c&aacute;c to&agrave;n nh&agrave; l&agrave; kh&aacute;c nhau. T&ograve;a nh&agrave; A chỉ bao gồm 1 lớp gồm c&oacute; 6 học sinh. 6 học sinh n&agrave;y v&ocirc; c&ugrave;ng kỳ lạ, mọi người trong trường đều chưa từng nh&igrave;n thấy mặt của họ, họ cũng chưa từng tham gia v&agrave;o c&aacute;c cuộc thi hay hoạt động giao lưu trong trường. Hay n&oacute;i c&aacute;ch kh&aacute;c, 6 người họ trong mắt người kh&aacute;c l&agrave; một điều v&ocirc; c&ugrave;ng huyền b&iacute;.<br />\r\n<br />\r\n<br />\r\n<br />\r\nHọc sinh t&ograve;a nh&agrave; B th&igrave; dễ gặp hơn. Họ đều l&agrave; những tiểu thư hoặc c&ocirc;ng tử tại một gia đ&igrave;nh gi&agrave;u c&oacute; n&agrave;o đ&oacute;. C&ocirc; khi mới bước v&agrave;o đ&acirc;y th&igrave; chỉ c&oacute; thể nhận x&eacute;t một điều: Họ thực sự đều l&agrave; những c&ocirc; g&aacute;i v&agrave; ch&agrave;ng trai với kh&iacute; chất v&agrave; nhan sắc tr&ecirc;n mức b&igrave;nh thường.<br />\r\n<br />\r\nV&agrave; cuối c&ugrave;ng l&agrave; t&ograve;a nh&agrave; của c&ocirc;, t&ograve;a C. Đ&acirc;y l&agrave; t&ograve;a nh&agrave; d&agrave;nh cho những sinh vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn nhưng c&oacute; th&agrave;nh t&iacute;ch học tập xuất sắc. Tuy bị một số hạn chế khi ở trường nhưng học ph&iacute; lại được miễn giảm ho&agrave;n to&agrave;n. Đ&acirc;y l&agrave; một m&ocirc;i trường trong mơ với c&aacute;c học sinh kh&oacute; khăn.<br />\r\n<br />\r\nTuy rằng ở đ&acirc;y sẽ c&oacute; sự ph&acirc;n biệt. Tuy nhi&ecirc;n, nếu c&ocirc; thực sự chăm chỉ v&agrave; nỗ lực ở đ&acirc;y, c&oacute; lẽ c&ocirc; sẽ y&ecirc;n b&igrave;nh m&agrave; tốt nghiệp tại ng&ocirc;i trường n&agrave;y.<br />\r\n<br />\r\n- N&agrave;y, nghĩ g&igrave; suy tư thế? - Mỹ Anh cắt ngang d&ograve;ng suy tư của c&ocirc;<br />\r\n<br />\r\n- &Agrave;, kh&ocirc;ng c&oacute; g&igrave;...<br />\r\n<br />\r\n- Cậu kh&ocirc;ng thấy, mọi người trong lớp đang nh&igrave;n bọn m&igrave;nh bằng &aacute;nh mắt kh&aacute; l&eacute;n l&uacute;t sao? - Mỹ Anh n&oacute;i<br />\r\n<br />\r\n- C&oacute; sao? - C&ocirc; đột nhi&ecirc;n quay lại ph&iacute;a lớp học, ngay lập tức to&agrave;n bộ &aacute;nh mắt của mọi người đều lảng sang chỗ kh&aacute;c - Kh&ocirc;ng phải, rất b&igrave;nh thường sao?<br />\r\n<br />\r\n- B&igrave;nh thường c&aacute;i g&igrave;? - Mỹ Anh l&ecirc;n giọng - &Aacute;nh nh&igrave;n bọn họ như l&agrave; thăm d&ograve; kẻ ăn cắp vậy, kh&oacute; chịu chết đi được<br />\r\n<br />\r\n- Vậy sao? Chứ kh&ocirc;ng phải do m&igrave;nh đẹp sao? - C&ocirc; lấy tay chỉ v&agrave;o mặt m&igrave;nh hồn nhi&ecirc;n n&oacute;i<br />\r\n<br />\r\n- Cậu... thật l&agrave;...<br />\r\n<br />\r\nMỹ Anh nh&igrave;n c&ocirc; bằng &aacute;nh mắt kh&ocirc;ng c&ograve;n lời g&igrave; để n&oacute;i, rồi ngay sau đ&oacute; bật cười. C&ocirc; khi thấy Mỹ Anh c&oacute; thể mỉm cười sảng kho&aacute;i như thế cũng ngay lập tức bật cười theo.<br />\r\n<br />\r\n- Chuẩn bị v&agrave;o lớp rồi, đi th&ocirc;i!<br />\r\n<br />\r\nMỹ Anh nhanh ch&oacute;ng kho&aacute;c tay c&ocirc; bước v&agrave;o lớp học. Trong l&uacute;c đi v&agrave;o c&ograve;n li&ecirc;n tục kể cho c&ocirc; c&aacute;c c&acirc;u chuyện cười khiến c&ocirc; cũng cảm thấy thoải m&aacute;i.<br />\r\n<br />\r\nNhưng m&agrave;... đ&uacute;ng l&agrave; mọi người đang nh&igrave;n c&ocirc; hơi kỳ qu&aacute;i, c&oacute; tiếng th&igrave; thầm to nhỏ v&agrave; cả những &aacute;nh mắt v&ocirc; c&ugrave;ng l&eacute;n l&uacute;t. C&ocirc; vốn dĩ theo qu&aacute;n t&iacute;nh đ&atilde; đi chậm lại để nh&igrave;n xung quanh nhưng lại bị Mỹ Anh k&eacute;o xồng xộc về ph&iacute;a trước.<br />\r\n<br />\r\nKhi cả hai đ&atilde; đến gần cuối lớp, đứng trước b&agrave;n của m&igrave;nh, c&ocirc; mới hiểu v&igrave; sao mọi người lại nh&igrave;n m&igrave;nh kỳ lạ như vậy.<br />\r\n<br />\r\nXung quanh b&agrave;n c&ocirc; l&agrave; một hiện trường v&ocirc; c&ugrave;ng lộn xộn...</h2>', 0, 'chuong-2-khoi-dau-khong-duoc-suon-se'),
(15, 12, 'Chương 1: Cô là nội ứng, cô tên là Kha Hựu', 'Chương 1: Cô là nội ứng, cô tên là Kha Hựu', '<h2>M&ugrave;a h&egrave; n&agrave;y, thật l&agrave; bồn chồn bất an.<br />\r\n<br />\r\nSau giờ ngọ oi bức, nhiệt độ cao nung n&oacute;ng mọi thứ, d&ugrave; đ&atilde; tới chạng vạng, hơi n&oacute;ng vẫn bốc l&ecirc;n khỏi mặt đất. C&acirc;y quạt b&ecirc;n giường vẫn kẽo kẹt xoay v&ograve;ng, thổi ra từng luồng gi&oacute; kh&ocirc; h&agrave;nh về ph&iacute;a đầu giường.<br />\r\n<br />\r\nNgo&agrave;i đường, tiếng người b&aacute;n h&agrave;ng rong th&eacute;t to, tiềng gầm của d&ograve;ng xe cộ vội v&atilde;, những tiếng n&oacute;i chuyện ầm ĩ đ&atilde; đ&aacute;nh thức hầu hết mọi người từ sớm. Nhưng vẫn c&ograve;n một người nằm &igrave; tr&ecirc;n giường, &aacute;nh t&agrave; dương chiếu xuống chiếc giường đơn sơ, l&agrave;m nổi bật một th&acirc;n ảnh kiều mỵ. H&ocirc; hấp nhẹ nh&agrave;ng, lồng ngực đồng thời nhấp nh&ocirc;, &aacute;o thun bị mồ h&ocirc;i hơi l&agrave;m ướt d&iacute;nh v&agrave;o da, đường cong thon thả nhưng rắn chắc triển lộ kh&ocirc;ng s&oacute;t miếng n&agrave;o.<br />\r\n<br />\r\nM&aacute;i t&oacute;c đen nh&aacute;nh ngắn tới vai, khu&ocirc;n mặt kh&ocirc;ng tới mức động l&ograve;ng người nhưng kh&aacute; thanh t&uacute;, l&agrave;n da trắng mịn. Mồ h&ocirc;i sớm phủ đầy tr&aacute;n, nhưng hai mắt c&ocirc; vẫn nhắm nghiền, h&ocirc; hấp vững v&agrave;ng. C&oacute; điều trong l&uacute;c ngủ say, đ&ocirc;i m&agrave;y c&ocirc; hơi nhăn lại.<br />\r\n<br />\r\nCốc cốc cốc.<br />\r\n<br />\r\nCốc cốc cốc.<br />\r\n<br />\r\n&quot;Tiểu Kha.&quot;<br />\r\n<br />\r\nTiếng vang kh&ocirc;ng lớn, nhưng c&oacute; thể gọi c&ocirc; tỉnh lại. Hai mắt chợt mở ra, đ&ocirc;i ngươi l&aacute;o li&ecirc;ng như qu&eacute;t một v&ograve;ng quanh căn ph&ograve;ng nhỏ, rồi nhanh ch&oacute;ng bật người dậy. C&ocirc; đưa tay lau mồ h&ocirc;i tr&ecirc;n tr&aacute;n, ngẩng đầu liếc nh&igrave;n về c&aacute;i b&agrave;n tr&ograve;n kh&aacute; bừa bộn, mặt c&ocirc; xuất hiện tr&ecirc;n c&aacute;i gương cỡ l&ograve;ng b&agrave;n tay đặt gần đ&oacute;. Thở ph&agrave;o một hơi, c&ocirc; vỗ vỗ mặt m&igrave;nh, c&uacute;i đầu h&ocirc; một tiếng: &quot;Kha Hựu.&quot; Sau đ&oacute; đứng l&ecirc;n, đi đến cửa.<br />\r\n<br />\r\n&quot;Cho &iacute;t bột.&quot; Đứng ngo&agrave;i cửa l&agrave; một c&ocirc; g&aacute;i mặt m&agrave;y hơi t&aacute;i, tr&ocirc;ng c&oacute; ch&uacute;t tiều tụy. C&ocirc; ta mặc &aacute;o voan mỏng xuy&ecirc;n thấu b&oacute; eo v&agrave; quần sọt jean &ocirc;m s&aacute;t. C&ocirc; ta chống nạnh, hơi nghi&ecirc;ng người dựa tr&ecirc;n tường, khiến th&acirc;n h&igrave;nh c&oacute; lồi c&oacute; l&otilde;m của c&ocirc; ta b&agrave;y ra tư th&aacute;i c&agrave;ng th&ecirc;m n&oacute;ng bỏng h&uacute;t mắt.<br />\r\n<br />\r\nC&ocirc; ta t&ecirc;n Phỉ Phỉ, l&agrave; một c&ocirc; n&agrave;ng kiếm ăn v&agrave;o ban đ&ecirc;m. Chỗ kiếm ăn của c&ocirc; ta ngay đối diện ph&ograve;ng nhỏ của Kha Hựu, một điều nữa, n&agrave;ng l&agrave; c&ocirc;ng quan tiểu thư(*). T&ecirc;n thật của Phỉ Phỉ l&agrave; g&igrave;, Kha Hựu chưa bao giờ hỏi đến. Hai người kh&ocirc;ng chạm mặt nhiều lắm, dường như mỗi lần đều l&agrave; c&oacute; li&ecirc;n quan đến c&aacute;i thứ &quot;bột&quot; n&agrave;y. Chỉ c&oacute; khi Phỉ Phỉ l&ecirc;n cơn kh&ocirc;ng kịp ra ngo&agrave;i mua thuốc, Phỉ Phỉ mới c&oacute; thể tới g&otilde; cửa ph&ograve;ng Kha Hựu. Phỉ Phỉ cảm thấy Kha Hựu nh&eacute;t tiền trong mắt. Tuy Kha Hựu lu&ocirc;n trở về l&uacute;c gần s&aacute;ng, cơ hội hai người gặp mặt cũng kh&ocirc;ng nhiều lắm, nhưng d&ugrave; g&igrave; cũng l&agrave;m h&agrave;ng x&oacute;m hơn một năm, thế m&agrave; b&aacute;n &quot;bột&quot; cho c&ocirc; ta lu&ocirc;n cao hơn b&ecirc;n ngo&agrave;i gấp đ&ocirc;i.<br />\r\n<br />\r\n(*): &#39;公关小姐&#39; &ndash;&nbsp;<em>t&igrave;m tr&ecirc;n baike th&igrave; thấy, đ&acirc;y l&agrave; một chức danh chỉ người l&agrave;m ng&agrave;nh quan hệ x&atilde; hội trong c&ocirc;ng ty, l&agrave;m PR, nhưng c&oacute; 1 chỗ lại n&oacute;i &quot;l&agrave; một nghề kiếm được nhiều tiền, c&agrave;ng xinh đẹp c&agrave;ng tiền đồ v&ocirc; lượng&quot;:3 chắc ai cũng biết l&agrave; nghề g&igrave;~</em><br />\r\n<br />\r\nBất đắc dĩ đ&atilde; l&ecirc;n cơn nghiện rồi, cơn kh&oacute; chịu bứt rứt đ&oacute; khi&ecirc;n c&ocirc; ta phải tới đ&acirc;y cầu cứu Kha Hựu. Phỉ Phỉ hơi run rẩy, c&ocirc; ta lấy ra mấy tờ tiền nhăn nh&uacute;m đưa đến trước mặt Kha Hựu, tay c&ocirc; tay cũng run lẩy bẩy.<br />\r\n<br />\r\nKha Hựu kh&ocirc;ng n&oacute;i g&igrave;, mặt kh&ocirc;ng đổi sắc nhận tiền của c&ocirc; ta, sau đ&oacute; trở lại trong ph&ograve;ng, cau m&agrave;y l&ocirc;i từ dưới s&agrave;ng ra một c&aacute;i rương sắt, ở tầng ngầm trong c&aacute;i rương lấy ra một bao thuốc nhỏ cỡ ng&oacute;n tay. C&ocirc; đứng dậy, mi gian đ&atilde; gi&atilde;n ra. Khi c&ocirc; đi ra cửa lần nữa, th&igrave; thấy một người đ&agrave;n &ocirc;ng từ ph&ograve;ng Phỉ Phỉ đi ra, g&atilde; đang giở tr&ograve; với Phỉ Phỉ. G&atilde; đ&agrave;n &ocirc;ng đ&oacute; c&uacute;i s&aacute;t b&ecirc;n tai Phỉ Phỉ n&oacute;i v&agrave;i c&acirc;u. Phỉ Phỉ gắng gượng trưng ra khu&ocirc;n mặt tươi cười h&ugrave;a theo.<br />\r\n<br />\r\nThấy Kha Hựu, g&atilde; kia cố sức bấm một c&aacute;i tr&ecirc;n eo Phỉ Phỉ, kh&ocirc;ng để &yacute; Phỉ Phỉ đau đến t&aacute;i mặt. G&atilde; d&acirc;m t&agrave; cười hu&yacute;t s&aacute;o một c&aacute;i, xoay người bỏ đi. Dưới h&agrave;ng hi&ecirc;n vọng lại tiếng n&oacute;i th&ocirc; thiển của g&atilde;: &quot;Lần sau anh sẽ t&igrave;m cưng nữa.&quot;<br />\r\n<br />\r\nKha Hựu ngẩng đầu, vẫn giữ im lặng, nh&igrave;n chằm chằm Phỉ Phỉ gần như muốn nhũn ra trước mắt, đi tới v&agrave;i bước, nh&eacute;t g&oacute;i thuốc v&agrave;o trong tay Phỉ Phỉ.<br />\r\n<br />\r\n&quot;Cảm ơn.&quot; Phỉ Phỉ b&oacute;p chặt tay, nhếch m&ocirc;i n&oacute;i một c&acirc;u rồi đi v&agrave;o ph&ograve;ng của m&igrave;nh. Ầm một tiếng, Xung quanh chỉ c&ograve;n lại tiếng c&aacute;nh cửa va đập v&agrave;o v&aacute;ch tường.<br />\r\n<br />\r\nKha Hựu đứng trước cửa ph&ograve;ng Phỉ Phỉ, chậm r&atilde;i thở d&agrave;i, xoay người đi về ph&ograve;ng m&igrave;nh. Cửa lớn đ&oacute;ng chặt, bất chợt, cảm gi&aacute;c buồn bực, nặng nề cuồn cuộn d&acirc;ng tr&agrave;o. Kha Hựu tới c&aacute;i tủ vải, tiện tay chọn ra một c&aacute;i quần jean, c&aacute;i &aacute;o thun trắng, xoay người đi v&agrave;o ph&ograve;ng tắm.<br />\r\n<br />\r\nMở v&ograve;i hoa sen, nước lạnh xối từ đầu xuống ch&acirc;n, &agrave;o &agrave;o tưới khắp th&acirc;n thể c&ocirc;. Dần dần, trong l&ograve;ng cũng b&igrave;nh tĩnh lại...<br />\r\n<br />\r\nC&ocirc; mặc c&aacute;i &aacute;o thun trắng v&agrave;o, mặc th&ecirc;m c&aacute;i quần jean giặt đến bạc m&agrave;u kia. Nh&igrave;n m&aacute;i t&oacute;c đen ngắn trong gương, c&ocirc; nhếch miệng, v&eacute;n t&oacute;c l&ecirc;n, đơn giản cột sau đầu, c&ograve;n mấy cọng leo queo rũ xuống b&ecirc;n cổ. Đầu ng&oacute;n tay b&uacute;n nhẹ l&ecirc;n gương, ph&aacute;t ra tiếng vang cộp cộp, cười tự n&oacute;i với c&aacute;i gương: &quot;Cố l&ecirc;n!&quot;<br />\r\n<br />\r\nChạng vạng, &#39;Dạ mị&#39; kh&aacute; quạnh quẽ, Kha Hựu dạo một v&ograve;ng, chỉ c&oacute; tốp năm tốp ba nh&acirc;n vi&ecirc;n phục vụ đang qu&eacute;t tước. Đại ca của c&ocirc;, Phiếu mập, cũng kh&ocirc;ng thấy tăm hơi.<br />\r\n<br />\r\nHai năm trước, Kha Hựu th&agrave;nh c&ocirc;ng d&ugrave;ng th&acirc;n phận bartender v&agrave;o l&agrave;m trong qu&aacute;n bar n&agrave;y. Mục đ&iacute;ch của c&ocirc;, l&agrave; điều tra chuyện kinh doanh ma t&uacute;y ph&iacute;a sau qu&aacute;n bar n&agrave;y, thu thập chứng cứ bu&ocirc;n lậu ma t&uacute;y của người quản l&yacute; Tăng Bằng Vũ. Do l&agrave; l&iacute;nh mới, l&uacute;c đầu nhiệm vụ của c&ocirc; tiến triển chậm chạp, trong một năm chỉ l&agrave; đ&aacute;nh bậy đ&aacute;nh bạ trao đổi v&agrave;i tin lặt vặt với cảnh s&aacute;t, kh&ocirc;ng gặp được Tăng Bằng Vũ. Nhưng lại t&oacute;m kh&ocirc;ng &iacute;t vụ mua b&aacute;n thuốc lắc thuốc k&iacute;ƈɦ ɖụƈ v&acirc;n v&acirc;n... Cũng may c&ocirc;ng phu kh&ocirc;ng phụ l&ograve;ng người, đầu năm thứ hai sau khi trở th&agrave;nh nội ứng, Kha Hựu trở th&agrave;nh thủ hạ đắc lực của Phiếu mập. Đắc lực đối với Phiếu mập m&agrave; n&oacute;i, mỗi th&aacute;ng giao giấy tờ trước, h&agrave;ng m&agrave; người kh&aacute;c kh&ocirc;ng ti&ecirc;u thụ được, Kha Hựu đều c&oacute; thể d&ugrave;ng thời gian ngắn nhất ti&ecirc;u h&oacute;a.<br />\r\n<br />\r\nDần d&agrave;, Phiếu mập bu&ocirc;ng xuống cảnh gi&aacute;c với Kha Hựu, cũng giao cho c&ocirc; sổ s&aacute;ch m&agrave; m&igrave;nh nắm giữ. Dưới sự t&iacute;n nhiệm của Phiếu mập, Kha Hựu d&ugrave;ng l&yacute; do cần tiền, tận lực nắm giữ những chất độc hại đ&oacute;, chuyển giao đến tay cảnh s&aacute;t, giảm thiểu số lượng chất đ&oacute; tuồng ra ngo&agrave;i thị trường. D&ugrave; rằng cũng kh&ocirc;ng đ&aacute;ng bao nhi&ecirc;u.<br />\r\n<br />\r\nM&agrave; h&ocirc;m nay, dưới sự nh&otilde;ng nhẽo đ&ograve;i hỏi của Kha Hựu, Phiếu mập đ&aacute;p ứng tiến cử Kha Hựu với Tăng Bằng Vũ, đồng thời cũng v&igrave; củng cố địa vị của ch&iacute;nh hắn trước mặt Tăng Bằng Vũ. Kha Hựu đi một v&ograve;ng trong đại sảnh, quay sang hỏi cậu trai đang qu&eacute;t r&aacute;c: &quot;Phiếu ca đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Đang ngồi với Bằng ca ở ph&ograve;ng bao số 144.&quot;<br />\r\n<br />\r\nKha Hựu rất tự nhi&ecirc;n cười xong, liền đi thẳng tới quầy bar, ly đồ c&aacute;c thứ đ&atilde; lau kh&ocirc; sạch sẽ. Trước khi tan l&agrave;m, c&ocirc; th&iacute;ch dọn dẹp xong xu&ocirc;i mọi thứ. V&igrave; vậy hiện tại, c&ocirc; đứng ở ngo&agrave;i quầy bar, nhẹ tay g&otilde; mặt quầy, tr&ocirc;ng như ch&aacute;n chết nh&igrave;n nh&igrave;n xung quanh. Đ&ocirc;i mắt cũng kh&ocirc;ng ngừng liếc về ph&iacute;a lối ra v&ocirc; chỗ ph&ograve;ng bao kia.<br />\r\n<br />\r\nCửa ph&ograve;ng bao mở ra, Phiếu mập từ b&ecirc;n trong đi ra, hướng ph&iacute;a Kha Hựu ngoắc ngoắc: &quot;Tiểu Kha.&quot;<br />\r\n<br />\r\nKha Hựu vừa lấy một xấp tiền trong t&uacute;i ra, vừa chạy chậm tới chỗ Phiếu mập: &quot;Phiếu ca.&quot;<br />\r\n<br />\r\nPhiếu mập c&uacute;i đầu n&oacute;i một c&acirc;u: &quot;Lanh lợi một ch&uacute;t, đ&acirc;y l&agrave; cơ hội biểu hiện của c&ocirc; em đ&oacute;.&quot;<br />\r\n<br />\r\nKha Hựu cười cười, nh&eacute;t xấp tiền v&agrave;o trong tay Phiếu mập: &quot;Nợ nần th&aacute;ng n&agrave;y.&quot;<br />\r\n<br />\r\nPhiếu mập cầm lấy tiền, đếm đếm một ch&uacute;t, cười vỗ vỗ vai Kha Hựu, đẩy cửa ph&ograve;ng bao ra: &quot;Bằng ca, đ&acirc;y l&agrave; Tiểu Kha em đ&atilde; đề cập với anh.&quot;<br />\r\n<br />\r\nKha Hựu đứng b&ecirc;n cạnh Phiếu mập, khẽ gật đầu: &quot;Bằng ca.&quot;<br />\r\n<br />\r\n&quot;Nữ?&quot; Tăng Bằng Vũ cau m&agrave;y, cười x&igrave;: &quot;Anh n&oacute;i A Phiếu n&agrave;y, m&agrave;y cũng kh&ocirc;ng sợ bị người ch&ecirc; cười?&quot;<br />\r\n<br />\r\nPhiếu mập do dự n&oacute;i: &quot;Bằng ca... c&aacute;i n&agrave;y....&quot;<br />\r\n<br />\r\nKha Hựu mỉm cười, gi&agrave;nh n&oacute;i trước: &quot;Bằng ca, em cần tiền.&quot;<br />\r\n<br />\r\n&quot;Đ&ograve;i tiền?&quot; Tăng Bằng Vũ từ s&ocirc; pha đứng l&ecirc;n, chậm r&atilde;i đi tới chỗ Kha Hựu, cầm lấy xấp tiền trong tay Phiếu mập vỗ vỗ: &quot;Đủ sao? Theo anh một đ&ecirc;m, cả đống n&agrave;y đều l&agrave; của em.&quot;<br />\r\n<br />\r\nTăng Bằng Vũ c&agrave;ng bước tới c&agrave;ng gần, Kha Hựu kh&ocirc;ng hề nhượng bộ, giằng co tại chỗ nh&igrave;n thẳng Tăng Bằng Vũ: &quot;Em biết gần đ&acirc;y trong tay Bằng ca c&oacute; một nh&oacute;m h&agrave;ng, lại bị bọn cớm canh rất chặt.&quot;<br />\r\n<br />\r\n&quot;Kẻ ngu đều biết, lũ cớm b&acirc;y giờ ng&agrave;y n&agrave;o cũng me anh đ&acirc;y.&quot; Tăng Bằng Vũ t&agrave; t&agrave; cười, cố &yacute; &aacute;p s&aacute;t v&agrave;o Kha Hựu, hơi thở ồ ồ phả l&ecirc;n b&ecirc;n cổ c&ocirc;: &quot;Một đứa con g&aacute;i như em, muốn n&oacute;i với anh l&agrave; em c&oacute; c&aacute;ch?&quot;<br />\r\n<br />\r\nKh&ocirc;ng để &yacute; tới đ&ocirc;i mắt n&oacute;ng bỏng của Tăng Bằng Vũ, &aacute;nh mắt Kha Hựu ki&ecirc;n định, b&igrave;nh tĩnh tự nhi&ecirc;n, giọng điệu kh&ocirc;ng ki&ecirc;u ngạo kh&ocirc;ng siểm nịnh: &quot;C&aacute;ch của phụ nữ.&quot;<br />\r\n<br />\r\nTăng Bằng Vũ cười nhạt, Kha Hựu chủ đồ kề s&aacute;t mặt lại gần Tăng Bằng Vũ, gh&eacute; v&agrave;o lỗ tai hắn thấp giọng n&oacute;i v&agrave;i c&acirc;u.<br />\r\n<br />\r\n&quot;Ha ha.&quot; Tăng Bằng Vũ quay đầu ra hiệu với thủ hạ. Thủ hạ đưa một bao thuốc lắc tới: &quot;Thử xem.&quot;<br />\r\n<br />\r\n---<br />\r\n<br />\r\nBan đ&ecirc;m, d&ograve;ng người tấp nập ở c&aacute;c khu trung t&acirc;m thương mại. Kha Hựu mặc c&aacute;i v&aacute;y d&agrave;i m&agrave;u trắng ng&agrave; in hoa, mang đ&ocirc;i gi&agrave;y g&oacute;t nhọn m&agrave;u v&agrave;ng nhạt, đeo t&uacute;i x&aacute;ch bằng da, trước ngực lủng lẳng một vi&ecirc;n bảo thạch m&agrave;u n&acirc;u, vừa đủ đẹp mắt. C&ocirc; đi thẳng tới thang cuốn, l&ecirc;n đến tầng cao nhất của trung t&acirc;m thương mại. Tầng n&agrave;y l&agrave; khu của h&agrave;ng xa xỉ phẩm, rất &iacute;t người qua lại. Kha Hựu t&ugrave;y &yacute; dừng lại gần b&ecirc;n thang cuốn, quay sang mặt k&iacute;nh chỉnh chỉnh t&oacute;c. Quả nhi&ecirc;n, ph&iacute;a sau c&oacute; người theo d&otilde;i. Từ l&uacute;c c&ocirc; thay đồ rời khỏi qu&aacute;n bar, đ&atilde; theo d&otilde;i đến b&acirc;y giờ, đ&acirc;y chắc l&agrave; cảnh s&aacute;t truy theo Tăng Bằng Vũ, c&oacute; lẽ bản th&acirc;n thay đổi trang phục khiến bọn họ ch&uacute; &yacute;.<br />\r\n<br />\r\nKha Hựu lượn quanh mấy gian h&agrave;ng, ra ra v&agrave;o v&agrave;o ph&ograve;ng thử đồ năm s&aacute;u lần. Mỗi lần thay, c&ocirc; kh&ocirc;ng chỉ đổi mỗi c&aacute;i quần, kết quả, lần n&agrave;o cũng tay kh&ocirc;ng đi ra. M&agrave; một nam một nữ cảnh s&aacute;t kia tự nhi&ecirc;n cũng theo sau. Qu&aacute; tr&igrave;nh n&agrave;y, ngo&agrave;i mấy nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng, Kha Hựu chưa từng n&oacute;i chuyện với bất kỳ ai, hai cảnh s&aacute;t đi theo ph&iacute;a sau c&ocirc; hiển nhi&ecirc;n c&oacute; ch&uacute;t bu&ocirc;ng lỏng. L&uacute;c Kha Hựu lại lần nữa bước v&agrave;o một gian h&agrave;ng, nam cảnh s&aacute;t đứng b&ecirc;n ngo&agrave;i h&uacute;t thuốc.<br />\r\n<br />\r\nKha Hựu lượn quanh một d&atilde;y kệ &aacute;o quần, một c&ocirc; g&aacute;i đứng đối diện đeo kim băng m&agrave;u đỏ trước ngực, cũng đeo c&aacute;i t&uacute;i giống c&ocirc; ho nhẹ một tiếng. Kha Hựu hiểu &yacute;, người nọ ch&iacute;nh l&agrave; người đối phương ph&aacute;i tới nhận h&agrave;ng. Nếu như dựa theo kế hoạch l&uacute;c đầu, khi hai người tiếp x&uacute;c nhất định sẽ khiến nữ cảnh s&aacute;t cũng đang ở trong gian h&agrave;ng ch&uacute; &yacute;.<br />\r\n<br />\r\nKha Hựu dựa theo ước định, ho nhẹ một tiếng đ&aacute;p lại, sau đ&oacute; tiện tay lấy ba c&aacute;i quần mang tới ph&ograve;ng thay đồ. Trong ph&ograve;ng đồ, Kha Hựu lấy bao thuốc lắc ra, nh&eacute;t v&agrave;o t&uacute;i quần thụng mang v&agrave;o khi n&atilde;y, lại ng&acirc;y người một hồi. C&ocirc; để t&uacute;i của m&igrave;nh ở lại ph&ograve;ng thay đồ, sau đ&oacute; đi ra. C&ocirc; g&aacute;i đứng đợi ở ngo&agrave;i mỉm cười gật đầu, nghi&ecirc;ng người đi v&agrave;o ph&ograve;ng thay đồ.<br />\r\n<br />\r\n&quot;Xin lỗi, kh&ocirc;ng c&oacute; c&aacute;i n&agrave;o hợp cả.&quot; Kha Hựu cầm quần tr&ecirc;n tay, &aacute;y n&aacute;y cười cười, sau đ&oacute; gi&uacute;p người b&aacute;n h&agrave;ng xếp lại đồ l&ecirc;n kệ. Sau đ&oacute;, c&ocirc; ra khỏi gian h&agrave;ng, cố &yacute; đi ngang qua hai cảnh s&aacute;t kia, len l&eacute;n liếc liếc mắt, sau đ&oacute; lượn v&agrave;o gian h&agrave;ng tiếp theo.<br />\r\n<br />\r\nNg&acirc;y người trong gian h&agrave;ng đ&oacute; kh&ocirc;ng đến ba ph&uacute;t, Kha Hựu vội v&atilde; chạy về gian h&agrave;ng khi n&atilde;y. Hai cảnh s&aacute;t kia đang canh ở cửa nhất thời cảnh gi&aacute;c, tỉ mỉ quan s&aacute;t cử động của Kha Hựu.<br />\r\n<br />\r\nTrong ba l&ocirc; kh&ocirc;ng c&oacute; thuốc, c&ocirc; g&aacute;i kia ở b&ecirc;n trong chắc vẫn đang t&igrave;m, Kha Hựu g&otilde; cửa ph&ograve;ng: &quot;Xin lỗi, t&ocirc;i để qu&ecirc;n t&uacute;i ở b&ecirc;n trong.&quot;<br />\r\n<br />\r\nNam cảnh s&aacute;t h&uacute;t thuốc cũng đi v&agrave;o cửa h&agrave;ng, c&ocirc; g&aacute;i trong ph&ograve;ng thay đồ mang vẻ tức giận đi ra, nh&eacute;t t&uacute;i Kha Hựu v&agrave;o tay Kha Hựu, c&uacute;i đầu n&oacute;i một c&acirc;u: &quot;Kiểm tra một ch&uacute;t, đồ rớt ở đ&acirc;u rồi &agrave;?&quot;<br />\r\n<br />\r\nKha Hựu đầu ti&ecirc;n l&agrave; sửng sốt, sau đ&oacute; mở t&uacute;i c&uacute;i đầu kiểm tra một phen, ngẩng đầu mỉm cười, &quot;Kh&ocirc;ng c&oacute;, cảm ơn c&ocirc;.&quot;<br />\r\n<br />\r\nKha Hựu n&oacute;i xong th&igrave; muốn đi, t&ecirc;n cảnh s&aacute;t kia đột nhi&ecirc;n chặn c&ocirc;, nữ cảnh s&aacute;t cũng đi tới trước mặt c&ocirc; g&aacute;i kia, &quot;Ch&uacute;ng t&ocirc;i nghi ngờ tr&ecirc;n người c&aacute;c c&ocirc; c&oacute; h&agrave;ng cấm, phiền c&aacute;c c&ocirc; mở t&uacute;i ra kiểm tra.&quot;<br />\r\n<br />\r\n-------<br />\r\n<br />\r\nT&aacute;c giả n&oacute;i ra suy nghĩ của m&igrave;nh: Ha, Kha Hựu ch&iacute;nh l&agrave; Đinh Tiểu Tuy&ecirc;n. Đ&agrave;o hố, thế nhưng, ta c&ograve;n thiếu phi&ecirc;n ngoại &Aacute;i ngục, c&ograve;n thiếu truyện của Hướng Diệc Song v&agrave; Tiểu Quỷ, cho n&ecirc;n, mỗi ng&agrave;y đều cập nhật chương! Sẽ tận lực!<br />\r\n<br />\r\n-------<br />\r\n<br />\r\nEditor c&oacute; lời muốn n&oacute;i: tiếc l&agrave; phi&ecirc;n ngoại của Hướng Diệc Song v&agrave; Tiểu Quỷ bị drop rồi~ TT^TT<br />\r\n<br />\r\nPs: l&acirc;u lắm mới quay lại thể loại hiện đại, m&igrave;nh sẽ cố gắng để bộ truyện &quot;hiện đại&quot; nhất c&oacute; thể~ ^.^</h2>', 0, 'chuong-1-co-la-noi-ung-co-ten-la-kha-huu');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(16, 12, 'Chương 2: Thật ra Kha Hựu là H', 'Chương 2: Thật ra Kha Hựu là H', '<h2>Trước mắt bao người, hai vị cảnh s&aacute;t kia d&ugrave; lục so&aacute;t người hay lục so&aacute;t t&uacute;i đều kh&ocirc;ng c&oacute; thu hoạch g&igrave;, chỉ c&oacute; thể phẫn nộ bỏ đi. Kha Hựu đợi hai t&ecirc;n cảnh s&aacute;t kia đi khuất dạng, mới đi lấy c&aacute;i quần kia thử lại lần nữa, rồi l&agrave;m bộ &aacute;y n&aacute;y m&agrave; mua n&oacute;. C&ocirc; g&aacute;i kia cũng bắt chước l&agrave;m theo, sau đ&oacute; tiến h&agrave;nh giao dịch ở một gian h&agrave;ng kh&aacute;c. Nhiệm vụ ho&agrave;n th&agrave;nh, Kha Hựu mang tiền về qu&aacute;n bar.<br />\r\n<br />\r\nNgo&agrave;i qu&aacute;n bar, một tiếng lốp xe thắng gấp tr&ecirc;n nền xi măng &ecirc; cả tai vang l&ecirc;n, một chiếc Ferrari California m&agrave;u xanh ngọc(*) dừng ở b&ecirc;n ch&acirc;n Kha Hựu. Chỉ th&ecirc;m khoảng 5 xăng ti m&eacute;t nữa th&ocirc;i, Kha Hựu sẽ tiếp x&uacute;c th&acirc;n mật với con Ferrari liều mạng n&agrave;y rồi.<br />\r\n<br />\r\nKha Hựu quay đầu, trừng mắt tức giận nh&igrave;n về ph&iacute;a ghế lại b&ecirc;n trong xe.<br />\r\n<br />\r\nC&ocirc; g&aacute;i ngồi tr&ecirc;n xe dường như kh&ocirc;ng th&egrave;m để &yacute; n&agrave;ng thiếu ch&uacute;t nữa th&igrave; đụng người ta, cũng ho&agrave;n to&agrave;n kh&ocirc;ng để &yacute; tới những &aacute;nh mắt n&oacute;ng bỏng k&eacute;o tới bởi v&igrave; tiếng gầm của xe m&igrave;nh. Dưới &aacute;nh đ&egrave;n đường lờ mờ, c&oacute; thể thấy mang m&aacute;ng n&agrave;ng đang rời chỗ. Hai cơ thể như linh x&agrave; động t&igrave;nh quấn lấy nhau. Hai gương mặt đều xinh đẹp như ẩn như hiện, c&ocirc; n&agrave;ng kia bất chấp cuốn lấy th&acirc;n thể n&oacute;ng bỏng của n&agrave;ng, h&ocirc;n l&ecirc;n nồng nhiệt.<br />\r\n<br />\r\nKha Hựu hừ lạnh một tiếng, cũng kh&ocirc;ng muốn d&acirc;y dưa với thứ con g&aacute;i nh&agrave; gi&agrave;u bừa b&atilde;i đ&oacute;, tự nhận kh&ocirc;ng may m&agrave; đi v&agrave;o qu&aacute;n bar.<br />\r\n<br />\r\nNgọn đ&egrave;n ch&oacute;i mắt, &acirc;m nhạc điếc tai, rượu nước đầu độc, trong kh&ocirc;ng kh&iacute; tr&agrave;n ngập hơi thở sa đọa.<br />\r\n<br />\r\n&Aacute;nh mắt mập mờ, mị hoặc l&otilde;&alpha; ɭồ, c&ocirc; n&agrave;ng vũ c&ocirc;ng m&uacute;a cột giữa đ&agrave;i t&ugrave;y &yacute; uốn &eacute;o, tr&ecirc;u chọc đến từng sợi d&acirc;y thần kinh của mỗi người.<br />\r\n<br />\r\nĐi qua cả đống người, rốt cuộc Kha Hựu cũng chen được tới quầy bar, b&ecirc;n m&eacute;p treo nụ cười tự tin: &quot;Phiếu ca.&quot;<br />\r\n<br />\r\nBartender của qu&aacute;n - Tiểu Bao nh&igrave;n thấy Kha Hựu vốn lu&ocirc;n mặc &aacute;o sơ mi quần jean nay lại tr&ograve;ng v&aacute;y, anh ta trợn to hai mắt kh&ocirc;ng thể tin m&agrave; th&eacute;t l&ecirc;n: &quot;Woa cha! Tiểu Kha c&ocirc; mặc v&aacute;y cũng kh&ocirc;ng tệ đ&oacute; chứ!&quot;<br />\r\n<br />\r\nNếu kh&ocirc;ng v&igrave; cả ng&agrave;y phải s&aacute;p s&aacute;p v&agrave;o đ&aacute;m đ&agrave;n &ocirc;ng, nếu kh&ocirc;ng v&igrave; chỗ ở kh&ocirc;ng an to&agrave;n, c&ocirc; cũng kh&ocirc;ng th&egrave;m cắt đi m&aacute;i t&oacute;c d&agrave;i th&acirc;n y&ecirc;u của m&igrave;nh, chỉnh m&igrave;nh th&agrave;nh bộ dạng muốn bao nhi&ecirc;u giản dị th&igrave; c&oacute; bấy nhi&ecirc;u giản dị như vầy đ&acirc;u. Kha Hựu dương mi kh&ocirc;ng th&egrave;m tiếp, quay sang n&oacute;i với Phiếu mập: &quot;Phiếu ca, nhiệm vụ ho&agrave;n th&agrave;nh.&quot;<br />\r\n<br />\r\nPhiếu mập g&otilde; g&otilde; quầy bar, cười ha hả, quay đầu ra hiệu cho Kha Hựu vị tr&iacute; g&oacute;c ph&ograve;ng: &quot;C&oacute; tệ hay kh&ocirc;ng tệ th&igrave; cũng kh&ocirc;ng c&oacute; li&ecirc;n quan tới ch&uacute; em đ&acirc;u. Lăng tiểu thư Lăng Như người ta đ&atilde; sớm đặt trước rồi.&quot;<br />\r\n<br />\r\nNghe thấy danh h&agrave;o Lăng tiểu thư, Kha Hựu bất gi&aacute;c nh&iacute;u m&agrave;y, ho nhẹ một tiếng: &quot;Phiếu ca, đừng lấy em ra đ&ugrave;a giỡn.&quot;<br />\r\n<br />\r\nPhiếu mập thu hồi khu&ocirc;n mặt tươi cười, nghi&ecirc;m t&uacute;c n&oacute;i, &quot;Bằng ca đang ở b&ecirc;n trong n&oacute;i chuyện với Minh Huy của Phi B&aacute;o đường, coi bộ l&agrave; tới g&acirc;y chuyện. C&ocirc; đợi l&aacute;t nữa hẵng đi v&agrave;o.&quot;<br />\r\n<br />\r\n&quot;Tiểu Kha, c&ocirc; x&aacute;c định kh&ocirc;ng muốn nh&agrave;o v&agrave;o lồng ngực anh &agrave;?&quot; &Aacute;nh mắt Tiểu Bao xuy&ecirc;n qua ph&iacute;a sau Kha Hựu chớp chớp, nở nụ cười nghiền ngẫm với Kha Hựu: &quot;N&oacute;i kh&ocirc;ng chừng anh c&oacute; thể cứu c&ocirc; đ&oacute;.&quot;<br />\r\n<br />\r\nPhiếu mập cắn răng t&aacute;t Tiểu Bao một c&aacute;i: &quot;L&agrave;m việc!&quot; Hất hất cằm, rồi n&oacute;i th&ecirc;m với Kha Hựu: &quot;Gần đ&acirc;y Phi B&aacute;o đường qua lại rất th&acirc;n với Phi Lang b&ecirc;n kia, hao hết t&acirc;m tư cũng muốn ăn bớt ph&acirc;n ngạch của Phi Hồ ch&uacute;ng ta. Bằng ca sứt đầu mẻ tr&aacute;n, c&ocirc; coi chừng một ch&uacute;t, đừng đỏ bọn ch&uacute;ng nắm được nhược điểm.&quot;<br />\r\n<br />\r\n&quot;Em biết rồi.&quot; Kha Hựu nghi&ecirc;ng đầu, kh&oacute;e mắt nh&igrave;n tho&aacute;ng qua Lăng Như đang chậm r&atilde;i bước tới b&ecirc;n n&agrave;y.<br />\r\n<br />\r\nĐế quốc ngầm do Lam Ti&ecirc;u Tần đứng đầu n&agrave;y, b&ecirc;n dưới chia th&agrave;nh ba đường ph&acirc;n c&ocirc;ng quản l&yacute; ba khối thị trường: c&aacute; độ, ma t&uacute;y, hộp đ&ecirc;m. Ch&uacute; Ba Lam Thừa Thi&ecirc;n ở Phi Hồ b&aacute;n ma t&uacute;y, ch&uacute; Tư Lam Thừa Trạch ở Phi B&aacute;o tr&ocirc;ng coi hộp đ&ecirc;m, ngang vế với Lam Ti&ecirc;u Tần - Lam Ti&ecirc;u Quang, tr&ocirc;ng coi Phi Lang đường, quản l&yacute; c&aacute; độ.<br />\r\n<br />\r\nLăng Như l&agrave; muội muội của Phi Lang đường Lăng H&uacute;c, suốt ng&agrave;y kh&ocirc;ng c&oacute; việc g&igrave; l&agrave;m, chỉ biết rượu ch&egrave; &ocirc;m ấp phụ nữ. Ai cũng biết Lăng tiểu thư phản nghịch, Lăng H&uacute;c c&agrave;ng phản đối, n&agrave;ng c&agrave;ng gi&oacute;ng trống khua chi&ecirc;ng m&agrave; đối nghịch với hắn. Lăng H&uacute;c bất đắc dĩ hạ lệnh cấm, ai d&aacute;m động tới Lăng Như th&igrave; chờ chặt tay đi! Lăng Như bị ăn bế m&ocirc;n canh nhưng hết lần n&agrave;y tới lần kh&aacute;c kh&ocirc;ng tin t&agrave;. V&igrave; vậy n&agrave;ng quyết định chọn người lu&ocirc;n c&oacute; vẻ cự người ng&agrave;n dặm - Kha Hựu, coi như l&agrave; mục ti&ecirc;u đầu ti&ecirc;n m&agrave; n&agrave;ng sống chết vẫn đ&ograve;i quấn lấy.<br />\r\n<br />\r\nKha Hựu th&iacute;ch con g&aacute;i kh&ocirc;ng c&oacute; sai, tr&ugrave;ng hợp Lăng tiểu thư l&agrave; vạn năm thụ. Tuy n&oacute;i lăn giường với Lăng tiểu thư kh&ocirc;ng c&oacute; tổn thất g&igrave;, thế nhưng đối với chuyện l&agrave;m y&ecirc;u, Kha Hựu vẫn duy tr&igrave; suy nghĩ &quot;thần th&aacute;nh&quot;, kh&ocirc;ng gặp được người trong định mệnh, th&igrave; phải thủ th&acirc;n như ngọc. Lăng Như kh&ocirc;ng thể đắc tội, Lăng H&uacute;c c&agrave;ng kh&ocirc;ng thể đắc tội, v&igrave; vậy Kha Hựu chỉ c&oacute; thể đ&aacute;nh th&aacute;i cực với Lăng Như, nhất qu&aacute;n ki&ecirc;n tr&igrave; th&aacute;i độ kh&ocirc;ng chấp nhận kh&ocirc;ng cự tuyệt.<br />\r\n<br />\r\nLăng Như dần tới gần, k&eacute;o theo m&ugrave;i rượu nồng nặc tr&ecirc;n người n&agrave;ng ta. Kha Hựu nuốt nuốt nước bọt, muốn chuồn đi lu&ocirc;n: &quot;Phiếu ca, em đi thay quần &aacute;o.&quot;<br />\r\n<br />\r\n&quot;Kha Hựu?&quot; Lăng Như một tay k&eacute;o Kha Hựu, l&ocirc;i c&ocirc; lại, sau khi nh&igrave;n r&otilde; Kha Hựu, mặt m&agrave;y n&agrave;ng ta đầy vẻ nghi hoặc: &quot;Em...&quot;<br />\r\n<br />\r\nĐ&egrave;n n&ecirc; &ocirc;ng trong qu&aacute;n bar lập l&ograve;e v&ocirc; định, biến ảo ra đủ mọi m&agrave;u sắc, hắt l&ecirc;n từng người ở đ&acirc;y.<br />\r\n<br />\r\nPhản ứng của Lăng Như c&oacute; ch&uacute;t bất ngờ, Kha Hựu chợt nhớ tới, bạn của Lăng Như trước đ&acirc;y, h&igrave;nh như đều l&agrave; mấy c&ocirc; tr&ocirc;ng rất nam t&iacute;nh. Nếu như bởi v&igrave; kiểu phục trang n&agrave;y, c&oacute; thể khiến Lăng Như ngoan ngo&atilde;n r&uacute;t lui, vậy th&igrave; thật sự l&agrave; thu hoạch ngo&agrave;i &yacute; muốn rồi. Kha Hựu đột nhi&ecirc;n x&aacute;n lạn cười rộ l&ecirc;n: &quot;Hi.&quot;<br />\r\n<br />\r\nLăng Như thu hồi vẻ bất ngờ, nh&igrave;n Kha Hựu từ tr&ecirc;n xuống dưới: &quot;H&ocirc;m nay l&agrave; sinh nhật của t&ocirc;i.&quot;<br />\r\n<br />\r\nTiểu Bao c&ugrave;ng Phiếu mập ở gần đ&oacute; kh&ocirc;ng c&oacute; hảo &yacute; m&agrave; ch&uacute;c mừng, Kha Hựu lấm l&eacute;t liếc Tiểu Bao, cười nhạt n&oacute;i với Lăng Như một c&acirc;u: &quot;Sinh nhật vui vẻ.&quot;<br />\r\n<br />\r\nLăng Như thoả m&atilde;n mỉm cười, s&aacute;p đến mặt Kha Hựu cấp tốc h&ocirc;n một c&aacute;i. Kha Hựu lập tức h&oacute;a đ&aacute;.<br />\r\n<br />\r\nTr&ecirc;n mặt Lăng Như kh&ocirc;ng giấu được n&eacute;t mừng rỡ: &quot;Kh&ocirc;ng phải v&igrave; sinh nhật t&ocirc;i n&ecirc;n em mới mặc th&agrave;nh như vậy đấy chứ?&quot;<br />\r\n<br />\r\nC&ocirc; n&agrave;ng tiểu thư n&agrave;y thật tự kỷ, xem ra trang phục như vậy cũng kh&ocirc;ng c&oacute; thể khiến Lăng Như ngoan ngo&atilde;n r&uacute;t lui rồi. Kha Hựu nh&iacute;u m&agrave;y: &quot;Ha ha, tr&ugrave;ng hợp h&ocirc;m nay cũng l&agrave; sinh nhật bạn t&ocirc;i. Hồi n&atilde;y mới ăn sinh nhất với c&ocirc; ấy xong.&quot;<br />\r\n<br />\r\n&quot;Em l&agrave;m g&igrave; c&oacute; bạn b&egrave; g&igrave;!&quot; Lăng Như lập tức thay đổi sắc mặt: &quot;Ng&agrave;y n&agrave;o t&ocirc;i cũng tới đ&acirc;y t&igrave;m em, t&acirc;m &yacute; của t&ocirc;i với em đ&atilde; rất r&otilde; r&agrave;ng rồi. Em kh&ocirc;ng từ chối, cũng kh&ocirc;ng chấp nhận t&ocirc;i. Rốt cuộc l&agrave; muốn như thế n&agrave;o!&quot;<br />\r\n<br />\r\nKha Hựu kh&ocirc;ng c&oacute; đẩy Lăng Như đang gần s&aacute;p lại gần, chỉ lui ra sau từng bước. Thẳng đến khi bị quầy bar chặn đường lui, Kha Hựu mới nghi&ecirc;ng đầu n&oacute;i: &quot;Chị say rồi.&quot;<br />\r\n<br />\r\nLăng Như tho&aacute;ng chốc lại trở n&ecirc;n ai o&aacute;n: &quot;V&igrave; sao kh&ocirc;ng chịu ở b&ecirc;n t&ocirc;i?&quot;<br />\r\n<br />\r\nKha Hựu cầu xin Phiếu mập gi&uacute;p đỡ, Tiểu Bao ở một b&ecirc;n che miệng cười. C&ograve;n Phiếu mập lại lộ vẻ kh&oacute; xử, cũng kh&ocirc;ng biết l&agrave;m thế n&agrave;o cho phải. Kha Hựu chỉ c&oacute; thể ki&ecirc;n tr&igrave; tự m&igrave;nh đối ph&oacute;: &quot;Kh&ocirc;ng d&aacute;m.&quot;<br />\r\n<br />\r\nLăng Như sửng sốt một ch&uacute;t, từ từ lui ra sau: &quot;Em sợ Lăng H&uacute;c?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng d&aacute;m tr&egrave;o cao.&quot; Kha Hựu thở d&agrave;i một hơi, đứng thẳng người, quay mặt đi, cũng d&ugrave;ng giọng điệu ai o&aacute;n để n&oacute;i: &quot;T&ocirc;i chỉ l&agrave; một đứa du c&ocirc;n thấp b&eacute; ở Phi Hồ, chị l&agrave; tiểu thư Phi Lang, kh&ocirc;ng hợp.&quot;<br />\r\n<br />\r\nLăng Như nghe xong, &aacute;nh mắt trở n&ecirc;n &ocirc;n nhu hẳn, k&eacute;o Kha Hựu đang tr&aacute;nh m&igrave;nh về: &quot;N&oacute;i như vậy, cũng kh&ocirc;ng phải l&agrave; v&igrave; em kh&ocirc;ng th&iacute;ch th&ocirc;i?&quot;<br />\r\n<br />\r\nKha Hựu dời trọng t&acirc;m c&acirc;u chuyện, c&ocirc; d&ugrave;ng đ&ocirc;i mắt đen l&aacute;y, s&aacute;ng trong như gương, kh&ocirc;ng c&oacute; nửa phần tạp chất nh&igrave;n Lăng Như: &quot;C&oacute; nhiều kh&aacute;ch rồi, một m&igrave;nh Tiểu Bao kh&ocirc;ng kham nổi, t&ocirc;i phải l&agrave;m việc.&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i cũng l&agrave; kh&aacute;ch của em.&quot; Lăng Như nh&igrave;n Kha Hựu, lấy ra một cuộn tiền trong bao tay đập l&ecirc;n quầy: &quot;Phiếu ca, h&ocirc;m nay l&agrave; sinh nhật t&ocirc;i, t&ocirc;i muốn để Kha Hựu v&agrave;o ph&ograve;ng bao biểu diễn cho t&ocirc;i.&quot;<br />\r\n<br />\r\nPhiếu mập đương nhi&ecirc;n kh&ocirc;ng muốn đắc tội Lăng Như, ngượng ng&ugrave;ng n&oacute;i: &quot;Lăng tiểu thư ph&acirc;n ph&oacute;, đương nhi&ecirc;n c&oacute; thể.&quot;<br />\r\n<br />\r\n---<br />\r\n<br />\r\nCồn k&iacute;ƈɦ ŧɦ&iacute;ƈɦ, &acirc;m nhạc m&ecirc; hoặc, trong ph&ograve;ng bao n&agrave;o l&agrave; uống rượu chơi đo&aacute;n số, nuốt m&acirc;y nhả kh&oacute;i, đổ x&uacute;c xắc, ai ai cũng l&agrave;m c&agrave;n n&oacute;i giỡn, h&ocirc; to gọi nhỏ gầm rũ ầm ĩ. Trong g&oacute;c ph&ograve;ng, từng cặp nam nữ hoặc l&agrave; nữ nữ tản ra hơi thở đầy mờ &aacute;m.<br />\r\n<br />\r\nChỉ c&oacute; Lăng Như, chủ nh&acirc;n ph&ograve;ng bao n&agrave;y đ&ecirc;m nay, một m&igrave;nh si m&ecirc; với Kha Hựu ở sau quầy bar.<br />\r\n<br />\r\nỞ đ&acirc;y đối với Lăng Như l&agrave; thi&ecirc;n đường, đối với Kha Hựu lại l&agrave; địa ngục gi&agrave;y v&ograve;.<br />\r\n<br />\r\nKha Hựu thầm mong thời gian mau mau tr&ocirc;i đi. Dưới cặp mắt n&oacute;ng ch&aacute;y của Lăng tiểu thư, Kha Hựu tưởng tượng bản th&acirc;n sắp bị nấu chảy rồi. Bất đắc dĩ chỉ c&oacute; thể hết sức chuy&ecirc;n t&acirc;m pha chế rượu, Kha Hựu cũng kh&ocirc;ng ngẩng đầu l&ecirc;n, hỏi: &quot;Chị muốn uống c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot; Bloody Mary.&quot;<br />\r\n<br />\r\nBloody Mary với nền l&agrave; Vodka, Kha Hựu bỏ ba vi&ecirc;n đ&aacute; v&agrave;o ly Vodka, lại th&ecirc;m muối cần t&acirc;y... Sau đ&oacute; cầm b&igrave;nh pha chế đơn giản lắc l&ecirc;n lắc xuống, ước chừng qua một ph&uacute;t đồng hồ, r&oacute;t tiếp nước c&agrave; chua đ&atilde; &eacute;p sẵn để trong quầy v&agrave;o, rồi tiếp tục lắc.<br />\r\n<br />\r\nLăng Như r&otilde; r&agrave;ng c&oacute; ch&uacute;t thất vọng. B&igrave;nh thường Kha Hựu pha chế trong quầy ba kh&ocirc;ng phải như thế...<br />\r\n<br />\r\n&quot;Bằng ca, c&ocirc; em pha chế n&agrave;y của anh chỉ được nhi&ecirc;u đ&oacute; th&ocirc;i &agrave;?&quot; Cửa ph&ograve;ng bao đột ngột bị đẩy ra, Minh Huy khi&ecirc;u kh&iacute;ch đi đến.<br />\r\n<br />\r\nKha Hựu ngừng tay, nh&igrave;n ph&iacute;a Tăng Bằng Vũ.<br />\r\n<br />\r\nVẻ mặt Tăng Bằng Vũ thật l&agrave; xấu x&iacute;, đứng ở ph&iacute;a sau Minh Huy, nh&aacute;y mắt với Kha Hựu.<br />\r\n<br />\r\n&quot;Như, sinh nhật vui vẻ.&quot; Minh Huy đi tới quầy bar, &aacute;nh mắt cứ nh&igrave;n chằm chằm v&agrave;o Kha Hựu, kh&oacute;e miệng hơi nhếch l&ecirc;n, n&oacute;i: &quot;C&oacute; v&agrave;i phần tư sắc, đổi tới chỗ của anh Huy đi. Anh Huy n&acirc;ng đỡ em.&quot;<br />\r\n<br />\r\nKha Hựu kh&ocirc;ng để &yacute; đến Minh Huy, mặt m&agrave;y lạnh nhạt, mở nắp, bỏ th&ecirc;m rượu đắng Angostura v&agrave; nước ớt v&agrave;o. Nắm tay cầm chặt b&igrave;nh pha chế, xoay v&agrave;i v&ograve;ng trong kh&ocirc;ng trung rồi lại rơi v&agrave;o tay Kha Hựu. B&igrave;nh pha chế trong tay Kha Hựu li&ecirc;n tục chuyển động, động t&aacute;c mặc d&ugrave; kh&ocirc;ng hoa lệ, nhưng tần suất chuyển động cũng c&oacute; thể khiến người ta hoa mắt.<br />\r\n<br />\r\nCộp một tiếng, b&igrave;nh inox nện một c&aacute;i thật mạnh tr&ecirc;n quầy gỗ, tiếng động nặng nề k&eacute;o d&agrave;i. Kha Hựu kh&ocirc;ng c&oacute; lập tức mở nắp ra, chỉ hơi cười, ng&oacute;n tay đặt tr&ecirc;n b&igrave;nh pha chế g&otilde; nhẹ, lẳng lặng đếm số trong l&ograve;ng.<br />\r\n<br />\r\nTăng Bằng Vũ nhướn m&agrave;y, thản nhi&ecirc;n đuổi mấy người trong ph&ograve;ng bao, nh&agrave;n nh&atilde; đi tới s&ocirc; pha ngồi xuống, chờ xem kịch.<br />\r\n<br />\r\nMột con pha chế m&agrave; cũng ki&ecirc;u ngạo cỡ đ&oacute;, thể diện của Minh Huy kh&ocirc;ng chấp nhận được, lại kh&ocirc;ng tiện nổi đi&ecirc;n, khẽ hừ một tiếng: &quot;Bằng ca, thủ hạ của anh đều mất dạy như thế hở?&quot;<br />\r\n<br />\r\nKha Hựu nhẹ nh&agrave;ng m&agrave; r&oacute;t hỗn hợp rượu đ&atilde; pha v&agrave;o ly đ&aacute;y bằng đ&atilde; chuẩn bị trước, trang tr&iacute; th&ecirc;m l&aacute;t chanh, c&acirc;y ống h&uacute;t rồi đẩy đến trước mặt Lăng Như, sau đ&oacute; ngẩng đầu mỉm cười với Minh Huy: &quot;Anh Huy, muốn uống g&igrave; ạ?&quot;<br />\r\n<br />\r\n&quot;Hole in one.&quot; Minh Huy d&ugrave;ng &aacute;nh mắt đầy th&acirc;m &yacute; quan s&aacute;t Kha Hựu, đi thẳng tới chỗ b&ecirc;n cạnh Tăng Bằng Vũ ngồi xuống.<br />\r\n<br />\r\nTăng Bằng Vũ hiển nhi&ecirc;n l&agrave; muốn để Minh Huy ăn ch&uacute;t bế m&ocirc;n canh, Kha Hựu khẽ nhếch nụ cười nhạt, thản nhi&ecirc;n đ&aacute;p: &quot;Kh&ocirc;ng biết.&quot;<br />\r\n<br />\r\n&quot;C&ocirc; biết c&aacute;i g&igrave;?&quot; Minh Huy đốt điếu thuốc, h&iacute;p mắt.<br />\r\n<br />\r\n&quot;Cảm ơn.&quot; Lăng Như mừng rỡ nhận lấy ly cocktail Kha Hựu đưa qua, bưng ly ngồi xuống b&ecirc;n cạnh Minh Huy, khuỷu tay cọ cọ Minh Huy, thấp giọng n&oacute;i: &quot;Đừng quậy.&quot;<br />\r\n<br />\r\nKha Hựu liếc nh&igrave;n Tăng Bằng Vũ vẫn cười kh&ocirc;ng n&oacute;i, y&ecirc;n lặng c&uacute;i đầu &eacute;p một ly nước chanh, r&oacute;t v&agrave;o loại rượu b&igrave;nh thường, lại r&oacute;t th&ecirc;m Champagne đến đầy ly, cắt l&aacute;t chanh mỏng trang tr&iacute; l&ecirc;n v&agrave;nh ly. Sau đ&oacute; bưng ly rượu đi tới trước mặt Minh Huy, d&acirc;ng l&ecirc;n bằng hai tay.<br />\r\n<br />\r\nMinh Huy nhận lấy uống một ngụm, kh&ocirc;ng c&oacute; t&yacute; cảm gi&aacute;c cồn n&agrave;o, cau m&agrave;y gh&eacute;t bỏ n&oacute;i: &quot;Đ&acirc;y l&agrave; thứ g&igrave;?&quot;<br />\r\n<br />\r\n&quot;C&acirc;y mắc cỡ.&quot; &Aacute;nh mắt Kha Hựu kh&oacute;a chặt tr&ecirc;n mặt Minh Huy, kh&ocirc;ng c&oacute; g&igrave; bất ngờ xảy ra, sắc mặt hắn nhất thời trầm xuống, nụ cười t&agrave; mị nơi kh&oacute;e miệng nhất tời cứng ngắc.<br />\r\n<br />\r\n&quot;Ha ha ha.&quot; Tăng Bằng Vũ rốt cuộc mở miệng: &quot;A Huy, chớ để &yacute;. C&ocirc; n&agrave;ng n&agrave;y t&iacute;nh t&igrave;nh như thế đ&oacute;.&quot;<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Kha Hựu m&oacute;c tiền thu được ở sau giao dịch khi n&atilde;y ra đưa tới trước mặt Tăng Bằng Vũ: &quot;Bằng ca.&quot;<br />\r\n<br />\r\nTăng Bằng Vũ phất tay, ra hiệu Kha Hựu lui ra ngo&agrave;i, Kha Hựu vừa định xoay người rời đi, Lăng Như đ&atilde; ngăn cản: &quot;Bằng ca, đ&ecirc;m nay Tiểu Kha bị em bao rồi m&agrave;.&quot;<br />\r\n<br />\r\nKha Hựu nhất thời sửng sốt, khu&ocirc;n mặt vốn trấn định tự nhi&ecirc;n hiện l&ecirc;n một tầng đỏ ửng.<br />\r\n<br />\r\nMinh Huy bị Kha Hựu chỉnh như thế, trong ngực nghẹn kh&iacute; chưa ti&ecirc;u, &aacute;p chế cơn nộ, hắn ra vẻ khinh thường n&oacute;i: &quot;Bằng ca, kh&ocirc;ng ngờ anh thế m&agrave; lại ăn hai m&aacute;ng đ&oacute;?&quot; Minh Huy vỗ vỗ vai Tăng Bằng Vũ: &quot;C&oacute; phải bị cớm d&ograve;m ng&oacute; s&aacute;t sao qu&aacute; kh&ocirc;ng tuồn h&agrave;ng được n&ecirc;n thiếu tiền? N&oacute;i với anh em một tiếng, chỗ em muốn loại n&agrave;o cũng c&oacute;, đưa đến cho anh?&quot;<br />\r\n<br />\r\nTăng Bằng Vũ đột nhi&ecirc;n cầm ly rượu đập xuống b&agrave;n, bực bội kh&ocirc;ng n&oacute;i g&igrave;.<br />\r\n<br />\r\n&quot;Huy, đừng n&oacute;i lung tung!&quot; Lăng Như đấm nhẹ Minh Huy một c&aacute;i, trừng hắn.<br />\r\n<br />\r\n&quot;Ấy, Lăng Như y&ecirc;u dấu.&quot; Minh Huy &ocirc;m ngực, cười h&igrave; h&igrave; n&oacute;i với Kha Hựu: &quot;Vậy xin lỗi, pha cho một ly tr&agrave; đ&aacute; Long Island.&quot; Minh Huy quay đầu r&oacute;t ly rượu, n&oacute;i với Tăng Bằng Vũ: &quot;Bằng ca, chỉ đ&ugrave;a một ch&uacute;t, kh&ocirc;ng ngại chớ?&quot;<br />\r\n<br />\r\nKha Hựu nh&igrave;n Tăng Bằng Vũ ngồi b&ecirc;n cạnh Minh Huy đang tươi cười, đại kh&aacute;i suy đo&aacute;n được &yacute; đồ của Tăng Bằng Vũ. H&ocirc;m nay Minh Huy tới, n&oacute;i r&otilde; ch&iacute;nh l&agrave; g&acirc;y sự khiến cho hai đường ph&acirc;n tranh, Tăng Bằng Vũ vẫn muốn giằng co, kh&ocirc;ng để Minh Huy thực hiện được. Thế nhưng nếu l&agrave; tới cửa g&acirc;y chuyện, cũng kh&ocirc;ng thể để hắn qu&aacute; ki&ecirc;u ngạo. Tăng Bằng Vũ kh&ocirc;ng tự ra mặt, v&igrave; vậy mới để m&igrave;nh l&agrave;m kh&oacute; dễ Minh Huy. Minh Huy sẽ kh&ocirc;ng thể mượn cớ. Chỉ c&oacute; điều, nếu cứ như vậy, bản th&acirc;n sẽ kh&ocirc;ng thể tr&aacute;nh khỏi cảnh bị đẩy l&ecirc;n nơi đầu s&oacute;ng ngọn gi&oacute;, th&agrave;nh vật hi sinh. Nhưng cũng may, Minh Huy nể mặt Lăng Như, cũng kh&ocirc;ng d&aacute;m l&agrave;m chuyện g&igrave; qu&aacute; đ&aacute;ng với m&igrave;nh.<br />\r\n<br />\r\nBỗng c&oacute; người g&otilde; cửa ph&ograve;ng bao, người đến c&uacute;i đều n&oacute;i b&ecirc;n tai Tăng Bằng Vũ: &quot;Ngưng tỷ tới, ở trong ph&ograve;ng tổng thống tr&ecirc;n lầu.&quot;<br />\r\n<br />\r\nTăng Bằng Vũ cau m&agrave;y, lập tức n&oacute;i với Kha Hựu: &quot;Ch&agrave;o hỏi đ&agrave;ng ho&agrave;ng.&quot; Lại quay đầu ch&agrave;o Lăng Như v&agrave; Minh Huy: &quot;Anh c&ograve;n c&oacute; ch&uacute;t việc, c&ocirc; cậu cứ ở lại chơi.&quot;<br />\r\n<br />\r\n-------<br />\r\n<br />\r\nEditor c&oacute; lời muốn n&oacute;i: chắc mọi người cũng đo&aacute;n được con Ferrari kia l&agrave; của ai rồi nhỉ~~<br />\r\n<br />\r\n(*) h&igrave;nh của em ấy (c&oacute; lẽ l&agrave; h&agrave;ng limited, kh&ocirc;ng t&igrave;m được ảnh chất lượng cho lắm)<img alt=\"Ái Ngục (Tiền Truyện) - Chương 2: Thật ra Kha Hựu là H\" src=\"https://em.wattpad.com/f57b29bbadd5f6ec8c5e70474f18a1a10b848411/68747470733a2f2f73332e616d617a6f6e6177732e636f6d2f776174747061642d6d656469612d736572766963652f53746f7279496d6167652f6644314c41366253324a445264673d3d2d3531313437393931362e313530326637366632393234663866633236303336373530343936372e6a7067\" /><br />\r\n<br />\r\n&Agrave; m&agrave; đến tận đ&acirc;y m&igrave;nh vẫn chưa hiểu c&aacute;i t&ecirc;n chương cho lắm~ :3</h2>', 0, 'chuong-2-that-ra-kha-huu-la-h'),
(17, 13, 'Chương 1: .1: Kết Hôn Trong Chớp Nhoáng', 'Chương 1: .1: Kết Hôn Trong Chớp Nhoáng', '<h2>9 giờ, ban đ&ecirc;m m&ecirc; người.<br />\r\n<br />\r\nĐường Ninh ở buổi tiệc độc th&acirc;n uống hơi nhiều, n&ecirc;n được chồng chưa cưới đưa về chung cư. Đầu đau như sắp nứt vậy, chỉ l&agrave; l&uacute;c c&ocirc; mở mắt ra, lại nh&igrave;n thấy một đ&ocirc;i nam nữ đang h&ocirc;n nhau kịch liệt ở dưới &aacute;nh đ&egrave;n mờ ảo.<br />\r\n<br />\r\nĐường Ninh giống như bị s&eacute;t đ&aacute;nh tr&uacute;ng, đờ đẫn nh&igrave;n hai người đang h&ocirc;n nhau đến đất trời đảo lộn ở b&ecirc;n m&eacute;p giường của c&ocirc;, cơn giận trong l&ograve;ng chớp c&aacute;i lao ra.<br />\r\n<br />\r\n&ldquo;Vũ Nhu, đừng l&agrave;m loạn, Đường Ninh vừa mới ngủ!&rdquo; Người đ&agrave;n &ocirc;ng vuốt ve eo của người phụ nữ, kiềm chế n&oacute;i.<br />\r\n<br />\r\n&ldquo;Thế n&agrave;o? Anh sợ vợ chưa cưới của m&igrave;nh tỉnh lại &agrave;?&rdquo; Mặc Vũ Nhu o&aacute;n giận hỏi, &ldquo;Ng&agrave;y mai hai người kết h&ocirc;n rồi, đ&ecirc;m h&ocirc;m nay, anh phải gi&agrave;nh cho em!&rdquo;<br />\r\n<br />\r\n&ldquo;Cục cưng, đừng l&agrave;m ồn, hai ta sang ph&ograve;ng kh&aacute;c đi!&rdquo; Người đ&agrave;n &ocirc;ng nhỏ giọng dỗ d&agrave;nh.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng được, em muốn ở chỗ n&agrave;y cơ! Em muốn l&agrave;m ở trước mặt c&ocirc; ta!&rdquo; Mặc Vũ Nhu nhanh ch&oacute;ng cởϊ &aacute;&sigma; sơ mi của người đ&agrave;n &ocirc;ng, hai người lại h&ocirc;n nhau thắm thiết lần nữa.<br />\r\n<br />\r\nĐường Ninh cố n&eacute;n nước mắt rơi xuống, c&ocirc; l&agrave;m sao cũng kh&ocirc;ng nghĩ đến việc người chồng chưa cưới ng&agrave;y mai sẽ đi l&atilde;nh chứng của m&igrave;nh lại d&aacute;m mang theo bồ nh&iacute; y&ecirc;u đương vụиɠ ŧяộʍ trước giường c&ocirc;.<br />\r\n<br />\r\n&ldquo;Ngoan, ch&uacute;ng ta đi v&agrave;o ph&ograve;ng tắm, kh&ocirc;ng phải em th&iacute;ch nhất ph&ograve;ng tắm sao?&rdquo;<br />\r\n<br />\r\n&ldquo;Thế th&igrave; anh đi v&agrave;o chuẩn bị nước trước đi!&rdquo; Mặc Vũ Nhu nhẹ đẩy lồng ngực người đ&agrave;n &ocirc;ng, chờ anh đi rồi, c&ocirc; mới đi tới trước mặt Đường Ninh, c&uacute;i đầu cười chế giễu, &ldquo;Đường Ninh, ng&agrave;y mai, t&ocirc;i sẽ kh&ocirc;ng để Vũ Ph&agrave;m c&oacute; cơ hội đi đăng k&yacute; kết h&ocirc;n với c&ocirc; đ&acirc;u, bởi v&igrave; t&ocirc;i c&oacute; thai rồi, anh ấy l&agrave; của t&ocirc;i!&rdquo;<br />\r\n<br />\r\nĐường Ninh nắm chặt hai tay, cố nhịn kh&ocirc;ng cho m&igrave;nh ph&aacute;t ra &acirc;m thanh, cho đến khi trong ph&ograve;ng tắm truyền đến tiếng r&ecirc;и ɾỉ nhiệt t&igrave;nh của hai người kia, c&ocirc; mới c&oacute; cảm gi&aacute;c l&ograve;ng m&igrave;nh đang vỡ vụn.<br />\r\n<br />\r\nBa năm trước, c&ocirc; vẫn l&agrave; người mẫu số một ở Thịnh Kinh, nhưng v&igrave; người đ&agrave;n &ocirc;ng n&agrave;y, c&ocirc; từ bỏ hết tất cả, nhường vị tr&iacute; người mẫu số một cho Mặc Vũ Nhu. H&oacute;a ra, c&ocirc; chỉ đang may v&aacute;y cưới cho người kh&aacute;c. Kh&ocirc;ng, c&ocirc; chỉ đang nằm mơ m&agrave; th&ocirc;i, đ&acirc;y l&agrave; một cơn &aacute;c mộng, c&ocirc; chỉ cần tỉnh lại sẽ tốt.<br />\r\n<br />\r\nĐường Ninh cố gắng lừa dối bản th&acirc;n, nhưng đợi đến nửa đ&ecirc;m, Mặc Vũ Nhu lấy l&yacute; do trong người kh&ocirc;ng khỏe, k&eacute;o H&agrave;n Vũ Ph&agrave;m rời khỏi kh&aacute;ch sạn, v&agrave; H&agrave;n Vũ Ph&agrave;m cũng nhanh ch&oacute;ng bỏ c&ocirc; ở lại.<br />\r\n<br />\r\nNhưng l&agrave; ng&agrave;y mai họ phải đi đăng k&yacute; kết h&ocirc;n rồi!<br />\r\n<br />\r\nĐường Ninh mang theo nụ cười chua x&oacute;t, vẫn giữ nguy&ecirc;n kế hoạch. V&agrave;o s&aacute;ng sớm ng&agrave;y h&ocirc;m sau, c&ocirc; l&aacute;i xe đến Cục D&acirc;n Ch&iacute;nh, l&uacute;c xuống xe gọi điện thoại cho H&agrave;n Vũ Ph&agrave;m, nhưng chỉ nghe được c&acirc;u trả lời lạnh l&ugrave;ng của anh qua điện thoại: &ldquo;Mặc Vũ Nhu bị thương tr&ecirc;n s&acirc;n khấu, anh cần phải xử l&yacute; chuyện quan trọng, việc đăng k&yacute; để ng&agrave;y kh&aacute;c b&agrave;n lại.&rdquo;<br />\r\n<br />\r\nSẽ kh&ocirc;ng c&oacute; ng&agrave;y kh&aacute;c! Đường Ninh tuyệt vọng, ở trong l&ograve;ng nhắc nhở ch&iacute;nh m&igrave;nh.<br />\r\n<br />\r\nĐường Ninh xo&aacute;y người, mang l&ecirc;n k&iacute;nh r&acirc;m chuẩn bị rời đi, lại nh&igrave;n thấy một b&oacute;ng d&aacute;ng cao lớn đi về hướng c&ocirc;, tr&ecirc;n người mặc một bộ t&acirc;y trang cổ điển m&agrave;u xanh đậm, trong t&uacute;i tiền ở trước ngực c&oacute; mang theo một c&aacute;i khăn qu&agrave;ng cổ m&agrave;u đỏ rượu, nh&igrave;n xuống ph&iacute;a dưới, l&agrave; đ&ocirc;i ch&acirc;n thon d&agrave;i thẳng tắp c&ugrave;ng đ&ocirc;i gi&agrave;y da mũi nhọn m&agrave;u n&acirc;u s&aacute;ng b&oacute;ng.<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng n&agrave;y&hellip;. Khiến người kh&aacute;c cảm nhận được sự &aacute;p lực mạnh mẽ, giống như vị ho&agrave;ng đế cao qu&yacute; ở thời Trung Cổ vậy!<br />\r\n<br />\r\nĐặc biệt l&agrave; l&uacute;c anh ta đến gần, mặc d&ugrave; c&oacute; đeo k&iacute;nh r&acirc;m, nhưng l&agrave;&hellip; khu&ocirc;n mặt kh&ocirc;ng dao động như đao kia, c&ugrave;ng với đ&ocirc;i m&ocirc;i mỏng gợi cảm, vẫn khiến người kh&aacute;c phải đi&ecirc;n cuồng.<br />\r\n<br />\r\nĐường Ninh nhận ra người đ&agrave;n &ocirc;ng n&agrave;y, Mặc Đ&igrave;nh - tổng gi&aacute;m đốc c&ocirc;ng ty giải tr&iacute; Hải Thụy. V&agrave;o l&uacute;c c&ocirc; c&ograve;n nổi tiếng, đ&atilde; từng nh&igrave;n thấy người n&agrave;y trong một buổi vũ hội.<br />\r\n<br />\r\nH&ocirc;m nay anh ta cũng kết h&ocirc;n?</h2>', 0, 'chuong-1-1-ket-hon-trong-chop-nhoang'),
(18, 13, 'Chương 2: .2: Kết Hôn Trong Chớp Nhoáng (2)', 'Chương 2: .2: Kết Hôn Trong Chớp Nhoáng (2)', '<h2>&ldquo;Tổng gi&aacute;m đốc, c&ocirc; Tr&igrave; kh&ocirc;ng c&oacute; mặt đ&uacute;ng giờ&hellip; Đ&atilde; trễ mười ph&uacute;t!&rdquo; Trợ l&yacute; đi theo sau cung k&iacute;nh n&oacute;i.<br />\r\n<br />\r\n&ldquo;Gọi điện thoại b&aacute;o cho nh&agrave; họ Tr&igrave;, nếu kết h&ocirc;n cũng đến muộn, vậy đừng đến nữa.&rdquo; Người đ&agrave;n &ocirc;ng lạnh l&ugrave;ng n&oacute;i.<br />\r\n<br />\r\n&ldquo;Nhưng chủ tịch đ&atilde; n&oacute;i, h&ocirc;m nay tổng gi&aacute;m đốc nhất định phải kết h&ocirc;n, cho d&ugrave; cưới y&ecirc;u qu&aacute;i hay g&igrave; th&igrave; chủ tịch cũng kh&ocirc;ng phản đối&hellip;&rdquo; Trợ l&yacute; rụt r&egrave; n&oacute;i.<br />\r\n<br />\r\n&ldquo;T&ugrave;y &yacute; chọn một người n&agrave;o đ&oacute; ở trong danh s&aacute;ch, t&ocirc;i chỉ cho cậu thời gian nửa tiếng&hellip;&rdquo; Người đ&agrave;n &ocirc;ng l&ecirc;n tiếng, c&oacute; vẻ kh&ocirc;ng m&agrave;ng ch&uacute;t t&igrave;nh cảm n&agrave;o.<br />\r\n<br />\r\nH&oacute;a ra&hellip;. l&agrave; người c&ugrave;ng cảnh ngộ. Nhưng lại c&oacute; ch&uacute;t điểm kh&aacute;c nhau, Mặc Đ&igrave;nh đứng tr&ecirc;n đỉnh cao, muốn ai cũng dễ như trở b&agrave;n tay, hơn nữa anh ta c&ograve;n l&agrave; người đ&agrave;n &ocirc;ng độc th&acirc;n ho&agrave;ng kim, thứ kh&ocirc;ng cần nhất ch&iacute;nh l&agrave; t&igrave;nh y&ecirc;u, kết h&ocirc;n cũng chỉ để ứng ph&oacute; với gia trưởng.<br />\r\n<br />\r\nTrong đầu Đường Ninh đột nhi&ecirc;n nảy ra một &yacute; tưởng, c&ocirc; th&aacute;o k&iacute;nh r&acirc;m xuống, đi đến trước mặt người đ&agrave;n &ocirc;ng, nhỏ giọng n&oacute;i: &ldquo;Gi&aacute;m đốc Mặc, c&ocirc; d&acirc;u của anh kh&ocirc;ng đến, ch&uacute; rể của t&ocirc;i cũng chạy, kh&ocirc;ng bằng&hellip; ch&uacute;ng ta kết h&ocirc;n với nhau?&rdquo;<br />\r\n<br />\r\nTrợ l&yacute; đứng ph&iacute;a sau Mặc Đ&igrave;nh sợ ng&acirc;y người, kh&ocirc;ng ngờ lại c&oacute; người phụ nữ mạnh dạn như thế&hellip;.<br />\r\n<br />\r\nSống lưng Đường Ninh thẳng tắp, bởi v&igrave; hiện tại, c&ocirc; đang d&ugrave;ng hết dũng kh&iacute; của bản th&acirc;n.<br />\r\n<br />\r\nMặc Đ&igrave;nh th&aacute;o k&iacute;nh r&acirc;m xuống, để lộ đ&ocirc;i mắt m&agrave;u đen, con ngươi giống hệt kim cương, to&aacute;t ra tia s&aacute;ng sắc b&eacute;n, nhưng một l&aacute;t sau, anh quay đầu nh&igrave;n trợ l&yacute;, n&oacute;i: &ldquo;Đưa tư liệu của c&ocirc; ta cho t&ocirc;i!&rdquo;<br />\r\n<br />\r\nTrợ l&yacute; đương nhi&ecirc;n biết th&acirc;n phận của Đường Ninh, trực tiếp lấy di động ra t&igrave;m kiếm t&ecirc;n của Đường Ninh, cung k&iacute;nh đưa ra. Hai ph&uacute;t sau, đ&ocirc;i m&ocirc;i mỏng của người đ&agrave;n &ocirc;ng khẽ mở, chỉ thốt ra một chữ với c&ocirc;: &ldquo;Được!&rdquo;<br />\r\n<br />\r\nĐường Ninh cảm thấy c&oacute; thể gặp được Mặc Đ&igrave;nh l&agrave; may mắn của c&ocirc;, bởi v&igrave; anh kh&ocirc;ng cần lợi dụng phụ nữ, cũng kh&ocirc;ng cần thứ gọi l&agrave; t&igrave;nh y&ecirc;u, cũng kh&ocirc;ng thiếu bạn giường.<br />\r\n<br />\r\nHơn nữa, c&ocirc; muốn H&agrave;n Vũ Ph&agrave;m phải hối hận!<br />\r\n<br />\r\nThủ tục kết h&ocirc;n của hai người được xử l&yacute; nhanh ch&oacute;ng, sau nửa tiếng, Đường Ninh đ&atilde; lấy được giấy đăng k&yacute; kết h&ocirc;n, từ giờ trở đi, c&ocirc; đ&atilde; th&agrave;nh phụ nữ c&oacute; chồng.<br />\r\n<br />\r\n&ldquo;Gi&aacute;m đốc Mặc, kh&ocirc;ng biết anh c&oacute; thời gian hay kh&ocirc;ng, c&oacute; thể nghe t&ocirc;i n&oacute;i mấy c&acirc;u được kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\n&ldquo;L&ecirc;n xe!&rdquo; Mặc Đ&igrave;nh đeo k&iacute;nh r&acirc;m l&ecirc;n, bước ra khỏi nơi đăng k&yacute;.<br />\r\n<br />\r\nĐường Ninh theo s&aacute;t Mặc Đ&igrave;nh, sau khi l&ecirc;n xe Rolls-Royce, c&ocirc; hơi lo lắng nh&igrave;n Mặc Đ&igrave;nh n&oacute;i: &ldquo;Cảm ơn anh đ&atilde; kết h&ocirc;n với t&ocirc;i, nếu anh c&oacute; y&ecirc;u cầu t&ocirc;i l&agrave;m g&igrave;, t&ocirc;i sẽ phối hợp v&ocirc; điều kiện, nhưng c&oacute; hai chuyện, t&ocirc;i mong anh c&oacute; thể đồng &yacute;.&rdquo;<br />\r\n<br />\r\n&ldquo;N&oacute;i!&rdquo; Mặc Đ&igrave;nh c&oacute; ch&uacute;t mệt mỏi nới lỏng vạt &aacute;o.<br />\r\n<br />\r\n&ldquo;Thứ nhất, nếu kh&ocirc;ng phải vạn bất đắc dĩ, th&igrave; việc kết h&ocirc;n n&agrave;y kh&ocirc;ng được c&ocirc;ng khai. Thứ hai, kh&ocirc;ng được xen v&agrave;o việc ri&ecirc;ng của t&ocirc;i, anh y&ecirc;n t&acirc;m, nếu ch&uacute;ng ta đ&atilde; kết h&ocirc;n, t&ocirc;i sẽ kh&ocirc;ng tiếp x&uacute;c với người đ&agrave;n &ocirc;ng kh&aacute;c.&rdquo;<br />\r\n<br />\r\nMặc Đ&igrave;nh nghe Đường Ninh n&oacute;i xong, kh&oacute;e miệng hơi nhếch l&ecirc;n, trong xe dần xuất hiện m&ugrave;i nguy hiểm: &ldquo;T&ocirc;i đồng &yacute;&hellip;. T&ocirc;i cho c&ocirc; thời gian đi dọn dẹp đống qu&aacute; khứ lộn xộn của m&igrave;nh, thử kết h&ocirc;n c&ugrave;ng t&ocirc;i. Nhưng l&agrave;, sau nửa năm&hellip; T&ocirc;i sẽ c&ocirc;ng khai tin tức kết h&ocirc;n của ch&uacute;ng ta một c&aacute;ch to&agrave;n diện.&rdquo;<br />\r\n<br />\r\n&ldquo;C&aacute;m ơn!&rdquo; Đường Ninh gật đầu.<br />\r\n<br />\r\n&ldquo;C&ograve;n nữa&hellip; T&ocirc;i kh&ocirc;ng đồng &yacute; chuyện vợ chồng ở ri&ecirc;ng! Cho c&ocirc; thời gian ba ng&agrave;y, dọn đến nơi t&ocirc;i chỉ định, sau đ&oacute; trợ l&yacute; của t&ocirc;i sẽ li&ecirc;n lạc với c&ocirc;!&rdquo;<br />\r\n<br />\r\nĐường Ninh kh&ocirc;ng phản đối, nếu đ&atilde; l&agrave; vợ chồng, y&ecirc;u cầu như vậy rất hợp l&yacute;, cho n&ecirc;n c&ocirc; ngoan ngo&atilde;n gật đầu: &ldquo;T&ocirc;i đồng &yacute;!&rdquo;<br />\r\n<br />\r\n&ldquo;Vậy th&igrave; tốt!&rdquo;<br />\r\n<br />\r\nSau khi hai b&ecirc;n thống nhất, Đường Ninh xuống xe rời đi, trợ l&yacute; ngồi v&agrave;o ghế điều khiển, nh&igrave;n Mặc Đ&igrave;nh qua k&iacute;nh chiếu hậu: &ldquo;Tổng gi&aacute;m đốc, giờ trở về c&ocirc;ng ty, hay trở về nh&agrave; cũ n&oacute;i chuyện n&agrave;y với chủ tịch?&rdquo;<br />\r\n<br />\r\n&ldquo;Cậu l&aacute;i xe đi theo Đường Ninh, sau đ&oacute; n&oacute;i cho t&ocirc;i h&agrave;nh tung của c&ocirc; ấy!&rdquo; Mặc Đ&igrave;nh n&oacute;i với trợ l&yacute; của m&igrave;nh, sau đ&oacute; xuống xe.<br />\r\n<br />\r\nBỗng nhi&ecirc;n t&igrave;m anh kết h&ocirc;n, kh&ocirc;ng c&oacute; khả năng kh&ocirc;ng c&oacute; chuyện g&igrave; xảy ra!<br />\r\n<br />\r\nL&agrave; tổng gi&aacute;m đốc của c&ocirc;ng ty giải tr&iacute; xuy&ecirc;n quốc gia, anh kh&ocirc;ng phải chưa từng nghe qua c&aacute;i t&ecirc;n Đường Ninh n&agrave;y. Đường Ninh từng l&agrave; người mẫu nổi tiếng trong giới người mẫu, nhưng v&agrave;o ba năm trước, c&ocirc; đột nhi&ecirc;n từ chối lời mời của c&ocirc;ng ty người mẫu hạng nhất Tinh Ho&agrave;ng, bị phong s&aacute;t (*) ho&agrave;n to&agrave;n. Sau đ&oacute;, c&ocirc; tuy&ecirc;n bố k&yacute; hợp đồng với c&ocirc;ng ty giải tr&iacute; Thi&ecirc;n Nghệ, hơn nữa c&ograve;n c&oacute; quan hệ th&acirc;n thiết với &ocirc;ng chủ H&agrave;n Vũ Ph&agrave;m.<br />\r\n<br />\r\n(*): &quot;Phong s&aacute;t&quot; l&agrave; một thuật ngữ trong giới giải tr&iacute; Hoa ngữ, d&ugrave;ng để chỉ những ng&ocirc;i sao bị kh&aacute;n giả quay lưng, sự nghiệp gần như bị cản trở to&agrave;n tập, kh&oacute; c&oacute; cơ hội quay lại hoạt động showbiz v&igrave; vướng phải scandal hay lỗi lầm cực lớn n&agrave;o đ&oacute;.</h2>', 0, 'chuong-2-2-ket-hon-trong-chop-nhoang-2'),
(19, 13, 'Chương 3: .1: Phản Đòn Đôi Cẩu Nam Nữ (*)', 'Chương 3: .1: Phản Đòn Đôi Cẩu Nam Nữ (*)', '<h2>(*) Cẩu nam nữ: &yacute; chỉ cặp đ&ocirc;i nɠɵạı ŧ&igrave;иɦ hoặc l&agrave;m chuyện xấu<br />\r\n<br />\r\nMột thế hệ si&ecirc;u mẫu từ đ&acirc;y ng&atilde; xuống&hellip;.<br />\r\n<br />\r\nC&oacute; điều giờ xem lại, chuyện xưa của Đường Ninh, c&oacute; vẻ kh&ocirc;ng chỉ giới hạn trong n&agrave;y.<br />\r\n<br />\r\n&hellip;&hellip;<br />\r\n<br />\r\nTr&ecirc;n thực tế, chưa bao giờ Đường Ninh thấy m&igrave;nh gan lớn như vậy, lại d&aacute;m kết h&ocirc;n với người xa lạ, nhưng chuyện n&agrave;y d&ugrave; sao cũng l&agrave;m rồi, c&ocirc; sẽ kh&ocirc;ng bao giờ hối hận.<br />\r\n<br />\r\nĐường Ninh quay lại xe chuẩn bị về nh&agrave;, nhưng c&ocirc; vừa mới khởi động xe, đ&atilde; nhận được cuộc gọi từ H&agrave;n Vũ Ph&agrave;m.<br />\r\n<br />\r\n&ldquo;Đường Ninh&hellip; Hiện giờ em đang ở đ&acirc;u?&rdquo;<br />\r\n<br />\r\n&ldquo;Ở trước cửa Cục D&acirc;n Ch&iacute;nh, đang chuẩn bị về nh&agrave;.&rdquo; Đường Ninh cố gắng l&agrave;m giọng điệu của m&igrave;nh b&igrave;nh tĩnh lại.<br />\r\n<br />\r\n&ldquo;Vũ Nhu c&oacute; một show diễn rất quan trọng, em đi thay cho c&ocirc; ấy đi, anh sẽ gọi chuy&ecirc;n gia trang điểm mang mặt nạ cho em, sẽ kh&ocirc;ng c&oacute; ai nhận ra đ&oacute; l&agrave; em đ&acirc;u.&rdquo; H&agrave;n Vũ Ph&agrave;m trực tiếp d&ugrave;ng giọng điệu ra lệnh đưa ra y&ecirc;u cầu cho Đường Ninh: &ldquo;Vũ Nhu bị thương, em chịu kh&oacute; một ch&uacute;t&hellip;.&rdquo;<br />\r\n<br />\r\n&ldquo;C&ocirc; Mặc kh&ocirc;ng phải bị thương sao? Vậy th&igrave; giới truyền th&ocirc;ng n&ecirc;n biết c&ocirc; ấy đang ở bệnh viện&hellip;&rdquo;<br />\r\n<br />\r\n&ldquo;Nhưng anh đ&atilde; n&oacute;i l&agrave; Vũ Nhu sẽ tham gia cho d&ugrave; bị thương, bảo em đi th&igrave; em cứ đi đi!&rdquo;<br />\r\n<br />\r\nThật kh&ocirc;ng biết xấu hổ! Trước kia, c&ocirc; thực sự đ&atilde; l&agrave;m rất nhiều chuyện ngu ngốc thay Mặc Vũ Nhu.<br />\r\n<br />\r\nH&oacute;a ra, tất cả đều l&agrave; lợi dụng, c&oacute; điều kh&ocirc;ng phải lần n&agrave;o c&ocirc; cũng sẽ gi&uacute;p Mặc Vũ Nhu may &aacute;o cưới!<br />\r\n<br />\r\nĐường Ninh giữ nguy&ecirc;n b&igrave;nh tĩnh, sau đ&oacute; gật đầu: &ldquo;Được, n&oacute;i cho em thời gian c&ugrave;ng địa chỉ, em lập tức đi qua.&rdquo;<br />\r\n<br />\r\n&ldquo;Đường Ninh, ch&uacute;ng ta sắp kết h&ocirc;n rồi, em cố gắng gi&uacute;p đỡ Vũ Nhu một ch&uacute;t, hiện tại sự nghiệp của c&ocirc; ấy đang đi l&ecirc;n!&rdquo;<br />\r\n<br />\r\n&ldquo;Em sẽ cố gắng gi&uacute;p đỡ c&ocirc; ấy!&rdquo; Đường Ninh n&oacute;i một c&aacute;ch ẩn &yacute;.<br />\r\n<br />\r\n&ldquo;Vậy anh c&uacute;p trước, l&aacute;t nữa ch&uacute;ng ta c&ugrave;ng nhau đi ăn cơm.&rdquo;<br />\r\n<br />\r\nH&agrave;n Vũ Ph&agrave;m ho&agrave;n to&agrave;n kh&ocirc;ng nhận ra được mọi chuyện đang dần thay đổi, c&oacute; khi b&acirc;y giờ anh ta c&ograve;n đang ngồi b&ecirc;n giường của Mặc Vũ Nhu lộ vẻ dịu d&agrave;ng ngọt ng&agrave;o? Đường Ninh c&uacute;p m&aacute;y, gọi điện thoại cho người đại diện, người đại diện của c&ocirc; nghe xong, lập tức nổi giận:<br />\r\n<br />\r\n&ldquo;Gi&aacute;m đốc H&agrave;n muốn em thay thế một kẻ hạng B như Mặc Vũ Nhu đi tr&igrave;nh diễn? C&oacute; nhầm kh&ocirc;ng vậy? Nếu kh&ocirc;ng phải em giải nghệ, th&igrave; kẻ như c&ocirc; ta ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; chỗ đứng n&agrave;o trong giới người mẫu.&rdquo;<br />\r\n<br />\r\n&ldquo;Chị Long, em đ&atilde; đồng &yacute; rồi&hellip;.&rdquo; Đường Ninh b&igrave;nh tĩnh n&oacute;i.<br />\r\n<br />\r\n&ldquo;Em thật sự muốn đi?&rdquo; Người đại diện tức đến hộc m&aacute;u, Đường Ninh v&agrave; Mặc Vũ Nhu đều l&agrave; người mẫu của c&ocirc;ng ty giải tr&iacute; Thi&ecirc;n Nghệ, c&oacute; điều Đường Ninh giải nghệ, n&ecirc;n người đại diện như c&ocirc; đ&atilde; phải chịu rất nhiều sự chế nhạo.<br />\r\n<br />\r\nĐường Ninh biết người đại diện của m&igrave;nh đang b&ecirc;nh vực kẻ yếu, v&igrave; vậy c&ocirc; cười n&oacute;i: &ldquo;Em sẽ kh&ocirc;ng ngu ngốc để người kh&aacute;c lợi dụng nữa đ&acirc;u!&rdquo;<br />\r\n<br />\r\nNghe được c&acirc;u n&agrave;y của Đường Ninh, người đại hiện lập tức sống dậy: &ldquo;N&oacute;i như vậy em đ&atilde; c&oacute; kế hoạch rồi?&rdquo;<br />\r\n<br />\r\n&ldquo;Chị Long, em hiện giờ chỉ c&oacute; thể tin tưởng chị, chị gi&uacute;p em l&agrave;m chuyện n&agrave;y được kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\n&ldquo;Em n&oacute;i đi.&rdquo; Người đại diện tuyệt đối chỉ trung th&agrave;nh với Đường Ninh, d&ugrave; sao hai người họ đều c&oacute; c&ugrave;ng lợi &iacute;ch chung.<br />\r\n<br />\r\n&ldquo;Mặc Vũ Nhu d&ugrave;ng mọi thủ đoạn giả vờ đi tr&igrave;nh diễn cho d&ugrave; bị thương, bởi v&igrave; n&oacute; ảnh hưởng đến cuộc b&igrave;nh chọn mười người mẫu h&agrave;ng đầu của năm, chị gi&uacute;p em đi đến bệnh viện Thi&ecirc;n Hạp thử xem.&rdquo;<br />\r\n<br />\r\n&ldquo;Chị hiểu rồi, bắt được bằng chứng c&ocirc; ta vẫn c&ograve;n ở trong bệnh viện, sau đ&oacute; vạch trần cho mọi người biết!&rdquo; Người đại diện k&iacute;ch động n&oacute;i.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng phải, c&ograve;n c&oacute; th&ocirc;ng tin lớn hơn nữa, c&ocirc; ta đang mang thai, l&agrave; con của H&agrave;n Vũ Ph&agrave;m! Tiện thể, chị gi&uacute;p em chuẩn bị một bản thanh minh, nội dung l&agrave; H&agrave;n Vũ Ph&agrave;m nhiều lần lợi dụng em đi tr&igrave;nh diễn gi&uacute;p Mặc Vũ Nhu, cụ thể ở mấy show diễn kia, cũng nhờ chị t&igrave;m chứng cứ gi&uacute;p em!&rdquo;<br />\r\n<br />\r\nNgười đại diện giật m&igrave;nh kinh ngạc, sau đ&oacute; mới hiểu được l&yacute; do th&aacute;i độ của Đường Ninh thay đổi, đ&uacute;ng l&agrave; đ&ocirc;i cẩu nam nữ kh&ocirc;ng biết xấu hổ, đ&atilde; y&ecirc;u đương vụиɠ ŧяộʍ c&ograve;n xem Đường Ninh như người hầu m&agrave; sai bảo.<br />\r\n<br />\r\n&ldquo;Đường Ninh, em cứ y&ecirc;n t&acirc;m, hai việc n&agrave;y chị sẽ gi&uacute;p em l&agrave;m tốt.&rdquo;<br />\r\n<br />\r\nĐường Ninh kh&ocirc;ng đ&aacute;p, nhưng trong l&ograve;ng lại v&ocirc; c&ugrave;ng b&igrave;nh tĩnh, người ta đối xử với c&ocirc; như thế n&agrave;o, c&ocirc; cũng sẽ đối xử với người ta như thế đấy!</h2>', 0, 'chuong-3-1-phan-don-doi-cau-nam-nu'),
(20, 13, 'Chương 4: .2: Phản Đòn Đôi Cẩu Nam Nữ (2)', 'Chương 4: .2: Phản Đòn Đôi Cẩu Nam Nữ (2)', '<h2>Nghĩ xong, Đường Ninh vội v&agrave;ng thu dọn đồ đạc, một m&igrave;nh l&aacute;i xe đi đến s&agrave;n catwalk, gặp trợ l&yacute; ở Thi&ecirc;n Nghệ của Mặc Vũ Nhu.<br />\r\n<br />\r\nĐ&oacute; l&agrave; một người đ&agrave;n &ocirc;ng c&oacute; th&acirc;n h&igrave;nh cường tr&aacute;ng, đ&atilde; kết h&ocirc;n, l&agrave; một kẻ kh&aacute; xảo tr&aacute;, tất nhi&ecirc;n, cũng c&oacute; thế lực kh&aacute; lớn.<br />\r\n<br />\r\nAnh ta kh&aacute; kh&aacute;ch kh&iacute; với Đường Ninh, mặc d&ugrave; danh kh&iacute; của Đường Ninh đ&atilde; qua đi, nhưng ở ph&iacute;a sau Đường Ninh c&oacute; nh&agrave; họ Đường kh&ocirc;ng thể xem thường.<br />\r\n<br />\r\n&ldquo;Sao b&acirc;y giờ c&ocirc; mới đến? Nhanh&hellip; t&ocirc;i dẫn c&ocirc; đi v&agrave;o trang điểm&hellip;.&rdquo;<br />\r\n<br />\r\n&ldquo;Show diễn h&ocirc;m nay l&agrave; show diễn thế n&agrave;o?&rdquo; Đường Ninh bị anh ta k&eacute;o đi, vừa đi vừa hỏi.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng phải show diễn quan trọng lắm!&rdquo; Trợ l&yacute; đ&aacute;p, c&oacute; điều, đ&acirc;y l&agrave; show diễn của nh&atilde;n hiệu nổi danh ở nước Ph&aacute;p - nh&atilde;n hiệu trang sức HF.<br />\r\n<br />\r\nSau show diễn n&agrave;y, Mặc Vũ Nhu sẽ c&oacute; khả năng trở th&agrave;nh người ph&aacute;t ng&ocirc;n của HF, vốn dĩ show diễn n&agrave;y bị hủy do Mặc Vũ Nhu bị thương, nhưng gi&aacute;m đốc H&agrave;n bảo c&oacute; thể lợi dụng Đường Ninh, tất nhi&ecirc;n anh phải nắm chặt lấy cơ hội n&agrave;y!<br />\r\n<br />\r\nThực ra l&uacute;c ở tr&ecirc;n đường Đường Ninh cũng đ&atilde; t&igrave;m hiểu về show diễn mang t&ecirc;n Vương Miện Ng&ocirc;i Sao n&agrave;y. Trợ l&yacute; của Mặc Vũ Nhu n&oacute;i dối với c&ocirc;, kh&ocirc;ng lẽ&hellip; B&igrave;nh thường c&ocirc; dễ lừa như vậy?<br />\r\n<br />\r\n&ldquo;Cũng nhờ địa vị hiện tại của Vũ Nhu, c&oacute; ph&ograve;ng trang điểm ri&ecirc;ng, c&ocirc; l&agrave; người l&ecirc;n s&acirc;n khấu cuối c&ugrave;ng, l&aacute;t nữa sẽ c&oacute; triển l&atilde;m trang sức, đ&acirc;y l&agrave; tr&igrave;nh tự l&ecirc;n s&acirc;n khấu&hellip;&rdquo; Trợ l&yacute; chỉ v&agrave;o trang sức ở tr&ecirc;n b&agrave;n trang điểm, n&oacute;i với Đường Ninh, sau đ&oacute; bảo thợ trang điểm trang điểm cho c&ocirc;.<br />\r\n<br />\r\nH&agrave;n Vũ Ph&agrave;m cho rằng, c&ocirc; đeo mặt nạ l&ecirc;n th&igrave; người kh&aacute;c sẽ kh&ocirc;ng nhận ra c&ocirc; l&agrave; Đường Ninh?<br />\r\n<br />\r\nMặc d&ugrave; l&agrave;m như vậy chỉ v&igrave; b&iacute; qu&aacute; h&oacute;a liều, nhưng c&ocirc; sẽ cho đ&ocirc;i cẩu nam nữ n&agrave;y một bất ngờ kh&ocirc;ng kịp trở tay.<br />\r\n<br />\r\nTr&ecirc;n thực tế, trợ l&yacute; của Mặc Đ&igrave;nh vẫn lu&ocirc;n đi theo Đường Ninh, sau khi hỏi thăm cẩn thận, biết c&ocirc; sẽ thay Mặc Vũ Nhu đi catwalk, liền gọi điện thoại cho Mặc Đ&igrave;nh. Mặc Đ&igrave;nh nghe xong, lập tức li&ecirc;n lạc với thư k&yacute;: &ldquo;T&ocirc;i muốn đến hiện trường catwalk của trang sức HF, sắp xếp ngay cho t&ocirc;i!&rdquo;<br />\r\n<br />\r\n&ldquo;V&acirc;ng, thưa tổng gi&aacute;m đốc!&rdquo;<br />\r\n<br />\r\nMặc d&ugrave; đấy kh&ocirc;ng phải một show diễn cao cấp, nhưng anh muốn nh&igrave;n thấy thực lực ch&acirc;n ch&iacute;nh của Đường Ninh.<br />\r\n<br />\r\n&hellip;..<br />\r\n<br />\r\nV&agrave;o 11 giờ buổi s&aacute;ng, ở s&acirc;n khấu show diễn Hải Nghệ đ&atilde; truyền đến tiếng &acirc;m nhạc cổ điển, r&otilde; r&agrave;ng show diễn đ&atilde; bắt đầu.<br />\r\n<br />\r\nỞ trong ph&ograve;ng h&oacute;a trang, Đường Ninh đ&atilde; trang điểm xong, đứng ở trước gương d&agrave;i y&ecirc;n lặng chờ đợi. C&ocirc; mặc một bộ v&aacute;y d&agrave;i b&oacute; s&aacute;t người, giản dị nhưng cũng kh&ocirc;ng mất đi sự th&aacute;nh khiết, tr&ecirc;n mặt mang một c&aacute;i mặt nạ m&agrave;u v&agrave;ng, lộ vẻ thần b&iacute;, t&oacute;c d&agrave;i buộc ở sau đầu, ph&iacute;a tr&ecirc;n c&agrave;i một đ&oacute;a hoa hồng m&agrave;u trắng, c&oacute; kh&iacute; chất xuất thần khiến người kh&oacute; thể tưởng tượng&hellip;<br />\r\n<br />\r\nTrợ l&yacute; ng&acirc;y người, thầm nghĩ, cho d&ugrave; Đường Ninh đứng y&ecirc;n tại chỗ, cũng c&oacute; thể khiến người kh&aacute;c kinh diễm. Đại ng&ocirc;n lần n&agrave;y của Mặc Vũ Nhu khẳng định sẽ gi&agrave;nh được!<br />\r\n<br />\r\n&ldquo;C&ocirc; sẽ ngồi tr&ecirc;n một chiếc ghế m&acirc;y từ tr&ecirc;n trời gi&aacute;ng xuống, đi v&agrave;o s&agrave;n catwalk! Chiếc lắc tay n&agrave;y ch&iacute;nh l&agrave; Vương Miện Ng&ocirc;i Sao, để t&ocirc;i đeo l&ecirc;n cho c&ocirc;!&rdquo; Trợ l&yacute; gỡ lắc tay xuống, cẩn thận đeo gi&uacute;p Đường Ninh, c&oacute; điều&hellip; Đường Ninh mảnh mai hơn Mặc Vũ Nhu, v&igrave; vậy c&aacute;i lắc tay n&agrave;y c&oacute; vẻ lớn hơn cổ tay c&ocirc;, l&uacute;c đeo l&ecirc;n n&oacute; sẽ trượt xuống c&aacute;nh tay&hellip;<br />\r\n<br />\r\nChiếc lắc tay n&agrave;y do ch&iacute;nh người s&aacute;ng lập HF thiết kế v&igrave; con g&aacute;i y&ecirc;u của m&igrave;nh, ở giữa những sợi li&ecirc;n m&agrave;u v&agrave;ng l&agrave; một chiếc vương miện được khảm kim cương trắng, hai b&ecirc;n vương miện l&agrave; hai vi&ecirc;n đ&aacute; qu&yacute; m&agrave;u trắng h&igrave;nh ng&ocirc;i sao, tượng trưng cho người cha v&agrave; người mẹ đang bao quanh đứa b&eacute; của m&igrave;nh.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o để đeo l&ecirc;n&hellip; B&acirc;y giờ phải l&agrave;m sao?&rdquo;<br />\r\n<br />\r\n&ldquo;Anh c&oacute; tin t&ocirc;i kh&ocirc;ng?&rdquo; Đường Ninh bỗng hỏi trợ l&yacute; của Mặc Vũ Nhu.<br />\r\n<br />\r\n&ldquo;B&acirc;y giờ t&ocirc;i chỉ c&oacute; thể tin c&ocirc;.&rdquo; Trợ l&yacute; gật đầu, trong gi&acirc;y ph&uacute;t quan trọng n&agrave;y, anh chỉ c&oacute; thể tin tưởng người mẫu. Đ&acirc;y l&agrave; đại ng&ocirc;n của Mặc Vũ Nhu, nếu l&agrave;m hỏng n&oacute;, gi&aacute;m đốc H&agrave;n khẳng định sẽ đuổi việc anh.<br />\r\n<br />\r\n&ldquo;Vậy anh giao chuyện n&agrave;y cho t&ocirc;i&hellip;&rdquo; Đường Ninh tự tin n&oacute;i.<br />\r\n<br />\r\n&ldquo;Vậy c&ocirc; đi chuẩn bị nhanh đi&hellip; Sắp đến lượt c&ocirc; l&ecirc;n s&acirc;n khấu rồi!&rdquo; Trợ l&yacute; ho&agrave;n to&agrave;n kh&ocirc;ng ch&uacute; &yacute; đến đ&ocirc;i mắt s&aacute;ng ngời ẩn dưới l&ocirc;ng mi của Đường Ninh.</h2>', 0, 'chuong-4-2-phan-don-doi-cau-nam-nu-2');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(21, 14, 'Chương 1: Cứu tôi, tôi cho cô ba trăm tỷ rưỡi!', 'Chương 1: Cứu tôi, tôi cho cô ba trăm tỷ rưỡi!', '<h2>&quot;Cứu t&ocirc;i, t&ocirc;i cho c&ocirc; ba trăm tỷ rưỡi!&quot; Một giọng nam trầm, kh&agrave;n đặc vang l&ecirc;n b&ecirc;n tai.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, cả người T&ocirc; No&atilde;n T&acirc;m đều đờ đẫn, tr&ecirc;n đường đi l&agrave;m về đ&atilde; bị một người đ&agrave;n &ocirc;ng v&agrave; v&agrave;o người th&igrave; cũng th&ocirc;i đi, đảng n&agrave;y c&ograve;n bị va đến ng&atilde; v&agrave;o đống r&aacute;c,<br />\r\n<br />\r\nM&ugrave;i hỗn hợp của tất cả c&aacute;c loại r&aacute;c xung quanh khiến người kh&aacute;c muốn n&ocirc;n mửa, nhưng vẫn cố gắng sống chết nhịn lại, kh&ocirc;ng n&ocirc;n ra ngo&agrave;i.<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i b&atilde;i r&aacute;c, tiếng bước ch&acirc;n hỗn loạn kh&ocirc;ng ngừng vang l&ecirc;n qua lại. &quot;Đuổi theo! Nhất định phải bắt được Lệ Minh Viễn!&quot; &quot;Anh ta bị thương rồi, kh&ocirc;ng thể chạy xa được!&quot;<br />\r\n<br />\r\n&quot;Thuốc cũng sắc ph&aacute;t huy t&aacute;c dụng rồi, đ&ecirc;m nay ch&uacute;ng ta nhất định phải bắt được anh ta, nếu kh&ocirc;ng th&igrave; ch&uacute;ng ta sẽ kh&ocirc;ng biết ăn n&oacute;i với c&ocirc; chủ thể n&agrave;o đ&acirc;u!&quot; .<br />\r\n<br />\r\nGiọng n&oacute;i dần xa dần, &yacute; thức trong n&atilde;o bộ của T&ocirc; No&atilde;n T&acirc;m cuối c&ugrave;ng cũng trở lại. Lệ Minh Viễn, c&ocirc; đ&atilde; từng nh&igrave;n thấy c&aacute;i t&ecirc;n n&agrave;y tr&ecirc;n c&aacute;c tạp, anh l&agrave; Lệ Minh Viễn, Tổng gi&aacute;m đốc của Tập đo&agrave;n Quốc Doanh. Người đ&agrave;n &ocirc;ng đẹp trai tr&ecirc;n tạp ch&iacute; đến mức khiến người ta phỉ nhổ lại ch&iacute;nh l&agrave; người đ&agrave;n &ocirc;ng đang nằm b&ecirc;n cạnh c&ocirc;! Cứu! Cắt răng cũng phải cứu về! C&ocirc; đang thiếu tiền. Anh lại c&oacute; tiền. Anh n&oacute;i cứu anh, anh sẽ đưa cho c&ocirc; ba trăm tỷ rưỡi! Mẹ c&ocirc; ốm nặng cần gh&eacute;p thận gấp. &quot;Đi theo t&ocirc;i, t&ocirc;i đưa chủ về nh&agrave;!&quot;.<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m đem hết sức lực c&aacute;i mạng gi&agrave; của m&igrave;nh c&ocirc;ng người đ&agrave;n &ocirc;ng về nh&agrave;, nhưng c&ocirc; lại hồn nhi&ecirc;n kh&ocirc;ng biết rằng m&igrave;nh cũng đang đồng thời cũng một con s&oacute;i đ&oacute;i về nh&agrave;,<br />\r\n<br />\r\nVề đến nh&agrave;, T&ocirc; No&atilde;n T&acirc;m khi&ecirc;ng nửa người của người đ&agrave;n &ocirc;ng kia ngồi vững tr&ecirc;n s&ocirc; pha, sau đ&oacute; xoay người đ&oacute;ng cửa lại.<br />\r\n<br />\r\nĐ&egrave;n ph&ograve;ng bật s&aacute;ng, T&ocirc; No&atilde;n T&acirc;m đang định đi tới b&ecirc;n người đ&agrave;n &ocirc;ng b&agrave;n bạc th&ugrave; lao, nhưng vừa quay đầu lại, khu&ocirc;n mặt tuấn t&uacute; đ&atilde; lập tức tức giận.<br />\r\n<br />\r\nDưới &aacute;nh đ&egrave;n v&agrave;ng mờ ảo, những đường n&eacute;t tr&ecirc;n khu&ocirc;n mặt của đ&agrave;n &ocirc;ng giống như những t&aacute;c phẩm nghệ thuật tinh xảo do thượng để tạo ra, đẹp đến kinh ngạc.<br />\r\n<br />\r\nMột đ&ocirc;i mắt đen c&ograve;n s&aacute;ng lấp l&aacute;nh hơn cả vi&ecirc;n ngọc trai đen. Sống mũi cao vừa phải, m&ocirc;i mỏng m&iacute;m chặt như đang cố gắng chịu đựng điều g&igrave; đ&oacute;.<br />\r\n<br />\r\nChiều cao của người đ&agrave;n &ocirc;ng n&agrave;y vừa vặn cao hơn T&ocirc; No&atilde;n T&acirc;m một c&aacute;i đầu, đ&aacute;nh gi&aacute; to&agrave;n thể ch&iacute;nh l&agrave; một anh ch&agrave;ng si&ecirc;u cấp đẹp trai một trăm phần trăm!<br />\r\n<br />\r\nChỉ l&agrave; l&uacute;c n&agrave;y đ&ocirc;i mắt s&acirc;u thẳm kia lại tr&agrave;n đầy sự n&oacute;ng bỏng. T&ocirc; No&atilde;n T&acirc;m kh&ocirc;ng khỏi cười thầm. Trong l&ograve;ng c&ocirc; kh&ocirc;ng khỏi rơi &ldquo;lộp bộp&quot; v&agrave;i c&aacute;i. C&ocirc; đ&atilde; qu&aacute; quen thuộc với kiểu &aacute;nh mắt như vậy rồi.<br />\r\n<br />\r\nNgược lại, T&ocirc; No&atilde;n T&acirc;m kh&ocirc;ng hề hoảng sợ, d&aacute;m t&ugrave;y tiện đưa người kh&aacute;c về nh&agrave; th&igrave; hẳn phải l&agrave; người c&oacute; năng lực tự bảo vệ m&igrave;nh th&igrave; mới d&aacute;m l&agrave;m vậy.<br />\r\n<br />\r\nC&ocirc; rất b&igrave;nh tĩnh n&oacute;i với người đ&agrave;n &ocirc;ng, n&oacute;i: &quot;Chủ đ&atilde;... tr&uacute;ng phải c&aacute;i g&igrave; rồi?&quot; | Người đ&agrave;n &ocirc;ng tiếp tục nh&igrave;n c&ocirc; bằng &aacute;nh mắt đỏ, kh&ocirc;ng l&ecirc;n tiếng.<br />\r\n<br />\r\n&ldquo;Ặc. Ch&uacute; đừng l&agrave;m loạn, ngoan ngo&atilde;n ở lại đ&acirc;y, t&ocirc;i ra ngo&agrave;i mua thuốc cho ch&uacute;&quot; Mẹ n&oacute;, c&ocirc; biết ngay m&agrave;, tiền đầu c&oacute; dễ kiếm như vậy, trước ti&ecirc;n h&atilde;y nghĩ c&aacute;ch để anh trở lại b&igrave;nh thường đ&atilde; rồi mới b&agrave;n đến chuyện tiền nong sau.<br />\r\n<br />\r\nNhưng c&ocirc; vừa mới quay người đi th&igrave; đ&atilde; bị một lực lớn k&eacute;o lại, to&agrave;n bộ cơ thể của c&ocirc; đập mạnh v&agrave;o ngực của người đ&agrave;n &ocirc;ng đ&oacute;...<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m v&ocirc; thức đưa tay l&ecirc;n ngực người đ&agrave;n &ocirc;ng. | C&oacute; h&iacute;t một hơi thật s&acirc;u, ngước mắt l&ecirc;n nh&igrave;n anh n&oacute;i: &quot;N&agrave;y, ngoan, nghe lời, gần nh&agrave; t&ocirc;i c&oacute; một hiệu thuốc. T&ocirc;i đi một tẹo l&agrave; sẽ về ngay. Chủ nhịn một ch&uacute;t th&ocirc;i...&quot;<br />\r\n<br />\r\nNhưng người đ&agrave;n &ocirc;ng đ&atilde; chịu đựng đủ l&acirc;u rồi, l&agrave;m sao anh c&oacute; thể chịu đựng được nữa. Khoảnh khắc T&ocirc; No&atilde;n T&acirc;m bị đẩy l&ecirc;n giường, tr&aacute;i tim c&ocirc; ho&agrave;n to&agrave;n tan n&aacute;t.<br />\r\n<br />\r\nNhấc một ch&acirc;n l&ecirc;n, cổ dồn sức lực đạp v&agrave;o đầu người đ&agrave;n &ocirc;ng, chỉ muốn đ&aacute;nh anh một c&aacute;i l&agrave;m anh ngất đi, vậy th&igrave; sẽ kh&ocirc;ng c&oacute; chuyện g&igrave; xảy ra.<br />\r\n<br />\r\nC&ograve;n tiền nong th&igrave; cứ để người tỉnh lại rồi n&oacute;i sau.<br />\r\n<br />\r\nNhưng c&ocirc; kh&ocirc;ng ngờ rằng, người đ&agrave;n &ocirc;ng trước mặt cũng kh&ocirc;ng phải một kẻ v&ocirc; dụng. Anh đưa tay ra, bắt lấy c&aacute;i ch&acirc;n đ&oacute; của c&ocirc;, sau đ&oacute; nắm lấy n&oacute; trong b&agrave;n tay rồi k&eacute;o về ph&iacute;a trước.<br />\r\n<br />\r\nV&ocirc; t&igrave;nh, tư thế của hai người trở n&ecirc;n thật kỳ quặc. &quot;Ch&uacute; muốn l&agrave;m g&igrave;! T&ocirc;i l&agrave; người vừa cứu mạng ch&uacute; đ&oacute;!&quot; Người đ&agrave;n &ocirc;ng đ&atilde; mất đi l&yacute; tr&iacute;, trong đ&ocirc;i mắt th&acirc;m s&acirc;u kh&ocirc;ng c&ograve;n g&igrave; kh&aacute;c ngo&agrave;i một ngọn lửa đang ch&aacute;y hừng hực. | Ngay cả khi T&ocirc; No&atilde;n T&acirc;m c&oacute; đai đen của Taekwondo, sở trường l&agrave; đ&aacute;nh nhau th&igrave; giờ ph&uacute;t n&agrave;y c&ocirc; cũng ho&agrave;n to&agrave;n kh&ocirc;ng phải đối thủ của người đ&agrave;n &ocirc;ng.<br />\r\n<br />\r\nT&igrave;nh cảnh bỗng chốc hỗn loạn. Tr&aacute;i tim của T&ocirc; No&atilde;n T&acirc;m đ&atilde; suy sụp đến c&ugrave;ng cực! C&ocirc; hối hận rồi! Nhưng đ&atilde; qu&aacute; trễ... Người đ&agrave;n &ocirc;ng đ&atilde; mất đi l&yacute; tr&iacute;, nhưng &yacute; ch&iacute; của anh vẫn c&ograve;n đ&oacute;, Cuối c&ugrave;ng cũng mềm l&ograve;ng.<br />\r\n<br />\r\nĐ&ocirc;i mắt lạnh l&ugrave;ng ph&aacute;t ra một loại &aacute;nh s&aacute;ng kỳ dị, anh nh&igrave;n thẳng v&agrave;o c&ocirc;, giọng n&oacute;i trầm thấp, c&oacute; ch&uacute;t bất lực: &quot;Ngoan... C&aacute;i g&igrave; cũng cho c&ocirc;<br />\r\n<br />\r\n| L&uacute;c n&agrave;y, T&ocirc; No&atilde;n T&acirc;m chỉ cảm thấy... M&aacute; n&oacute;, cuộc sống chấm dứt từ đ&acirc;y!<br />\r\n<br />\r\n----------------------------</h2>', 0, 'chuong-1-cuu-toi-toi-cho-co-ba-tram-ty-ruoi'),
(22, 14, 'Chương 2: Bà đây khinh một tên đàn ông người giấy như chú!', 'Chương 2: Bà đây khinh một tên đàn ông người giấy như chú!', '<h2>Ng&agrave;y h&ocirc;m sau, trời s&aacute;ng choang, T&ocirc; No&atilde;n T&acirc;m bị đ&aacute;nh thức bởi tiếng chu&ocirc;ng b&aacute;o thức. Đầu c&ocirc; đau đến muốn nứt ra, c&ocirc; từ từ ngồi dậy khỏi giường, tắt đồng hồ b&aacute;o thức, rồi t&igrave;m kiếm người đ&agrave;n &ocirc;ng trong ph&ograve;ng. Những ph&aacute;t hiện ra rằng người đ&agrave;n &ocirc;ng kh&ocirc;ng c&ograve;n ở đ&oacute; nữa. Rất tốt! L&agrave;m xong chuyện th&igrave; chạy! Quả khốn nạn! N&oacute;i l&agrave; đưa ba trăm tỷ rưỡi, nhưng b&acirc;y giờ đến rằm cũng kh&ocirc;ng giữ lại một c&aacute;i. Đột nhi&ecirc;n, &aacute;nh mắt c&ocirc; men theo ch&uacute;t &aacute;nh s&aacute;ng nh&igrave;n đến tủ cạnh giường, c&oacute; một tờ giấy lặng lẽ nằm ở đấy. Tr&ecirc;n đ&oacute; c&oacute; viết số điện thoại, c&ograve;n c&oacute; cả t&ecirc;n nữa. Lệ Minh Viễn... Giỏi lắm! L&agrave;m xong chuyện xấu c&ograve;n d&aacute;m để lại phương thức li&ecirc;n lạc? B&agrave; đ&acirc;y khinh một t&ecirc;n đ&agrave;n &ocirc;ng người giấy như ch&uacute;! Ngay lập tức, T&ocirc;n No&atilde;n T&acirc;m kh&ocirc;ng chịu được nữa, c&ocirc; &ocirc;m nửa giận nhập d&atilde;y số v&agrave;o điện thoại, mau ch&oacute;ng gọi đi. Điện thoại đổ chu&ocirc;ng ba lần rồi được kết nối.<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m vừa mở miệng đ&atilde; mắng: &quot;Lệ Minh Viện đ&uacute;ng kh&ocirc;ng! &Ocirc;ng nội nh&agrave; ch&uacute; ấy, đ&uacute;ng l&agrave; một người dạ thủ, s&uacute;c sinh, kh&ocirc;ng phải cải loại tốt l&agrave;nh g&igrave;! B&agrave; đ&acirc;y tốt bụng cửu ch&uacute; một mạng, thế m&agrave; ch&uacute; lại đối xử với t&ocirc;i như vậy. Mẹ nh&agrave; chủ chứ, đ&acirc;y l&agrave; h&agrave;nh động m&agrave; một con người c&oacute; thể l&agrave;m ra được sao! Ngay cả khi l&uacute;c đ&oacute;, đầu &oacute;c chứ kh&ocirc;ng được minh mẫn m&agrave; chủ vẫn c&ograve;n X. mạnh đến vậy!&rdquo;. | Đầu d&acirc;y b&ecirc;n kia điện thoại: &quot;...&quot;<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m c&agrave;ng mắng c&agrave;ng tức, rồi tự m&igrave;nh lại kh&oacute;c trước.<br />\r\n<br />\r\n| &quot;Mẹ nh&agrave; chủ chử, chủ c&oacute; biết lần đầu l&agrave;m con g&aacute;i c&oacute; cảm gi&aacute;c như thế n&agrave;o kh&ocirc;ng? T&ocirc; No&atilde;n T&acirc;m t&ocirc;i đ&atilde; đ&atilde; bị t&ecirc;n khốn nh&agrave; ch&uacute; | hủy hoại cả đời rồi, chủ c&oacute; biết kh&ocirc;ng? T&ocirc;i c&ograve;n chưa c&oacute; bạn trai, c&ograve;n chưa y&ecirc;u đương lần n&agrave;o m&agrave; đ&atilde; bị chủ l&agrave;m vậy rồi! Thử đ&oacute; l&agrave; t&ocirc;i giữ g&igrave;n cho người m&agrave; t&ocirc;i y&ecirc;u, cũng như người y&ecirc;u t&ocirc;i, để cả hai c&ugrave;ng tận hưởng những gi&acirc;y ph&uacute;t tr&acirc;n qu&yacute; nhất! Nhưng tất cả đều bị ch&uacute; hủy hoại rồi! Đồ khốn, t&ocirc;i sẽ kh&ocirc;ng để y&ecirc;n cho chủ đầu!&quot;.<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m vừa kh&oacute;c vừa la h&eacute;t v&agrave;o điện thoại một hồi l&acirc;u, nhưng đầu d&acirc;y b&ecirc;n kia vẫn kh&ocirc;ng đả động g&igrave;. C&agrave;ng nghĩ c&agrave;ng tức giận, cuối c&ugrave;ng c&ocirc; &ocirc;m mặt kh&oacute;c th&agrave;nh tiếng. Chỉ nghe thấy ở đầu d&acirc;y b&ecirc;n kia, một giọng nam trầm thấp đột nhi&ecirc;n vang l&ecirc;n: &quot;T&ocirc;i c&oacute; thể chịu tr&aacute;ch nhiệm.&quot; Kh&ocirc;ng n&oacute;i th&igrave; kh&ocirc;ng sao, vừa n&oacute;i liền khiển T&ocirc; No&atilde;n T&acirc;m x&ugrave; l&ocirc;ng:<br />\r\n<br />\r\n&quot;Ch&uacute; chịu tr&aacute;ch nhiệm? Chủ chịu tr&aacute;ch nhiệm như thế n&agrave;o? Chủ c&oacute; thể trả lại những g&igrave; t&ocirc;i đ&atilde; mất kh&ocirc;ng? C&oacute; thể kh&ocirc;ng! T&ecirc;n khốn, chủ kh&ocirc;ng biết xấu hổ m&agrave; n&oacute;i ra từ tr&aacute;ch nhiệm!&quot;.<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m su&yacute;t ch&uacute;t nữa l&agrave; tắt thở. Chỉ nghe thấy ở b&ecirc;n kia vang l&ecirc;n một cầu kh&aacute;c: &quot;T&ocirc;i c&oacute; thể kết h&ocirc;n với c&ocirc;.&quot; &quot;Ai cần chứ! T&ocirc;i cũng kh&ocirc;ng th&iacute;ch chủ, ai cần ch&uacute; cưới t&ocirc;i? Chủ đi m&agrave; cưới cải &ocirc;ng nội chủ đấy, khốn nạn, cầm th&uacute;!&quot; &quot;B&iacute;p&rdquo; một tiếng, T&ocirc; No&atilde;n T&acirc;m đ&atilde; c&uacute;p điện thoại. Sau đ&oacute; c&ocirc; tức giận đến mức n&eacute;m điện thoại xuống đất, rồi lại đau l&ograve;ng bật dậy khỏi giường, cầm điện thoại l&ecirc;n xem. May mắn thay, kh&ocirc;ng bị hỏng. Nh&igrave;n thời gian tr&ecirc;n điện thoại, đ&atilde; mười một giờ s&aacute;ng.<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m kh&ocirc;ng quan t&acirc;m đến chuyện kh&aacute;c, vội v&agrave;ng từ tr&ecirc;n giường bật dậy, vội v&agrave;ng đi v&agrave;o ph&ograve;ng tắm rửa sạch sẽ, sau đ&oacute; đến bệnh viện đưa cơm trưa cho mẹ.<br />\r\n<br />\r\nKết quả l&agrave; vừa ra khỏi giường, hai ch&acirc;n liền mềm nhũn, cả người ng&atilde; xuống đất, su&yacute;t nữa t&eacute; xỉu. Ngay lập tức, trong l&ograve;ng c&ocirc; c&agrave;ng th&ecirc;m căm gh&eacute;t t&ecirc;n đ&agrave;n &ocirc;ng ch&oacute; m&aacute; Lệ Minh Viễn đ&oacute;.<br />\r\n<br />\r\nMười hai giờ trưa.<br />\r\n<br />\r\nKh&ocirc;ng kịp nấu thức ăn cho mẹ n&ecirc;n T&ocirc; No&atilde;n T&acirc;m chỉ đ&agrave;nh mua một phần thức ăn dinh dưỡng ở b&ecirc;n ngo&agrave;i rồi mang đến bệnh viện cho mẹ.<br />\r\n<br />\r\nKhi vừa bước đến cửa ph&ograve;ng của mẹ, c&ocirc; nghe thấy tiếng la h&eacute;t định tại nhức &oacute;c từ trong truyền ra: &ldquo;T&ocirc; Ngọc Mỹ, đồ đ&agrave;n b&agrave; h&egrave;n hạ! M&agrave;y mau chết sớm đi! Tao n&oacute;i cho m&agrave;y biết, nh&agrave; họ Cố chủng tạo sẽ kh&ocirc;ng trả gi&uacute;p m&agrave;y một đồng viện ph&iacute; n&agrave;o đ&acirc;u! Đừng tưởng rằng sau khi m&agrave;y chết rồi th&igrave; nh&agrave; họ Cố ch&uacute;ng tạo sẽ thu nhận c&aacute;i đứa con hoang đ&oacute;. Tao cảnh c&aacute;o m&agrave;y kh&ocirc;ng được gọi điện cho Cổ Minh Đức nữa! &Ocirc;ng ấy l&agrave; chồng tạo, kh&ocirc;ng li&ecirc;n quan g&igrave; đến m&agrave;y cả! Cũng sẽ kh&ocirc;ng bao giờ nhận con nhỏ thối tha đ&oacute; về nh&agrave; đầu!&quot;<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m vừa định x&ocirc;ng v&agrave;o th&igrave; nghe thấy tiếng mẹ m&igrave;nh vừa kh&oacute;c vừa van xin người phụ nữ: &quot;B&agrave; Cổ, cầu xin b&agrave;... No&atilde;n T&acirc;m l&agrave; đứa nhỏ của nh&agrave; họ Cố. Sau khi t&ocirc;i chết đi, con b&eacute; kh&ocirc;ng về nh&agrave; họ Cố th&igrave; sẽ đi đ&acirc;u đ&acirc;y.&quot;<br />\r\n<br />\r\n----------------------------</h2>', 0, 'chuong-2-ba-day-khinh-mot-ten-dan-ong-nguoi-giay-nhu-chu'),
(23, 14, 'Chương 3: Ba trăm tỷ rưỡi kia, là của cô!', 'Chương 3: Ba trăm tỷ rưỡi kia, là của cô!', '<h2>&quot;Đ&atilde; kh&ocirc;ng bỏ được n&oacute; rồi, th&igrave; dứt kho&aacute;t để cho n&oacute; ở c&ugrave;ng với m&agrave;y đến chết đi! D&ugrave; sao th&igrave; thử con g&aacute;i h&egrave;n hạ đỏ m&atilde;i vẫn ho&agrave;n h&egrave;n hạ th&ocirc;i, sống cũng chỉ tổ ph&aacute; hoại hạnh ph&uacute;c gia đ&igrave;nh của người kh&aacute;c!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng... T&ocirc;i kh&ocirc;ng muốn ph&aacute; ngoại gia đ&igrave;nh người ta, chuyện năm đ&oacute; c&oacute; hiểu lầm. L&agrave; do Cố Minh Đức..&quot; &quot;Ch&aacute;t!&quot; một tiếng, T&ocirc; Ngọc Mỹ mặc đồ bệnh nh&acirc;n bị đ&aacute;nh lệch cả mặt. &quot;Đồ h&egrave;n hạ! M&agrave;y c&ograve;n d&aacute;m n&oacute;i chuyện năm đ&oacute; với tao?&quot; .<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m kh&ocirc;ng nhịn nổi nữa, nhanh ch&oacute;ng x&ocirc;ng v&agrave;o, vung tay về ph&iacute;a khu&ocirc;n mặt ki&ecirc;u căng ương ngạnh, lu&ocirc;n tự cảm thấy hơn người ta một bậc kia.<br />\r\n<br />\r\n&quot;Ch&aacute;t! Ch&aacute;t!&quot; Hai tiếng v&agrave; vang l&ecirc;n, b&agrave; Cổ như vừa tỉnh mộng. Sau khi nh&igrave;n r&otilde; l&agrave; ai đ&aacute;nh, cả người b&agrave; ta như muốn nhảy dựng cả l&ecirc;n! &quot;T&ocirc; No&atilde;n T&acirc;m! M&agrave;y d&aacute;m đ&aacute;nh tao! M&agrave;y c&oacute; biết tao l&agrave; ai kh&ocirc;ng!&quot;<br />\r\n<br />\r\n&quot;B&agrave; l&agrave; ai chẳng li&ecirc;n quan g&igrave; đến t&ocirc;i! Mau c&uacute;t đi! Đ&acirc;y l&agrave; ph&ograve;ng bệnh do mẹ t&ocirc;i chi tiền ra để sắp xếp, kh&ocirc;ng hoan ngh&ecirc;nh thứ r&aacute;c rưởi như b&agrave; đến đ&acirc;y!&quot;<br />\r\n<br />\r\n&quot;Được l&agrave;m! T&ocirc; Ngọc Mỹ, đ&acirc;y ch&iacute;nh l&agrave; đứa con g&aacute;i ngoan m&agrave; m&agrave;y đ&atilde; sinh ra đấy! C&ograve;n muốn đến nh&agrave; Cổ bọn tao sống sao? Mơ đi!&quot;<br />\r\n<br />\r\n| &quot;&Ocirc;i chao, c&oacute; ai hiếm lạ nh&agrave; họ Cố c&aacute;c người đ&acirc;u! C&oacute; xin t&ocirc;i cũng kh&ocirc;ng đi! C&uacute;t nhanh đi! Nh&igrave;n b&agrave; th&ecirc;m ch&uacute;t nữa cũng khiến t&ocirc;i buồn n&ocirc;n!&quot;<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m vừa n&oacute;i, vừa ra tay th&ocirc; lỗ đẩy b&agrave; Cố ra, đẩy thẳng đến cửa ph&ograve;ng bệnh, sau đ&oacute; đứng ở h&agrave;nh lang hộ to một tiếng: &quot;B&aacute;c sĩ! Y tả! Phiền đến đ&acirc;y, chuyển mẹ t&ocirc;i tới ph&ograve;ng cao cấp, kh&ocirc;ng c&oacute; &yacute; kiến của b&agrave;, bất kỳ ai cũng kh&ocirc;ng được v&agrave;o quấy rầy!&quot; &quot;T&ocirc; No&atilde;n T&acirc;m! Con khốn nạn n&agrave;y! M&agrave;y nhớ kỹ đẩy cho tao! Sau n&agrave;y tao sẽ kh&ocirc;ng để m&agrave;y sống y&ecirc;n ổn đ&acirc;u!&quot; | &quot;B&agrave; Cổ y&ecirc;n t&acirc;m đi, nh&agrave; họ Cổ c&aacute;c người sau n&agrave;y c&oacute; xin t&ocirc;i đến, t&ocirc;i cũng kh&ocirc;ng đến! C&oacute; lưu lạc cũng kh&ocirc;ng đến nương nhờ b&agrave;, cho n&ecirc;n sau n&agrave;y t&ocirc;i sống c&oacute; tốt hay kh&ocirc;ng, kh&ocirc;ng phải điều m&agrave; b&agrave; c&oacute; thể định đoạt! Đừng c&oacute; lắm mồm nữa, c&uacute;t!&rdquo;.<br />\r\n<br />\r\nT&ocirc; No&atilde;n T&acirc;m ch&aacute;n gh&eacute;t đẩy b&agrave; ta đi, b&agrave; Cổ kh&ocirc;ng đứng vững bước ch&acirc;n lảo đảo, su&yacute;t nữa ng&atilde; lăn tr&ecirc;n mặt đất.<br />\r\n<br />\r\n&quot;T&ocirc; No&atilde;n T&acirc;m! Con h&egrave;n hạ n&agrave;y! Tạo cho m&agrave;y biết, m&agrave;y xong đời rồi!&quot;. | Vừa đ&uacute;ng l&uacute;c, y t&aacute; nghe thấy tiếng, vẻ mặt kh&oacute; hiểu nh&igrave;n T&ocirc; No&atilde;n T&acirc;m n&oacute;i: &quot;C&ocirc; T&ocirc;, kh&ocirc;ng phải c&ocirc; vẫn c&ograve;n đang trả g&oacute;p tiền giải phẫu cho mẹ c&ocirc; sao? Nhất định phải đổi sang ph&ograve;ng cao cấp? Ở đ&oacute; một đ&ecirc;m tốn hơn ba triệu đấy.&quot;<br />\r\n<br />\r\nB&agrave; Cổ vừa mới b&ograve; dậy nghe thấy thế, cười lạnh một tiếng n&oacute;i: &quot;Ha ha... T&ocirc; No&atilde;n T&acirc;m, đừng c&oacute; cố đấm ăn x&ocirc;i, chỉ c&oacute; hai kẻ ngh&egrave;o kiết x&aacute;c l&agrave; m&agrave;y v&agrave; mẹ m&agrave;y, c&ograve;n muốn ở ph&ograve;ng cao cấp? Hạng người như c&aacute;c người chỉ sợ đến chết cũng chưa biết được, c&aacute;i g&igrave; l&agrave; cao cấp!&quot;<br />\r\n<br />\r\n&quot;Cải loại đ&ecirc; h&egrave;n c&aacute;c người, chỉ xứng với cuộc sống kh&ocirc;ng bằng heo ch&oacute; m&agrave; th&ocirc;i!&rdquo;. Lời n&oacute;i n&agrave;y, khiến ngay cả y t&aacute; cũng li&ecirc;n tục nh&iacute;u m&agrave;y.<br />\r\n<br />\r\nB&agrave; Cổ n&agrave;y, đ&acirc;u c&ograve;n l&agrave; một phu nh&acirc;n quyền sang ph&uacute; qu&yacute;... Chẳng kh&aacute;c n&agrave;o một người đ&agrave;n b&agrave; chanh chua nơi phố chợ. | Nghe tới đ&acirc;y, c&ocirc; g&aacute;i tỏ ra v&ocirc; c&ugrave;ng quật cường mở miệng cười: &quot;Đổi! Nhận tiền thuế cho t&ocirc;i bốn nh&acirc;n vi&ecirc;n an ninh của bệnh viện, hai mươi tư tiếng đồng hồ thay phi&ecirc;n canh giữ ở cửa ph&ograve;ng bệnh của mẹ t&ocirc;i, kh&ocirc;ng c&oacute; sự cho ph&eacute;p của mẹ v&agrave; tối, kh&ocirc;ng cho ai v&agrave;o thăm!&quot;.<br />\r\n<br />\r\nNgủ, cũng đ&atilde; ngủ rồi! Thử đ&aacute;ng gi&aacute; nhất của đời con g&aacute;i, đ&atilde; kh&ocirc;ng thể giữ được. Như vậy, th&acirc;n nh&acirc;n duy nhất ở tr&ecirc;n đời n&agrave;y của c&ocirc;, c&ocirc; nhất định phải bảo vệ thật tốt! Ba trăm tỷ rưỡi m&agrave; Lệ Minh Viễn cam kết kia, nhất định l&agrave; của c&ocirc;!<br />\r\n<br />\r\n----------------------------</h2>', 0, 'chuong-3-ba-tram-ty-ruoi-kia-la-cua-co'),
(24, 15, 'Chương 1: Làm quen.', 'Chương 1: Làm quen.', '<h2>Tinh tinh!<br />\r\n<br />\r\nHạ Diệp nh&igrave;n xuống tin nhắn th&ocirc;ng b&aacute;o biến động số dư t&agrave;i khoản cuối c&ugrave;ng, ngo&agrave;i niềm vui v&igrave; kiếm được tiền ra c&ocirc; c&ograve;n cảm thấy nhẹ nh&otilde;m v&igrave; đ&atilde; được giải tho&aacute;t.<br />\r\n<br />\r\nTừ khi mới trưởng th&agrave;nh, c&ocirc; một m&igrave;nh tới nước M học tập, đ&egrave;n s&aacute;ch năm năm, trong năm năm đ&oacute; ngo&agrave;i học tập ra th&igrave; chỉ c&oacute; c&ocirc;ng việc.<br />\r\n<br />\r\nTừ năm thứ hai sau khi tới nước M, Hạ Diệp đ&atilde; kh&ocirc;ng ti&ecirc;u một xu n&agrave;o của gia đ&igrave;nh, năm hai mươi hai tuổi, c&ocirc; tốt nghiệp khoa Khoa học v&agrave; C&ocirc;ng nghệ Ivy League, sau đ&oacute; chăm chỉ l&agrave;m việc trong ba năm, trong suốt qu&atilde;ng thời gian đ&oacute;, c&ocirc; chẳng bao giờ để dư thừa một gi&acirc;y một ph&uacute;t n&agrave;o cho m&igrave;nh thở.<br />\r\n<br />\r\nTrong những năm th&aacute;ng ấy, c&ocirc; rất hiếm khi ph&aacute;t sinh những lịch tr&igrave;nh m&agrave; kh&ocirc;ng nằm trong kế hoạch, n&ecirc;n hiện tại muốn tự thưởng cho m&igrave;nh một chuyến rong chơi tự do tự tại.<br />\r\n<br />\r\nHạ Diệp quyết định thực hiện một chuyến du lịch tự do, đi tới đ&acirc;u hay tới đ&oacute;, từ th&agrave;nh phố biển Thủ Cảng cho tới th&agrave;nh phố Triều Ch&acirc;u.<br />\r\n<br />\r\nHai th&agrave;nh phố n&agrave;y đều nằm trong top th&agrave;nh phố cấp một quốc gia, thế nhưng diện mạo đ&ocirc; thị lại kh&aacute;c nhau một trời một vực.<br />\r\n<br />\r\nThủ Cảng l&agrave; một hải cảng l&acirc;u đời, nơi quy tụ c&aacute;c thương nh&acirc;n từ khắp nơi tr&ecirc;n thế giới tới đ&acirc;y giao thương, tạo n&ecirc;n sự đa dạng về văn h&oacute;a, nơi đ&acirc;y c&oacute; nền c&ocirc;ng nghiệp m&agrave;u x&aacute;m* phổ biến, lại l&agrave; cảng tự do*, l&agrave; một điểm thu h&uacute;t kh&aacute;ch du lịch rất nổi tiếng.<br />\r\n<br />\r\n*Ng&agrave;nh c&ocirc;ng nghiệp m&agrave;u x&aacute;m: Được d&ugrave;ng để chỉ những ng&agrave;nh nghề đụng đến m&eacute;p vực của ph&aacute;p luật, nhưng nh&igrave;n chung kh&ocirc;ng vi phạm ph&aacute;p luật, tu&acirc;n theo hệ thống ph&aacute;p luật hiện h&agrave;nh, n&oacute; cũng tiềm ẩn những nguy cơ nhất định cho x&atilde; hội, nhưng chưa c&oacute; cơ sở ph&aacute;p l&yacute; x&aacute;c định để hạn chế quản l&yacute;.<br />\r\n<br />\r\n*Cảng tự do: L&agrave; cảng hoặc khu vực cảng kh&ocirc;ng thuộc thẩm quyền của hải quan. Trong lĩnh vực n&agrave;y, h&agrave;ng h&oacute;a nước ngo&agrave;i c&oacute; thể được tự do gia c&ocirc;ng, đ&oacute;ng g&oacute;i lại, sửa đổi, xếp, dỡ, lưu kho, trưng b&agrave;y, t&aacute;i xuất, v.v. m&agrave; kh&ocirc;ng cần kiểm so&aacute;t hải quan v&agrave; được miễn thuế hải quan. Tuy nhi&ecirc;n, khi h&agrave;ng h&oacute;a đi v&agrave;o cơ quan hải quan của quốc gia nơi ch&uacute;ng đặt trụ sở, th&igrave; phải nộp thuế hải quan.<br />\r\n<br />\r\nTh&agrave;nh phố Triều Ch&acirc;u l&agrave; một th&agrave;nh phố c&oacute; bề d&agrave;y di sản lịch sử, với những t&ograve;a nh&agrave; cổ k&iacute;nh trang nghi&ecirc;m v&agrave; lộng lẫy, quy hoạch đ&ocirc; thị đẹp mắt, phần lớn người d&acirc;n l&agrave; nh&acirc;n vi&ecirc;n văn ph&ograve;ng ổn định, so với trước đ&acirc;y, th&igrave; nơi đ&acirc;y ho&agrave;n to&agrave;n th&iacute;ch hợp cho việc sinh sống l&acirc;u d&agrave;i.<br />\r\n<br />\r\n&mdash;&ndash;<br />\r\n<br />\r\nTrong một kh&aacute;ch sạn nhỏ tại Thủ Cảng&hellip;<br />\r\n<br />\r\n&ldquo;Cho hỏi c&oacute; c&ograve;n ph&ograve;ng trống kh&ocirc;ng?&rdquo; Cả hai người c&ugrave;ng đồng thanh hỏi nh&acirc;n vi&ecirc;n tại quầy lễ t&acirc;n.<br />\r\n<br />\r\nNh&acirc;n vi&ecirc;n lễ t&acirc;n ngẩng đầu nh&igrave;n hai vị kh&aacute;ch đứng ph&iacute;a trước, ngập ngừng n&oacute;i: &ldquo;Chỉ&hellip; Chỉ c&ograve;n lại một ph&ograve;ng ti&ecirc;u chuẩn hai giường đơn th&ocirc;i ạ.&rdquo;<br />\r\n<br />\r\nKế hoạch kh&ocirc;ng theo kịp sự bất ngờ ngo&agrave;i &yacute; muốn, chưa kể đến việc Hạ Diệp c&ograve;n chẳng buồn l&ecirc;n kế hoạch cho chuyến đi n&agrave;y, đ&atilde; l&acirc;u như vậy chưa về nước, t&igrave;nh h&igrave;nh nơi đ&acirc;y cũng chỉ dựa v&agrave;o ấn tượng của c&ocirc; từ mấy năm trước m&agrave; th&ocirc;i, vậy n&ecirc;n đ&atilde; gặp đ&uacute;ng m&ugrave;a du lịch cao điểm, tất cả kh&aacute;ch sạn đều chật k&iacute;n, đến cả kh&aacute;ch sạn hạng hai cũng chẳng c&ograve;n lấy một ph&ograve;ng trống.<br />\r\n<br />\r\nHạ Diệp đ&atilde; đi t&igrave;m hết nửa con phố, cả đường Bắc Minh, từ đầu tới cuối kh&ocirc;ng c&ograve;n nơi n&agrave;o c&oacute; ph&ograve;ng trống.<br />\r\n<br />\r\nTr&ecirc;n phố l&uacute;c n&agrave;o cũng n&aacute;o nhiệt v&agrave; nhộn nhịp, đ&aacute;m đ&ocirc;ng ở khắp mọi nơi, v&igrave; nằm ở vị tr&iacute; đắc địa, ph&iacute;a Đ&ocirc;ng hướng ra bở biền d&agrave;i v&ocirc; tận, ph&iacute;a T&acirc;y đối diện với trung t&acirc;m giải tr&iacute; v&agrave; s&ograve;ng b&agrave;i lớn nhất Hồng K&ocirc;ng, do đ&oacute; gi&aacute; ph&ograve;ng trung b&igrave;nh cũng cao hơn so với những nơi kh&aacute;c rất nhiều.<br />\r\n<br />\r\nNh&acirc;n vi&ecirc;n lễ t&acirc;n đ&aacute;p lại rồi đứng chờ đợi c&acirc;u trả lời, nhưng chỉ thấy một đ&ocirc;i nam nữ đang sững sờ đứng nh&igrave;n nhau trước quầy.<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng b&ecirc;n cạnh l&ecirc;n tiếng trước: &ldquo;Kh&aacute;ch sạn ph&iacute;a Bắc đều đ&atilde; k&iacute;n chỗ, nếu c&ocirc; kh&ocirc;ng ở th&igrave; t&ocirc;i sẽ thu&ecirc; ph&ograve;ng n&agrave;y.&rdquo;<br />\r\n<br />\r\nHạ Diệp h&iacute;t s&acirc;u một hơi như hạ quyết t&acirc;m: &ldquo;Thu&ecirc;.&rdquo;<br />\r\n<br />\r\nC&ocirc; đ&atilde; đi t&igrave;m cả ph&iacute;a Nam nhưng cũng chẳng c&ograve;n ph&ograve;ng trống n&agrave;o cả.<br />\r\n<br />\r\nNhững năm gần đ&acirc;y, Thủ Cảng nổi tiếng với ng&agrave;nh c&ocirc;ng nghiệp giải tr&iacute; ph&aacute;t triển kh&aacute; mạnh, với nhiều loại h&igrave;nh phong ph&uacute;, dưới hệ thống tư ph&aacute;p* chỉ như vật trưng b&agrave;y th&igrave; ngược lại c&agrave;ng khiến ng&agrave;nh du lịch ph&aacute;t triển kh&ocirc;ng ngừng.<br />\r\n<br />\r\n*Hệ thống tư ph&aacute;p bao gồm bốn cơ quan ch&iacute;nh: c&ocirc;ng, viện kiểm s&aacute;t, luật v&agrave; bộ, c&ugrave;ng tạo th&agrave;nh hệ thống tư ph&aacute;p của đất nước t&ocirc;i. Trong thực tiễn tư ph&aacute;p, n&oacute; thường đề cập đến hệ thống t&ograve;a &aacute;n v&agrave; hệ thống viện kiểm s&aacute;t. Dưới sự r&agrave;ng buộc của Hiến ph&aacute;p, hai hệ thống n&agrave;y ho&agrave;n thiện c&aacute;c hoạt động tư ph&aacute;p của đất nước dựa tr&ecirc;n c&aacute;c nguy&ecirc;n tắc đ&uacute;ng đắn, hợp ph&aacute;p, kịp thời, độc lập, c&ocirc;ng bằng v&agrave; b&igrave;nh đẳng, để đời sống ph&aacute;p luật b&igrave;nh thường của đất nước được tiến h&agrave;nh b&igrave;nh thường.<br />\r\n<br />\r\nDo nằm ở vị tr&iacute; bi&ecirc;n giới, ng&agrave;y c&agrave;ng hỗn tạp, n&ecirc;n danh tiếng Cảng tự do trước đ&acirc;y đ&atilde; ho&agrave;n to&agrave;n bị x&oacute;a sổ v&agrave; thay v&agrave;o đ&oacute; n&oacute; đ&atilde; trở th&agrave;nh Cảng &ldquo;tự do&rdquo; theo đ&uacute;ng nghĩa đen. Ng&agrave;y nay, người ta gọi đ&acirc;y l&agrave; nơi d&agrave;nh cho người t&igrave;m kiếm sự phấn kh&iacute;ch, thi&ecirc;n đường d&agrave;nh cho những kẻ đi&ecirc;n, hoặc l&agrave; nơi để trải nghiệm cuộc sống, cũng chỉ v&igrave; c&aacute;i t&ecirc;n gọi &ldquo;tự do&rdquo; mỹ miều của n&oacute;.<br />\r\n<br />\r\n<br />\r\n<br />\r\nTừ trước tới giờ, Hạ Diệp chưa từng sống chung ph&ograve;ng với người lạ, lại c&ograve;n l&agrave; nam giới, thậm ch&iacute; c&ocirc; c&ograve;n chưa từng nghi&ecirc;m t&uacute;c hẹn h&ograve; lần n&agrave;o v&agrave; c&ocirc;ng lao đ&oacute; ho&agrave;n to&agrave;n l&agrave; nhờ mẹ Hạ.<br />\r\n<br />\r\nKể từ khi mẹ c&ocirc; &lsquo;b&oacute;p chết&rsquo; mối t&igrave;nh g&agrave; b&ocirc;ng ng&acirc;y thơ của c&ocirc; từ hồi cấp hai, th&igrave; c&aacute;i gọi l&agrave; t&igrave;nh y&ecirc;u trong s&aacute;ng, khờ khạo cũng từ đ&oacute; m&agrave; biến mất v&agrave; dường như chưa từng quay trở lại.<br />\r\n<br />\r\nNgay khi vừa v&agrave;o cấp hai, c&ocirc; đ&atilde; hẹn h&ograve; với một ch&agrave;ng trai c&ugrave;ng lớp, nhưng lại bị mẹ m&igrave;nh ph&aacute;t hiện v&agrave; ra lệnh phải lập tức chia tay, nếu kh&ocirc;ng b&agrave; sẽ giải quyết sự việc th&ocirc;ng qua gi&aacute;o vi&ecirc;n chủ nhiệm.<br />\r\n<br />\r\nL&uacute;c đ&oacute;, c&ocirc; c&ograve;n c&atilde;i lại lời mẹ: &ldquo;Ch&uacute;ng con chưa từng l&agrave;m điều g&igrave; sao tr&aacute;i! Tại sao lại kh&ocirc;ng được?&rdquo;<br />\r\n<br />\r\nNhưng mẹ c&ocirc; chẳng để lời n&oacute;i lọt v&agrave;o tai, b&agrave; n&oacute;i với c&ocirc; bằng giọng ra lệnh: &ldquo;Hiện tại con đang ti&ecirc;u tiền của mẹ, v&igrave; vậy bắt buộc phải nghe theo lời mẹ. Bạn trai con c&oacute; cho con tiền ti&ecirc;u kh&ocirc;ng? Cậu ấy c&oacute; thể nộp học ph&iacute; cho con kh&ocirc;ng? C&oacute; đủ khả năng nu&ocirc;i con hay kh&ocirc;ng? Con c&oacute; tư c&aacute;ch g&igrave; để y&ecirc;u đương? Nếu c&oacute; bản lĩnh đ&oacute; th&igrave; bảo cậu ấy nu&ocirc;i con đi! Nếu hiện tại cậu ấy c&oacute; thể nu&ocirc;i con, th&igrave; mẹ sẽ kh&ocirc;ng tham gia v&agrave;o việc n&agrave;y&hellip;&rdquo;<br />\r\n<br />\r\nKhi đ&oacute;, Hạ Diệp cảm thấy bản th&acirc;n m&igrave;nh v&ocirc; c&ugrave;ng nhục nh&atilde;.<br />\r\n<br />\r\nD&ugrave; kh&ocirc;ng phục nhưng c&ocirc; vẫn phải bằng l&ograve;ng chấp nhận thỏa hiệp, v&igrave; vậy c&ocirc; mới lu&ocirc;n khao kh&aacute;t độc lập, khao kh&aacute;t sự tự do, khao kh&aacute;t được tho&aacute;t khỏi g&ocirc;ng c&ugrave;m của mẹ, nếu kh&ocirc;ng sẽ chẳng đến mức quyết t&acirc;m một m&igrave;nh ra nước ngo&agrave;i du học khi vừa mới học xong cấp ba.<br />\r\n<br />\r\nHạt giống tự do đ&atilde; ghim s&acirc;u trong tr&aacute;i tim c&ocirc; rồi dần dần b&eacute;n rễ, nảy mầm&hellip;<br />\r\n<br />\r\nV&agrave; niềm khao kh&aacute;t t&igrave;nh y&ecirc;u thuở mười ba của Hạ Diệp đ&atilde; tan vỡ v&agrave; biến th&agrave;nh khao kh&aacute;t tiền bạc.<br />\r\n<br />\r\nC&acirc;u n&oacute;i &ldquo;Mẹ nu&ocirc;i con, th&igrave; con buộc phải nghe theo lời mẹ&rdquo; của mẹ c&ocirc; đ&atilde; b&igrave;nh an theo c&ocirc; đi hết thời thanh xu&acirc;n. Nhưng trong th&acirc;m t&acirc;m c&ocirc;, kh&aacute;t vọng tự do vẫn kh&ocirc;ng ngừng tăng l&ecirc;n, c&ocirc; kh&ocirc;ng muốn h&agrave;nh vi của m&igrave;nh bị hạn chế bởi bất kỳ ai, v&igrave; vậy mới lu&ocirc;n tin tưởng trong tiềm thức rằng m&igrave;nh sẽ ho&agrave;n to&agrave;n được tự do miễn l&agrave; c&oacute; đủ tiền.<br />\r\n<br />\r\nHạ Diệp c&uacute;i đầu, nhanh ch&oacute;ng điền v&agrave;o tờ phiếu rồi giao cho nh&acirc;n vi&ecirc;n ở quầy lễ t&acirc;n, nhưng vừa mới đưa ra đ&atilde; thu lại, v&igrave; c&ocirc; g&aacute;i đ&oacute; đang chăm ch&uacute; nh&igrave;n người đ&agrave;n &ocirc;ng b&ecirc;n cạnh, Hạ Diệp cũng đưa &aacute;nh mắt nh&igrave;n theo, người đ&agrave;n &ocirc;ng đ&oacute; c&oacute; nước da n&acirc;u khỏe khoắn, t&oacute;c tai được cắt gọn g&agrave;ng, c&ugrave;ng cặp l&ocirc;ng m&agrave;y tinh anh, l&uacute;c n&agrave;y đang c&uacute;i đầu, rũ mắt điền v&agrave;o tờ phiếu.<br />\r\n<br />\r\nNgười đ&agrave;n &ocirc;ng cảm nhận được &aacute;nh mắt d&ograve; x&eacute;t đến từ ph&iacute;a b&ecirc;n cạnh liền quay đầu nh&igrave;n sang, để tr&aacute;nh bối rối, Hạ Diệp vội v&agrave;ng quay mặt đi, sau đ&oacute; đưa tờ phiếu c&ugrave;ng thẻ t&iacute;n dụng cho nh&acirc;n vi&ecirc;n lễ t&acirc;n. C&ocirc; nh&acirc;n vi&ecirc;n lễ t&acirc;n cũng đ&atilde; ho&agrave;n hồn trở lại, vội v&agrave;ng cắm thẻ v&agrave;o pos, tiếp đ&oacute; lại nhận lấy thẻ của Kiều Triết đang đặt tr&ecirc;n mặt quầy, sau khi đ&atilde; thanh to&aacute;n xong xu&ocirc;i, liền n&oacute;i với cả hai người họ: &ldquo;Ph&ograve;ng của hai vị ở tầng hai, b&ecirc;n tr&aacute;i cửa thang m&aacute;y.&rdquo;<br />\r\n<br />\r\nHạ Diệp kh&ocirc;ng khỏi thở d&agrave;i, quả nhi&ecirc;n l&agrave; đ&agrave;n &ocirc;ng đẹp thật phiền phức, qu&aacute; dễ d&agrave;ng thu h&uacute;t ong bướm.<br />\r\n<br />\r\nSau đ&oacute;, c&ocirc; lại kh&ocirc;ng khỏi li&ecirc;n tưởng tới cảng Tự do cũng l&agrave; một nơi nổi tiếng của những &ldquo;cuộc gặp gỡ&rdquo;, rất nhiều người đến đ&acirc;y cũng chỉ v&igrave; một lần muốn được trải nghiệm sự k&iacute;ƈɦ ŧɦ&iacute;ƈɦ của cuộc &ldquo;gặp gỡ&rdquo; với c&aacute;i đẹp ấy.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y Hạ Diệp kh&ocirc;ng khỏi trợn tr&ograve;n mắt, đ&acirc;y kh&ocirc;ng phải mục đ&iacute;ch của chuyến du lịch, vậy n&ecirc;n c&ocirc; nhanh ch&oacute;ng dẹp bỏ mớ hỗn độn đ&oacute; ra khỏi đầu rồi cầm lấy thẻ c&ugrave;ng vali quay người đi l&ecirc;n lầu, chẳng hy vọng g&igrave; ở một kh&aacute;ch sạn hạng hai sẽ c&oacute; người chuy&ecirc;n m&ocirc;n gi&uacute;p kh&aacute;ch h&agrave;ng di chuyển h&agrave;nh l&yacute;.<br />\r\n<br />\r\nKiểu Triết đi theo ph&iacute;a sau, nh&igrave;n c&ocirc; g&aacute;i chẳng tốn ch&uacute;t sức lực n&agrave;o khi nhấc chiếc vali 28 inch, n&ecirc;n lập tức từ bỏ &yacute; định gi&uacute;p đỡ của m&igrave;nh v&agrave; lu&ocirc;n giữ khoảng c&aacute;ch th&iacute;ch hợp với c&ocirc;.<br />\r\n<br />\r\nCăn ph&ograve;ng n&agrave;y rất nhỏ, chỉ c&oacute; hai cửa sổ, một cửa hướng ra bờ biển, qua khung cửa k&iacute;nh c&oacute; thể nh&igrave;n thấy những gợn s&oacute;ng do gi&oacute; biển thổi qua, cửa sổ c&ograve;n lại hướng về ph&iacute;a trung t&acirc;m giải tr&iacute;, b&ecirc;n ngo&agrave;i l&agrave; v&ocirc; số c&aacute;c loại đ&egrave;n neon phản chiếu bầu trời đ&ecirc;m đầy sao.<br />\r\n<br />\r\nCuối c&ugrave;ng l&agrave; một ph&ograve;ng tắm độc lập bằng k&iacute;nh mờ ph&iacute;a b&ecirc;n phải cửa ra v&agrave;o, Hạ Diệp &acirc;m thầm g&agrave;o th&eacute;t trong l&ograve;ng, kh&aacute;ch sạn n&agrave;y sao kh&ocirc;ng tiết kiệm th&ecirc;m ch&uacute;t tiền nữa đi!<br />\r\n<br />\r\nTrong ph&ograve;ng c&oacute; hai giường đơn phong c&aacute;ch phương T&acirc;y được đặt ch&iacute;nh giữa căn ph&ograve;ng, giữa hai giường c&oacute; một chiếc tủ &acirc;m tường, đối diện c&oacute; treo một chiếc tivi, ph&iacute;a tr&ecirc;n l&agrave; điều h&ograve;a, tủ quần &aacute;o được đặt b&ecirc;n cạnh, kh&ocirc;ng gian nhỏ đến mức đ&aacute;ng kinh ngạc.<br />\r\n<br />\r\nHạ Diệp kh&ocirc;ng khỏi chửi thầm, cắt x&eacute;n kh&ocirc;ng gian tới mức n&agrave;y để x&acirc;y th&ecirc;m v&agrave;i ph&ograve;ng nữa cho thu&ecirc;, rốt cuộc c&oacute; thể kiếm được th&ecirc;m bao nhi&ecirc;u tiền cơ chứ?<br />\r\n<br />\r\nSau khi Kiều Triết đặt h&agrave;nh l&yacute; xuống, anh bắt đầu kiểm tra c&aacute;c vật dụng b&ecirc;n trong v&agrave; thiết bị ph&ograve;ng tắm.<br />\r\n<br />\r\nHạ Diệp nh&igrave;n Kiều Triết đi ra từ ph&ograve;ng tắm: &ldquo;T&ocirc;i t&ecirc;n l&agrave; Hạ Diệp, hạ l&agrave; m&ugrave;a hạ, c&ograve;n diệp l&agrave; ngọn lửa v&agrave; t&igrave;nh y&ecirc;u cuộc sống.&rdquo;<br />\r\n<br />\r\nAnh nh&igrave;n c&ocirc;, cũng giới thiệu ngắn gọn về bản th&acirc;n: &ldquo;Kiều Triết, triết trong triết học.&rdquo;<br />\r\n<br />\r\nSau đ&oacute;, lại nghe thấy c&ocirc; n&oacute;i: &ldquo;T&ocirc;i tắm phải mất nửa tiếng đồng hồ.&rdquo; Tất nhi&ecirc;n &yacute; nghĩa của c&acirc;u n&oacute;i n&agrave;y cũng tương đương với c&acirc;u &ldquo;Cũng đến giờ ăn cơm rồi&rdquo;.<br />\r\n<br />\r\n<br />\r\n<br />\r\nHạ Diệp cho rằng sau ngần ấy năm ở một m&igrave;nh, th&igrave; tự học c&aacute;ch l&agrave;m quen ch&iacute;nh l&agrave; c&aacute;ch l&agrave;m việc ph&aacute;t huy t&aacute;c dụng tốt nhất.<br />\r\n<br />\r\nKiều Triết nh&igrave;n đ&ocirc;i mắt s&aacute;ng ngời như biết n&oacute;i của c&ocirc;, nhưng tr&ecirc;n mặt lại lộ ra vẻ mệt mỏi, r&otilde; r&agrave;ng l&agrave; đ&atilde; đi cả một qu&atilde;ng đường d&agrave;i, anh đ&aacute;p: &ldquo;Nửa tiếng sau t&ocirc;i sẽ quay lại.&rdquo; N&oacute;i xong liền quay người đi ra ngo&agrave;i, kh&ocirc;ng ch&uacute;t suy nghĩ cũng chẳng dừng lại, n&oacute;i chung l&agrave; kh&ocirc;ng mấy thoải m&aacute;i.<br />\r\n<br />\r\nĐột nhi&ecirc;n trong ph&ograve;ng chỉ c&ograve;n lại một m&igrave;nh Hạ Diệp, c&ocirc; nhanh ch&oacute;ng chọn một bộ đồ ngủ c&oacute; in h&igrave;nh tr&aacute;i dưa hấu sặc sỡ nhất trong số những bộ đồ ngủ của c&ocirc;, tuy rằng l&agrave; quần đ&ugrave;i nhưng n&oacute; ph&ugrave; hợp hơn nhiều so với những chiếc v&aacute;y hai d&acirc;y bằng vải lụa m&agrave;u hồng kia.<br />\r\n<br />\r\nTrong ph&ograve;ng tắm chứa đầy hơi nước, những giọt nước len lỏi qua m&aacute;i t&oacute;c mềm mại của c&ocirc;, rồi dần dần lan tới chiếc cổ nhỏ nhắn, từ xương quai xanh qua bầu ngực đến v&ugrave;ng bụng dưới mịn m&agrave;ng cuối c&ugrave;ng t&aacute;ch ra theo hai ch&acirc;n&hellip;<br />\r\n<br />\r\nHạ Diệp nhắm mắt lại, cảnh tượng khi n&atilde;y lại hiện l&ecirc;n trong đầu c&ocirc;, vẻ mặt của người đ&agrave;n &ocirc;ng kia qu&aacute; mức b&igrave;nh tĩnh, t&ugrave;y rằng &aacute;nh mắt c&oacute; ch&uacute;t xa c&aacute;ch nhưng lại kh&ocirc;ng khiến người kh&aacute;c kh&oacute; chịu, điều n&agrave;y khiến người c&oacute; d&aacute;ng v&oacute;c được người người m&ecirc; mẩn c&ugrave;ng khu&ocirc;n mặt v&ocirc; c&ugrave;ng xinh xắn l&agrave; c&ocirc; cũng c&oacute; ch&uacute;t sửng sốt.<br />\r\n<br />\r\nNghĩ đến đ&acirc;y, Hạ Diệp lập tức mở mắt, bước đến gương nh&igrave;n ch&iacute;nh m&igrave;nh đang trần trụi giơ tay b&oacute;p hai bầu ngực rồi nghi&ecirc;ng h&ocirc;ng nh&igrave;n xuống bờ m&ocirc;ng đẫy đ&agrave;, lẩm bẩm một m&igrave;nh: &ldquo;Chẳng lẽ do m&igrave;nh vườn kh&ocirc;ng nh&agrave; trống qu&aacute; l&acirc;u n&ecirc;n kh&ocirc;ng tiết ra nội tiết tố nữ hay sao&hellip;&rdquo;<br />\r\n<br />\r\nKiểu Triết xuống lầu ăn tối, nh&acirc;n tiện l&agrave;m quen lu&ocirc;n với con phố gần đ&oacute;.<br />\r\n<br />\r\nNửa giờ sau&hellip;<br />\r\n<br />\r\nB&ecirc;n ngo&agrave;i vang l&ecirc;n tiếng g&otilde; cửa, Hạ Diệp đặt m&aacute;y sấy t&oacute;c xuống chạy tới mở cửa, c&aacute;nh cửa vừa h&eacute; ra Kiều Triết đ&atilde; bị hương thơm tươi m&aacute;t tr&ecirc;n t&oacute;c c&ocirc; bao lấy.<br />\r\n<br />\r\nAnh cụp mắt nh&igrave;n xuống đường n&eacute;t thanh t&uacute; tr&ecirc;n khu&ocirc;n mặt Hạ Diệp, v&agrave;i sợi t&oacute;c x&otilde;a hai b&ecirc;n m&aacute; c&ograve;n vương vấn ch&uacute;t hơi nước, anh thấy c&ocirc; mỉm cười: &ldquo;T&ocirc;i tắm xong rồi!&rdquo;<br />\r\n<br />\r\nXem ra ở c&ocirc; thực sự dư thừa sức sống, Kiều Triết lấy lại b&igrave;nh tĩnh rồi khẽ &ldquo;ừm&rdquo; một tiếng.<br />\r\n<br />\r\nHạ Diệp sấy t&oacute;c xong, tới nằm ngơ ng&aacute;c tr&ecirc;n giường, th&igrave; tới lượt Kiều Triệt đi v&agrave;o ph&ograve;ng tắm.<br />\r\n<br />\r\nTrong ph&ograve;ng tắm, những giọt nước bắn tung t&oacute;e, Hạ Diệp l&eacute;n l&uacute;t nh&igrave;n đường n&eacute;t cơ thể của người đ&agrave;n &ocirc;ng in tr&ecirc;n tấm k&iacute;nh mờ, trong đầu kh&ocirc;ng khỏi hiện l&ecirc;n những suy nghĩ mờ &aacute;m, khiến hai m&aacute; ửng hồng, dần dần rơi v&agrave;o trạng th&aacute;i thất thần.<br />\r\n<br />\r\n&ldquo;Lạch cạch!&rdquo; Đột nhi&ecirc;n c&oacute; tiếng k&eacute;o cửa k&iacute;nh truyền tới. Hạ Diệp lập tức chui v&agrave;o trong chăn, che k&iacute;n đầu lại.<br />\r\n<br />\r\nNgay khi Kiều Triết đi ra, anh thấy dưới lớp chăn b&ocirc;ng trắng tinh, phồng l&ecirc;n một khối tr&ograve;n tr&ograve;n, cao cao, liền lập tức cau m&agrave;y ngạc nhi&ecirc;n.<br />\r\n<br />\r\nMột l&uacute;c sau, Hạ Diệp bị ngạt thở, vội v&agrave;ng chui ra khỏi chăn thở hổn hển.<br />\r\n<br />\r\nKiều Triết mặc tr&ecirc;n người chiếc &aacute;o ba lỗ m&agrave;u đen, l&uacute;c n&agrave;y đang dọn dẹp lại giường, Hạ Diệp nh&igrave;n chằm chằm v&agrave;o những đường n&eacute;t cơ bắp tr&ecirc;n c&aacute;nh tay anh, &aacute;nh mắt từ bờ vai rộng, vững ch&atilde;i dần đi xuống v&ograve;ng eo hẹp săn chắc&hellip;<br />\r\n<br />\r\nKiều Triết đột ngột quay người lại, bắt gặp &aacute;nh mắt như thi&ecirc;u như đốt, động t&aacute;c nuốt nước bọt của Hạ Diệp cũng lọt v&agrave;o mắt anh.<br />\r\n<br />\r\nNh&igrave;n trộm người kh&aacute;c, c&ograve;n bị người ta bắt gặp khiến nhiệt độ tr&ecirc;n mặt Hạ Diệp lập tức găng vọt. C&ocirc; cố giữ b&igrave;nh tĩnh, giọng n&oacute;i tự nhi&ecirc;n hết sức c&oacute; thể: &ldquo;C&aacute;i đ&oacute;, anh c&oacute; thể lấy gi&uacute;p t&ocirc;i chai nước tr&ecirc;n b&agrave;n tivi được kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\nKiều Triết đưa chai nước cho Hạ Diệp: &ldquo;C&ocirc; đến đ&acirc;y du lịch &agrave;?&rdquo;<br />\r\n<br />\r\n&ldquo;Ừm. Anh th&igrave; sao?&rdquo;<br />\r\n<br />\r\n&ldquo;L&agrave;m việc.&rdquo;<br />\r\n<br />\r\nHạ Diệp uống nước xong, đặt chai nước l&ecirc;n b&agrave;n cạnh đầu giường, hỏi: &ldquo;Vậy anh ở lại đ&acirc;y bao l&acirc;u? T&ocirc;i ở đ&acirc;y khoảng nửa th&aacute;ng.&rdquo;<br />\r\n<br />\r\nKiều Triết trả lời: &ldquo;T&ocirc;i sẽ rời đi sau khi ho&agrave;n th&agrave;nh c&ocirc;ng việc, thời gian kh&ocirc;ng x&aacute;c định.&rdquo; N&oacute;i xong anh lại im lặng v&agrave; kh&ocirc;ng c&oacute; &yacute; định tiếp tục tr&ograve; chuyện.<br />\r\n<br />\r\nLần đầu gặp mặt, Hạ Diệp cũng kh&ocirc;ng muốn n&oacute;i qu&aacute; nhiều: &ldquo;Th&igrave; ra l&agrave; vậy.&rdquo; Sau đ&oacute; nhắm mặt lại.</h2>', 0, 'chuong-1-lam-quen');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(25, 15, 'Chương 2: Quét ổ mại dâm.', 'Chương 2: Quét ổ mại dâm.', '<h2>Ng&agrave;y h&ocirc;m sau, khi Hạ Diệp thức dậy, chiếc giường b&ecirc;n ph&iacute;a đối diện đ&atilde; trống trơn, chăn, ga được sắp xếp gọn g&agrave;ng, đ&acirc;y thực sự l&agrave; một th&oacute;i quen khiến người kh&aacute;c kh&oacute; chịu, Hạ Diệp nghĩ.<br />\r\n<br />\r\nC&ocirc; kh&ocirc;ng muốn dọn dẹp giường của m&igrave;nh, nhưng nếu kh&ocirc;ng sắp xếp chăn gối lại cẩn thận th&igrave; sẽ tạo ra sự tương phản qu&aacute; lớn.<br />\r\n<br />\r\nHạ Diệp mặc một chiếc quần jeans v&agrave; &aacute;o sơ mi trắng, buộc t&oacute;c đu&ocirc;i ngựa, tr&ocirc;ng rất năng động v&agrave; tr&agrave;n đầy sức sống, c&ocirc; định đi dạo quanh khu một chuyến.<br />\r\n<br />\r\nSau khi ăn v&agrave;i m&oacute;n ăn nhẹ đặc sản địa phương v&agrave; l&agrave;m quen với c&aacute;c điểm tham quan kiến tr&uacute;c nổi tiếng gần đ&oacute;, c&ocirc; dự định sẽ l&ecirc;n kế hoạch cẩn thận về h&agrave;nh tr&igrave;nh sắp tới, d&ugrave; sao th&igrave; c&ocirc; cũng đ&atilde; được trải nghiệm s&acirc;u sắc sự đau khổ về việc kh&ocirc;ng l&ecirc;n trước kế hoạch cho chuyến đi.<br />\r\n<br />\r\nHạ Diệp vốn định tối qua chỉ ở tạm đ&oacute; một đ&ecirc;m, h&ocirc;m nay sẽ đi t&igrave;m kh&aacute;ch sạn kh&aacute;c hỏi xem c&oacute; c&ograve;n ph&ograve;ng trống hay kh&ocirc;ng, v&igrave; n&oacute;i thế n&agrave;o đi chăng nữa cũng kh&ocirc;ng n&ecirc;n ở chung với một người đ&agrave;n &ocirc;ng xa lạ.<br />\r\n<br />\r\nSau khi ra ngo&agrave;i, Hạ Diệp đ&atilde; nắm bắt được một số tin tức lớn nhỏ mới về Thủ cảng, v&iacute; dụ như: C&oacute; đo&agrave;n người mẫu chuyển giới lớn nhất Th&aacute;i Lan tới đ&acirc;y biểu diễn, một tay đại ph&uacute; h&agrave;o n&agrave;o đ&oacute; đang đ&aacute;nh b&agrave;i trong s&ograve;ng bạc hay l&agrave; ph&ograve;ng đấu gi&aacute; dưới l&ograve;ng đất chuẩn bị mở cửa, v&igrave; vậy kh&aacute;ch du lịch c&agrave;ng ng&agrave;y c&agrave;ng đ&ocirc;ng, n&ecirc;n tất cả c&aacute;c kh&aacute;ch sạn đều đ&atilde; chật k&iacute;n ph&ograve;ng, c&ocirc; kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;ch đ&agrave;nh tiếp tục ở lại kh&aacute;ch sạn m&agrave; m&igrave;nh đang thu&ecirc;.<br />\r\n<br />\r\nMới s&aacute;ng sớm Kiều Triết đ&atilde; ra ngo&agrave;i v&agrave; kết nối với người nằm v&ugrave;ng, lần n&agrave;y nhiệm vụ của anh l&agrave; truy bắt &ldquo;C&aacute; mập trắng&rdquo;.<br />\r\n<br />\r\nL&yacute; do tại sao nhiệm vụ lần n&agrave;y lại được giao cho Kiều Triết l&agrave; bởi v&igrave; trước đ&acirc;y anh đ&atilde; từng l&agrave; &ldquo;cộng sự&rdquo; của &ldquo;C&aacute; mập trắng&rdquo; v&agrave; cũng chỉ c&oacute; anh mới quen với phong c&aacute;ch h&agrave;nh sự của đối phương.<br />\r\n<br />\r\nTin tức về C&aacute; mập trắng đ&atilde; được một cuộc gọi nặc danh tiết lộ v&agrave;i ng&agrave;y trước, người giấu t&ecirc;n đ&oacute; đ&atilde; khai b&aacute;o c&aacute;c chi tiết về hoạt động sắp tới của C&aacute; mập trắng, về cơ bản kh&aacute; tr&ugrave;ng khớp với manh mối m&agrave; người nằm v&ugrave;ng cung cấp cho đội trinh s&aacute;t, v&igrave; vậy mới c&oacute; kế hoạch hiện tại, chỉ c&oacute; điều thời gian c&oacute; ch&uacute;t gấp r&uacute;t.<br />\r\n<br />\r\n&ldquo;C&aacute; mập trắng&rdquo; đ&atilde; từng hợp t&aacute;c với tr&ugrave;m ma t&uacute;y lớn nhất nước M, con người v&ocirc; c&ugrave;ng xảo quyệt, chưa ai từng nh&igrave;n thấy mặt anh ta, tuy nhi&ecirc;n trong giới đ&atilde; c&oacute; những lan truyền rằng C&aacute; mập trắng l&agrave; một mỹ nh&acirc;n v&ocirc; c&ugrave;ng xinh đẹp, n&oacute;ng bỏng.<br />\r\n<br />\r\nTheo nhiều nguồn tin kh&aacute;c nhau, đ&acirc;y l&agrave; lần giao dịch cuối c&ugrave;ng, C&aacute; mập trắng định rửa tay trong chậu v&agrave;ng, vậy n&ecirc;n đ&acirc;y cũng l&agrave; giao dịch lớn nhất Đ&ocirc;ng Nam &Aacute; trong thời gian qua. B&ecirc;n ph&iacute;a nước M cũng đ&atilde; cử nh&acirc;n sự cao cấp sang chuẩn bị cho c&aacute;c cuộc gặp mặt trực tiếp, thời gian giao dịch được ấn định trong mười ng&agrave;y tới.<br />\r\n<br />\r\nBuổi tối, sau khi tắm rửa sạch sẽ, Hạ Diệp leo l&ecirc;n giường ngủ, c&ocirc; dự định h&ocirc;m sau cũng sẽ dậy thật sớm.<br />\r\n<br />\r\nKhi Kiều Triết trở về th&igrave; người tr&ecirc;n giường đ&atilde; ngủ say. M&aacute;i t&oacute;c đen x&otilde;a tr&ecirc;n nền gối trắng, chỉ c&oacute; duy nhất chiếc đầu nhỏ xinh lộ ra khỏi chăn, l&uacute;c n&agrave;y h&ocirc; hấp đang nhẹ nh&agrave;ng đều đều.<br />\r\n<br />\r\nHai người dường như l&agrave; người quen cũ lại như người xa lạ, họ kh&ocirc;ng mấy nhiệt t&igrave;nh cũng chẳng qu&aacute; xa l&aacute;nh, cứ tự nhi&ecirc;n như vậy, nhưng lại mang đến cho đối phương một cảm gi&aacute;c an t&acirc;m kh&oacute; giải th&iacute;ch.<br />\r\n<br />\r\nKhi Kiều Triết bước ra khỏi ph&ograve;ng tắm, anh chỉ mặc tr&ecirc;n người một chiếc quần đ&ugrave;i ngủ, một tay vẫn đang cầm khăn tắm lau t&oacute;c.<br />\r\n<br />\r\nKhi kh&ocirc;ng c&oacute; cảm gi&aacute;c an to&agrave;n Hạ Diệp lu&ocirc;n ngủ kh&ocirc;ng s&acirc;u giấc, c&ocirc; đ&atilde; sớm bị đ&aacute;nh thức bởi tiếng nước chảy, l&uacute;c n&agrave;y đang sững sờ nh&igrave;n l&ecirc;n trần nh&agrave;, vừa thấy Kiều Triết đi ra liền lập tức quay mặt di chuyển ảnh mắt về ph&iacute;a anh.<br />\r\n<br />\r\nKiều Triết thấy c&ocirc; đang nh&igrave;n m&igrave;nh: &ldquo;Xin lỗi đ&atilde; l&agrave;m c&ocirc; tỉnh giấc.&rdquo;<br />\r\n<br />\r\nHạ Diệp vẫn đang ngưỡng mộ cơ bắp rắn chắc tr&ecirc;n người anh n&ecirc;n ho&agrave;n to&agrave;n kh&ocirc;ng để &yacute; tới lời xin lỗi n&agrave;y, đột ngột l&ecirc;n tiếng: &ldquo;Anh đang quyến rũ t&ocirc;i đấy &agrave;?&rdquo;<br />\r\n<br />\r\nVừa dứt lời c&ocirc; mới &yacute; thức được m&igrave;nh đ&atilde; n&oacute;i g&igrave;, nhưng nghĩ tới đ&acirc;y l&agrave; nơi n&agrave;o lại cảm thấy chẳng c&oacute; g&igrave; đ&aacute;ng để bận t&acirc;m, kh&ocirc;ng cần thiết phải qu&aacute; kiềm chế bản th&acirc;n.<br />\r\n<br />\r\nĐột nhi&ecirc;n Kiều Triết c&oacute; suy nghĩ muốn tr&ecirc;u chọc c&ocirc; g&aacute;i n&agrave;y, anh cố &yacute; bước tới, cẩn thận nh&igrave;n chằm chằm v&agrave;o đ&ocirc;i mắt c&ocirc;, như thể muốn đọc được điều g&igrave; đ&oacute; th&ocirc;ng qua n&oacute;.<br />\r\n<br />\r\nHạ Diệp thấy Kiều Triệt đang dần dần tiến về ph&iacute;a m&igrave;nh liền lập tức chui v&agrave;o trong chăn, tự hỏi người n&agrave;y sẽ kh&ocirc;ng tưởng thật đ&oacute; chứ?<br />\r\n<br />\r\nKiều Triệt đột nhi&ecirc;n muốn bật cười, đ&acirc;y c&oacute; phải l&agrave; đang sợ h&atilde;i rồi kh&ocirc;ng? Sau đ&oacute;, anh n&oacute;i với người đang n&eacute;p m&igrave;nh dưới chăn: &ldquo;B&acirc;y giờ biết sợ rồi hả?&rdquo;<br />\r\n<br />\r\nDưới lớp chăn truyền tới &acirc;m thanh như bị b&oacute;p nghẹt: &ldquo;Sợ c&aacute;i đầu anh, t&ocirc;i chỉ sợ nhiệt huyết của m&igrave;nh d&acirc;ng l&ecirc;n, l&uacute;c đ&oacute; chảy m&aacute;u mũi sẽ l&agrave;m bẩn chăn th&ocirc;i.&rdquo;<br />\r\n<br />\r\nC&aacute;ch lớp chăn, anh &aacute;p xuống tai c&ocirc; th&igrave; thầm: &ldquo;Kh&ocirc;ng n&oacute;ng sao?&rdquo;<br />\r\n<br />\r\n<br />\r\n<br />\r\nNgười trong chăn chậm r&atilde;i lộ ra đ&ocirc;i mắt s&aacute;ng ngời, nh&igrave;n Kiều Triết đang mỉm cười với m&igrave;nh, nụ cười tuy rực rỡ nhưng kh&ocirc;ng hề đem theo du͙ƈ vọиɠ, v&igrave; vậy Hạ Diệp trừng mắt n&oacute;i: &ldquo;X&iacute;!&rdquo;<br />\r\n<br />\r\nĐột nhi&ecirc;n b&ecirc;n ngo&agrave;i vang l&ecirc;n tiếng cạy cửa, c&oacute; ai đ&oacute; x&ocirc;ng v&agrave;o ph&ograve;ng, s&aacute;u người mặc đồng phục cảnh s&aacute;t tiến v&agrave;o trong, g&atilde; b&eacute;o dẫn đầu giơ thẻ ng&agrave;nh l&ecirc;n n&oacute;i: &ldquo;Xuất tr&igrave;nh chứng minh thư ra đ&acirc;y rồi mở vali ra kiểm tra.&rdquo;<br />\r\n<br />\r\nHạ Diệp ngồi dậy, đ&aacute;nh gi&aacute; s&aacute;u người đ&agrave;n &ocirc;ng &aacute;m đầy m&ugrave;i kh&oacute;i thuốc trước mặt, đủ loại d&aacute;ng dấp đều c&oacute; cả, cao, thấp, mập, ốm, quần &aacute;o ch&uacute;ng mặc tr&ecirc;n người như đồ b&agrave;y b&aacute;n ở vỉa h&egrave;, trong l&ograve;ng c&oacute; ch&uacute;t nghi ngờ: Chẳng lẽ l&agrave; qu&eacute;t ổ mại d&acirc;m? Nhưng qu&eacute;t ổ mại d&acirc;m th&igrave; đ&acirc;u c&oacute; cần thiết phải kiểm tra h&agrave;nh l&yacute;?<br />\r\n<br />\r\nKiểu Triết khẽ cau m&agrave;y, nghi&ecirc;m nghị n&oacute;i: &ldquo;Cho t&ocirc;i xem lệnh kh&aacute;m x&eacute;t của anh.&rdquo;<br />\r\n<br />\r\nT&ecirc;n mập đứng đầu bắt đầu chửi rủa: &ldquo;Con mẹ n&oacute;, m&agrave;y việc g&igrave; phải lắm lời, kh&ocirc;ng thấy thẻ cảnh s&aacute;t trong tay &ocirc;ng đ&acirc;y &agrave;?&rdquo; N&oacute;i xong, liền n&acirc;ng thẻ cảnh s&aacute;t trong tay m&igrave;nh l&ecirc;n.<br />\r\n<br />\r\nHạ Diệp biết nơi n&agrave;y rất hỗn loạn, nhưng kh&ocirc;ng ngờ lại c&oacute; người giả mạo cảnh s&aacute;t một c&aacute;ch trắng trợn như vậy, hơn nữa thoạt nh&igrave;n đ&atilde; biết bọn ch&uacute;ng đ&atilde; qu&aacute; quen với việc phạm tội, ỷ thế đ&ocirc;ng người, kh&ocirc;ng biết ki&ecirc;ng nể l&agrave; g&igrave;.<br />\r\n<br />\r\nThế giới ngầm nơi đ&acirc;y chắc hẳn v&ocirc; c&ugrave;ng hung hăng ngang ngược, hoặc l&agrave; cảnh s&aacute;t kh&ocirc;ng thể kiểm so&aacute;t, hoặc cũng c&oacute; thể ch&iacute;nh ph&iacute;a cảnh s&aacute;t đ&atilde; cố t&igrave;nh nhắm mắt cho qua, c&ugrave;ng nhau cấu kết phạm tội, gi&uacute;p ch&uacute;ng l&agrave;m vương l&agrave;m tướng, hiếp đ&aacute;p d&acirc;n địa phương v&agrave; kh&aacute;ch du lịch.<br />\r\n<br />\r\nNghĩ lại th&igrave; c&ocirc; cũng đ&atilde; từng nghe thấy c&oacute; người n&oacute;i rằng họ đ&atilde; gặp phải v&agrave;i t&ecirc;n c&ocirc;n đồ đ&oacute;ng giả cảnh s&aacute;t để kiểm tra ph&ograve;ng của kh&aacute;ch du lịch sau đ&oacute; trộm đồ, nhưng thật kh&ocirc;ng ngờ bản th&acirc;n m&igrave;nh lại gặp phải cảnh n&agrave;y.<br />\r\n<br />\r\n&ldquo;Nếu kh&ocirc;ng c&oacute; lệnh kh&aacute;m x&eacute;t, th&igrave; anh kh&ocirc;ng đủ điều kiện để kiểm tra ch&uacute;ng t&ocirc;i.&rdquo; Hạ Diệp n&oacute;i.<br />\r\n<br />\r\nCăn ph&ograve;ng vốn đ&atilde; chật chội, l&uacute;c n&agrave;y s&aacute;u t&ecirc;n c&ugrave;ng ập v&agrave;o một l&uacute;c, quả thực l&agrave; kh&ocirc;ng thể nh&eacute;t th&ecirc;m người thứ bảy v&agrave;o nữa. Người đ&agrave;n &ocirc;ng gầy hơn đứng gần Hạ Diệp nhất bắt đầu trừng mắt uy hiếp: &ldquo;Kh&ocirc;ng nghe thấy anh Dũng của tụi tao n&oacute;i g&igrave; &agrave;? Nhanh l&ecirc;n, con mẹ n&oacute;, mở va li ra.&rdquo;<br />\r\n<br />\r\nĐối phương n&oacute;i xong muốn k&eacute;o Hạ Diệp ra khỏi giường, nhưng anh ta vừa t&uacute;m được c&aacute;nh tay c&ocirc;, đột nhi&ecirc;n lại h&eacute;t l&ecirc;n một tiếng ch&oacute;i tai: &ldquo;&Aacute;!&rdquo;<br />\r\n<br />\r\nHạ Diệp bẻ ngược ng&oacute;n tay vừa chạm v&agrave;o người m&igrave;nh của anh ta ra sau, người đ&agrave;n &ocirc;ng ngữ kh&iacute; lạnh l&ugrave;ng: &ldquo;Đợt l&aacute;t nữa xem tao l&agrave;m chết m&agrave;y thế n&agrave;o, con đ&agrave;n b&agrave; đ&ecirc; tiện!&rdquo;<br />\r\n<br />\r\nNhững người kh&aacute;c cũng phản ứng lại, ch&uacute;ng bắt đầu x&ocirc;ng tới muốn k&eacute;o Hạ Diệp đi.<br />\r\n<br />\r\nHạ Diệp nắm chặt b&agrave;n tay c&ograve;n lại, đập mạnh khớp ng&oacute;n trỏ v&agrave; ng&oacute;n giữa v&agrave;o động mạch chủ tr&ecirc;n cổ đối phương, g&atilde; đ&agrave;n &ocirc;ng gầy g&ograve; đột ngột mở to mắt, ng&atilde; lăn xuống đất, co giật một hồi, bọt m&eacute;p tr&agrave;o ra.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một trong những phương ph&aacute;p tự vệ trong v&otilde; thuật cổ truyền, động mạch chủ cung cấp tới 80% lượng m&aacute;u cho tim v&agrave; đầu, nếu gặp t&aacute;c động đột ngột sẽ g&acirc;y hiện tượng ch&oacute;ng mặt, n&ocirc;n mửa, thậm ch&iacute; l&agrave; sốc v&agrave; tử vong.<br />\r\n<br />\r\nMột t&ecirc;n kh&aacute;c nh&igrave;n đồng đội của m&igrave;nh ng&atilde; lăn dưới đất liền lao tới t&uacute;m t&oacute;c Hạ Diệp, c&ocirc; nhanh như chớp nghi&ecirc;ng người n&eacute; tr&aacute;nh, đồng thời đập mạnh đầu t&ecirc;n đ&oacute; v&agrave;o g&oacute;c giường, g&atilde; ta vừa ngẩng l&ecirc;n lại bị Hạ Diệp d&ugrave;ng c&ugrave;i chỏ đ&aacute;nh, phần đầu lại đập v&agrave;o cạnh giường, m&aacute;u chảy loang lổ tr&ecirc;n ga trải giường m&agrave;u trắng.<br />\r\n<br />\r\nKiều Triệt đứng chặn một b&ecirc;n Hạ Diệp, kh&ocirc;ng cho những t&ecirc;n kh&aacute;c lao tới, đ&aacute;m c&ograve;n lại bắt đầu khuơ ch&acirc;n m&uacute;a tay, Kiều Triết t&uacute;m lấy một trong số đ&oacute;, chỉ với một c&uacute; đ&aacute;nh, &acirc;m thanh gi&ograve;n tan vang l&ecirc;n, k&egrave;m theo đ&oacute; l&agrave; tiếng g&agrave;o th&eacute;t, t&ecirc;n kia &ocirc;m chặt c&aacute;nh tay m&igrave;nh lăn lộn dưới s&agrave;n.<br />\r\n<br />\r\nTiếp theo, anh lại tung ch&acirc;n đạp t&ecirc;n ph&iacute;a sau đang x&ocirc;ng đến, l&uacute;c n&agrave;y mắt thấy một t&ecirc;n đang định d&ugrave;ng gậy đập Kiều Triết, Hạ Diệp đột ngột lao về ph&iacute;a người đ&agrave;n &ocirc;ng cầm gậy, nhưng lại v&ocirc; t&igrave;nh đập thắt lưng v&agrave;o th&agrave;nh giường, khiến c&ocirc; đau đớn r&iacute;t l&ecirc;n một tiếng.<br />\r\n<br />\r\nTrong ph&ograve;ng ngập tr&agrave;n những tiếng r&ecirc;и ɾỉ, mấy người quằn quại ng&atilde; lăn dưới đất v&igrave; đau đớn.<br />\r\n<br />\r\nĐột nhi&ecirc;n một trong số những t&ecirc;n c&ocirc;n đồ đ&oacute; cầm dao lao về ph&iacute;a Kiều Triết, Hạ Diệp h&eacute;t lớn: &ldquo;Cẩn thận!&rdquo;<br />\r\n<br />\r\nC&ocirc; kinh h&atilde;i đến mức qu&ecirc;n cả cơn đau đớn tr&ecirc;n eo, d&ugrave;ng hết tốc lực lao tới t&uacute;m lấy cổ tay t&ecirc;n c&ocirc;n đồ, thế nhưng lưỡi dao đ&atilde; đổi hướng đ&acirc;m thẳng v&agrave;o Hạ Diệp, khiến c&ocirc; kh&ocirc;ng kịp tr&aacute;nh, bả vai bị rạch một đường lớn, m&aacute;u ngay lập tức phun ra.<br />\r\n<br />\r\nKiểu Triết nhanh ch&oacute;ng nắm chặt cổ tay người đ&agrave;n &ocirc;ng c&ugrave;ng con dao, chỉ nghe thấy tiếng răng rắc của xương, con dao rơi xuống đất, g&atilde; đ&agrave;n &ocirc;ng h&eacute;t l&ecirc;n sau đ&oacute; bị Kiều Triệt quật ng&atilde; xuống s&agrave;n v&agrave; cũng l&agrave; người cuối c&ugrave;ng bị hạ gục.<br />\r\n<br />\r\nKiều Triệt nh&igrave;n Hạ Diệp, c&ocirc; vừa mới d&ugrave;ng điện thoại b&aacute;o cảnh s&aacute;t, l&uacute;c n&agrave;y đang kh&oacute; khăn quay đầu lại nh&igrave;n kiểm tra viết thương của m&igrave;nh. Kiều Triết chạy tới, &ocirc;m đầu c&ocirc;: &ldquo;Đừng cử động.&rdquo;<br />\r\n<br />\r\nSau đ&oacute;, anh nhẹ nh&agrave;ng k&eacute;o &aacute;o Hạ Diệp xuống để vết thương v&agrave; vải &aacute;o kh&ocirc;ng d&iacute;nh v&agrave;o nhau, tr&ecirc;n l&agrave;n da mịn m&agrave;ng xuất hiện một đường rạch gớm ghiếc, từ vai tới xương sống vẫn đang c&ograve;n rỉ m&aacute;u.<br />\r\n<br />\r\n<br />\r\n<br />\r\nVẻ mặt Kiều Triết v&ocirc; v&ugrave;ng nghi&ecirc;m nghị, kh&ocirc;ng biết l&agrave; kh&ocirc;ng đ&agrave;nh l&ograve;ng hay đang &aacute;y n&aacute;y.<br />\r\n<br />\r\nAnh nhanh ch&oacute;ng v&agrave;o ph&ograve;ng vệ sinh lấy ra chiếc khăn tắm, ấn chặt l&ecirc;n vết thương của Hạ Diệp.<br />\r\n<br />\r\nKiều Triết liếc nh&igrave;n đống hỗn độn dưới nền nh&agrave;, hai người bị Hạ Diệp phản đ&ograve;n đều đ&atilde; ngất lịm, bốn người c&ograve;n lại cũng bị thương ở mức độ nặng nhẹ kh&aacute;c nhau, l&uacute;c n&agrave;y đang b&ograve; tr&ecirc;n mặt đất thở hổn hển.<br />\r\n<br />\r\nNh&igrave;n đ&aacute;m người đ&oacute;, trong đầu Kiều Triết l&oacute;e l&ecirc;n c&aacute;c động t&aacute;c vừa rồi của Hạ Diệp, tấn c&ocirc;ng phần đầu v&agrave; động mạch chủ, ra đ&ograve;n ch&iacute; mạng, đ&aacute;y mắt anh tho&aacute;ng hiện l&ecirc;n một tia suy tư, anh nh&igrave;n đến khu&ocirc;n mặt đang t&aacute;i nhợt v&igrave; bị thương, liền nhanh ch&oacute;ng mặc &aacute;o lại cho c&ocirc;, rồi k&eacute;o tay c&ocirc;, n&oacute;i: &ldquo;Đến bệnh viện trước đ&atilde;.&rdquo;<br />\r\n<br />\r\nAnh kho&aacute;c vai đỡ Hạ Diệp bước ra ngo&agrave;i, nhẹ nh&agrave;ng d&ugrave;ng tay cố định chiếc khăn tắm ph&iacute;a sau. Hạ Diệp m&iacute;m m&ocirc;i, cuộn chặt c&aacute;c ng&oacute;n tay kh&ocirc;ng c&ograve;n sắc huyết.<br />\r\n<br />\r\nKhi ra tới cửa kh&aacute;ch sạn, xe cảnh s&aacute;t cũng vừa hay đi tới. Một vi&ecirc;n cảnh s&aacute;t trẻ xuống xe trước, nếu nh&igrave;n kỹ một ch&uacute;t sẽ thấy tr&ecirc;n cằm anh ấy vẫn c&ograve;n một &iacute;t r&acirc;u, &aacute;nh mắt đỏ ngầu v&agrave; gương mặt anh tuấn lộ ra phần kiệt quệ.<br />\r\n<br />\r\nKhi vi&ecirc;n cảnh s&aacute;t nh&igrave;n thấy người ở cửa, &aacute;nh mắt anh ấy lập tức chuyển th&agrave;nh vẻ tự b&agrave;o, vội v&agrave;ng chạy tới với vẻ kinh ngạc: &ldquo;Kiều Triết! C&aacute;i t&ecirc;n n&agrave;y, cậu đến đ&acirc;y từ khi n&agrave;o thế?&rdquo;<br />\r\n<br />\r\nKiều Triết n&eacute; tr&aacute;nh trả lời c&acirc;u hỏi của anh ấy: &ldquo;L&acirc;m Nghị, vừa rồi l&agrave; ch&uacute;ng t&ocirc;i b&aacute;o cảnh s&aacute;t, s&aacute;u t&ecirc;n giả mạo cảnh s&aacute;t x&ocirc;ng v&agrave;o kiểm tra ph&ograve;ng, cậu lập tức cử người l&ecirc;n ph&ograve;ng đưa hai t&ecirc;n bất tỉnh đến bệnh viện, nếu c&ograve;n chậm nữa th&igrave; chẳng giữ nổi mạng bọn ch&uacute;ng đ&acirc;u.&rdquo;<br />\r\n<br />\r\nSau đ&oacute;, anh lại bổ sung th&ecirc;m: &ldquo;Ng&agrave;y mai t&ocirc;i sẽ tới t&igrave;m cậu lấy lời khai, tiện thể t&igrave;m gi&uacute;p t&ocirc;i một kh&aacute;ch sạn kh&aacute;c, cảm ơn.&rdquo; N&oacute;i xong, anh giơ tay vẫy một chiếc taxi lại.<br />\r\n<br />\r\nL&acirc;m Nghị c&ograve;n đang nghĩ, kh&ocirc;ng hiểu tại sao một người đ&agrave;n &ocirc;ng bảo thủ như Kiều Triết lại đưa phụ nữ v&agrave;o kh&aacute;ch sạn. Năm đ&oacute;, kh&aacute; nhiều nữ sinh trong trường &ocirc;m mộng với anh, nhưng anh đều từ chối kh&ocirc;ng thương tiếc, khiến mấy c&ocirc; g&aacute;i sợ h&atilde;i đến mức kh&ocirc;ng d&aacute;m đ&ugrave;a giỡn, chỉ d&aacute;m đứng từ xa ngắm nh&igrave;n.<br />\r\n<br />\r\nHầu hết mọi người đều cho rằng v&igrave; anh ở c&ugrave;ng với nam giới trong qu&acirc;n đội qu&aacute; l&acirc;u n&ecirc;n xu hướng t&iacute;nh dục cũng từ đ&oacute; m&agrave; thay đổi! Hồi đ&oacute;, L&acirc;m Nghị c&ograve;n lo lắng cho &ldquo;b&ocirc;ng c&uacute;c nhỏ&rdquo; của m&igrave;nh n&ecirc;n n&eacute; tr&aacute;nh anh một thời gian, v&igrave; sợ bị anh th&iacute;ch.<br />\r\n<br />\r\nKhi L&acirc;m Nghị phản ứng lại th&igrave; chỉ c&ograve;n thấy b&oacute;ng d&aacute;ng của chiếc taxi, Kiều Triết v&agrave; Hạ Diệp đ&atilde; l&ecirc;n xe rời đi.<br />\r\n<br />\r\nL&acirc;m Nghị nhanh ch&oacute;ng l&ecirc;n lầu, v&agrave;o ph&ograve;ng xem hiện trường vụ &aacute;n, b&ecirc;n trong một người ngất xỉu s&ugrave;i bọt m&eacute;p, một người nằm lịm tr&ecirc;n giường, đầu c&ograve;n chảy m&aacute;u, bốn người c&ograve;n lại đang r&ecirc;и ɾỉ, nằm sấp dưới mặt đất ở c&aacute;c vị tr&iacute; kh&aacute;c nhau, mấy người kh&ocirc;ng ngất th&igrave; đang tạm thời đau đớn đến mức mất khả năng tự vận động.<br />\r\n<br />\r\nMột vi&ecirc;n cảnh s&aacute;t kh&aacute;c n&oacute;i với L&acirc;m Nghị vừa bước v&agrave;o: &ldquo;Đều c&ograve;n sống, đ&atilde; t&igrave;m được thẻ cảnh s&aacute;t giả tr&ecirc;n người bọn ch&uacute;ng.&rdquo; Anh ta vừa n&oacute;i vừa chuyển đồ b&ecirc;n trong ra ngo&agrave;i.<br />\r\n<br />\r\nL&acirc;m Nghị c&uacute;i đầu liếc nh&igrave;n những tấm thẻ cảnh s&aacute;t giả, lại nhớ tới lời n&oacute;i của Kiều Triết, n&ecirc;n nhanh ch&oacute;ng ra chỉ thị: &ldquo;Đều c&ograve;n sống cả th&igrave; kh&ocirc;ng cần lưu giữ hiện trường, trước ti&ecirc;n đưa hai t&ecirc;n đang h&ocirc;n m&ecirc; kia tới bệnh viện, c&ograve;n những t&ecirc;n kh&aacute;c l&ocirc;i hết về đồn.&rdquo;<br />\r\n<br />\r\nL&acirc;m Nghị trầm ng&acirc;m nh&igrave;n hai t&ecirc;n đang bị v&aacute;c ra ngo&agrave;i, với năng lực v&agrave; sự chuy&ecirc;n nghiệp của Kiều Triết, anh ho&agrave;n to&agrave;n c&oacute; thể kh&ocirc;ng d&ugrave;ng tới thủ đoạn ch&iacute; mạng như vậy, vậy th&igrave; ch&iacute;nh l&agrave; c&ocirc; g&aacute;i kia đ&atilde; ra tay với hai t&ecirc;n n&agrave;y.<br />\r\n<br />\r\nKiều Triết y&ecirc;u cầu t&agrave;i xế taxi đưa họ đến ph&ograve;ng cấp cứu gần nhất. B&aacute;c sĩ trực ph&ograve;ng cấp cứu của bệnh viện nhỏ l&agrave; một b&agrave; l&atilde;o, b&agrave; l&atilde;o vừa tiễn xong bệnh nh&acirc;n cuối c&ugrave;ng, l&uacute;c n&agrave;y đang đang thu dọn đồ đạc.<br />\r\n<br />\r\nKiều Triết đưa Hạ Diệp v&agrave;o ph&ograve;ng kh&aacute;m bệnh v&agrave; giải th&iacute;ch ngắn gọn với b&agrave; l&atilde;o rằng c&ocirc; bị một con dao cứa phải.<br />\r\n<br />\r\nHạ Diệp c&ugrave;ng b&agrave; l&atilde;o bước v&agrave;o gian ph&ograve;ng b&ecirc;n trong, c&ograve;n Kiều Triết đứng đợi b&ecirc;n ngo&agrave;i. Căn ph&ograve;ng vừa v&agrave;o tr&ocirc;ng gọn g&agrave;ng, ngăn nắp hơn, b&ecirc;n trong c&oacute; k&ecirc; một chiếc giường đơn s&aacute;t v&aacute;ch tường, cạnh đ&oacute; l&agrave; tủ k&iacute;nh v&agrave; chiếc b&agrave;n chứa đầy chai lọ, b&agrave; l&atilde;o bảo Hạ Diệp ngồi xuống giường, sau đ&oacute; đeo găng tay cao su d&ugrave;ng một lần v&agrave;o.<br />\r\n<br />\r\nB&agrave; d&ugrave;ng k&eacute;o cắt lớp quần &aacute;o xung quanh vết thương của Hạ Diệp, sau đ&oacute; lấy nh&iacute;p gắp b&ocirc;ng g&ograve;n để s&aacute;t tr&ugrave;ng vết thương, tiếp đến lại x&eacute;t nghiệm uốn v&aacute;n tr&ecirc;n da cho c&ocirc;, b&agrave; l&atilde;o nh&igrave;n v&agrave;o miệng vết thương n&oacute;i: &ldquo;D&agrave;i ch&iacute;n ph&acirc;n, may l&agrave; chỉ s&acirc;u khoảng hai ph&acirc;n, n&ecirc;n chỉ cần kh&acirc;u tr&ecirc;n bề mặt hai mũi l&agrave; được.&rdquo;<br />\r\n<br />\r\nN&oacute;i xong, liền quay người lấy thứ g&igrave; đ&oacute; trong tủ k&iacute;nh, Hạ Diệp r&otilde; r&agrave;ng l&agrave; cứng đờ người khi nghe thấy từ kh&acirc;u, c&ocirc; c&oacute; thể chịu được cơn đau bỏng r&aacute;t của thuốc s&aacute;t tr&ugrave;ng vừa rồi, nhưng cứ nghĩ đến mũi kim xuy&ecirc;n qua thịt l&agrave; da đầu lại t&ecirc; dại, c&ocirc; lắp bắp hỏi: &ldquo;C&oacute;, c&oacute;, c&oacute; thể kh&ocirc;ng&hellip; kh&ocirc;ng kh&acirc;u được kh&ocirc;ng ạ?&rdquo;<br />\r\n<br />\r\nB&agrave; l&atilde;o quay lại, liếc nh&igrave;n Hạ Diệp đang căng thẳng qua cặp k&iacute;nh l&atilde;o: &ldquo;Kh&ocirc;ng được, nếu kh&ocirc;ng kh&acirc;u vết thương sẽ rất kh&oacute; l&agrave;nh, c&ocirc; b&eacute; đường sợ, chỉ c&oacute; hai mũi th&ocirc;i, một ch&uacute;t l&agrave; xong rồi.&rdquo; N&oacute;i xong lại tiếp tục chuẩn bị dụng cụ.<br />\r\n<br />\r\nB&agrave; l&atilde;o bước đến b&ecirc;n cửa n&oacute;i với Kiều Triết: &ldquo;Vết thương tương đối s&acirc;u n&ecirc;n cần kh&acirc;u hai mũi, c&ocirc; g&aacute;i đang hoảng sợ khi biết m&igrave;nh cần phải kh&acirc;u, anh l&agrave; bạn trai c&ocirc; ấy phải kh&ocirc;ng, v&agrave;o đ&acirc;y an ủi c&ocirc; ấy một ch&uacute;t đi.&rdquo;<br />\r\n<br />\r\nSau khi nghe thấy c&acirc;u n&agrave;y, Kiều Triết khẽ khựng lại, nhưng cũng kh&ocirc;ng giải th&iacute;ch điều g&igrave;, chỉ cất điếu thuốc đang cầm tr&ecirc;n tay rồi bước v&agrave;o ph&ograve;ng.</h2>', 0, 'chuong-2-quet-o-mai-dam'),
(26, 15, 'Chương 3: Thay quần áo.', 'Chương 3: Thay quần áo.', '<h2>Kiều Triết đi về ph&iacute;a Hạ Diệp l&uacute;c n&agrave;y đang cau chặt m&agrave;y, hai người liếc nh&igrave;n nhau trong một khoảnh khắc ngắn ngủi, rồi Hạ Diệp lại c&uacute;i đầu nh&igrave;n xuống đất, như thể kh&ocirc;ng muốn để sự sợ h&atilde;i của m&igrave;nh lọt v&agrave;o mắt anh.<br />\r\n<br />\r\nAnh bước tới nắm lấy b&agrave;n tay đang t&uacute;m chặt v&agrave;o vạt &aacute;o của c&ocirc;, Hạ Diệp hơi sửng sốt, sau đ&oacute; mới nắm lấy.<br />\r\n<br />\r\nC&ocirc; cảm nhận được b&agrave;n tay rộng lớn của anh, tr&ecirc;n l&ograve;ng b&agrave;n tay c&ograve;n c&oacute; một vết chai d&agrave;y, sau đ&oacute; c&ocirc; lại quay sang một b&ecirc;n tiếp tục chờ đợi.<br />\r\n<br />\r\nB&agrave; l&atilde;o đưa mũi kim xuy&ecirc;n qua l&agrave;n da mỏng manh một c&aacute;ch dứt kho&aacute;t, v&igrave; kh&ocirc;ng c&oacute; thuốc g&acirc;y t&ecirc;, b&agrave;n tay đang nắm tay Kiều Triệt đột ngột siết chặt, cả người c&ocirc; khẽ run l&ecirc;n.<br />\r\n<br />\r\nHạ Diệp cảm nhận r&otilde; r&agrave;ng mũi kim v&agrave; sợi chỉ đang cọ x&aacute;t tr&ecirc;n da thịt m&igrave;nh, c&ocirc; đau đến mức đổ đầy mồ h&ocirc;i, hai h&agrave;ng l&ocirc;ng m&agrave;y c&agrave;ng th&ecirc;m nh&iacute;u chặt.<br />\r\n<br />\r\nTay c&ograve;n lại của Kiều Triết nhẹ nh&agrave;ng vuốt ve m&aacute;i t&oacute;c Hạ Diệp, anh ki&ecirc;n nhẫn c&ugrave;ng c&ocirc; chịu đựng qua khoảng thời gian tưởng chừng như d&agrave;i v&ocirc; tận n&agrave;y.<br />\r\n<br />\r\nHạ Diệp nghiến răng, cơn đau tr&ecirc;n lưng vẫn kh&ocirc;ng hề suy giảm, một l&uacute;c sau thấy ph&iacute;a sau kh&ocirc;ng c&ograve;n động t&iacute;nh g&igrave; nữa, c&ocirc; mới chậm r&atilde;i mở mắt ra.<br />\r\n<br />\r\nThấy đ&ocirc;i mắt đỏ hoe của c&ocirc;, Kiều Triết cảm nhận tr&aacute;i tim m&igrave;nh như bị thứ g&igrave; đ&oacute; v&ocirc; c&ugrave;ng mềm mại chạm phải.<br />\r\n<br />\r\nB&agrave; l&atilde;o đặt lại c&acirc;y kim c&ugrave;ng chỉ v&agrave;o chiếc hộp sứ, rồi n&oacute;i: &ldquo;Đứng dậy đi, quay lưng về ph&iacute;a t&ocirc;i.&rdquo;<br />\r\n<br />\r\nHạ Diệp quay mặt về ph&iacute;a Kiều Triết, lại nghe b&agrave; l&atilde;o n&oacute;i với anh: &ldquo;Cậu đỡ c&ocirc; ấy một ch&uacute;t.&rdquo; N&oacute;i xong liền khẽ k&eacute;o chiếc quần đ&ugrave;i của c&ocirc; xuống, để lộ ra nửa v&ograve;ng m&ocirc;ng, Hạ Diệp giật m&igrave;nh: &ldquo;Chờ đ&atilde;&hellip;&rdquo;, c&ograve;n chưa n&oacute;i hết c&acirc;u đ&atilde; bị b&agrave; l&atilde;o cắt ngang bằng một c&aacute;i vỗ nhẹ l&ecirc;n eo: &ldquo;C&uacute;i xuống.&rdquo;<br />\r\n<br />\r\nCảm gi&aacute;c tiếp theo m&agrave; Hạ Diệp cảm nhận được đ&oacute; l&agrave; một khoảng m&ocirc;ng lạnh to&aacute;t, dựa v&agrave;o kinh nghiệm sống &iacute;t ỏi của m&igrave;nh th&igrave; c&ocirc; đo&aacute;n đ&oacute; l&agrave; c&aacute;i lạnh do lau cồn.<br />\r\n<br />\r\nC&aacute;nh tay Kiều Triết bị c&ocirc; t&uacute;m chặt v&agrave; cứ thế nằm &uacute;p trong v&ograve;ng tay anh, Kiều Triết cố gắng hết sức c&oacute; thể để quay mặt đi chỗ kh&aacute;c rồi đột nhi&ecirc;n cảm thấy người trong v&ograve;ng tay m&igrave;nh đột ngột căng cứng lại.<br />\r\n<br />\r\nĐ&atilde; l&acirc;u lắm rồi Hạ Diệp kh&ocirc;ng ti&ecirc;m, lần trước đ&oacute; l&agrave; khi c&ograve;n học tiểu học, nếu n&oacute;i về cảm gi&aacute;c th&igrave; chỉ thấy t&ecirc; dại v&agrave; lạnh ngắt, c&ocirc; rất muốn kh&oacute;c, đ&acirc;y l&agrave; c&aacute;i g&igrave; thế n&agrave;y?<br />\r\n<br />\r\nB&agrave; l&atilde;o nhanh ch&oacute;ng r&uacute;t mũi kim ra rồi ấn b&ocirc;ng g&ograve;n l&ecirc;n vị tr&iacute; ti&ecirc;m, n&oacute;i với Kiều Triết: &ldquo;C&ocirc; ấy bị thương ở vai, n&ecirc;n hạn chế cử động kẻo vết thương lại hở, cậu ấn b&ocirc;ng gi&uacute;p c&ocirc; ấy một l&uacute;c đ&atilde;.&rdquo;<br />\r\n<br />\r\nKiều Triết nghe xong nhất thời kinh ngạc sau đ&oacute; mới chậm r&atilde;i duỗi tay ra, d&acirc;y thần kinh tr&ecirc;n đầu khẽ giật khi nh&igrave;n đến nửa b&ecirc;n m&ocirc;ng trắng mịn của c&ocirc;. Anh nhẹ nh&agrave;ng ấn tay l&ecirc;n cục b&ocirc;ng, rồi nhanh ch&oacute;ng quay đầu đi chỗ kh&aacute;c, đầu ng&oacute;n tay v&ocirc; t&igrave;nh chạm phải m&ocirc;ng Hạ Diệp, cảm gi&aacute;c trơn mịn khiến đầu ng&oacute;n tay khẽ run, hai m&aacute; lập tức ửng đỏ.<br />\r\n<br />\r\nHạ Diệp c&uacute;i đầu cố gắng để m&igrave;nh dễ thở nhất c&oacute; thể, trước đ&acirc;y c&ocirc; lu&ocirc;n cảm thấy thời gian tr&ocirc;i qua rất nhanh, nhưng đ&ecirc;m nay c&ocirc; mới thấu hiểu thế n&agrave;o gọi l&agrave; một gi&acirc;y tựa năm d&agrave;i.<br />\r\n<br />\r\nTrong l&ograve;ng Hạ Diệp đ&atilde; thầm ch&agrave;o hỏi đến tổ ti&ecirc;n đời thứ mười t&aacute;m của m&igrave;nh rồi, c&ocirc; bực bội tự chửi m&igrave;nh một tiếng: Thế n&agrave;o, chuyến đi kh&ocirc;ng c&oacute; kế hoạch, đi tới đ&acirc;u hay tới đ&oacute; vui chứ, bất ngờ qu&aacute; nhỉ? Ngạc nhi&ecirc;n chưa? C&oacute; k&iacute;ƈɦ ŧɦ&iacute;ƈɦ kh&ocirc;ng?<br />\r\n<br />\r\n&ldquo;Chắc l&agrave; kh&ocirc;ng chảy m&aacute;u nữa rồi.&rdquo;<br />\r\n<br />\r\nThấy c&ocirc; nghiến răng nghiến lợi thều th&agrave;o n&oacute;i, Kiều Triết vừa nghe thấy liền gỡ miếng b&ocirc;ng g&ograve;n ra, nhẹ nh&agrave;ng gi&uacute;p c&ocirc; k&eacute;o quần đ&ugrave;i l&ecirc;n.<br />\r\n<br />\r\nHạ Diệp đỏ mặt n&oacute;i cảm ơn.<br />\r\n<br />\r\nKiều Triết đi đến chỗ b&agrave; l&atilde;o lấy những thuốc m&agrave; Hạ Diệp cần uống, đồng thời mua th&ecirc;m mấy loại thuốc mỡ b&ocirc;i vết thương.<br />\r\n<br />\r\nTrước khi ra về, b&agrave; cụ dặn d&ograve;: &ldquo;Mấy ng&agrave;y tới kh&ocirc;ng được để vết thương tiếp x&uacute;c với nước, tr&aacute;nh ăn cay, tr&aacute;nh cử động mạnh, khi cởϊ &aacute;&sigma; nhớ ch&uacute; &yacute; đừng chạm v&agrave;o vết thương, một tuần sau tới cắt chỉ.&rdquo;<br />\r\n<br />\r\nHạ Diệp đỏ mặt, vội v&agrave;ng đ&aacute;p: &ldquo;Cảm ơn b&agrave;. Con nhớ rồi ạ.&rdquo;<br />\r\n<br />\r\n<br />\r\n<br />\r\nSau khi rời khỏi ph&ograve;ng kh&aacute;m, hai người trầm mặc đi cạnh nhau, Kiều Triết nh&igrave;n địa chỉ mới do L&acirc;m Nghị gửi tới tr&ecirc;n điện thoại, n&oacute;i với c&ocirc;: &ldquo;Tạm thời đ&ecirc;m nay h&atilde;y đến kh&aacute;ch sạn mới c&ugrave;ng t&ocirc;i, v&igrave; ph&ograve;ng cũ chắc chắn kh&ocirc;ng thể ở được nữa rồi.&rdquo;<br />\r\n<br />\r\nĐ&oacute; l&agrave; ngữ điệu ra lệnh, kh&ocirc;ng cho Hạ Diệp c&oacute; thời gian để từ chối, sau đ&oacute; Kiều Triết vươn tay vẫy một chiếc taxi lại.<br />\r\n<br />\r\nAnh gi&uacute;p Hạ Diệp mở cửa, hai người ngồi ở ph&iacute;a sau, Kiều Triết n&oacute;i cho t&agrave;i xế địa chỉ rồi lại rơi v&agrave;o trạng th&aacute;i trầm mặc như ban đầu.<br />\r\n<br />\r\nHạ Diệp nhớ ra h&agrave;nh l&yacute; của m&igrave;nh vẫn c&ograve;n ở kh&aacute;ch sạn cũ: &ldquo;Quần &aacute;o của t&ocirc;i vẫn c&ograve;n ở kh&aacute;ch sạn kia.&rdquo;<br />\r\n<br />\r\n&ldquo;H&agrave;nh l&yacute; đều đ&atilde; được chuyển đi rồi, xin lỗi, chưa hỏi &yacute; kiến c&ocirc; đ&atilde; tự &yacute; chuyển đồ của c&ocirc; đi.&rdquo; H&agrave;nh l&yacute; l&agrave; do L&acirc;m Nghị chuyển.<br />\r\n<br />\r\n&ldquo;Cảm ơn, l&aacute;t nữa t&ocirc;i sẽ gửi anh chi ph&iacute; y tế c&ugrave;ng tiền ph&ograve;ng kh&aacute;ch sạn.&rdquo;<br />\r\n<br />\r\nKiều Triết kh&ocirc;ng đ&aacute;p lại, anh kh&ocirc;ng muốn tranh luận về những điều v&ocirc; nghĩa n&agrave;y.<br />\r\n<br />\r\nSau khi đến kh&aacute;ch sạn mới, Kiều Triết nhận thẻ ph&ograve;ng từ nh&acirc;n vi&ecirc;n lễ t&acirc;n, l&ecirc;n đến ph&ograve;ng mới ph&aacute;t hiện ra rằng c&aacute;i t&ecirc;n L&acirc;m Nghị kia đ&atilde; tự cho rằng m&igrave;nh th&ocirc;ng minh n&ecirc;n đặt ph&ograve;ng đơn một giường lớn.<br />\r\n<br />\r\n&ldquo;Bạn t&ocirc;i kh&ocirc;ng biết mối quan hệ của ch&uacute;ng ta, t&ocirc;i sẽ ngủ sofa.&rdquo; Kiều Triết c&oacute; ch&uacute;t &aacute;y n&aacute;y, l&agrave; do bản th&acirc;n anh đ&atilde; qu&ecirc;n kh&ocirc;ng n&oacute;i r&otilde; với L&acirc;m Nghị.<br />\r\n<br />\r\nThấy chiếc vali kh&ocirc;ng bị hư hại g&igrave;, Hạ Diệp mới c&oacute; thể thả lỏng, giọng n&oacute;i vui vẻ: &ldquo;Kh&ocirc;ng sao đ&acirc;u, huống hồ b&acirc;y giờ t&ocirc;i c&ograve;n cần anh nữa m&agrave;.&rdquo; N&oacute;i xong liền mỉm cười với Kiều Triết.<br />\r\n<br />\r\nKiều Triết hơi bối rối trước sự thay đổi th&aacute;i độ qu&aacute; nhanh của c&ocirc;, vừa rồi r&otilde; r&agrave;ng bầu kh&ocirc;ng kh&iacute; vẫn đang rất ảm đạm.<br />\r\n<br />\r\nHạ Diệp đi quanh ph&ograve;ng một v&ograve;ng, căn ph&ograve;ng n&agrave;y cũng kh&aacute; giống với ph&ograve;ng ban đầu bọn họ thu&ecirc;, chỉ kh&aacute;c l&agrave; c&oacute; th&ecirc;m chỗ k&ecirc; sofa ph&iacute;a trước cửa sổ, Hạ Diệp thầm nghĩ chắc c&oacute; lẽ to&agrave;n bộ kh&aacute;ch sạn ở khu thủ Cảng n&agrave;y đều l&agrave; c&ugrave;ng một chủ, quả thực l&agrave; qu&aacute; biết c&aacute;ch tiết kiệm kh&ocirc;ng gian.<br />\r\n<br />\r\nC&ocirc; nh&igrave;n bộ đồ ngủ tr&ecirc;n người m&igrave;nh thầm nghĩ chắc chắn kh&ocirc;ng thể mặc n&oacute; đi ngủ nữa rồi, sau đ&oacute; liền đến t&igrave;m một chiếc v&aacute;y hai d&acirc;y kh&aacute;c bằng vải lụa, sau một hồi ngắm ngh&iacute;a, lại nh&igrave;n xuống quần &aacute;o tr&ecirc;n người m&igrave;nh, nếu tự cởϊ &aacute;&sigma;, sẽ đụng đến vết thương tr&ecirc;n bả vai, vết thương vốn đ&atilde; đau chắc chắn sẽ c&agrave;ng đau hơn.<br />\r\n<br />\r\nV&igrave; vậy Hạ Diệp kh&ocirc;ng ch&uacute;t do dự gọi Kiều Triết l&uacute;c n&agrave;y đang k&eacute;o r&egrave;m cửa: &ldquo;Kiều Triết, anh c&oacute; thể gi&uacute;p t&ocirc;i thay quần &aacute;o được kh&ocirc;ng, chiếc &aacute;o đang mặc ph&iacute;a sau d&iacute;nh nhớp nh&aacute;p cảm gi&aacute;c rất kh&oacute; chịu.&rdquo; N&oacute;i xong lại chỉ v&agrave;o bộ đồ ngủ đang để tr&ecirc;n giường, rồi nhắm mắt giơ tay l&ecirc;n.<br />\r\n<br />\r\nKiều Triết khẽ khựng lại một gi&acirc;y sau đ&oacute; mới nhận ra &yacute; định của Hạ Diệp, anh lặng lẽ bước tới sau lưng c&ocirc;, v&eacute;n chiếc &aacute;o d&iacute;nh b&ecirc; bết m&aacute;u l&ecirc;n, khi k&eacute;o đến bả vai, anh nhẹ nh&agrave;ng d&ugrave;ng tay che chắn, bảo vệ vết thương, để tr&aacute;nh đụng phải miếng băng gạc d&aacute;n b&ecirc;n ngo&agrave;i, sau khi cởi bỏ chiếc &aacute;o, Hạ Diệp d&ugrave;ng tay che lấy ngực, giữ chặt chiếc &aacute;&sigma; ɭ&oacute;ŧ m&agrave;u trắng kh&ocirc;ng nh&atilde;n hiệu duy nhất c&ograve;n s&oacute;t lại tr&ecirc;n người, d&acirc;y &aacute;&sigma; ɭ&oacute;ŧ cũng bị d&iacute;nh m&aacute;u, đứt l&agrave;m đ&ocirc;i do vết dao cắt, v&ocirc; hồn bu&ocirc;ng th&otilde;ng tr&ecirc;n eo c&ocirc;.<br />\r\n<br />\r\nNh&igrave;n tấm lưng mịn m&agrave;ng của Hạ Diệp, vết m&aacute;u tr&ecirc;n &aacute;&sigma; ɭ&oacute;ŧ c&ograve;n chưa kh&ocirc; hẳn, tản ra vị tanh ngọt của m&aacute;u thịt, tiếp đến đập v&agrave;o mắt anh l&agrave; vết bầm t&iacute;m tr&ecirc;n eo c&ocirc;, Kiều Triết bu&ocirc;ng c&aacute;nh tay đang định với lấy chiếc &aacute;o lụa mặc v&agrave;o cho c&ocirc;, n&oacute;i: &ldquo;Đợi t&ocirc;i một ch&uacute;t.&rdquo;<br />\r\n<br />\r\nHạ Diệp nh&igrave;n Kiều Triết đi v&agrave;o ph&ograve;ng tắm, b&ecirc;n trong truyền đến tiếng nước chảy, ngay sau đ&oacute; &acirc;m thanh dừng lại, c&ocirc; thấy Kiều Triết tay tr&aacute;i cầm một chiếc khăn tắm m&agrave;u trắng đ&atilde; được nh&uacute;ng nước, anh bước tới chỗ b&agrave;n tr&agrave;, lấy thuốc từ trong t&uacute;i ra, n&oacute;i với Hạ Diệp vẫn đang &ocirc;m ngực m&igrave;nh ngồi tr&ecirc;n giường: &ldquo;C&ocirc; nằm xuống giường đi, t&ocirc;i gi&uacute;p c&ocirc; chườm khăn lạnh l&ecirc;n vết bầm rồi b&ocirc;i thuốc.&rdquo;<br />\r\n<br />\r\nHạ Diệp ngoan ngo&atilde;n nghe theo, thầm nghĩ người đ&agrave;n &ocirc;ng n&agrave;y thật chu đ&aacute;o.<br />\r\n<br />\r\nKiều Triết bước đến b&ecirc;n giường nhẹ nh&agrave;ng chườm khăn l&ecirc;n vết bầm tr&ecirc;n eo c&ocirc;. Đột ngột bị đụng, Hạ Diệp theo phản xạ k&ecirc;u l&ecirc;n: &ldquo;Đau.&rdquo;<br />\r\n<br />\r\n&ldquo;Khi ra tay sao kh&ocirc;ng nghĩ đến việc sẽ bị đau chứ?&rdquo; N&oacute;i xong, động t&aacute;c tr&ecirc;n tay anh lại nhẹ nh&agrave;ng hơn một ch&uacute;t.<br />\r\n<br />\r\nC&ugrave;ng với độ m&aacute;t của chiếc khăn, Hạ Diệp đ&atilde; cảm thấy bớt đau hơn rất nhiều, c&ocirc; thoải m&aacute;i nheo mắt nh&igrave;n anh: &ldquo;L&uacute;c đ&oacute; kh&ocirc;ng đau m&agrave;! Chẳng phải l&agrave; anh kh&ocirc;ng biết t&igrave;nh h&igrave;nh khi đ&oacute; thế n&agrave;o, ra tay chỉ l&agrave; việc sớm hay muộn m&agrave; th&ocirc;i, đ&aacute;nh đ&ograve;n bất ngờ ch&iacute;nh l&agrave; phương ph&aacute;p tốt nhất. Bằng kh&ocirc;ng th&igrave; sao hai người ch&uacute;ng ta c&oacute; thể đấu lại s&aacute;u t&ecirc;n kia chứ, nhưng m&agrave; l&uacute;c đ&oacute; t&ocirc;i kh&ocirc;ng hề hay biết anh lại lợi hại đến vậy, nếu biết sớm anh c&oacute; thể đ&aacute;nh lại ch&uacute;ng th&igrave; t&ocirc;i đ&atilde; chẳng cần liều mạng vậy rồi.&rdquo;<br />\r\n<br />\r\nKiều Triết kh&ocirc;ng biết n&ecirc;n ph&ecirc; b&igrave;nh hay khen ngợi c&ocirc; nữa: &ldquo;C&ocirc; cũng l&yacute; lẽ t&agrave;i t&igrave;nh gớm, một c&ocirc; g&aacute;i nhỏ nhắn như c&ocirc; m&agrave; ra tay t&agrave;n độc như vậy, cũng may l&agrave; hai t&ecirc;n kia đ&atilde; tỉnh lại.&rdquo;<br />\r\n<br />\r\nHạ Diệp thản nhi&ecirc;n hỏi: &ldquo;Nếu kh&ocirc;ng tỉnh lại th&igrave; thế n&agrave;o?&rdquo;<br />\r\n<br />\r\nKiều Triết nhất thời kh&ocirc;ng biết n&oacute;i sao, anh ngẫm nghĩ hồi l&acirc;u: &ldquo;Theo luật th&igrave; như vậy kh&ocirc;ng được t&iacute;nh l&agrave; tự vệ nữa rồi.&rdquo;<br />\r\n<br />\r\n<br />\r\n<br />\r\nC&ocirc; nh&igrave;n chằm chằm vẻ mặt của Kiều Triết, chống một tay dựng người l&ecirc;n hỏi: &ldquo;Anh l&agrave; cảnh s&aacute;t sao?&rdquo; Biểu t&igrave;nh nghi&ecirc;m t&uacute;c.<br />\r\n<br />\r\nKhăn tắm theo động t&aacute;c của c&ocirc; m&agrave; tuột xuống, Kiều Triết ấn eo Hạ Diệp: &ldquo;Nằm xuống, đừng cử động.&rdquo; N&oacute;i xong anh lại lật mặt kh&aacute;c của chiếc khăn chườm l&ecirc;n vết bầm: &ldquo;Ng&agrave;y mai, t&ocirc;i sẽ đến đồn cảnh s&aacute;t ghi lời khai, c&ograve;n phải xem mức độ thương tật của hai người đ&oacute; th&igrave; mới đưa ra được ph&aacute;n đo&aacute;n cụ thể.&rdquo;<br />\r\n<br />\r\n&ldquo;Gi&uacute;p t&ocirc;i lấy điện thoại tr&ecirc;n b&agrave;n.&rdquo; Hạ Diệp nh&iacute;u m&agrave;y trầm ng&acirc;m.<br />\r\n<br />\r\nKiều Triết nh&igrave;n c&ocirc; đầy nghi hoặc: &ldquo;Đ&atilde; hơn một giờ s&aacute;ng rồi, ngủ một giấc thật ngon đi đ&atilde;, ng&agrave;y mai dậy t&iacute;nh sau.&rdquo;<br />\r\n<br />\r\nHạ Diệp ngơ ng&aacute;c hỏi: &ldquo;Anh chắc chắn luật l&agrave; như vậy sao? Gi&uacute;p t&ocirc;i lấy điện thoại, t&ocirc;i hẹn Luật sư s&aacute;ng mai, sau đ&oacute; c&ugrave;ng đến lấy lời khai.&rdquo; N&oacute;i xong, c&ocirc; lại bổ sung th&ecirc;m: &ldquo;Chiếc khăn n&agrave;y hết lạnh rồi.&rdquo; &Acirc;m lượng cũng dần dần nhỏ lại.<br />\r\n<br />\r\nKiều Triết nghiến răng đưa điện thoại cho c&ocirc;, sau đ&oacute; cầm khăn v&agrave;o ph&ograve;ng tắm ng&acirc;m lại nước lạnh.<br />\r\n<br />\r\nL&uacute;c đi ra, thấy c&ocirc; đang bất động cầm điện thoại, anh nhẹ nh&agrave;ng đặt chiếc khăn lạnh l&ecirc;n eo, Hạ Diệp bị lạnh khẽ giật m&igrave;nh, mới ho&agrave;n hồn trở lại: &ldquo;Vậy c&ograve;n anh th&igrave; sao? Đồng phạm?&rdquo;<br />\r\n<br />\r\nKiều Triết bật cười trước c&acirc;u hỏi thẳng thừng của c&ocirc;: &ldquo;Đừng lo lắng, nhiều nhất chỉ hai đến ba năm th&ocirc;i!&rdquo;<br />\r\n<br />\r\nHạ Diệp đột nhi&ecirc;n thả lỏng trước giọng điệu đ&ugrave;a cợt của anh: &ldquo;Anh thoải m&aacute;i như vậy, c&oacute; thấy bộ dạng của người sắp bị ngồi t&ugrave; đ&acirc;u? &Agrave;? Người m&agrave; anh mở miệng ra l&agrave; sai bảo &yacute;, chắc l&agrave; th&acirc;n thiết lắm nhỉ, anh c&oacute; quan hệ thế n&agrave;o với vi&ecirc;n cảnh s&aacute;t đ&oacute;? Anh ấy kh&ocirc;ng mắt nhắm mắt mở cho qua được &agrave;?&rdquo; N&oacute;i xong liền cẩn thận nh&igrave;n chằm chằm n&eacute;t mặt Kiều Triết.<br />\r\n<br />\r\n&ldquo;Mở miệng ra l&agrave; sai bảo? Vậy c&ocirc; thấy t&ocirc;i v&agrave; c&ocirc; c&oacute; th&acirc;n thiết kh&ocirc;ng? Mối quan hệ giữa ch&uacute;ng ta l&agrave; g&igrave;?&rdquo;<br />\r\n<br />\r\n&ldquo;Ừm th&igrave; rất th&acirc;n! Ch&uacute;ng ta l&agrave; người từng ngủ chung ph&ograve;ng với nhau!&rdquo; Hạ Diệp n&oacute;i với vẻ mặt &aacute;i muội.<br />\r\n<br />\r\nSau khi nghe những lời Hạ Diệp n&oacute;i, Kiều Triết mới nhận ra m&igrave;nh đ&atilde; rơi v&agrave;o bẫy của c&ocirc;, liền tr&ecirc;u chọc: &ldquo;Vậy t&ocirc;i c&oacute; &iacute;ch lợi g&igrave;?&rdquo;<br />\r\n<br />\r\nHạ Diệp khẽ đưa mắt n&oacute;i: &ldquo;Anh thấy đấy, vết thương tr&ecirc;n vai t&ocirc;i l&agrave; v&igrave; anh n&ecirc;n mới bị thương v&agrave; chắc chắn sẽ để lại sẹo, ảnh hưởng tới thẩm mỹ, nhưng t&ocirc;i sẽ miễn cưỡng chấp nhận việc anh lấy th&acirc;n b&aacute;o đ&aacute;p vậy!&rdquo;<br />\r\n<br />\r\nKiều Triết nh&igrave;n vẻ mặt đang tỏ ra tiếc nuối của c&ocirc;, lại c&ograve;n d&aacute;m n&oacute;i l&agrave; miễn cưỡng chấp nhận, anh c&uacute;i người th&igrave; thầm v&agrave;o tai c&ocirc;: &ldquo;Được, d&ugrave; sao th&igrave; sớm muộn g&igrave; c&ocirc; cũng l&agrave; người của t&ocirc;i, vậy đ&ecirc;m nay ch&uacute;ng ta ngủ chung giường với nhau, c&ocirc; kh&ocirc;ng để &yacute; chứ?&rdquo; N&oacute;i xong, anh nhấc khăn tắm ra, nh&uacute;ng b&ocirc;ng g&ograve;n v&agrave;o rượu thuốc, chuẩn bị xoa l&ecirc;n v&ugrave;ng da t&iacute;m t&aacute;i của c&ocirc;.<br />\r\n<br />\r\nHạ Diệp cảm nhận r&otilde; nhịp đập của tim m&igrave;nh, nhất thời kh&ocirc;ng phản ứng lại, c&ocirc; nh&igrave;n chằm chằm Kiều Triết chỉ đang b&ocirc;i thuốc cho m&igrave;nh, tựa như những lời vừa rồi kh&ocirc;ng phải l&agrave; do anh n&oacute;i vậy.<br />\r\n<br />\r\nTrong kh&ocirc;ng kh&iacute; nồng nặc m&ugrave;i thuốc, cứ thế cho đến khi Kiều Triết b&ocirc;i thuốc xong Hạ Diệp vẫn chưa ho&agrave;n hồn trở lại, Kiều Triết nh&igrave;n vẻ mặt ngốc nghếch của c&ocirc;, liền k&eacute;o c&ocirc; ra khỏi cơn kinh ngạc: &ldquo;Đ&ugrave;a c&ocirc; đ&oacute;, mới vậy m&agrave; đ&atilde; ng&acirc;y ra rồi?&rdquo;<br />\r\n<br />\r\nHạ Diệp l&uacute;c n&agrave;y mới phản ứng lại, c&ocirc; v&ugrave;i mặt xuống giường nghẹn ng&agrave;o n&oacute;i: &ldquo;Hứ, l&agrave;m g&igrave; c&oacute; chuyện đ&oacute;, mau gi&uacute;p t&ocirc;i th&aacute;o m&oacute;c &aacute;&sigma; ɭ&oacute;ŧ ra đi, t&ocirc;i muốn thay bộ đồ ngủ kh&aacute;c.&rdquo;<br />\r\n<br />\r\nLần n&agrave;y, tới lượt Kiều Triết ngẩn người: &ldquo;C&aacute;i, c&aacute;i g&igrave;?&rdquo;<br />\r\n<br />\r\nHạ Diệp quay lại nh&igrave;n vẻ mặt c&oacute; ch&uacute;t sững sờ của anh, nhịn kh&ocirc;ng được, bật cười: &ldquo;Sao n&agrave;o? T&ocirc;i đ&atilde; l&agrave; người của anh rồi, anh c&ograve;n thẹn th&ugrave;ng c&aacute;i g&igrave;? Hay đ&acirc;y l&agrave; lần đầu ti&ecirc;n anh gi&uacute;p một c&ocirc; g&aacute;i cởi nội y? Hả?&rdquo;<br />\r\n<br />\r\nKiều Triết thầm nghĩ, đ&acirc;y quả thực l&agrave; lần đầu ti&ecirc;n anh tiếp x&uacute;c gần gũi với một c&ocirc; g&aacute;i như vậy, cũng kh&ocirc;ng phải chưa từng gặp gỡ phụ nữ, m&agrave; l&agrave; chưa từng gặp người n&agrave;o lộ liễu như c&ocirc;.<br />\r\n<br />\r\nTừ năm mười s&aacute;u tuổi, Kiều Triết đ&atilde; v&agrave;o qu&acirc;n đội v&agrave; bắt đầu tập luyện, anh rất &iacute;t khi tiếp x&uacute;c với người kh&aacute;c giới, trước năm mười s&aacute;u tuổi lại ph&aacute;t cuồng v&igrave; b&oacute;ng rổ v&agrave; game, anh cảm thấy y&ecirc;u đương qu&aacute; l&atilde;ng ph&iacute; thời gian, n&ecirc;n ngoại trừ học h&agrave;nh th&igrave; chỉ c&oacute; b&oacute;ng rổ v&agrave; game.<br />\r\n<br />\r\nTiếp đ&oacute;, sau đợt huấn luyện v&agrave; tham gia c&ocirc;ng việc, cũng c&oacute; hẹn h&ograve; một lần, c&ocirc; g&aacute;i đ&oacute; l&agrave; ch&aacute;u g&aacute;i của một người bạn của &ocirc;ng nội anh, l&uacute;c đ&oacute; dưới sự th&uacute;c &eacute;p của &ocirc;ng nội anh mới quyết định thử xem sao, đồng thời cũng nhằm x&oacute;a tan nghi ngờ của bạn b&egrave; về xu hướng t&iacute;nh dục của m&igrave;nh.<br />\r\n<br />\r\nBan đầu, anh chỉ hẹn h&ograve; qua mạng với c&ocirc; g&aacute;i đ&oacute;, v&igrave; sau khi tỏ t&igrave;nh với anh chẳng được bao l&acirc;u đối phương lại đi Ph&aacute;p du học. Cũng chẳng bao l&acirc;u sau đ&oacute;, bạn trai người Ph&aacute;p của c&ocirc; ta đ&atilde; li&ecirc;n lạc với anh, c&ograve;n gửi cho anh tấm ảnh hai người họ ở b&ecirc;n nhau, thậm ch&iacute; c&ograve;n gửi cả những tấm ảnh giường chiếu hết sức th&acirc;n mật kh&aacute;c nữa, cảm gi&aacute;c bỗng chốc biến th&agrave;nh người thứ ba khiến anh kh&ocirc;ng thể ngu&ocirc;i ngoai trong một thời gian ngắn, v&igrave; vậy đ&atilde; n&oacute;i r&otilde; r&agrave;ng với c&ocirc; g&aacute;i đ&oacute;, rồi kết th&uacute;c cuộc t&igrave;nh &ldquo;online&rdquo; của m&igrave;nh.</h2>', 0, 'chuong-3-thay-quan-ao');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(27, 16, 'Chương 1: NGÀY Ở TIỆM MỲ NATSUKASHII', 'Chương 1: NGÀY Ở TIỆM MỲ NATSUKASHII', '<h2>Ở ngoại &ocirc; Tokyo phồn hoa, c&oacute; một tiệm mỳ đặc biệt, t&ecirc;n l&agrave; Natsukashii. Tiệm mỳ sẽ nghỉ v&agrave;o thứ Bảy v&agrave; Chủ Nhật cuối c&ugrave;ng của th&aacute;ng. Đ&acirc;y l&agrave; nơi sẵn s&agrave;ng lắng nghe c&acirc;u chuyện của tất cả mọi người. Đ&uacute;ng vậy, tất cả moi người.<br />\r\n<br />\r\n- 5h rồi, mở cửa tiệm th&ocirc;i_ C&ocirc; chủ của tiệm mỳ bắt đầu mở cửa đ&oacute;n thực kh&aacute;ch.<br />\r\n<br />\r\n- Cho 2 t&ocirc; mỳ udon<br />\r\n<br />\r\n- 1 t&ocirc; ramen nh&eacute;<br />\r\n<br />\r\n- Th&ecirc;m tempura cho ch&uacute;ng em nh&eacute;<br />\r\n<br />\r\nKhung giờ n&agrave;y qu&aacute;n rất đ&ocirc;ng, ph&agrave;n lớn kh&aacute;ch l&agrave; học sinh c&aacute;c trường tan học v&agrave; những nh&acirc;n vi&ecirc;n l&agrave;m việc ở ngoại &ocirc;. &Agrave; tất nhi&ecirc;n l&agrave; kh&ocirc;ng thể thiếu người d&acirc;n trong khu rồi. Qu&aacute;n mỳ n&agrave;y kh&aacute; nổi tiếng ở đ&acirc;y bởi v&igrave; hương vị tươi ngon c&ugrave;ng với c&aacute;ch phục vụ đặc biệt chả giống ai.<br />\r\n<br />\r\n- N&agrave;y nh&oacute;c, mới bem nhau thua đấy &agrave;?_ Nara chủ tiệm cười khẩy nh&igrave;n 2 đứa nh&oacute;c mặt mũi lấm lem mới v&agrave;o.<br />\r\n<br />\r\n- C&ograve;n l&acirc;u, t&ocirc;i chỉ bị t&eacute; th&ocirc;i_ Một t&ecirc;n nh&oacute;c biện minh<br />\r\n<br />\r\n- &Uacute;i trời, đừng qua mắt chị, mấy nh&oacute;c đ&aacute;nh nhau thua l&agrave; c&aacute;i chắc, tr&ocirc;ng yếu x&igrave;u đi m&agrave;_ Nara kh&ocirc;ng ngừng tr&ecirc;u chọc<br />\r\n<br />\r\n- Chị..._ 2 đứa nh&oacute;c tức kh&ocirc;ng n&oacute;i n&ecirc;n lời<br />\r\n<br />\r\n- Được rồi Nara, đừng chọc tụi nh&oacute;c nữa_ B&aacute;c Fuji ở một b&ecirc;n nhắc nhở, tay bưng 2 t&ocirc; mỳ đặt l&ecirc;n b&agrave;n<br />\r\n<br />\r\n- V&acirc;ng v&acirc;ng... M&agrave; nay 2 đứa nh&oacute;c kia th&ecirc;m k&iacute;nh ngữ v&agrave;o_ Nara k&iacute; đầu 2 đứa<br />\r\n<br />\r\n- Kh&ocirc;ng bao giờ_ 2 đứa h&igrave; hục ăn mỳ<br />\r\n<br />\r\n- Chậc chậc, mau ăn mỳ rồi về nh&agrave; với ba mẹ đi, lần sau c&oacute; đ&aacute;nh th&igrave; đ&aacute;nh cho thắng, kh&ocirc;ng chị tăng gấp đ&ocirc;i tiền mỳ đấy_ Nara<br />\r\n<br />\r\nRẦM.... Bỗng nhi&ecirc;n từ đ&acirc;u một đ&aacute;m thanh ni&ecirc;n học cấp Ba xuất hiện đạp đổ đồ d&ugrave;ng trong qu&aacute;n<br />\r\n<br />\r\n- Qu&yacute; kh&aacute;ch muốn d&ugrave;ng g&igrave; ạ?_ Nara v&ocirc; c&ugrave;ng vui vẻ hỏi han mấy vị kh&aacute;ch mới v&agrave;o<br />\r\n<br />\r\n- Ch&agrave;, qu&aacute;n của b&agrave; c&ocirc; n&agrave;y l&agrave;m ăn khấm kh&aacute; qu&aacute; nhỉ?_ Một t&ecirc;n trong đ&aacute;m nh&igrave;n quanh qu&aacute;n n&oacute;i<br />\r\n<br />\r\n- V&acirc;ng v&acirc;ng, qu&aacute;n ch&uacute;ng t&ocirc;i cũng b&igrave;nh thường th&ocirc;i, kh&ocirc;ng được như những g&igrave; cậu n&oacute;i đ&acirc;u_ Nara cười đ&aacute;p<br />\r\n<br />\r\n- B&agrave; c&ocirc;, b&agrave; biết đấy, l&agrave;m ăn tốt như vậy dễ bị bọn lưu manh d&ograve;m ng&oacute; lắm, vậy n&ecirc;n v&igrave; tấm l&ograve;ng nghĩa hiệp n&agrave;y tụi t&ocirc;i rất sẵn l&ograve;ng gi&uacute;p đỡ qu&aacute;n b&agrave; c&ocirc;_ T&ecirc;n b&ecirc;n cạnh n&oacute;i<br />\r\n<br />\r\n- Oh, th&igrave; ra l&agrave; vậy. Qu&iacute; h&oacute;a qu&aacute;, nhưng m&agrave; qu&aacute;n t&ocirc;i nhỏ thế n&agrave;y n&ecirc;n chắc kh&ocirc;ng g&acirc;y tư th&ugrave; với ai đ&acirc;u_ Nara vẫn giữ nguy&ecirc;n nụ cười tr&ecirc;n m&ocirc;i<br />\r\n<br />\r\n- N&agrave;y b&agrave; c&ocirc;, th&ocirc;ng minh l&ecirc;n đi, được dưới trướng bảo hộ của tui t&ocirc;i l&ograve;a vinh dự đấy, biết điều thi mau đồng &yacute; đi_ T&ecirc;n đ&oacute; n&oacute;i<br />\r\n<br />\r\n- ồ vậy &agrave;, nghe c&oacute; vẻ hấp dẫn nhỉ, vậy ch&uacute;ng ta ra ngo&agrave;i b&agrave;n về việc n&agrave;y nh&eacute;, ở đ&acirc;y kh&aacute;ch của ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng thấy thỏa m&aacute;i_ Nara n&oacute;i rồi bước ra khoảng đất trống cạnh tiệm mỳ, theo sao l&agrave; lũ nh&oacute;c cấp 3<br />\r\n<br />\r\n- Ch&agrave;, vậy &yacute; của cậu l&agrave; cậu muốn bảo hộ cho tiệm ch&uacute;ng t&ocirc;i &agrave;?_ Nara<br />\r\n<br />\r\n- Đ&uacute;ng vậy, tuy nhi&ecirc;n ch&uacute;ng t&ocirc;i cũng cần v&agrave;i thứ gọi l&agrave;..._ T&ecirc;n cầm đầu vừa n&oacute;i vừa ra hiệu<br />\r\n<br />\r\n- T&ocirc;i hiểu rồi, nhưng m&agrave; tiếc qu&aacute;, t&ocirc;i kh&ocirc;ng sẵn l&ograve;ng l&agrave;m việc n&agrave;y đ&acirc;u_ Nara lắc đầu tỏ vẻ bất lực<br />\r\n<br />\r\n- B&agrave; c&ocirc; b&agrave; bị ngu hả, ch&uacute;ng t&ocirc;i tới để th&ocirc;ng b&aacute;o chứ kh&ocirc;ng cần b&agrave; c&ocirc; đồng &yacute; đ&acirc;u_ Một t&ecirc;n &aacute;p s&aacute;t lại Nara<br />\r\n<br />\r\nCỐP... C&aacute;i mu&ocirc;i trong tay Nara gi&aacute;ng cho t&ecirc;n đ&oacute; 1 c&uacute; cực mạnh<br />\r\n<br />\r\n- Phải biết giữ khoảng c&aacute;ch chứ, d&ugrave; g&igrave; người ta cũng l&agrave; nữ m&agrave;, đ&acirc;u c&oacute; &aacute;p s&aacute;t lại như vậy được_ Nara huơ huơ c&aacute;i mu&ocirc;i trong tay<br />\r\n<br />\r\n- Con mụ đi&ecirc;n n&agrave;y, ch&aacute;n sống rồi &agrave;_ T&ecirc;n đ&oacute; tức giận lao l&ecirc;n định đ&aacute;nh Nara<br />\r\n<br />\r\nC&Oacute;P CỐP.... Tay của nh&oacute;c đ&oacute; chưa động đến được người c&ocirc; th&igrave; đ&atilde; bị ăn tiếp 2 c&uacute; v&agrave;o cổ tay<br />\r\n<br />\r\n- Kh&ocirc;ng n&ecirc;n đ&aacute;nh phụ nữ đ&acirc;u_ Nara lắc đầu dạy dỗ<br />\r\n<br />\r\n- Rượu mời kh&ocirc;ng uống lại muốn uống rượu phạt &agrave;, tụi b&acirc;y mau cho bả biết thế n&agrave;o l&agrave; lễ độ đi_ T&ecirc;n cầm đầu ra l&ecirc;n cho 4 thằng đ&agrave;n em đằng sau<br />\r\n<br />\r\n- V&acirc;ng, đại ca<br />\r\n<br />\r\nBỐP..BỐP...RẦM...RẦM....<br />\r\n<br />\r\n- Ch&agrave;, mấy đứa l&agrave;m vậy chị buồn đ&oacute;. N&agrave;o nh&oacute;c, l&uacute;c n&atilde;y nh&oacute;c mới n&oacute;i g&igrave; ấy nhở?_ Nara với chiều cao 1m75 ngang h&agrave;ng với cả t&ecirc;n cầm đầu kho&aacute;c v&aacute;i hắn m&uacute;a m&uacute;a c&aacute;i mu&ocirc;i trong tay. T&ecirc;n nh&oacute;c đ&oacute; chưa hết b&agrave;ng ho&agrave;ng nh&igrave;n đ&aacute;m đ&agrave;n em của m&igrave;nh bị b&agrave; c&ocirc; n&agrave;y d&ugrave;ng mu&ocirc;i đ&aacute;nh tới mức nhấc tay kh&ocirc;ng nổi<br />\r\n<br />\r\n- &Agrave;..&agrave;..Kh&ocirc;ng... Em đ&acirc;u c&oacute; n&oacute;i g&igrave; đ&acirc;u ạ, em đến để ăn mỳ m&agrave;_ Hắn kh&eacute;p n&eacute;p<br />\r\n<br />\r\n- ồ thế &agrave;, chắc chị nghe lầm rồi. Với lại, gọi bằng b&agrave; c&ocirc; l&agrave; kh&ocirc;ng tốt đ&acirc;u_ Nara đưa c&aacute;i mu&ocirc;i lại gần tay t&ecirc;n nh&oacute;c<br />\r\n<br />\r\n- Hơ hơ, em nhớ rồi ạ_ Hắn đổ mồ h&ocirc;i lạnh<br />\r\n<br />\r\n- Vậy mấy đứa muốn ăn g&igrave; n&agrave;o?_ Nara nở nụ cười như l&uacute;c ban đầu<br />\r\n<br />\r\n- M...Mỳ soba ạ_ Cả bọn lắp bắp<br />\r\n<br />\r\n- Được rồi, 5 t&ocirc; soba b&aacute;c Fuji_ Nara<br />\r\n<br />\r\n- C&oacute; liền đ&acirc;y_ B&aacute;c Fuji<br />\r\n<br />\r\nCảnh tượng 1 đ&aacute;m thanh ni&ecirc;n cấp 3 ngồi kh&eacute;p n&eacute;p ăn mỳ, tay run cầm kh&ocirc;ng nổi đũa, kh&ocirc;ng biết l&agrave;m sao ăn t&ocirc; mỳ trước mặt với c&aacute;i tay sưng v&ugrave; thật sự v&ocirc; c&ugrave;ng giải tr&iacute;.<br />\r\n<br />\r\n- Mấy đứa đ&oacute;, lo ăn rồi c&ograve;n về học h&agrave;nh cho đ&agrave;ng ho&agrave;ng đi_ Nara<br />\r\n<br />\r\n- V&acirc;ng thưa chị_ Cả lũ c&uacute;i đầu<br />\r\n<br />\r\n- Nếu muồn th&igrave; cứ đến đ&acirc;y ăn mỳ, nhớ đừng qu&ecirc;n quy tắc đấy_ Nara<br />\r\n<br />\r\n- V&acirc;nggg<br />\r\n<br />\r\nNara trước giờ kh&ocirc;ng hề ngại mấy chuyện n&agrave;y, l&uacute;c vừa mới đến đ&acirc;y cũng kh&ocirc;ng &iacute;t t&ecirc;n đến quấy rối, nhưng kết cục th&igrave; chỉ c&oacute; một. Ở tiệm mỳ n&agrave;y c&oacute; 1 quy tắc đặc biết, cho dừ người đến c&oacute; người thường, quan chức, x&atilde; hội đen hay tội phạm, qu&aacute;n đều sẽ tiếp đ&atilde;i. Tuy nhi&ecirc;n, kh&ocirc;ng được ph&eacute;p g&acirc;y sự trong qu&aacute;n, nếu c&oacute; đ&aacute;nh người th&igrave; chỉ c&oacute; một m&igrave;nh Nara được ph&eacute;p đ&aacute;nh th&ocirc;i.<br />\r\n<br />\r\nTiệm mỳ Natsukashii mở cửa từ 5h đến 12 đ&ecirc;m. Trời c&agrave;ng khuya kh&aacute;ch cũng vắng dần, chỉ c&ograve;n v&agrave;i người tăng ca về trễ. Đ&ocirc;i khi c&oacute; v&agrave;i vị kh&aacute;ch đặc biệt gh&eacute; qua. 12h, thu dọn bảng hiệu, tắt đ&egrave;n. Một ng&agrave;y ở Natsukashii kh&eacute;p lại.</h2>', 0, 'chuong-1-ngay-o-tiem-my-natsukashii'),
(28, 16, 'Chương 2: KHÁCH NGÀY MƯA', 'Chương 2: KHÁCH NGÀY MƯA', '<h2>- H&ocirc;m nay mưa to qu&aacute; nhỉ?_ Nara nh&igrave;n ra ngo&agrave;i trời, mưa như tr&uacute;t nước<br />\r\n<br />\r\n- Phải, h&ocirc;m nay qu&aacute;n cũng vắng kh&aacute;ch hẳn_ B&aacute;c Fuji<br />\r\n<br />\r\n- H&ocirc;m nay b&aacute;c về nghỉ ngơi sớm đi, trời mưa c&uacute;ng chẳng mấy ai ra ngo&agrave;i ăn mỳ đ&acirc;u. Ở tiệm để một m&igrave;nh con l&agrave; được_ Nara<br />\r\n<br />\r\n- Vậy cũng được, thế b&aacute;c về trước đ&acirc;y_ B&aacute;c Fuji<br />\r\n<br />\r\n- Tranh thủ l&uacute;c trời đang mưa nhỏ lại b&aacute;c mau về đi. B&aacute;c đi đường cẩn thận_ Nara nhắc nhở<br />\r\n<br />\r\n- B&aacute;c biết rồi_ B&aacute;c Fuji n&oacute;i rồi mở &ocirc; đi về<br />\r\n<br />\r\n- Ch&agrave;, mưa to thế n&agrave;y kh&ocirc;ng biết c&ograve;n người kh&aacute;ch n&agrave;o kh&ocirc;ng?_ Nara than thở. Nhưng m&agrave; c&ocirc; đ&atilde; tự qui định rằng đ&uacute;ng 12h mới đ&oacute;ng cửa, th&ocirc;i th&igrave; ngồi chơi kh&ocirc;ng vậy.<br />\r\n<br />\r\nMới tạnh một t&iacute; m&agrave; đ&atilde; mưa to lại rồi. Chăc s&ocirc;ng trời đang n&oacute;i lời tạm biệt với m&ugrave;a h&egrave; đ&acirc;y m&agrave;. B&acirc;y giờ m&agrave; c&oacute; người uống rượu c&ugrave;ng vui phải biết.<br />\r\n<br />\r\nTầm 10h30, mưa bắt đầu tạnh dần, chữ c&ograve;n lất phất v&agrave;i hạt. Mưa to l&agrave;m kh&ocirc;ng kh&iacute; lạnh th&ecirc;m, Nara với tay lấy chiếc &aacute;o kho&aacute;c gi&oacute; rồi bước ra cửa cảm nhận c&aacute;i lạnh về đ&ecirc;m.<br />\r\n<br />\r\nTrong l&agrave;n mưa lất phất ấy, xuất hiện b&oacute;ng người. L&agrave; một người đ&agrave;n &ocirc;ng. Anh ta kh&ocirc;ng c&oacute; đem theo d&ugrave;, m&agrave; tr&ocirc;ng cũng chẳng c&oacute; g&igrave; l&agrave; gấp g&aacute;p tr&uacute; mưa cả. Cứ như l&agrave; anh ta đang tận hưởng cơn mưa n&agrave;y vậy.<br />\r\n<br />\r\n- Quyết định vậy đi, đ&oacute; l&agrave; vị kh&aacute;ch cuối c&ugrave;ng ng&agrave;y h&ocirc;m nay_ Nara n&oacute;i<br />\r\n<br />\r\n- Anh g&igrave; đ&oacute; ơi, anh c&oacute; muốn l&agrave;m một b&aacute;t ramen n&oacute;ng hối kh&ocirc;ng?_ Nara gọi ngưởi đ&oacute; khi anh ta đi nganh qua cửa tiệm<br />\r\n<br />\r\n- Anh n&ecirc;n v&agrave;o nghỉ 1 t&iacute; đi, ướt mưa dễ bị cảm lắm đ&oacute;_ Thấy người đ&agrave;n &ocirc;ng đ&oacute; kh&ocirc;ng trả lời, Nara n&oacute;i tiếp. Người đ&oacute; chần chừ 1 l&aacute;t rồi cũng bước v&agrave;o tiệm<br />\r\n<br />\r\n- Vậy anh d&ugrave;ng 1 b&aacute;t ramen nh&eacute;_ Nara cười tươi_ C&oacute; muốn th&ecirc;m rượu kh&ocirc;ng?<br />\r\n<br />\r\n- 1 ly l&agrave; được rồi_ Người đ&oacute; cởi bỏ &aacute;o kho&aacute;c ngo&agrave;i<br />\r\n<br />\r\n- C&oacute; liền đ&acirc;y_ Nara quay người chuẩn bị m&oacute;n ăn, trong suốt thời gian đ&oacute; 2 người kh&ocirc;ng n&oacute;i th&ecirc;m c&acirc;u n&agrave;o<br />\r\n<br />\r\n- Của anh đ&acirc;y. Ch&uacute;c ngon miệng_ Nara đặt b&aacute;t m&igrave; nghi ng&uacute;t kh&oacute;i l&ecirc;n b&agrave;n. Người đ&agrave;n &ocirc;ng ăn một c&aacute;ch từ tốn, c&ograve;n Nara bắt đầu dọn dẹp qu&aacute;n<br />\r\n<br />\r\n- Mưa to thế n&agrave;y m&agrave; c&ocirc; vẫn mở qu&aacute;n khuya thế &agrave;?_ Người đ&oacute; mở lời<br />\r\n<br />\r\n- &Agrave; v&acirc;ng, tiệm m&igrave; của t&ocirc;i đ&uacute;ng 12h mới đ&oacute;ng cửa_ Nara trả lời<br />\r\n<br />\r\n- Tiệm n&agrave;y chỉ c&oacute; m&igrave;nh c&ocirc;, mở khuya vậy lỡ c&oacute; chuyện g&igrave; th&igrave; sao?_ Người đ&oacute;<br />\r\n<br />\r\n- Sẽ kh&ocirc;ng c&oacute; chuyện g&igrave; đ&acirc;u_ Nara cười<br />\r\n<br />\r\n- Sao c&ocirc; biết đuộc?_ Người đ&agrave;n &ocirc;ng<br />\r\n<br />\r\n- T&ocirc;i tin l&agrave; như vậy_ Nara<br />\r\n<br />\r\n- Tr&ocirc;ng c&ocirc; c&oacute; vẻ lạc quan nhỉ?_ Người đ&oacute;<br />\r\n<br />\r\n- Haha, ai cũng bảo t&ocirc;i vậy_ Nara_ V&igrave; sao anh lại để người ướt mưa vậy?_ Nara hoi<br />\r\n<br />\r\n- T&ocirc;i c&oacute; việc gần đ&acirc;y nhưng kh&ocirc;ng mang theo d&ugrave;_ Người đ&agrave;n &ocirc;ng<br />\r\n<br />\r\n- &Agrave;, t&ocirc;i hiểu rồi. Anh cảm thấy mỳ của qu&aacute;n t&ocirc;i như thế n&agrave;o?_ Nara<br />\r\n<br />\r\n- Kh&ocirc;ng tệ_ Người đ&agrave;n &ocirc;ng g&aacute;c đũa<br />\r\n<br />\r\n- Cảm ơn anh_ Nara thu dọn b&aacute;t đũa<br />\r\n<br />\r\n- Hết bao nhi&ecirc;u?_ Người đ&agrave;n &ocirc;ng r&uacute;t v&iacute;<br />\r\n<br />\r\n- Của anh hết 1000 y&ecirc;n_ Nara<br />\r\n<br />\r\n- Ở đ&acirc;y c&oacute; thanh to&aacute;n bằng thẻ kh&ocirc;ng, h&ocirc;m nay toi kh&ocirc;ng mang theo tiền mặt_ Người đ&agrave;n &ocirc;ng r&uacute;t ra tấm thẻ đen<br />\r\n<br />\r\n- Ngại qu&aacute; chỗ ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; m&aacute;y quẹt_ Nara lắc đầu_ Kh&ocirc;ng sao cả, anh c&oacute; thể trả sau<br />\r\n<br />\r\n- Lỡ như t&ocirc;i kh&ocirc;ng quay lại th&igrave; sao?_ Người đ&agrave;n &ocirc;ng đ&oacute;<br />\r\n<br />\r\n- Chuyện đ&oacute; t&iacute;nh sau. Anh c&oacute; thể cho t&ocirc;i biết t&ecirc;n được kh&ocirc;ng? &Iacute;t ra nếu annh c&oacute; chuồn mất th&igrave; t&ocirc;i vẫn biết được t&ecirc;n anh l&agrave; g&igrave;?_ Nara<br />\r\n<br />\r\n- Nếu t&ocirc;i chuồn mất c&ocirc; sẽ b&aacute;o cảnh s&aacute;t sao?_ Người đ&agrave;n &ocirc;ng cười<br />\r\n<br />\r\n- Kh&ocirc;ng kh&ocirc;ng, t&ocirc;i chỉ muốn biết t&ecirc;n anh th&ocirc;i_ Nara phẩy tay<br />\r\n<br />\r\n- Akashi Takeomi, đ&oacute; l&agrave; t&ecirc;n t&ocirc;i_ Người đ&agrave;n &ocirc;ng suy nghĩ một l&aacute;t rồi n&oacute;i<br />\r\n<br />\r\n- V&acirc;ng, anh Takeomi, t&ocirc;i sẽ ghi nhớ_ Nara<br />\r\n<br />\r\n- C&ocirc; sẽ sớm qu&ecirc;n c&aacute;i t&ecirc;n n&agrave;y th&ocirc;i_ Người đ&agrave;n &ocirc;ng đ&oacute; quay người rời đi<br />\r\n<br />\r\n- Kh&ocirc;ng đ&acirc;u, t&ocirc;i giỏi nhất l&agrave; nhớ t&ecirc;n m&agrave;_ Nara cười tươi_ &Agrave;, anh n&ecirc;n cầm theo d&ugrave; n&agrave;y_ Nara đưa cho Takeomi c&acirc;y d&ugrave;<br />\r\n<br />\r\n- Kh&ocirc;ng cần đ&acirc;u_ Takeomi nh&igrave;n chiếc &ocirc;<br />\r\n<br />\r\n- Anh cứ cầm đi, d&ugrave; sao trời cũng đang mưa_ Nara<br />\r\n<br />\r\n- Vậy cảm ơn c&ocirc;_ Takeomi cầm lấy chiếc &ocirc; rồi rời đi</h2>', 0, 'chuong-2-khach-ngay-mua'),
(29, 17, 'Chương 1: Song tinh', 'Chương 1: Song tinh', '<h2>&lsquo;&rsquo;B&aacute;c sĩ Lưu, ph&iacute;a ngo&agrave;i c&oacute; việc cần t&igrave;m anh&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Kh&ocirc;ng c&oacute; hẹn trước&rsquo;&rsquo;<br />\r\n<br />\r\nLưu Nguy&ecirc;n, một b&aacute;c sĩ chữa trị, cũng l&agrave; người hướng dẫn tốt nhất tại Tarifa, anh ngay cả đầu cũng kh&ocirc;ng ngẩng l&ecirc;n m&agrave; chỉ nh&agrave;n nhạt thốt ra mấy chữ.<br />\r\n<br />\r\n&lsquo;&rsquo;Hẹn trước c&aacute;i quỷ ấy, Lưu Nguy&ecirc;n, cậu c&oacute; c&ograve;n nhớ t&ocirc;i l&agrave; cấp tr&ecirc;n của cậu hay kh&ocirc;ng? T&ocirc;i kh&ocirc;ng phải l&agrave; l&iacute;nh g&aacute;c sai vặt đứng trước cửa ph&ograve;ng của cậu đ&acirc;u&rsquo;&rsquo;. Người vừa bước v&agrave;o trợn mắt l&ecirc;n giận dữ.<br />\r\n<br />\r\n&lsquo;&rsquo;Ồ&hellip;ra l&agrave; &ocirc;ng, b&ecirc;n ngo&agrave;i c&oacute; việc? N&oacute;i qua t&igrave;nh h&igrave;nh cho t&ocirc;i nghe trước một ch&uacute;t.&rsquo;&rsquo; Lưu Nguy&ecirc;n cuối c&ugrave;ng cũng ngước mắt l&ecirc;n nh&igrave;n qua Bộ trưởng Bộ Y tế.<br />\r\n<br />\r\n&lsquo;&rsquo;B&ecirc;n ngo&agrave;i Th&aacute;p Gang c&oacute; một L&iacute;nh G&aacute;c đang thức tỉnh&hellip;&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Thức tỉnh l&agrave; vấn đề của ph&iacute;a chi&ecirc;u sinh, li&ecirc;n quan g&igrave; tới Bộ Y tế của ch&uacute;ng ta?&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Đ&uacute;ng l&agrave; kh&ocirc;ng li&ecirc;n quan tới Bộ Y tế, nhưng họ cần một hướng dẫn. B&ecirc;n chi&ecirc;u sinh kh&ocirc;ng thể kiểm so&aacute;t được người, đứa trẻ kia rất c&oacute; thể sẽ cuồng h&oacute;a&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;&Yacute; của &ocirc;ng l&agrave; c&aacute;c L&iacute;nh G&aacute;c đ&atilde; được đ&agrave;o tạo kh&ocirc;ng thể đ&aacute;nh lại được một đứa nh&oacute;c vừa mới thức tỉnh? Mấy t&ecirc;n hướng dẫn tại hiện trường th&igrave; sao?&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Đi rồi, đều đ&atilde; đi hết rồi, nhưng vẫn kh&ocirc;ng thể đột ph&aacute; được l&aacute; chắn tinh thần.&rsquo;&rsquo;<br />\r\n<br />\r\n<br />\r\n<br />\r\n&lsquo;&rsquo;Hờ, qu&aacute; đ&aacute;ng thương cho gi&aacute; trị vũ lực k&eacute;m cỏi của t&ocirc;i, nếu xảy ra sự cố Tarifa c&oacute; lo ma chay kh&ocirc;ng?&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Gi&aacute; trị vũ lực k&eacute;m...T&ocirc;i kh&ocirc;ng rảnh để n&oacute;i nhảm với anh. T&ocirc;i biết anh kh&ocirc;ng vừa mắt với mấy người ở tr&ecirc;n kia nhưng tốt xấu g&igrave; cũng xem qua h&igrave;nh ảnh truyền tới chứ, khu trung t&acirc;m sẽ xảy ra chuyện đ&oacute;.&rsquo;&rsquo;<br />\r\n<br />\r\nLưu Nguy&ecirc;n liếc nh&igrave;n m&agrave;n h&igrave;nh: &lsquo;&rsquo;Nhỏ thế n&agrave;y l&agrave; mới đang học trung học? M&aacute;i t&oacute;c v&agrave;ng đ&oacute;&hellip;Thế n&agrave;y, n&agrave;y? C&oacute; thể đ&aacute;nh được kh&ocirc;ng? Sao lại đẹp trai như vậy? Một chọi năm, wow. Hey hey thế n&agrave;y kh&ocirc;ng được, xung quanh đ&oacute; nhiều người lắm, sẽ xảy ra chuyện mất.&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Đi đ&acirc;y, nhớ gửi định vị cho t&ocirc;i.&rsquo;&rsquo; Lưu Nguy&ecirc;n mỉm cười với cấp tr&ecirc;n của m&igrave;nh, n&eacute;m &aacute;o blouse trắng xuống rồi cứ thế ti&ecirc;u s&aacute;i sải ch&acirc;n d&agrave;i bước ra ngo&agrave;i.<br />\r\n<br />\r\n&lsquo;&rsquo;Rốt cuộc th&igrave; t&ocirc;i l&agrave; cấp tr&ecirc;n hay anh l&agrave; cấp tr&ecirc;n?&rsquo;&rsquo;<br />\r\n<br />\r\nĐến hiện trường, Lưu Nguy&ecirc;n cau m&agrave;y nh&igrave;n đống đổ n&aacute;t trước mặt. Xung quanh đ&oacute; c&aacute;c đồng nghiệp đang tuyệt vọng x&acirc;y dựng một r&agrave;o cản tinh thần để khống chế cục diện.<br />\r\n<br />\r\n&lsquo;&rsquo;N&oacute; c&oacute; t&aacute;c dụng kh&ocirc;ng?&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;Anh đ&atilde; tới rồi?&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;B&aacute;c sĩ Lưu, h&atilde;y v&agrave;o th&aacute;p từ ph&iacute;a ngo&agrave;i, v&igrave; kh&ocirc;ng thể th&agrave;nh lập được li&ecirc;n kết cho n&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng d&aacute;m v&agrave;o. Q&uacute;a nguy hiểm&rsquo;&rsquo;<br />\r\n<br />\r\nLưu Nguy&ecirc;n kh&ocirc;ng v&agrave;o th&aacute;p, anh lặng lẽ thả ra thể tinh thần của m&igrave;nh, sương m&ugrave; m&agrave;u trắng dần ngưng tụ th&agrave;nh h&igrave;nh d&aacute;ng một con b&aacute;o, n&oacute; nhảy l&ecirc;n khoảng ba m&eacute;t rồi nhẹ nh&agrave;ng đ&aacute;p xuống m&eacute;p cửa sổ của tầng mục ti&ecirc;u. Ph&iacute;a b&ecirc;n trong, đ&aacute;m L&iacute;nh G&aacute;c nằm ngang dọc khắp nơi, chỉ c&oacute; một cậu b&eacute; vẫn đang đứng ở đ&oacute;, b&ecirc;n cạnh l&agrave; một ch&uacute; sư tử h&ugrave;ng dũng oai vệ, kh&iacute; ph&aacute;ch hi&ecirc;n ngang.<br />\r\n<br />\r\n<br />\r\n<br />\r\nSư tử nh&igrave;n thấy anh th&igrave; lao về ph&iacute;a cửa sổ nhưng lại bị bật lại như va phải một bức tường trong suốt, n&oacute; l&ugrave;i lại hai bước, kh&ocirc;ng cam l&ograve;ng muốn lao tới lần nữa.<br />\r\n<br />\r\nKh&ocirc;ng thể tr&igrave; ho&atilde;n qu&aacute; l&acirc;u, cần phải tốc chiến tốc thắng mới được, trong đầu Lưu Nguy&ecirc;n suy nghĩ kh&ocirc;ng biết mấy bạn nhỏ trung học b&acirc;y giờ th&iacute;ch c&aacute;i g&igrave; đ&acirc;y.<br />\r\n<br />\r\n&lsquo;&rsquo;N&agrave;y&hellip;sư tử nhỏ, bĩnh tĩnh, em t&ecirc;n l&agrave; g&igrave;?&rsquo;&rsquo;<br />\r\n<br />\r\n&lsquo;&rsquo;C&uacute;t đi!&rsquo;&rsquo;<br />\r\n<br />\r\nLưu Nguy&ecirc;n cảm thấy sự phản kh&aacute;ng mạnh mẽ v&agrave; những cảm x&uacute;c kh&ocirc;ng c&oacute; nguồn gốc hết sức hỗn loạn. Anh cảm thấy trường hợp n&agrave;y kh&aacute; phức tạp nhưng vẫn giữ nụ cười m&agrave; cố gắng tr&ograve; truyện bằng &yacute; thức với cậu thiếu ni&ecirc;n.<br />\r\n<br />\r\n&lsquo;&rsquo;T&ocirc;i l&agrave; Lưu Nguy&ecirc;n. Em ngừng lại trước đ&atilde;, tối nay khai hắc(1) gọi em được chứ? Em c&oacute; muốn c&ugrave;ng chơi với t&ocirc;i kh&ocirc;ng?&rsquo;&rsquo;<br />\r\n<br />\r\nTiếng k&iacute;nh cửa sổ vỡ tan cuối c&ugrave;ng cũng dừng lại.<br />\r\n<br />\r\nLưu Nguy&ecirc;n cảm nhận được r&agrave;o cản tinh thần của đối phương trong ph&uacute;t chốc xuất hiện kẽ hở, anh nh&acirc;n cơ hội n&agrave;y nhanh ch&oacute;ng giải ph&oacute;ng thể tinh thần của m&igrave;nh, cố gắng x&oacute;a dịu sự cuồng nộ mạnh mẽ của cậu nh&oacute;c.<br />\r\n<br />\r\nĐ&uacute;ng thế, những người b&igrave;nh thường lu&ocirc;n nghĩ rằng c&aacute;c L&iacute;nh G&aacute;c trời sinh ra đ&atilde; hiếu chiến bạo lực n&ecirc;n chẳng ai đi t&igrave;m hiểu cặn kẽ nguy&ecirc;n nh&acirc;n. L&iacute;nh G&aacute;c c&oacute; được năm loại gi&aacute;c quan si&ecirc;u nhạy b&eacute;n, l&uacute;c n&agrave;o cũng trong trạng th&aacute;i tiếp nhận một lượng lớn th&ocirc;ng tin đi&ecirc;n cuồng tới từ mọi ph&iacute;a, c&oacute; thể kh&ocirc;ng phong bế bản th&acirc;n đ&atilde; l&agrave; rất lợi hại rồi. Thế nhưng ai m&agrave; kh&ocirc;ng từng l&agrave; một đứa trẻ chưa từng trải qua huấn luyện. T&acirc;m l&yacute; của L&iacute;nh G&aacute;c t&igrave;nh cờ l&agrave; chủ đề nghi&ecirc;n cứu của Lưu Nguy&ecirc;n trong hai năm qua, đối với anh đề t&agrave;i n&agrave;y cực kỳ c&oacute; sức h&uacute;t.<br />\r\n<br />\r\nTheo t&ocirc;i đi, nơi n&agrave;y c&oacute; &aacute;nh s&aacute;ng.</h2>', 0, 'chuong-1-song-tinh'),
(30, 17, 'Chương 2: Song tinh', 'Chương 2: Song tinh', '<h2>&quot;N&agrave;y l&agrave; đệ v&agrave;i lần. . . Lưu Nguy&ecirc;n, ngươi đi xử để &yacute; một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;Lại thế n&agrave;o? Gần đ&acirc;y ngươi ba ng&agrave;y lưỡng đầu lại đ&acirc;y, đều phải tưởng ngươi &aacute;m luyến ta, kh&ocirc;ng phải đ&atilde; n&oacute;i mạ, kh&ocirc;ng ước.&quot;<br />\r\n<br />\r\nLưu Nguy&ecirc;n hạ &yacute; thức chuyển chuyển trong tay&quot; b&uacute;t&quot;, c&aacute;i gần như muốn tuyệt t&iacute;ch đ&ocirc;ng t&acirc;y, ngo&agrave;i &yacute; muốn c&oacute; thể bang trợ hắn tự hỏi.<br />\r\n<br />\r\n&quot;Lại kh&ocirc;ng ước, ngươi thế n&agrave;o kh&ocirc;ng thay đổi t&ecirc;n l&agrave; tiếu kh&ocirc;ng ước a, kh&ocirc;ng phải, kh&ocirc;ng phải lai với ngươi giảng n&agrave;y, nh&agrave; ngươi c&aacute;i tiểu bằng hữu a, kh&oacute;a thượng vừa l&ecirc;n lại đ&atilde; xảy ra chuyện.&quot; Y liệu bộ trưởng gần đ&acirc;y thực l&agrave;m phức tạp, hắn cũng kh&ocirc;ng muốn ba ng&agrave;y lưỡng đầu xuất hiện tại Lưu Nguy&ecirc;n n&agrave;y tiểu ph&aacute; căn ph&ograve;ng, nếu kh&ocirc;ng bị thuộc hạ o&aacute;n giận phiền, n&agrave;y tồi hắn cũng kh&ocirc;ng muốn quản hảo mạ.<br />\r\n<br />\r\nLưu Nguy&ecirc;n cười khổ một ch&uacute;t, tiểu bằng hữu kh&ocirc;ng phải nh&agrave; hắn, nhưng lai hắn n&agrave;y b&aacute;o danh tần dẫn đ&atilde; l&agrave; một lễ b&aacute;i một lần, hắn bản chức tinh thần chải vuốt sợi, kh&ocirc;ng phải chuy&ecirc;n chức t&acirc;m phụ, nhưng học năm đều phải qu&aacute; xong rồi, t&acirc;m phụ đối tiểu bằng hữu tựa hồ th&uacute;c thủ v&ocirc; s&aacute;ch, cấp hắn b&igrave;nh trắc tao hắn nhịn kh&ocirc;ng được muốn nh&uacute;ng tay, xuống lần nữa đi tiểu bằng hữu cũng bị lui học, nhưng l&agrave; lui học &yacute; tứ h&agrave;m x&uacute;c trứ chuyện g&igrave;. . . Lưu Nguy&ecirc;n l&agrave; r&otilde; r&agrave;ng, hơn nữa tiểu bằng hữu tinh thần lực l&agrave; hi hữu S cấp, kh&ocirc;ng thể d&ugrave;ng, liền hủy rụng, th&aacute;p l&yacute; lu&ocirc;n lu&ocirc;n l&agrave; như vậy xử để &yacute;.<br />\r\n<br />\r\nHắn bắt đầu c&oacute; ch&uacute;t hậu hối, khi ấy c&oacute; phải hay kh&ocirc;ng l&agrave;m c&aacute; sai lầm quyết định.<br />\r\n<br />\r\nT&acirc;m phụ b&ecirc;n kia c&aacute;o trạng nhưng nhiều, thuyết tiểu bằng hữu kh&ocirc;ng phối hợp, vấn chuyện g&igrave; cũng kh&ocirc;ng thuyết, tinh thần li&ecirc;n kết th&agrave;nh lập kh&ocirc;ng được, một lời kh&ocirc;ng hợp liền ra tay, c&ugrave;ng s&agrave;i cương bắt đầu c&ograve;n c&oacute; người chọn hấn t&igrave;m c&aacute;i đ&aacute;nh, hiện tại c&aacute; c&aacute; n&eacute; c&ograve;n kh&ocirc;ng kịp, h&ocirc;m nay kh&ocirc;ng biết ai lại đi t&igrave;m ma phiền.<br />\r\n<br />\r\nTinh thần li&ecirc;n kết th&agrave;nh lập kh&ocirc;ng được nan đạo tr&aacute;ch hắn mạ, hắn cũng thực khổ n&atilde;o, mỗi hồi bị đưa lai, tiểu bằng hữu gần như cũng kh&ocirc;ng n&oacute;i chuyện, cũng kh&ocirc;ng rất tưởng th&iacute;nh người n&oacute;i chuyện, thế n&agrave;o cũng phải hắn xuất ra du h&iacute; bắt đầu đ&aacute;nh, mới hội thi&ecirc;n đầu nh&igrave;n ch&iacute;nh m&igrave;nh liếc mắt một c&aacute;i, l&uacute;c n&agrave;y hậu tinh thần c&aacute;i chắn mới hội yếu đuối hạ lai, hắn c&oacute; thứ th&agrave;nh c&ocirc;ng c&ocirc;ng v&agrave;o thuận trứ li&ecirc;n kết sờ tiến tiểu bằng hữu tinh thần đồ cảnh l&yacute;, ngo&agrave;i &yacute; muốn ph&aacute;t hiện, b&ecirc;n trong ch&iacute;nh l&agrave; một mảnh tối đen, chuyện g&igrave; đều kh&ocirc;ng c&oacute;, thật sự, một v&ocirc; sở hữu.<br />\r\n<br />\r\nLi&ecirc;n hắn đều kh&ocirc;ng c&aacute;ch n&agrave;o tại b&ecirc;n trong nghỉ ngơi bao l&acirc;u, so với hắn tưởng ho&agrave;n kh&oacute; giải quyết.<br />\r\n<br />\r\n&quot;Ta đ&atilde; biết, nhượng hắn lại đ&acirc;y đi.&quot;<br />\r\n<br />\r\n&quot;Dương Kiệt.&quot;<br />\r\n<br />\r\nThiếu ni&ecirc;n &aacute;nh mắt lạnh l&ugrave;ng, mạo T k&eacute;o cao c&aacute;i trứ đầu, im lặng chỉ lộ ra &aacute;nh mắt nh&igrave;n hắn, t&igrave;nh cảm hảo l&agrave; đi ở thuỷ triều mũi nhọn v&otilde;ng ẩn thiếu ni&ecirc;n.<br />\r\n<br />\r\nLưu Nguy&ecirc;n sự trước hiểu biết sự t&igrave;nh đại kh&aacute;i trải qua, bọn hắn h&ocirc;m nay thực chiến diễn luyện, ph&iacute;a trước đều hảo hảo, kỹ năng ph&acirc;n gần như m&atilde;n ph&acirc;n, khước tại biến dị sinh vật kia một quan liền tạp trụ, hắn một người đ&atilde;i tại g&oacute;c, tử cũng kh&ocirc;ng động, b&igrave;nh trắc huấn luyện vi&ecirc;n nhượng khu&ocirc;ng nghĩ kh&iacute; trực tiếp h&igrave;nh chiếu đến trước mặt hắn, trong nh&aacute;y mắt hắn liền biến mất tại nguy&ecirc;n xử, trực tiếp t&igrave;m được khu&ocirc;ng nghĩ kh&iacute; đem đ&ocirc;ng t&acirc;y<br />\r\n<br />\r\nCấp tạp.<br />\r\n<br />\r\nHảo a, n&agrave;y &yacute; nghĩ đơn giản th&ocirc; bạo lại th&ocirc;ng minh, Lưu Nguy&ecirc;n vẫn thực h&acirc;n thưởng, tiểu bằng hữu bản th&acirc;n vấn đề hắn ho&agrave;n đang t&igrave;m, n&agrave;y x&atilde; hội vấn đề hắn lại giải quyết kh&ocirc;ng được, hắn Lưu Nguy&ecirc;n ch&iacute;nh l&agrave; c&aacute; l&yacute; ngoại kh&ocirc;ng phải người c&acirc;u th&ocirc;ng kiều lương.<br />\r\n<br />\r\n<br />\r\n<br />\r\n&quot;Ta cũng kh&ocirc;ng muốn niệm ngươi, lai ta n&agrave;y vậy nhiều lần, ngươi chỉ muốn đ&aacute;nh nhau du h&iacute;? Kh&ocirc;ng theo ta t&acirc;m sự?&quot;<br />\r\n<br />\r\nLưu Nguy&ecirc;n phản kh&oacute;a tại ghế tr&ecirc;n, một c&aacute; ch&iacute;nh h&igrave;nh, trường ch&acirc;n nho&aacute;ng l&ecirc;n một c&aacute;i nho&aacute;ng l&ecirc;n một c&aacute;i đẩy trứ tay vịn y chuyển quyển quyển.<br />\r\n<br />\r\n&quot;Ch&iacute;nh l&agrave; ta h&ocirc;m nay kh&ocirc;ng nghĩ đ&aacute;nh du h&iacute;, tưởng n&oacute;i chuyện phiếm, được kh&ocirc;ng? .&quot;<br />\r\n<br />\r\nDương Kiệt nghe n&oacute;i nh&igrave;n hắn một c&aacute;i, y&ecirc;n lặng đứng l&ecirc;n.<br />\r\n<br />\r\n&quot;Ai, Dương Kiệt, ngươi hảo đ&aacute;ng gh&eacute;t a, kh&ocirc;ng phải. . . Ch&uacute;ng ta solo, ta thắng ngươi đến theo ta n&oacute;i chuyện phiếm.&quot;<br />\r\n<br />\r\nNguy&ecirc;n tưởng tiểu bằng hữu hội trực tiếp rời khỏi, xuất hồ &yacute; li&ecirc;u, người khước l&agrave; đại lạt lạt ngồi xuống lai, đại da dường như, đứa nhỏ ngươi ho&agrave;n nhớ kỹ ở đ&acirc;y l&agrave; ta m&agrave; b&agrave;n mạ? Lưu Nguy&ecirc;n ph&uacute;c phỉ.<br />\r\n<br />\r\nLưu Nguy&ecirc;n cười kh&ocirc;ng đường chọn lựa, hảo kh&iacute; a vẫn muốn bảo tr&igrave; mỉm cười, hắn dễ d&agrave;ng mạ hắn, solo muốn thắng Dương Kiệt, hắn chỉ sợ đến trước hắc tiến t&yacute; phục kh&iacute;.<br />\r\n<br />\r\n&quot;Ngươi tuyển phụ trợ? Dương Kiệtngươi l&agrave; khinh thường ai đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Đi đi, ta liền đương ngươi tưởng theo ta n&oacute;i chuyện phiếm.&quot;<br />\r\n<br />\r\nKh&ocirc;ng, ngươi cũng kh&ocirc;ng tưởng theo ta n&oacute;i chuyện phiếm, ngươi chỉ tưởng tại tr&ecirc;n người của ta t&igrave;m cảm gi&aacute;c th&agrave;nh tựu.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng đ&aacute;nh nửa, Dương Kiệt, ngươi trở về đi.&quot;<br />\r\n<br />\r\nLưu Nguy&ecirc;n đứng dậy tưởng khai m&ocirc;n tiễn kh&aacute;ch, khước kiến tiểu bằng hữu đối hắn nhếch một c&aacute;i ng&oacute;n trỏ, &quot;Cuối c&ugrave;ng một v&aacute;n.&quot;, a, c&oacute; tiến bước a, tứ c&aacute; tự, giai đoạn t&iacute;nh thắng lợi, t&aacute;i tiếp t&aacute;i lệ tiếu y sư.<br />\r\n<br />\r\nLưu Nguy&ecirc;n tại b&ecirc;n cạnh hắn ngồi xuống, kh&ocirc;ng đường chọn lựa điểm tiếp thu thỉnh mời, s&aacute;ch, tiểu bằng hữu minh hiển tại ph&oacute;ng thủy, tưởng n&oacute;i chuyện phiếm c&oacute; thể thuyết a, chuyện g&igrave; biệt xoay t&acirc;m tư a, Lưu Nguy&ecirc;n thở d&agrave;i.<br />\r\n<br />\r\n&quot;Dương Kiệt? Ngươi c&aacute;n đi?&quot; Mắt thấy huyết lượng kiến hồng, tiểu bằng hữu đột nhi&ecirc;n đứng l&ecirc;n, tiểu sư tử cũng đột nhi&ecirc;n xuất hiện tại hắn b&ecirc;n cạnh, c&aacute;i đu&ocirc;i kh&ocirc;ng ngừng ph&aacute;t trứ s&agrave;n nh&agrave;, hắn thị tuyến theo n&agrave;y một người một sư đồng thời chuyển hướng cửa khẩu.</h2>', 0, 'chuong-2-song-tinh');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(31, 18, 'Chương 1: Viên gia tam công tử', 'Chương 1: Viên gia tam công tử', '<h2>Kiến An năm thứ năm(năm 200 CN), Vi&ecirc;n Thiệu l&uacute;c n&agrave;y đ&atilde; đ&aacute;nh bại C&ocirc;ng T&ocirc;n Toản, nhất thống 4 ch&acirc;u K&yacute;, Thanh, Tịnh, U, tập kết qu&acirc;n linh xu&ocirc;i nam c&ugrave;ng người đang &ldquo;Hiệp thi&ecirc;n tử lệnh chư hầu&rdquo; &ndash; T&agrave;o Th&aacute;o đ&aacute;nh trận Quan Độ. Trong chiến dịch n&agrave;y, song phương kẻ tiến người l&ugrave;i, c&oacute; thắng c&oacute; bại, hầu như đ&atilde; huy động to&agrave;n bộ lực lượng của song h&ugrave;ng. Cứ như vậy giằng co nhau tại chiến trường Quan Độ, k&eacute;o d&agrave;i r&ograve;ng r&atilde; gần hai năm<br />\r\n<br />\r\nSong h&ugrave;ng giao phong khiến đại thế thi&ecirc;n hạ trở n&ecirc;n hỗn loạn kh&ocirc;ng r&otilde;. Nhưng m&agrave; v&agrave;o l&uacute;c n&agrave;y, phụ cận trung qu&acirc;n Vi&ecirc;n Thiệu, trong một t&ograve;a lều vải xa hoa, một biến h&oacute;a lặng lẽ ph&aacute;t sinh.<br />\r\n<br />\r\n&ldquo;Nước&hellip;Cho ta nước&hellip;&rdquo;<br />\r\n<br />\r\nKhi tỉnh lại, Vi&ecirc;n Thượng cảm thấy đầu như bị nổ tung, khốn khổ v&ocirc; c&ugrave;ng.<br />\r\n<br />\r\nM&iacute; mắt Vi&ecirc;n Thượng nặng nề, đầu đau như muốn nứt ra, kh&ocirc;ng thể n&agrave;o mở mắt ra được. Tai hắn chỉ nghe tiếng ong ong, qua một hồi l&acirc;u mới nghe thấy một tr&agrave;ng tiếng n&oacute;i nhỏ.<br />\r\n<br />\r\nMột &acirc;m thanh từ xa vang l&ecirc;n b&ecirc;n tai: &ldquo;Tam đệ ta bệnh t&igrave;nh thế n&agrave;o rồi?&rdquo;<br />\r\n<br />\r\nSau đ&oacute; hắn nghe thấy một giọng n&oacute;i cung kinh trả lời: &ldquo;Nhị c&ocirc;ng tử kh&ocirc;ng cần qu&aacute; lo lắng. Đ&ecirc;m qua Chủ c&ocirc;ng đ&atilde; lệnh qu&acirc;n y trong qu&acirc;n xem qua. Tam c&ocirc;ng tử chỉ l&agrave; do h&agrave;nh qu&acirc;n l&acirc;u ng&agrave;y dẫn đến nhiễm phong h&agrave;n khiến cho th&acirc;n thể mới c&oacute; ch&uacute;t kh&ocirc;ng khỏe. Cũng kh&ocirc;ng đang lo ngại, h&ocirc;m nay ng&agrave;i ấy tỉnh dậy rồi uống một thang dược (nước thuốc) liền ngủ tới b&acirc;y giờ.&rdquo;<br />\r\n<br />\r\nTrong l&uacute;c đ&oacute;, Vi&ecirc;n Thượng nghe xong liền cảm thấy m&ecirc; man&hellip;<br />\r\n<br />\r\nNhiễm phong h&agrave;n? Uống dược thang? Tam c&ocirc;ng tử ?<br />\r\n<br />\r\nĐ&acirc;y l&agrave; đang n&oacute;i ta sao?<br />\r\n<br />\r\nTa nhớ ng&agrave;y h&ocirc;m qua l&agrave; ng&agrave;y kết th&uacute;c một năm đại học, ta c&ugrave;ng c&aacute;c huynh đệ c&ugrave;ng phong uống say một chầu, đến s&aacute;ng th&igrave; loạng choạng đi về nh&agrave;. Tại sao vừa mới tỉnh dậy th&igrave; liền bị ph&aacute;n l&agrave; nhiễm phong h&agrave;n?<br />\r\n<br />\r\nLại n&oacute;i b&acirc;y giờ c&ograve;n c&oacute; người d&ugrave;ng hai từ &ldquo;phong h&agrave;n&rdquo;? Phải n&oacute;i l&agrave; bị cảm mạo chứ.<br />\r\n<br />\r\nChậm r&atilde;i mở mắt ra, đập v&agrave;o mắt hắn l&agrave; một khu&ocirc;n mặt trắng n&otilde;n. Đ&acirc;y l&agrave; n&eacute;t mặt vừa quen thuộc vừa xa lạ, l&agrave; khu&ocirc;n mặt của một nam tử trẻ tuổi!<br />\r\n<br />\r\nNam tử n&agrave;y tuổi chừng hai mươi, buộc t&oacute;c l&ecirc;n cao, t&oacute;c d&agrave;i x&otilde;a tới vai, th&acirc;n mặc một bộ gi&aacute;p trụ, trang phục y chang mấy tướng qu&acirc;n thời cổ đại. C&aacute;i d&aacute;ng dấp quỷ dị n&agrave;y, kh&ocirc;ng biết tại sao lại khiến Vi&ecirc;n Thượng c&oacute; một cảm gi&aacute;c quen thuộc, giống như đ&atilde; gặp ở đ&acirc;u đ&oacute;.<br />\r\n<br />\r\n&ldquo;Tam đệ, ngươi đ&atilde; tỉnh!&rdquo;<br />\r\n<br />\r\nNam tử kia thấy Vi&ecirc;n Thượng trợn mắt, vội v&agrave;ng tiến tới, giơ tay sờ nhẹ tr&aacute;n Vi&ecirc;n Thượng, l&ocirc;ng m&agrave;y hơi nhăn lại n&oacute;i: &ldquo;Vẫn c&ograve;n tốt&hellip;Kh&ocirc;ng c&oacute; n&oacute;ng lắm! Người đ&acirc;u, mau mang nước tới! Kh&ocirc;ng cần qu&aacute; n&oacute;ng, vừa s&ocirc;i l&agrave; được!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng hơi kinh ngạc, nam tử n&agrave;y rốt cuộc l&agrave; ai? Trang phục v&ocirc; c&ugrave;ng qu&aacute;i dị, Hoa Hạ 56 c&aacute;i d&acirc;n tộc từ khi n&agrave;o l&ograve;i ra c&aacute;i loại dị hỡm thế n&agrave;y!<br />\r\n<br />\r\nNgay l&uacute;c đ&oacute;, đột nh&ecirc;n c&oacute; một c&otilde; k&yacute; ức vừa quen thuộc vừa xa lạ nổ tung trong đầu hắn, lượng k&yacute; ức ấy thật sự qu&aacute; lớn. Vi&ecirc;n Thượng hừ một tiếng, mắt nhắm lại, nhất thời lại ngất xỉu.<br />\r\n<br />\r\nNam tử trước mặt Vi&ecirc;n Thượng nhất thời bối rối, dưới t&igrave;nh huống cấp b&aacute;ch giơ tay t&iacute;nh t&aacute;t tỉnh Vi&ecirc;n Thượng, nhưng tay vừa mới giơ lu&ocirc;n hạ xuống. Hoảng loạn một hồi liền hung hăng h&ocirc; lớn: &ldquo;Tam đệ! Ngươi kh&ocirc;ng n&ecirc;n h&ugrave; dọa nhị ca nha. Tam đệ! Người đ&acirc;u&hellip; gọi quan qu&acirc;n y đến, nhanh!&rdquo;<br />\r\n<br />\r\nTrong b&oacute;ng tối, tiềm thức k&yacute; ức cho hắn biết giờ hắn đ&atilde; biến th&agrave;nh một người kh&aacute;c. Người n&agrave;y gọi l&agrave; Vi&ecirc;n Thượng, l&agrave; Tam c&ocirc;ng tử của Đại Tướng quan, K&yacute; Ch&acirc;u mục Vi&ecirc;n Thiệu. Hắn từ nhỏ đ&atilde; c&oacute; cơm ngon &aacute;o đẹp, hưởng thụ vinh h&oacute;a ph&uacute; qu&yacute;, đ&atilde; được định sẵn lớn l&ecirc;n sẽ l&agrave; thi&ecirc;n chi ki&ecirc;u tử, c&oacute; cha mẹ thương y&ecirc;u, c&oacute; người hầu nhất h&ocirc; b&aacute; ứng&hellip; Đ&uacute;ng rồi, c&ograve;n c&oacute; hai người ca ca m&agrave; hắn kh&ocirc;ng hề th&iacute;ch.<br />\r\n<br />\r\nB&acirc;y giờ, ch&iacute;nh m&igrave;nh đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; d&acirc;n ch&uacute;ng b&igrave;nh thường của thế kỷ 21 m&agrave; lại biến th&agrave;nh một con ch&aacute;u thế gia sinh trưởng những năm cuối Đ&ocirc;ng H&aacute;n.<br />\r\n<br />\r\nLẽ n&agrave;o, lẽ n&agrave;o ta xuy&ecirc;n qua? Từ hiện đại xuy&ecirc;n tới cổ đại. Đ&atilde; biến th&agrave;nh một kẻ kh&aacute;c vừa xa lạ vừa quen thuộc&hellip;<br />\r\n<br />\r\nVi&ecirc;n Thượng lần thứ hai mở mắt, nh&igrave;n thấy b&ecirc;n giường l&agrave; một nam tử trẻ tuổi bộ mặt hiện vẻ lo lắng, yết hầu kh&ocirc; khốc. Căn cứ theo k&yacute; ức vừa k&eacute;o tới, theo bản năng thấp giọng gọi một tiếng: &ldquo;Nhị ca?&rdquo;<br />\r\n<br />\r\nK&yacute; ức vừa dung hợp nhắc nhở hắn, người n&agrave;y l&agrave; huynh trưởng thứ hai của m&igrave;nh &ndash; Vi&ecirc;n Hi.<br />\r\n<br />\r\nVi&ecirc;n Hi nghe gọi một tiếng, quay đầu nh&igrave;n lại, nhất thời vừa mừng vừa sợ. Lần n&agrave;y minh từ U Ch&acirc;u thay phụ th&acirc;n vận chuyển binh giới, chiến m&atilde; tới đại doanh Quan Độ, vốn định thừa cơ hội tốt n&agrave;y c&ugrave;ng tiểu đệ l&acirc;u ng&agrave;y kh&ocirc;ng gặp uống v&agrave;i ch&eacute;n. Kh&ocirc;ng ngờ răng mới tới đ&acirc;y lại biết hắn nhiễm phong h&agrave;n, liền v&ocirc;i v&agrave;ng tới thăm. Kết quả kh&ocirc;ng tới th&igrave; kh&ocirc;ng sao, vừa tới thăm liền thấy đệ đ&ecirc; vừa tỉnh lại ngất xỉu khiến cho hắn bị dọa đến hoảng sợ.<br />\r\n<br />\r\n&ldquo;Tam đệ, ngươi như vậy l&agrave; dọa vi huynh đấy!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi xoa mồ h&ocirc;i tr&ecirc;n tr&aacute;n, chạy tới b&ecirc;n giường, nh&igrave;n kh&iacute; sắc tr&ecirc;n mặt Vi&ecirc;n Thượng, n&oacute;i: &ldquo;Cảm thấy sao rồi? C&oacute; cảm thấy muốn bất tỉnh nữa kh&ocirc;ng? Vi huynh đ&atilde; gọi người đi t&igrave;m qu&acirc;n y tới đ&acirc;y để hắn hảo hảo trị liệu cho ngươi!&rdquo;<br />\r\n<br />\r\n&ldquo;Nhị ca, kh&ocirc;ng n&ecirc;n phiền to&aacute;i. Tiểu bệnh th&ocirc;i ma, kh&ocirc;ng c&oacute; g&igrave; trở ngại đ&acirc;u.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng suy yếu nở nụ cười. Tuy rằng kh&ocirc;ng biết ch&iacute;nh m&igrave;nh tột c&ugrave;ng l&agrave; bị bệnh g&igrave;, nhưng tinh thần c&ugrave;ng th&acirc;n thể dường như đ&atilde; c&oacute; một ch&uacute;t ăn &yacute;, dần dần dung hợp c&ugrave;ng nhau. Chứ nếu như l&uacute;c trước khi h&ocirc;n m&ecirc; th&igrave; kh&ocirc;ng ổn.<br />\r\n<br />\r\nVi&ecirc;n Hi thấy đệ đệ dường như kh&ocirc;ng phải cố tỏ vẻ, t&acirc;m trạng như đặt được một tảng đ&aacute; lớn, cẩn trọng ngồi xuống m&eacute;p giường: &ldquo;Tam đệ, vi huynh lần n&agrave;y từ U Ch&acirc;u tới Quan Độ vận chuyển binh giới, đ&atilde; phải chịu đựng mấy ng&agrave;y. Vốn hy vọng lần n&agrave;y c&ugrave;ng người uống một bữa, kh&ocirc;ng nghĩ rằng tới đ&acirc;y lại nghe tin ngươi sinh bệnh b&egrave;n vội v&agrave;ng tới thăm&hellip;Ngươi thường ng&agrave;y y&ecirc;u th&iacute;ch đao thương, săn bắn, th&acirc;n thể coi như cường tr&aacute;ng, l&agrave;m sao mới theo qu&acirc;n xuất chinh v&agrave;i ng&agrave;y liền nhận lấy h&agrave;n tật. Kh&ocirc;ng biết c&ocirc;ng phu ng&agrave;y thường của ngươi đi đ&acirc;u a.&rdquo;<br />\r\n<br />\r\nTrong lời n&oacute;i của Vi&ecirc;n Hi, Vi&ecirc;n Thượng cảm thấy c&oacute; g&igrave; đ&oacute; kh&ocirc;ng th&iacute;ch hợp. Nhưng giờ khắc n&agrave;y đầu &oacute;c qu&aacute; mức kh&oacute; chịu, trong nhất thời kh&ocirc;ng suy nghĩ được nhiều như vậy. Miễn cưỡng trả lời: &ldquo;L&agrave;m phiền nhị ca lo lắng&hellip;&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi lắc đầu, gi&uacute;p hắn sửa chăn, cười n&oacute;i: &ldquo;Tiểu tử n&agrave;y, cũng biết nhị ca lo cho ngươi &agrave;. Coi như ngươi c&ograve;n ch&uacute;t lương t&acirc;m. Sự thật l&agrave; phụ th&acirc;n tuy rằng đang ở giữa đại trướng nghị sự nhưng t&acirc;m tư lại đang lo lắng cho ngươi v&ocirc; c&ugrave;ng.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng nghe vậy liền bừng tỉnh, nhếch m&ocirc;i cười n&oacute;i: &ldquo;Phụ th&acirc;n tuy nhớ nhưng lại kh&ocirc;ng đến thăm, chỉ c&oacute; nhị ca l&agrave; thật sự lo lắng cho ta a&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi nghe vậy cười mắng: &ldquo;Tiểu tử th&uacute;i nhiễm bệnh uống thuốc đắng m&agrave; miệng vẫn rất ngọt nha. Đ&aacute;ng tiếc ta đ&acirc;y kh&ocirc;ng bị c&aacute;i bộ d&aacute;ng của người lừa gạt đ&acirc;u&hellip;. Haha, ngươi cho rằng phụ th&acirc;n kh&ocirc;ng muốn tới thăm ngươi sao? Nếu kh&ocirc;ng phải trong qu&acirc;n xảy ra đại sự, chỉ sợ l&atilde;o nh&acirc;n gia chạy tới c&ograve;n sớm hơn ta. Tiểu tử ngươi chỉ bệnh một trận m&agrave; ảnh hưởng tới kh&ocirc;ng biết bao nhi&ecirc;u tinh thần của ch&uacute;ng ta.&rdquo;<br />\r\n<br />\r\nVừa n&oacute;i xong, qu&acirc;n y lệnh vừa vặn tới xem bệnh cho Vi&ecirc;n Thượng. Vi&ecirc;n Hi đứng dậy tr&aacute;nh chỗ, đứng c&aacute;ch đ&oacute; kh&ocirc;ng xa, vẻ mặt nghi&ecirc;m nghị nh&igrave;n qu&acirc;n y lệnh kia bắt mạch chẩn bệnh.<br />\r\n<br />\r\nVi&ecirc;n Thượng t&ugrave;y &yacute; để y quan kia bắt mạch, nh&igrave;n Vi&ecirc;n Hi n&oacute;i : &ldquo;Nhị ca, ngươi n&oacute;i trong qu&acirc;n vừa xảy ra đại sự? Thật ra l&agrave; c&oacute; chuyện g&igrave;?&rdquo;<br />\r\n<br />\r\n&ldquo;A! Đừng n&oacute;i nữa!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi thở d&agrave;i, giọng c&oacute; ch&uacute;t o&aacute;n giận: &ldquo;Mấy ng&agrave;y trước, Hứa Du &ndash; Hứa Tử Viễn* bởi v&igrave; gia nh&acirc;n ở Nghiệp Thanh lộng quyền ăn hối lộ, bị phụ th&acirc;n tr&aacute;ch cứ v&agrave;i c&acirc;u liền đột nhi&ecirc;n biến mất. Phụ th&acirc;n ph&aacute;i người trong phạm vi mười dặm lục so&aacute;t nhưng kh&ocirc;ng c&oacute; dấu vết. Phụ th&acirc;n nghi ngờ l&atilde;o thất phu ấy v&igrave; phẫn hận trong l&ograve;ng m&agrave; chuyển sang đầu h&agrave;ng T&agrave;o Th&aacute;o. Những việc kh&aacute;c kh&ocirc;ng n&oacute;i, chỉ việc Hứa Du theo phụ th&acirc;n nhiều năm, biết r&otilde; nội t&igrave;nh qu&acirc;n ta đ&atilde; l&agrave; bất lợi to lớn với ch&uacute;ng ta. V&igrave; vậy phụ th&acirc;n triệu tập thủ hạ phụ t&aacute; thương nghị đối s&aacute;ch mấy ng&agrave;y liền.&rdquo;<br />\r\n<br />\r\n&ldquo;Hứa&hellip;Hứa Du đầu T&agrave;o?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng nhất thời như bị dội một g&aacute;o nước lạnh. Kết hợp k&yacute; ức trong đầu c&ugrave;ng lời Vi&ecirc;n Hi vừa kể, trong gi&acirc;y l&aacute;t hiểu tại sao n&atilde;y giờ trong l&ograve;ng cảm thấy bất an.<br />\r\n<br />\r\n&ldquo;Nhị ca&hellip;Chẳng lẽ&hellip;hiện tại ch&uacute;ng ta đang đ&aacute;nh trận Quan Độ?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi nghe vậy liền c&oacute; ch&uacute;t ngạc nhi&ecirc;n, kỳ qu&aacute;i đưa tay sờ tr&aacute;n Vi&ecirc;n Thượng, n&oacute;i : &ldquo;Tạm đệ, ngươi thật kh&ocirc;ng phải mắc bệnh hồ đồ rồi chứ? Qu&acirc;n ta c&ugrave;ng T&agrave;o Th&aacute;o giằng co ở Quan Độ đ&atilde; hơn nửa năm rồi đ&oacute;&rdquo;<br />\r\n<br />\r\nNằm ở tr&ecirc;n giường, Vi&ecirc;n Thượng trong l&ograve;ng cảm thấy hồi hộp. Một cỗ dự cảm kh&ocirc;ng tốt lan khắp người hắn khiến cơ thể hắn cảm thấy r&eacute;t run, đầu ng&agrave;y c&agrave;ng đau như muốn nứt ra.<br />\r\n<br />\r\nCon trai Vi&ecirc;n Thiệu&hellip;.Trận chiến Quan Độ&hellip;Hứa Du đầu T&agrave;o! Shit! Ta thật sống lại thời kỳ quỷ qu&aacute;i n&agrave;y!<br />\r\n<br />\r\nTrong lịch sử, trận chiến Quan Độ nổi tiếng l&agrave; chiến dịch ti&ecirc;u biểu, kinh điển cho việc lấy yếu đ&aacute;nh mạnh. Hoa Bắc hai đại ki&ecirc;u h&ugrave;ng: T&agrave;o Th&aacute;o c&ugrave;ng Vi&ecirc;n Thiệu, v&igrave; tranh gi&agrave;nh phương bắc m&agrave; l&agrave;m một hồi quyết chiến! Cuối c&ugrave;ng bởi T&agrave;o Th&aacute;o giỏi về tiếp thu trung ng&ocirc;n, tin d&ugrave;ng mưu kế Hứa Du, đột k&iacute;ch kho l&uacute;a &Ocirc; S&agrave;o khiến cho qu&acirc;n t&acirc;m Vi&ecirc;n Thi&ecirc;u đại loạn, sau đ&oacute; thuận đường đ&aacute;nh tan chủ lực Vi&ecirc;n qu&acirc;n. Sau chiến dịch n&agrave;y, H&agrave; Bắc Vi&ecirc;n thị tổn thương nguy&ecirc;n kh&iacute; dẫn đến thất bại ho&agrave;n to&agrave;n, cuối c&ugrave;ng bị T&aacute;o Th&agrave;o th&ocirc;n t&iacute;nh m&agrave; diệt vong.<br />\r\n<br />\r\nTrận chiến Quan Độ l&agrave; một hồi hoa lệ nhất trong đời T&agrave;o Th&aacute;o. Nhờ trận chiến n&agrave;y m&agrave; một gian h&ugrave;ng thống nhất phương Bắc, x&acirc;y dựng căn cơ vững chắc.<br />\r\n<br />\r\nĐối với T&agrave;o Th&aacute;o, Quan Độ l&agrave; một đoạn đặc sắc nhất nhưng với Vi&ecirc;n thị nhất tộc, lại l&agrave; sự bắt đầu của một cơn &aacute;c mộng.<br />\r\n<br />\r\nĐầu ti&ecirc;n l&agrave; nguy&ecirc;n kh&iacute; đại thương, tinh binh Vi&ecirc;n thị gần như chết sạch. Vi&ecirc;n Thiệu sinh bệnh, kh&ocirc;ng l&acirc;u sau tạ thế. Người kế thừa hắn Vi&ecirc;n Thượng ở ngo&agrave;i phải đối ph&oacute; với một đời ki&ecirc;u h&ugrave;ng T&agrave;o Th&aacute;o, b&ecirc;n trong l&agrave; một huynh trưởng rắp t&acirc;m l&agrave;m phản.<br />\r\n<br />\r\nChiến đấu một hồi, tất chiến tất bại, mất đất h&atilde;m th&agrave;nh, bị &eacute;p chỉ c&ograve;n c&aacute;ch tha hương tới Li&ecirc;u Đ&ocirc;ng nương nhờ C&ocirc;ng T&ocirc;n Khang. Nhưng cuối c&ugrave;ng thủ cấp bị cắt xuống đem d&acirc;ng T&agrave;o Th&aacute;o, chịu một c&aacute;i chết sỉ nhục.<br />\r\n<br />\r\nC&oacute; thể n&oacute;i Vi&ecirc;n Thượng một đời ngắn ngủi vừa l&agrave; may mắn, vừa l&agrave; bất hạnh. May mắn v&igrave; hắn sinh ra trong một danh m&ocirc;n Tứ thế Tam c&ocirc;ng như Vi&ecirc;n gia, vừa sinh ra đ&atilde; định sẵn l&agrave; thi&ecirc;n chi ki&ecirc;u tử, hưởng mọi vinh hoa ph&uacute; qu&yacute;. Bất hạnh v&igrave; nửa đời sau của hắn trở th&agrave;nh đ&aacute; k&ecirc; ch&acirc;n cho b&aacute; nghiệp của ki&ecirc;u h&ugrave;ng T&agrave;o Th&aacute;o, biến th&agrave;nh một bộ bạch cốt!<br />\r\n<br />\r\nT&acirc;n sinh Vi&ecirc;n Thượng qu&aacute; khổ rồi. Tiền th&acirc;n của hắn hưởng hết vinh hoa, để lại những cực khổ từ trận chiến Quan Độ cho t&ecirc;n xui xẻo vừa mượn x&aacute;c ho&agrave;n hồn n&agrave;y nhận lấy.<br />\r\n<br />\r\nMột lần c&oacute; được sinh mệnh, dường như c&oacute; địa vị cao cao tại thượng, nhưng thật ra giống như ph&ugrave; dung sớm nổ. Tất cả đều thay đổi sau trận chiến Quan Độ. &Aacute;c mộng của ch&iacute;nh m&igrave;nh sẽ bắt đầu, thậm ch&iacute; sau mấy năm hắn c&ograve;n c&oacute; thể chết oan uổng, đầu một nơi th&acirc;n một nẻo.<br />\r\n<br />\r\nThượng đế thật sự biết c&aacute;ch giỡn với hắn. Đ&ugrave;ng một c&aacute;i quăng hắn trở về thời cổ đại c&ograve;n sợ kh&ocirc;ng đủ chuyện, lại quyết định cho hắn ở đ&acirc;y chơi mấy năm sau đ&oacute; lại giết hắn.<br />\r\n<br />\r\nM&igrave;nh thật sự phải chịu kết cục như vậy?<br />\r\n<br />\r\nAi m&agrave; cam t&acirc;m? Nếu đổi lại l&agrave; ai cũng sẽ kh&ocirc;ng cam t&acirc;m!<br />\r\n<br />\r\nSắc mặt Vi&ecirc;n Thượng đột nhi&ecirc;n trở n&ecirc;n trắng bệch, con mắt c&ograve;n c&oacute; vẻ v&ocirc; hồn, Vi&ecirc;n Hi trong l&ograve;ng nhất thời hoảng hốt.<br />\r\n<br />\r\n&ldquo;Tam đệ, tam đệ, ngươi lại bị sao vậy? Kh&ocirc;ng n&ecirc;n dọa vi huynh. Y quan, ngươi mau xem huynh đệ ta rốt cuộc l&agrave; xảy ra chuyện g&igrave;?&rdquo;<br />\r\n<br />\r\nY quan nghe vậy liền cuống qu&iacute;t tiến tới xem x&eacute;t lại thấy Vi&ecirc;n Thượng d&ugrave;ng tay r&aacute;ng chống cơ thể đứng dậy, tiến tới ghi vai Vi&ecirc;n Hi, hắn d&ugrave;ng lực mạnh tới mức thiếu ch&uacute;t nữa k&eacute;o ng&atilde; Vi&ecirc;n Hi.<br />\r\n<br />\r\n&ldquo;Tam đệ, ngươi l&agrave;m g&igrave; vậy?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng đầu đau muốn nứt nhưng vẫn cắn răng hướng Vi&ecirc;n Hi n&oacute;i: &ldquo;Nhị ca, ta muốn đi gặp phụ th&acirc;n! Lập tức l&ecirc;n ngựa!&rdquo;<br />\r\n<br />\r\n&ldquo;A?&rdquo; Vi&ecirc;n Hi nghe vậy c&oacute; ch&uacute;t đơ người, sau đ&oacute; lại như hiểu r&otilde; c&aacute;i g&igrave;, cười ha ha tr&ecirc;u chọc: &ldquo;Tam đệ, ngươi cũng kh&ocirc;ng c&ograve;n l&agrave; tiểu h&agrave;i tử. Bị bệnh liền muốn t&igrave;m cha mẹ, muốn l&agrave;m nũng sao?&rdquo;<br />\r\n<br />\r\n&ldquo;L&agrave;m nũng c&aacute;i rắm&hellip; Kh&ocirc;ng phải chuyện n&agrave;y!&rdquo; Vi&ecirc;n Thượng th&acirc;n thể suy yếu, tr&ecirc;n đầu mồ h&ocirc;i đổ r&ograve;ng r&ograve;ng: &ldquo; Ta c&oacute; qu&acirc;n vụ trọng yếu muốn b&agrave;n bạc với phụ th&acirc;n! Việc n&agrave;y li&ecirc;n quan tới sinh tử to&agrave;n qu&acirc;n, tuyệt đối kh&ocirc;ng thể tr&igrave; ho&atilde;n!&rdquo;<br />\r\n<br />\r\nĐương nhi&ecirc;n kh&ocirc;ng thể tr&igrave; ho&atilde;n, trong lời n&oacute;i vừa rồi của Vi&ecirc;n Hi Hứa Du đ&atilde; đầu h&agrave;ng T&agrave;o Th&aacute;o, trận chiến Quan Độ ph&aacute;t triển tới giai đoạn cuối c&ugrave;ng, sinh tử chỉ trong một c&aacute;i chớp mắt! Một việc xử l&yacute; kh&ocirc;ng tốt, kh&ocirc;ng n&oacute;i tới việc đại qu&acirc;n Vi&ecirc;n Thi&ecirc;u biến th&agrave;nh tro bụi, c&aacute;i mạng nhỏ của m&igrave;nh cũng bị li&ecirc;n lụy v&agrave;o.<br />\r\n<br />\r\n&ldquo;Ngươi&hellip; c&oacute; qu&acirc;n vụ?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi ngạc nhi&ecirc;n nh&igrave;n Vi&ecirc;n Thượng, thấy gượng mặt tuấn t&uacute; trắng bệch, đầu đổ đầy mồ h&ocirc;i vẫn liều mạng muốn đứng dậy quần &aacute;o, tr&ocirc;ng kh&ocirc;ng giống l&agrave;m bộ.<br />\r\n<br />\r\n&ldquo;Tam đệ, th&acirc;n thể của ngươi đang yếu nhược. Việc g&igrave; phải gấp g&aacute;p như vậy, chờ mấy ng&agrave;y nữa kh&ocirc;ng được a2?&rdquo;<br />\r\n<br />\r\n&ldquo;Mấy ng&agrave;y nữa?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng nghe vậy kh&ocirc;ng khỏi một trận cười khổ. Chỉ sợ mấy ng&agrave;y nữa, binh sĩ Vi&ecirc;n qu&acirc;n đều bị T&agrave;o Qu&acirc;n ch&eacute;m giết trong m&agrave;n đ&ecirc;m rồi.<br />\r\n<br />\r\n&ldquo;Nhị ca, qu&acirc;n t&igrave;nh khẩn cấp. Thật sự l&agrave; kh&ocirc;ng chờ được! Ta hiện tại khẩn trưởng đi gặp phụ th&acirc;n..!&rdquo;<br />\r\n<br />\r\nN&oacute;i tới đ&acirc;y, Vi&ecirc;n Thượng t&iacute;nh thay &aacute;o nhưng lại su&yacute;t ng&atilde; ra đất, may l&agrave; Vi&ecirc;n Hi kịp thơi giơ tay đỡ lấy.<br />\r\n<br />\r\nVi&ecirc;n Thượng kho&aacute;t tay &aacute;o, biểu thị kh&ocirc;ng sao, vội v&agrave;ng n&oacute;i: &ldquo;Nhị ca, th&acirc;n thể ta quả thực hư nhược, đến cả tự thay &aacute;o cũng kh&ocirc;ng được. Ngươi gọi mấy người tới gi&uacute;p ta&hellip;&rdquo;<br />\r\n<br />\r\nMặc d&ugrave; Vi&ecirc;n Hi kh&ocirc;ng đồng &yacute; nhưng thấy Vi&ecirc;n Thượng đến cả &aacute;o cũng thay kh&ocirc;ng nổi những vẫn muốn gặp Vi&ecirc;n Thiệu, đủ thấy đ&aacute;y l&ograve;ng hắn c&oacute; bao nhi&ecirc;u hoảng hốt. Vi&ecirc;n Hi sao lại c&oacute; thể qu&aacute; đ&aacute;ng ngăn cản?<br />\r\n<br />\r\nVi&ecirc;n Hi thở d&agrave;i, xoay người hướng về ph&iacute;a ngo&agrave;i trướng gọi to.<br />\r\n<br />\r\n&ldquo;Người đ&acirc;u&hellip;người đ&acirc;u! Tam c&ocirc;ng tử muốn đi b&aacute;i kiến chủ c&ocirc;ng. Mau gi&uacute;p Tam c&ocirc;ng tử mặc quan y!&rdquo;</h2>', 0, 'chuong-1-vien-gia-tam-cong-tu'),
(32, 18, 'Chương 2: Nguy cơ tràn ngập', 'Chương 2: Nguy cơ tràn ngập', '<h2>Thay quan phục, rửa mặt, v&ocirc; số những lễ nghi tuy phiền phức nhưng Vi&ecirc;n Thượng lại phải từng bước từng bước ho&agrave;n th&agrave;nh<br />\r\n<br />\r\nCổ nh&acirc;n ch&uacute; trọng lễ nghi. Đặc biệt Vi&ecirc;n Thị l&agrave; Tứ Thế Tam C&ocirc;ng lại c&agrave;ng coi trọng những thứ hư vinh rắc rối n&agrave;y.<br />\r\n<br />\r\nMấy người hầu gi&uacute;p Vi&ecirc;n Thượng thay trang phục, chỉnh sửa khu&ocirc;n mặt, sau đ&oacute; đưa hắn một tấm gương đồng để hắn xem lại.<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng thừa nhận, tuy rằng vị Vi&ecirc;n gia tam c&ocirc;ng tử n&agrave;y trong lịch sử kh&ocirc;ng c&oacute; danh tiếng, nhưng dung mạo lại thật sự rất đẹp. M&agrave;y như kiếm, mắt như sao, phong thần tuấn t&uacute;. Khuyết điểm duy nhất l&agrave; do chưa khỏi phong h&agrave;n, da hơi c&oacute; ch&uacute;t trắng bệch hư tho&aacute;t.<br />\r\n<br />\r\nSo s&aacute;nh với vẻ ngo&agrave;i kh&ocirc;i ng&ocirc;, c&oacute; ch&uacute;t đ&ocirc;n hậu của Vi&ecirc;n Hi, dung mạo Vi&ecirc;n Thượng thực sự hơn kh&ocirc;ng chỉ một ch&uacute;t.<br />\r\n<br />\r\nNgẫm lại th&igrave; nhớ tới kiếp trước đọc một cuốn s&aacute;ch c&oacute; n&oacute;i rằng Vi&ecirc;n Thiệu cũng l&agrave; một nam tử bề ngo&agrave;i tuấn lu&atilde;ng oai h&ugrave;ng, đối với vật chất b&ecirc;n ngo&agrave;i v&ocirc; c&ugrave;ng coi trọng. Nguy&ecirc;n nh&acirc;n hắn cưng chiều thậm ch&iacute; truyền vị cho tam tử Vi&ecirc;n Thương cũng bao gồm việc Vi&ecirc;n Thượng tuấn l&atilde;ng bất ph&agrave;m, rất c&oacute; thần th&aacute;i hắn l&uacute;c tuổi c&ograve;n trẻ.<br />\r\n<br />\r\nSo với l&atilde;o T&agrave;o, L&atilde;o Vi&ecirc;n n&agrave;y x&eacute;t tr&ecirc;n phương diện chiến tranh đ&aacute;nh nhau th&igrave; kh&ocirc;ng bằng, nhưng sinh con được c&aacute;i đều đẹp trai.<br />\r\n<br />\r\nSau khi xong xu&ocirc;i, Vi&ecirc;n Thượng liền vội v&agrave;ng chạy ra ngo&agrave;i. Từ giường hắn đến cửa trướng chỉ tầm mười bước ngắn ngủi những hắn tr&ecirc;n kh&uacute;c đường n&agrave;y t&eacute; ng&atilde; tới ba lần. Từ đ&oacute; c&oacute; thể thấy được th&acirc;n thể hắn suy nhược tới mức n&agrave;o.<br />\r\n<br />\r\nVi&ecirc;n Hi kh&ocirc;ng đ&agrave;nh l&ograve;ng, vội v&agrave;ng tiến tới đỡ hắn, bất đắc dĩ n&oacute;i: &ldquo;Tam đệ, ngươi đến cả đi cũng kh&ocirc;ng nổi sao c&ograve;n r&aacute;ng sức, rốt cuộc phải chạy tới So&aacute;i trướng để l&agrave;m g&igrave;? C&oacute; chuyện g&igrave; m&agrave; ng&agrave;y sau kh&ocirc;ng thể n&oacute;i nhất định phỉa n&oacute;i trong h&ocirc;m nay?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng nghe vậy kh&ocirc;ng khỏi cười khổ, người cho l&agrave; ta ăn ở kh&ocirc;ng chạy đi nh&aacute;o chuyện? Sinh tử bản th&acirc;n l&agrave; chuyện đại sự, ai d&aacute;m kh&ocirc;ng quan t&acirc;m? Ta b&acirc;y giờ dễ chịu nằm tr&ecirc;n giường, sợ rằng mấy năm sau từ giường &ecirc;m trực tiếp đổi th&agrave;nh quan t&agrave;i! C&ograve;n phải chuẩn bị hai cỗ, một c&aacute;i cho đầu, một c&aacute;i cho thi thể&hellip;..!<br />\r\n<br />\r\n&ldquo;Nhị ca, ngươi kh&ocirc;ng r&otilde;. Ch&uacute;ng ta b&acirc;y giờ đ&atilde; l&agrave; nước l&ecirc;n tới ch&acirc;n, ta nếu như c&ograve;n kh&ocirc;ng đi gặp phụ thần sợ rằng kh&ocirc;ng bao l&acirc;u nữa cơ nghiệp tứ ch&acirc;u của phụ th&acirc;n khổ cực tạo n&ecirc;n phải chắp tay d&acirc;ng cho người kh&aacute;c. Trận Quan Độ đ&atilde; tới giai đoạn cuối c&ugrave;ng, bại trận chỉ trong một c&aacute;i nh&aacute;y mắt. Ngươi, ta kể cả phụ th&acirc;n cũng đ&atilde; đứng s&aacute;t v&aacute;ch n&uacute;i rồi!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi nghe vậy nhất thời kinh h&atilde;i, vội v&agrave;ng xem x&eacute;t bốn ph&iacute;a v&agrave;i lần, sau đ&oacute; quay đầu lại, đ&egrave; &eacute;p &acirc;m thanh xuống tới cực điểm n&oacute;i với Vi&ecirc;n Thượng: &ldquo;Tam đệ, ngươi đi&ecirc;n rồi sao? Loại lời n&oacute;i n&agrave;y sao c&oacute; thể n&oacute;i lung tung! Phụ th&acirc;n h&agrave;nh qu&acirc;n tối kỵ loại lời n&oacute;i bất lợi như vậy! Nếu c&oacute; người rắp t&acirc;m g&acirc;y rối đ&ecirc;m chuyện n&agrave;y truyền ra, phụ th&acirc;n nhất định phạt ngươi thật nặng!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng nở một nụ cười khổ, bất đắc dĩ n&oacute;i: &ldquo;Nhị ca, ngươi kh&ocirc;ng tin ta?&rdquo;<br />\r\n<br />\r\n&ldquo;Ta&hellip;.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi nhất thời c&oacute; ch&uacute;t nghẹn lời. Hắn thực sự kh&ocirc;ng thể tin những lời n&oacute;i kia lại từ đệ đệ vốn tự đại th&agrave;nh t&iacute;nh, coi rẻ anh h&ugrave;ng thi&ecirc;n hạ ch&iacute;nh miệng n&oacute;i ra.<br />\r\n<br />\r\nVi&ecirc;n Hi c&agrave;ng kh&ocirc;ng thể tin trong lời n&oacute;i Vi&ecirc;n Thượng &yacute; bảo danh m&ocirc;n gia tộc Tứ Thế Tam C&ocirc;ng, chủ nh&acirc;n H&agrave; Bắc cầm binh trăm vạn h&ugrave;ng cứ bắc địa, sẽ bị người kh&aacute;c đ&aacute;nh bại! Điều n&agrave;y c&oacute; thể sao?<br />\r\n<br />\r\nVi&ecirc;n Thượng kh&ocirc;ng ngốc, Vi&ecirc;n Hi tuy kh&ocirc;ng n&oacute;i g&igrave; nhưng từ sắc mặt của hắn đ&atilde; c&oacute; thể nh&igrave;n ra Vi&ecirc;n HI vẫn kh&ocirc;ng v&igrave; những lời hung &aacute;c n&agrave;y m&agrave; cảnh gi&aacute;c.<br />\r\n<br />\r\nTr&aacute;i lại, vẻ mặt Vi&ecirc;n Hi khiến hắn cảm gi&aacute;c được, hiện tại trong mắt vị nhị ca n&agrave;y m&igrave;nh tựa như một t&ecirc;n bệnh nh&acirc;n đần độn u m&ecirc;, kh&ocirc;ng biết bản th&acirc;n đang n&oacute;i c&aacute;i g&igrave;!<br />\r\n<br />\r\nVi&ecirc;n Hi c&ograve;n như vậy, kh&ocirc;ng cần phải n&oacute;i g&igrave; l&agrave; Vi&ecirc;n Thiệu.<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng nhiều lời với ngươi nữa, ta khẩn trưởng đi tới trung qu&acirc;n trướng!&rdquo; Vi&ecirc;n Thượng đem suy nghĩ hỗn loạn trong đầu đặt sang một b&ecirc;n, vội v&agrave;ng đi ra ngo&agrave;i trướng.<br />\r\n<br />\r\nKh&ocirc;ng cần quản hắn tin hay kh&ocirc;ng tin, số mệnh tương lai của m&igrave;nh d&agrave;i hay ngắn mới l&agrave; quan trọng. Chuyện đến nước n&agrave;y, d&ugrave; như thế n&agrave;o m&igrave;nh cũng phải thử một lần.<br />\r\n<br />\r\nVi&ecirc;n Hi trừng mắt Vi&ecirc;n Thượng, nh&igrave;n b&oacute;ng lưng hắn hồi l&acirc;u. Tam đệ h&ocirc;m nay sao lại trở n&ecirc;n qu&aacute;i dị như vậy? Nếu kh&ocirc;ng quản hắn cho tốt, lỡ hắn chạy đến chỗ phụ th&acirc;n n&oacute;i bậy n&oacute;i bạ, m&igrave;nh c&ograve;n c&oacute; thể y&ecirc;n ổn sao?<br />\r\n<br />\r\nTức giận giẫm ch&acirc;n một c&aacute;i, Vi&ecirc;n Hi đuổi theo, k&eacute;o Vi&ecirc;n Thượng lại trịnh trọng n&oacute;i: &ldquo;Tam đệ, ngươi thật sự kh&ocirc;ng đi kh&ocirc;ng được?&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thượng quay đầu liếc nh&igrave;n Vi&ecirc;n Hi, hận r&egrave;n sắt kh&ocirc;ng th&agrave;nh th&eacute;p n&oacute;i: &ldquo;Nhị cam t&igrave;nh thế b&acirc;y giờ kh&ocirc;ng đi ch&iacute;nh l&agrave; ngồi chờ chết&hellip;&rdquo;<br />\r\n<br />\r\n&ldquo;Được, vậy ngươi l&ecirc;n xe ngựa của ta, ta c&ugrave;ng ngươi đi gặp phụ th&acirc;n!&rdquo;<br />\r\n<br />\r\n&ldquo;Gi&aacute;!&rdquo;<br />\r\n<br />\r\nKh&ocirc;ng l&acirc;u sau, một cỗ song m&atilde; chiến xa từ trong trại của Vi&ecirc;n Thượng lấy tốc độ cực nhanh chạy tới vị tr&iacute; trung qu&acirc;n so&aacute;i trướng của Vi&ecirc;n Thiệu.<br />\r\n<br />\r\nTr&ecirc;n chiến xa, một b&ecirc;n l&agrave; Vi&ecirc;n Hi chau m&agrave;y ngồi nghi&ecirc;m chỉnh, b&ecirc;n kia l&agrave; Vi&ecirc;n Thượng mặt m&agrave;y trắng bệch, do kh&ocirc;ng quen xe ngựa x&oacute;c nảy n&ecirc;n hắn l&uacute;c n&agrave;y v&ocirc; c&ugrave;ng kh&oacute; chịu.<br />\r\n<br />\r\nTrung qu&acirc;n so&aacute;i trướng của Vi&ecirc;n Thiệu c&aacute;ch trại của Vi&ecirc;n Thượng khoảng năm dặm, ở nơi s&acirc;u nhất trong doanh trại Vi&ecirc;n qu&acirc;n. Bốn ph&iacute;a Đ&ocirc;ng T&acirc;y Nam Bắc được bảo vệ k&iacute;n kẽ nước chảy kh&ocirc;ng lọt. Ngay cả chiến xa của Vi&ecirc;n Hi cũng phải bị l&iacute;nh tuần tra kiểm tra v&agrave;i lần.<br />\r\n<br />\r\nTr&ecirc;n đường tới so&aacute;i trướng, nội t&acirc;m Vi&ecirc;n Thượng cũng tho&aacute;ng chấn kinh.<br />\r\n<br />\r\nTrại liền trại, doanh liền doanh, trăm bước một trạm canh, xung quanh mười dặm đều c&oacute; l&iacute;nh g&aacute;c tay nắm trưởng m&acirc;u.<br />\r\n<br />\r\nB&ecirc;n trong doanh trướng c&oacute; qu&acirc;n sĩ cưỡi ngựa li&ecirc;n tục tuần tra, kh&iacute; thế ngất trời.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; qu&acirc;n sĩ dưới trướng Vi&ecirc;n Thiệu, ngang dọc tứ ch&acirc;u, l&agrave; căn cơ cường đại để H&agrave; Bắc ki&ecirc;u h&ugrave;ng nam hạ gi&agrave;nh trung nguy&ecirc;n.<br />\r\n<br />\r\nTừ Nghiệp th&agrave;nh đến K&yacute; ch&acirc;u, H&agrave; Bắc rồi đến bắc địa &Ocirc; Ho&agrave;n, cơ nghiệp n&agrave;y đều l&agrave; do một tay Vi&ecirc;n Thiệu tạo n&ecirc;n, hơn nữa c&ograve;n trở n&ecirc;n ho&agrave;n to&agrave;n vững chắc.<br />\r\n<br />\r\nD&ugrave; trong lịch sử Vi&ecirc;n Thiệu l&agrave; kẻ thất bại nhưng thấy một cảnh n&agrave;y phải c&ocirc;ng nhận Vi&ecirc;n Thiệu hắn l&uacute;c n&agrave;y v&ocirc; c&ugrave;ng cường đại, huy ho&agrave;ng, vinh quang.<br />\r\n<br />\r\nBộ d&aacute;ng qu&acirc;n sĩ thế n&agrave;y, ai c&oacute; tư c&aacute;ch n&oacute;i Vi&ecirc;n Thiệu hắn kh&ocirc;ng phải một đời ki&ecirc;u h&ugrave;ng?<br />\r\n<br />\r\nVi&ecirc;n Thiệu t&acirc;m t&igrave;nh t&aacute;n thưởng qu&acirc;n sĩ Vi&ecirc;n Thiệu cường đại. Nhưng đột nhi&ecirc;n, trong đầu hắn hiện l&ecirc;n một vấn đề&hellip;.<br />\r\n<br />\r\nC&oacute; thể đ&aacute;nh bại đội qu&acirc;n như thế n&agrave;y th&igrave; t&ecirc;n T&agrave;o Th&aacute;o kia đ&aacute;ng sợ tới mức n&agrave;o&hellip;<br />\r\n<br />\r\nVi&ecirc;n Thượng đang nhập t&acirc;m suy nghĩ th&igrave; &acirc;m thanh xa phu k&eacute;o hắn trở lại với thực tại: &ldquo;Nhị c&ocirc;ng tử, tam c&ocirc;ng tử! Ph&iacute;a trước l&agrave; trung qu&acirc;n so&aacute;i trướng của chủ c&ocirc;ng, k&iacute;nh xin hai vị c&ocirc;ng tử xuống xe đi bộ.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi nhảy xuống xe trước, quay đầu n&oacute;i : &ldquo;Tam đệ, ph&iacute;a trước l&agrave; so&aacute;i trướng, kh&ocirc;ng thể đi xe v&agrave;o. Ngươi phải chịu kh&oacute; đi bộ đoạn đường n&agrave;y, để nhị ca gi&uacute;p ngươi.&rdquo;<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng cần phải vậy, ta chưa yếu tới mức đi bộ một đoạn n&agrave;y cũng kh&ocirc;ng nổi..&rdquo;<br />\r\n<br />\r\nLời chưa n&oacute;i hết, Vi&ecirc;n Thượng mới bước xuống xe ngựa ch&acirc;n đ&atilde; loạng choạng, su&yacute;t nữa t&eacute; ngất.<br />\r\n<br />\r\nMẹ n&oacute;! Lửa đ&atilde; thi&ecirc;u tới m&ocirc;ng rồi m&agrave; hết lần n&agrave;y tới lần kh&aacute;c hắn lại mang bộ d&aacute;ng yếu ớt nhiễm phong h&agrave;n.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; thương đế muốn chơi hắn chết a!<br />\r\n<br />\r\nVi&ecirc;n Thượng quyết định kh&ocirc;ng sinh cường nữa, việc quan trọng nhất b&acirc;y giờ l&agrave; gặp được Vi&ecirc;n Thiệu.<br />\r\n<br />\r\n&ldquo;Nhị ca, vẫn l&agrave; ngươi d&igrave;u ta v&agrave;o đi th&ocirc;i. Th&acirc;n thể ta đang rất yếu, l&agrave;m phiền huynh trưởng nhẹ nh&agrave;ng ch&uacute;t, tiểu đệ cảm k&iacute;ch v&ocirc; c&ugrave;ng.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Hi &ldquo;&hellip;.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n thị trung qu&acirc;n so&aacute;i trướng.<br />\r\n<br />\r\nSo&aacute;i trướng diện t&iacute;ch lớn bằng một căn ph&ograve;ng lớn, lớn hơn v&agrave;i ba lần so với lều vải b&igrave;nh thường.<br />\r\n<br />\r\nBốn g&oacute;c so&aacute;i trướng để bốn c&aacute;i chậu than bằng đồng, b&ecirc;n trong than ch&aacute;y rực đỏ. Ở giữa l&agrave; một c&aacute;i &aacute;n thư, tr&ecirc;n mặt &aacute;n thư phủ một tấm gấm v&oacute;c Hồng Mi&ecirc;n sạch sẽ. Từ trong đỉnh hương tỏa ra l&agrave;n kh&oacute;i xanh mịt mờ. Tất cả cho thấy chủ nh&acirc;n so&aacute;i trướng l&agrave; một người v&ocirc; c&ugrave;ng ch&uacute; trọng lễ nghi.<br />\r\n<br />\r\nNgồi sau &aacute;n thư một người th&acirc;n mang kim gi&aacute;p, kho&aacute;c hồng b&agrave;o, đầu đội mũ quan, dưới cằm r&acirc;u d&agrave;i nửa thước chỉnh tề, th&acirc;n thể ưỡn thẳng kh&ocirc;ng hề c&oacute; bộ d&aacute;ng giống như người tuổi gần năm mươi, đ&ocirc;i mắt s&aacute;ng như sao trời cực kỳ c&oacute; thần, c&oacute; thể n&oacute;i l&agrave; tướng mạo đường đường, cực kỳ oai h&ugrave;ng.<br />\r\n<br />\r\nKh&ocirc;ng cần đo&aacute;n cũng biết người n&agrave;y l&agrave; H&agrave; Bắc chi h&ugrave;ng, người đứng đầu thi&ecirc;n hạ hiện nay, Vi&ecirc;n Thiệu.<br />\r\n<br />\r\nVi&ecirc;n Thiệu qu&eacute;t &aacute;nh mắt nh&igrave;n to&agrave;n bộ quần thần đang đứng hai b&ecirc;n trong so&aacute;i trướng, &aacute;nh mắt thể hiện mấy phần buồn bực c&ugrave;ng kh&ocirc;ng cam l&ograve;ng.<br />\r\n<br />\r\n&ldquo;Chư vị.&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thiệu trầm mặc một hồi sau đ&oacute; chậm r&atilde;i mở miệng n&oacute;i, hai b&ecirc;n văn v&otilde; cả người r&ugrave;ng m&igrave;nh, l&agrave;m bộ d&aacute;ng khi&ecirc;m tốn khom người, cẩn thận lắng nghe.<br />\r\n<br />\r\n&ldquo;Dựa v&agrave;o tin tức th&aacute;m tử, Hứa Du trong đem h&ocirc;m trước đ&atilde; tho&aacute;t ra đại doanh ta, suốt đem ki&ecirc;n tr&igrave; chạy về hướng nam, tung t&iacute;ch v&ocirc; c&ugrave;ng quỷ dị. Ta lu&acirc;n phi&ecirc;n ph&aacute;i ra th&aacute;m b&aacute;o li&ecirc;n tục điều tra nhưng kẻ n&agrave;y giả dối, rất giỏi ẩn nấp, đến n&agrave;y đ&atilde; kh&ocirc;ng c&ograve;n tung t&iacute;ch. Nghĩ tới nghĩ lui chỉ sợ Hứa Du b&acirc;y giờ đ&atilde; h&agrave;ng T&agrave;o. Ngươi n&agrave;y theo ta nhiều năm, biết r&otilde; thực hư qu&acirc;n ta, lần n&agrave;y theo T&agrave;o A Man tất c&oacute; mưu đồ. Mọi người c&oacute; ai c&oacute; thượng s&aacute;ch g&igrave; kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\nNghe xong lời n&agrave;y, mọi người ở đ&acirc;y người th&igrave; vuốt r&acirc;u, người thi nh&iacute;u m&agrave;y, c&ograve;n kh&ocirc;ng cũng l&agrave; mặt ủ m&agrave;y chau, hiển nhi&ecirc;n đối với việc Hứa Du đầu h&agrave;ng địch kh&aacute; l&agrave; đau đầu.<br />\r\n<br />\r\nC&aacute;i n&agrave;y cũng kh&oacute; tr&aacute;ch, hai qu&acirc;n giao chiến nếu như c&oacute; ngươi biết r&otilde; phe ta lại nương nhờ địch doanh sẽ g&acirc;y ảnh hưởng gấp g&aacute;p. Hứa Du ngươi đi kh&ocirc;ng cần gấp g&aacute;p nhưng b&ecirc;n trong đại doanh Vi&ecirc;n Thiệu chỉ sợ phải định đoạt hết thảy ph&ograve;ng ngự c&ugrave;ng s&aacute;ch lược tiến c&ocirc;ng, ti&ecirc;u h&agrave;o vật lực, t&agrave;i lực, nh&acirc;n lực. Chuyện như vậy kh&ocirc;ng phải đ&ocirc;i lời hiến kế liền dễ d&agrave;ng giải quyết.<br />\r\n<br />\r\n&ldquo;Chủ c&ocirc;ng, Hứa Du biết r&otilde; hư thực qu&acirc;n ta, nếu như quả thực đầu T&agrave;o sẽ l&agrave; họa rất lớn. Việc khẩn cấp trước mắt l&agrave; phải đem cự m&atilde;, cung nỏ, trạm g&aacute;c, doanh trại bố tr&iacute; lại to&agrave;n bộ để tr&aacute;nh khỏi T&agrave;o qu&acirc;n tập k&iacute;ch.&rdquo; Người đầu ti&ecirc;n đứng ra l&agrave; mưu thần Ph&ugrave;ng Kỷ.<br />\r\n<br />\r\nVi&ecirc;n Thiệu nghe vậy, suy nghĩ kỹ rồi n&oacute;i: &ldquo;Lời Nguy&ecirc;n Đồ rất đ&uacute;ng. T&agrave;o A Man gian tr&aacute;, quỷ kế đa đoan, nếu biết được thực hư qu&acirc;n ta kh&ocirc;ng khỏi nảy sinh mưu đồ, vẫn n&ecirc;n tăng mạnh ph&ograve;ng bị. Người đ&acirc;u, truyền qu&acirc;n lệnh, lệnh tướng sĩ tam qu&acirc;n thay phi&ecirc;n canh g&aacute;c suốt đ&ecirc;m, đề ph&ograve;ng T&agrave;o qu&acirc;n dạ tập!&rdquo;<br />\r\n<br />\r\n&ldquo;Chủ c&ocirc;ng chậm đ&atilde;!&rdquo;<br />\r\n<br />\r\nTiếng n&oacute;i vừa dứt, lại thấy một văn sĩ khu&ocirc;n mặt thon gầy, xương g&ograve; m&aacute; cực cao ra lời khuy&ecirc;n can: &ldquo;Chủ c&ocirc;ng, lời Ph&ugrave;ng Nguy&ecirc;n Đồ theo thần tuyệt kh&ocirc;ng thể l&agrave;m! Chủ c&ocirc;ng c&ugrave;ng T&agrave;o Th&aacute;o quen biết nhiều năm, biết r&otilde; gian tặc n&agrave;y gian manh xảo tr&aacute;, bụng dạ cực s&acirc;u, v&ocirc; c&ugrave;ng đa nghi! Hứa Du bỏ mạnh theo yếu, lấy t&iacute;nh t&igrave;nh T&agrave;o Th&aacute;o h&aacute; c&oacute; thể kh&ocirc;ng nghi ngờ?L&agrave;m sao c&oacute; thể dễ d&agrave;ng tin tưởng hắn! L&uacute;c n&agrave;y chủ c&ocirc;ng lại thay đổi to&agrave;n bộ b&agrave;i bố qu&acirc;n doanh th&igrave; quả thật dư thừa. Kh&ocirc;ng băng nh&acirc;n l&uacute;c Hứa Du kh&ocirc;ng được T&agrave;o tặc tin cậy, lấy qu&acirc;n binh quy m&ocirc; lớn m&atilde;nh c&ocirc;ng Quan Độ!<br />\r\n<br />\r\nNgười vừa n&oacute;i ch&iacute;nh l&agrave; K&yacute; Ch&acirc;u biệt gi&aacute; Qu&aacute;ch Đồ<br />\r\n<br />\r\nVi&ecirc;n Thiệu nghe vậy, sừng sỡ một ch&uacute;t, sau đ&oacute; chậm r&atilde;i gật đầu cẩn trọng n&oacute;i: &ldquo;Qu&aacute;ch C&ocirc;ng Tắc n&oacute;i như vậy&hellip;.cũng rất c&oacute; đạo l&yacute;.&rdquo;<br />\r\n<br />\r\n&ldquo;Chủ c&ocirc;ng kh&ocirc;ng thể!&rdquo;<br />\r\n<br />\r\nPh&ugrave;ng Kỷ thấy Qu&aacute;ch Đồ c&ocirc;ng nhi&ecirc;n chống đối hắn, t&acirc;m trạng nhất thời tức giận, vội la l&ecirc;n: &ldquo;Chủ c&ocirc;ng, Qu&aacute;ch Đồ xưa nay cổ hủ. T&agrave;o tặc l&agrave; dạng người g&igrave;, chẳng lẽ kh&ocirc;ng biết l&uacute;c n&agrave;o cần phải quyết đo&aacute;n? Huống hồ Hứa Du với hắn cũng c&oacute; giao t&igrave;nh, sao lại kh&ocirc;ng được trọng dụng? Những l&uacute;c thế n&agrave;y tuyệt đối kh&ocirc;ng thể t&ugrave;y tiện tiến binh, trước cố thủ đại doanh, sau lại chiếm Quan Độ mới l&agrave; thượng s&aacute;ch!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thiệu vừa lấy tay vuốt r&acirc;u, động t&aacute;c ng&agrave;y c&agrave;ng nhanh, vừa gật đầu: &ldquo;Kh&ocirc;ng sai, T&agrave;o A Man c&ugrave;ng ta với Hứa Du đều l&agrave; bạn cũ, Nguy&ecirc;n Đồ lời n&agrave;y n&oacute;i tr&uacute;ng yếu điểm&hellip;&rdquo;<br />\r\n<br />\r\nQu&aacute;ch Đồ cũng kh&ocirc;ng phải l&agrave; người g&igrave; hiền l&agrave;nh, nghe vậy cười lạnh một tiếng, đ&aacute;p lại Ph&ugrave;ng Kỷ: &ldquo;Ph&ugrave;ng Nguy&ecirc;n Đồ lời n&agrave;y thật giống như lời con n&iacute;t. Bạn cũ th&igrave; thế n&agrave;o? Hứa Du l&agrave; hạng người tham t&agrave;i, thay đổi thất thường, loại tiểu nh&acirc;n thi&ecirc;n hạ coi thường như vậy d&ugrave; cho T&agrave;o Th&aacute;o t&agrave;i năng kh&ocirc;ng bằng chủ c&ocirc;ng nhưng cũng l&agrave; chư hầu một phương th&igrave; l&agrave;m sao c&oacute; thể trọng dụng? Những lời ấy thật l&agrave; buồn cười!&rdquo;<br />\r\n<br />\r\nNghe xong lời n&agrave;y, văn v&otilde; trong trướng đều gật đầu. Vi&ecirc;n Thiệu thấy thế trong l&ograve;ng c&oacute; ch&uacute;t bu&ocirc;ng lỏng.<br />\r\n<br />\r\nPh&ugrave;ng Kỷ thấy lời lẽ Qu&aacute;ch Đồ chiếm thế thượng ph&ograve;ng, lập tức mở miệng đ&aacute;p trả: &ldquo;Qu&aacute;ch C&ocirc;ng Tắc, chủ c&ocirc;ng anh minh sao ngươi d&aacute;m ở nơi n&agrave;y lắm mồm? Hứa Du tham t&agrave;i th&igrave; thế n&agrave;o? Ngươi kh&ocirc;ng phải cũng T&agrave;o Th&aacute;o kia d&ugrave;ng ngươi lu&ocirc;n kh&ocirc;ng để &yacute; xuất th&acirc;n nh&acirc;n phẩm, c&aacute;i g&igrave; h&agrave;n m&ocirc;n, binh tốt, h&agrave;ng tướng, ph&agrave;m c&oacute; lợi hắn đều sử dụng! N&oacute;i chi một người t&agrave;i như Hứa Du! Huống chi b&acirc;y giờ đại binh ta &aacute;p s&aacute;t, T&agrave;o Th&aacute;o l&agrave;m sao c&oacute; thể lo lắng nhiều như vậy? Những lời của ngươi thật hoang đường!&rdquo;<br />\r\n<br />\r\nVi&ecirc;n Thiệu nghe vậy bừng tỉnh: &ldquo;Kh&ocirc;ng sai, T&agrave;o A Man d&ugrave;ng người lu&ocirc;n như vậy. Thất phu như Hứa Du cũng chưa chắc kh&ocirc;ng được trong dụng.&rdquo;<br />\r\n<br />\r\nN&oacute;i tới đ&acirc;y Vi&ecirc;n Thiệu cảm thấy băn khoăn, vỗ b&agrave;n một c&aacute;i, lầm bầm n&oacute;i: &ldquo;Hai vị n&oacute;i đều c&oacute; đạo l&yacute;, quả thật khiến người ta kh&oacute; lựa chọn nghe theo&hellip;&rdquo;<br />\r\n<br />\r\nQu&aacute;ch Đồ nghe vậy cuống l&ecirc;n, mở miệng muốn n&oacute;i hai c&acirc;u th&igrave; thấy th&acirc;n binh thủ doanh bước v&agrave;o, quỳ trước Vi&ecirc;n Thiệu n&oacute;i: &ldquo;Khởi bẩm chủ c&ocirc;ng, nhị c&ocirc;ng tử c&ugrave;ng tam c&ocirc;ng tử ngo&agrave;i trướng cầu kiến!&rdquo;<br />\r\n<br />\r\n&ldquo;Ồ?&rdquo; Vi&ecirc;n Thiệu từ t&acirc;m trạng băn khoăn cảnh gi&aacute;c kh&ocirc;ng thể đưa ra lựa chọn liền trở n&ecirc;n vui vẻ, đứng l&ecirc;n n&oacute;i: &ldquo;Hiển Phủ con ta tới? Hắn kh&ocirc;ng phải chưa hết bệnh sao? Sao lại chạy tới nơi n&agrave;y, mau, mau ch&oacute;ng k&ecirc;u con ta tiến v&agrave;o đ&acirc;y!&rdquo;</h2>', 0, 'chuong-2-nguy-co-tran-ngap');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(33, 19, 'Chương 1: Đầu bếp', 'Chương 1: Đầu bếp', '<h2>Trong một căn bếp của kh&aacute;ch sạn năm sao cao cấp, mọi người mặc bộ đồng phục m&agrave;u trắng c&ugrave;ng với đội chiếc mũ đầu bếp, đứng bận rộn trước b&agrave;n bếp.<br />\r\n<br />\r\nTrong số đ&oacute; c&oacute; một đầu bếp nhỏ đang c&uacute;i đầu nghi&ecirc;m t&uacute;c c&ugrave;ng cẩn thận m&agrave; b&agrave;y đĩa. Đem vi&ecirc;n sỏi m&agrave;i giũa đến b&oacute;ng lo&aacute;ng, đặt tr&ecirc;n c&aacute;c cục băng (đ&aacute;), ở giữa l&agrave; một c&aacute;i ch&eacute;n tinh xảo, trong ch&eacute;n l&agrave; c&agrave;ng cua.<br />\r\n<br />\r\nM&oacute;n n&agrave;y được gọi l&agrave; &ldquo;H&agrave;nh hương cua kiềm*&rdquo;, Hồ Tiểu B&igrave; l&agrave;m m&oacute;n n&agrave;y đ&atilde; nhiều ng&agrave;y, ch&iacute;nh x&aacute;c m&agrave; n&oacute;i, l&agrave; vụ kh&aacute;ch n&agrave;o đ&oacute; mỗi ng&agrave;y đều ăn m&oacute;n n&agrave;y, hơn nữa c&ograve;n chỉ định l&agrave; do Hồ Tiểu B&igrave; l&agrave;m.<br />\r\n<br />\r\n(<em>*&rdquo;H&agrave;nh hương cua kiềm&rdquo; c&oacute; t&ecirc;n kh&aacute;c l&agrave; &ldquo;Lưỡi cua cay&rdquo;. M&oacute;n n&agrave;y cay, dễ l&agrave;m, được d&ugrave;ng l&agrave;m m&oacute;n khai vị. T&ecirc;n gốc:葱香蟹钳</em>.)<br />\r\n<br />\r\n<br />\r\n<br />\r\nNgười kh&aacute;ch đ&oacute; n&oacute;i, kh&ocirc;ng phải hắn kh&ocirc;ng được.<br />\r\n<br />\r\nPh&ograve;ng bếp c&oacute; ch&uacute;t n&oacute;ng, dau khi l&agrave;m xong hắn đứng thẳng dậy, v&agrave; lau mồ h&ocirc;i tr&ecirc;n tr&aacute;n.<br />\r\n<br />\r\nNhư thế mới thấy, Hồ Tiểu B&igrave; lớn lớn da thịt mềm mịn, một m&agrave;u trắng hồng, ngũ quan tinh xảo như b&uacute;p b&ecirc; SD*, đ&ocirc;i mắt to chớp chớp, h&agrave;ng mi giống như tiểu hồ điệp bay m&uacute;a.<br />\r\n<br />\r\n<em>B&uacute;p b&ecirc; SD</em><br />\r\n<br />\r\nRất kh&oacute; tưởng tượng, rằng một người tinh tế như thế l&agrave; một đầu bếp. Đều quan trọng l&agrave;, đ&ocirc;i b&agrave;n tay kia nh&igrave;n giống như người kh&ocirc;ng l&agrave;m việc g&igrave;.<br />\r\n<br />\r\nCho n&ecirc;n khi Hồ Tiểu B&igrave; được quản l&yacute; kh&aacute;ch sạn đem đến, tất cả mọi người đều kh&ocirc;ng tin hắn l&agrave; đầu bếp, n&oacute;i hắn l&agrave; c&ocirc;ng tử đến để trải nghiệm cuộc sống.<br />\r\n<br />\r\nHồ Tiểu B&igrave; cũng biết ch&iacute;nh m&igrave;nh lớn l&ecirc;n c&oacute; ch&uacute;t nộn*, nhưng m&agrave; kh&ocirc;ng c&oacute; biện ph&aacute;p, bọn họ l&agrave; hồ ly nhất tộc, mỗi người l&agrave;n da đều rất tốt, v&ocirc; luận l&agrave;m việc vất vả thế n&agrave;o, đều c&oacute; l&agrave;n da mịn m&agrave;ng, v&agrave; c&oacute; t&iacute;nh đ&agrave;n hồi. Thật kh&oacute; để kh&ocirc; a.<br />\r\n<br />\r\n<em>(*nộn: non nớt, yếu đuối, mềm mịn)</em><br />\r\n<br />\r\nL&uacute;c đầu, c&aacute;c đầu bếp cho rằng Hồ Tiểu B&igrave; đến l&agrave; để chơi. Ai lại ngờ rằng, khi hắn nấu đồ ăn cho mọi người nếm thử th&igrave; ai cũng bị tr&ugrave; nghệ của hắn chinh phục.<br />\r\n<br />\r\nChấp nhận phục v&agrave; t&ocirc;n thờ hắn l&agrave;m thần bếp.<br />\r\n<br />\r\nKh&oacute; tr&aacute;ch quản l&yacute; hao tổn t&acirc;m sức d&ugrave;ng bất cứ gi&aacute; n&agrave;o cũng phải mang người tới.<br />\r\n<br />\r\nSau khi m&oacute;n ăn của Hồ Tiểu B&igrave; được bưng ra ngo&agrave;i th&igrave; hắn tiếp tục nấu tiếp m&oacute;n mới.<br />\r\n<br />\r\nC&aacute;i nồi, c&aacute;i muỗng, c&aacute;i xẻng m&agrave; Hồ Tiểu B&igrave; đang d&ugrave;ng đều l&agrave; do hắn mang đến. Đ&oacute; l&agrave; những dụng cụ thuần đen, bề mặt ch&uacute;ng kh&aacute;c nhau, kỳ thật bị r&oacute;t linh lực v&agrave;o.<br />\r\n<br />\r\nGia vị d&ugrave;ng cũng l&agrave; do Hồ Tiểu B&igrave; mang đến, hắn c&oacute; phương ph&aacute;p chế biến nguy&ecirc;n liệu ri&ecirc;ng, căn bản kh&ocirc;ng cần d&ugrave;ng đến gia vị của kh&aacute;ch sạn.<br />\r\n<br />\r\nNh&oacute;m đầu bếp hỏi hắn, gia vị của Hồ Tiểu B&igrave; c&oacute; g&igrave; kh&aacute;c với gia vị trong kh&aacute;ch sạn, hắn chỉ n&oacute;i l&agrave; do sư phụ hắn truyền cho.<br />\r\n<br />\r\nBởi v&igrave; c&oacute; sự xuất hiện của Hồ Tiểu B&igrave;, số lượng thực kh&aacute;ch cũng tăng l&ecirc;n kh&ocirc;ng &iacute;t, rất nhiều vị kh&aacute;ch chỉ đ&iacute;ch danh Hồ Tiểu B&igrave; nấu, nhất thời Hồ Tiểu B&igrave; trở th&agrave;nh một ng&ocirc;i sao đầu bếp, quản l&yacute; cười đến kh&ocirc;ng kh&eacute;p miệng được, cảm thấy &aacute;nh mắt m&igrave;nh kh&ocirc;ng tồi.<br />\r\n<br />\r\nNhưng mặt kh&aacute;c, cũng sinh ra kh&ocirc;ng &iacute;t sự đố kỵ.<br />\r\n<br />\r\nTrong một lần, c&oacute; một đầu bếp nh&acirc;n l&uacute;c ph&ograve;ng bếp kh&ocirc;ng c&oacute; ai, tr&aacute;nh camera m&agrave; trộm nếm thử gia vị của Hồ Tiểu B&igrave;, &yacute; định t&igrave;m được b&iacute; mật trong đ&oacute;.<br />\r\n<br />\r\nNhưng m&agrave;, sau khi nếm người đầu bếp nếm xong th&igrave; ph&aacute;t hiện gia vị của Hồ Tiểu B&igrave; cũng kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt với gia vị b&igrave;nh thường, căn bản kh&ocirc;ng nếm ra g&igrave; đặc sắc, v&igrave; thế người đầu bếp đ&oacute; đ&atilde; trộm mang một &iacute;t về nh&agrave; m&agrave; nghi&ecirc;n cứu, nhưng d&ugrave; c&oacute; nghi&ecirc;n cứu thế n&agrave;o, cũng vẫn kh&ocirc;ng nghi&ecirc;n cứu ra, bởi v&igrave;, n&oacute; căn bản giống với gia vị b&igrave;nh thường.<br />\r\n<br />\r\nĐầu bếp kia sau khi trộm gia vị của Hồ Tiểu B&igrave; th&igrave; bị ph&aacute;t hiện, Hồ Tiểu B&igrave; liền khiếu nại với quản l&yacute;. Sau đ&oacute; vị đầu bếp kia liền thu thập đồ vật rời đi. Đến cuối c&ugrave;ng vị đầu bếp kia vẫn kh&ocirc;ng hiểu chuyện g&igrave; đ&atilde; xảy ra, ăn trộm g&agrave; kh&ocirc;ng được c&ograve;n mất đi nắm th&oacute;c.<br />\r\n<br />\r\nHồ Tiểu B&igrave; biết, gia vị n&agrave;y, cần phải d&ugrave;ng nồi v&agrave; muỗng của m&igrave;nh, th&igrave; mới c&oacute; thể trở th&agrave;nh gia vị thần kỳ.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, đầu bếp trưởng ở cửa h&ocirc; l&ecirc;n<br />\r\n<br />\r\n&ldquo;Tiểu B&igrave; B&igrave;, vị kh&aacute;ch kia&hellip;&hellip;&rdquo;<br />\r\n<br />\r\nHồ Tiểu B&igrave; thấy vị bếp trưởng kia muốn n&oacute;i rồi lại th&ocirc;i, buồn bực, chẳng lẽ c&oacute; chuyện g&igrave; lớn?<br />\r\n<br />\r\nHắn nhanh ch&oacute;ng bỏ việc đang l&agrave;m xuống, rửa tay sạch sẽ rồi đi ra ngo&agrave;i.<br />\r\n<br />\r\nĐầu bếp trưởng l&agrave; người đ&agrave;n &ocirc;ng ba mươi mấy tuổi, ở Đ&ocirc;ng Bắc. L&atilde;o thường k&ecirc;u c&aacute;c đồng nghiệp bằng t&ecirc;n, duy chỉ c&oacute; Hồ Tiểu B&igrave; k&ecirc;u l&agrave; &ldquo;Tiểu B&igrave; B&igrave;&rdquo;.<br />\r\n<br />\r\nVừa ra khỏi cửa, Hồ Tiểu B&igrave; đ&atilde; bị đầu bếp trưởng vỗ vỗ vai, thấm th&iacute;a n&oacute;i<br />\r\n<br />\r\n&ldquo;Tiểu B&igrave; B&igrave; a, ngươi l&agrave;m đầu bếp l&acirc;u như vậy, hẳn l&agrave; c&oacute; kết th&ugrave; đi? &ldquo;<br />\r\n<br />\r\nKết th&ugrave;?<br />\r\n<br />\r\nKh&ocirc;ng c&oacute;.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; lắc đầu.<br />\r\n<br />\r\nĐầu bếp trưởng tiếp tục: &ldquo;Nếu vị kh&aacute;ch đ&oacute; n&oacute;i m&oacute;n ăn của cậu c&oacute; vấn đề, cậu&hellip; &ldquo;<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng c&oacute; khả năng đ&oacute;, t&ocirc;i tin m&oacute;n ăn kh&ocirc;ng c&oacute; vấn đề&rdquo;. Hồ Tiểu B&igrave; khẩn trương đ&aacute;nh g&atilde;y lời đầu bếp trưởng, h&agrave;ng l&ocirc;ng m&agrave;y thanh t&uacute; như d&iacute;nh lại với nhau, tr&ocirc;ng rất đau khổ.<br />\r\n<br />\r\nĐầu bếp trưởng kh&ocirc;ng thể chịu được dỗ d&agrave;nh: &ldquo;Kh&ocirc;ng c&oacute; kh&ocirc;ng c&oacute;, t&ocirc;i cũng tin l&agrave; kh&ocirc;ng c&oacute;, nhưng vị kh&aacute;ch đ&oacute; n&oacute;i c&oacute;, hay cậu tự m&igrave;nh đi hỏi r&otilde; xem? &ldquo;<br />\r\n<br />\r\nHồ Tiểu B&igrave; gật gật đầu, h&iacute;t s&acirc;u một hơi, vừa đi vừa sửa sang lại mũ đầu bếp, sau đ&oacute; ng&oacute;n tay giảo* lại c&ugrave;ng nhau, do khẩn trương, vạn nhất thực sự c&oacute; vấn đề l&agrave;m sao b&acirc;y giờ?<br />\r\n<br />\r\n<em>(*giảo絞:xoắn, vặn)&nbsp;</em><br />\r\n<br />\r\nC&oacute; thể bị mất việc?&nbsp;<br />\r\n<br />\r\nHắn lắc lắc đầu, kh&ocirc;ng nghĩ tới lại l&agrave;m cho chiếc mũ mới sửa lại trở n&ecirc;n xi&ecirc;u vẹo, nh&igrave;n qua rất l&agrave; buồn cười.<br />\r\n<br />\r\nHắn đứng ở trước cửa ph&ograve;ng VIP g&otilde; g&otilde;, b&ecirc;n trong truyền ra &acirc;m thanh k&ecirc;u Hồ Tiểu B&igrave; đi v&agrave;o.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; kh&ocirc;ng d&aacute;m ngẩng đầu, đi v&agrave;o b&ecirc;n trong với một bộ d&aacute;ng mang đầy vẻ ẩn khuất c&ugrave;ng l&uacute;ng t&uacute;ng.<br />\r\n<br />\r\nĐập v&agrave;o mắt Hồ Tiểu B&igrave; đầu ti&ecirc;n ch&iacute;nh l&agrave; đ&ocirc;i gi&agrave;y m&agrave; người đ&agrave;n &ocirc;ng đang mang, l&agrave; một đ&ocirc;i gi&agrave;y da m&agrave;u đen, b&oacute;ng lo&aacute;ng v&agrave; sạch sẽ. L&ecirc;n tr&ecirc;n l&agrave; một đ&ocirc;i vớ c&ugrave;ng m&agrave;u, biến mất ở ống quần t&acirc;y rộng nh&igrave;n kh&ocirc;ng thấy được da.&nbsp;<br />\r\n<br />\r\nQuần t&acirc;y kh&ocirc;ng phải l&agrave; một m&agrave;u đen thuần, m&agrave; c&oacute; m&agrave;u xanh lam, ống quần b&oacute; s&aacute;t v&agrave;o cặp đ&ugrave;i rắn chắc thon d&agrave;i. Lộ ra một sự hoang d&atilde; nhưng lại bị giam cầm.&nbsp;<br />\r\n<br />\r\nTim Hồ Tiểu B&igrave; bỗng nhi&ecirc;n đập li&ecirc;n hồi.&nbsp;<br />\r\n<br />\r\nL&agrave;m sao b&acirc;y giờ, hắn kh&ocirc;ng thể ở c&ugrave;ng một chỗ với nam nh&acirc;n n&agrave;y, nếu kh&ocirc;ng sẽ&hellip;&nbsp;<br />\r\n<br />\r\nHắn kh&ocirc;ng d&aacute;m nghĩ tới a.&nbsp;<br />\r\n<br />\r\nCả người nam nh&acirc;n n&agrave;y đều tỏa ra hoocmone nam nồng đậm, hắn c&oacute; ch&uacute;t kh&ocirc;ng thể khống chế được.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; kh&ocirc;ng d&aacute;m nh&igrave;n tiếp l&ecirc;n tr&ecirc;n, hắn cảm gi&aacute;c được &aacute;nh mắt nam nh&acirc;n quết tr&ecirc;n người hắn, nếu l&uacute;c n&agrave;y m&agrave; ở h&igrave;nh d&aacute;ng cửa hồ ly th&igrave; nhất định những sợi l&ocirc;ng mao trắng tr&ecirc;n người hắn sẽ dựng cả l&ecirc;n.&nbsp;<br />\r\n<br />\r\n&ldquo;Trước&hellip;&hellip; Ti&ecirc;n sinh?&rdquo; Hồ Tiểu B&igrave; lắp bắp n&oacute;i.<br />\r\n<br />\r\n&ldquo;Ngươi ch&iacute;nh l&agrave; đầu bếp l&agrave;m m&oacute;n n&agrave;y?&rdquo;<br />\r\n<br />\r\nGiọng n&oacute;i n&agrave;y nghe qua c&oacute; ch&uacute;t b&igrave;nh ổn v&agrave; trầm tĩnh kh&ocirc;ng giống như c&oacute; vẻ g&igrave; tức giận.&nbsp;<br />\r\n<br />\r\n&ldquo;V&acirc;ng, v&acirc;ng. C&oacute;, c&oacute; vấn đề g&igrave; kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\nNam nh&acirc;n đan ch&eacute;o tay lại với nhau, để tr&ecirc;n b&agrave;n, hiện l&ecirc;n ng&oacute;n tay thon d&agrave;i, mạnh mẽ, đeo tr&ecirc;n tay một c&aacute;i đồng hồ đơn giản.&nbsp;<br />\r\n<br />\r\nNam nh&acirc;n: &ldquo;Đương nhi&ecirc;n l&agrave; c&oacute; vấn đề.&rdquo;<br />\r\n<br />\r\n&ldquo;Ng&agrave;i n&oacute;i đi&hellip;&hellip;&rdquo; Hồ Tiểu B&igrave; thấp thỏm đến tim muốn nhảy ra ngo&agrave;i.&nbsp;<br />\r\n<br />\r\n&ldquo;Đến đ&acirc;y&rdquo; Nam nh&acirc;n n&oacute;i.<br />\r\n<br />\r\nHồ Tiểu B&igrave; nhanh ch&oacute;ng lắc đầu, kh&ocirc;ng được a, lại gần một ch&uacute;t hắn c&oacute; thể ngửi r&otilde; r&agrave;ng m&ugrave;i hương tr&ecirc;n người nam nh&acirc;n.&nbsp;<br />\r\n<br />\r\nNhưng l&agrave;, kh&ocirc;ng đi qua sẽ kh&ocirc;ng biết được vấn đề của m&oacute;n ăn. Hồ Tiểu B&igrave; cắn cắn m&ocirc;i, đi qua.&nbsp;<br />\r\n<br />\r\nL&uacute;c n&agrave;y, hắn thấy được phần tr&ecirc;n của nam nh&acirc;n, cuối c&ugrave;ng dừng lại ở cổ.&nbsp;<br />\r\n<br />\r\nNam nh&acirc;n mặc tr&ecirc;n người bộ t&acirc;y trang, &aacute;o sơ mi mở hai c&uacute;c, c&ugrave;ng với c&agrave; vạt m&agrave;u đỏ rũ trước bụng dưới.&nbsp;<br />\r\n<br />\r\n&Aacute;o sơ mi nam nh&acirc;n phồng l&ecirc;n, tựa hồ thấy được cơ bắp, ch&uacute;ng kh&ocirc;ng lớn kh&ocirc;ng nhỏ vừa phải, kh&ocirc;ng qu&aacute; khoa trương.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; kh&ocirc;ng thể chống đỡ được, thiếu ch&uacute;t nữa liền nhịn kh&ocirc;ng được.&nbsp;<br />\r\n<br />\r\nHắn vội v&agrave;ng n&oacute;i: &ldquo;Ti&ecirc;n sinh, mời ng&agrave;i n&oacute;i.&rdquo;<br />\r\n<br />\r\n&ldquo;Ngươi nh&igrave;n xem m&oacute;n n&agrave;y, v&igrave; c&aacute;i g&igrave; c&ugrave;ng với lần trước kh&ocirc;ng giống nhau?&rdquo;<br />\r\n<br />\r\nGiọng nam nh&acirc;n đặc biệt gần, như l&agrave; n&oacute;i ngay b&ecirc;n tai hắn.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; cẩn thận nếm, nhưng lại kh&ocirc;ng ph&aacute;t hiện kh&aacute;c với m&oacute;n lần trước: &ldquo;Kh&ocirc;ng c&oacute; a&hellip; Rốt cuộc l&agrave; n&oacute; kh&aacute;c g&igrave;? &ldquo;<br />\r\n<br />\r\nNam nh&acirc;n hừ một tiếng: &ldquo;Một cỗ tao vị*&rdquo;<br />\r\n<br />\r\n<em>(*Cổ tao vị: m&ugrave;i h&ocirc;i, tanh)</em><br />\r\n<br />\r\n&ldquo;!&rdquo; Hồ Tiểu B&igrave; chấn động, đ&ocirc;i mắt to tr&ograve;n mở ra, nh&igrave;n v&egrave; ph&iacute;a nam nh&acirc;n, nhưng từ &aacute;nh mắt đầu ti&ecirc;n, hắn qu&ecirc;n mất hắn định n&oacute;i g&igrave;.&nbsp;<br />\r\n<br />\r\nNguy&ecirc;n nh&acirc;n, nam nh&acirc;n trước mắt, qu&aacute; đẹp.&nbsp;<br />\r\n<br />\r\nNam nh&acirc;n nhướng m&agrave;y, n&oacute;i: &ldquo;Thế n&agrave;o? &ldquo;<br />\r\n<br />\r\nHồ Tiểu B&igrave; l&uacute;c n&agrave;y mới ho&agrave;n hồn, nhanh ch&oacute;ng n&oacute;i: &ldquo;Kh&ocirc;ng kh&ocirc;ng c&oacute;&hellip;&hellip;&rdquo;<br />\r\n<br />\r\nHắn l&agrave; hồ ly kh&ocirc;ng sai, nhưng đồ ăn th&igrave; v&ocirc; tội, một ch&uacute;t cũng kh&ocirc;ng c&oacute; tao vị, thật sự.&nbsp;<br />\r\n<br />\r\nHơn nữa, bọn họ l&agrave; hồ ly th&agrave;nh tinh, tr&ecirc;n người bọn họ l&agrave; m&ugrave;i hương kh&ocirc;ng phải l&agrave; tao vị, nhưng nh&acirc;n loại n&agrave;y đều hiểu lầm.&nbsp;<br />\r\n<br />\r\nTa giận nga.<br />\r\n<br />\r\nĐ&ocirc;i mắt to tr&ograve;n của hắn bị &eacute;p xuống bởi cặp l&ocirc;ng m&agrave;y, l&ocirc;ng mi d&agrave;i hơi động, miệng lẩm nhẩm.&nbsp;<br />\r\n<br />\r\nNh&igrave;n bộ dạng uẩn kh&uacute;c cực kỳ, hơn nữa ngũ quan hắn tinh xảo, nh&igrave;n thấy như một tiểu khả &aacute;i.&nbsp;<br />\r\n<br />\r\nNam nh&acirc;n nh&igrave;n Hồ Tiểu B&igrave; một v&ograve;ng, thấy được to&agrave;n bộ vẻ uẩn kh&uacute;c của hắn, đầu quả tim nhường như bị ai đ&acirc;m v&agrave;o, c&oacute; ch&uacute;t ngứa.&nbsp;<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng c&oacute;?&rdquo; Nam nh&acirc;n tựa s&aacute;t v&agrave;o Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\n&ldquo;Vậy ngươi đến nh&agrave; ta, ta l&agrave;m đồ ăn cho cho ngươi nếm thử thứ g&igrave; gọi l&agrave; nam nh&acirc;n vị&rdquo;<br />\r\n<br />\r\nNam nh&acirc;n vị?<br />\r\n<br />\r\nL&agrave; vị đặc biệt m&agrave; sư phụ n&oacute;i trước khi chết sao?&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; sảng kho&aacute;i gật đầu đồng &yacute;.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung kh&ocirc;ng nghĩ tới t&ecirc;n tiểu thử ngốc n&agrave;y sẽ như thế m&agrave; nhanh ch&oacute;ng đ&aacute;p ứng, y c&ograve;n nghĩ phải lừa t&ecirc;n ngốc n&agrave;y th&ecirc;m v&agrave;i c&acirc;u.<br />\r\n<br />\r\nAi, ngốc như vậy, bị người kh&aacute;c lừa đi th&igrave; phải l&agrave;m dao b&acirc;y giờ?&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung đứng dậy, hắn vừa đứng l&ecirc;n, so với Hồ Tiểu B&igrave; cao hơn nửa c&aacute;i đầu, m&agrave; Hồ Tiểu B&igrave; c&ograve;n cao chưa tới lỗ tai hắn.&nbsp;<br />\r\n<br />\r\nNh&igrave;n như vậy, Hồ Tiểu B&igrave; đ&atilde; c&oacute; vẻ nhỏ giờ c&ograve;n nhỏ hơn.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; cảm thấy bất m&atilde;n, trong ph&ograve;ng to&agrave;n bộ đều l&agrave; hơi thở nam t&iacute;nh của Ti&ecirc;u Chung, Hồ Tiểu B&igrave; cảm thấy hắn như người say. Nhận l&uacute;c c&ograve;n ch&uacute;t l&iacute; tr&iacute; cuối c&ugrave;ng Hồ Tiểu B&igrave; bước nhanh ra khỏi ph&ograve;ng, bởi v&igrave;, nếu c&ograve;n ở trong ph&ograve;ng hắn kh&ocirc;ng d&aacute;m đảm bảo sẽ ph&aacute;t sinh ra chuyện g&igrave; đ&acirc;u nha.&nbsp;<br />\r\n<br />\r\nĐầu bếp trưởng kinh ngạc khi nghe Hồ Tiểu B&igrave; n&oacute;i muốn xin nghỉ, phải biết rằng, Hồ Tiểu B&igrave; từ khi l&agrave;m việc đều kh&ocirc;ng xin nghỉ ph&eacute;p, hơn nữa, mỗi th&aacute;ng đều c&oacute; tiền thưởng. Nhưng h&ocirc;m nay l&agrave;m sao lại xin nghỉ, c&aacute;c m&oacute;n ăn cũng chưa l&agrave;m xong hắn l&agrave;m sao giải th&iacute;ch với c&aacute;c vị kh&aacute;ch đ&acirc;y?&nbsp;<br />\r\n<br />\r\nNhưng l&agrave; kh&ocirc;ng c&oacute; biện ph&aacute;p, cuối c&ugrave;ng đầu bếp trưởng vẫn bị lạc trong đ&ocirc;i mắt nhỏ của Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; đi rồi, đầu bếp trưởng ở ph&ograve;ng bếp bi phẫn m&agrave; n&oacute;i: &ldquo;Đại ca của ch&uacute;ng ta, xin nghỉ, xin nghỉ!&rdquo;<br />\r\n<br />\r\nSau đ&oacute; to&agrave;n thể mọi người trong bếp r&ecirc;n l&ecirc;n v&agrave;i tiếng.&nbsp;<br />\r\n<br />\r\nỞ dưới kh&aacute;ch sạn, Hồ Tiểu B&igrave; ngồi tr&ecirc;n xe thương vụ của Ti&ecirc;u Chung, cửa xe đ&oacute;ng lại, hắn ph&aacute;t hiện kh&ocirc;ng gian b&ecirc;n trong xe nhỏ hơn kh&ocirc;ng gian trong ph&ograve;ng ri&ecirc;ng l&uacute;c n&atilde;y, kia chẳng phải hắn sẽ ngửi được hơi thở của Ti&ecirc;u Chung gấp bội sao?!&nbsp;<br />\r\n<br />\r\nThuốc vi&ecirc;n*&hellip;&hellip;<br />\r\n<br />\r\n<em>(*editor cũng kh&ocirc;ng hiểu sao chỗ n&agrave;y l&agrave; thuốc vi&ecirc;n nữa ạ</em><em>&nbsp;)&nbsp;</em><br />\r\n<br />\r\n&Yacute; đồ muốn xuống xe, nhưng m&agrave; xe đ&atilde; chạy.<br />\r\n<br />\r\nHồ Tiểu B&igrave; che miệng mũi lại, nghi&ecirc;ng đầu nh&igrave;n ra ngo&agrave;i cửa sổ xem phong cảnh, k&ecirc;u Ti&ecirc;u Chung l&aacute;i xe với tốc độ cao.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung cảm thấy kỳ qu&aacute;i: &ldquo;Ngươi l&agrave; mắc tiểu hay l&agrave; đ&aacute;nh rắm?&rdquo;<br />\r\n<br />\r\n&ldquo;&hellip;&hellip;&rdquo; Hồ Tiểu B&igrave; lắc đầu.<br />\r\n<br />\r\nChe miệng mũi lại l&agrave; biện ph&aacute;p thật xuẩn a, trong chốc l&aacute;t hắn liền chịu kh&ocirc;ng nổi, n&oacute;i: &ldquo;Ta muốn mở cửa sổ.&rdquo;<br />\r\n<br />\r\n&ldquo;Như vậy mở cửa sổ sẽ rất lạnh?&rdquo; Ti&ecirc;u Chung cảm thấy tiểu ngốc tử n&agrave;y đầu &oacute;c nhất định c&oacute; vấn đề.<br />\r\n<br />\r\nHiện tại l&agrave; m&ugrave;a đ&ocirc;ng, tuy rằng ph&iacute;a nam kh&ocirc;ng c&oacute; tuyết, nhưng lại lạnh đến thấu xương.&nbsp;<br />\r\n<br />\r\nCuối c&ugrave;ng Hồ Tiểu B&igrave; vẫn l&agrave; mở cửa sổ, bất qu&aacute; hắn cũng chỉ mở một r&atilde;nh nhỏ, nhưng cũng kh&aacute; tốt, hắn nhẹ nh&otilde;m ngồi tr&ecirc;n ghế thở ra.&nbsp;<br />\r\n<br />\r\nBỗng nhi&ecirc;n, hắn nhớ tới một vấn đề, nếu l&agrave; nam nh&acirc;n n&agrave;y thật sự nấu ăn so với hắn hảo, kia chẳng phải l&agrave; hắn sẽ bị người nam nh&acirc;n n&agrave;y khiếu nại sao? Nếu ph&agrave;n n&agrave;n, danh tiếng sẽ kh&ocirc;ng tốt, kh&aacute;ch sạn vạn nhất kh&ocirc;ng cần hắn th&igrave; l&agrave;m sao b&acirc;y giờ?&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; lập tức ngồi thẳng, vấn đề n&agrave;y thật sự nghi&ecirc;m trọng.<br />\r\n<br />\r\nL&agrave;m sao b&acirc;y giờ, l&agrave;m c&aacute;ch n&agrave;o để nam nh&acirc;n n&agrave;y kh&ocirc;ng khiếu nại hắn, c&oacute; phải hay kh&ocirc;ng lấy l&ograve;ng nam nh&acirc;n n&agrave;y?<br />\r\n<br />\r\nKh&ocirc;ng gian b&ecirc;n trong xe đột nhi&ecirc;n xuất hiện một m&ugrave;i hương nh&agrave;n nhạt.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; tất nhi&ecirc;n kh&ocirc;ng biết, m&agrave; Ti&ecirc;u Chung b&ecirc;n cạnh ngửi thấy, hơn nữa, c&ograve;n ngửi ra được m&ugrave;i hương l&agrave; truyền ra từ người Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\nM&ugrave;i hương qua mũi Ti&ecirc;u Chung, rồi chậm r&atilde;i đi v&agrave;o ngực, khiến cho tim hắn đột nhi&ecirc;n nhảy l&ecirc;n một ch&uacute;t, l&agrave;m hắn thiếu ch&uacute;t kh&ocirc;ng giữ được tay l&aacute;i.&nbsp;<br />\r\n<br />\r\nNhưng miệng lưỡi Ti&ecirc;u Chung vẫn rất độc.&nbsp;<br />\r\n<br />\r\n&ldquo;Tại sao ngươi kh&ocirc;ng c&oacute; nam nh&acirc;n vị?&rdquo;<br />\r\n<br />\r\n_________________<br />\r\n<br />\r\n<em>T&aacute;c giả c&oacute; lời muốn n&oacute;i:&nbsp;</em><br />\r\n<br />\r\n<em>Hồ Tiểu B&igrave;:</em>&nbsp;To&agrave;n th&acirc;n ta mỗi một tế b&agrave;o, đều đang k&ecirc;u g&agrave;o &mdash; &ldquo;Ta sắp chịu kh&ocirc;ng nổi. Hắn đ&aacute;ng chết, kh&ocirc;ng chỗ n&agrave;o sắp đặt m&ecirc; người &ldquo;.&nbsp;</h2>', 0, 'chuong-1-dau-bep'),
(34, 19, 'Chương 2: Người đàn ông “hoang dã”', 'Chương 2: Người đàn ông “hoang dã”', '<h2>Hồ Tiểu B&igrave; vừa nghe &ldquo;Nam nh&acirc;n vị&rdquo;, m&iacute; mắt nhảy một ch&uacute;t, t&acirc;m n&oacute;i: Nếu ta c&oacute; nam nh&acirc;n vị, th&igrave; đ&atilde; kh&ocirc;ng xuống n&uacute;i.<br />\r\n<br />\r\nHồ Tiểu B&igrave; hiểu lầm &yacute; tứ của Ti&ecirc;u Chung, Ti&ecirc;u Chung chỉ l&agrave; độc miệng m&agrave; n&oacute;i, cố &yacute; muốn xem phản ứng của Hồ Tiểu B&igrave;. Nam nh&acirc;n b&igrave;nh thường khi nghe người kh&aacute;c n&oacute;i bản th&acirc;n kh&ocirc;ng c&oacute; &ldquo;nam nh&acirc;n vị*&rdquo;, khẳng định trong l&ograve;ng sẽ kh&ocirc;ng thoải m&aacute;i.&nbsp;<br />\r\n<br />\r\n<em>(*nam nh&acirc;n vị: nam t&iacute;nh)&nbsp;</em><br />\r\n<br />\r\nL&uacute;c trước khi Ti&ecirc;u Chung lần đầu ti&ecirc;n nh&igrave;n thấy Hồ Tiểu B&igrave;, đ&atilde; bị kinh ngạc, cảm th&aacute;n như thế n&agrave;o c&oacute; một nam h&agrave;i tử tinh xảo như thế, nhưng m&agrave; đối với c&aacute;c đồng sự th&igrave; thấy Hồ Tiểu B&igrave; như một &ldquo;Tiểu nương ph&aacute;o*&rdquo;<br />\r\n<br />\r\n<em>(*nương ph&aacute;o: Nương ph&aacute;o chỉ một người con trai c&oacute; c&aacute;ch cư xử, ăn n&oacute;i, t&iacute;nh c&aacute;ch giống con g&aacute;i, kh&ocirc;ng li&ecirc;n quan đến ngoại h&igrave;nh c&oacute; nữ t&iacute;nh hay kh&ocirc;ng, cũng kh&ocirc;ng li&ecirc;n quan đến người đ&oacute; l&agrave; trai thẳng hay kh&ocirc;ng.)&nbsp;</em><br />\r\n<br />\r\nKhẩu thị t&acirc;m phi th&ecirc;m độc miệng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y Hồ Tiểu B&igrave; c&uacute;i đầu n&oacute;i: &ldquo;Về sau sẽ c&oacute;.&rdquo;<br />\r\n<br />\r\nGiọng n&oacute;i c&oacute; ch&uacute;t nhỏ, như Ti&ecirc;u Chung vẫn c&oacute; thể nghe được, cảm thấy lời n&oacute;i n&agrave;y của Hồ Tiểu B&igrave; ấu trĩ đến đ&aacute;ng y&ecirc;u, liền cười.&nbsp;<br />\r\n<br />\r\nNhưng m&agrave; tiếng cười của Ti&ecirc;u Chung bị Hồ Tiểu B&igrave; nghe thấy, ch&iacute;nh l&agrave; cười nhạo, y đang cười nhạo hắn, cười nhạo một c&aacute;ch trần trụi, hừ.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; ở trước mặt Ti&ecirc;u Chung liền cảm thấy tự ti, đầu hận kh&ocirc;ng thể v&ugrave;i v&agrave;o ngực l&agrave;m đ&agrave; điểu.&nbsp;<br />\r\n<br />\r\nHắn bỗng nhớ tới nhiệm vụ b&iacute; mật m&agrave; sư phụ trước khi mất đ&atilde; giao cho hắn: &ldquo;T&igrave;m một người đ&agrave;n &ocirc;ng tinh kh&iacute; tr&agrave;n trề, gả cho hắn. Ban ng&agrave;y ngửi thấy hương vị đ&agrave;n &ocirc;ng tr&ecirc;n người hắn, buổi tối bị hắn &lsquo;tinh hoa&rsquo; dễ chịu, nếu kh&ocirc;ng, liền giống như vi sư m&agrave; chết đi&rdquo;.&nbsp;<br />\r\n<br />\r\nSư phụ Hồ Tiểu B&igrave; cũng l&agrave; một đại mỹ nh&acirc;n, bất qu&aacute; l&agrave; nam a. Hắn tuy l&agrave; hồ tộc, nhưng trời sinh l&agrave; thẳng, bẻ kh&ocirc;ng cong, hắn c&ograve;n b&agrave;i x&iacute;ch tiếp x&uacute;c với nam nh&acirc;n. Băng thanh ngọc khiết suốt đời, cuối c&ugrave;ng bởi v&igrave; kh&ocirc;ng bị nam nh&acirc;n &ldquo;dễ chịu&rdquo; n&ecirc;n phải qua đời ở tuổi 30.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; đi theo sư phụ học tr&ugrave; nghệ, sau khi đem sư phụ mai t&aacute;ng liền xuống n&uacute;i ho&agrave;n th&agrave;nh nhiệm vụ được giao.&nbsp;<br />\r\n<br />\r\nNhưng m&agrave; khi Hồ Tiểu B&igrave; tới nh&acirc;n gian, hắn một l&ograve;ng nh&agrave;o v&agrave;o tr&ugrave; nghệ, đ&atilde; qu&ecirc;n mất chuyện phải t&igrave;m nam nh&acirc;n.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; ph&aacute;t hiện, nếu hắn c&ugrave;ng nam nh&acirc;n ở c&ugrave;ng một chỗ, hắn nhất định sẽ ph&aacute;t t&igrave;nh. L&uacute;c đầu hắn đ&atilde; kh&ocirc;ng khống chế được, hai tai hồ ly sẽ hiện ra, nhưng sau n&agrave;y, hắn dần dần c&oacute; thể chống cự, c&oacute; thể khống chế bản th&acirc;n, n&ecirc;n mới kh&ocirc;ng bị người kh&aacute;c ph&aacute;t hiện. Thật sự nguy hiểm a, n&ecirc;u bị ph&aacute;t hiện, thật sự kh&ocirc;ng biết sẽ ra sao đ&acirc;y.&nbsp;<br />\r\n<br />\r\nNhưng tuy l&agrave; Hồ Tiểu B&igrave; hắn dễ ph&aacute;t t&igrave;nh thật, nhưng cũng kh&ocirc;ng biểu đạt hắn th&iacute;ch đối phương, hắn cảm tấn t&igrave;m được nam nh&acirc;n m&igrave;nh th&iacute;ch thật sự c&oacute; ch&uacute;t kh&oacute; khăn.&nbsp;<br />\r\n<br />\r\nKể từ khi xuống n&uacute;i, nam nh&acirc;n b&ecirc;n cạnh hắn l&agrave; người đầu ti&ecirc;n hắn thấy kinh diễm. Ngay khi nhớ đến sư phụ n&oacute;i &ldquo;Dễ chịu&rdquo;, h&ocirc; hấp Hồ Tiểu B&igrave; liền trở n&ecirc;n dồn dập. &ldquo;Th&igrave;nh thịch&rdquo;<br />\r\n<br />\r\nTai hồ ly b&ecirc;n tr&aacute;i nh&ocirc; ra khỏi m&aacute;i t&oacute;c m&agrave;u đen, m&agrave; Hồ Tiểu B&igrave; b&ecirc;n n&agrave;y c&ograve;n mơ mơ m&agrave;ng m&agrave;ng suy nghĩ cũng kh&ocirc;ng ph&aacute;t gi&aacute;c. Ti&ecirc;u Chung thấy người b&ecirc;n cạnh im lặng kh&ocirc;ng n&oacute;i g&igrave; thật l&acirc;u, vừa l&aacute;i xe vừa d&agrave;nh ra hau gi&acirc;y xem Hồ Tiểu B&igrave; hắn đang l&agrave;m g&igrave;.&nbsp;<br />\r\n<br />\r\n&ldquo;!!! &ldquo;<br />\r\n<br />\r\nKh&ocirc;ng nh&igrave;n kh&ocirc;ng biết, vừa nh&igrave;n thấy liền bị dọa sợ. C&aacute;i thứ m&agrave;u trắng tr&ecirc;n đầu l&agrave; tai sao? L&ocirc;ng x&ugrave; x&ugrave;, v&agrave;nh tai phiếm hồng, nh&igrave;n qua cảm thấy c&oacute; ch&uacute;t mềm mại. Hơn nữa chỉ c&oacute; một lỗ tai!?&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung quay đầu lại thật nhanh, tiếp tục l&aacute;i xe, nhưng t&acirc;m t&igrave;nh loạn cứ như gặp phải ma, lần đầu ti&ecirc;n trong đời thấy được cảnh quỷ dị như vậy. Y chớp chớp mắt, ho&agrave;i nghi m&igrave;nh vừa rồi c&oacute; phải sinh ảo gi&aacute;c hay kh&ocirc;ng.&nbsp;<br />\r\n<br />\r\nXe nhanh ch&oacute;ng đ&atilde; chạy đến gara, Ti&ecirc;u Chung dừng xe lại, quay sang nh&igrave;n Hồ Tiểu B&igrave; một lần nữa.&nbsp;<br />\r\n<br />\r\nKh&ocirc;ng thấy, vậy l&agrave; vốn kh&ocirc;ng c&oacute;?&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; vừa ngẩng đầu, liền nh&igrave;n thấy vẻ mặt nghi&ecirc;m t&uacute;c nh&igrave;n hắn của Ti&ecirc;u Chung, trong l&ograve;ng trầm xuống, vươn tay l&ecirc;n sờ, ph&aacute;t hiện tai hồ ky kh&ocirc;ng tho&aacute;t ra mới nhẹ nh&otilde;m thở ra.&nbsp;<br />\r\n<br />\r\n&ldquo;Ti&ecirc;n sinh, tới rồi sao?&rdquo;<br />\r\n<br />\r\nTi&ecirc;u Chung cởi bỏ đai an to&agrave;n: &ldquo;Tới rồi.&rdquo;<br />\r\n<br />\r\nHai người xuống xe, Ti&ecirc;u Chung khoanh tay nh&igrave;n Hồ Tiểu B&igrave; ph&iacute;a trước. Ti&ecirc;u Chung liền đi đến trước mặt Hồ Tiểu B&igrave;, vươn tay phải: &ldquo;Ti&ecirc;u Chung&rdquo;<br />\r\n<br />\r\nĐ&acirc;y l&agrave; muốn bắt tay đi?&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; nh&igrave;n tay đưa ra của Ti&ecirc;u Chung do dự, rồi cẩn thận vươn tay ra: &ldquo;T&ocirc;i t&ecirc;n Hồ Tiểu B&igrave;&rdquo;.&nbsp;<br />\r\n<br />\r\n&ldquo;Tiểu da?&rdquo; Ti&ecirc;u Chung nhướng m&agrave;y, giữ b&agrave;n tay Hồ Tiểu B&igrave; đang c&oacute; &yacute; thu hồi thật chặt.&nbsp;<br />\r\n<br />\r\n&ldquo;Nghịch ngợm* sao?&rdquo;<br />\r\n<br />\r\n&ldquo;Đ&uacute;ng vậy&rdquo;.&nbsp;<br />\r\n<br />\r\n(*t&ecirc;n Hồ Tiểu B&igrave; tiếng trung l&agrave; 胡小皮.&nbsp;<br />\r\n<br />\r\nM&agrave; từ 皮/p&iacute;/ c&oacute; nghĩa l&agrave; da, nghịch ngợm).&nbsp;<br />\r\n<br />\r\n&ldquo;Nhưng m&agrave; người kh&ocirc;ng gầy ch&uacute;t n&agrave;o, giống như kho&aacute;c da người(?)&rdquo;.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung quan s&aacute;t biểu cảm tr&ecirc;n khu&ocirc;n mặt của Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; bị dọa sợ, đột nhi&ecirc;n r&uacute;t tay về. Hồ Tiểu B&igrave; hắn ch&iacute;nh l&agrave; hồ ly kh&ocirc;ng sai, nhưng hồ ly kh&ocirc;ng kho&aacute;c da người. Hiện tại, hồ ly bọn họ muốn th&agrave;nh tinh cũng kh&ocirc;ng dễ d&agrave;ng g&igrave;, n&ecirc;n c&agrave;ng kh&ocirc;ng thể đi hại người. Bộ d&aacute;ng hiện tại của Hồ Tiểu B&igrave; hắn, l&agrave; h&oacute;a th&agrave;nh người một c&aacute;ch tự nhi&ecirc;n, kh&ocirc;ng phải d&ugrave;ng da người.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; vội v&agrave;ng giải th&iacute;ch:&rdquo; Kh&ocirc;ng đ&uacute;ng, thật sự kh&ocirc;ng phải&rdquo;.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung cười, đ&uacute;t tay v&ocirc; trong t&uacute;i quần t&acirc;y, nhẹ nh&agrave;ng n&oacute;i: &ldquo;T&ocirc;i n&oacute;i giỡn th&ocirc;i&rdquo; Ngươi khẩn trương c&aacute;i g&igrave; chứ?&nbsp;<br />\r\n<br />\r\nHai người c&ugrave;ng nhau v&agrave;o thang m&aacute;y, l&uacute;c n&agrave;y trong thang m&aacute;y, chỉ c&oacute; hai người bọn họ. Hồ Tiểu B&igrave; ph&aacute;t hiện kh&ocirc;ng gian trong thang m&aacute;y c&ograve;n nhỏ hơn kh&ocirc;ng gian ở trong xe, to&agrave;n bộ kh&ocirc;ng kh&iacute; b&ecirc;n trong thang m&aacute;y đều tr&agrave;n ngập hơi thở của Ti&ecirc;u Chung.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung l&agrave; một người c&oacute; hương vị đ&agrave;n &ocirc;ng hết sức mạnh mẽ, to&agrave;n th&acirc;n lộ ra sự hoang d&atilde; nhưng lại bị g&ograve; b&oacute;, giam cầm. Bộ t&acirc;y trang tr&ecirc;n người Ti&ecirc;u Chung l&agrave;m cho người ta cảm thấy như một con th&uacute; c&oacute; một bộ da kh&aacute;c bao bọc tr&ecirc;n người.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; đứng sau Ti&ecirc;u Chung, nh&igrave;n chằm chằm v&agrave; th&acirc;n thể Ti&ecirc;u Chung, Hồ Tiểu B&igrave; hắn rất &iacute;t khi nh&igrave;n chằm chằm chằm người kh&aacute;c, nhưng m&agrave;, d&aacute;ng người Ti&ecirc;u Chung thật sự rất hấp dẫn. N&oacute; như nam ch&acirc;m vậy, giữ chặt hắn, khiến Hồ Tiểu B&igrave; hắn kh&ocirc;ng thể tho&aacute;t ra.&nbsp;<br />\r\n<br />\r\nTới tầng 32, Ti&ecirc;u Chung đi ra trước dẫn đường, y ph&aacute;t hiện Hồ Tiểu B&igrave; kh&ocirc;ng được tự nhi&ecirc;n, rất khẩn trương, nhưng c&aacute;i g&igrave; cũng kh&ocirc;ng n&oacute;i. Ti&ecirc;u Chung xoay người nh&igrave;n Hồ Tiểu B&igrave; l&agrave;m c&aacute;i động t&aacute;c &ldquo;Thỉnh&rdquo;.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; nh&igrave;n nh&agrave; của Ti&ecirc;u Chung, hai ch&acirc;n như bị xi măng đ&ocirc;ng cứng lại, kh&ocirc;ng thể động đậy.&nbsp;<br />\r\n<br />\r\nĐ&acirc;y l&agrave; lần đầu ti&ecirc;n hắn đến nh&agrave; ở của nh&acirc;n loại, hơn nữa lại l&agrave; nh&agrave; của một nam nh&acirc;n. Tưởng tượng đến việc ở chung ph&ograve;ng với Ti&ecirc;u Chung như vậy, Hồ Tiểu B&igrave; liền cảm thấy lỗ tai c&oacute; ch&uacute;t ngứa, nhịn kh&ocirc;ng được muốn bật hai tai ra ngo&agrave;i.&nbsp;<br />\r\n<br />\r\n&ldquo;Cậu sợ c&aacute;i g&igrave;, t&ocirc;i sẽ ăn cậu sao?&rdquo;<br />\r\n<br />\r\nTi&ecirc;u Chung thấy Hồ Tiểu B&igrave; một bộ d&aacute;ng sắp &ldquo;Lừng lẫy hy sinh&rdquo;, liền nhịn kh&ocirc;ng được n&oacute;i ra một c&acirc;u.<br />\r\n<br />\r\nDa đầu Hồ Tiểu B&igrave; cứng lại, bước v&agrave;o trong nh&agrave;, hai ch&acirc;n như mang phải ch&igrave;.&nbsp;<br />\r\n<br />\r\nNgay khi c&aacute;nh cửa đ&oacute;ng lại, Hồ Tiểu B&igrave; liền đề ph&ograve;ng cảnh gi&aacute;c, động vật trời sinh đ&atilde; c&oacute; khả năng tự vệ kh&ocirc;ng thể nghi ngờ.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung vẫn quan s&aacute;t nhất cử nhất động của Hồ Tiểu B&igrave;, nhưng m&agrave; y vẫn kh&ocirc;ng thấy xảy ra chuyện g&igrave; bất thường. L&uacute;c n&agrave;y chỉ đống quần &aacute;o tr&ecirc;n sofa, kh&ocirc;ng ch&uacute;t kh&aacute;ch kh&iacute; n&agrave;o n&oacute;i: &ldquo;Đem quần &aacute;o của t&ocirc;i ra ban c&ocirc;ng, ở đ&oacute; c&oacute; một c&aacute;i m&aacute;y giặt, mở m&aacute;y giặt ra, bỏ quần &aacute;o v&agrave;o l&agrave; được&rdquo;<br />\r\n<br />\r\nCuối c&ugrave;ng c&ograve;n bỏ th&ecirc;m c&acirc;u: &ldquo;Cảm ơn.&rdquo;<br />\r\n<br />\r\nN&oacute;i xong liền bắt đầu cởi t&acirc;y trang tr&ecirc;n người. Hồ Tiểu B&igrave; l&uacute;c n&agrave;y v&igrave; kh&ocirc;ng muốn xem Ti&ecirc;u Chung cởi quần &aacute;o, Hồ Tiểu B&igrave; giống như bị kim đ&acirc;m, lập tức &ocirc;m lấy đống quần &aacute;o tr&ecirc;n ghế sofa m&agrave; chạy.&nbsp;<br />\r\n<br />\r\n&ldquo;Sai rồi, đ&oacute; l&agrave; ph&ograve;ng bếp.&rdquo; Ti&ecirc;u Chung thấy Hồ Tiểu B&igrave; chạy loạn, m&agrave; nhắc nhở.&nbsp;<br />\r\n<br />\r\n&ldquo;Lại sai rồi, đ&oacute; l&agrave; ph&ograve;ng ngủ.&rdquo; Ti&ecirc;u Chung liền nh&igrave;n chỗ Hồ Tiểu B&igrave; chạy m&agrave; n&oacute;i.<br />\r\n<br />\r\nCuối c&ugrave;ng Hồ Tiểu B&igrave; cũng nhảy được đến ban c&ocirc;ng, Ti&ecirc;u Chung cười khẽ một tiếng, lắc đầu, cầm lấy tạp dề m&agrave;u x&aacute;m, đi đến tủ lạnh, một b&ecirc;n xem nguy&ecirc;n liệu nấu ăn suy x&eacute;t xem n&ecirc;n l&agrave;m m&oacute;n g&igrave;, một b&ecirc;n lại thắt d&acirc;y lưng tập dề.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung cầm nguy&ecirc;n liệu nấu ăn đi v&agrave;o trong bếp, nghe thấy động tĩnh từ ban c&ocirc;ng, &acirc;m thanh ph&aacute;t ra từ m&aacute;y giặt truyền đến, Ti&ecirc;u Chung mới y&ecirc;n t&acirc;m m&agrave; nấu ăn.&nbsp;<br />\r\n<br />\r\nL&uacute;c Hồ Tiểu B&igrave; đi đến ph&ograve;ng bếp, nh&igrave;n thấy người đ&agrave;n &ocirc;ng cao lớn hơi khom lưng, tr&ecirc;n người mang một c&aacute;i tạp dề m&agrave;u x&aacute;m, tr&ecirc;n người mặc một c&aacute;i &aacute;o sơ mi sọc c&ugrave;ng m&agrave;u, được sơ vin ở quần t&acirc;y.&nbsp;<br />\r\n<br />\r\nNh&igrave;n như vậy, khiến cho Hồ Tiểu B&igrave; cảm thấy sự hoang d&atilde; của Ti&ecirc;u Chung trở n&ecirc;n &ocirc;n h&ograve;a hơn ch&uacute;t, giống như, sư tử bị cạo l&ocirc;ng.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; kh&ocirc;ng d&aacute;m nh&igrave;n Ti&ecirc;u Chung nữa, hắn vẫn như cũ rất cảnh gi&aacute;c, duỗi tay sờ sờ đầu, x&aacute;c định lỗ tai kh&ocirc;ng bị hiện ra.&nbsp;<br />\r\n<br />\r\nĐối với người kh&aacute;c, Hồ Tiểu B&igrave; đảm bảo lỗ tai hồ ly của hắn sẽ kh&ocirc;ng hiện ra, nhưng l&agrave; ở chung với Ti&ecirc;u Chung th&igrave; thực sự kh&oacute; n&oacute;i a.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; đi qua, ngoan ngo&atilde;n m&agrave; đứng một b&ecirc;n nh&igrave;n, tuy nhi&ecirc;n vẫn duy tr&igrave; một khoảng c&aacute;ch nhất định, tựa như sợ Ti&ecirc;u Chung xắt rau ở tr&ecirc;n người hắn.&nbsp;<br />\r\n<br />\r\n&ldquo;Đ&acirc;y l&agrave;&hellip;&hellip; L&agrave;m c&aacute; hương thịt ti*?&rdquo; Hồ Tiểu B&igrave; hỏi.<br />\r\n<br />\r\n<em>(*&rdquo;C&aacute; hương thịt ti&rdquo;: thịt băm hương c&aacute;. T&ecirc;n gốc 做的鱼香肉丝.&nbsp;&nbsp;)</em><br />\r\n<br />\r\n&ldquo;Cậu cho rằng t&ocirc;i l&agrave;m h&agrave;nh hương cua kiềm*?&rdquo; Ti&ecirc;u Chung.<br />\r\n<br />\r\n<em>(*muốn biết th&ecirc;m chi tiết vui l&ograve;ng xem lại chương 1)&nbsp;</em><br />\r\n<br />\r\n&ldquo;T&ocirc;i cho rằng&hellip;&hellip;&rdquo;<br />\r\n<br />\r\n&ldquo;M&oacute;n kia l&atilde;ng ph&iacute; thời gian, m&oacute;n n&agrave;y cũng tương tự. T&agrave;i năng nấu nướng n&agrave;y, v&ocirc; luận l&agrave;m m&oacute;n g&igrave;, đều c&oacute; thể thấy được bản lĩnh&rdquo;. Ti&ecirc;u Chung n&oacute;i.&nbsp;<br />\r\n<br />\r\nL&agrave;m h&agrave;nh hương cua kiềm?<br />\r\n<br />\r\nTi&ecirc;u Chung bật cười trong l&ograve;ng, c&aacute;i t&ecirc;n tiểu tử ngốc n&agrave;y, l&uacute;c ấy Ti&ecirc;u Chung n&oacute;i đồ ăn của Hồ Tiểu B&igrave; c&oacute; vấn đề, thật ra, l&agrave; t&igrave;m một c&aacute;i cớ để lừa người ta, kh&ocirc;ng ngờ Hồ Tiểu B&igrave; t&ecirc;n ngốc n&agrave;y lại tin l&agrave; thật, lại c&ograve;n kh&ocirc;ng hề nghi ngờ m&agrave; theo y về.&nbsp;<br />\r\n<br />\r\nMay mắn t&ecirc;n tiểu tử ngốc n&agrave;y l&agrave; bị y lừa, nếu như bị người xấu lừa đi, l&agrave;m sao nhịn được vẻ ngo&agrave;i của t&ecirc;n tiểu tử n&agrave;y, t&ecirc;n ngốc Hồ Tiểu B&igrave; n&agrave;y sớm bị ăn sạch sẽ rồi.&nbsp;<br />\r\n<br />\r\nN&oacute;i giống như Ti&ecirc;u Chung y l&agrave; người tốt kh&ocirc;ng bằng.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; lại bị Ti&ecirc;u Chung tiếp tục lừa, cảm thấy Ti&ecirc;u Chung n&oacute;i rất đ&uacute;ng, nghi&ecirc;m t&uacute;c m&agrave; gật đầu.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; nh&igrave;n c&aacute;ch Ti&ecirc;u Chung nấu ăn, thật l&agrave; tay gi&agrave; đời, nhưng l&agrave; vẫn c&oacute; ch&uacute;t vấn đề, l&uacute;c Hồ Tiểu B&igrave; n&oacute;i ra, Ti&ecirc;u Chung lại n&oacute;i một chuỗi l&yacute; do, Hồ Tiểu B&igrave; lại tin lần nữa.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; cũng kh&ocirc;ng n&oacute;i tiếp nữa, Ti&ecirc;u Chung l&agrave; một luật sư cao cấp, t&agrave;i ăn thuộc h&agrave;ng bậc nhất, cấp dưới đặt cho y danh hiệu &ldquo;Trạc lậu* đại thần&rdquo;. &Yacute; nghĩa như t&ecirc;n, ch&iacute;nh l&agrave; chuy&ecirc;n m&ocirc;n t&igrave;m ra c&aacute;c lỗi logic trong diễn đạt của người kh&aacute;c. Chẳng sợ người ta đ&uacute;ng, Ti&ecirc;u Chung y đều c&oacute; thể &ldquo;xả&rdquo; ra một tập hợp c&aacute;c lời phản b&aacute;c &ldquo;Rất c&oacute; logic&rdquo;, khiến cho rất nhiều người mắc kẹt trong bẫy của hắn.&nbsp;<br />\r\n<br />\r\n<em>(*Trạc 戳: ch&acirc;m chọc</em><br />\r\n<br />\r\n<em>Lậu 漏: để lộ, bỏ s&oacute;t, trốn tr&aacute;nh)</em><br />\r\n<br />\r\nGiống Hồ Tiểu B&igrave; n&agrave;y một t&ecirc;n tiểu bạch thiệp thế kh&ocirc;ng th&acirc;m*, tự nhi&ecirc;n l&agrave; bị &ldquo;Trạc lậu đại thần&rdquo; lừa đến xoay v&ograve;ng.&nbsp;<br />\r\n<br />\r\n<em>(*thiệp thế kh&ocirc;ng th&acirc;m: bước v&agrave;o đời một c&aacute;ch thật th&agrave;)&nbsp;</em><br />\r\n<br />\r\nCuối c&ugrave;ng m&oacute;n ăn đ&atilde; được ho&agrave;n th&agrave;nh, m&ugrave;i hương của m&oacute;n &ldquo;C&aacute; hương thịt ti&rdquo; tỏa ra tứ ph&iacute;a, Hồ Tiểu B&igrave; tạm thời kh&ocirc;ng ngửi thấy m&ugrave;i hương tr&ecirc;n người Ti&ecirc;u Chung.&nbsp;<br />\r\n<br />\r\n&ldquo;Thơm qu&aacute;&rdquo;.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung đắc &yacute;, nhưng lại cố gắng kiềm chế &yacute; cười tr&ecirc;n mặt. Ti&ecirc;u Chung cởi tạp dề xuống, bưng đồ ăn ra ngo&agrave;i, tr&ecirc;n tay c&ograve;n c&oacute; một đ&ocirc;i đũa.&nbsp;<br />\r\n<br />\r\nC&aacute;c m&oacute;n ăn đều đủ sắc đủ hương sắc, được b&agrave;y tr&ecirc;n b&agrave;n đ&aacute; cẩm thạch, c&ugrave;ng với những hoa văn tr&ecirc;n b&agrave;n ăn, tạo n&ecirc;n bối cảnh ưu nh&atilde;, phụ trợ cho m&oacute;n ăn.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung gắp một miếng c&agrave; t&iacute;m, đưa tới b&ecirc;n miệng Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\n&ldquo;Nếm thử, nam nh&acirc;n vị&rdquo;<br />\r\n<br />\r\nNam nh&acirc;n vị?<br />\r\n<br />\r\nHồ Tiểu B&igrave; tự nhi&ecirc;n lại c&oacute; ch&uacute;t hổ thẹn, nh&igrave;n b&agrave;n tay Ti&ecirc;u Chung cầm đũa, gắp thức ăn cho hắn, tim hắn liền đột ngột đập nhanh.&nbsp;<br />\r\n<br />\r\nNếu m&agrave; đ&ocirc;i b&agrave;n tay đẹp đẽ n&agrave;y, c&oacute; thể sờ sờ l&ocirc;ng tr&ecirc;n cằm, hoặc tr&ecirc;n bụng th&igrave; tốt biết mấy, nhất định sẽ rất thoải m&aacute;i, Hồ Tiểu B&igrave; hắn nhất định sẽ rất ung dung m&agrave; hưởng thụ.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; h&aacute; miệng, đem miếng c&agrave; t&iacute;m nhỏ bỏ v&agrave;o miệng.&nbsp;<br />\r\n<br />\r\n&ldquo;! &ldquo;<br />\r\n<br />\r\nTrong nh&aacute;y mắt, Hồ Tiểu B&igrave; cảm thấy to&agrave;n bộ thế với đều ph&aacute;t s&aacute;ng, đỉnh đầu nổ tung, c&ograve;n c&oacute; mấy ph&aacute;o hoa bảy m&agrave;u.&nbsp;<br />\r\n<br />\r\n&ldquo;Ngon, ngon, ăn rất ngon! &ldquo;<br />\r\n<br />\r\nThật sự l&agrave; si&ecirc;u cấp mĩ vị, c&ugrave;ng với m&oacute;n ăn m&agrave; Hồ Tiểu B&igrave; l&agrave;m thực sự kh&ocirc;ng giống nhau. Kh&ocirc;ng chỉ c&oacute; cảm x&uacute;c, m&agrave; c&ograve;n cả hương vị, hỏa hậu*, đều rất kh&aacute;c, phi thường v&oacute; cảm gi&aacute;c bay.&nbsp;<br />\r\n<br />\r\n<em>(*hỏa hậu 火候 nghĩa l&agrave;: lượng nhiệt sử dụng trong nấu ăn cũng như thời gian.&nbsp;</em><br />\r\n<br />\r\n<em>Nguồn: baidu)&nbsp;</em><br />\r\n<br />\r\n&ldquo;Th&igrave;nh thịch&hellip; Th&igrave;nh thịch! &ldquo;<br />\r\n<br />\r\nHai lỗ tai hồ ly xuất hiện, c&ograve;n hưng phấn m&agrave; run rẩy.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung l&uacute;c n&agrave;y đảm bảo, ch&iacute;nh m&igrave;nh kh&ocirc;ng bị hoa mắt, tận mắt nh&igrave;n thấy lỗ tai hồ ly xuất hiện từ t&oacute;c Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\nL&uacute;c n&agrave;y hai người, c&oacute; c&ograve;n di chuyển đ&acirc;u!<br />\r\n<br />\r\nNhững thứ n&agrave;y đều l&agrave; thật sự&hellip;&hellip;<br />\r\n<br />\r\nTi&ecirc;u Chung trong l&ograve;ng thực sự kinh ngạc, thật l&acirc;u kh&ocirc;ng c&oacute; phản ứng. Cho d&ugrave; trước đ&acirc;y, từng đối mặt với rất nhiều luật sư cao cấp kh&aacute;c, th&igrave; cũng kh&ocirc;ng bị thua. Tay cầm đũa của Ti&ecirc;u Chung cứng đờ, nh&igrave;n v&agrave;o người vẫn đang hưởng thụ m&oacute;n ăn, kh&ocirc;ng hề ph&aacute;t gi&aacute;c phản ứng của Ti&ecirc;u Chung, Hồ Tiểu B&igrave;.&nbsp;<br />\r\n<br />\r\nHồ Tiểu B&igrave; bị mỹ thực l&agrave;m mất đi t&iacute;nh cảnh gi&aacute;c, vui sướng hỏi Ti&ecirc;u Chung: &ldquo;Đ&acirc;y l&agrave; nam nh&acirc;n vị? &ldquo;<br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng&rdquo;&nbsp;<br />\r\n<br />\r\nHầu kết Ti&ecirc;u Chung giật giật, nh&igrave;n Hồ Tiểu B&igrave;, yết hầu thắt lại.&nbsp;<br />\r\n<br />\r\n&ldquo;N&agrave;y kh&ocirc;ng phải, m&oacute;n n&agrave;y chỉ l&agrave; c&oacute; ch&uacute;t hơi thở của t&ocirc;i m&agrave; th&ocirc;i, nam nh&acirc;n vị ch&acirc;n ch&iacute;nh, ở tr&ecirc;n người t&ocirc;i. &ldquo;<br />\r\n<br />\r\n&ldquo;?&rdquo;Hồ Tiểu B&igrave; tỏ vẻ kh&ocirc;ng hiểu.<br />\r\n<br />\r\nTi&ecirc;u Chung cảm thấy Hồ Tiểu B&igrave; ngu như vậy, hẳn l&agrave; sẽ kh&ocirc;ng c&ugrave;ng y diễn &ldquo;Hồ y&ecirc;u h&iacute; tinh&rdquo; trong Li&ecirc;u Trai* đi?&nbsp;<br />\r\n<br />\r\n<em>(*Li&ecirc;u Trai: l&agrave; tiểu thuyết Li&ecirc;u Trai Ch&iacute; Dị. T&ecirc;n Tiếng h&aacute;n l&agrave; 聊齋志異, với &yacute; nghĩa l&agrave; những chuyện qu&aacute;i dị ch&eacute;p ở căn nh&agrave; tạm, l&agrave; tập truyện ngắn gồm 431 thi&ecirc;n, ra đời v&agrave;o đầu đời nh&agrave; Thanh (cuối thế kỷ 17) của nh&agrave; văn TQ Bồ T&ugrave;ng Linh. Bộ truyện n&agrave;y được coi l&agrave; một k&igrave; thư v&agrave; được đ&aacute;nh gi&aacute; l&agrave; đỉnh cao của tiểu thuyết văn ng&ocirc;n thời cổ đại. T&aacute;c phẩm n&agrave;y cũng được dịch sang tiếng Việt v&agrave; c&oacute; chuyển thể th&agrave;nh Phim. Phim điện ảnh như: Thiếu nữ u hồn, Họa B&igrave;, Họa B&iacute;ch, Đại chiến &acirc;m dương. Phim truyền h&igrave;nh: Li&ecirc;u Trai, Li&ecirc;u trai I, Li&ecirc;u Trai II, Li&ecirc;u Trai Kỳ nữa, Họa B&igrave; &hellip;.&nbsp;</em><br />\r\n<br />\r\n<em>Nguồn:wikipedia)&nbsp;</em><br />\r\n<br />\r\nTi&ecirc;u Chung đ&atilde; hưởng qua nam nh&acirc;n vị sao?&nbsp;<br />\r\n<br />\r\nBiết l&agrave;m c&aacute;ch n&agrave;o kh&ocirc;ng?<br />\r\n<br />\r\nCảm thụ qua c&aacute;i gọi l&agrave; &ldquo;dễ chịu&rdquo; sao?&nbsp;<br />\r\n<br />\r\nKhẳng định l&agrave; kh&ocirc;ng c&oacute;.&nbsp;<br />\r\n<br />\r\nTi&ecirc;u Chung bu&ocirc;ng đũa xuống, tạm thời kh&ocirc;ng t&iacute;nh n&oacute;i cho Hồ Tiểu B&igrave; biết về chuyện tai hắn.&nbsp;<br />\r\n<br />\r\n&ldquo;Thế n&agrave;o, t&ocirc;i n&oacute;i kh&ocirc;ng sai đi, m&oacute;n ăn của cậu c&oacute; một m&ugrave;i h&ocirc;i. &ldquo;<br />\r\n<br />\r\n&ldquo;&hellip;&hellip;&rdquo; Hồ Tiểu B&igrave;.<br />\r\n<br />\r\nTi&ecirc;u Chung: &ldquo;Đ&oacute; l&agrave; bởi v&igrave;, n&oacute; đến từ ch&iacute;nh bản th&acirc;n cậu&rdquo;<br />\r\n<br />\r\n_______<br />\r\n<br />\r\n<em>T&aacute;c giả c&oacute; lời muốn n&oacute;i:&nbsp;</em><br />\r\n<br />\r\n<em>Hồ Tiểu B&igrave;:</em>&nbsp;Ta muốn giải oan&hellip;&hellip;<br />\r\n<br />\r\n<em>Tiểu ng&acirc;n h&agrave;</em>: Mở miệng n&oacute;i.<br />\r\n<br />\r\n<em>Hồ Tiểu B&igrave;</em>: Ti&ecirc;u Chung n&oacute;i tr&ecirc;n người ta c&oacute; một cỗ tao vị, nhưng đ&oacute; l&agrave; m&ugrave;i hương.<br />\r\n<br />\r\n<em>Tiểu ng&acirc;n h&agrave;:</em>&nbsp;Vậy chứng minh a.</h2>', 0, 'chuong-2-nguoi-dan-ong-“hoang-da”');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(35, 21, 'Chương 01: Yêu bạn trai hotboy của bạn cùng phòng', 'Chương 01: Yêu bạn trai hotboy của bạn cùng phòng', '<h2>T&ocirc;i y&ecirc;u bạn trai của bạn c&ugrave;ng ph&ograve;ng, c&aacute;i n&agrave;y quả thật l&agrave; kh&ocirc;ng c&oacute; đạo đức, t&ocirc;i biết, nhưng m&agrave; ai bảo t&ocirc;i l&agrave; một c&ocirc; n&agrave;ng t&acirc;m cơ cơ chứ.<br />\r\n<br />\r\nT&ocirc;i t&ecirc;n Tống Nhu, lớn l&ecirc;n cũng kh&ocirc;ng được xinh đẹp cho lắm, ưu điểm duy nhất l&agrave; d&aacute;ng người v&ocirc; c&ugrave;ng gợi cảm, ngực cup D, l&agrave;n da cũng tương đối trắng, thế n&ecirc;n người theo đuổi t&ocirc;i cũng kh&ocirc;ng &iacute;t, nhưng t&ocirc;i đ&atilde; c&oacute; người m&igrave;nh th&iacute;ch rồi.<br />\r\n<br />\r\nT&ocirc;i th&iacute;ch hotboy &mdash;&mdash; Kinh Phong.<br />\r\n<br />\r\nC&oacute; ai m&agrave; kh&ocirc;ng th&iacute;ch cậu ấy chứ, đẹp trai, th&agrave;nh t&iacute;ch tốt, c&ograve;n l&agrave; thiếu gia con nh&agrave; si&ecirc;u gi&agrave;u, t&iacute;nh c&aacute;ch khi&ecirc;m tốn lễ ph&eacute;p, nh&acirc;n phẩm thượng thừa.<br />\r\n<br />\r\nLần đầu ti&ecirc;n nh&igrave;n thấy Kinh Phong l&agrave; đang ngồi nghe giảng trong học viện, tr&ugrave;ng hợp như vậy, t&ocirc;i lại ngồi ở b&ecirc;n cạnh cậu ấy.<br />\r\n<br />\r\nH&ocirc;m đ&oacute;, t&ocirc;i ngửi thấy m&ugrave;i hương đặc trưng tr&ecirc;n người cậu ấy, trong l&uacute;c mọi người đang chăm ch&uacute; nghe giảng trong ph&ograve;ng học th&igrave; t&ocirc;i lại đạt tới cao tr&agrave;o, l&agrave; c&aacute;i loại do t&acirc;m l&yacute; hưng phấn khiến th&acirc;n thể kh&ocirc;ng chịu khống chế m&agrave; cao tr&agrave;o, kh&ocirc;ng một ai ph&aacute;t hiện, bọn họ c&ograve;n tưởng rằng t&ocirc;i &uacute;p mặt tr&ecirc;n b&agrave;n l&agrave; bởi v&igrave; cảm mạo. Sau giờ tan học, chờ đến khi mọi người rời đi, t&ocirc;i nh&iacute;ch m&ocirc;ng sang vị tr&iacute; m&agrave; cậu ấy đ&atilde; ngồi rồi tự an ủi, ph&iacute;a dưới ướŧ &aacute;ŧ đến kh&ocirc;ng nỡ nh&igrave;n.<br />\r\n<br />\r\nTừ đ&oacute; về sau, t&ocirc;i lu&ocirc;n kh&ocirc;ng thể tự kiềm chế m&ecirc; luyến muốn l&ecirc;n giường với cậu ấy.<br />\r\n<br />\r\nNhưng m&agrave; hotboy thường sẽ quen với hoa kh&ocirc;i, hoa kh&ocirc;i lại ch&iacute;nh l&agrave; bạn c&ugrave;ng ph&ograve;ng của t&ocirc;i &mdash;&mdash; Thẩm Giai Giai.<br />\r\n<br />\r\nHai người từ khi l&ecirc;n cấp ba đ&atilde; l&agrave; một đ&ocirc;i, v&agrave;o đại học cũng hợp t&igrave;nh hợp l&yacute; thi đậu v&agrave;o c&ugrave;ng trường nổi tiếng. Một người l&agrave; hoa kh&ocirc;i một người l&agrave; hotboy, quả thực đ&uacute;ng chuẩn như trong truyện cổ t&iacute;ch vậy.<br />\r\n<br />\r\nĐ&aacute;ng tiếc l&agrave; trong c&acirc;u chuyện cổ t&iacute;ch đẹp đẽ n&agrave;y lại c&oacute; một nữ phụ độc &aacute;c lu&ocirc;n t&ugrave;y l&uacute;c nh&ograve;m ng&oacute;, đ&oacute; ch&iacute;nh l&agrave; t&ocirc;i.<br />\r\n<br />\r\nĐối với cậu ấy, cho d&ugrave; c&oacute; t&igrave;nh tay hai hay tay ba đi chăng nữa, t&ocirc;i cũng kh&ocirc;ng để t&acirc;m lắm. D&aacute;ng vẻ của Kinh Phong tuyệt đối kh&ocirc;ng phải l&agrave; biểu hiện của một người đang say đắm trong t&igrave;nh y&ecirc;u cuồng nhiệt, ngược lại c&agrave;ng giống như kiểu t&igrave;m một người bạn g&aacute;i th&iacute;ch hợp, tận hưởng l&uacute;c hai người ở b&ecirc;n nhau. Bọn họ kh&ocirc;ng c&oacute; c&atilde;i nhau, kh&ocirc;ng c&oacute; tranh chấp, đương nhi&ecirc;n cũng kh&ocirc;ng c&oacute; qu&aacute; nhiều ngọt ng&agrave;o.<br />\r\n<br />\r\nV&agrave;o th&aacute;ng 7, Thẩm Giai Giai v&agrave; Kinh Phong quyết định đi du lịch ở Thanh Hải. T&ocirc;i biết trong lần du lịch n&agrave;y hai người t&iacute;nh to&aacute;n định d&acirc;ng lần đầu ti&ecirc;n cho nhau, trở th&agrave;nh một đ&ocirc;i trai g&aacute;i y&ecirc;u nhau đ&uacute;ng nghĩa danh xứng với thực.<br />\r\n<br />\r\nT&ocirc;i quyết định &ldquo;th&ograve; một ch&acirc;n&rdquo; v&agrave;o.<br />\r\n<br />\r\n&ldquo;Tiểu Giai, tớ muốn t&igrave;m hiểu một ch&uacute;t về cậu bạn Cao Thi&ecirc;n Vũ cũng ở k&yacute; t&uacute;c x&aacute;, nhưng lại thấy ngại qu&aacute;&hellip;&hellip;&rdquo;<br />\r\n<br />\r\nThời điểm n&oacute;i những lời n&agrave;y, mặt t&ocirc;i đỏ bừng, thể hiện một n&eacute;t thẹn th&ugrave;ng của con g&aacute;i đến v&ocirc; c&ugrave;ng thuần thục.<br />\r\n<br />\r\nTiểu Giai đơn thuần n&ecirc;n cũng kh&ocirc;ng nghĩ nhiều, c&ograve;n v&ocirc; c&ugrave;ng vui vẻ:<br />\r\n<br />\r\n&ldquo;Trời ạ, Tiểu Nhu h&oacute;a ra cậu l&agrave; c&oacute; &yacute; tứ với Cao Thi&ecirc;n Vũ &agrave;, trước đ&oacute; Cao Thi&ecirc;n Vũ c&ograve;n nhờ tớ đi thử l&ograve;ng cậu một ch&uacute;t. Cậu m&agrave; kh&ocirc;ng n&oacute;i ra, tớ c&ograve;n tưởng rằng cậu kh&ocirc;ng c&oacute; hứng th&uacute; chứ!&rdquo;<br />\r\n<br />\r\n&ldquo;Tiểu Giai, tớ, tớ cũng kh&ocirc;ng r&otilde; nữa&hellip;&hellip; Tớ kh&ocirc;ng th&acirc;n với cậu ấy lắm, tớ n&ecirc;n l&agrave;m g&igrave; b&acirc;y giờ?&rdquo;<br />\r\n<br />\r\nT&ocirc;i mờ mịt nh&igrave;n Tiểu Giai, dường như c&ograve;n tiện mồm cảm th&aacute;n một c&acirc;u:<br />\r\n<br />\r\n&ldquo;Cậu c&ugrave;ng Kinh Phong chuẩn bị đi Thanh Hải hả? Th&iacute;ch gh&ecirc;, tớ vẫn lu&ocirc;n mong được đến nơi n&agrave;y một lần.&rdquo;<br />\r\n<br />\r\n&Aacute;nh mắt Thẩm Giai Giai s&aacute;ng l&ecirc;n, h&aacute;o hức đề nghị:<br />\r\n<br />\r\n&ldquo;Ch&agrave;, đ&uacute;ng đ&uacute;ng đ&uacute;ng, ch&uacute;ng m&igrave;nh c&oacute; thể bốn người c&ugrave;ng đi Thanh Hải nha, vừa vặn tớ c&ugrave;ng A Phong chỉ c&oacute; hai người cũng c&oacute; ch&uacute;t nh&agrave;m ch&aacute;n, c&oacute; hai cậu đi c&ugrave;ng sẽ chơi c&agrave;ng vui hơn&hellip;&hellip; Đến l&uacute;c đ&oacute; tớ l&agrave;m cố vấn gi&uacute;p cậu, tạo cơ hội để hai người được ở chung với nhau, cậu cảm thấy thế n&agrave;o?&rdquo;Thấy t&ocirc;i thẹn th&ugrave;ng gật đầu, Tiểu Giai v&ocirc; c&ugrave;ng vui vẻ, hưng phấn đi li&ecirc;n hệ với hai ch&agrave;ng trai c&ograve;n lại.<br />\r\n<br />\r\nT&ocirc;i hơi hơi cong kh&oacute;e miệng, cực kỳ chờ mong lần đi chơi xa th&uacute; vị n&agrave;y!<br />\r\n<br />\r\n--------------<br />\r\n<br />\r\nNg&agrave;y đầu ti&ecirc;n lữ h&agrave;nh, t&ocirc;i gặp Kinh Phong, cũng gặp được Cao Thi&ecirc;n Vũ.<br />\r\n<br />\r\nVẻ ngo&agrave;i của Kinh Phong thật lạnh l&ugrave;ng, nhưng bất luận đối với ai cũng đều duy tr&igrave; phong độ th&acirc;n sĩ cơ bản, thoạt nh&igrave;n liền biết l&agrave; kh&ocirc;ng phải con c&aacute;i của một gia đ&igrave;nh b&igrave;nh thường c&oacute; thể dạy dỗ được như vậy.<br />\r\n<br />\r\nCao Thi&ecirc;n Vũ lại thuộc về loại h&igrave;nh &aacute;nh mặt trời tỏa nắng ấm &aacute;p, th&iacute;ch vận động, tuy lớn l&ecirc;n kh&ocirc;ng được đẹp trai khiến người người kh&aacute;c muốn rụng trứng như Kinh Phong, nhưng cũng l&agrave; một anh ch&agrave;ng s&aacute;ng sủa dễ nh&igrave;n.<br />\r\n<br />\r\nBốn người ch&uacute;ng t&ocirc;i ngồi tr&ecirc;n xe bu&yacute;t, trong l&uacute;c c&atilde;i nhau ầm ĩ th&igrave; đ&atilde; tới T&acirc;y Ninh.<br />\r\n<br />\r\nSau khi đến T&acirc;y Ninh, ch&uacute;ng t&ocirc;i thu&ecirc; một chiếc xe con địa phương, dọc theo đường v&ograve;ng du lịch, thấy địa phương n&agrave;o hứng th&uacute; th&igrave; chơi đến vui vẻ mới th&ocirc;i, c&ograve;n địa phương n&agrave;o kh&ocirc;ng c&oacute; hứng th&uacute;, chỉ hạ cửa k&iacute;nh xe xuống ng&oacute; sơ qua m&agrave; th&ocirc;i, sau đ&oacute; lại tiếp tục h&agrave;nh tr&igrave;nh.<br />\r\n<br />\r\nCuối c&ugrave;ng l&agrave; ngồi m&aacute;y bay, bay đến hồ Thanh Hải.<br />\r\n<br />\r\nNg&agrave;y h&ocirc;m đ&oacute; cũng l&agrave; sinh nhật t&ocirc;i.<br />\r\n<br />\r\nSau khi kết th&uacute;c h&agrave;nh tr&igrave;nh ban ng&agrave;y, ch&uacute;ng t&ocirc;i về tới ph&ograve;ng nghỉ, t&ocirc;i v&agrave; Tiểu Giai một ph&ograve;ng, hai ch&agrave;ng trai một ph&ograve;ng, tất cả đều l&agrave; ph&ograve;ng giường đ&ocirc;i.<br />\r\n<br />\r\nTa lấy c&aacute;i b&aacute;nh kem nhỏ l&uacute;c n&atilde;y mua ở cửa h&agrave;ng dưới lầu ra, từng miếng từng miếng ăn xong, kh&oacute;e mắt kh&ocirc;ng tự gi&aacute;c mang theo hai giọt nước mắt.<br />\r\n<br />\r\nVừa l&uacute;c quay đầu bị Tiểu Giai thấy được, c&ocirc; n&agrave;ng vốn dĩ bởi v&igrave; đang xem h&agrave;i m&agrave; cười ha ha một c&aacute;ch ng&acirc;y dại:<br />\r\n<br />\r\n&ldquo;Tiểu Nhu, sao thế, sao cậu lại kh&oacute;c?&rdquo;<br />\r\n<br />\r\nT&ocirc;i lau lau nước mắt, như l&agrave; kh&ocirc;ng n&eacute;n được, n&oacute;i ra việc h&ocirc;m nay l&agrave; sinh nhật t&ocirc;i, kể lể về ho&agrave;n cảnh gia đ&igrave;nh phức tạp của t&ocirc;i, tủi th&acirc;n n&oacute;i t&ocirc;i chưa từng được ai tổ chức một buổi sinh nhật trọn vẹn&hellip;&hellip; L&agrave; chuyện xưa ch&acirc;n thật m&agrave; th&ecirc; thảm của t&ocirc;i.<br />\r\n<br />\r\nQuả nhi&ecirc;n, Tiểu Giai đau l&ograve;ng kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nLập tức đ&aacute;nh thức hai bạn nam ở ph&ograve;ng c&aacute;ch v&aacute;ch, gọi kh&aacute;ch sạn đặt một bữa tiệc lớn, k&ecirc;u bia, muốn cho t&ocirc;i một bữa sinh nhật thật l&agrave; ho&agrave;nh tr&aacute;ng.<br />\r\n<br />\r\nBốn tiếng sau, nh&igrave;n ba người uống say khướt, t&ocirc;i lặng lẽ cười.</h2>', 0, 'chuong-01-yeu-ban-trai-hotboy-cua-ban-cung-phong'),
(36, 22, 'Chương 1: Chết đi sống lại', 'Chương 1: Chết đi sống lại', '<h2>Những giọt nắng yếu ớt cuối ng&agrave;y cũng dần tắt liệm bỏ lại trong kh&ocirc;ng gian bao la l&agrave; một mảnh đen tối trầm mặc như t&acirc;m hồn của Ivy l&uacute;c n&agrave;y &ldquo;lại một ng&agrave;y v&ocirc; nghĩa tr&ocirc;i qua&rdquo; nghĩ thầm n&agrave;ng khẽ nhếch m&ocirc;i tự giễu bản th&acirc;n<br />\r\n<br />\r\nKh&eacute;p quyển s&aacute;ch đang đọc dỡ tr&ecirc;n tay , đưa đ&ocirc;i mắt v&ocirc; hồn nh&igrave;n v&agrave;o khoảng hư kh&ocirc;ng xa xa . B&oacute;ng tối c&ugrave;ng sự im lặng kh&ocirc;ng khỏi khiến Ivy chạnh l&ograve;ng hồi tưởng những chuyện đ&atilde; xảy ra : n&agrave;ng vốn c&oacute; một gia đ&igrave;nh hạnh ph&uacute;c , vốn l&agrave; đứa v&ocirc; ưu v&ocirc; lo như bao đứa nhỏ ng&acirc;y thơ kh&aacute;c &hellip; Rồi một ng&agrave;y cha n&agrave;ng phản bội vợ con bỏ theo người phụ nữ kh&aacute;c . Mẹ n&agrave;ng chọn sự giải tho&aacute;t bằng c&aacute;ch uống cả trăm vi&ecirc;n thuốc ngủ . Ivy c&ograve;n nhớ &ocirc;m x&aacute;c b&agrave; lạnh băng trong đ&ocirc;i tay b&eacute; nhỏ n&agrave;ng đau l&ograve;ng tột độ đau đến bất tri bất gi&aacute;c . M&atilde;i đến khi c&oacute; người ph&aacute;t hiện b&aacute;o cảnh s&aacute;t . Sau đ&oacute; n&agrave;ng được đưa v&agrave;o c&ocirc; nhi viện . Suốt hai năm ở đ&acirc;y n&agrave;ng như một người đ&atilde; chết kh&ocirc;ng để t&acirc;m đến bất cứ thứ g&igrave;.<br />\r\n<br />\r\nMột buổi s&aacute;ng cuối đ&ocirc;ng năm Ivy l&ecirc;n mười , ph&aacute;t hiện &aacute;nh mắt người đ&agrave;n b&agrave; xinh đẹp chăm ch&uacute; d&ograve; x&eacute;t m&igrave;nh . Ivy r&uacute;ng động kh&ocirc;ng tự chủ bước về ph&iacute;a người phụ nữ đ&ocirc;i mắt h&uacute;t hồn kia . Im lặng ngước nh&igrave;n , thật l&acirc;u sau người phụ nữ khẽ mỉm cười n&oacute;i &ldquo;c&oacute; muốn đi c&ugrave;ng ta kh&ocirc;ng nh&oacute;c con ?&rdquo; thu lại &aacute;nh mắt d&ograve; x&eacute;t b&agrave; ta l&agrave;m động t&aacute;c nheo mắt tiếp lời &ldquo;ta sẽ dạy con lầy bất kỳ thứ g&igrave; con muốn&rdquo; . Trong v&ocirc; thức Ivy gật đầu . Đ&oacute; l&agrave; Alice một si&ecirc;u trộm nổi tiếng thế giới &hellip;<br />\r\n<br />\r\nSuốt mười hai năm kh&ocirc;ng ngừng cố gắng dưới sự dạy dỗ khắc nghiệt của mẹ nu&ocirc;i Alice , Ivy trở th&agrave;nh &ldquo;nữ đạo ch&iacute;ch si&ecirc;u cấp&rdquo; . N&agrave;ng cũng dần ch&ocirc;n chặt qu&aacute; khứ buồn v&agrave;o l&ograve;ng vui vẻ lạc quan hơn . N&agrave;ng th&iacute;ch tự do - muốn l&agrave;m g&igrave; th&igrave; l&agrave;m , qu&iacute; trọng sinh mạng &ndash; từng chết đi sống lại c&agrave;ng hiểu r&otilde; - được sống đ&atilde; l&agrave; hạnh ph&uacute;c v&igrave; vậy n&agrave;ng c&agrave;ng phải sống thật tốt . Nhưng lại kh&ocirc;ng thể y&ecirc;u thương một ai thật l&ograve;ng n&agrave;ng sợ như mẹ - t&igrave;nh cảm khiến người ta yếu đuối mất tự chủ m&agrave; n&agrave;ng th&igrave; ko th&iacute;ch như vậy &hellip;<br />\r\n<br />\r\nReng &hellip;&hellip;.. Reng &hellip;&hellip;&hellip;.<br />\r\n<br />\r\nĐiện thoại reo vang k&eacute;o Ivy trở về với thực tế . Bước nhanh đến b&agrave;n để điện thoại tiện thể đem th&acirc;n thể lười biếng ch&ocirc;n người v&agrave;o sofa cầm chiếc điện thoại thấy d&atilde;y số lạ Ivy chần chờ rồi nhấn n&uacute;t nghe &ldquo; Alo ! xin hỏi ai đầu&hellip;&rdquo; chưa kịp n&oacute;i hết c&acirc;u đầu d&acirc;y b&ecirc;n kia truyền đến giọng n&oacute;i ngọt ng&agrave;o đầy sủng nịch của mẹ nu&ocirc;i &ldquo; Ivy c&oacute; nhớ mama ko ? L&aacute;y xe ra s&acirc;n bay T đ&oacute;n mama đi , mama c&oacute; qu&agrave; cho con ! mama đợi , nhanh nh&eacute; !&rdquo; chưa kịp trả lời đầu d&acirc;y b&ecirc;n kia đ&atilde; c&uacute;p m&aacute;y<br />\r\n<br />\r\nĐ&uacute;ng t&aacute;c phong b&aacute; đạo của mẹ nu&ocirc;i Ivy ch&aacute;n nản lắc đầu miễn cưỡng đứng dậy với tay lấy ch&igrave;a kh&oacute;a chiếc Audi A6 mui trần nhanh ch&oacute;ng ra khỏi cửa l&aacute;y xe đến s&acirc;n bay T . Gi&oacute; đ&ecirc;m se lạnh khẽ r&ugrave;ng m&igrave;nh nhưng n&agrave;ng lại th&iacute;ch cảm gi&aacute;c buốt buốt da thịt thế n&agrave;y . Với tay mở một ca kh&uacute;c s&ocirc;i động Ivy nh&uacute;ng nh&uacute;ng vai theo điệu nhạc .<br />\r\n<br />\r\nXe đang chạy với tốc độ kh&aacute; cao tr&ecirc;n đường bỗng nhi&ecirc;n một đứa b&eacute; băng ngang trước đầu xe , Ivy giật nảy m&igrave;nh phản ứng v&ocirc; điều kiện n&agrave;ng xoay mạnh v&ocirc;-lăng đứa b&eacute; tho&aacute;t khỏi c&aacute;i chết trong gang tấc nhưng &hellip;<br />\r\nẦm... tiếng chiếc xe va đập mạnh v&agrave;o cột điện , trong khoảng khắc cuối c&ugrave;ng Ivy thấy m&aacute;u của m&igrave;nh chảy ra rất rất nh&igrave;u . Kh&ocirc;ng n&agrave;ng l&agrave; si&ecirc;u trộm quốc tế lu&ocirc;n sẵn s&agrave;ng đối mặt nguy hiểm thậm ch&iacute; l&agrave; c&aacute;i chết nhưng &hellip; nhưng l&yacute; do phải l&agrave; bị s&uacute;ng bắn hay bị bắt rồi thủ ti&ecirc;u chứ ... Sao lại l&agrave; v&igrave; tai nạn giao th&ocirc;ng v&igrave; tr&aacute;nh một đứa nh&oacute;c . Thế n&agrave;y &ldquo;đồng nghiệp&rdquo; trong giới &ldquo;đạo ch&iacute;ch&rdquo; sẽ cười khinh bỉ n&agrave;ng mất (lạy chị =.= chết đến nơi c&ograve;n sĩ diện) . Nghĩ đến đ&acirc;y n&agrave;ng thầm rủa &ldquo;tổ sư người n&agrave;o để đứa b&eacute; chạy lung tung ra đường hại n&agrave;ng chết oan&rdquo; . Cơ thể đau nhức như vừa bị nghiền dưới m&aacute;y xay thịt , khiến &yacute; thức n&agrave;ng dần liệm đi trong m&ecirc; mang n&agrave;ng kh&ocirc;ng ngừng cầu nguyện &ldquo;&ocirc;ng trời t&ocirc;i ko muốn chết thế n&agrave;y . T&ocirc;i muốn sống &hellip; muốn sống &hellip; sống &hellip;&rdquo;</h2>', 0, 'chuong-1-chet-di-song-lai'),
(37, 22, 'Chương 2: Xuyên qua Ai Cập cổ đại', 'Chương 2: Xuyên qua Ai Cập cổ đại', '<h2>Ko phải l&agrave; cơn đau thịt n&aacute;t xương tan như vừa rồi nhưng cũng kh&ocirc;ng thể n&oacute;i ho&agrave;n to&agrave;n &ldquo;dễ chịu&rdquo; . Bị đ&aacute;nh thức bởi tiếng n&oacute;i ồn &agrave;o xung quanh , Ivy mệt mỏi cố mở mắt trong t&igrave;nh trạng to&agrave;n th&acirc;n &ecirc; ẩm . Điều đầu ti&ecirc;n Ivy nhận thức l&agrave; xung quanh c&oacute; rất nhiều người họ n&oacute;i thứ ng&ocirc;n ngữ m&agrave; Ivy chưa nghe qua bao giờ (mặc d&ugrave; số quốc gia n&agrave;ng &ldquo;gh&eacute; thăm&rdquo; tr&ecirc;n thế giới ko phải l&agrave; &iacute;t) nhưng kỳ lạ l&agrave; n&agrave;ng lại hiểu lời họ n&oacute;i . M&agrave; c&aacute;ch ăn mặc của những người n&agrave;y rất giống trong phim Cleopatra m&agrave; n&agrave;ng từng xem &ldquo;đang quay phim sao ?&rdquo; rất nhanh n&agrave;ng loại bỏ suy nghĩ n&agrave;y v&igrave; cảnh tượng trước mắt thật đến trăm phần trăm . Bằng thần kinh nhạy b&eacute;n Ivy li&ecirc;n kết những sự kiện đ&atilde; x&atilde;y ra &ldquo;tai nạn - ng&ocirc;n ngữ kỳ lạ - trang phục người ai cập cổ đại&rdquo; v&agrave;i gi&acirc;y sau n&agrave;ng nở một nụ cười rộng đến m&eacute;p tai &ldquo;m&igrave;nh c&ograve;n sống , sống l&agrave; tốt , sống l&agrave; qu&aacute; tốt &hellip;&rdquo; kh&ocirc;ng ngừng lặp đi lặp lại như thế , rồi chợt nh&iacute;u chặt đ&ocirc;i m&agrave;y khiến nụ cười chưa kịp tắt kết hợp với đ&ocirc;i m&agrave;y nh&iacute;u lại nh&igrave;n mặt Ivy l&uacute;c n&agrave;y qu&aacute;i gỡ v&ocirc; c&ugrave;ng mất tự nhi&ecirc;n &ldquo;xuy&ecirc;n kh&ocirc;ng , n&agrave;ng xuy&ecirc;n kh&ocirc;ng&rdquo; . Từ vui mừng chuyển sang lo lắng chỉ trong v&ograve;ng 1 c&aacute;i chớp mắt chắc cũng chỉ c&oacute; người n&agrave;y l&agrave;m được =.=<br />\r\n<br />\r\nIvy phục hồi trạng th&aacute;i b&igrave;nh tĩnh ngay sau đ&oacute; . N&agrave;ng mặc kệ c&oacute; xuy&ecirc;n hay kh&ocirc;ng với n&agrave;ng &ldquo;sống&rdquo; l&agrave; quan trọng nhất . Kh&ocirc;ng l&agrave;m si&ecirc;u trộm ở thế kỷ 21 được th&igrave; ta về cổ đại &ldquo;trộm vặt&rdquo; cũng kh&ocirc;ng tồi , n&agrave;ng kh&ocirc;ng tin với &ldquo;t&agrave;i năng đạo ch&iacute;ch&rdquo; của m&igrave;nh lại chết đ&oacute;i ở đ&acirc;y đ&acirc;u &hellip; &ldquo;Lũ n&ocirc; lệ khốn kiếp&rdquo; tiếng h&eacute;t ch&oacute;i tay của t&ecirc;n b&eacute;o &uacute; n&eacute;t mặt dữ tợn căng l&ecirc;n b&oacute;ng lưỡng v&igrave; lớp mỡ d&agrave;y vang l&ecirc;n &ldquo;phải nghe lời những c&aacute;c vị chủ nh&acirc;n mua ch&uacute;ng m&agrave;y kh&ocirc;ng th&igrave; tao đ&aacute;nh chết &rdquo; hắn h&eacute;t tiếp , tay phải cầm c&aacute;i roi da vung l&ecirc;n quật &ldquo;bộp&rdquo; xuống nền đất . Xong hắn lại hướng về đ&aacute;m đ&ocirc;ng nở nụ cười giả tạo &ldquo;ch&agrave;o h&agrave;ng&rdquo; Ivy chỉ muốn x&ocirc;ng l&ecirc;n đập v&agrave;o c&aacute;i bản mặt thối kia cho hắn &ldquo;ngủm&rdquo; lu&ocirc;n . Nh&igrave;n lại bản th&acirc;n l&uacute;c n&agrave;y n&agrave;ng ph&aacute;t hiện cơ thể hơi đau nhức rất nhiều vết thương mới c&oacute; cũ c&oacute; to&agrave;n bộ l&agrave; vết roi &ldquo;chết tiện , l&atilde;o b&eacute;o khốn kiếp d&aacute;m đ&aacute;nh n&agrave;ng , c&oacute; cơ hội n&agrave;ng sẽ cho ngươi th&agrave;nh heo quay&rdquo; thầm rủa . Hai tay bị tr&oacute;i g&ocirc; sau lưng bằng sợi thừng to đ&ugrave;ng buộc chặt đến nỗi cỗ tay đều rớm m&aacute;u . N&agrave;ng hừ lạnh d&ugrave;ng một ch&uacute;t xảo thuật nới lỏng sợi d&acirc;y chuyện n&agrave;y đối với si&ecirc;u trộm như n&agrave;ng qu&aacute; dễ d&agrave;ng , kh&ocirc;ng vội th&aacute;o d&acirc;y ho&agrave;n to&agrave;n n&agrave;y quyết định &ldquo;&ocirc;m c&acirc;y đợi khỉ&rdquo; xem t&igrave;nh h&igrave;nh kh&ocirc;ng kh&eacute;o đ&aacute;nh b&agrave;i chuồn kh&ocirc;ng được bị hắn giết oan th&igrave; toi đời . Lại nghĩ &ldquo;&Ocirc;ng trời , người ta xuy&ecirc;n kh&ocirc;ng th&igrave; l&agrave;m c&ocirc;ng ch&uacute;a , vương phi &hellip; quyền cao chức trọng , ăn ngon mặc đẹp , v&agrave;ng bạc đầy nh&agrave; &hellip; c&ograve;n m&igrave;nh xuy&ecirc;n qua l&agrave;m n&ocirc; lệ bị đ&aacute;nh , bị mắng c&ograve;n bị xem như thứ đồ rẻ tiền ko đ&aacute;ng gi&aacute; 5 x&acirc;u tiền nữa . hazzz bất c&ocirc;ng qu&aacute; , bất c&ocirc;ng qu&aacute; &hellip;&rdquo; c&agrave;ng nghĩ c&agrave;ng bực n&agrave;ng quyết định kh&ocirc;ng th&egrave;m nghĩ nữa d&ugrave; sao rất nhanh th&ocirc;i n&agrave;ng sẽ đi t&igrave;m tự do của m&igrave;nh<br />\r\n<br />\r\nCũng đến l&uacute;c Ivy bị t&ecirc;n b&eacute;o &ldquo;ch&agrave;o h&agrave;ng&rdquo; . Hắn th&ocirc; lỗ k&eacute;o c&aacute;nh tay tr&aacute;i nhỏ nhắn của n&agrave;ng buộc n&agrave;ng đứng dậy &ldquo;C&aacute;c qu&iacute; &ocirc;ng gh&eacute; qua xem , đ&acirc;y l&agrave; &ldquo;h&agrave;ng&rdquo; đ&aacute;ng gi&aacute; nhất trong đ&aacute;m nộ lệ lần n&agrave;y nữ n&ocirc; 14 tuổi c&oacute; m&agrave;u da trắng hồng mềm mịn như lụa hiếm c&oacute; , mười x&acirc;u tiền&rdquo; . Th&acirc;n thể n&agrave;y mới 14 tuổi h&egrave;n chi mỏng manh qu&aacute; , m&agrave; m&agrave;u da của n&agrave;ng hiện giờ so với người ở đ&acirc;y đ&uacute;ng l&agrave; trắng hơn một ch&uacute;t nhưng cũng đ&acirc;u c&oacute; g&igrave; lạ lắm sao lại đ&aacute;ng gi&aacute; gấp đ&ocirc;i người kh&aacute;c . Lại nh&igrave;n &aacute;nh mắt bọn người ở dưới nh&igrave;n ch&ograve;ng chọc v&agrave;o n&agrave;ng như đang đ&aacute;nh gi&aacute; m&oacute;n h&agrave;ng Ivy cực kỳ kh&oacute; chịu&hellip;Trong l&uacute;c bọn họ đang mặc cả thương lượng &ldquo;gi&aacute;&rdquo; mua n&agrave;ng &hellip; Th&igrave; ph&iacute;a xa chợ bu&ocirc;n b&aacute;n n&ocirc; lệ huy&ecirc;n n&aacute;o truyền đến &acirc;m thanh nhốn nh&aacute;o &ldquo;ho&agrave;ng đế đang cưỡi ngựa qua đ&acirc;y&rdquo; , &ldquo;ho&agrave;ng đế Ramesses II đến đấy&rdquo; , ph&iacute;a trước lần lượt cuối người như đợt s&oacute;ng truyền đến người ph&iacute;a sau li&ecirc;n thanh &ldquo;ho&agrave;ng đế vạn tuế&rdquo; &hellip; Ivy nghe r&otilde; tiếng th&igrave; thầm v&ocirc; số thiếu nữ &ldquo;ho&agrave;ng thượng đẹp trai qu&aacute;&rdquo; , &ldquo;ng&agrave;i thật dũng m&atilde;nh , ki&ecirc;u h&ugrave;ng&rdquo; , &ldquo;sự lạnh l&ugrave;ng của ng&agrave;i cũng thật đẹp&rdquo; &hellip; Ramesses II sao ? Ivy kh&ocirc;ng nghi&ecirc;n cứu s&acirc;u lịch sử Ai cập cỗ đại nhưng lại v&ocirc; c&ugrave;ng hứng th&uacute; với đồ cổ hơn 3000 tuổi đương nhi&ecirc;n cũng biết t&ecirc;n tuổi của vị pharaon nổi tiếng n&agrave;y . Hiếu kỳ muốn xem dung nhan &ldquo;người nổi tiếng cổ kim&rdquo; nghe th&ecirc;m những lời t&aacute;n thưởng của những thiếu nữ kia t&acirc;m l&yacute; th&iacute;ch ngắm trai đẹp của con g&aacute;i ai m&agrave; kh&ocirc;ng c&oacute; . N&agrave;ng trộm liếc mắt về ph&iacute;a con hắc m&atilde; phi như bay xa xa tr&ecirc;n lưng n&oacute; l&agrave; một thiếu ni&ecirc;n chừng 17 , 18 tuổi d&aacute;ng người oai phong bờ vai rộng vạm vỡ như v&aacute;c được cả bầu trời , l&agrave;ng da m&agrave;u n&acirc;u đồng dưới &aacute;nh mặt trời ch&oacute;i chang kết hợp v&agrave;i giọt mồ h&ocirc;i chảy qua khu&ocirc;n ngực rắn chắc tỏa ra thứ &aacute;nh s&aacute;ng b&oacute;ng lo&aacute;ng ẩn hiện đẹp kh&ocirc;ng sao tả xiết . Hắn để trần phần tr&ecirc;n , phần dưới chỉ quấn quanh một miếng vải lanh trắng xếp nếp được đ&iacute;nh lại ở phần h&ocirc;ng mạnh mẽ bằng thắt lưng v&agrave;ng nạm ngọc trang phục n&agrave;y được gọi l&agrave; skhen-ti . Ở Ai Cập cổ đại skhen-ti rất thịnh h&agrave;nh v&igrave; ch&uacute;ng kh&aacute; dễ mặc , kh&ocirc;ng c&oacute; đường may nhưng được quấn rất tỉ mĩ v&agrave; tinh tế . Chiếc skhen-ti nữa k&iacute;nh nữa hở t&ocirc;n l&ecirc;n cơ bụng s&aacute;u m&uacute;i v&agrave; cặp ch&acirc;n thon d&agrave;i chắc khỏe của thiếu ni&ecirc;n . Ivy thầm tặc lưỡi &ldquo;wa , chuẩn hơn cả si&ecirc;u mẫu nam ấy chứ!&rdquo; . D&aacute;ng người th&iacute; qu&aacute; &ldquo;hot&rdquo; kh&ocirc;ng biết mặt mũi thế n&agrave;o xa qu&aacute; nh&igrave;n hổng r&otilde; chờ một ch&uacute;t hắn qua đ&acirc;y phải hảo hảo nh&igrave;n một phen &hellip;<br />\r\n(Vải lanh : vải được l&agrave;m từ loạt c&acirc;y c&ugrave;ng t&ecirc;n trồng rất nhiều ở ven hai b&ecirc;n bờ s&ocirc;ng Nile . L&agrave; loại vải được người Ai Cập thời bấy giờ rất ưa chuộng)</h2>', 0, 'chuong-2-xuyen-qua-ai-cap-co-dai'),
(38, 22, 'Chương 3: Pharaon Ramesses II', 'Chương 3: Pharaon Ramesses II', '<h2>&ldquo;Địa b&agrave;n&rdquo; của l&atilde;o b&eacute;o l&agrave; s&aacute;t lề đường l&uacute;c ho&agrave;ng đế ngang qua th&igrave; sẽ được &ldquo;chi&ecirc;m ngưỡng&rdquo; ng&agrave;i kh&aacute; r&otilde; r&agrave;ng . Đang cao hứng với suy nghĩ &ldquo;ngắm zai đẹp&rdquo; &aacute;nh mắt hướng thẳng ph&iacute;a trước d&otilde;i theo b&oacute;ng h&igrave;nh cưỡi tr&ecirc;n con hắc m&atilde; sắp đến kia . &ldquo;Oa ,oa &hellip;..&rdquo; theo phản xạ Ivy chuyển &aacute;nh mắt về hướng ngược lại nơi c&oacute; tiếng kh&oacute;c đứa b&eacute; vang l&ecirc;n . Đứa b&eacute; khoảng 3 tuổi đang đứng giữa đường hai tay dụi dụi mắt . Ngước &aacute;nh mắt ng&acirc;y thơ t&igrave;m mẹ . Giữa biển người , người đ&agrave;n b&agrave; h&eacute;t ch&oacute;i tai cầu khẩn &ldquo;cứu con t&ocirc;i , ai cứu con t&ocirc;i &hellip;&rdquo; nhưng kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o chen qua đ&aacute;m đ&ocirc;ng . Lại m&ocirc;t lần nữa phản xạ th&acirc;n thể nhanh hơn bộ n&atilde;o Ivy lao ra đường , kh&ocirc;ng biết d&acirc;y tr&oacute;i được n&agrave;ng vứt đi l&uacute;c n&agrave;o . Trong v&ocirc; thức n&agrave;ng chỉ kịp dang tay &ocirc;m lấy đứa b&eacute; . Tiếng v&oacute; ngựa rất gần , thập ch&iacute; l&agrave; đ&atilde; ngay b&ecirc;n cạnh . Trong gi&acirc;y ph&uacute;t sinh tử n&agrave;ng thầm rủa bản th&acirc;n &ldquo;m&igrave;nh thật ngu , một lần chết v&igrave; cứu đứa nh&oacute;c ở thế kỷ 21 d&acirc;m xe v&agrave;o cột điện b&acirc;y giờ lại sắp chết v&igrave; cứu đứa nh&oacute;c cổ đại n&agrave;y để bị ngựa giẫm , kh&ocirc;ng lẽ số m&igrave;nh chết v&igrave; trẻ con&hellip;&rdquo; kh&oacute;c kh&ocirc;ng ra nước mắt trong gi&acirc;y l&aacute;t n&agrave;ng cố ng&atilde; người sang l&agrave;ng đường gần nhất lấy th&acirc;n m&igrave;nh che k&iacute;nh người đứa b&eacute; với hi vọng t&ecirc;n ho&agrave;ng đế kia kỹ thuật cưỡi ngựa tốt một ch&uacute;t để &hellip; chỉ cho ngựa giẫm hai ch&acirc;n n&agrave;ng th&ocirc;i c&ugrave;ng lắm th&igrave; t&agrave;n tận kh&ocirc;ng đến nổi phải chết &hellip;<br />\r\n<br />\r\nH&iacute;&iacute;&iacute;&iacute;&iacute;&hellip;&hellip;&hellip; chỉ thấy b&oacute;ng đen cả người lẫn ngựa trong gang tấc phi th&acirc;n l&ecirc;n kh&ocirc;ng rồi đ&aacute;p xuống ph&iacute;a trước tiếng phanh ngựa x&eacute; m&agrave;ng nhỉ &ldquo;thần th&aacute;nh ơi , n&agrave;ng c&ograve;n sống , t&ecirc;n n&agrave;y kỹ thuật cưỡi ngựa qu&aacute; kh&ocirc;ng tồi , kh&ocirc;ng tồi ch&uacute;t n&agrave;o&hellip; qu&aacute; tuyệt&hellip;..&rdquo; đang ph&aacute;t đi&ecirc;n vui mừng kh&ocirc;n siết v&igrave; tho&aacute;t khỏi tử thần lần thứ hai th&igrave; &hellip; con người đen trước mặt l&ecirc;n tiếng &ldquo;muốn chết ?&rdquo; Hai từ lạnh băng tho&aacute;t ra từ kh&oacute;e m&ocirc;i mỏng đầy gợi cảm kia &ldquo;đẹp&hellip;đẹp trai qu&aacute;&hellip;&hellip;&hellip;.!&rdquo; v&ocirc; thức nuốt nước bọt một c&aacute;i . T&ecirc;n n&agrave;y c&oacute; một vẻ y&ecirc;u nghiệt chết người . Đ&ocirc;i m&agrave;y rậm đế vương , đ&ocirc;i mắt phượng xếch ngược đầy cao qu&iacute; được t&ocirc; bằng bột than xanh ghi c&agrave;ng nổi bật vạn phần (ng&agrave;y xưa pharaon Ai Cập cũng vẽ mắt nh&eacute;) nhưng đặc biệt nhất l&agrave; tr&ograve;ng mắt n&acirc;u nhạt hiếm c&oacute; của hắn quyến rũ hớp hồn nh&igrave;n v&agrave;o như bị th&ocirc;i mi&ecirc;n kh&ocirc;ng dời đi được . Chiếc mũi cao thẳng c&acirc;n xứng c&ugrave;ng đ&ocirc;i m&ocirc;i mỏng m&iacute;m chặt tất cả kết hợp tạo n&ecirc;n sự y&ecirc;u nghiệt lạnh l&ugrave;ng khiến người kh&aacute;c kh&ocirc;ng r&eacute;t m&agrave; rung. Qu&aacute; ho&agrave;n hảo con người n&agrave;y muốn uy phong c&oacute; uy phong , muốn tướng mạo c&oacute; tướng mạo . Lại nuốt xuống một ngụm nước bọt Ivy kh&ocirc;ng kh&aacute;ch kh&iacute; &ldquo;ngắm nh&igrave;n miễn ph&iacute;&rdquo; t&aacute;c phẩm phi thường tuyệt mĩ nam của tạo h&oacute;a &hellip;<br />\r\n<br />\r\nNgười thiếu ni&ecirc;n cảm thấy bực bội v&igrave; &aacute;nh mắt &ldquo;h&aacute;o sắc&rdquo; của thiếu nữ n&agrave;y . Lại c&oacute; cả loại phụ nữ nh&igrave;n đ&agrave;n &ocirc;ng th&egrave;m thuồng chảy nước bọt ra ngo&agrave;i , m&agrave; người đ&agrave;n &ocirc;ng đ&oacute; lại l&agrave; một pharaon quyền lực tối cao l&agrave; hắn . Nheo đ&ocirc;i mắt phượng n&eacute;m cho đối phương c&aacute;i nh&igrave;n khinh bỉ , tay cầm kiếm tuốt khỏi vỏ mũi kiếm hướng thẳng mi t&acirc;m thiếu nữ nghiến răng gằn từng tiếng &ldquo;v&ocirc; lễ&rdquo;. Kh&ocirc;ng thoải m&aacute;i khi bị vật thể lạ che đi tằm mắt Ivy đưa tay phải gạt nhẹ dị vật sang một b&ecirc;n ti&ecirc;n thể đưa tay tr&aacute;i l&ecirc;n quẹt nước bọt tr&ecirc;n miệng . Bất ngờ trước h&agrave;nh động liều lĩnh của thiếu nữ Ramesses thu lại kiếm đưa ch&acirc;n đạp n&agrave;ng một c&aacute;i . Vai truyền đến cơn đau đớn tức th&igrave; Ivy tỉnh t&aacute;o hẳn &ldquo;hoa hồng đ&uacute;ng l&agrave; đầy gai nhọn , mĩ nam n&agrave;y c&oacute; b&ocirc; mặt thi&ecirc;n thần nhưng lại thực &aacute;c quỷ&rdquo; t&ecirc;n chết tiện , bạo lực phụ nữ nếu ở thế kỷ 21 n&agrave;ng sẽ kiện cho hắn đi t&ugrave; . Nhưng giờ ph&uacute;t n&agrave;y n&agrave;ng nhận ra hiện thực lu&ocirc;n t&agrave;n nhẫn v&ocirc; c&ugrave;ng . T&ecirc;n cuồng bạo lực trước mặt l&agrave; pharaon quyền lực v&ocirc; hạn c&ograve;n n&agrave;ng chỉ l&agrave; một n&ocirc; lệ mạng kh&ocirc;ng bằng con kiến hix . Nắm r&otilde; t&igrave;nh h&igrave;nh trước mắt Ivy nhanh tr&iacute; n&eacute;n đau đớn cố nặn ra nụ cười gượng gạo chỉnh lại tư thế rất cung k&iacute;nh cuối đầu hướng người trước mặt &ldquo;bệ hạ vạn tuế&rdquo; kh&ocirc;ng d&aacute;m ngẩng đầu , n&agrave;ng tiếp lời &ldquo;k&iacute;nh thưa pharaon vĩ đại , người nắm quyền lực tối cao ở Ai Cập gi&agrave;u mạnh n&agrave;y , người l&agrave; thi&ecirc;n t&agrave;i qu&acirc;n sự trăm năm hiếm gặp , vị minh qu&acirc;n t&agrave;i đức vẹn to&agrave;n y&ecirc;u d&acirc;n như con &hellip; danh tiếng của ng&agrave;y vang xa khắp nơi người người t&ocirc;n k&igrave;nh nể phục &hellip;&rdquo; n&agrave;ng phung ra trăm ng&agrave;n lời n&oacute;i nịnh h&oacute;t đến ch&iacute;nh m&igrave;nh c&ograve;n nổi hết gai &oacute;c . Nghe những lời t&acirc;n bốc mới lạ nhưng lại như r&oacute;t mật v&agrave;o tai n&agrave;y khiến người n&agrave;o đ&oacute; rất h&agrave;i l&ograve;ng hắn nở một nụ cười tựa tếu phi tếu (cười như kh&ocirc;ng cười) , đoạn hắn tự tin khẳng định &ldquo;đương nhi&ecirc;n&rdquo; . Ivy khinh thường nghĩ &ldquo;c&aacute; cắn c&acirc;u rồi , muốn giết n&agrave;ng , nằm mơ đi nh&eacute;&rdquo; d&ugrave; l&agrave; pharaon quyền lực cũng chỉ l&agrave; một thiếu ni&ecirc;n miệng c&ograve;n h&ocirc;i sữa lại kh&ocirc;ng th&iacute;ch những lời nịnh h&oacute;t sao ? rồi ng&agrave;i sẽ hối hận rất nhanh v&igrave; sự tự phụ của m&igrave;nh ho&agrave;ng đế ạk . Tr&ecirc;n đầu lại truyền đến &acirc;m thanh de dọa &ldquo;nhưng &hellip; đừng tưởng ngươi n&oacute;i ra những lời m&agrave; con n&iacute;t cũng biết đ&oacute; sẽ tho&aacute;t tội v&ocirc; lễ của ngươi&rdquo; . Bầu kh&ocirc;ng kh&iacute; im lặng dưới cơn thịnh nộ của pharaon kh&ocirc;ng khỏi khiến người người kh&ocirc;ng r&eacute;t m&agrave; rung . Ivy kh&ocirc;ng vội trả lời cẩn thận xem x&eacute;t xung quanh . đập v&agrave;o mắt n&agrave;ng dầu ti&ecirc;n l&agrave; vẽ mặt kh&oacute; coi như ăn phải kiến h&ocirc;i của t&ecirc;n b&eacute;o &uacute; hắn lo sợ đến nỗi khu&ocirc;n mặt mập mạp rịn đầy mồ h&ocirc;i b&oacute;ng lo&aacute;ng , sợ n&agrave;ng li&ecirc;n lụy đ&acirc;y m&agrave; . Kế đ&oacute; l&agrave; vẻ đ&aacute;ng thương của mẹ nh&oacute;c con n&agrave;ng cứu , b&agrave; ấy nh&igrave;n n&agrave;ng đầy lo lắng v&agrave; &aacute;i n&aacute;y với vị &acirc;n nh&acirc;n kh&ocirc;ng quen biết n&agrave;y v&igrave; con b&agrave; m&agrave; sẽ mất mạng , nghĩ lại ho&agrave;ng đế bệ hạ của thần d&acirc;n Ai Cập n&agrave;y đ&uacute;ng l&agrave; t&agrave;i năng hơn người nhưng ng&agrave;i cũng nổi với sự t&agrave;n bạo nhẫn t&acirc;m chưa từng tha ai được sống nếu d&aacute;m đắc tội ng&agrave;i . Lại nh&igrave;n xung quanh đa số người d&acirc;n chứng kiến sự việc lại tội nghiệp cho c&ocirc; g&aacute;i tốt bụng , gan dạ nhưng bạc mệnh v&igrave; ai cũng hiểu mức độ t&agrave;n nhẫn với tử tội của pharaon<br />\r\n<br />\r\nRamesses nh&igrave;n nh&igrave;n người con g&aacute;i 13 ,14 tuổi trước mặt nghĩ lại kh&ocirc;ng khỏi cảm phục sự can đảm của n&agrave;ng nhưng vẫn l&agrave; một đứa ngu xuẩn tưởng v&agrave;i lời khua m&ocirc;i m&uacute;a m&eacute;p xu nịnh c&oacute; thề giữ được mạng d&aacute;m cản đường lại c&ograve;n d&ugrave;ng th&aacute;i độ xem thường với người quyền uy tối đa như hắn &ldquo;n&agrave;ng kh&ocirc;ng đ&aacute;ng sống&rdquo; . Đoạn ch&agrave;ng hướng đ&aacute;m binh l&iacute;nh ra lệnh &ldquo;người đ&acirc;u ! giải phạm nh&acirc;n về đem n&oacute; giao cho c&ocirc;ng ch&uacute;a Henutmire ng&agrave;y mai tế sống d&acirc;n l&ecirc;n thần linh&rdquo; . Hai chữ &ldquo;tế sống&rdquo; như s&eacute;t đ&aacute;nh tr&ecirc;n người Ivy , &ldquo;đ&ugrave;a sao chỉ l&agrave; nh&igrave;n hắn một ch&uacute;t lại đem m&igrave;nh tế sống , đ&uacute;ng l&agrave; h&ocirc;n qu&acirc;n t&agrave;n bạo m&agrave;&rdquo; . Hai t&ecirc;n binh l&iacute;nh đầu đội mũ đồng , th&acirc;n mặc gi&aacute;p trụ t&aacute;ch ra khỏi h&agrave;ng ngũ hướng đến n&agrave;ng . Đến l&uacute;c rồi , t&ecirc;n chết tiệt nếu Ivy n&agrave;y dễ chết thế th&igrave; mười mấy năm trước đ&atilde; chết rồi đ&acirc;u c&oacute; ở đ&acirc;y để ngươi khi dễ , x&eacute;t cho c&ugrave;ng cũng chỉ l&agrave; một t&ecirc;n oắc ho&agrave;ng đế 17 , 18 tuổi muốn đấu tr&iacute; với si&ecirc;u trộm bịp bợm 22 tuổi như n&agrave;ng ba chữ th&ocirc;i &ldquo;kh&ocirc;ng c&oacute; cửa&rdquo; mặc d&ugrave; đang trong th&acirc;n x&aacute;c thiếu nữ nhỏ b&eacute; nhưng đầu &oacute;c n&agrave;ng vẫn mang theo tri thức tiến bộ của thế kỷ 21 nh&eacute; . Hai t&ecirc;n l&iacute;nh định bắt lấy n&agrave;ng Ivy b&igrave;nh tĩnh thưa &ldquo;ho&agrave;ng đế bệ hạ t&ocirc;n k&iacute;nh , trước khi bị định tội xin cho thần đước n&oacute;i v&agrave;i c&acirc;u&rdquo; . Ramesses vẻ mặt khinh thường , xem đứa ngốc như ngươi c&ograve;n tr&ograve; g&igrave; hay ho , giơ tay l&agrave;m động t&aacute;c ra lệnh hai t&ecirc;n l&iacute;nh tạm dừng , lười biếng mở đ&ocirc;i m&ocirc;i mỏng cao qu&iacute; phung ra một từ &ldquo;n&oacute;i&rdquo; . Ivy bắt đầu thấy &aacute;c cảm với t&ecirc;n ho&agrave;ng đế mặt thi&ecirc;n thần t&acirc;m &aacute;c quỷ n&agrave;y lắm rồi n&agrave;ng n&eacute;n bực l&ecirc;n tiếng &ldquo;pharaon vĩ đại người t&agrave;i đức vẹn to&agrave;n y&ecirc;u d&acirc;n như con&rdquo; n&agrave;ng nhấn mạnh cụm từ &ldquo;t&agrave;i đức vẹn to&agrave;n y&ecirc;u d&acirc;n như con&rdquo; tr&iacute;ch từ những lời t&aacute;n dương l&uacute;c n&atilde;y của n&agrave;ng đ&atilde; được hắn x&aacute;c nhận &ldquo;đương nhi&ecirc;n&rdquo; cơ m&agrave; , dừng một ch&uacute;t tay đưa l&ecirc;n chỉ chỉ v&agrave;o đứa b&eacute; n&aacute;ng tiếp lời &ldquo;đứa b&eacute; n&agrave;y l&agrave; thần d&acirc;n Ai Cập &ndash; thế th&igrave; n&oacute; cũng được xem như &ldquo;con&rdquo; của ng&agrave;i rồi . Thần v&igrave; cứu mạng n&oacute; mới lao ra đường kh&ocirc;ng hề cố &yacute; cản ngựa bệ hạ . Th&acirc;n l&agrave;m &ldquo;phụ th&acirc;n&rdquo; lẽ n&agrave;o ng&agrave;i nghĩ h&agrave;nh động cứu &ldquo;con&rdquo; ng&agrave;i l&agrave; v&ocirc; lễ&rdquo; dừng một ch&uacute;t n&agrave;ng ngẩng đầu quan s&aacute;t sắc mặt c&agrave;ng l&uacute;c c&agrave;ng đen của hắn trong l&ograve;ng cười thầm giờ mới biết &ldquo;m&egrave;o n&agrave;o hơn miễu n&agrave;o&rdquo; đ&uacute;ng l&agrave; &ldquo;ngựa non h&aacute;o đ&aacute;&rdquo; nghe v&agrave;i lời khen tặng m&aacute;t ruột vội v&agrave;ng x&aacute;c nhận ngay . Giờ hối hận cũng kh&ocirc;ng kịp nữa rồi haha &hellip; N&agrave;ng bu&ocirc;ng lu&ocirc;n c&acirc;u quyết định thắng thua hướng con người đen chậm r&atilde;i n&oacute;i &ldquo;lẽ n&agrave;o &hellip; những lời kia về ng&agrave;i &hellip; kh&ocirc;ng phải sự thật m&agrave; ng&agrave;i l&agrave; v&igrave; vua &hellip;như lời đồn&rdquo; Ivy d&ugrave;ng lời n&oacute;i h&agrave;m &yacute; &uacute;p mở nuốt v&agrave;o bốn chữ &ldquo;h&ocirc;n qu&acirc;n bạo ngược&rdquo; m&agrave;-ai-cũng-hiểu-l&agrave;-c&aacute;i-g&igrave;-đ&oacute; khiến Ramesses kh&ocirc;ng c&oacute; cơ hội bắt tội n&agrave;ng khi qu&acirc;n .<br />\r\n<br />\r\n&Yacute; định ban đầu của hắn l&agrave; xem n&agrave;ng th&agrave;nh con chuột ngu xuẩn đ&ugrave;a giỡn một ch&uacute;t , thật kh&ocirc;ng ngờ lời n&oacute;i sắc sảo kh&ocirc;ng c&oacute; kẽ hỡ lại từ một thiếu nữ 13 ,14 tuổi c&oacute; thể n&oacute;i ra đủ biết sự ngu ngốc ban đầu của c&ocirc; g&aacute;i n&agrave;y chỉ l&agrave; vẻ ngụy trang đằng sau con người nh&otilde; b&eacute; n&agrave;y hẳn l&agrave; sự th&ocirc;ng minh s&aacute;ng suốt hiếm c&oacute; , kẻ c&oacute; thể l&agrave;m hắn mất đề ph&ograve;ng rồi c&ocirc;ng k&iacute;ch bất ngờ trở tay kh&ocirc;ng kịp khiến hắn th&agrave;nh tr&ograve; cười mặc n&agrave;ng đ&ugrave;a giỡn th&igrave; kh&ocirc;ng thể tầm thường . Giết n&agrave;ng l&uacute;c n&agrave;y chẳng kh&aacute;c n&agrave;o thừa nhận hắn l&agrave; bạo qu&acirc;n kh&aacute;t m&aacute;u kh&ocirc;ng n&oacute;i l&yacute; lẽ trước mặt thần d&acirc;n uy danh của hắn sẽ mất sạch . Tha cho n&agrave;ng đồng nghĩa hắn chấp nhận m&igrave;nh thua , ki&ecirc;u h&atilde;nh của một pharaon kh&ocirc;ng cho ph&eacute;p hắn chịu nỗi nhục n&agrave;y hơn nữa bao nhi&ecirc;u người chứng kiến hắn-pharaon cao qu&iacute; thua l&yacute; n&agrave;ng-c&ocirc; g&aacute;i nhỏ tầm thường c&ograve;n đ&acirc;u l&agrave; t&ocirc;n nghi&ecirc;m một v&igrave; vua . Hắn phải l&agrave;m thế n&agrave;o cho tốt ?</h2>', 0, 'chuong-3-pharaon-ramesses-ii'),
(39, 23, 'CHƯƠNG 1', 'CHƯƠNG 1', '<h2>Bắt đầu từ g&agrave; g&aacute;y một tiếng, tr&acirc;u b&ograve; lục tục k&eacute;o thợ cầy đến đoạn đường ph&iacute;a trong điếm tuần.<br />\r\n<br />\r\nMọi ng&agrave;y, giờ ấy, những con vật n&agrave;y cũng như những người cổ cầy, vai bừa kia, đ&atilde; lần lượt đi m&ograve; ra ruộng l&agrave;m việc cho chủ. H&ocirc;m nay, v&igrave; cổng l&agrave;ng chưa mở, ch&uacute;ng phải chia qu&atilde;ng đứng rải r&aacute;c ở hai vệ đường, giống như một lũ phu vờ chờ đ&oacute;n những &ocirc;ng quan lớn.<br />\r\n<br />\r\nDưới b&oacute;ng tối của rặng tre um t&ugrave;m, tiếng tr&acirc;u thở ph&igrave; ph&ograve;, tiếng b&ograve; đập đu&ocirc;i đen đ&eacute;t, xen với tiếng người khạc kh&uacute;ng khắng.<br />\r\n<br />\r\nCảnh tượng điếm tuần th&igrave;nh l&igrave;nh hiện ra trong &aacute;nh lửa lập l&ograve;e của chiếc mồi rơm bị thổi. Cạnh giẫy s&agrave;o, gi&aacute;o ngả nghi&ecirc;ng dựng ở gi&aacute;p tường, một lũ tuần phu lố nhố ngồi tr&ecirc;n lớp chiếu quằn qu&egrave;o. C&oacute; người ph&igrave; ph&ograve; thổi mồi. C&oacute; người ve ve mồi thuốc v&agrave; ch&igrave;a tay chờ đ&oacute;n điếu đ&oacute;m. C&oacute; người h&aacute; miệng ng&aacute;p d&agrave;i. C&oacute; người đang hai tay dụi mắt. Cũng c&oacute; người gối đầu tr&ecirc;n c&aacute;i miệng hiệu sừng tr&acirc;u, ngảnh mặt v&agrave;o v&aacute;ch m&agrave; ng&aacute;y.<br />\r\n<br />\r\nC&aacute;i điếu c&agrave;y v&agrave; c&aacute;i đ&oacute;m lửa bị năm, s&aacute;u người chuyền tay, chiếu đi, chiếu lại độ v&agrave;i ba v&ograve;ng, &aacute;nh lửa lại tắt, trong điếm chỉ c&ograve;n tiếng n&oacute;i chuyện rầm rầm.<br />\r\n<br />\r\nGi&agrave; g&aacute;y giục. Trời s&aacute;ng mờ mờ.<br />\r\n<br />\r\nTr&acirc;u b&ograve;, con đứng, con nằm, thi nhau quai hai h&agrave;m răng nhai trầu su&ocirc;ng v&agrave; nhả ra những cục nước bọt to bằng c&aacute;i trứng.<br />\r\n<br />\r\nThợ cầy khắp lượt d&ugrave;ng bắp cầy, vai cầy l&agrave;m ghế ngồi, c&ugrave;ng nhau b&agrave;n t&aacute;n b&acirc;ng quơ về chuyện sưu thuế.<br />\r\n<br />\r\nNhững con ch&egrave;o bẻo chẽo choẹt h&oacute;t tr&ecirc;n ngọn tre như muốn họa lại kh&uacute;c ca r&eacute;o rắt m&agrave; người qu&ecirc; vẫn gọi l&agrave; kh&uacute;c &quot;v&aacute;y c&ocirc;, c&ocirc; cởi&quot; của mấy con ch&agrave;o m&agrave;o đậu tr&ecirc;n c&agrave;nh xoan, đon đả ch&agrave;o vẻ tươi đẹp của cảnh trời buổi s&aacute;ng.<br />\r\n<br />\r\nToang cổng vẫn đ&oacute;ng chặt. Tuần phu lại lần lượt thổi mồi h&uacute;t thuốc l&agrave;o.<br />\r\n<br />\r\n- &Ocirc;ng Trương ơi, &ocirc;ng l&agrave;m ơn mở cổng cho t&ocirc;i đ&aacute;nh tr&acirc;u ra đồng ạ. Mọi ng&agrave;y b&acirc;y giờ t&ocirc;i đ&atilde; cầy được ba s&agrave;o ruộng rồi. H&ocirc;m nay, b&acirc;y giờ c&ograve;n nhong nh&oacute;ng ở đ&acirc;y... Phỏng chừng từ giờ đến trưa, cầy sao cho xong c&aacute;i ruộng mẫu hai? Th&ocirc;i &ocirc;ng l&agrave;m ph&uacute;c, l&agrave;m đức...<br />\r\n<br />\r\nSau tiếng năn nỉ của anh ch&agrave;ng cục mịch, vai v&aacute;c cầy, tay cầm thừng tr&acirc;u, trương tuần quẳng c&aacute;i điếu cầy xuống đất, thở nốt kh&oacute;i thuốc trong miệng, rồi giương đ&ocirc;i mắt say thuốc lờ đờ:<br />\r\n<br />\r\n- Nay chẳng xong th&igrave; mai! &Ocirc;ng L&yacute; đ&atilde; bảo thuế c&ograve;n thiếu nhiều, kh&ocirc;ng cho một con tr&acirc;u, con b&ograve; n&agrave;o ra đồng hết thảy...<br />\r\n<br />\r\n- Thưa &ocirc;ng, &ocirc;ng chủ t&ocirc;i n&oacute;ng t&iacute;nh lắm kia! &Ocirc;ng ấy đ&atilde; dặn buổi nay t&ocirc;i phải cầy cho xong ruộng ấy, nếu kh&ocirc;ng xong, đến trưa về ăn cơm, &ocirc;ng ấy mắng như t&aacute;t nước v&agrave; n&oacute;i như m&oacute;c cơm ra... Chớ t&ocirc;i c&oacute; muốn vất vả v&agrave;o m&igrave;nh l&agrave;m g&igrave;? Vả lại, &ocirc;ng L&yacute; sai tuần đ&oacute;ng cổng, cốt để bắt tr&acirc;u, bắt b&ograve; của người thiếu thuế kia chứ! &Ocirc;ng chủ t&ocirc;i nộp thuế đủ từ h&ocirc;m qua rồi, xin &ocirc;ng mở cổng cho t&ocirc;i...!<br />\r\n<br />\r\nTrương tuần nhăn mặt:<br />\r\n<br />\r\n- T&ocirc;i kh&ocirc;ng phải tộc biểu, kh&ocirc;ng phải phần thu, biết đếch g&igrave; đ&acirc;u &ocirc;ng chủ anh nộp thuế rồi hay chưa nộp. B&acirc;y giờ mở cổng cho anh, chốc nữa &ocirc;ng L&yacute; &ocirc;ng ấy chửi cha t&ocirc;i l&ecirc;n, anh c&oacute; nghe hộ t&ocirc;i kh&ocirc;ng?<br />\r\n<br />\r\nAnh ch&agrave;ng cục mịch lủi thủi đ&aacute;nh tr&acirc;u gồng cầy l&ugrave;i xuống, để nhường khu đất trước điếm cho người kh&aacute;c v&aacute;c bừa v&agrave; đuổi tr&acirc;u l&ecirc;n.<br />\r\n<br />\r\n- Người ta tr&acirc;u của nh&agrave;, &ocirc;ng chẳng cho ra th&igrave; chớ. Đ&acirc;y t&ocirc;i, tr&acirc;u thu&ecirc;... đồng hai một buổi đ&aacute;p, &ocirc;ng ạ. Xin &ocirc;ng lượng t&igrave;nh m&agrave; ngỏ cổng cho t&ocirc;i ra đồng kẻo trưa qu&aacute; mất rồi.<br />\r\n<br />\r\nNgười ấy n&oacute;i tuy thiết tha, trương tuần chỉ đ&aacute;p lại bằng c&aacute;i lắc đầu:<br />\r\n<br />\r\n- Nếu trưa qu&aacute; th&igrave; bừa lu&ocirc;n đến chiều. Tr&acirc;u thu&ecirc; sợ g&igrave;!<br />\r\n<br />\r\n- Người ta tr&acirc;u của nh&agrave;, &ocirc;ng chẳng cho ra th&igrave; chớ. Đ&acirc;y t&ocirc;i, tr&acirc;u thu&ecirc;... đồng hai một buổi đ&aacute;p, &ocirc;ng ạ. Xin &ocirc;ng lượng t&igrave;nh m&agrave; ngỏ cổng cho t&ocirc;i ra đồng kẻo trưa qu&aacute; mất rồi.<br />\r\n<br />\r\nNgười ấy n&oacute;i tuy thiết tha, trương tuần chỉ đ&aacute;p lại bằng c&aacute;i lắc đầu:<br />\r\n<br />\r\n- T&ocirc;i chẳng nghĩ lại, nghĩ đi g&igrave; cả! Bố t&ocirc;i sống lại, bảo t&ocirc;i mở cổng n&agrave;y b&acirc;y giờ, t&ocirc;i cũng xin chịu đừng n&oacute;i anh...<br />\r\n<br />\r\nNgười ấy lại v&aacute;c bừa, đưa tr&acirc;u trở lại chỗ cũ với c&aacute;i sắc mặt th&igrave;u thịu.<br />\r\n<br />\r\nMặt trời ngấp ngh&eacute; mặt lũy, muốn nh&ograve;m v&agrave;o điếm. Tuần phi&ecirc;n lẻ tẻ v&aacute;c s&agrave;o, cắp chiếu, đeo hiệu đi về. Trong điếm chỉ c&ograve;n trương-tuần duỗi gối kiểu gọng bừa, ngồi tr&ecirc;n chiếc chiếu điểm những tro mồi, b&atilde; điếu, đốc suất mấy t&ecirc;n đ&agrave;n em ở lại canh ng&agrave;y.<br />\r\n<br />\r\nXa xa nẻo trong đ&igrave;nh, một hồi m&otilde; c&aacute; thật d&agrave;i, tiếp lu&ocirc;n đến trống ngũ li&ecirc;n nện đủ ba hồi ch&iacute;n tiếng.<br />\r\n<br />\r\nNhư c&oacute; vẻ kinh ngạc về những thứ hiệu lệnh dữ dội, mấy con tr&acirc;u b&ograve; đang nằm c&ograve;ng queo tr&ecirc;n đường, đồng thời l&oacute;p ng&oacute;p đứng dậy.<br />\r\n<br />\r\n- Mẹ cha ch&uacute;ng n&oacute;! H&ocirc;m nay vẫn chưa đ&oacute;ng thuế, ch&uacute;ng n&oacute; định để tội vạ cho ai? Được! Cứ bướng đi, &ocirc;ng m&agrave; bắt hết tr&acirc;u b&ograve; b&aacute;n r&aacute;o!...<br />\r\n<br />\r\nTiếng chửi om s&ograve;m như giục mấy chục cặp mắt ngơ ngẩn của bọn cầy đều phải nghi&ecirc;ng về ph&iacute;a đ&igrave;nh. &Ocirc;ng l&yacute; n&aacute;ch cắp cuốn sổ, một tay cầm c&acirc;y gậy song, một tay xếch đ&ocirc;i ống quần m&oacute;ng lợn, vừa đi vừa ra ph&iacute;a điếm tuần vừa th&eacute;t mắng những người chậm thuế.<br />\r\n<br />\r\nĐ&atilde; năm h&ocirc;m nay, nghĩa l&agrave; sau khi b&agrave;i bổ tr&igrave;nh phủ đ&atilde; giao về với một chữ &yacute;, L&yacute; trưởng Đ&ocirc;ng x&aacute; ng&agrave;y n&agrave;o cũng vất vả về thuế.<br />\r\n<br />\r\nĐầu ti&ecirc;n hắn c&ograve;n cho m&otilde; đi rao. Rồi đến tộc biểu, phần thu đi hỏi. Rồi đến đầy tớ của hắn đưa đầy tớ ch&aacute;nh tổng đi th&uacute;c từng người. Trong năm ng&agrave;y nay, ng&agrave;y n&agrave;o cũng vậy, m&otilde; c&aacute;, trống th&uacute;c li&ecirc;n hồi, hiệu ốc, hiệu sừng thổi inh ỏi. Suốt từ s&aacute;ng sớm cho tới tối mịt, trong l&agrave;ng l&uacute;c n&agrave;o cũng như đ&aacute;m đ&aacute;nh cướp. B&acirc;y giờ đ&atilde; gần đến ng&agrave;y đổ thuế, c&ocirc;ng việc c&agrave;ng gấp. H&ocirc;m qua hắn đ&atilde; l&ecirc;n phủ, xin với &ocirc;ng phủ ph&aacute;i cho một người cai lệ v&agrave; hai người l&iacute;nh cơ về l&agrave;ng để trừng trị những kẻ bướng bỉnh.<br />\r\n<br />\r\nNhờ c&oacute; c&aacute;i thần thế ấy, hắn mới chửi rỡ, th&eacute;t mắng khắp l&agrave;ng cho oai.<br />\r\n<br />\r\nThợ cầy v&agrave; tuần phu đều biết c&aacute;i h&aacute;ch dịch của &ocirc;ng L&yacute;, ai nấy chỉ đ&aacute;p lại những c&acirc;u chửi ch&ugrave;m chửi lợp bằng sự n&iacute;n im.<br />\r\n<br />\r\nTr&acirc;u, b&ograve; nhiều con vẫy tai như muốn ch&agrave;o một người chức việc chăm chỉ phận sự trong khi người ấy qua trước mặt ch&uacute;ng.<br />\r\n<br />\r\nBước l&ecirc;n s&agrave;n điếm, L&yacute; trưởng quăng tạch cuốn sổ xuống s&agrave;n, giơ tay chỉ v&agrave;o mặt mấy t&ecirc;n đ&agrave;n em:<br />\r\n<br />\r\n- Hiệu kh&ocirc;ng thổi, để l&agrave;m sỏ bố ch&uacute;ng bay &agrave;?<br />\r\n<br />\r\nMột hồi c&ograve;i tu tu đồng thời nổi l&ecirc;n, hiệu sừng sen với hiệu ốc theo đ&uacute;ng hiệu ốc cố theo đ&uacute;ng mệnh của nhất l&yacute; chi trưởng .<br />\r\n<br />\r\nĐập hai b&agrave;n ch&acirc;n v&agrave;o nhau, rũ cho sạch bụi, rồi co ch&acirc;n l&ecirc;n ngồi v&agrave;o chiếu, L&yacute; trưởng vớ lu&ocirc;n lấy chiếu điếu cầy v&agrave; sai tuần phủ lấy đ&oacute;m, thổi lửa.<br />\r\n<br />\r\nMấy anh thợ c&agrave;y của những điền chủ đ&atilde; nộp đủ thuế, bạo đạn tiến đến trước mặt &ocirc;ng L&yacute;:<br />\r\n<br />\r\n- Thưa &ocirc;ng, trưa lắm rồi! Xin &ocirc;ng cho tuần mở cổng để ch&uacute;ng t&ocirc;i đ&aacute;nh tr&acirc;u đi cầy!...<br />\r\n<br />\r\n- Thong thả! H&atilde;y đứng đấy! Cầy đ&atilde; n&oacute;ng bằng thuế của nh&agrave; nước &agrave;?<br />\r\n<br />\r\nVừa n&oacute;i, L&yacute; trưởng vừa giặt mồi thuốc v&agrave;o điếu, h&uacute;t lu&ocirc;n một sạp ba điếu. Kh&oacute;i thuốc theo hai lỗ mũi tu&ocirc;n ra như hai ng&agrave; voi. L&yacute; trưởng d&otilde;ng dạc:<br />\r\n<br />\r\n- Trương tuần, anh bảo n&oacute; mở cổng ra. Nh&agrave; n&agrave;o đủ thuế th&igrave; cho tr&acirc;u b&ograve; ra đồng. C&ograve;n nh&agrave; n&agrave;o thiếu th&igrave; bắt tr&acirc;u b&ograve; điệu cả về đ&igrave;nh cho t&ocirc;i, để t&ocirc;i liệu cho ch&uacute;ng n&oacute;!<br />\r\n<br />\r\nSau một tiếng dạ của Trương tuần, mấy t&ecirc;n đ&agrave;n em loay hoay th&aacute;o n&ecirc;m, r&uacute;t then cổng. Rồi c&ugrave;ng đứng do&atilde;i ch&acirc;n ch&egrave;o, cố lấy hết sức của lực điền, xuống gạch v&igrave; cối cổng đ&atilde; m&ograve;n nhiều qu&aacute;. L&yacute; trưởng đứng l&ecirc;n mở sổ sướng t&ecirc;n những người đủ thuế cho Trương tuần nghe. Ước chừng mươi con tr&acirc;u b&ograve; được đi với bọn thợ cầy c&ugrave;ng ra ngo&agrave;i cổng. C&ograve;n độ hai chục con nữa đều phải v&acirc;ng lệnh &ocirc;ng L&yacute;, theo g&oacute;t Trương tuần v&agrave; tuần phu lũ lượt k&eacute;o v&agrave;i s&acirc;n đ&igrave;nh, chờ khi phải chịu tội thay cho chủ.</h2>', 0, 'chuong-1');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(40, 23, 'CHƯƠNG 2', 'CHƯƠNG 1', '<h2>M&otilde; c&aacute; tr&ecirc;n cột đ&igrave;nh lạ h&aacute; miệng nhận những c&acirc;y d&ugrave;i giận dữ. Trống c&aacute;i dưới x&aacute; đ&igrave;nh lại lỳ mặt chịu những c&aacute;i nện phũ ph&agrave;ng. Vừa dứt hồi vang của những tiếng m&otilde;, tiếng trống bị những lũy tre ngăn cản phải lật trở lại, L&yacute; trưởng liệng d&ugrave;i trống xuống s&acirc;n đ&igrave;nh v&agrave; qu&aacute;t một c&aacute;ch ra d&aacute;ng ra phết:<br />\r\n<br />\r\n- Từ s&aacute;ng đến giờ vẫn chưa đem chiếu ra trải, th&igrave; cha n&oacute; ngồi v&agrave;o đ&acirc;u? &Agrave; c&aacute;i thằng Mới l&aacute;o thật! Xong thuế rồi &ocirc;ng tống cổ...<br />\r\n<br />\r\nMẹ Mới ở ph&iacute;a sau đ&igrave;nh lếch thếch &ocirc;m mấy chiếc chiếu đặt l&ecirc;n s&agrave;n đ&igrave;nh:<br />\r\n<br />\r\n- Bẩm &ocirc;ng h&ocirc;m qua &ocirc;ng dặn bố n&oacute; nh&agrave; con s&aacute;ng nay phải đi chợ sớm...<br />\r\n<br />\r\n- Trải ra! Rồi đi gọi c&aacute;c &ocirc;ng phần việc đến đ&acirc;y... Kệnh dệnh, kệnh dệnh! Bố người ta đấy m&agrave; h&ocirc;m n&agrave;o cũng bắt người ta phải mời... Chẳng qua việc l&agrave; việc l&agrave;ng, chứ việc ri&ecirc;ng nh&agrave; ai! Ng&agrave;y mai m&agrave; kh&ocirc;ng đủ thuế, th&igrave; &ocirc;ng khai hết ra cho, thử xem thằng n&agrave;o ngồi t&ugrave;?<br />\r\n<br />\r\nĐằng sau c&oacute; tiếng l&eacute;o x&eacute;o:<br />\r\n<br />\r\n- L&agrave;m g&igrave; m&agrave; gắt gỏng dữ thế &ocirc;ng L&yacute;? B&agrave; ch&aacute;u bận đi mang cơm thợ cầy, t&ocirc;i phải nấu ch&iacute;n nồi c&aacute;m cho con lợn sề n&oacute; ăn. V&igrave; n&oacute; mới đẻ, sợ bỏ đ&oacute;i th&igrave; n&oacute; mất sữa, rồi con n&oacute; xấu. Bởi thế t&ocirc;i mới chậm ra một ch&uacute;t, chứ c&oacute; kệnh dệnh g&igrave; đ&acirc;u?<br />\r\n<br />\r\nNhững c&acirc;u ph&acirc;n trần tha thiết k&eacute;o d&agrave;i từ ngo&agrave;i đường v&agrave;o đến giọt đ&igrave;nh. Thủ quỹ nhanh nhảu bước l&ecirc;n thềm đ&igrave;nh với c&aacute;i tr&aacute;p sơn đen bệ vệ nấp ở sườn tay phải. L&yacute; trưởng vội v&agrave;ng n&oacute;i chữa:<br />\r\n<br />\r\nT&ocirc;i n&oacute;i &ocirc;ng kh&aacute;c người kia! Chứ &ocirc;ng... nh&agrave; neo, lắm việc, t&ocirc;i c&oacute; tr&aacute;ch g&igrave; &ocirc;ng đ&acirc;u...K&igrave;a, c&aacute;c &ocirc;ng ấy đ&atilde; ra cả k&igrave;a<br />\r\n<br />\r\nChưởng bạ &ocirc;m ống sổ v&agrave; một tập sổ đi đầu một bọn độ hơn mười người: Thư k&yacute; lồng khăn xếp v&agrave;o c&aacute;nh tay, Ph&oacute; l&yacute; quần &aacute;o th&acirc;m tr&ecirc;n cổ. Ch&aacute;nh hội vắt &aacute;o the b&ecirc;n vai, năm s&aacute;u &ocirc;ng kỳ cựu v&agrave; tộc biểu lệt xệt k&eacute;o đ&ocirc;i guốc gỗ đ&atilde; m&ograve;n hết g&oacute;t. Cả bọn lật đật tiến từ x&oacute;m giữa v&agrave;o đ&igrave;nh. Ai nấy trụt guốc cầm tay, nhảy l&ecirc;n s&agrave;n đ&igrave;nh, đ&uacute;t guốc v&agrave;o dưới gậm chiếu rồi ngồi l&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nCon vợ thằng Mới cung k&iacute;nh đệ l&ecirc;n b&ecirc;n cạnh cột đ&igrave;nh một c&aacute;i điếu đ&agrave;n đựng trong một chiếc b&aacute;t gỗ đ&aacute;nh đai bằng lạt v&agrave; c&aacute;i mồi rơm d&agrave;i chừng một sải. Hai đứa con n&oacute; kỳ kệch khi&ecirc;ng l&ecirc;n chỗ đ&oacute; nồi nước ch&egrave; kh&ocirc; mới nấu, kh&oacute;i đương bốc l&ecirc;n nghi ng&uacute;t v&agrave; chồng b&aacute;t đ&agrave;n sơn bằng những c&aacute;u nước ch&egrave;.<br />\r\n<br />\r\nCh&aacute;nh hội vừa khơi m&agrave;o về chuyện thuế th&igrave; người nh&agrave; L&iacute; trưởng lễ mễ bưng 1 khay đ&egrave;n thuốc phiện rước Ch&aacute;nh tổng từ nẻo x&oacute;m dưới đi l&ecirc;n. &Ocirc;ng cai lệ v&agrave; 2 &ocirc;ng l&iacute;nh cơ hộ tống cụ Ch&aacute;nh v&agrave;o trước cửa đ&igrave;nh. Trong đ&aacute;m đ&ocirc;ng mỗi người 1 c&acirc;u:<br />\r\n<br />\r\n- Cụ đ&atilde; ra!<br />\r\n<br />\r\n- Lạy cụ.<br />\r\n<br />\r\n- Xin mời cụ l&ecirc;n tr&ecirc;n.<br />\r\n<br />\r\nCh&aacute;nh tổng khoan thai bỏ giầy, bước qua một giẫy chiếu dưới, để lại mặt chiếu hai h&agrave;ng dấu ch&acirc;n đầy c&aacute;t bụi, rồi vắt vẻo ngồi l&ecirc;n chiếc chiếu tr&ecirc;n c&ugrave;ng. Bộ khay đ&egrave;n tự tay người nh&agrave; L&yacute; trưởng sang tay cai lệ, l&ecirc;n nằm l&agrave;m bạn với quan trong h&agrave;ng tổng.<br />\r\n<br />\r\nNgay từ s&aacute;ng sớm, cụ Ch&aacute;nh đ&atilde; c&oacute; điểm t&acirc;m độ v&agrave;i chục điếu ở nh&agrave; L&yacute; trưởng, cho n&ecirc;n cụ rất tỉnh t&aacute;o. Nghi&ecirc;m nghị, cụ l&ecirc;n giọng kẻ cả:<br />\r\n<br />\r\n- C&aacute;c &ocirc;ng chức việc l&agrave;ng n&agrave;y thật lười qu&aacute;. H&ocirc;m nay m&agrave; c&oacute; thuế th&igrave; be thiếu b&eacute;t, chưa thu được 1 nửa b&agrave;i chỉ! T&ocirc;i hạn từ giờ đến tối phải thu cho đủ. Nếu ko đủ, s&aacute;ng mai t&ocirc;i phải tr&igrave;nh quan.<br />\r\n<br />\r\nL&yacute; trưởng đ&oacute;n:<br />\r\n<br />\r\n- V&acirc;ng! Xin cụ th&uacute;c giục đi cho! Chứ mấy h&ocirc;m nay c&aacute;c &ocirc;ng ấy cứ ỳ ra đ&oacute;, chẳng &ocirc;ng n&agrave;o chịu ra miệng ra tiếng th&igrave; phỏng ai n&oacute; nộp cho?<br />\r\n<br />\r\nMọi người đều im lặng. Kh&ocirc;ng phải sợ c&acirc;u dọa nạt của Ch&aacute;nh tổng L&iacute; trưởng, họ c&ograve;n m&atilde;i nh&igrave;n ra cổng đ&igrave;nh<br />\r\n<br />\r\nThằng Mới kĩu kịt tr&ecirc;n vai chiếc đ&ograve;n g&aacute;nh treo hai chiếc quang. Giằng trước l&agrave; một xanh vừa gan vừa phổi, vừa tiết để tr&ecirc;n rỗ l&ograve;ng. Đằng sau th&igrave; một th&uacute;ng thịt lợn.<br />\r\n<br />\r\nLững thững ở ngo&agrave;i tiến v&agrave;o, n&oacute; đặt quang g&aacute;nh xuống s&acirc;n đ&igrave;nh, v&agrave; mời &ocirc;ng L&yacute; ra xem.<br />\r\n<br />\r\n- Bẩm &ocirc;ng, h&ocirc;m nay thịt đắt. Tất cả hết 6 đồng rưỡi.<br />\r\n<br />\r\n&Ocirc;ng Ch&aacute;nh hội, &ocirc;ng thủ quỹ v&agrave; nhiều &ocirc;ng kh&aacute;c c&ugrave;ng đổ x&ocirc; ra. Ai nấy đều biểu m&ocirc;i ch&ecirc; đắt, Ch&aacute;nh tổng nằm trong giữa đ&igrave;nh n&oacute;i ra:<br />\r\n<br />\r\n- Đắt với rẻ chẳng qua cũng của mấy đứa phụ canh, c&aacute;c &ocirc;ng c&oacute; mất g&igrave; đ&acirc;u! Th&ocirc;i để cho n&oacute; đem về l&agrave;m đi, trưa lắm rồi!<br />\r\n<br />\r\nThằng Mới mừng rỡ cất quang g&aacute;nh tr&ecirc;n vai. L&yacute; trưởng dặn theo:<br />\r\n<br />\r\n- Phải đ&aacute;nh mỗi m&acirc;m một b&aacute;t tiết canh, m&agrave;y nh&eacute;!<br />\r\n<br />\r\nRồi mọi người c&ugrave;ng l&ecirc;n s&agrave;n đ&igrave;nh, bắt đầu nh&uacute;ng tay v&agrave;o việc.<br />\r\n<br />\r\nL&yacute; trưởng quăng cuốn sổ điền ra chiếu v&agrave; bảo thư k&iacute;:<br />\r\n<br />\r\n- &Ocirc;ng đọc cho c&aacute;c &ocirc;ng kia t&iacute;nh, xem rằng chủ n&agrave;o bao nhi&ecirc;u... rồi bi&ecirc;n ghi v&agrave;o mảnh giấy. T&iacute;nh những chủ chưa nộp đồng n&agrave;o ấy th&ocirc;i.<br />\r\n<br />\r\nThủ quỹ mở tr&aacute;p lấy ra c&aacute;i b&agrave;n t&iacute;nh, c&aacute;i b&uacute;t ch&igrave; v&agrave; một tờ giấy t&acirc;y, rồi đưa b&agrave;n t&iacute;nh cho &ocirc;ng L&yacute; cựu:<br />\r\n<br />\r\n- &Ocirc;ng t&iacute;nh ta, t&ocirc;i t&iacute;nh t&acirc;y, rồi c&ugrave;ng kh&aacute;p lại, hễ 2 đằng m&agrave; ko sai nhau th&igrave; đ&uacute;ng.<br />\r\n<br />\r\nMột người đ&agrave;n b&agrave; r&oacute;n r&eacute;n bước v&agrave;o s&agrave;n đ&igrave;nh với một chuỗi tiền chinh trong tay:<br />\r\n<br />\r\n- Lạy cụ Ch&aacute;nh, lại c&aacute;c cụ ạ...<br />\r\n<br />\r\nB&ecirc;n b&agrave;n đ&egrave;n, 1 cậu l&iacute;nh cơ ngẩng cổ l&ecirc;n qu&aacute;t:<br />\r\n<br />\r\n- L&agrave;m g&igrave; m&agrave; đến h&ocirc;m nay mới đem nộp thuế? Lại c&ograve;n v&aacute;c những khải định ra đấy! Ai lấy cho?<br />\r\n<br />\r\nL&yacute; trưởng gạt đi:<br />\r\n<br />\r\n- Người nh&agrave; t&ocirc;i đấy... c&aacute;c &ocirc;ng t&iacute;nh gi&ugrave;m, xem b&agrave; ấy c&oacute; tất cả bao nhi&ecirc;u ruộng đất tất cả.<br />\r\n<br />\r\nThư k&yacute; giở sổ đọc:<br />\r\n<br />\r\n- Nguyễn Thị Qu&iacute; điền dĩ hạ: nhất sở Đồng Bắc xứ bảy s&agrave;o ch&iacute;n thước, nhất sở Đồng C&aacute; xứ s&aacute;u s&agrave;o ba thước hai thốn...<br />\r\n<br />\r\nL&yacute; cựu vừa gẩy con to&aacute;n l&aacute;ch c&aacute;ch vừa nhẩm<br />\r\n<br />\r\n- Nhất ngũ như ngũ, nhị ngũ nhất thập chi, tam ngũ nhất thập ngũ...<br />\r\n<br />\r\nThủ quỹ chăm chỉ để hai con mắt v&agrave;o mảnh giấy t&acirc;y dưới ngọn b&uacute;t ch&igrave;. Một hồi sau, Thư k&yacute; đọc hết, L&yacute; cựu hỏi:<br />\r\n<br />\r\n- C&oacute; phải 3 mẫu 4 s&agrave;o 5 thước ko? Thủ quỹ đ&aacute;p:<br />\r\n<br />\r\n- Kh&ocirc;ng biết. B&acirc;y giờ t&ocirc;i mới t&iacute;nh. Cộng bằng t&iacute;nh t&acirc;y vẫn chậm hơn t&iacute;nh ta. &Ocirc;ng biết thế.<br />\r\n<br />\r\nThị Qu&iacute; n&oacute;i theo:<br />\r\n<br />\r\n- V&acirc;ng, &ocirc;ng thủ t&iacute;nh kỹ cho. Ruộng nh&agrave; ch&aacute;u c&oacute; đ&acirc;u m&agrave; được 3 mẫu.<br />\r\n<br />\r\nMột hồi nữa, thủ quỹ h&igrave; hục với c&acirc;y b&uacute;t ch&igrave;, rồi ngẩng đầu l&ecirc;n mắng Thị Qu&iacute;:<br />\r\n<br />\r\n- B&agrave; n&oacute;i lạ! Bốn mẫu hai ruộng cả thảy, sao d&aacute;m bảo kh&ocirc;ng được ba mẫu!<br />\r\n<br />\r\nThị Qu&iacute; thề sống thế chết:<br />\r\n<br />\r\n- Ch&aacute;u c&oacute; ăn gian th&igrave; trời kh&ocirc;ng chứng! Thật quả ruộng của nh&agrave; ch&aacute;u chỉ c&oacute; hai mẫu bẩy s&agrave;o...<br />\r\n<br />\r\nL&yacute; cựu, Thủ quỹ c&ugrave;ng s&ocirc; lại dồn Thị Qu&iacute;. Người đ&agrave;n b&agrave; ấy nhất định kh&ocirc;ng chịu. C&aacute;i b&agrave;n t&iacute;nh v&agrave; c&acirc;y b&uacute;t ch&igrave; phải l&agrave;m việc th&ecirc;m nhiều lần nữa.<br />\r\n<br />\r\nCh&aacute;nh tổng sốt ruột, giục L&yacute; trưởng:<br />\r\n<br />\r\n- Ai t&iacute;nh cứ t&iacute;nh, ai đi đốc cứ đi đốc. Chứ x&uacute;m nhau ở cả đ&acirc;y, th&igrave; đến tết cũng kh&ocirc;ng thể thu xong thuế!<br />\r\n<br />\r\nL&yacute; trưởng lấy l&agrave;m phải, liền sai mấy t&ecirc;n gia nh&acirc;n đi đưa c&aacute;c &ocirc;ng lệ, l&iacute;nh cơ đầy tớ Ch&aacute;nh tổng v&agrave;o khắp những nh&agrave; đinh c&ugrave;ng v&agrave; dặn:<br />\r\n<br />\r\n- Đứa n&agrave;o chưa c&oacute; sưu hay chưa c&oacute; thuế th&igrave; cứ ch&oacute;i cổ n&oacute; v&agrave; điệu ra đ&acirc;y, để t&ocirc;i cho ch&uacute;ng 1 mẻ.<br />\r\n<br />\r\nM&otilde; lại th&uacute;c, trống lại giục, t&ugrave; v&agrave; lại inh ỏi thổi l&ecirc;n.<br />\r\n<br />\r\n&Aacute;nh nắng bứt rứt chiếu đến nửa s&acirc;n đ&igrave;nh. Mấy con tr&acirc;u, b&ograve; vẫn nằm g&ograve; lưng dưới những gốc gạo, gốc đa, hơi thở trong cổ họng k&eacute;o ra hồng hộc.</h2>', 0, 'chuong-2'),
(41, 23, 'CHƯƠNG 3', 'CHƯƠNG 3', '<h2>Nếp nh&agrave; tranh lủn củn nấp dưới rặng tre l&agrave; ng&agrave;, lặng lẽ &uacute;p lấy khu đất dề th&agrave;nh v&agrave; k&iacute;n đ&aacute;o n&aacute;u trong một con x&oacute;m cuối l&agrave;ng &ETH;&ocirc;ng x&aacute;, đứng xa ng&oacute; lại, c&oacute; thể lầm với nơi nhốt lợn hay chứa tro, đ&oacute; l&agrave; nh&agrave; của Nguyễn Văn Dậu.<br />\r\n<br />\r\nVới tầm cao vừa xế mặt th&agrave;nh, giẫy m&aacute;i hi&ecirc;n cườm cượp nh&ograve;m ra ven th&agrave;nh, lu&ocirc;n lu&ocirc;n phạt kẻ ra v&agrave;o bằng những c&aacute;i cộc bươu đầu cho&aacute;ng &oacute;c, nếu họ qu&ecirc;n kh&ocirc;ng c&uacute;i đầu.<br />\r\n<br />\r\nVới lớp r&ecirc;u xanh điểm những l&aacute; tre v&agrave;ng &uacute;a v&agrave; những cỏ gấu phất phơ, c&aacute;i s&acirc;n đất rộng bằng đường bừa, lỗ chỗ vết ch&acirc;n ch&oacute; sen vết ch&acirc;n người, vừa l&agrave;m đường đi, vừa l&agrave;m khu vực &quot;ho&atilde;n xung&quot;, ph&ograve;ng bị những l&uacute;c mưa r&agrave;o, đất ở ch&acirc;n th&agrave;nh khỏi lở v&agrave;o thềm v&agrave; cửa.<br />\r\n<br />\r\nBao nhi&ecirc;u bộ phận cần c&oacute; của một gia đ&igrave;nh, đều được thu cả v&agrave;o trong nh&agrave;.<br />\r\n<br />\r\nNh&agrave; cất theo lối kiến tr&uacute;c phổ th&ocirc;ng của c&aacute;c cụ, to&agrave;n thể gồm c&oacute; một ch&aacute;i v&agrave; hai gian.<br />\r\n<br />\r\nGian đầu l&agrave; buồng.<br />\r\n<br />\r\nBức m&agrave;nh r&aacute;ch m&eacute;p lướt thướt rủ ngo&agrave;i cửa buồng, cũng như bức ph&ecirc;n nan nứa sừng sững chắn ngang l&ograve;ng nh&agrave;, đều hết sức giữ cho trong buồng c&oacute; vẻ b&iacute; mật. Tr&aacute;i lại, những khe hở ở cạnh cửa v&agrave; nhưng lỗ thủng ở ch&acirc;n ph&ecirc;n lại c&ugrave;ng ra &yacute; ph&ocirc; s&ograve;ng, như muốn khai rằng: ngo&agrave;i chiếc giường tre gẫy gi&aacute;t, k&ecirc; gi&aacute;p bức bụa, trong n&agrave;y c&oacute; một chum mẻ, vại h&agrave;n, chen nhau đứng b&ecirc;n cạnh đống đất hang chuột.<br />\r\n<br />\r\n&ETH;ối với buồng, ấy l&agrave; bếp đun, cảnh tượng lại c&agrave;ng bề bộn.<br />\r\n<br />\r\nDưới từng m&aacute;y ch&aacute;i thấp hẹp, những luồng &aacute;nh s&aacute;ng xanh t&iacute;a, th&ocirc;ng thống k&eacute;o thẳng từ n&oacute;c đến nền, cố hun cho kh&ocirc; những chỗ đ&ecirc;m qua mưa dột.<br />\r\n<br />\r\nCạnh b&oacute; củi giong ẩm ướt, đo&agrave;n vung sứt miệng hềnh hệch nằm ngửa trong những c&aacute;i rế tre, như muốn cười với lũ m&ecirc;u đất thư nh&agrave;n, l&ocirc;ng lốc lăn nghi&ecirc;ng lăn ngửa.<br />\r\n<br />\r\nTr&ecirc;n b&atilde;i tro trấu tanh b&agrave;nh, nguội lạnh, mấy h&ograve;n gạch vỡ chầu đầu v&agrave;o nhau, nghi&ecirc;ng m&igrave;nh nằm theo thế ch&acirc;n vạc, đương chờ đến l&uacute;c đội nồi.<br />\r\n<br />\r\nKề đ&oacute; đến b&agrave;n thờ.<br />\r\n<br />\r\nChiếc ngưỡng tre uốn lưng g&ugrave; g&ugrave; đỡ lấy một đố tre, d&otilde;ng dạc đứng ra h&igrave;nh chữ &quot;m&ocirc;n&quot; v&agrave; hộ vệ cho một mớ nan tre l&agrave;nh ph&agrave;nh long bựt, giường thờ giống như c&aacute;i chạn đựng b&aacute;t. lơ lửng g&aacute;c giữa khoảng cột của bếp v&agrave; buồng.<br />\r\n<br />\r\nTờ giấy xanh lạnh l&ugrave;ng phủ ngo&agrave;i v&aacute;ch đất tỏ rằng nh&agrave; mới c&oacute; tang.<br />\r\n<br />\r\nNhưng mớ mạng nhện chằng chịt quấn tr&ecirc;n đ&aacute;m ch&acirc;n hường lơ thơ, lại l&agrave;m chứng cho một thời gian kh&aacute; l&acirc;u, chủ nh&acirc;n kh&ocirc;ng hề c&uacute;ng lễ. Rồi ở giữa nh&agrave;, chiếc chiếu r&aacute;ch thủng xộc xệch phủ tr&ecirc;n tấm phản long đinh.<br />\r\n<br />\r\nRồi ở dưới phản, mấy con ch&oacute; con đ&ograve;i ăn, ăng ẳng k&ecirc;u kh&ocirc;ng dứt tiếng.<br />\r\n<br />\r\nV&agrave; ở ngo&agrave;i hi&ecirc;n, quang, g&aacute;nh, liềm, h&aacute;i sen, với &aacute;o yếm, &aacute;o, v&aacute;y, quần, l&ocirc;i th&ocirc;i rủ suốt mấy gian ruổi.<br />\r\n<br />\r\nTrong tay bồng đứa con g&aacute;i hai tuổi, chị Dậu thơ thẩn ngồi tr&ecirc;n chiếc ch&otilde;ng long nan. C&aacute;i nhanh nhảu của đ&ocirc;i mắt sắc ngọt, c&aacute;i sinh sắn của cặp m&ocirc;i đỏ tươi, c&aacute;i mịn m&agrave;ng của nước da đen gi&ograve;n v&agrave; c&aacute;i nuột n&agrave; của người đ&agrave;n b&agrave; hai mươi bốn tuổi, vẫn kh&ocirc;ng đ&aacute;nh đổ những c&aacute;i lo phiền buồn b&atilde; trong đ&aacute;y tim. N&eacute;t mặt rầu rầu, chị im lặng nh&igrave;n sự ngoan ngo&atilde;n của hai đứa con nhỏ.<br />\r\n<br />\r\nC&aacute;i T&yacute; v&agrave; thằng Dần, đương hỳ hục bới đống rễ khoai, t&igrave;m những củ mập, củ gi&agrave;y, bỏ v&agrave;o trong rổ. Ch&uacute;ng n&oacute; mừng reo h&yacute; hửng khi thấy một củ nguy&ecirc;n l&agrave;nh, v&agrave; ch&uacute;ng n&oacute; c&atilde;i cọ lầu nhầu khi lục m&atilde;i chẳng được g&igrave; cả.<br />\r\n<br />\r\nMặt trời gần đến đỉnh đầu.<br />\r\n<br />\r\n&Aacute;nh nắng xuy&ecirc;n qua lũy tre chiếu xuống trước thềm khoang cửa.<br />\r\n<br />\r\n&ETH;ằng sau g&agrave; g&aacute;y te te.<br />\r\n<br />\r\nN&oacute;c bếp l&aacute;ng giềng ngọn kh&oacute;i bốc l&ecirc;n nghi ng&uacute;t.<br />\r\n<br />\r\nThằng Dần với bộ mặt th&igrave;u thịu, bỏ đống rễ khoai đứng dậy l&ugrave;ng b&ugrave;ng:<br />\r\n<br />\r\n- Nhặt m&atilde;i từ s&aacute;ng đến giờ mới được ba mẫu khoai ranh! Con kh&ocirc;ng phải tội m&agrave; bới nữa. U đi mua gạo mau l&ecirc;n! H&agrave;ng x&oacute;m nấu cơm trưa rồi, nh&agrave; ta vẫn chưa ăn cơm s&aacute;ng! Ch&uacute;ng con đ&oacute;i qu&aacute;!<br />\r\n<br />\r\nNg&oacute; con một c&aacute;ch đau đớn, chị Dậu ngọt ng&agrave;o:<br />\r\n<br />\r\n- Con h&atilde;y cố nhặt th&ecirc;m v&agrave;i chục mẫu nửa, rồi chị n&oacute; luộc cho ăn, chứ u l&agrave;m g&igrave; c&oacute; tiền đ&ocirc;ng gạo?<br />\r\n<br />\r\nThằng b&eacute; phụng phịu:<br />\r\n<br />\r\n- H&ocirc;m qua v&agrave; h&ocirc;m kia u b&aacute;n hai g&aacute;nh khoai lang được năm h&agrave;o m&agrave;... đ&atilde; ti&ecirc;u g&igrave; đ&acirc;u!<br />\r\n<br />\r\nVừa n&oacute;i n&oacute; vừa tung tăng chạy đến cạnh mẹ, toan lần dải yếm của mẹ. C&aacute;i T&yacute; thỏ thẻ khuy&ecirc;n em:<br />\r\n<br />\r\n- Tiền b&aacute;n khoai c&ograve;n phải để d&agrave;nh đ&oacute;ng sưu cho &ocirc;ng L&yacute; chứ? Dễ được đem m&agrave; mua gạo đấy hẳn? Em c&oacute; đ&oacute;i th&igrave; h&atilde;y ăn tạm củ khoai sống vậy!<br />\r\n<br />\r\nC&acirc;u n&oacute;i nghĩa l&yacute; của con b&eacute; bảy tuổi, h&igrave;nh như c&oacute; một sức mạnh thần b&iacute;, khiến cho chị Dậu hai h&agrave;ng nước mắt chạy quanh. Uể oải, chị bế c&aacute;i Tỉu l&ecirc;n sườn v&agrave; lừ thừ đi ra ngo&agrave;i cổng, để ng&oacute;ng xem chồng đ&atilde; về chưa.<br />\r\n<br />\r\nChồng chị, - anh Nguyễn Văn Dậu - tuy mới hai mươi s&aacute;u tuổi, nhưng đ&atilde; học nghề l&agrave;m ruộng đến mười bảy năm.<br />\r\n<br />\r\nVới c&aacute;i sức khỏe mạnh, với c&aacute;i t&iacute;nh nhanh nhẩu, với c&aacute;i đức chịu thương chịu kh&oacute; trong nghề cầy thu&ecirc; cuốc mướn, những năm son rỗi, vợ chồng nh&agrave; anh cũng vẫn kiếm được thừa ăn. Mấy năn nay phần v&igrave; th&oacute;c cao, gạo k&eacute;m, phần th&igrave; con đẻ th&ecirc;m ra, vợ bận mọn lu&ocirc;n, sự ti&ecirc;u d&ugrave;ng của anh mới kh&ocirc;ng được rồi r&agrave;o như trước, vặt mũi chỉ đủ đ&uacute;t miệng m&agrave; th&ocirc;i.<br />\r\n<br />\r\nRồi th&aacute;ng t&aacute;m năm ngo&aacute;i, mẹ anh bị bệnh qua đời. Lại th&aacute;ng gi&ecirc;ng năm nay, thằng em trai của anh cũng v&igrave; phải gi&oacute; m&agrave; chết. Dầu anh hết sức tằn tiện, hai c&aacute;i ma ấy cũng cứ l&ocirc;i k&eacute;o đi mất của anh hai m&oacute;n l&agrave; l&agrave;ng hết đ&uacute;ng s&aacute;u đồng, v&agrave; hai cổ quan t&agrave;i hết gần t&aacute;m đồng. Nh&agrave; kh&ocirc;ng c&oacute;, anh phải quang co tần tảo cho c&oacute;. Hai c&aacute;i rớp ấy cũng đủ đưa anh l&ecirc;n đến bậc nh&igrave;, bậc nhất trong hạng c&ugrave;ng đinh rồi. Hơn nữa, th&aacute;ng ba đến giờ thần bệnh sốt r&eacute;t ở đ&acirc;u k&eacute;o đến, n&oacute; bắt anh cứ ph&atilde;i nghĩ việc nằm nh&agrave;.<br />\r\n<br />\r\nThế l&agrave; gần ba th&aacute;ng trời, những sự đ&oacute;ng g&oacute;p chi ti&ecirc;u của một gia đ&igrave;nh năm miệng ăn, hết thảy tr&ocirc;ng v&agrave;o hai b&agrave;n tay trắng của người đ&agrave;n b&agrave; con mọn.<br />\r\n<br />\r\nSự đ&oacute;i r&aacute;ch của con v&agrave; sự lầm than của vợ, đắp đổi tiến đến trước mắt, đ&atilde; l&agrave;m cho anh gan ruột nẫu n&agrave;. Th&ecirc;m mấy bữa nay, L&yacute; trưởng ng&agrave;y ng&agrave;y sai người giục thuế, anh c&agrave;ng luống cuống như con kiến b&ograve; trong chảo n&oacute;ng, kh&ocirc;ng biết lo liệu thế n&agrave;o. Sớm ng&agrave;y, cơn sốt đ&atilde; tan anh phải gắng gượng chống gậy ra đi, cũng định vay mượn lấy một v&agrave;i đồng, trả cho xong &quot;m&oacute;n nợ nh&agrave; nước&quot;. Nhưng đi th&igrave; đi, ch&iacute;nh anh cũng kh&ocirc;ng tự biết m&igrave;nh s&egrave; đi đến nh&agrave; a, v&agrave; chừng bao giờ th&igrave; về.<br />\r\n<br />\r\nMong m&atilde;i kh&ocirc;ng thấy b&oacute;ng chồng, chị Dậu lại bỗng c&aacute;i Tỉu trở v&agrave;o, ngồi phịch xuống ch&otilde;ng.<br />\r\n<br />\r\nC&aacute;i T&yacute; vẫn loay hoay với đống rễ khoai. Thằng Dần đương r&aacute;u r&aacute;u nhai mẫu khoai sống.<br />\r\n<br />\r\nB&ecirc;n nh&agrave; l&aacute;ng giềng c&oacute; tiếng m&acirc;m b&aacute;t lạch cạch. Mấy con n&iacute;t t&iacute;u t&iacute;t gọi nhau ăn cơm. M&ugrave;i canh rưa v&agrave; m&ugrave;i c&aacute; khi theo ngọn gi&oacute; nồm đưa sang ng&agrave;o ngạt.<br />\r\n<br />\r\nThằng b&eacute; h&aacute;u đ&oacute;i nuốt vội miếng khoai trong miệng, vớ tấm &aacute;o n&acirc;u toạc vạt kho&aacute;c lu&ocirc;n v&agrave;o m&igrave;nh, đon đả chạy lại vỗ v&agrave;o vai mẹ:<br />\r\n<br />\r\n- Con sang chơi b&ecirc;n nh&agrave; &ocirc;ng B&aacute;c, u nh&eacute;!<br />\r\n<br />\r\nC&aacute;i T&yacute; chừng mắt:<br />\r\n<br />\r\n- B&ecirc;n ấy sắp sửa ăn cơm ấy m&agrave;...! Sang l&agrave;m g&igrave;? Rồi b&agrave; B&aacute;c lại đuổi oai o&aacute;i như h&ocirc;m nọ ấy. Th&ocirc;i, đ&oacute;i th&igrave; chịu vậy, chứ em cứ nhặt cố lấy mấy củ nữa, rồi chị đứng l&ecirc;n bắc nồi.<br />\r\n<br />\r\nThằng Dần quăng tọt c&aacute;i &aacute;o v&agrave;o ch&otilde;ng, ngoảnh đ&iacute;t quay lại, tr&ecirc;n mặt đầy vẻ thất vọng:<br />\r\n<br />\r\n- Khổ lắm, bao nhi&ecirc;u củ ngon củ l&agrave;nh, u đ&atilde; lựa hết đem b&aacute;n, ở đ&acirc;y chỉ c&ograve;n những rễ v&agrave; rễ, lấy đ&acirc;u ra khoai m&agrave; nhặt?<br />\r\n<br />\r\nC&aacute;i T&yacute; lại dịu n&eacute;t mặt:<br />\r\n<br />\r\n- Chả b&aacute;n th&igrave; lấy tiền đ&acirc;u đ&oacute;ng sưu?... Em h&atilde;y chịu kh&oacute; nhặt đi với chị! H&atilde;y c&ograve;n v&ocirc; khối củ mẫm ra đấy.<br />\r\n<br />\r\n- Nhưng m&agrave; em c&ograve;n đ&oacute;i v&agrave;ng cả mắt, kh&ocirc;ng nhặt được nữa...!<br />\r\n<br />\r\nN&oacute;i xong, thằng Dần bước đến b&ecirc;n cạnh rổ khoai, chọn lấy một củ lớn nhất, n&oacute; ch&ugrave;i v&agrave;o bụng cho sạch c&aacute;t bụi rồi cho v&agrave;o mồm cắn...<br />\r\n<br />\r\nChị Dậu tuy vẫn ngồi im kh&ocirc;ng n&oacute;i, nhưng ở kh&oacute;e mắt, nước mắt lại cứ chan chứa chảy ra. Chừng như kh&ocirc;ng muốn để cho c&aacute;c con thấy sự đau l&ograve;ng của m&igrave;nh, chị vờ ngảnh mặt nh&igrave;n v&agrave;o trong v&aacute;ch.</h2>', 0, 'chuong-3'),
(42, 23, 'CHƯƠNG 4', 'CHƯƠNG 4', '<h2>Con ch&oacute; c&aacute;i nằm ở gậm phản bỗng chốc vẫy đu&ocirc;i rối r&iacute;t, tỏ ra d&aacute;ng bộ vui mừng.<br />\r\n<br />\r\nAnh Dậu lữ thử từ cổng tiến v&agrave;o với c&aacute;i vẻ mặt xanh ngắt v&agrave; buồn rứt như kẻ sắp bị t&ugrave; tội.<br />\r\n<br />\r\nC&aacute;i T&yacute;, thằng Dần c&ugrave;ng vỗ tay reo:<br />\r\n<br />\r\n- A! Thầy đ&atilde; về! A! Thầy đ&atilde; về!...<br />\r\n<br />\r\nMặc kệ ch&uacute;ng n&oacute;, anh ch&agrave;ng ốm yếu im lặng dựa gậy l&ecirc;n tấm ph&ecirc;n cửa, nặng nhọc chống tay c&agrave;o gối v&agrave; bước l&ecirc;n thềm. rồi lảo đảo đi đến cạnh phản, anh ta lăn kềnh l&ecirc;n tr&ecirc;n chiếc chiếu r&aacute;ch.<br />\r\n<br />\r\nChị Dậu &ocirc;m con v&agrave;o ngồi b&ecirc;n phản, sờ tay v&agrave;o tr&aacute;n chồng v&agrave; sẽ s&agrave;ng hỏi:<br />\r\n<br />\r\n- Thế n&agrave;o? Thầy em c&oacute; mệt lắm kh&ocirc;ng? Sao chậm về thế? Tr&aacute;n đ&atilde; n&oacute;ng l&ecirc;n đ&acirc;y m&agrave;!<br />\r\n<br />\r\nAnh Dậu nằm thừ kh&ocirc;ng cựa, cũng kh&ocirc;ng trả lời. Chị Dậu lại gặng:<br />\r\n<br />\r\n- Chắc thầy em mệt lắm th&igrave; phải? Từ s&aacute;ng đến giờ đi những đ&acirc;u? Hỏi vay của ai?<br />\r\n<br />\r\nVắt tay l&ecirc;n tr&aacute;n, anh Dậu thở một tiếng d&agrave;i v&agrave; cất c&aacute;i giọng lề dề của người ốm:<br />\r\n<br />\r\n- T&ocirc;i l&ecirc;n nh&agrave; l&atilde;o Hội &Iacute;ch.<br />\r\n<br />\r\n- C&oacute; được đồng n&agrave;o hay kh&ocirc;ng?<br />\r\n<br />\r\n- Chẳng được g&igrave; cả. T&ocirc;i cũng biết c&aacute;i thằng gi&agrave; ấy n&oacute; bẩn như ch&oacute;, m&igrave;nh đả mắc nợ của n&oacute;, chẳng khi n&agrave;o n&oacute; lại cho m&igrave;nh v&acirc;y th&ecirc;m. Nhưng v&igrave; b&iacute; qu&aacute; cho n&ecirc;n phải nhắm mắt đi liều. Quả nhi&ecirc;n mới cất miệng hỏi, n&oacute; đ&atilde; mắng sấm xơi sới, rồi lại dọa rằng: &quot;Ba đồng cầm đất cầm nh&agrave;, l&atilde;i mười hai ph&acirc;n, đến th&aacute;ng s&aacute;u n&agrave;y th&igrave; vừa hết hạn, t&iacute;nh th&agrave;nh ra năm đồng một h&agrave;o s&aacute;u xu. Nếu kh&ocirc;ng trả được n&oacute; sẽ cắm cả nh&agrave; đất để l&agrave;m chuồng x&iacute;&quot;.<br />\r\n<br />\r\nChị Dậu cau đ&ocirc;i l&ocirc;ng m&agrave;y:<br />\r\n<br />\r\n- Trời đất ơi! Cắm cả nh&agrave; đất để l&agrave;m chuồng x&iacute;! Ăn n&oacute;i như thế th&igrave; c&ograve;n trời đất n&agrave;o nữa?... Thế sao thầy em kh&ocirc;ng đến &ocirc;ng cậu hỏi tạm lấy một đồng vậy?<br />\r\n<br />\r\n- C&oacute;! T&ocirc;i c&oacute; hỏi! nhưng cũng chẳng ăn thua g&igrave;. &Ocirc;ng ấy nhiếc m&atilde;i vợ chồng sức d&agrave;i, vai rộng m&agrave; c&oacute; v&agrave;i ba đồng bạc tiền sưu cũng kh&ocirc;ng chạy nổi...<br />\r\n<br />\r\nChị Dậu giậm ch&acirc;n xuống đất:<br />\r\n<br />\r\n- Khốn nạn! Ấy &ocirc;ng cậu đấy! &Ocirc;ng cậu gi&agrave;u c&oacute; nứt đổ l&aacute; v&aacute;ch, cả đời ch&aacute;u mới hỏi vay mấy đồng, c&ograve;n nỡ nhiếc m&oacute;c như thế, tr&aacute;ch chi người ngo&agrave;i!... Thế b&acirc;y giờ thầy em đ&atilde; định hay chưa?<br />\r\n<br />\r\nAnh Dậu n&iacute;n lặng một hồi, rồi lại thở d&agrave;i tiếng nữa:<br />\r\n<br />\r\n- T&ocirc;i đ&atilde; n&oacute;i với cụ Nghị Quế ở th&ocirc;n &ETH;o&agrave;i... hay l&agrave; b&aacute;n qu&aacute;ch...<br />\r\n<br />\r\n&ETH;ương n&oacute;i giở c&acirc;u, anh Dậu ngập ngừng lại th&ocirc;i, như c&oacute; vật g&igrave; nghẹn ở cổ họng. Chị Dậu cố gắng:<br />\r\n<br />\r\n- B&aacute;n qu&aacute;ch c&aacute;i g&igrave;? Thầy em cứ n&oacute;i, ở đ&acirc;y c&oacute; ai m&agrave; sợ!<br />\r\n<br />\r\nAnh Dậu đơm dớm nước mắt:<br />\r\n<br />\r\n- Hay l&agrave; b&aacute;n qu&aacute;ch c&aacute;i T&yacute; cho cụ ấy?<br />\r\n<br />\r\nChị Dậu cũng nước mắt chảy qua g&ograve; m&aacute; d&ograve;ng d&ograve;ng. Chị cứ c&uacute;i gầm mặt xuống, kh&ocirc;ng biết trả lời ra sao. C&aacute;i T&yacute; nghe thấy ru,ng rời củ khoai trong tay, vội v&agrave;ng đứng l&ecirc;n năn nỉ:<br />\r\n<br />\r\n- Con van thầy! Con van U! thầy u để con ở nh&agrave; chơi với em con. Con van thầy! Con van U! Thầy U đừng đem b&aacute;n con.<br />\r\n<br />\r\nThằng Dần nhả vội miếng khoai trong miệng, v&agrave; sụt sịt kh&oacute;c:<br />\r\n<br />\r\n- Em kh&ocirc;ng, n&agrave;o! Em kh&ocirc;ng, n&agrave;o! em kh&ocirc;ng cho b&aacute;n chị T&yacute; n&agrave;o! C&oacute; b&aacute;n th&igrave; b&aacute;n c&aacute;i Tỉu kia k&igrave;a!<br />\r\n<br />\r\nAnh Dậu cũng như chị Dậu, ai n&aacute;y s&egrave; sẽ gạt thầm nước mắt v&agrave; cũng giả c&aacute;ch l&agrave;m thinh.<br />\r\n<br />\r\nC&aacute;i T&yacute; vẫn bai bải van như tế sao, một hai xin thầy u đừng b&aacute;n n&oacute; đi, tội nghiệp cho n&oacute;.<br />\r\n<br />\r\nChừng như cầm l&ograve;ng kh&ocirc;ng đậu với những lời ng&acirc;y thơ v&agrave; th&ecirc; thảm của con b&eacute; ngoan ngo&atilde;n. Chị<br />\r\n<br />\r\nDậu xoa su&yacute;t c&aacute;i Tỉu v&agrave; y&ecirc;n ủi c&aacute;i T&yacute;:<br />\r\n<br />\r\n- Kh&ocirc;ng! Thầy con n&oacute;i đ&ugrave;a ấy th&ocirc;i! B&aacute;n con th&igrave; lấy ai ẩm con b&eacute; n&agrave;y cho u đi l&agrave;m?<br />\r\n<br />\r\nC&aacute;i T&yacute; vui vẻ ngồi v&agrave;o cạnh đống rễ khoai, n&oacute; lại h&yacute; ho&aacute;y bới bới nhặt nhặt.<br />\r\n<br />\r\nMột bầu kh&ocirc;ng kh&iacute; tẻ ngắt bao phủ cả mấy gian nh&agrave;, người ta c&oacute; thể nghe r&otilde; tiếng dập ở hai tr&aacute;i<br />\r\n<br />\r\ntim của hai vợ chồng anh trai c&ugrave;ng.<br />\r\n<br />\r\nNgo&agrave;i đường c&oacute; tiếng ch&oacute; sủa x&ocirc; x&aacute;t. Rồi tiếng roi quất v&agrave;o v&aacute;ch đ&egrave;n đẹt. Rồi tiếng người hỏi dữ dội:<br />\r\n<br />\r\n- &ETH;ĩ Dậu c&oacute; nh&agrave; đấy kh&ocirc;ng?<br />\r\n<br />\r\nChị Dậu lật đật bỗng con b&eacute; con ra cổng đuổi ch&oacute;. Anh Dậu cũng ngồi nhỏm dậy v&agrave; ng&oacute; ra s&acirc;n.<br />\r\n<br />\r\nNgười nh&agrave; L&yacute; trưởng ngh&ecirc;nh ngang m&uacute;a chiếc tay thước với sợi dầy thừng, đưa &ocirc;ng Cai lệ v&agrave; c&acirc;y<br />\r\n<br />\r\nroi song h&ugrave;ng dũng bước v&agrave;o trong cổng. Hai người c&ugrave;ng ra oai giộ con ch&oacute; c&aacute;i v&agrave; c&ugrave;ng nhẩy l&ecirc;n thềm. Phủ đầu, &ocirc;ng Cai lệ chỉ c&acirc;y roi v&agrave;o mặt anh Dậu v&agrave; qu&aacute;t:<br />\r\n<br />\r\n- Sưu đ&acirc;u? Sao kh&ocirc;ng đem nộp? Người ta v&agrave;o hỏi vẫn c&ograve;n ngồi ỳ ra đ&oacute;!<br />\r\n<br />\r\nAnh Dậu lẩy bẩy đứng dậy. C&aacute;i mặt xanh xao khi ấy đ&atilde; đổi ra sắc t&aacute;i m&eacute;t. Run run, anh sẽ giơ tay g&atilde;i tai:<br />\r\n<br />\r\n- Thưa &ocirc;ng, t&ocirc;i chưa c&oacute;.<br />\r\n<br />\r\n&Ocirc;ng Cai lệ thị h&ugrave;ng bằng hai con mắt giương tr&ograve;n:<br />\r\n<br />\r\n- Cả năm chỉ c&oacute; hai đồng bảy h&agrave;o tiền sưu, b&acirc;y giờ anh vẫn chưa c&oacute; bao giờ anh định mới c&oacute;?<br />\r\n<br />\r\nVới c&aacute;i d&aacute;ng điệu c&oacute; vẻ van lơn, anh Dậu hổn hển thở v&agrave; đ&aacute;p:<br />\r\n<br />\r\n- Thưa &ocirc;ng, v&igrave; t&ocirc;i đau yếu, n&ecirc;n chưa lo kịp, xin &ocirc;ng thư cho đến mai. Thuế c&ograve;n năm bữa nữa mới đăng trường kia m&agrave;!<br />\r\n<br />\r\n- &Agrave;! thuế c&ograve;n năm h&ocirc;m nữa mới đăng trường, cho n&ecirc;n anh kh&ocirc;ng nộp vội, phải kh&ocirc;ng?<br />\r\n<br />\r\nHỏi vậy nhưng &ocirc;ng Cai lệ kh&ocirc;ng để cho kẻ bị hỏi được c&oacute; thời gian ph&acirc;n trần. Nhanh như cắt, &ocirc;ng chuyển phắt c&acirc;y roi song sang tay tr&aacute;i v&agrave; nắm chặt năm ng&oacute;n tay phải, đưa lu&ocirc;n v&agrave;o ngực anh Dậu một mớ quả thụi. C&aacute;i thụi chuy&ecirc;n m&ocirc;n của người c&ocirc;ng kh&aacute;c hẳn thứ thụi phổ th&ocirc;ng của thường d&acirc;n. Anh Dậu chỉ ức ức mấy tiếng, chứ kh&ocirc;ng thể k&ecirc;u l&ecirc;n được. Thuận tay, &ocirc;ng Cai lệ t&uacute;m lấy cổ &aacute;o anh Dậu v&agrave; ngảnh lại bảo &ocirc;ng người nh&agrave; l&yacute; trưởng;<br />\r\n<br />\r\n- Thừng đ&acirc;u? Tr&oacute;i cổ n&oacute; lại. C&oacute; một suất sưu b&acirc;y giờ chưa nộp lại c&ograve;n chực giở l&yacute; sự?<br />\r\n<br />\r\nTức th&igrave; hai người x&uacute;m lại, mỗi người nắm một c&aacute;nh tay anh Dậu bẻ quặt ra đằng sau lưng, rồi luồn thừng v&agrave;o v&agrave; riết thật chặt. Họ tr&oacute;i anh ch&agrave;ng khốn nạn giống kiểu như nh&agrave; qu&ecirc; tr&oacute;i ch&oacute; để l&agrave;m thịt vậy.<br />\r\n<br />\r\nThằng Dần &ograve;a kh&oacute;c:<br />\r\n<br />\r\nC&aacute;i T&yacute; mếu m&aacute;o nhếch nh&aacute;c, chạy đến cạnh &ocirc;ng Cai lệ, chắp tay v&aacute;i lấy v&aacute;i để:<br />\r\n<br />\r\n- Ch&aacute;u lạy hai &ocirc;ng! Hai &ocirc;ng tha cho thầy ch&aacute;u. Hai &ocirc;ng đừng tr&oacute;i thầy ch&aacute;u!<br />\r\n<br />\r\nSẵn c&acirc;y roi trong tay, &ocirc;ng Cai lệ quất lu&ocirc;n cho n&oacute; mấy c&aacute;i v&agrave;o đ&iacute;t v&agrave; qu&aacute;t:<br />\r\n<br />\r\n- Bước ngay!<br />\r\n<br />\r\nCon b&eacute; đau qu&aacute;. N&oacute; lăn đ&agrave;nh đạch xuống đất v&agrave; n&oacute; rẫy rụa như con g&agrave; phải nước n&oacute;ng. Thằng Dần c&agrave;ng kh&oacute;c dữ.<br />\r\n<br />\r\n&Ocirc;ng Cai lệ đương cơn thịnh nộ, lại thẳng tay giơ cay roi song, toan trừng phạt thằng b&eacute; n&agrave;y. Chị Dậu lếch thếch &ocirc;m con b&eacute; con chạy v&agrave;o:<br />\r\n<br />\r\n- Th&ocirc;i. T&ocirc;i xin &ocirc;ng Cai! &Ocirc;ng tha cho ch&aacute;u... Ch&uacute;ng n&oacute; h&atilde;y c&ograve;n b&eacute; bỏng.<br />\r\n<br />\r\nLời n&oacute;i tiết tha của người đ&agrave;n b&agrave; c&oacute; duy&ecirc;n vẫn c&oacute; đ&ocirc;i ch&uacute;t hiệu lực. Thằng b&eacute; ng&acirc;y thơ đ&atilde; được &ocirc;ng Cai &acirc;n x&aacute;.<br />\r\n<br />\r\nNgảnh lại thấy chồng bị tr&oacute;i chặt qu&aacute;, mặt mũi nhăn như chuột kẹp, chị Dậu lạ dẽ d&agrave;ng k&ecirc;u van &ocirc;ng Cai:<br />\r\n<br />\r\n- Nh&agrave; t&ocirc;i đương ốm... Xin &ocirc;ng l&agrave;m ph&uacute;c nới rộng n&uacute;t thừng ra cho!<br />\r\n<br />\r\nLần n&agrave;y kh&ocirc;ng c&oacute; kết qủa, những lời nằn n&igrave; của chị chỉ được &ocirc;ng Cai đ&aacute;p lại bằng một giọng oai nghi&ecirc;m:<br />\r\n<br />\r\n- Kh&ocirc;ng phải nới! Tr&oacute;i thế cũng chưa chết n&agrave;o! M&agrave; c&oacute; chết nữa, chồng chị c&agrave;ng khỏi đ&oacute;ng sưu.<br />\r\n<br />\r\nAnh Dần n&oacute;i xen:<br />\r\n<br />\r\n- Nhưng t&ocirc;i đau qu&aacute;... xin &ocirc;ng h&atilde;y nới lỏng ra cho t&ocirc;i một t&yacute;. N&agrave;o t&ocirc;i c&oacute; chạy mất đ&acirc;u!<br />\r\n<br />\r\n&Ocirc;ng Cai lệ nổi cơn l&ocirc;i đ&igrave;nh, tặng lu&ocirc;n anh Dậu năm bảy c&aacute;i t&aacute;t v&agrave;o mặt v&agrave; hằm h&egrave;:<br />\r\n<br />\r\n- Bướng với &ocirc;ng &agrave;. M&agrave;y c&oacute; tội th&igrave; &ocirc;ng mới tr&oacute;i. Lại c&ograve;n bướng với &ocirc;ng &agrave;!<br />\r\n<br />\r\nChị Dậu nh&igrave;n &ocirc;ng Cai lệ bằng đ&ocirc;i con mắt đỏ ngầu:<br />\r\n<br />\r\n- Th&ocirc;i, t&ocirc;i cũng lạy &ocirc;ng nữa! Chẳng qua nay mai đủ thuế th&igrave; th&ocirc;i, l&agrave;m g&igrave; &ocirc;ng phải h&agrave;nh hạ chồng t&ocirc;i qu&aacute; vậy? C&oacute; đ&aacute;nh th&igrave; &ocirc;ng cứ đ&aacute;nh t&ocirc;i n&agrave;y. bao nhi&ecirc;u tội t&ocirc;i xin chịu cả. Chồng t&ocirc;i đau ốm chẳng l&agrave;m n&ecirc;n tội.<br />\r\n<br />\r\n- &Agrave;! M&agrave;y th&aacute;ch &ocirc;ng phải kh&ocirc;ng? Hừ! Thấy &ocirc;ng nể mặt m&agrave;y c&agrave;ng l&agrave;m gi&agrave;! &ETH;ược! &Ocirc;ng thử cho m&agrave;y biết tay! N&agrave;y th&aacute;ch! N&agrave;y th&aacute;ch! N&agrave;y th&aacute;ch!!!<br />\r\n<br />\r\nMỗi tiếng &quot; n&agrave;y th&aacute;ch&quot; từ miệng &ocirc;ng Cai đ&atilde; đưa ra, chị Dậu lại được hai ba quả thụi v&agrave;o ngực.<br />\r\n<br />\r\n&ETH;ứng kh&ocirc;ng vững, chị n&agrave;y ng&atilde; phịch xuống đất. C&aacute;i Tỉu nằm trong l&ograve;ng mẹ kh&oacute;c ngặt kh&oacute;c nghẻo. C&aacute;i T&yacute;, thằng Dần mỗi đứa nấp một x&oacute; cột nh&igrave;n trộm bố mẹ phải đ&ograve;n. Cả hai đứa c&ugrave;ng nức nở kh&oacute;c kh&ocirc;ng ra tiếng.<br />\r\n<br />\r\nNgười nh&agrave; L&yacute; trưởng chỉ c&aacute;i tay thước v&agrave;o mặt chị Dậu:<br />\r\n<br />\r\n- Kh&ocirc;ng thấy người n&agrave;o lắm đều như chị. Nếu nh&agrave; ai cũng bướng như nh&agrave; n&agrave;y, c&oacute; lẽ thuế của nh&agrave; nước đến bỏ. Th&ocirc;i! &ETH;ứng dậy m&agrave; đi chạy sưu cho chồng, đừng ngồi ăn vạ đ&acirc;y nữa!<br />\r\n<br />\r\nRồi hai &ocirc;ng hằm hằm t&uacute;m lấy đầu thừng, sền sệt điệu anh Dậu xuống thềm. Tới cổng, anh ch&agrave;ng khốn nạn quay lại dặn vợ:<br />\r\n<br />\r\n- U n&oacute; để c&aacute;i Tỉu ở nh&agrave;, sang ngay b&ecirc;n cụ Nghị Quế cho t&ocirc;i...</h2>', 0, 'chuong-4');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(43, 23, 'CHƯƠNG 5', 'CHƯƠNG 5', '<h2>Bước v&agrave;o khỏi cổng th&ocirc;n &ETH;o&agrave;i, đ&atilde; thấy nh&agrave; &ocirc;ng Nghị Quế.<br />\r\n<br />\r\nN&oacute; l&agrave; một đ&aacute;m bung xung nhọn như ngọn th&aacute;p, h&ugrave;ng dũng &uacute;p tr&ecirc;n đo&agrave;n bịch vừa đồ sộ, dường như<br />\r\n<br />\r\nph&ocirc; nh&agrave; m&igrave;nh th&oacute;c để h&agrave;ng bốn, năm m&ugrave;a.<br />\r\n<br />\r\nN&oacute; l&agrave; một lũ đốn rơm, đốn rạ lớn bằng tr&aacute;i n&uacute;i, chen nhau đứng b&ecirc;n cạnh ngọn m&iacute;t, ngọn sung, dường như khoe &ocirc;ng chủ cầy cấy đến mấy trăm mẫu.<br />\r\n<br />\r\nN&oacute; l&agrave; những t&ograve;a m&aacute;i ng&oacute;i muốn bảo tồn quốc t&uacute;y bằng những &quot;đấu&quot; vu&ocirc;ng tr&ograve;m tr&otilde;m, những s&ocirc;i tầu cong rướn v&agrave; những con c&aacute; ch&eacute;p &quot; mảnh sứ&quot; h&aacute; miệng nằm gi&aacute;p tường hồi.<br />\r\n<br />\r\nN&oacute; l&agrave; một ng&ocirc;i dương cơ rộng chừng ba mẫu, qu&acirc;y quần trong bốn bức tường gạch cắm mảng chai, cảnh tượng phức tạp, giống như ng&ocirc;i chợ đ&oacute;ng trong khu trại, họp đủ c&aacute;c vật sang, h&egrave;n, c&aacute;c kiểu cũ, mới.<br />\r\n<br />\r\nC&aacute;i cơ nghiệp ấy tuy tự tổ ti&ecirc;n để lại, &ocirc;ng Nghị cũng c&oacute; khai th&aacute;c th&ecirc;m nhiều.<br />\r\n<br />\r\n&Ocirc;ng kh&ocirc;ng đi bu&ocirc;n, kh&ocirc;ng đi thầu, chỉ l&agrave;m &ocirc;ng chủ ruộng kiếm &ocirc;ng chủ thả l&atilde;i.<br />\r\n<br />\r\n&ETH;ụng đến của &ocirc;ng nhẹ nhất cũng l&agrave; mười ph&acirc;n. V&agrave; vay từ một đồng trở l&ecirc;n, đều phải viết ruộng<br />\r\n<br />\r\nhay nh&agrave;, hoặc phải gửi vật g&igrave; kh&aacute;c l&agrave;m đồ bảo đảm. Qu&aacute; hạn th&igrave; mất. Dương cơ &ocirc;ng ở cũng nhờ ruộng nương &ocirc;ng cấy, hạc đồng, lọ sứ &ocirc;ng thờ, phần nhiều l&agrave; của những người vay nợ hết hạn kh&ocirc;ng trả, bị &ocirc;ng chiếm lấy v&agrave; bắt lấy.<br />\r\n<br />\r\nNh&agrave; &ocirc;ng đời đời ph&aacute;t ph&aacute;t về b&ecirc;n h&agrave;o. Bước đường c&ocirc;ng danh của &ocirc;ng cũng bắt đầu từ chức L&yacute; trưởng vượt qua những bậc Ph&oacute; tổng, Ch&aacute;nh tổng rồi, cơm rượu, b&ograve; lợn v&agrave; quan phủ, quan tỉnh hiệp sức v&oacute; nhau đưa &ocirc;ng l&ecirc;n ghế nghị vi&ecirc;n.<br />\r\n<br />\r\nKh&ocirc;ng như những kẻ mặt người dạ th&uacute;, mượn tiếng &quot;thay mặt d&acirc;n&quot; để h&oacute;t Ch&aacute;nh phủ m&agrave; xin xỏ việc n&agrave;y việc kia, &ocirc;ng ra nghị trường, chỉ cốt mua c&aacute;i &quot;vị thứ t&acirc;n thời&quot;, lấy chỗ mỗi năm v&agrave;i lần, ăn uống v&agrave;... ngồi nằm với bọn tai mặt trong xứ.<br />\r\n<br />\r\nC&aacute;i đức &quot;kh&ocirc;ng th&egrave;m biết...chữ&quot; của &ocirc;ng hơn hẳn c&aacute;c bạn đồng viện, tuy những &ocirc;ng ấy chỉ xuất th&acirc;n bằng nghề l&aacute;i lợn hay cai phu. V&agrave;o viện &ocirc;ng cũng như hầu hết c&aacute;c &ocirc;ng nghị kh&aacute;c, kh&ocirc;ng b&agrave;n v&agrave; kh&ocirc;ng cần nghe ai b&agrave;n. Nhưng, &ocirc;ng cũng chỉ ng&aacute;p vặt, chớ kh&ocirc;ng ngủ gật bao giờ, v&igrave; sợ đ&ocirc;i giầy ch&iacute; long để dưới ch&acirc;n ghế lỡ bị mất trộm trong khi &ocirc;ng phải co ch&acirc;n đặt l&ecirc;n mặt ghế cho hợp th&oacute;i quen của &ocirc;ng.<br />\r\n<br />\r\nTừ ng&agrave;y &quot;giữ chức &ocirc;ng Nghị&quot;, danh tiếng của &ocirc;ng đ&atilde; bay khắp tỉnh Trung sơn. Thế nhưng suốt năm ch&iacute; tối, &ocirc;ng kh&ocirc;ng phải thết một &ocirc;ng kh&aacute;ch s&aacute;o, trừ ra hai ng&agrave;y giỗ cha v&agrave; giỗ mẹ.<br />\r\n<br />\r\nCố nhi&ecirc;n, &ocirc;ng phải l&agrave; một người cơ chỉ, so k&egrave; từ một đồng xu trở đi. Vậy m&agrave; lắm khi &ocirc;ng lại rất h&agrave;o. Nhất l&agrave; những dịp tết ta, tết t&acirc;y, kh&ocirc;ng dịp n&agrave;o &ocirc;ng qu&ecirc;n kh&ocirc;ng bắt chim ng&oacute;i, g&agrave; m&aacute;i theo những th&uacute;ng gạo t&aacute;m thơm v&agrave;o cửa mấy &ocirc;ng to lớn.<br />\r\n<br />\r\nVới &ocirc;ng, vợ chồng &ETH;ĩ Dậu kh&ocirc;ng c&oacute; họ h&agrave;ng th&acirc;n th&iacute;ch g&igrave; cả. Nhưng v&igrave; c&aacute;c hắn cầy thu&ecirc; cấy mướn<br />\r\n<br />\r\ncho &ocirc;ng đ&atilde; nhiều, n&ecirc;n đều biết nh&agrave;, biết người.<br />\r\n<br />\r\nSau khi Cai lệ v&agrave; người nh&agrave; L&yacute; trưởng đ&atilde; dẫn anh Dậu ra đ&igrave;nh, chị Dậu theo lời chồng dặn, trao con b&eacute; con cho c&aacute;i T&yacute;, vớ chiếc n&oacute;n r&aacute;ch đeo v&agrave;o cổ tay. Thằng dần kh&oacute;c nhếch kh&oacute;c nh&aacute;c, rầm rĩ k&ecirc;u đ&oacute;i. Giả điếc, chị cứ lủi thủi cắp n&oacute;n ra đi.<br />\r\n<br />\r\n&Aacute;nh nắng th&aacute;ng năm r&aacute;t như lửa ch&agrave;m v&agrave;o mặt. Hơi nước dưới đồng bốc l&ecirc;n, n&oacute;ng như hơi trong ch&otilde; x&ocirc;i. Những con cua chửa v&agrave; những con rắn nước chịu kh&ocirc;ng nổi sức n&oacute;ng của nước trong ruộng, rủ nhau b&ograve; l&ecirc;n mặt đường v&agrave; n&uacute;p v&agrave;o c&aacute;c kh&oacute;m cỏ.<br />\r\n<br />\r\n&ETH;&ocirc;ng x&aacute; với &ETH;o&agrave;i th&ocirc;n vẫn l&agrave; một x&atilde;, c&aacute;ch nhau độ ba c&acirc;y xộ trong l&uacute;c nắng gắt, người ta coi đoạn đường ấy như con đường thi&ecirc;n l&yacute; trong b&atilde;i c&aacute;t gi&agrave;. Nhưng, với chị Dậu n&oacute; kh&ocirc;ng m&ugrave;i g&igrave;, v&igrave; đời chị đ&atilde; quen h&agrave;ng ng&agrave;y rạn mặt với thần nắng.<br />\r\n<br />\r\nNhờ sức che chơ của chiếc n&oacute;n r&aacute;ch, chị chỉ bước rảo một th&ocirc;i th&igrave; tới cổng nh&agrave; Nghị Quế. Mặt trời<br />\r\n<br />\r\nđứng b&oacute;ng. Tr&acirc;u b&ograve; lũ lượt l&ocirc;i thợ cầy ở ngo&agrave;i đồng về. Thập th&ograve; ngo&agrave;i cổng, chị n&acirc;ng vạt &aacute;o n&acirc;u mốc lau những giọt mồ h&ocirc;i nhễ nhoại tr&ecirc;n đ&ocirc;i l&ocirc;ng m&agrave;y v&agrave; mở to đ&ocirc;i mắt qu&aacute;ng nắng nh&ograve;m v&agrave;o<br />\r\n<br />\r\ntrong cổng.<br />\r\n<br />\r\nTr&ecirc;n c&aacute;i s&acirc;n gạch B&aacute;t tr&agrave;ng m&ocirc;ng m&ecirc;nh như b&atilde;i đ&aacute; b&oacute;ng, kh&ocirc;ng điểm một vết b&oacute;ng r&acirc;m. &ETH;&agrave;n chim bồ c&acirc;u chổng m&ocirc;ng mổ tr&ecirc;n nong đỗ. Con g&agrave; m&aacute;i ấp cục t&aacute;c từ tr&ecirc;n cửa chuồng nhảy xuống, ưỡn đ&iacute;t b&oacute;n một b&atilde;i ph&acirc;n cho chậu lan. Mấy con lợn con theo mẹ nghễu nghện diễu chung quanh chậu nước vo gạo.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; người n&agrave;o qua lại. &ETH;&aacute;nh bạo, chị Dậu xăm xăm tiến v&agrave;o s&acirc;n. Một đ&agrave;n ch&oacute; dữ như đ&agrave;n h&ugrave;m ở đ&acirc;u nhất tề sồ ra. Ch&uacute;ng n&oacute; nhảy chồm l&ecirc;n tận mặt người lạ.<br />\r\n<br />\r\nHoảng qu&aacute;, chị Dậu ngồi phịch xuống gạch, vừa vung c&aacute;i n&oacute;n khua đuổi những con &aacute;c th&uacute;, vừa k&ecirc;u:<br />\r\n<br />\r\n- Anh bếp c&oacute; nh&agrave; kh&ocirc;ng? L&agrave;m ph&uacute;c đ&aacute;nh ch&oacute; cho t&ocirc;i với!<br />\r\n<br />\r\nTrong nh&agrave; c&oacute; tiếng th&eacute;t ra the th&eacute;, nghe r&otilde; l&agrave; tiếng đ&agrave;n b&agrave;:<br />\r\n<br />\r\n- L&agrave;m g&igrave; m&agrave; nheo nh&eacute;o thế? Nh&agrave; ai kh&ocirc;ng c&oacute; ch&oacute;? L&agrave;m sao đi kh&ocirc;ng mang gậy? Thằng bếp c&ograve;n bận việc kh&aacute;c, n&oacute; kh&ocirc;ng phải hầu ch&uacute;ng bay!<br />\r\n<br />\r\nRồi lại im. Th&igrave; ra vợ chồng &ocirc;ng Nghị đương ăn cơm ở nh&agrave; kh&aacute;ch. Nghe tiếng chị Dậu l&eacute;o x&eacute;o, b&agrave; Nghị bực m&igrave;nh, qu&aacute;t đổng v&agrave;i c&acirc;u cho oai, chứ b&agrave; kh&ocirc;ng th&egrave;m đứng dậy. T&iacute;nh b&agrave; xưa nay vẫn thế, chẳng khi n&agrave;o lại chịu hạ m&igrave;nh m&agrave; đi mắng ch&oacute;, đuổi ch&oacute; cho kẻ vay nợ hay người l&agrave;m thu&ecirc;.<br />\r\n<br />\r\n&ETH;&agrave;n ch&oacute; cứ v&acirc;y k&iacute;n chung quanh chị Dậu như qu&acirc;n đ&egrave;n c&ugrave;. H&igrave;nh như ch&uacute;ng n&oacute; muốn cố l&agrave;m hết phận sự với chủ: con n&agrave;o con ấy nhe răng l&egrave; lưỡi, chỉ chực vồ v&agrave;o hai ch&acirc;n chị ch&agrave;ng đ&aacute;ng thương. C&aacute;i n&oacute;n d&ugrave;ng l&agrave;m kh&iacute; giới đ&atilde; bị đ&agrave;n vật cắn r&aacute;ch tan t&agrave;nh. Chị Dậu luống cuống kh&ocirc;ng biết l&agrave;m thế n&agrave;o. B&iacute; qu&aacute;, chị phải giơ hai nắm tay đ&aacute;nh nhau bộ với &quot;đội l&iacute;nh coi nh&agrave;&quot; của &ocirc;ng Nghị.<br />\r\n<br />\r\nMột mụ thợ cấy ở dưới nh&agrave; bếp lật đật v&aacute;c gậy chạy l&ecirc;n. Mụ đuổi đ&agrave;n ch&oacute; t&aacute;n loạn mỗi con chạy đi mỗi ngả.<br />\r\n<br />\r\nChị Dậu l&oacute;p ng&oacute;p đứng dậy v&agrave; đi đến trước nh&agrave; kh&aacute;ch. B&agrave;n tay phải bị ch&oacute; cắn v&agrave;o l&uacute;c n&agrave;o kh&ocirc;ng biết, m&aacute;u chảy đầm đ&igrave;a. Gh&eacute; răng, chị x&eacute; một miếng ở đầu dải yếm, buộc chặt lấy chỗ bị thương. Rồi rụt r&egrave;, chị l&ecirc;n bực thềm:<br />\r\n<br />\r\n- Thưa lạy hai cụ ạ!<br />\r\n<br />\r\nB&agrave; Nghị gắt:<br />\r\n<br />\r\n- Ch&oacute; cắn v&agrave;o tay phải kh&ocirc;ng? Cho chết!<br />\r\n<br />\r\n&ETH;ương l&uacute;c người ta ăn uống, ai bảo cứ dẫn x&aacute;c v&agrave;o? Hỏi g&igrave;?<br />\r\n<br />\r\n&Ocirc;ng Nghị đặt b&aacute;t xuống m&acirc;m, vừa nhai nhồm nho&agrave;m vừa đ&oacute;n:<br />\r\n<br />\r\n- S&aacute;ng ng&agrave;y chồng n&oacute; đ&atilde; sang xin b&aacute;n đứa con. Chắc n&oacute; đến để n&oacute;i nốt chuyện ấy, chứ g&igrave; nữa.<br />\r\n<br />\r\n- Bẩm cụ phải, con sang thưa cụ về việc ấy.<br />\r\n<br />\r\nB&agrave; Nghị đưa mắt ra hiệu cho &ocirc;ng Nghị rồi bảo chị Dậu:<br />\r\n<br />\r\n- Thong thả! H&atilde;y ngồi đấy! &ETH;ể cho người ta ăn xong c&aacute;i đ&atilde;. &ETH;ừng quấy rầy!<br />\r\n<br />\r\n&Ocirc;ng Nghị như đ&atilde; hiểu &yacute; của vợ, cứ việc chan chan gắp gắp, kh&ocirc;ng n&oacute;i kh&ocirc;ng rằng.<br />\r\n<br />\r\nKh&eacute;p n&eacute;p, chị Dậu ngồi phệt xuống mặt thềm gạch. Mọi ng&agrave;y tuy vẫn l&agrave;m mướn cho &ocirc;ng Nghị ấy,<br />\r\n<br />\r\nnhưng chi, chỉ th&igrave; thọt ra v&agrave;o trong mấy gian bếp, chưa biết nh&agrave; tr&ecirc;n thế n&agrave;o. H&ocirc;m nay bạ men tới đ&acirc;y, ấy l&agrave; lần đầu chị được tr&ocirc;ng thấy cảnh tượng nh&agrave; kh&aacute;ch của &ocirc;ng d&acirc;n biểu. Trong mắt chị nh&agrave; ấy lịch sự v&ocirc; c&ugrave;ng. N&agrave;o ở cạnh bức ho&agrave;ng phi khảm trai, mấy c&ocirc; con g&aacute;i tồng ngồng đ&ugrave;i, v&uacute; vừa nằm, vừa tủm tỉm cười t&igrave;nh.<br />\r\n<br />\r\nN&agrave;o ở giữa đ&ocirc;i c&acirc;u sơn then thiếp v&agrave;ng hai thằng b&eacute; con b&eacute;o tr&ograve;n v&agrave; xoay trần, lễ mễ khi&ecirc;ng hộp sữa b&ograve; cao lớn gần bằng ch&uacute;ng n&oacute;.<br />\r\n<br />\r\nN&agrave;o ở b&ecirc;n chiếc độc b&igrave;nh men đỏ, c&aacute;i điếu ống vất vểu vươn c&agrave;nh xe tr&uacute;c d&agrave;i thườn thượt như c&aacute;i cần c&acirc;u.<br />\r\n<br />\r\nRồi ở trong c&aacute;i tủ ch&egrave; chạm d&acirc;y nho, một rổ trứng g&agrave; đầy l&ugrave;m, ngất nghểu chồng tr&ecirc;n bộ khay ch&egrave;<br />\r\n<br />\r\ntrắng b&oacute;ng. Rồi ở trước c&aacute;i sập gụ l&ecirc;n nước, bốn chiếc ghế gụ mặt đ&aacute; c&ugrave;ng chầu v&agrave;o một chiếc b&agrave;n<br />\r\n<br />\r\nm&acirc;y sơn xanh. V&agrave; ở đầu c&aacute;i giường t&acirc;y sơn quang dầu, quần lĩnh th&acirc;m v&agrave; khăn qu&agrave;ng nhiễu xanh c&ugrave;ng vắt một chỗ. Biết bao của qu&iacute; vật lạ, chị Dậu nh&igrave;n vẫn chưa khắp. Tr&ecirc;n b&agrave;n ăn c&oacute; tiếng leng<br />\r\n<br />\r\nkeng.<br />\r\n<br />\r\n&Ocirc;ng Nghị đ&acirc;m ch&eacute;o đ&ocirc;i đũa qua mặt m&acirc;m, bưng b&aacute;t nước canh, trợn mắt h&uacute;p một c&aacute;i đ&aacute;nh &quot;soạt&quot;. Rồi &ocirc;ng vừa nhai, vừa nuốt, vừa giục thằng nhỏ lấy tăm. B&agrave; Nghị cầm đĩa d&ograve; kho ăn dở, ch&uacute;t v&agrave;o trong ni&ecirc;u, sai thằng bếp treo l&ecirc;n lao m&agrave;n v&agrave; dặn:<br />\r\n<br />\r\n- B&agrave; đ&atilde; đếm kỹ từng miếng rồi đấy. C&ograve;n mười bốn miếng tất cả. Hễ mất miếng n&agrave;o th&igrave; chết với b&agrave;!<br />\r\n<br />\r\nThằng bếp bưng m&acirc;m cơm ra. Thằng nhỏ đệ chậu nước v&agrave;o. &Ocirc;ng Nghị, b&agrave; Nghị mỗi người d&uacute;ng ba<br />\r\n<br />\r\nng&oacute;n tay v&agrave;o chậu, vuốt qua hai m&eacute;p một lượt, rồi c&ugrave;ng uống nước, xỉa răng. Hai đứa đầy tớ đứng hai<br />\r\n<br />\r\nb&ecirc;n cầm quạt phẩy lại. B&agrave; Nghị mở c&aacute;i tr&aacute;p tr&ograve;n lấy trầu v&agrave; cau nhai với một v&agrave;i sợi thuốc. &Ocirc;ng Nghị<br />\r\n<br />\r\nnhắc c&aacute;i điếu ống để tr&ecirc;n trốc tủ, giặt thuốc, ch&acirc;m lửa, v&iacute;t c&aacute;i xe ch&uacute;c v&agrave;o miệng. Bắt ch&acirc;n chữ &quot;Ngũ&quot; &ocirc;ng vểnh mặt h&uacute;t s&ograve;ng sọc một hơi:<br />\r\n<br />\r\n- Con mẹ kia! B&aacute;n con b&aacute;n c&aacute;i thế n&agrave;o? V&agrave;o m&agrave; n&oacute;i chuyện với b&agrave;... !<br />\r\n<br />\r\nR&oacute;n r&eacute;n, chị Dậu đứng n&eacute;p v&agrave;o cửa v&agrave; ngập ngừng:<br />\r\n<br />\r\n- Thưa lạy hai cụ, nh&agrave; con t&uacute;ng qu&aacute;, xin hai cụ cứu gi&uacute;p con.<br />\r\n<br />\r\nB&agrave; Nghị nhả miếng b&atilde; trầu cầm tay, quai thật d&agrave;i cặp m&ocirc;i cắn chỉ:<br />\r\n<br />\r\n- Chẳng cứu với vớt g&igrave; cả! M&agrave;y c&oacute; b&aacute;n đứa con g&aacute;i tao mua!<br />\r\n<br />\r\n- Xin v&acirc;ng!<br />\r\n<br />\r\n- S&aacute;ng ng&agrave;y chồng m&agrave;y n&oacute;i rằng con b&eacute; ấy đ&atilde; l&ecirc;n bẩy tuổi, xin lấy ba đồng. Cụ &ocirc;ng tưởng n&oacute; n&oacute;i thật, bằng l&ograve;ng cho n&oacute; hai đồng. Nhưng giờ nghe n&oacute;i con m&agrave;y mới c&oacute; s&aacute;u tuổi, th&igrave; tao triết đi một nửa, chỉ cho một đồng th&ocirc;i. Thuận b&aacute;n th&igrave; về đưa nhau sang đ&acirc;y!<br />\r\n<br />\r\nChị Dậu ngơ ng&aacute;c:<br />\r\n<br />\r\n- Thưa cụ, thật quả ch&aacute;u đ&atilde; l&ecirc;n bẩy, n&oacute; đẻ th&aacute;ng dần năm T&yacute;. Ch&uacute;ng con kh&ocirc;ng d&aacute;m n&oacute;i dối thưa cụ!<br />\r\n<br />\r\n- Tao kh&ocirc;ng thể tin c&aacute;i miệng vợ chồng nh&agrave; m&agrave;y! Người ta m&aacute;ch tao l&agrave; n&oacute; l&ecirc;n s&aacute;u, th&igrave; tao hay n&oacute; l&ecirc;n s&aacute;u. Chứ tao biết đ&acirc;u n&oacute; đẻ năm t&yacute; hay đẻ năm tỵ năm tỳ.. !<br />\r\n<br />\r\nChị Dậu đờ mặt, kh&ocirc;ng biết n&oacute;i sao. B&agrave; Nghị tiếp:<br />\r\n<br />\r\n- V&igrave; c&aacute;i hai b&ecirc;n kia...<br />\r\n<br />\r\n&Ocirc;ng Nghị cau mặt ngắt lời:<br />\r\n<br />\r\n- Sao b&agrave; cứ gọi con bằng lối x&aacute;ch m&eacute; như vậy? T&ocirc;i đ&atilde; dặn b&agrave; phải gọi n&oacute; l&agrave; mợ... B&acirc;y giờ ở nh&agrave; c&aacute;c quan, con g&aacute;i đều được k&ecirc;u l&agrave; mợ tất cả. T&ocirc;i h&egrave;n ra cũng l&agrave; một Nghị vi&ecirc;n, c&oacute; l&uacute;c ngồi với quan Sứ quan Thượng, danh gi&aacute; kh&ocirc;ng k&eacute;m g&igrave; một &ocirc;ng quan. B&agrave; c&oacute; gọi con g&aacute;i b&agrave; l&agrave; mợ, cũng kh&ocirc;ng qu&aacute; lạm kia m&agrave;!<br />\r\n<br />\r\nB&agrave; Nghị cười ngặt, cười nghẹo:<br />\r\n<br />\r\n- Thế th&igrave; &ocirc;ng lầm rồi! Con d&acirc;u kia... người ta mới gọi l&agrave; mợ; con g&aacute;i th&igrave; người ta gọi l&agrave; c&ocirc;. Chẳng cứ g&igrave; nh&agrave; c&aacute;c quan, t&ocirc;i thấy những nh&agrave; &ocirc;ng k&yacute;, &ocirc;ng ph&aacute;n, những nh&agrave; bu&ocirc;n b&aacute;n ở H&agrave; Nội đều như thế cả.<br />\r\n<br />\r\n- Miễn l&agrave; đừng c&oacute; gọi ch&uacute;ng n&oacute; bằng c&aacute;i kiểu tục tằn của những nh&agrave; bố cu, bố đĩ... Mợ th&igrave; mợ, c&ocirc; th&igrave; c&ocirc;....<br />\r\n<br />\r\nB&agrave; Nghị ra bộ đắc &yacute;:<br />\r\n<br />\r\n- V&acirc;ng, th&igrave; c&ocirc;!... C&ocirc; hai b&ecirc;n kia hiếm hoi mấy lần xem b&oacute;i, thầy b&oacute;i đều bảo c&ocirc; ấy phải nu&ocirc;i con nu&ocirc;i th&igrave; mới đứng số. Bởi thế, tao muốn mua cho c&ocirc; ấy một đứa, để n&oacute; &quot;g&aacute;nh v&aacute;c đỡ đi&quot;. Chứ nh&agrave; tao đ&acirc;y thiếu g&igrave; người hầu hạ? Vả lại, con b&eacute; mới l&ecirc;n s&aacute;u tuổi, đ&atilde; l&agrave;m được c&ocirc;ng trạng g&igrave; m&agrave; tao phải chuốc? Huống chi n&oacute; sang ở với c&ocirc; ấy, cơm no &aacute;o l&agrave;nh, lại kh&ocirc;ng sung sướng gấp trăm gấp ngh&igrave;n ở nh&agrave; với vợ chồng m&agrave;y hay sao? &ETH;&aacute;ng lẽ biếu kh&ocirc;ng th&igrave; phải... Cho một đồng cũng qu&aacute; lắm rồi. Kh&ocirc;ng phải n&agrave;i nẫn g&igrave; nữa!<br />\r\n<br />\r\nChị Dậu ngồi đờ như gỗ, kh&ocirc;ng biết trả lời ra sao.</h2>', 0, 'chuong-5');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(44, 25, 'Tiếng chim hót trong bụi mận gai - Chương 01-02', 'Tiếng chim hót trong bụi mận gai - Chương 01-02', '<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Ng&agrave;y 8 th&aacute;ng 12 năm 1915, Meggie Cleary bước v&agrave;o năm thứ tư của tuổi đời. Dọn dẹp xong buổi ăn s&aacute;ng, kh&ocirc;ng n&oacute;i một lời v&agrave; hơi đột ngột, mẹ của Meggie đặt l&ecirc;n hai tay c&ocirc; một c&aacute;i hộp g&oacute;i trong giấy m&agrave;u hạt dẻ rồi bảo c&ocirc; b&eacute; ra ngo&agrave;i s&acirc;n. Nghe lời mẹ, Meggie ra ngồi ngay trước cửa nh&agrave;, sau bụi c&acirc;y đậu chổi, rồi vội v&agrave;ng mở chiếc hộp. Mấy ng&oacute;n tay c&ocirc; b&eacute; vụng về, hơn nữa, giấy g&oacute;i rất d&agrave;y. Nhưng ngay l&uacute;c đ&oacute;, một m&ugrave;i thơm thoang thoảng l&agrave;m Meggie nghĩ đến cửa h&agrave;ng b&aacute;ch h&oacute;a Wahinẹ C&ocirc; b&eacute; đo&aacute;n ngay rằng, d&ugrave; b&ecirc;n trong chứa đựng thứ g&igrave; đi nữa, chắc chắn m&oacute;n qu&agrave; ấy cũng được mua từ cửa h&agrave;ng b&aacute;ch h&oacute;a chứ kh&ocirc;ng phải do ở nh&agrave; l&agrave;m hay do ai đ&oacute; đ&atilde; chọ Mở hết lớp giấy b&ecirc;n ngo&agrave;i, Meggie nhận ra một c&aacute;i g&igrave; đ&oacute; thật mịn v&agrave; &oacute;ng &aacute;nh ở g&oacute;c hộp - C&ocirc; b&eacute; x&eacute; toạc ra.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Agn&egrave;s! Ồ Agn&egrave;s! - c&ocirc; b&eacute; th&igrave; thầm một c&aacute;ch &acirc;u yếm, mắt s&aacute;ng l&ecirc;n khi nh&igrave;n thấy một con b&uacute;p b&ecirc; nằm &ecirc;m &aacute;i trong mớ giấy vụn.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Đ&uacute;ng l&agrave; một ph&eacute;p mầu. Một ph&eacute;p mầu thật. Từ trước đến nay Meggie mới đến cửa h&agrave;ng Wahine chỉ một lần th&ocirc;i, hồi th&aacute;ng 5 vừa rồi, c&ocirc; b&eacute; được cha mẹ thưởng v&igrave; đ&atilde; tỏ ra rất ngoan.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Lần đ&oacute; tr&ecirc;n chiếc xe b&ograve; c&oacute; mui, c&ocirc; b&eacute; phải hết sức cố gắng mới ngồi y&ecirc;n được b&ecirc;n cạnh mẹ. Vui qu&aacute;, Meggie kh&ocirc;ng kịp nh&igrave;n thấy g&igrave; v&agrave; cũng kh&ocirc;ng nhớ g&igrave; cả, ngo&agrave;i Agn&egrave;s, con b&uacute;p b&ecirc; tuyệt đẹp mắc chiếc v&aacute;y x&ograve;e rộng bằng xa tanh hồng kết ren chung quanh, được đặt tr&ecirc;n quầy của cửa h&agrave;ng b&aacute;ch h&oacute;a. Ngay l&uacute;c ấy, Meggie k&iacute;n đ&aacute;o đặt t&ecirc;n Agn&egrave;s cho con b&uacute;p b&ecirc; xinh đẹp, c&aacute;i t&ecirc;n kh&aacute; lịch sự m&agrave; c&ocirc; b&eacute; biết. Vậy m&agrave; nhiều th&aacute;ng sau, l&ograve;ng th&egrave;m muốn ấy của Meggie cũng chỉ l&agrave; mơ ước. Meggie chưa bao giờ c&oacute; b&uacute;p b&ecirc; v&agrave; kh&ocirc;ng hề tưởng tượng rằng một b&eacute; g&aacute;i như m&igrave;nh ng&agrave;y n&agrave;o đ&oacute; sẽ c&oacute; một con b&uacute;p bệ L&acirc;u nay c&ocirc; b&eacute; thường vui th&iacute;ch với những thứ đồ chơi m&agrave; c&aacute;c anh trai của c&ocirc; vứt bỏ, chẳng hạn như những c&aacute;i c&ograve;i, c&aacute;i n&aacute; bắn thun v&agrave; những h&igrave;nh l&iacute;nh x&ugrave; x&igrave;. L&agrave;m sao c&ocirc; d&aacute;m mơ ước được chơi với Ang&egrave;s.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Tay vuốt nhẹ l&ecirc;n chiếc &aacute;o của b&uacute;p b&ecirc;, chiếc &aacute;o đẹp nhất trong số những &aacute;o m&agrave; c&ocirc; b&eacute;thấy phụ nữ thường mặc, Meggie bế nhẹ Ang&egrave;s l&ecirc;n. Tay v&agrave; ch&acirc;n của b&uacute;p b&ecirc; đều cử động được, cổ v&agrave; th&acirc;n m&igrave;nh cũng thế. M&agrave;u t&oacute;c v&agrave;ng &oacute;ng &aacute;nh c&oacute; điểm những hạt trai, khu&ocirc;n mặt l&agrave;m bằng sứ được vẽ rất kh&eacute;o v&agrave; đẹp. Mắt b&uacute;p b&ecirc; m&agrave;u xanh, sinh động đến kinh ngạc. Đ&ocirc;i mắt ấy s&aacute;ng l&ecirc;n qua hai h&agrave;ng mi cong v&uacute;t. Khi đặt Ang&egrave;s nằm xuống, Meggie kh&aacute;m ph&aacute; ra rằng b&uacute;p b&ecirc; c&ograve;n biết nhắm mắt lại. Ph&iacute;a tr&ecirc;n g&ograve; m&aacute; cao m&agrave;u hồng c&oacute; một nốt ruồi. Hai m&ocirc;i hơi h&eacute; ra cho thấy những chiếc răng m&agrave;u trắng thật nhỏ. Meggie đặt b&uacute;p b&ecirc; tr&ecirc;n đầu gối, t&igrave;m một chỗ ngồi &ecirc;m &aacute;i rồi nh&igrave;n ngắm một c&aacute;ch say mệ</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Trong khi Meggie vẫn ngồi như thế th&igrave; sau bụi c&acirc;y đậu chổi, Jack v&agrave; Hughie đang chơi đ&ugrave;a b&ecirc;n cạnh đ&aacute;m cỏ mọc cao s&aacute;t h&agrave;ng r&agrave;o. Cả hai nh&igrave;n thấy m&aacute;i t&oacute;c m&agrave;u hung đặc biệt của Meggie, m&agrave;u t&oacute;c đ&uacute;ng l&agrave; của d&ograve;ng họ Cleary, ngoại trừ Frank l&agrave; c&oacute; t&oacute;c m&agrave;u kh&aacute;c.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Jack vừa th&uacute;c c&ugrave;i chỏ v&agrave;o anh m&igrave;nh vừa liến thoắng chỉ Meggiẹ Cả hai đang chơi tr&ograve; qu&acirc;n l&iacute;nh truy l&ugrave;ng một thổ d&acirc;n Maori phản bội. Meggie kh&ocirc;ng để &yacute; đến hai anh v&agrave; vẫn đắm nh&igrave;n Agn&egrave;s th&acirc;n y&ecirc;u, miệng hu&yacute;t gi&oacute; nho nhỏ những &acirc;m thanh quen thuộc.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Meggie, m&agrave;y đang cầm g&igrave; đ&oacute;? Jack vừa la to vừa nh&agrave;o tới - Đưa tao xem.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Ừ, đưa xem.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Hughie n&oacute;i th&ecirc;m v&agrave;o, vừa cười kh&uacute;c kh&iacute;ch vừa t&igrave;m c&aacute;ch chặn Meggie kh&ocirc;ng cho chạy tho&aacute;t.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">C&ocirc; b&eacute; &ocirc;m chặc b&uacute;p b&ecirc; v&agrave;o ngực v&agrave; lắc đầu.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Kh&ocirc;ng, b&uacute;p b&ecirc; của em. M&oacute;n qu&agrave; sinh nhật của em.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Cứ đưa bọn tao xem. Bọn tao chỉ muốn xem qua th&ocirc;i.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Trong niềm vui v&agrave; h&atilde;nh diện vừa được mẹ tặng qu&agrave;, c&ocirc; b&eacute; đưa b&uacute;p b&ecirc; ra khoe:</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- C&aacute;c anh xem n&agrave;y, n&oacute; đẹp qu&aacute;. N&oacute; t&ecirc;n Agn&egrave;s đ&oacute;.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Agn&egrave;s? Agn&egrave;s? Giọng Jack chế diễu. T&ecirc;n g&igrave; m&agrave; tồi thế. Tại sao kh&ocirc;ng gọi n&oacute; l&agrave; Margaret hay Bettỷ</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Kh&ocirc;ng thể gọi kh&aacute;c hơn v&igrave; n&oacute; l&agrave; Agn&egrave;s!</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Hughie nh&igrave;n c&aacute;nh tay con b&uacute;p b&ecirc; cử động, cậu ta hu&yacute;t gi&oacute; v&agrave; n&oacute;i:</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Jack nh&igrave;n xem, tay n&oacute; cử động được đấy.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Cử động l&agrave;m sao? Đưa tao xem.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Kh&ocirc;ng! C&ocirc; b&eacute; la l&ecirc;n v&agrave; lại &ocirc;m b&uacute;p b&ecirc; s&aacute;t v&agrave;o ngực, nước mắt sắp tr&agrave;o ra. - Kh&ocirc;ng, c&aacute;c anh l&agrave;m hư b&uacute;p b&ecirc; của em... A, Jack, đừng đụng v&agrave;o n&oacute;, anh sẽ l&agrave;m g&atilde; b&uacute;p b&ecirc; của em mất.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Hai b&agrave;n tay r&aacute;m nắng, dơ bẩn của Jack chụp l&ecirc;n cổ tay Meggie, siết thật mạnh.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Đừng m&egrave; nheo nữa, tao m&aacute;ch với anh Bob b&acirc;y giờ.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Jack siết mạnh hai cổ tay của Meggie, trong khi Hughie t&uacute;m lấy chiếc v&aacute;y của b&uacute;p b&ecirc; k&eacute;o ra.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Đưa đ&acirc;y - giọng Jack nhỏ nhẹ lại. Nếu kh&ocirc;ng, tao sẽ siết mạnh đau lắm đ&oacute;.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Kh&ocirc;ng, kh&ocirc;ng, Jack! Em năn nỉ anh. Anh l&agrave;m hỏng b&uacute;p b&ecirc; b&acirc;y giờ. Em van anh để cho n&oacute; y&ecirc;n. Đừng lấy của em...</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">D&ugrave; hai tay bị siết c&agrave;ng l&uacute;c c&agrave;ng mạnh, c&ocirc; b&eacute; vẫn kh&ocirc;ng chịu bu&ocirc;ng con b&uacute;p b&ecirc; ra. Meggie kh&oacute;c to l&ecirc;n, ch&acirc;n đ&aacute; v&agrave;o Jack.</span></span></span></span></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Đ&atilde; lấy được rồi! Hughie reo l&ecirc;n đắc thắng khi b&uacute;p b&ecirc; vừa vuột khỏi tay Meggiẹ Chiếc &aacute;o ngo&agrave;i v&agrave; b&ecirc;n trong cả chiếc quần l&oacute;t cũng đều tơi tả. Con b&uacute;p b&ecirc; ho&agrave;n to&agrave;n trần trụi. Ch&uacute;ng nắm hai ch&acirc;n b&uacute;p b&ecirc; k&eacute;o ngược ra ph&iacute;a sau, c&ograve;n c&aacute;i đầu ch&uacute;ng quay đủ hướng v&agrave; kh&ocirc;ng th&egrave;m ch&uacute; &yacute; đến Meggie kh&oacute;c một m&igrave;nh. Trong gia đ&igrave;nh họ Cleary vẫn thế, ai m&agrave; kh&ocirc;ng c&oacute; khả năng đối ph&oacute; th&igrave; chịu chứ kh&ocirc;ng c&oacute; ai hổ trợ hay thương hại. Nguy&ecirc;n tắc ấy cũng &aacute;p dụng cho cả c&aacute;c c&ocirc; g&aacute;i. Từng ch&ugrave;m t&oacute;c v&agrave;ng của con b&uacute;p b&ecirc; bị vứt ra rải đầy dưới đất, c&ograve;n c&aacute;c hạt trai nhấp nh&aacute;nh văng mất trong cỏ, một chiếc gi&agrave;y dơ bẩn v&ocirc; t&igrave;nh dẫm l&ecirc;n chiếc v&aacute;y nằm tr&ecirc;n đất. Meggie quỳ gối xuống, hai tay run rẩy lượm lại những mảnh &aacute;o quần b&eacute; nhỏ của b&uacute;p b&ecirc;, rồi m&ograve; mẫn trong đ&aacute;m cỏ mong t&igrave;m lại những hạt trai. Nước mắt l&agrave;m cho c&ocirc; b&eacute; kh&ocirc;ng c&ograve;n nh&igrave;n thấy g&igrave; nữa, tr&aacute;i tim đau nh&oacute;i một c&aacute;ch lạ l&ugrave;ng, v&igrave; rằng từ trước đến nay, chưa bao giờ c&ocirc; b&eacute; lại cảm nhận một điều g&igrave; đ&oacute; c&oacute; thể gọi l&agrave; đau đớn.</span></span></span></span></h2>\r\n\r\n<div class=\"truyen_text\" style=\"border:0px; padding:0px; text-align:justify\">\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\"><span style=\"font-size:medium\">&nbsp;chương 2:</span></span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Frank nh&uacute;ng thanh sắt đỏ v&agrave;o nước lạnh g&acirc;y n&ecirc;n một tiếng x&egrave;o, rồi đứng thẳng người l&ecirc;n. Anh kh&ocirc;ng c&ograve;n đau lưng nữa. C&oacute; lẽ v&igrave; đ&atilde; quen c&ocirc;ng việc ở l&ograve; r&egrave;n. Chỉ cần s&aacute;u th&aacute;ng th&ocirc;i - cha anh n&oacute;i như thế. Nhưng Frank hiểu l&agrave; biết bao nhi&ecirc;u năm th&aacute;ng tr&ocirc;i qua từ khi anh l&agrave;m quen với nơi đ&acirc;y; anh đ&atilde; đếm thời gian trong o&aacute;n giận v&agrave; hận th&ugrave;. Quẳng chiếc b&uacute;a v&agrave;o th&ugrave;ng, b&agrave;n tay run run, Frank vuốt ngược m&aacute;i t&oacute;c phủ k&iacute;n vầng tr&aacute;n rồi cởi tấm tạp đề cũ m&egrave;m bằng dạ Chiếc &aacute;o sơ mi nằm chờ Frank tr&ecirc;n đống rơm, anh bước đi nặng nề, dừng lại một l&uacute;c v&agrave; &aacute;nh mắt mất h&uacute;t qua một khe hở của nh&agrave; khọ Mắt anh đen, mở trừng trừng nh&igrave;n về hướng ấy.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">D&aacute;ng người hơi thấp nhỏ, khoảng một thước s&aacute;u mươi, Frank gầy như những đứa con trai đang lớn, nhưng vai v&agrave; tay trần của anh đều g&acirc;n guốc v&igrave; cầm b&uacute;a l&acirc;u ng&agrave;y. Nước da hơi xanh, mịn, b&oacute;ng lo&aacute;ng mồ h&ocirc;i. T&oacute;c v&agrave; cặp mắt đen l&aacute;nh c&oacute; c&aacute;i g&igrave; đ&oacute; xa lạ v&igrave; rằng đ&ocirc;i m&ocirc;i d&agrave;y v&agrave; c&aacute;i mũi th&ocirc; thẳng kh&ocirc;ng phảng phất ch&uacute;t n&agrave;o những n&eacute;t chung nhất của gia đ&igrave;nh n&agrave;y. Ở mẹv&agrave; Frank h&igrave;nh như c&oacute; d&ograve;ng m&aacute;u Maorị Frank gần 16 tuổi, Bob 11, Jack 10, Hughie 9, Stuart 5 v&agrave; c&ocirc; b&eacute; Meggie... Frank nhớ ra h&ocirc;m nay ng&agrave;y 8 th&aacute;ng 12 Meggie vừa tr&ograve;n 4 tuổi. Anh mặc &aacute;o v&agrave;o v&agrave; rời nh&agrave; khọ</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Căn nh&agrave; của gia đ&igrave;nh Cleary nằm tr&ecirc;n một ngọn đồi cao hơn nh&agrave; kho v&agrave; chuồng ngựa chừng ba mươi m&eacute;t. Hầu hết nh&agrave; cửa ở T&acirc;y T&acirc;y Lan được x&acirc;y bằng gỗ, kh&ocirc;ng c&oacute; lầu, d&agrave;i v&agrave; rộng để khi c&oacute; động đất th&igrave; kh&ocirc;ng bị sập tất cả. Chung quanh nh&agrave; mọc đầy những c&acirc;y đậu chổi, m&ugrave;a n&agrave;y trĩu nặng những b&ocirc;ng v&agrave;ng; cỏ mọc bao la, xanh r&igrave; như phần nhiều c&aacute;c đồng cỏ nu&ocirc;i s&uacute;c vật. Ngay giữa m&ugrave;a đ&ocirc;ng, c&oacute; những mảng tuyết suốt cả ng&agrave;y kh&ocirc;ng tan v&igrave; rợp b&oacute;ng m&aacute;t, cỏ vẫn kh&ocirc;ng v&agrave;ng &uacute;a. Rồi m&ugrave;a h&egrave; d&agrave;i v&agrave; dịu d&agrave;ng mang trả lại cho thi&ecirc;n nhi&ecirc;n một m&agrave;u xanh đậm. Mưa cũng kh&ocirc;ng qu&aacute; nặng hạt để c&oacute; thể l&agrave;m g&atilde; cả những lo&agrave;i c&acirc;y mảnh khảnh nhất. Tai họa ở T&acirc;y T&acirc;y Lan gầm l&ecirc;n từ trong l&ograve;ng đất chứ kh&ocirc;ng bao giờ sa xuống từ tr&ecirc;n trời. Bao giờ cũng thế, con người sống ở nơi đ&acirc;y như lu&ocirc;n ở trong một đợi chờ thấp thỏm trước sự chuyển động, tưởng chừng nghe thấy dưới ch&acirc;n m&igrave;nh những tiếng &acirc;m thanh kh&ocirc;ng ngớt vang dội từ l&ograve;ng đất. V&igrave; rằng b&ecirc;n trong l&ograve;ng đất ẩn chứa một sức mạnh kinh ho&agrave;ng, một sức mạnh c&oacute; bi&ecirc;n độ dữ dội đến đỗi c&aacute;ch đấy ba mươi năm, một quả n&uacute;i nguy&ecirc;n vẹn đ&atilde; bị x&oacute;a mất ho&agrave;n to&agrave;n.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">D&ugrave; thế, đất ở đ&acirc;y thật hiền h&ograve;a, hiếu kh&aacute;ch. Từ nh&agrave; nh&igrave;n ra xa l&agrave; một c&aacute;nh đồng xanh như m&agrave;u ngọc b&iacute;ch tr&ecirc;n chiếc nhẫn đ&iacute;nh h&ocirc;n của Fiona Clearỵ Đường viền của c&aacute;c ngọn đồi vẽ l&ecirc;n nền trời m&agrave;u xanh nhạt; ngọn n&uacute;i Egmont cao ba ng&agrave;n m&eacute;t như muốn đ&acirc;m thủng c&aacute;c tầng m&acirc;y. Sườn n&uacute;i hai b&ecirc;n kho&aacute;c m&agrave;u trắng của tuyết hết sức c&acirc;n đối khiến cho Frank vội v&atilde; v&igrave; anh nhớ lời cha dặn kh&ocirc;ng được rời bỏ l&ograve; r&egrave;n. Lệnh của cha anh l&uacute;c n&agrave;o cũng nghi&ecirc;m khắc. Đi v&ograve;ng v&agrave;o nh&agrave;, Frank bắt gặp mấy đứa em.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Ch&iacute;nh Frank đ&atilde; đưa mẹ đi Wahine để mua b&uacute;p b&ecirc; cho Meggie, tuy nhi&ecirc;n anh đ&atilde; phải tự hỏi điều g&igrave; đ&atilde; phải th&uacute;c đẩy mẹ anh chịu ti&ecirc;u x&agrave;i như thế. Với mẹ, một m&oacute;n qu&agrave; sinh nhật phải l&agrave; một thứ g&igrave; c&oacute; &iacute;ch lợi cho cuộc sống hiện tại; gia đ&igrave;nh kh&ocirc;ng dư dả tiền để phung ph&iacute;. Kh&ocirc;ng bao giờ mẹ anh mua đồ chơi cho bất cứ ai để l&agrave;m qu&agrave; sinh nhật. C&aacute;c con của b&agrave; nh&acirc;n ng&agrave;y n&agrave;y đều nhận được quần &aacute;o. Ng&agrave;y sinh nhật hoặc Noel l&agrave; dịp để sắm th&ecirc;m m&oacute;n g&igrave; đ&oacute; cho tủ &aacute;o quần c&ograve;n rất trống trải. C&oacute; lẽ mẹ đ&atilde; bắt gặp c&aacute;i nh&igrave;n th&egrave;m muốn của Meggie trong lần ra tỉnh. Khi Frank hỏi mẹ về quyết định bất thường n&agrave;y, b&agrave; chỉ n&oacute;i đ&ocirc;i ba c&acirc;u về niềm mong ước của mấy c&ocirc; b&eacute; c&oacute; được con b&uacute;p b&ecirc;, rồi b&agrave; chuyển sang đề t&agrave;i kh&aacute;c.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Giữa lối đi, Jack v&agrave; Hughie đang giằng co, cả hai đứa đang nắm tay ch&acirc;n con b&uacute;p b&ecirc; một c&aacute;ch th&ocirc; bạo. Frank chỉ nh&igrave;n thấy Meggie từ ph&iacute;a sau lưng, c&ocirc; b&eacute; đang đứng như trời trồng nh&igrave;n hai anh m&igrave;nh dằn vặt Agn&egrave;s.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Đồ qui? sứ, bẩn thỉu. Frank qu&aacute;t.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Jack v&agrave; Hughie nhanh ch&acirc;n, biến ngay v&agrave; bỏ lại con b&uacute;p bệ Khi Frank nổi n&oacute;ng th&igrave; kh&ocirc;n hồn bỏ đi nơi kh&aacute;c.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Tao m&agrave; bắt gặp tụi b&acirc;y rớ v&agrave;o con b&uacute;p b&ecirc; lần nữa, th&igrave; tụi b&acirc;y lảnh ngay một thanh sắt đỏ v&agrave;o m&ocirc;ng, đồ thối tha! Frank h&eacute;t l&ecirc;n.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Frank khom người xuống, hai tay đặt l&ecirc;n vai Meggie lay nhẹ.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Th&ocirc;i! th&ocirc;i! c&oacute; g&igrave; đ&acirc;u m&agrave; kh&oacute;c! Đừng sợ. Tụi n&oacute; bỏ đi hết rồi v&agrave; kh&ocirc;ng bao giờ d&aacute;m sờ v&agrave;o con b&uacute;p b&ecirc; của em đ&acirc;u. Anh hứa với em m&agrave;. B&acirc;y giờ em h&atilde;y tặng cho anh một nụ cười sinh nhật đi n&agrave;o...</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Mặt c&ocirc; b&eacute; như sưng, m&iacute; mắt phồng l&ecirc;n. C&ocirc; nh&igrave;n Frank tr&acirc;n tr&acirc;n bằng đ&ocirc;i mắt n&acirc;u, mở to, chứa đầy bi kịch vừa xảy ra v&agrave; c&ocirc; b&eacute; kh&ocirc;ng n&oacute;i n&ecirc;n lời. Frank lấy từ trong t&uacute;i quần một chiếc khăn bẩn, lau mặt em g&aacute;i một c&aacute;ch vụng về, rồi đặt khăn l&ecirc;n mũi bảo: - Hỉ mũi đi cưng.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">C&ocirc; b&eacute; nghe lời, hết kh&oacute;c nhưng tiếng nấc vẫn chưa dứt.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Frank - Frank - Frank! Mấy ảnh... đ&atilde; lấy... Agn&egrave;s của em! T&oacute;c của Agn&egrave;s bị rứt ra hết rồi v&agrave; bị mất cả những hạt trai xinh đẹp. Frank, mấy hạt trai đ&oacute; rơi trong cỏ đ&acirc;y n&egrave;... nhưng em t&igrave;m kh&ocirc;ng ra.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Rồi c&ocirc; b&eacute; lại kh&oacute;c. Giọt nước mắt rơi xuống tay Frank. Anh đưa tay l&ecirc;n nh&igrave;n v&agrave; lấy lưỡi liếm.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- B&acirc;y giờ phải t&igrave;m lại mấy hạt trai. Nhưng em sẽ kh&ocirc;ng t&igrave;m thấy nếu cứ kh&oacute;c m&atilde;i. C&ograve;n chuyện n&agrave;y nữa, sao em vẫn ngọng nghịu như một em b&eacute; mới biết n&oacute;i? Hỉ mũi nữa đi v&agrave; nhặt Agn&egrave;s tội nghiệp của em l&ecirc;n. Nếu em kh&ocirc;ng mặc ngay quần &aacute;o v&agrave;o cho n&oacute;, n&oacute; sẽ bị cảm nắng b&acirc;y giờ.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Frank đặt Meggie ngồi xuống b&ecirc;n lối đi rồi dịu d&agrave;ng trao cho c&ocirc; b&eacute; cho b&uacute;p bệ Xong cậu ta vẹt cỏ đi t&igrave;m hạt trai, kh&ocirc;ng mấy chốc vừa reo l&ecirc;n một c&aacute;ch đắc thắng vừa giơ cao cho Meggie xem hạt trai mới t&igrave;m được.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Đ&acirc;y mới chỉ l&agrave; hạt thứ nhất. Em chờ xem hai anh em m&igrave;nh sẽ t&igrave;m lại đủ tất cả.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Meggie nh&igrave;n người anh cả của m&igrave;nh bằng đ&ocirc;i mắt ngưởng mộ trong khi Frank tiếp tục vẹt cỏ, thỉnh thoảng đưa l&ecirc;n cao khoe một hạt trai vừa t&igrave;m được. B&acirc;y giờ Meggie bỗng nhớ lại l&agrave;n da của Agn&egrave;s rất mỏnh dễ bị &aacute;nh mặt trời thi&ecirc;u đốt, thế l&agrave; c&ocirc; b&eacute; chăm ch&uacute; lo mặc quần &aacute;o lại cho b&uacute;p bệ Thật ra b&uacute;p b&ecirc; cũng kh&ocirc;ng mang một vết thương n&agrave;o đ&aacute;ng kể, t&oacute;c chỉ rối l&ecirc;n, tay ch&acirc;n dơ bẩn nhưng vẫn cử động được b&igrave;nh thường. Tr&ecirc;n m&aacute;i t&oacute;c của Meggie ở mỗi b&ecirc;n c&oacute; chiếc lược c&agrave;i, c&ocirc; b&eacute; lấy xuống một c&aacute;i để chải t&oacute;c cho b&uacute;p bệ</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Vụng về, c&ocirc; b&eacute; l&agrave;m một chiếc nơ lớn tr&ecirc;n đầu của Agn&egrave;s bung ra. Thảm kịch. Tất cả t&oacute;c tai Agn&egrave;s đều s&uacute;t hết v&agrave; d&iacute;nh tr&ecirc;n chiếc lược c&agrave;i. Ph&iacute;a tr&ecirc;n c&aacute;i tr&aacute;n rộng của b&uacute;p b&ecirc; chẳng c&oacute; g&igrave; kh&aacute;c. Kh&ocirc;ng c&oacute; đầu, kh&ocirc;ng c&oacute; xương sọ Chỉ c&oacute; một lỗ trống t&aacute;c ho&aacute;c. Meggie run rẩy gần như hoảng hốt, nhưng mắt vẫn nh&igrave;n v&agrave;o ph&iacute;a trong lỗ trống. Những đường n&eacute;t lồi l&otilde;m của mắt, mũi, miệng nh&igrave;n từ b&ecirc;n trong thật khiếp đảm, nhất l&agrave; hai con mắt của Agn&egrave;s chỉ l&agrave; hai lỗ s&acirc;u hoắm, c&oacute; một sợi d&acirc;y kẽm xuy&ecirc;n qua...</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Tiếng h&eacute;t ch&aacute;t ch&uacute;a của Meggie kh&ocirc;ng phải l&agrave; tiếng h&eacute;t của trẻ thợ Meggie quăng Agn&egrave;s xuống đất rồi tiếp tục la, hai tay &ocirc;m mặt, run rẩy. Frank đến gỡ c&aacute;c ng&oacute;n tay của Meggie rồi bế em g&aacute;i v&agrave;o l&ograve;ng, đặt đầu c&ocirc; b&eacute; v&agrave;o chỗ &ecirc;m ả nhất giữa cổ v&agrave; vai m&igrave;nh. C&ocirc; b&eacute; c&agrave;ng thu m&igrave;nh s&aacute;t v&agrave;o Frank, t&igrave;m kiếm nơi người an niềm an ủi, cho đến l&uacute;c cơn x&uacute;c động giảm xuống, c&ocirc; b&eacute; mới ngửi thấy c&aacute;i m&ugrave;i dể chịu từ th&acirc;n người của Frank, đ&oacute; l&agrave; m&ugrave;i ngựa, m&ugrave;i mồ h&ocirc;i v&agrave; sắt nung đỏ.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Khi Meggie y&ecirc;n l&ograve;ng trở lại, Frank hỏi em g&aacute;i của m&igrave;nh nguy&ecirc;n do của sự hoảng hốt. Anh ta nhặt b&uacute;p b&ecirc; l&ecirc;n v&agrave; đau x&oacute;t nh&igrave;n thấy c&aacute;i đầu trống rỗng v&agrave; cố nhớ xem trong c&aacute;i thế giới tuổi thơ của m&igrave;nh c&oacute; bao giờ xảy ra nổi khiếp sợ lạ l&ugrave;ng như thế kh&ocirc;ng. Anh nhớ lại h&igrave;nh ảnh đ&aacute;ng gh&eacute;t lởn vởn như những b&oacute;ng ma, những tiếng n&oacute;i x&igrave; x&agrave;o v&agrave; những c&aacute;i nh&igrave;n gh&eacute;t bỏ. C&ograve;n gương mặt của mẹ nhăn nh&oacute;, khắc khổ, b&agrave;n tay của mẹ run rẩy nắm chặt lấy tay m&igrave;nh, người căng thẳng.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">C&ograve;n Meggie đ&atilde; thấy g&igrave; m&agrave; hoảng sợ như thế? Anh biết rằng Meggie sẽ &iacute;t hoảng hốt hơn nếu Agn&egrave;s đ&aacute;ng thương bị chảy m&aacute;u khi t&oacute;c bị rứt ra. D&ugrave; cho m&aacute;u c&oacute; thật. V&igrave; rằng trong gia đ&igrave;nh họ Cleary kh&ocirc;ng c&oacute; tuần n&agrave;o l&agrave; tr&aacute;nh được chuyện người n&agrave;y hay người nọ bị đổ m&aacute;u.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Meggie n&oacute;i thật nhỏ:</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Em sợ hai con mắt của n&oacute; - Trong khi n&oacute;i, Meggie kh&ocirc;ng d&aacute;m nh&igrave;n con b&uacute;p bệ</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Kh&ocirc;ng Meggie, đừng sợ, n&oacute; đẹp tuyệt, một kỳ quan.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Frank c&uacute;i mặt v&agrave;o m&aacute;i t&oacute;c của em g&aacute;i - m&aacute;i t&oacute;c đẹp, d&agrave;y v&agrave; s&aacute;ng rực. Phải mất nửa tiếng đồng hồ vỗ về, Frank mới thuyết phục được Meggie chịu quay lưng lại nh&igrave;n Agn&egrave;s v&agrave; th&ecirc;m một khoảng thời gian như thế để d&aacute;m nh&igrave;n thẳng v&agrave;o c&aacute;i lỗ trống tr&ecirc;n đầu b&uacute;p bệ Frank chỉ cho em xem hai con mắt của b&uacute;p b&ecirc; động đậy như thế n&agrave;o, bằng c&aacute;ch n&agrave;o m&agrave; hai mắt mở ra v&agrave; kh&eacute;p lại.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Th&ocirc;i b&acirc;y giờ đ&atilde; đến l&uacute;c anh em m&igrave;nh phải v&agrave;o nh&agrave;. Frank bế Meggie bằng một c&aacute;nh tay, con b&uacute;p b&ecirc; đặt giữa ngực Frank v&agrave; ngực Meggiẹ M&igrave;nh sẽ nhờ mẹ chữa lại con b&uacute;p bệ Mẹ sẽ giặt, ủi lại quần &aacute;o cho n&oacute; v&agrave; t&igrave;m c&aacute;ch d&aacute;n lại bộ t&oacute;c. C&ograve;n anh sẽ đ&iacute;nh lại hạt trai bằng những c&acirc;y kim găm, chắc chắn sẽ kh&ocirc;ng bao giờ bị rơi ra nữa, mặc t&igrave;nh cho em muốn chải kiểu t&oacute;c n&agrave;o cho n&oacute; cũng được.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Fiona Cleary đang ở trong bếp gọt khoai. Đ&oacute; l&agrave; một người đ&agrave;n b&agrave; c&oacute; nước da hơi nhợt nhạt, người thấp, nhưng gương mặt kh&aacute; nghi&ecirc;m v&agrave; cứng rắn. B&agrave; c&oacute; một d&aacute;ng đẹp, một th&acirc;n h&igrave;nh mảnh khảnh d&ugrave; đ&atilde; s&aacute;u lần sinh. Chiếc &aacute;o d&agrave;i vải tr&uacute;c b&acirc;u gần chấm đất, thật sạch; một chiếc tạp dề lớn m&agrave;u trắng, hồ cứng, ph&iacute;a tr&ecirc;n được buộc quanh cổ, ph&iacute;a dưới &ocirc;m th&acirc;n người v&agrave; cột lại bằng một c&aacute;i nơ thắt sau lưng thật gọn v&agrave; đẹp. Từ s&aacute;ng đến chiếu tối, b&agrave; ở trong bếp v&agrave; ở khu vườn ph&iacute;a sau nh&agrave;; đ&ocirc;i gi&agrave;y ống ngắn m&agrave;u đen rất bẩn của b&agrave; chỉ c&oacute; một con đường quen thuộc lui tới bếp đến chỗ giặt quần &aacute;o, từ vườn ra đến s&agrave;o phơi quần &aacute;o rồi quay trở lại bếp.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">B&agrave; đặt con dao l&ecirc;n b&agrave;n, nh&igrave;n Frank v&agrave; Meggie; m&ocirc;i b&agrave; bỗng m&iacute;m lại:</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Meggie, mẹ chỉ cho ph&eacute;p con mặc chiếc &aacute;o đẹp nhất v&agrave;o ng&agrave;y chủ nhật với một điều kiện: con kh&ocirc;ng l&agrave;m bẩn. B&acirc;y giờ con nh&igrave;n xem ra c&aacute;i thứ g&igrave;. Đ&uacute;ng l&agrave; một đứa trẻ dơ d&aacute;y.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Mẹ ơi, kh&ocirc;ng phải lỗi của Meggie đ&acirc;u - Frank b&agrave;o chữa. Ch&iacute;nh Jack v&agrave; Hughie đ&atilde; gi&agrave;nh lấy b&uacute;p b&ecirc; của Meggie để xem tay ch&acirc;n cử động như thế n&agrave;o. Con c&oacute; hứa với Meggie l&agrave; mẹ v&agrave; con sẽ chữa lại con b&uacute;p bệ L&agrave;m được phải kh&ocirc;ng mẹ?</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- Con đưa mẹ xem - Fiona vừa n&oacute;i vừa đưa tay ra. B&agrave; l&agrave; một người lặng lẽ, &iacute;t n&oacute;i, v&agrave; c&oacute; vẻ như sống kh&ocirc;ng được tự nhi&ecirc;n. Những g&igrave; trong đầu b&agrave; kh&oacute; ai đo&aacute;n biết, ngay cả chồng b&agrave;. B&agrave; nhường quyền gi&aacute;o dục c&aacute;c con cho chồng v&agrave; tu&acirc;n thủ c&aacute;c mệnh lệnh của chồng m&agrave; kh&ocirc;ng c&oacute; một phản ứng hay than phiền n&agrave;o, ngoại trừ một v&agrave;i trường hợp hết sức đặc biệt. Meggie nghe c&aacute;c anh m&igrave;nh n&oacute;i nhỏ với nhau rằng mẹ cũng sợ ba như ch&uacute;ng v&agrave; nếu thật thế th&igrave; mẹ đ&atilde; giấu giếm c&aacute;i sợ ấy dưới một lớp ngo&agrave;i b&igrave;nh thản. Tr&ecirc;n n&eacute;t mặt ấy cũng tỏ ra một ch&uacute;t bướng bỉnh. B&agrave; kh&ocirc;ng bao giờ cười, cũng như kh&ocirc;ng bao giờ nổi giận.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Fiona đặt con b&uacute;p b&ecirc; l&ecirc;n c&aacute;i kệ gần bếp v&agrave; nh&igrave;n Meggie:</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">- S&aacute;ng mai mẹ sẽ giặt quần &aacute;o cho Agn&egrave;s v&agrave; sẽ chải t&oacute;c lại. Sau buổi ăn chiều nay, anh con sẽ d&aacute;n t&oacute;c v&agrave; kh&ocirc;ng chừng phải tắm cho n&oacute; sạch sẽ.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Meggie tỏ ra bằng l&ograve;ng, nụ cười tr&ecirc;n m&ocirc;i. Đ&ocirc;i l&uacute;c Meggie rất th&egrave;m được nghe tiếng cười của mẹ nhưng điều đ&oacute; lại kh&ocirc;ng bao giờ c&oacute; được. Meggie cảm gi&aacute;c rằng m&igrave;nh đang chia xẻ với mẹ một điều g&igrave; đ&oacute; rất đặc biệt m&agrave; cha c&ocirc; v&agrave; c&aacute;c anh kh&ocirc;ng thể hiểu; thế m&agrave; c&ocirc; b&eacute; vẫn kh&ocirc;ng thể n&agrave;o kh&aacute;m ph&aacute; những g&igrave; chứa đựng b&ecirc;n trong con người m&agrave; Meggie hết l&ograve;ng y&ecirc;u thương.</span></span></span></span></h2>\r\n\r\n<h2><span style=\"font-size:22px\"><span style=\"font-family:&quot;Palatino Linotype&quot;,Arial,&quot;Times New Roman&quot;,sans-serif\"><span style=\"color:#2b2b2b\"><span style=\"background-color:#f4f4f4\">Ngo&agrave;i Frank, kh&ocirc;ng một đứa con trong gia đ&igrave;nh n&agrave;y hiểu được rằng Fiona lu&ocirc;n ở trong t&acirc;m trạng mệt mỏi, sự mệt mỏi kh&ocirc;ng dễ g&igrave; vơi bớt. C&oacute; qu&aacute; nhiều việc phải l&agrave;m nhưng lại kh&ocirc;ng c&oacute; dư tiền v&agrave; kh&ocirc;ng đủ th&igrave;giờ giải quyết. B&agrave; chỉ tr&ocirc;ng cậy v&agrave;o đ&ocirc;i taỵ Ngay b&acirc;y giờ c&ocirc; b&eacute; cũng đ&atilde; gi&uacute;p mẹ một số c&ocirc;ng việc vặt m&atilde;nh nhưng với bốn tuổi đầu, Meggie chưa đủ sức l&agrave;m nhẹ bớt g&aacute;nh nặng của b&agrave;. S&aacute;u đứa con trai, chỉ c&oacute; một đứa con g&aacute;i, lại l&agrave; con &uacute;t. Những ai quen biết gia đ&igrave;nh n&agrave;y đều thương hại b&agrave; nhưng điều ấy đ&acirc;u c&oacute; l&agrave;m thay đổi được g&igrave;. C&aacute;i giỏ đựng đồ cần v&aacute; lại đầy những chiếc vớ chưa được mạng, c&oacute; cả que đang d&iacute;nh v&agrave;o một chiếc vớ kh&aacute;c đang dở dang. Hughie đ&atilde; cao nh&ograve;ng kh&ocirc;ng c&ograve;n mặc được những c&aacute;i &aacute;o xănđai cũ; với Jack th&igrave; lại kh&ocirc;ng đủ lớn để mặc &aacute;o của anh.</span></span></span></span></h2>\r\n</div>', 0, 'tieng-chim-hot-trong-bui-man-gai-chuong-01-02');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(45, 25, 'Tiếng chim hót trong bụi mận gai - Chương 03-04', 'Tiếng chim hót trong bụi mận gai - Chương 03-04', '<h2>Padraic Cleary - tức Pađy - c&oacute; mặt ở nh&agrave; trong tuần lễ sinh nhật của Meggie l&agrave; chuyện ho&agrave;n to&agrave;n t&igrave;nh cờ. M&ugrave;a cắt long cừu chưa đến, &ocirc;ng tạm l&agrave;m việc gần nh&agrave;, l&agrave;m đất v&agrave; trồng trọt. Pađy chuy&ecirc;n nghề cắt l&ocirc;ng cừu, l&agrave;m thời vụ từ giữa m&ugrave;a h&egrave; đến hết m&ugrave;a đ&ocirc;ng, sau đ&oacute; l&agrave; thời gian cừu đẻ. Th&ocirc;ng thường Pađy t&igrave;m đủ việc l&agrave;m để chịu đựng qua m&ugrave;a xu&acirc;n v&agrave; những th&aacute;ng đầu m&ugrave;a h&egrave;; &ocirc;ng phụ lo cho cừu đẻ, c&agrave;y đất, vắt sữa b&ograve;, hai lần mỗi ng&agrave;y ở trang trại kế b&ecirc;n. &ocirc;ng sẵn s&agrave;ng đi đến bất cứ đ&acirc;u miễn c&oacute; việc l&agrave;m, để mặc gia đ&igrave;nh sống trong căn nh&agrave; tuy lớn nhưng c&oacute; vẻ cổ xưa, một m&igrave;nh vợ phải tự lo liệu c&aacute;c thứ. &ocirc;ng kh&ocirc;ng thể l&agrave;m kh&aacute;c hơn v&igrave; kh&ocirc;ng c&oacute; đất đai ri&ecirc;ng.</h2>\r\n\r\n<h2>Mỗi ng&agrave;y Pađy về nh&agrave; l&uacute;c mặt trời khuất b&oacute;ng, đ&egrave;n đ&atilde; đốt l&ecirc;n, in r&otilde; những c&aacute;i b&oacute;ng đen nhảy m&uacute;a tr&ecirc;n trần. Tập hợp ở m&aacute;i hi&ecirc;n nh&agrave; sau, mấy đứa con trai chơi đ&ugrave;a với một con ếch, ngoại trừ Frank. Padraic dư biết con trai lớn của &ocirc;ng hiện giờ ở đ&acirc;u. &ocirc;ng nghe r&otilde; tiếng bửa củi đều đặn. &ocirc;ng dừng lại ở hi&ecirc;n nh&agrave;, đ&aacute; v&agrave;o m&ocirc;ng Jack một c&aacute;i v&agrave; đưa tay xoa đầu Bob một c&aacute;ch thương y&ecirc;u:</h2>\r\n\r\n<h2>- Ra phụ Frank chẻ củi, đồ l&agrave;m biếng. R&aacute;ng l&agrave;m xong trước khi mẹ đặt b&agrave;n, bằng kh&ocirc;ng tao sẽ cho một trận n&ecirc;n th&acirc;n.</h2>\r\n\r\n<h2>Padraic ra hiệu bằng đầu hướng về Fiona đang bận rộn b&ecirc;n c&aacute;i chảo; &ocirc;ng kh&ocirc;ng bao giờ &ocirc;m h&ocirc;n vợ trước mặt con c&aacute;i. Những biểu hiện t&igrave;nh cảm ấy theo &ocirc;ng n&ecirc;n giới hạn trong ph&ograve;ng ngủ của hai vợ chồng. &ocirc;ng cởi đ&ocirc;i ủng d&iacute;nh đầy b&ugrave;n; Meggie mang đến cho cha một đ&ocirc;i d&eacute;p. &ocirc;ng nở nụ cười đ&oacute;n lấy. Trong l&ograve;ng lu&ocirc;n tr&agrave;n ngập một t&igrave;nh cảm rất lạ như bị chinh phục mỗi khi nh&igrave;n thấy Meggie con g&aacute;i m&igrave;nh. N&oacute; xinh qu&aacute;, t&oacute;c đẹp l&agrave;m sao; &ocirc;ng cầm một loạn t&oacute;c lay nhẹ rồi thả xuống, th&iacute;ch th&uacute; nh&igrave;n con. &ocirc;ng nhấc bổng Meggie l&ecirc;n rồi hai cha con c&ugrave;ng ngả xuống chiếc ghế b&agrave;nh &ecirc;m &aacute;i đặt ở trong nh&agrave; bếp, đ&oacute; l&agrave; chiếc ghế b&agrave;nh thời Windsor, c&oacute; l&oacute;t gối dựa. Meggie nằm gọn trong l&ograve;ng cha, hai tay b&aacute; v&agrave;o cổ, gương mặt tươi tắn ngước l&ecirc;n hy vọng được đ&ugrave;a giỡn với tr&ograve; chơi buổi tối - nh&igrave;n &aacute;nh s&aacute;ng xuy&ecirc;n qua những sợi r&acirc;u ngắn m&agrave;u v&agrave;ng của chạ</h2>\r\n\r\n<h2>- Em c&oacute; thấy khỏe kh&ocirc;ng Feẻ - Pađy hỏi thăm vợ.</h2>\r\n\r\n<h2>- B&igrave;nh thường th&ocirc;i anh ạ. C&ograve;n anh đ&atilde; l&agrave;m xong c&aacute;i h&agrave;ng r&agrave;o dưới kia chưa?</h2>\r\n\r\n<h2>- Xong rồi. Anh c&oacute; thể bắt tay l&agrave;m h&agrave;ng r&agrave;o ph&iacute;a tr&ecirc;n v&agrave;o s&aacute;ng mai. Nhưng lạy ch&uacute;a, anh mệt qu&aacute;.</h2>\r\n\r\n<h2>- Em cũng thấy anh mệt... Roberson c&ograve;n giao cho anh con ngựa c&aacute; t&iacute;nh bất thường nữa phải kh&ocirc;ng?</h2>\r\n\r\n<h2>- Vẫn thế - Chẳng lẽ em nghĩ rằng hắn sẽ l&agrave;m việc với con ngựa tồi ấy v&agrave; d&agrave;nh cho anh một con ngựa tốt hơn sao? Tay anh r&atilde; rời cả. H&igrave;nh như con ngựa c&aacute;i n&agrave;y thuộc loại cứng đầu nhất ở T&acirc;n T&acirc;y Lan.</h2>\r\n\r\n<h2>- Kh&ocirc;ng quan trọng. Mấy con ngựa của &ocirc;ng gi&agrave; Roberson đều thuần cả. Sắp tới anh sẽ l&agrave;m với l&atilde;o phải kh&ocirc;ng?</h2>\r\n\r\n<h2>- Chưa l&agrave;m ngay đ&acirc;u.</h2>\r\n\r\n<h2>&ocirc;ng nhồi thuốc loại rẻ tiền v&agrave;o c&aacute;i tẩu rồi lấy một que củi mồi lửa từ trong l&ograve; đang ch&aacute;y đỏ. Sau đ&oacute; &ocirc;ng lại ngả người l&ecirc;n ghế b&agrave;nh, r&iacute;t mạnh một hơi.</h2>\r\n\r\n<h2>- Con g&aacute;i n&oacute;i cho ba nghe con thấy thế n&agrave;o khi được bốn tuổi - Pađy hỏi con.</h2>\r\n\r\n<h2>- Con thấy th&iacute;ch lắm ba ơi.</h2>\r\n\r\n<h2>- Mẹ c&oacute; cho con qu&agrave; kh&ocirc;ng?</h2>\r\n\r\n<h2>- Tại sao ba v&agrave; mẹ biết được Agn&egrave;s m&agrave; chọn cho con?</h2>\r\n\r\n<h2>- Agn&egrave;s? (&ocirc;ng liếc nh&igrave;n qua Fiona, vừa mỉm cười vừa nh&iacute;u m&agrave;y d&ograve; vợ). C&oacute; phải t&ecirc;n của n&oacute; l&agrave; Agn&egrave;s?</h2>\r\n\r\n<h2>đạ, N&oacute; đẹp tuyệt ba ạ. Con muốn nh&igrave;n n&oacute; suốt ng&agrave;y.</h2>\r\n\r\n<h2>- Rất may l&agrave; c&ograve;n lại c&aacute;i g&igrave; đ&oacute; để nh&igrave;n - Fiona n&oacute;i bằng giọng hơi cằn nhằn. Jack v&agrave; Hughie đ&atilde; đoạt con b&uacute;p b&ecirc; trước khi Meggie c&oacute; thời giờ nh&igrave;n ngắm.</h2>\r\n\r\n<h2>- Hừ! Lại chuyện khỉ của mấy thằng con trai, phải trị ch&uacute;ng. Con b&uacute;p b&ecirc; con c&oacute; hỏnh nhiều h&ocirc;ng?</h2>\r\n\r\n<h2>- Kh&ocirc;ng đến nỗi. Frank đ&atilde; bắt gặp hai đứa kịp l&uacute;c.</h2>\r\n\r\n<h2>- Frank? L&agrave;m sao n&oacute; lại c&oacute; mặt ở đ&oacute;? N&oacute; phải ở dưới l&ograve; r&egrave;n suốt ng&agrave;y kia m&agrave;. Humter chờ n&oacute; l&agrave;m xong c&aacute;i c&aacute;nh cửa nhỏ.</h2>\r\n\r\n<h2>- Đ&uacute;ng l&agrave; n&oacute; ở l&ograve; r&egrave;n suốt ng&agrave;y - Fiona trả lời ngaỵ N&oacute; chỉ l&ecirc;n nh&agrave; tho&aacute;ng một c&aacute;i để lấy c&aacute;i g&igrave; đ&oacute; cần cho c&ocirc;ng việc. Pađy quả rất khắc nghiệt với Frank.</h2>\r\n\r\n<h2>- Ba ơi! Anh Frank dễ thương lắm. Kh&ocirc;ng c&oacute; anh ấy, Agn&egrave;s đ&atilde; chết rồi... Sau bữa cơm, anh Frank sẽ d&aacute;n lại t&oacute;c tr&ecirc;n đầu con b&uacute;p b&ecirc; cho con.</h2>\r\n\r\n<h2>- Vậy th&igrave; tốt. Pađy n&oacute;i lầm nhầm bằng một giọng mỏi mệt.</h2>\r\n\r\n<h2>Mấy đứa con của &ocirc;ng đều c&oacute; t&oacute;c m&agrave;u hung d&agrave;y v&agrave; lượn s&oacute;ng, tuy vậy kh&ocirc;ng c&oacute; đứa n&agrave;o m&agrave;u t&oacute;c lại rực s&aacute;ng dữ dội như &ocirc;ng. Pađy thấp người, chắc như th&eacute;p, ch&acirc;n v&ograve;ng kiềng do cả đời ngồi tr&ecirc;n lưng ngựa, hai c&aacute;nh tay d&agrave;i ra sau những năm l&agrave;m c&ocirc;ng việc cắt l&ocirc;ng cừu; ngực v&agrave; hai c&aacute;nh tay che phủ bởi một lớp l&ocirc;ng m&agrave;u v&agrave;ng. Đ&ocirc;i mắt m&agrave;u xanh v&agrave; thường nheo lại như người thủy thủ hướng tầm nh&igrave;n ra xạ Gương mặt &ocirc;ng kh&ocirc;ng c&oacute; g&igrave; l&agrave; kiểu c&aacute;ch lại dễ g&acirc;y cảm t&igrave;nh. Mũi tuyệt đẹp, đ&uacute;ng l&agrave; mũi r&ocirc;manh, khiến cho những người đồng hương &aacute;i Nhĩ Lan của &ocirc;ng phải ghen tức. &ocirc;ng vẫn c&ograve;n giữ được giọng n&oacute;i dịu d&agrave;ng của v&ugrave;ng Galway, nhưng sau hai mươi năm sống ở miền đất xa x&ocirc;i n&agrave;y &ocirc;ng kh&ocirc;ng c&ograve;n n&oacute;i năng nhanh nhẩu như xưa, giống như một c&aacute;i đồng hồ treo tường cũ kỹ cần được l&ecirc;n d&acirc;y lại. Một người hạnh ph&uacute;c v&igrave; đ&atilde; th&agrave;nh c&ocirc;ng trong cuộc sống lao động gay go vẫn xứng đ&aacute;ng hơn nhiều người kh&aacute;c v&agrave; mặc d&ugrave; cai quản cằn nh&agrave; n&agrave;y bằng một thứ kỷ luật sắt, &ocirc;ng vẫn được con c&aacute;i y&ecirc;u thương, ngoại trừ một đứa. Nếu kh&ocirc;ng đủ b&aacute;nh m&igrave; cho mọi người, &ocirc;ng nhịn ăn; nếu phải chọn lựa giữa quần &aacute;o mới cho bản th&acirc;n hay cho một đứa con n&agrave;o đ&oacute;, &ocirc;ng bằng l&ograve;ng mặc quần &aacute;o cũ. Theo c&aacute;ch của &ocirc;ng, đ&oacute; l&agrave; một bằng chứng biểu thị t&igrave;nh thương d&agrave;nh cho con, cụ thể hơn h&agrave;ng biết bao nhiều c&aacute;i h&ocirc;n dễ d&agrave;ng ban ph&aacute;t. &ocirc;ng dễ n&oacute;ng giận v&agrave; đ&atilde; từng giết người. Nhưng sự may mắn đ&atilde; nghi&ecirc;ng về ph&iacute;a &ocirc;ng h&ocirc;m đ&oacute;; nạn nh&acirc;n l&agrave; một người Anh chủ nh&acirc;n chiếc t&agrave;u đậu ở cảng Dun Lacghaizẹ</h2>\r\n\r\n<h2>Fiona xuất hiện ở ngưỡng cửa sau v&agrave; n&oacute;i lớn:</h2>\r\n\r\n<h2>- V&agrave;o b&agrave;n ăn.</h2>\r\n\r\n<h2>Mấy cậu con trai lần lượt bước v&agrave;o; người cuối c&ugrave;ng l&agrave; Frank tay &ocirc;m b&oacute; củi đến đặt v&agrave;o chiếc th&ugrave;ng b&ecirc;n cạnh l&ograve; sưởi. Pađy để Meggie đứng xuống rồi đến ngồi ở đầu b&agrave;n. Ri&ecirc;ng Meggie ngồi tr&ecirc;n một c&aacute;i th&ugrave;ng gỗ đặt b&ecirc;n cạnh chạ Fiona chia thức ăn v&agrave;o dĩa một c&aacute;ch nhanh v&agrave; gọn như một người phục vụ chuy&ecirc;n nghiệp, rồi mang mỗi lượt hai dĩa cho gia đ&igrave;nh, trước hết cho Pađy rồi đến Frank, người sau c&ugrave;ng l&agrave; Meggiẹ B&agrave; lo cho m&igrave;nh khi mọi người đ&atilde; c&oacute; phần.</h2>\r\n\r\n<h2>- Lại c&aacute;i m&oacute;n ragu ch&aacute;n chết! Stuart cằn nhằn; hai tay cầm dao v&agrave; nĩa nhưng mặt m&agrave;y nhăn nh&oacute;.</h2>\r\n\r\n<h2>- Ăn đi! Pađy ra lệnh.</h2>\r\n\r\n<h2>Fiona cũng ngồi v&agrave;o b&agrave;n v&agrave; nuốt nhanh phần ăn của m&igrave;nh. Rồi b&agrave; đứng l&ecirc;n đặt đầy b&aacute;nh v&agrave;o những c&aacute;i dĩa mới. Đ&oacute; l&agrave; những b&aacute;nh b&iacute;ch qui do b&agrave; l&agrave;m, b&ecirc;n tr&ecirc;n rưới đường, mứt v&agrave; một loại kem. Cũng ch&iacute;nh b&agrave; mang một lượt hai dĩa l&ecirc;n b&agrave;n. Khi xong đ&acirc;u đấy rồi, Fiona mới ngồi thở v&agrave; bắt đầu ăn phần tr&aacute;ng miệng kh&ocirc;ng c&oacute; g&igrave; vội v&atilde;.</h2>\r\n\r\n<h2>- Ngon qu&aacute;! Meggie la to l&ecirc;n. B&aacute;nh ngon qu&aacute;!</h2>\r\n\r\n<h2>C&ocirc; b&eacute; cắm s&acirc;u chiếc muỗng qua lớp kem.</h2>\r\n\r\n<h2>- Bữa ăn n&agrave;y l&agrave; mừng sinh nhật của con đ&oacute; Meggie - Pađy vui vẻ n&oacute;i - Do đ&oacute; mẹ đ&atilde; l&agrave;m m&oacute;n tr&aacute;ng miệng m&agrave; con th&iacute;ch nhất.</h2>\r\n\r\n<h2>Sau đ&oacute; Fiona mang ra một chiếc ấm thật lớn rồi r&oacute;t cho mỗi người một t&aacute;ch tr&agrave; bốc hơi. Cả gia đ&igrave;nh ngồi lại ở b&agrave;n n&oacute;i chuyện, uống tr&agrave; hoặc đọc s&aacute;ch khoảng một tiếng đồng hỗ nữa. Pađy ngậm ống vố, đầu nghi&ecirc;ng xuống đọc quyển s&aacute;ch vừa mượn của thư viện lưu động. Fiona cứ lo ch&acirc;m đầy tr&agrave; v&agrave;o mấy chiếc t&aacute;ch trong khi Bob cũng đọc. Những đứa con kh&aacute;c nhỏ hơn chuẩn bị cho thời kh&oacute;a biểu ng&agrave;y mai.</h2>\r\n\r\n<h2>Nh&agrave; trường đ&atilde; trả học sinh về trong dịp nghỉ h&egrave;; c&aacute;c cậu con trai được tự do tỏ ra rất hăng h&aacute;i đảm nhận c&ocirc;ng việc được giao. Bob sơn phết lại b&ecirc;n ngo&agrave;i căn nh&agrave;, chỉ những nơi cần thiết; Jack v&agrave; Hughie đi kiếm gỗ về để l&agrave;m củi, chăm s&oacute;c những phần phụ thuộc của nh&agrave; v&agrave; vắt sửa; Stuart trồng rau. Thỉnh thoảng Pađy ngưng đọc s&aacute;ch ngẩng đầu l&ecirc;n để bổ sung th&ecirc;m cho ai đ&oacute; một c&ocirc;ng việc. Phần Fiona th&igrave; chẳng n&oacute;i một lời, c&ograve;n Frank ngồi im tr&ecirc;n ghế uống hết t&aacute;ch tr&agrave; n&agrave;y sang t&aacute;ch tr&agrave; kh&aacute;c.</h2>\r\n\r\n<h2>Cuối c&ugrave;ng Fiona đưa mắt ra hiệu gọi Meggie đến ngồi tr&ecirc;n một c&aacute;i ghế cao, b&agrave; chải t&oacute;c cho con g&aacute;i, rồi bảo Meggie đi ngủ, c&ugrave;ng l&uacute;c với Stuart v&agrave; Hughiẹ Jack v&agrave; Bob xin đi ra ngo&agrave;i cho ch&oacute; ăn; Frank đặt con b&uacute;p b&ecirc; tr&ecirc;n tấm v&aacute;n k&ecirc; l&agrave;m b&agrave;n viết v&agrave; bắt đầu d&aacute;n lại t&oacute;c cho n&oacute;. Pađy vương vai, gấp s&aacute;ch lại rồi đặt c&aacute;i ống vố tr&ecirc;n chiếc ghế vỏ ốc lớn được d&ugrave;ng l&agrave;m gạt t&agrave;n thuốc.</h2>\r\n\r\n<h2>- Th&ocirc;i, t&ocirc;i đi ngủ nha mẹ ch&uacute;ng n&oacute;.</h2>\r\n\r\n<h2>- Ch&uacute;c anh ngủ ngon, Pađỵ</h2>\r\n\r\n<h2>Fiona dọn dẹp thật nhanh, ch&eacute;n dĩa mang ra sau bếp rồi bắt đầu ngồi rửa. Frank v&ugrave;i đầu v&agrave;o việc sửa con b&uacute;p bệ Nhưng khi liếc nh&igrave;n chồng dĩa cao ngất, cậu ta bật dậy v&agrave; kh&ocirc;ng n&oacute;i một lời đi lấy n&ugrave;i giẻ phụ mẹ. Frank biết rằng nếu bị cha m&igrave;nh bắt gặp th&igrave; &ocirc;ng kh&ocirc;ng tha đ&acirc;u. &ocirc;ng ph&acirc;n c&ocirc;ng rất dứt kho&aacute;t: đ&agrave;n &ocirc;ng con trai kh&ocirc;ng được rớ v&agrave;o c&ocirc;ng việc của đ&agrave;n b&agrave;. Biết thế, nhưng Frank vẫn chờ cha m&igrave;nh vừa đi nghỉ l&agrave; bắt tay v&agrave;o phụ mẹ. Fiona tr&igrave;u mến nh&igrave;n con trai lớn:</h2>\r\n\r\n<h2>- Frank &agrave;, kh&ocirc;ng c&oacute; con mẹ kh&ocirc;ng biết sẽ xoay sở ra sao. Nhưng đ&aacute;ng l&yacute; con kh&ocirc;ng n&ecirc;n nh&uacute;ng tay v&agrave;o. S&aacute;ng mai con sẽ mệt...</h2>\r\n\r\n<h2>- Mẹ đừng lọ Rửa v&agrave;i c&aacute;i dĩa chẳng hao tốn bao nhi&ecirc;u sức. Gi&uacute;p mẹ đỡ mệt một ch&uacute;t đ&acirc;u phải l&agrave; chuyện lớn.</h2>\r\n\r\n<h2>- C&ocirc;ng việc n&agrave;y l&agrave; của mẹ, v&agrave; chẳng c&oacute; g&igrave; vất vả đ&acirc;u Frank ạ.</h2>\r\n\r\n<h2>- Con mong ng&agrave;y n&agrave;o đ&oacute; nh&agrave; m&igrave;nh gi&agrave;u c&oacute; để c&oacute; thể mướn th&ecirc;m người phụ mẹ.</h2>\r\n\r\n<h2>- Con đừng mơ chuyện h&atilde;o huyền.</h2>\r\n\r\n<h2>Với tay lấy miếng giẻ, b&agrave; lau hai b&agrave;n tay ửng đỏ đầy x&agrave; ph&ograve;ng. Khi nh&igrave;n Frank, trong đ&ocirc;i mắt của b&agrave; chứa đựng một nỗi &acirc;u lo m&ocirc;ng lung. B&agrave; đo&aacute;n ra sự bất m&atilde;n của con v&agrave; nỗi cay đắng vượt qua mức độ nổi loạn b&igrave;nh thường của một người lao động với số phận của m&igrave;nh.</h2>\r\n\r\n<h2>- Frank, con kh&ocirc;ng n&ecirc;n c&oacute; những ước muốn đi&ecirc;n rồ. N&oacute; chỉ g&acirc;y phiền lụy cho con. Ch&uacute;ng ta thuộc tầng lớp c&ugrave;ng cực c&oacute; nghĩa l&agrave; chẳng bao giờ gi&agrave;u v&agrave; cũng kh&ocirc;ng c&oacute; đầy tớ đ&acirc;u con ạ. Con h&atilde;y bằng l&ograve;ng với ch&iacute;nh con v&agrave; những g&igrave; con c&oacute;. Khi con n&oacute;i ra những chuyện như thế con đ&atilde; l&agrave;m nhục cha con đ&oacute;. &ocirc;ng kh&ocirc;ng uống rượu, kh&ocirc;ng chơi bời, ngượi lại l&agrave;m việc rất cực nhọc v&igrave; ch&uacute;ng tạ &ocirc;ng kh&ocirc;ng giữ riệng một xu cho &ocirc;ng, tất cả cho ch&uacute;ng ta đấy th&ocirc;i.</h2>\r\n\r\n<h2>Đ&ocirc;i vai g&acirc;n guốc của Frank nh&uacute;m lại, n&eacute;t mặt sa sầm xuống:</h2>\r\n\r\n<h2>- Mẹ ạ, nhưng tại sao muốn tho&aacute;t khỏi số phần của m&igrave;nh lại bị tr&aacute;ch cứ? Con kh&ocirc;ng thấy điều g&igrave; xấu nếu con mong cho mẹ c&oacute; được một người gi&uacute;p việc để mẹ bớt cực.</h2>\r\n\r\n<h2>- Điều đ&oacute; chưa phải tốt đ&acirc;u con, v&igrave; n&oacute; kh&ocirc;ng xảy ra như thế! Con biết gia đ&igrave;nh m&igrave;nh kh&ocirc;ng đủ tiền để lo cho con học tiếp v&agrave; khi con kh&ocirc;ng tiếp tục đến trường th&igrave; l&agrave;m thế n&agrave;o con c&oacute; thể tr&aacute;nh được c&aacute;i số phận của một người lao động cực nhọc... Giọng n&oacute;i, c&aacute;ch ăn mặc, hai b&agrave;n tay chai của con đ&atilde; cho thấy con phải lao động mới nu&ocirc;i sống được m&igrave;nh. Kh&ocirc;ng c&oacute; g&igrave; l&agrave; nhục khi c&oacute; hai b&agrave;n tay chai. Cũng như cha con thường n&oacute;i: khi c&oacute; một người c&oacute; hai b&agrave;n tay như thế th&igrave; ai cũng biết ngay đ&oacute; l&agrave; một người lương thiện.</h2>\r\n\r\n<h2>Frank nh&uacute;n vai kh&ocirc;ng n&oacute;i.</h2>\r\n\r\n<h2>Anh đứng dậy đặt con b&uacute;p b&ecirc; bằng sứ tr&ecirc;n đầu tủ bu&yacute;p ph&ecirc;, ph&iacute;a sau hợp b&aacute;nh b&iacute;ch quỵ Anh kh&ocirc;ng sợ mấy thằng em trai c&oacute; những h&agrave;nh độg ph&aacute; hoại lần thứ hai; ch&uacute;ng ng&aacute;n Frank nổi giận hơn la cha v&igrave; rằng bản chất của Frank c&oacute; một ch&uacute;t hung dữ. Nhưng c&aacute;i t&iacute;nh xấu ấy kh&ocirc;ng bao giờ Frank lộ ra khi anh ở b&ecirc;n cạnh mẹ hay c&ocirc; em g&aacute;i Meggiẹ</h2>\r\n\r\n<h2>Fiona nh&igrave;n Frank m&agrave; l&ograve;ng đau nh&oacute;i; dường như ở Frank c&oacute; c&aacute;i g&igrave; đ&oacute; c&ocirc; đơn v&agrave; tuyệt vọng v&agrave; biết bao nhiều điều băn khoăn v&acirc;y h&atilde;m. Phải chi Pađy v&agrave; Frank h&ograve;a thuận! Nhưng cả hai đều kh&ocirc;ng nh&igrave;n mọi sự việc dưới con mắt giống nhau. Họ hục hặc nhau thường xuy&ecirc;n.</h2>\r\n\r\n<h2>Ngoại trừ một lần được đi chơi ở Wahine c&aacute;ch đ&acirc;y t&aacute;m th&aacute;ng. Meggie chưa bao giờ đi xa hơn nh&agrave; kho v&agrave; c&aacute;i l&ograve; r&egrave;n ở dưới thung lũng. Cho n&ecirc;n s&aacute;ng đầu ti&ecirc;n Meggie đi học ở trường, c&ocirc; bị căng thẳng đến đỗi n&ocirc;n ra ngo&agrave;i những g&igrave; đ&atilde; ăn s&aacute;ng. Thế l&agrave; Meggie được bế về ph&ograve;ng ngủ, tắm rửa sạch sẽ v&agrave; thay một bộ đồ kh&aacute;c. Bộ quần &aacute;o mới m&agrave;u xanh đậm với cổ m&agrave;u trắng theo kiểu l&iacute;nh thủy, được thay bằng một c&aacute;i &aacute;o cho&agrave;ng m&agrave;u n&acirc;u xấu x&iacute; với h&agrave;ng n&uacute;t c&agrave;i tr&ecirc;n cổ rất cao, khiến Meggie c&oacute; cảm gi&aacute;c như bị nghẹt thở.</h2>\r\n\r\n<h2>Bob, Jack, Hughie v&agrave; Stuart vừa chạy vừa nhảy c&ograve; c&ograve; đ&atilde; ra tới ngo&agrave;i cổng. L&uacute;c đ&oacute; Fiona mới lo xong cho Meggie, b&agrave; th&uacute;c hối con g&aacute;i mang theo thức ăn s&aacute;ng, b&aacute;nh m&igrave; xăng-u&yacute;ch phết mứt, bỏ v&agrave;o chiếc cặp cũ.</h2>\r\n\r\n<h2>- Mau l&ecirc;n Meggie, mọi người trễ hết đấy. Bob ra đến đường quay đầu lại la lớn.</h2>\r\n\r\n<h2>Meggie chạy theo mấy anh đang khuất dần xa xạ</h2>\r\n\r\n<h2>Từ nh&agrave; của họ Cleary đến thị trến Wahine khoảng chừng t&aacute;m c&acirc;y số.</h2>\r\n\r\n<h2>Meggie nh&igrave;n cột c&acirc;y số chỉ đường m&agrave; ch&acirc;n tay rụng rời. Tai của Bob lắng nghe tiếng chu&ocirc;ng trường đ&atilde; đổ chưa, c&ograve;n mắt th&igrave; liếc sang Meggie đầy băn khoăn; c&ocirc; b&eacute; đi bước thấp bước cao, thỉnh thoảng lại thở ra mệt nhọc. Dưới m&aacute;i t&oacute;c d&agrave;y m&agrave;u hung, gương mặt đỏ hồng của Meggie bỗng trở n&ecirc;n nhợt nhạt. Bob giao chiếc cặp của m&igrave;nh cho Jack rồi đến b&ecirc;n em g&aacute;i:</h2>\r\n\r\n<h2>- Meggie, em ngồi l&ecirc;n lưng anh, anh c&otilde;ng em đi cho nhanh.</h2>\r\n\r\n<h2>B&acirc;y giờ th&igrave; Meggie c&oacute; thể kh&aacute;m ph&aacute; Wahine trong tư thế thoải m&aacute;i.</h2>\r\n\r\n<h2>Wahine chẳng c&oacute; g&igrave; nhiều để xem. Kh&ocirc;ng rộn rịp bằng một thị trấn lớn, nh&agrave; cửa trải đều hai b&ecirc;n con đường ch&iacute;nh tr&aacute;ng nhựa. Kh&aacute;ch sạn l&agrave; t&ograve;a nh&agrave; đ&aacute;ng ch&uacute; &yacute; nhất cũng chỉ c&oacute; một tầng với một tấm lợp bằng vải chịu tr&ecirc;n hai c&acirc;y trụ nh&ocirc; ra s&aacute;t đường để che nắng. Cửa h&agrave;ng b&aacute;ch h&oacute;a l&agrave; nơi đ&aacute;ng ch&uacute; &yacute; kế đ&oacute;. Thị trấn chưa c&oacute; một nh&agrave; chứa xe, &ocirc; t&ocirc; ở đ&acirc;y c&ograve;n rất hiếm. Trường học v&agrave; Nh&agrave; thờ Anh quốc gi&aacute;o đang được x&acirc;y kế b&ecirc;n nhau, đối diện với nh&agrave; thờ Th&aacute;nh T&acirc;m v&agrave; trường của gi&aacute;o xứ.</h2>\r\n\r\n<h2>Khi anh em Cleary đi ngang cửa h&agrave;ng b&aacute;ch h&oacute;a th&igrave; chu&ocirc;ng trường của gi&aacute;o xứ vừa đổ, sau đ&oacute; l&agrave; chu&ocirc;ng trường của thị trấn. Họ bước nhanh hơn v&agrave;o s&acirc;n trường kịp l&uacute;c năm mươi học sinh kh&aacute;c bắt đầu xếp h&agrave;ng trước một nữ tu thấp người, tay cầm một c&acirc;y roi m&acirc;y d&agrave;i. Đ&oacute; l&agrave; xơ Agathạ</h2>\r\n\r\n<h2>Khong bị l&ocirc;i cuốn bởi những tiếng cười đ&ugrave;a kh&uacute;c kh&iacute;ch của đ&aacute;m học sinh, Bob v&agrave; c&aacute;c em m&igrave;nh đứng nghi&ecirc;m trang rồi lần lượt đi theo h&agrave;ng v&agrave;o lớp trong tiếng h&aacute;t Niềm tin của c&aacute;c Th&aacute;nh Tổ phụ của ch&uacute;ng ta, xơ Catherine đệm dương cầm. Khi học sinh cuối c&ugrave;ng v&agrave;o lớp, xơ Agatha mới rời khỏi vị tr&iacute; của m&igrave;nh. B&agrave; đi thẳng đến anh em Clearỵ</h2>\r\n\r\n<h2>Meggia chưa bao giờ được nh&igrave;n thấy một nữ tu, n&ecirc;n miệng h&aacute; hốc ra.</h2>\r\n\r\n<h2>- N&agrave;y Robert Cleary, tại sao em đi trễ? Giọng n&oacute;i của xơ Agatha kh&ocirc; khan như h&eacute;t l&ecirc;n.</h2>\r\n\r\n<h2>- Thưa d&igrave;, con xin lỗi - Bob trả lời vụng về, mắt d&aacute;n chặt v&agrave;o c&acirc;y roi m&acirc;y đung đưa sau lưng xơ Agathạ</h2>\r\n\r\n<h2>- Tại sao c&aacute;c em đi trễ? B&agrave; hỏi gặng lần nữa.</h2>\r\n\r\n<h2>- Con xin lỗi xợ</h2>\r\n\r\n<h2>- H&ocirc;m nay l&agrave; ng&agrave;y tựu trường, Robert Cleary; t&ocirc;i nghĩ rằng v&agrave;o một dịp như thế, c&aacute;c em phải r&aacute;ng đi học đ&uacute;ng giờ chứ?</h2>\r\n\r\n<h2>Meggie run sợ, tập trung can đảm rồi l&ecirc;n tiếng:</h2>\r\n\r\n<h2>- Thưa xơ, đ&oacute; l&agrave; lỗi của con - Tiếng n&oacute;i nhỏ như th&igrave; thầm v&agrave; tắt nghẽn trong cổ.</h2>\r\n\r\n<h2>Cặp mắt m&agrave;u xanh đầy đe dọa rời khỏi Bob, chuyển sang hướng Meggie tưởng như xuy&ecirc;n thủng v&agrave;o tận tim c&ocirc; b&eacute;, trong khi đ&oacute; Meggie vẫn hồn nhi&ecirc;n giương đ&ocirc;i mắt nh&igrave;n vị nữ tu, ho&agrave;n to&agrave;n kh&ocirc;ng &yacute; thức rằng m&igrave;nh vừa phạm một sơ hở.</h2>\r\n\r\n<h2>- Tại sao lại lỗi của c&ocirc; - Vị nữ tu hỏi Meggie bằng một giọng lạnh l&ugrave;ng chưa bao giờ Meggie được nghe.</h2>\r\n\r\n<h2>- Dạ thưa, con n&ocirc;n ra đầy b&agrave;n, l&agrave;m lấm cả quần &aacute;o. Mẹ con phải lau rửa v&agrave; thay quần &aacute;o kh&aacute;c cho con, do đ&oacute; ch&uacute;ng con bị trễ - Meggie giải th&iacute;ch một c&aacute;ch vụng về.</h2>\r\n\r\n<h2>N&eacute;t mặt của xơ Agatha kh&ocirc;ng hề thay đổi nhưng miệng m&iacute;m chặt lại như một c&aacute;i l&ograve; xo bị căng ra qu&aacute; sức, c&ograve;n đầu ngọn roi th&igrave; hạ thấp xuống v&agrave;i centim&eacute;t.</h2>\r\n\r\n<h2>Chuyện g&igrave; thế? B&agrave; hỏi Bob như n&oacute;i với một loại s&acirc;u bọ v&ocirc; c&ugrave;ng kinh tỏm như chưa từng c&oacute; trong danh mục khoa hoc.</h2>\r\n\r\n<h2>- Xin xơ tha lỗi chọ Đ&acirc;y l&agrave; Meghann, em g&aacute;i của con.</h2>\r\n\r\n<h2>- Nghe đ&acirc;y nh&eacute;, phải cho c&ocirc; em g&aacute;i của cậu biết c&oacute; những chuyện m&agrave; những người c&oacute; lễ gi&aacute;o kh&ocirc;ng bao giờ n&oacute;i đến. Trong mọi trường hợp, ch&uacute;ng ta kh&ocirc;ng đề cập đến bất cứ một m&oacute;n quần &aacute;o l&oacute;t n&agrave;o. Kh&ocirc;ng bao giờ v&agrave; kh&ocirc;ng bao giờ. Tất cả những đứa trẻ con nh&agrave; đ&agrave;ng ho&agrave;ng đều phải hiểu điều ấy. Đưa tay ra, tất cả.</h2>\r\n\r\n<h2>- Nhưng thưa xơ, lỗi của con m&agrave;! Meggie k&ecirc;u l&ecirc;n.</h2>\r\n\r\n<h2>- Im ngay! Agatha quay qua Meggie ra lệnh - T&ocirc;i kh&ocirc;ng cần biết ai l&agrave; thủ phạm. Tất cả đều đi trễ, vậy tất cả phải bị phạt. - S&aacute;u roi.</h2>\r\n\r\n<h2>B&agrave; kết &aacute;n bằng giọng đều đều, chứa đựng sự đắc &yacute;.</h2>\r\n\r\n<h2>Meggie hốt hoảng nh&igrave;n hai b&agrave;n tay kh&ocirc;ng nh&uacute;c nh&iacute;ch của Bob, rồi nghe tiếng r&iacute;t của c&acirc;y roi d&agrave;i quất xuống nhanh đến nỗi mắt Meggie kh&ocirc;ng theo kịp. Tiếng voi vang l&ecirc;n khi đụng v&agrave;o b&agrave;n tay, nơi da thịt mỏng v&agrave; mềm. Những đường d&agrave;iđỏ ửng nổi l&ecirc;n ngay; lần đ&aacute;nh sau tr&uacute;ng gần ng&oacute;n tay v&agrave; lần ch&oacute;t tr&uacute;ng ngay c&aacute;c đầu ng&oacute;n tay đau thốn tới &oacute;c. Xơ Agatha sử dụng c&acirc;y roi một c&aacute;ch đặc biệt ch&iacute;nh x&aacute;c. Th&ecirc;m ba roi nữa quất xuống dưới tay b&ecirc;n kia của Bob, rồi b&agrave; quay sang Jack. Mặt Bob t&aacute;i nhợt nhưng kh&ocirc;ng một tiếng r&ecirc;n. V&agrave; xơ Agatha đ&atilde; d&ugrave;ng c&aacute;ch đ&aacute;nh ấy với c&aacute;c đứa em trai của Bob, kể cả Stuart hiền hậu v&agrave; dịu d&agrave;ng nhất. Tất cả kh&ocirc;ng một tiếng r&ecirc;n. Đến Meggie, c&ocirc; b&eacute; nhắm mắt lại khi ngọn roi giơ l&ecirc;n cao v&agrave; kh&ocirc;ng thấy n&oacute; sa xuống l&uacute;c n&agrave;o. Nhưng sự đau đớn thật dữ dội, da thịt như bị x&eacute; ra v&agrave; nhức thấu xương. Rồi ngọn roi thứ hai quất xuống; cơn đau l&ecirc;n đến vai th&igrave; ngọn roi thứ ba v&agrave; thứ tư tiếp theo, tr&uacute;ng ngay những đầu ng&oacute;n taỵ Meggie cắn m&ocirc;i đến khi rướm m&aacute;u, v&agrave; cảm thấy xấu hổ khi bật ra tiếng kh&oacute;c. Tức giận phẫn nộ trước một sự bất c&ocirc;ng như thế, Meggie mở lớn đ&ocirc;i mắt nh&igrave;n xơ Agathạ Đ&oacute; l&agrave; b&agrave;i học đầu ti&ecirc;n của c&ocirc; b&eacute;, b&agrave;i học kh&ocirc;ng chứa đựng những điều m&agrave; đ&aacute;ng ra vị nữ tu phải đưa v&agrave;o.</h2>\r\n\r\n<h2>M&atilde;i cho đến giờ ăn trưa, hai b&agrave;n tay Meggie mới c&oacute; thể hoạt động lại b&igrave;nh thường. Meggie đ&atilde; trải qua một buổi s&aacute;ng đầy sợ h&atilde;i sửng sốt về những g&igrave; đ&atilde; xảy ra, đ&atilde; nghe thấy. Ngồi trước chiếc b&agrave;n c&oacute; hai ghế, ở tận c&ugrave;ng ph&ograve;ng học, Meggie cũng kh&ocirc;ng ch&uacute; &yacute; người bạn c&ugrave;ng b&agrave;n m&igrave;nh l&agrave; ai. C&ocirc; b&eacute; trải qua giờ cơm trưa buồn b&atilde;.</h2>\r\n\r\n<h2>Khi tiếng chu&ocirc;ng b&aacute;o hiệu tiết học trưa bắt đầu, Meggie t&igrave;m một chỗ đứng v&agrave;o h&agrave;ng, l&uacute;c ấy c&ocirc; b&eacute; mới nhận ra những g&igrave; chung quanh. Nỗi xấu hổ bị phạt chưa vơi nhưng c&ocirc; b&eacute; vẫn ngẩng cao đầu v&agrave; l&agrave;m như kh&ocirc;ng nghe những tiếng x&igrave; x&agrave;o trong đ&aacute;m bạn nhỏ c&ugrave;ng lớp, cũng như kh&ocirc;ng để &yacute; đến những chiếc c&ugrave;i chỏ th&uacute;c v&agrave;o nhau.</h2>\r\n\r\n<h2>Xơ Agatha đứng oai vệ trước học sinh với c&acirc;y roi d&agrave;i trong tay; xơ Declan đi đi lại lại ở cuối h&agrave;ng. Xơ Catherine ngồi trước đ&agrave;n dương cầm bắt đầu đ&aacute;nh bản: H&atilde;y tiến l&ecirc;n, c&aacute;c con chi&ecirc;n của Ch&uacute;a. C&aacute;c c&ocirc; cậu dễ thương n&agrave;y đi đều bước như những người l&iacute;nh nhỏ - xơ Catherine thầm nghĩ như thế với tất cả niềm ki&ecirc;u h&atilde;nh.</h2>\r\n\r\n<h2>Trong ba nữ tu, xơ Declan l&agrave; mẫu ch&eacute;p nguy&ecirc;n bản của xơ Agatha với mười lăm tuổi nhỏ hơn, chỉ c&oacute; xơ Catherine th&igrave; c&ograve;n c&oacute; ch&uacute;t g&igrave; nh&acirc;n từ.</h2>\r\n\r\n<h2>Ở h&agrave;ng ghế cuối lớp, Meggie liều lĩnh liếc mắt nh&igrave;n sang c&ocirc; bạn g&aacute;i nhỏ ngồi chung b&agrave;n với m&igrave;nh. Một nụ cười s&uacute;n răng đ&aacute;p lại. Đ&ocirc;i mắt đen tr&ograve;n l&agrave;m gương mặt u buồn trở n&ecirc;n rực s&aacute;ng. C&ocirc; b&eacute; thu h&uacute;t Meggie c&oacute; m&agrave;u t&oacute;c đỏ hoe, gương mặt đầy những vết t&agrave;n nhang.</h2>\r\n\r\n<h2>- Bạn t&ecirc;n g&igrave;? C&ocirc; b&eacute; c&oacute; vẻ đẹp buồn hỏi Meggie, miệng cắn đầu c&acirc;y b&uacute;t ch&igrave;.</h2>\r\n\r\n<h2>- Meggie Cleary - tiếng trả lời của Meggie thật nhỏ.</h2>\r\n\r\n<h2>- Hai em đằng kia! một giọng kh&ocirc; khan vang l&ecirc;n từ đầu lớp.</h2>\r\n\r\n<h2>Meggie giật th&oacute;t người, nh&igrave;n d&aacute;o d&aacute;c xung quanh hoảng sợ, tim c&ocirc; b&eacute; như va chạm v&agrave;o lồng ngực. Meggie nhận ra l&agrave; cả lớp đang nh&igrave;n m&igrave;nh. Xơ Agatha bước xuống đi thẳng đến Meggiẹ Sự sợ h&atilde;i khiến Meggie mong chờ một ph&eacute;p lạ n&agrave;o gi&uacute;p m&igrave;nh biến mất, hoặc phải co ch&acirc;n chạy ngay khỏi nơi n&agrave;y. Ph&iacute;a sau l&agrave; bức tường, hai b&ecirc;n l&agrave; những b&agrave;n học, c&ograve;n trước mặt l&agrave; xơ Agathạ</h2>\r\n\r\n<h2>- Meghann Cleary, em đ&atilde; n&oacute;i chuyện.</h2>\r\n\r\n<h2>- Thưa xơ, v&acirc;ng.</h2>\r\n\r\n<h2>- Em n&oacute;i g&igrave; đấy?</h2>\r\n\r\n<h2>- T&ecirc;n con, thưa xợ</h2>\r\n\r\n<h2>- T&ecirc;n em? Xơ Agatha lặp lại bằng giọng chế diễu vừa đảo mắt nh&igrave;n khắp lớp, như muốn giải th&iacute;ch những lời l&ecirc;n &aacute;n của m&igrave;nh.</h2>\r\n\r\n<h2>- N&agrave;y c&aacute;c em, hết chuyện rồi! Th&ecirc;m một th&agrave;nh vi&ecirc;n của gia đ&igrave;nh Cleary mang lại vinh dự cho trường của ch&uacute;ng ta bằng sự hiện diện của họ Do đ&oacute; c&ocirc; ta phải loan b&aacute;o một c&aacute;ch ầm ĩ t&ecirc;n m&igrave;nh (rồi xơ quay lại ph&iacute;a Meggie). Đứng l&ecirc;n! Em phải đứng l&ecirc;n khi t&ocirc;i đang n&oacute;i với em, đồ con nhỏ rừng r&uacute;! Đưa hai tay ra.</h2>\r\n\r\n<h2>Meggie bước nhanh ra khỏi h&agrave;ng ghế, những lọn t&oacute;c d&agrave;i tung l&ecirc;n rồi rơi xuống che khuất một phần gương mặt. C&ocirc; b&eacute; vặn ch&eacute;o hai tay một c&aacute;ch tuyệt vọng nhưng xơ Agatha vẫn kh&ocirc;ng nh&uacute;c nh&iacute;ch, b&agrave; ta cứ chờ, cứ chờ, cứ chờ... Sau c&ugrave;ng Meggie t&igrave;m được ch&uacute;t gan dạ ch&igrave;a l&ograve;ng b&agrave;n tay ra. Nhưng ngay l&uacute;c c&acirc;y m&acirc;y đập xuống, c&ocirc; b&eacute; r&uacute;t b&agrave;n tay lại thật nhanh vừa thở hổn hển, mặt xanh như cắt. Xơ Agatha chụp lấy v&agrave; k&eacute;o ch&ugrave;m t&oacute;c hung về ph&iacute;a b&agrave;, gương mặt của Meggie &aacute;p s&aacute;t v&agrave;o cặp k&iacute;nh của b&agrave;.</h2>\r\n\r\n<h2>- Đưa tay ra, Meghann Cleary - xơ Agatha ra lệnh, giọng lịch sự nhưng lạnh l&ugrave;ng v&agrave; quyết liệt.</h2>\r\n\r\n<h2>Meggie h&aacute; miệng ra v&agrave; n&ocirc;n cả buổi ăn trưa l&ecirc;n chiếc &aacute;o d&agrave;i của xơ Agathạ Tiếp đ&oacute; l&agrave; tiếng h&eacute;t kinh h&atilde;i của học sinh trong lớp; trong khi đ&oacute; xơ Agatha trố mắt nh&igrave;n những thứ gớm ghiếc vừa n&ocirc;n ra chảy d&agrave;i từ tr&ecirc;n xuống theo những đường xếp của chiếc &aacute;o. Mặt b&agrave; đỏ gay v&igrave; tức giận đi&ecirc;n người c&ugrave;ng với ch&uacute;t kinh h&atilde;i. C&acirc;y m&acirc;y quất xuống kh&ocirc;ng cần biết nhắm v&agrave;o đ&acirc;u, Meggie đưa hai tay l&ecirc;n che mặt rồi chạy trốn v&agrave;o một g&oacute;c lớp, miệng vẫn tiếp tục n&ocirc;n mửa. Xơ Agatha đ&atilde; mỏi mệt kh&ocirc;ng th&egrave;m đ&aacute;nh nữa, b&agrave; chỉ ra cửa:</h2>\r\n\r\n<h2>- Ra khỏi đ&acirc;y! Về nh&agrave; của m&agrave;y đi, đồ thất học đ&aacute;ng gh&ecirc; tởm.</h2>\r\n\r\n<h2>Rồi b&agrave; quay phắt lại bước sang lớp học của xơ Declan.</h2>\r\n\r\n<h2>Đ&ocirc;i mắt luống cuống của Meggie t&igrave;m thấy Stuart, người anh học chung lớp ra hiệu c&ocirc; phải v&acirc;ng lời. C&aacute;i nh&igrave;n hiền h&ograve;a của Stuart đầy thương hại v&agrave; th&ocirc;ng cảm với Meggiẹ C&ocirc; b&eacute; lấy khăn tay ch&ugrave;i miệng v&agrave; khập khiễng bước ra khỏi lớp. C&ograve;n những hai tiếng đồng hồ nữa mới hết giờ học; Meggie rời s&acirc;n trường như một c&aacute;i m&aacute;y đi xuống đường v&agrave; biết rằng c&aacute;c anh m&igrave;nh sẽ kh&ocirc;ng theo kịp. V&igrave; qu&aacute; sợ h&atilde;i, Meggie kh&ocirc;ng biết phải đứng lại nơi n&agrave;o để chờ c&aacute;c anh; Meggie muốn về trước một m&igrave;nh v&agrave; chỉ một m&igrave;nh th&ocirc;i để kể với mẹ tất cả những g&igrave; xảy ra.</h2>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"height:260px; width:86%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top\">\r\n			<h2><strong>Chương 4</strong></h2>\r\n\r\n			<h2>&nbsp;</h2>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Fiona su&yacute;t nữa ng&atilde; v&agrave;o con g&aacute;i khi bước qua ngưỡng cửa sau nh&agrave;, tay x&aacute;ch một giỏ quần &aacute;o sắp sửa đem phơi. Meggie đang ngồi tr&ecirc;n bậc cao nhất ở ngo&agrave;i h&agrave;ng hi&ecirc;n, đầu gục xuống, t&oacute;c rũ rượi, dơ bẩn. Fiona đặt chiếc giỏ nặng xuống, thở ra, đưa tay vẹt qua một b&ecirc;n phần t&oacute;c che khuất mặt Meggiẹ</h2>\r\n\r\n<h2>- N&oacute;i cho mẹ nghe chuyện g&igrave; đ&atilde; xảy rả B&agrave; hỏi bằng giọng mệt mỏi.</h2>\r\n\r\n<h2>- Con n&ocirc;n ra đầy &aacute;o của xơ Agathạ</h2>\r\n\r\n<h2>- Ch&uacute;a ơi! Fiona k&ecirc;u l&ecirc;n, hai tay chống v&agrave;o h&ocirc;ng.</h2>\r\n\r\n<h2>- B&agrave; cũng đ&atilde; quất cho con nhiều roi - Meggie n&oacute;i kh&ocirc;ng ra tiếng, nước mắt r&agrave;n rụa.</h2>\r\n\r\n<h2>- Thật sạch sẽ qu&aacute; - Fiona đứng l&ecirc;n kh&oacute; nhọc x&aacute;ch chiếc giỏ quần &aacute;o đầy nắp - Meggie, mẹ kh&ocirc;ng biết phải l&agrave;m g&igrave; đ&acirc;y, rồi ba về sẽ cho một trận.</h2>\r\n\r\n<h2>N&oacute;i xong b&agrave; băng qua s&acirc;n đi về ph&iacute;a h&agrave;ng d&acirc;y phơi quần &aacute;o.</h2>\r\n\r\n<h2>Meggie lấy tay quệt nước mắt; nh&igrave;n theo mẹ một l&uacute;c rồi đứng l&ecirc;n v&agrave; đi xuống con đường dẫn đến l&ograve; r&egrave;n.</h2>\r\n\r\n<h2>Frank vừa đ&oacute;ng m&oacute;ng xong cho con ngựa c&aacute;i của &ocirc;ng Roberson th&igrave; Meggie xuất hiện. Anh ta quay lại v&agrave; thấy Meggiẹ Những kỷ niệm kh&ocirc;ng lấy g&igrave; tốt đẹp m&agrave; cậu ta từng trải qua ở nh&agrave; trường lại ập đến. Meggie qu&aacute; nhỏ b&eacute;, mũm mĩm thơ ng&acirc;y biết bao, v&agrave; những g&igrave; Frank nh&igrave;n thấy trước mắt khiến anh kh&ocirc;ng thể kh&ocirc;ng nghĩ đến việc giết ngay xơ Agathạ Giết b&agrave; ta, giết b&agrave; ta thật sự. Anh ta bu&ocirc;ng mấy thứ đang cầm trong tay, th&aacute;o c&aacute;i tạp dề bằng da ra v&agrave; đến nhanh b&ecirc;n Meggiẹ</h2>\r\n\r\n<h2>- Chuyện g&igrave; đ&atilde; xảy ra em g&aacute;i cưng của anh? Frank vừa hỏi vừa qu&igrave; xuống trước mặt em g&aacute;i.</h2>\r\n\r\n<h2>M&ugrave;i n&ocirc;n mửa x&ocirc;ng ra từ em g&aacute;i l&agrave;m cho Frank cũng kh&oacute; chịu nhưng cậu ta cố chịu đựng.</h2>\r\n\r\n<h2>- Anh Frank - Frank - Frank! C&ocirc; b&eacute; n&oacute;i như r&ecirc;n siết, gương mặt nhăn nh&oacute; đau đớn, v&agrave; b&acirc;y giờ nước mắt mới tu&ocirc;n tr&agrave;o. Meggie nh&agrave;o tới &ocirc;m gh&igrave; lấy Frank, v&agrave; kh&oacute;c kh&ocirc;ng ra tiếng, nhưng lại rất đau khổ.</h2>\r\n\r\n<h2>Khi Meggie dịu bớt cơn x&uacute;c động, Frank bế em g&aacute;i đặt tr&ecirc;n đống cỏ kh&ocirc; m&ugrave;i dễ chịu, b&ecirc;n cạnh con ngựa c&aacute;i của &ocirc;ng Roberson. Cả hai ngồi y&ecirc;n lặng nh&igrave;n con vật nhấm ở rơm. Đầu của Meggie ngả v&agrave;o phần ngực trần &ecirc;m &aacute;i của Frank.</h2>\r\n\r\n<h2>- Tại sao b&agrave; ta phạt tất cả anh em m&igrave;nh hở anh Frank? - Meggie hỏi. Em đ&atilde; n&oacute;i l&agrave; lỗi ri&ecirc;ng của em m&agrave;.</h2>\r\n\r\n<h2>B&acirc;y giờ th&igrave; Frank đ&atilde; quen c&aacute;i m&ugrave;i to&aacute;t ra từ chiếc &aacute;o của em v&agrave; anh ta kh&ocirc;ng c&ograve;n ch&uacute; &yacute; nữa. Bất chợt, Frank đưa tay ra, lơ đ&atilde;ng vuốt mũi của con ngựa c&aacute;i, đẩy n&oacute; ra khi n&oacute; c&oacute; vẻ qu&aacute; th&acirc;n mật.</h2>\r\n\r\n<h2>- Ch&uacute;ng ta ngh&egrave;o, Meggie, đ&oacute; l&agrave; l&yacute; do ch&iacute;nh. C&aacute;c d&igrave; phước lu&ocirc;n lu&ocirc;n gh&eacute;t học sinh ngh&egrave;o. Nếu em c&ograve;n đi học ở c&aacute;i trường hắc &aacute;m đ&oacute; của xơ Agatha v&agrave;i ng&agrave;y nữa, em sẽ thấy b&agrave; ta kh&ocirc;ng chỉ nhắm v&agrave;o anh em Cleary, m&agrave; cả anh em Marshall Mac Donald. Tất cả đều ngh&egrave;o. Ngược lại, nếu ch&uacute;ng ta gi&agrave;u, đi học bằng xe ngựa c&oacute; mui sập sang trọng như d&ograve;ng họ &Oacute; Brien, c&aacute;c b&agrave; xơ sẽ nhảy tới &ocirc;m cổ ch&uacute;ng tạ Nhưng ch&uacute;ng ta kh&ocirc;ng c&oacute; điều kiện để tặng một c&acirc;y đ&agrave;n &oacute;cgơ cho nh&agrave; thờ, cũng kh&ocirc;ng c&oacute; những &aacute;o cho&agrave;ng lễ nạm v&agrave;ng, hay một con ngựa, một chiếc xe cũ tặng cho c&aacute;c b&agrave; nữ tu d&ugrave;ng việc ri&ecirc;ng, n&ecirc;n ch&uacute;ng ta chẳng l&agrave; g&igrave;. C&aacute;c b&agrave; muốn đối xử với ch&uacute;ng ta như thế n&agrave;o t&ugrave;y &yacute;.</h2>\r\n\r\n<h2>C&ocirc; b&eacute; ng&aacute;i ngủ, hai m&iacute; mắt tr&igrave; nặng xuống. Frank đặt em nằm tr&ecirc;n đống cỏ kh&ocirc; v&agrave; trở lại c&ocirc;ng việc của m&igrave;nh, tr&ecirc;n m&ocirc;i điểm một nụ cười v&agrave; cất tiếng h&aacute;t nho nhỏ.</h2>\r\n\r\n<h2>Meggie đang ngủ th&igrave; Pađy bước v&agrave;o, tay lấm đầy ph&acirc;n v&igrave; &ocirc;ng ta vừa ch&ugrave;i rửa chuồng ngựa của &ocirc;ng Jarman.</h2>\r\n\r\n<h2>- Mẹ con mới cho ba hay la Meggie bị phạt ở trường v&agrave; bị đuổi về nh&agrave;. Con c&oacute; biết l&yacute; do hay kh&ocirc;ng?</h2>\r\n\r\n<h2>Frank để c&aacute;i trục xe qua một b&ecirc;n.</h2>\r\n\r\n<h2>- Tội nghiệp n&oacute; đ&atilde; n&ocirc;n v&agrave;o &aacute;o của xơ Agathạ</h2>\r\n\r\n<h2>Pađy đổi sắc mặt, mắt nh&igrave;n thẳng về ph&iacute;a bức tường xa nhất như đang t&igrave;m một th&aacute;i độ Rồi &ocirc;ng lại hướng mắt về ph&iacute;a Meggiẹ</h2>\r\n\r\n<h2>- Con b&eacute; bị x&uacute;c động qu&aacute; mức ngay buổi học đầu ti&ecirc;n chứ g&igrave;?</h2>\r\n\r\n<h2>- Con kh&ocirc;ng r&otilde;. N&oacute; đ&atilde; n&ocirc;n ở nh&agrave; trước khi đi v&agrave; do đ&oacute; l&agrave;m cho cả bốn đứa phải trễ giờ. Ch&uacute;ng đến trường sau khi kiểng đổ. Mỗi đứa đều bị ăn s&aacute;u roi, ri&ecirc;ng Meggie bấn loạn v&igrave; cho rằng n&oacute; l&agrave; người c&oacute; lỗi duy nhất đ&aacute;ng bị phạt. Sau buổi ăn trưa, Meggie lại bị xơ Agatha đ&aacute;nh một lần nữa, thế l&agrave; Meggie của ch&uacute;ng ta đ&atilde; tr&uacute;t to&agrave;n bộ b&aacute;nh m&igrave; v&agrave; mứt l&ecirc;n tr&ecirc;n chiếc &aacute;o d&agrave;i m&agrave;u đen của xơ Agathạ</h2>\r\n\r\n<h2>- Rồi chuyện g&igrave; xảy ra tiếp đ&oacute;?</h2>\r\n\r\n<h2>- Xơ Agatha đ&atilde; tặng th&ecirc;m một trận đ&ograve;n cho Meggie v&agrave; đuổi n&oacute; về nh&agrave;.</h2>\r\n\r\n<h2>- Như vậy, Meggie đ&atilde; bị phạt l&agrave; đ&uacute;ng. Ba rất k&iacute;nh trọng c&aacute;c d&igrave; phước v&agrave; ba cũng &yacute; thức rằng ch&uacute;ng ta kh&ocirc;ng c&oacute; quyền chỉ tr&iacute;ch h&agrave;nh động của c&aacute;c d&igrave;. Nhưng ba mong rằng c&aacute;c d&igrave; sẽ sử dụng roi thước bớt đi. Ba biết c&aacute;c d&igrave; phải khổ t&acirc;m lắm mới nh&eacute;t được v&agrave;o những c&aacute;i đầu kh&oacute; bảo của người &aacute;i Nhĩ Lan ch&uacute;ng ta một ch&uacute;t văn h&oacute;a. D&ugrave; sao đ&oacute; l&agrave; ng&agrave;y đi học đầu ti&ecirc;n của Meggiẹ</h2>\r\n\r\n<h2>Frank nh&igrave;n cha sửng sốt. L&acirc;u nay chưa bao giờ Pađy n&oacute;i chuyện với con trai lớn trong tứ thế giữa hai người đ&agrave;n &ocirc;ng. Lần đầu ti&ecirc;n được l&ocirc;i ra khỏi những o&aacute;n giận thường xuy&ecirc;n đầy ắp, Frank hiểu ra rằng d&ugrave; với bề ngo&agrave;i cứng rắn, cha m&igrave;nh vẫn tỏ ra &acirc;u yếm Meggie hơn cả bọn con trai. Frank cảm thấy gần với cha hơn. Anh mỉm cười kh&ocirc;ng c&oacute; ch&uacute;t ẩn &yacute;.</h2>\r\n\r\n<h2>- Meggie l&agrave; một đứa trẻ tuyệt vời phải kh&ocirc;ng bả Pađy t&aacute;n đồng một c&aacute;ch lơ đ&atilde;ng c&acirc;u hỏi của Frank v&igrave; &ocirc;ng đang nh&igrave;n Meggiẹ Con ngựa c&aacute;i nhe răng, hỉnh mũi thở ph&igrave; ph&igrave;; Meggie trở m&igrave;nh, lăn qua một b&ecirc;n rồi mởt mắt. Khi nh&igrave;n thấy cha đứng kế b&ecirc;n Frank, c&ocirc; b&eacute; bật ngồi dậy, mặt t&aacute;i nhợt v&igrave; sợ sệt.</h2>\r\n\r\n<h2>- Sao c&ocirc; g&aacute;i b&eacute; nhỏ, con đ&atilde; trải qua một ng&agrave;y rất mệt nhọc phải kh&ocirc;ng?</h2>\r\n\r\n<h2>Pađy bước tới, bế Meggie l&ecirc;n v&agrave; kh&ocirc;ng khỏi giựt m&igrave;nh v&igrave; m&ugrave;i tanh x&ocirc;ng l&ecirc;n mũi. Nhưng &ocirc;ng nh&uacute;n vai v&agrave; &ocirc;m con g&aacute;i v&agrave;o l&ograve;ng.</h2>\r\n\r\n<h2>- Con bị đ&aacute;nh đ&ograve;n bằng gậy... Meggie n&oacute;i với chạ</h2>\r\n\r\n<h2>- N&agrave;y nh&eacute;, xơ Agatha cho ba biết đ&acirc;y chưa phải l&agrave; lần cuối c&ugrave;ng đ&acirc;u (&ocirc;ng đặt Meggie l&ecirc;n vai) - B&acirc;y giờ tốt hơn hết con xem mẹ c&oacute; sẵn nước n&oacute;ng để tắm kh&ocirc;ng. Con g&aacute;i ba m&agrave; h&ocirc;i h&aacute;m hơn cả chuồng ngựa của Jarman.</h2>\r\n\r\n<h2>Những lần n&ocirc;n mửa của Meggie đ&atilde; mang lại một kết quả kh&aacute; tốt. Xơ Agatha vẫn d&ugrave;ng roi đ&aacute;nh c&ocirc; b&eacute; nhưng b&acirc;y giờ b&agrave; lu&ocirc;n lu&ocirc;n giữ một khoảng c&aacute;ch đủ để tr&aacute;nh những hậu quả bất ngờ. Do đ&oacute; m&agrave; sực mạnh của l&agrave;n roi v&agrave; sự ch&iacute;nh x&aacute;c đ&atilde; kh&ocirc;ng c&ograve;n như trước.</h2>\r\n\r\n<h2>Khi Meggie chưa đến trường th&igrave; Stuart l&agrave; mục ti&ecirc;u ch&iacute;nh của ngọn roi trong tay xơ Agathạ Nhưng thật ra, Meggie lại l&agrave; đối tượng h&agrave;nh hạ th&iacute;ch th&uacute; hơn, v&igrave; t&aacute;nh hay mơ mộng v&agrave; k&iacute;n đ&aacute;o của Stuart &iacute;t c&oacute; chỗ sơ hở cho xơ Agatha khai th&aacute;c.</h2>\r\n\r\n<h2>Meggie quen tay tr&aacute;i. Đ&oacute; l&agrave; c&aacute;i tội đ&aacute;ng trị nhất. Ng&agrave;y tập viết đầu, khi c&ocirc; b&eacute; cầm cục phấn l&ecirc;n, xơ Agatha đ&atilde; lao v&agrave;o c&ocirc; b&eacute; như C&eacute;sar lao v&agrave;o l&iacute;nh Gaulois:</h2>\r\n\r\n<h2>- Meghann Cleary, bỏ cục phấn xuống ngay! b&agrave; ta h&eacute;t l&ecirc;n.</h2>\r\n\r\n<h2>Thế l&agrave; một trận chiến dằng lại diễn ra. Meggie quen tay tr&aacute;i, bất trị, kh&ocirc;ng khoan nhượng. Khi xơ Agatha bẻ cụp b&agrave;n tay mặt của Meggie tr&ecirc;n t&aacute;m bảng, Meggie thấy đầu &oacute;c đảo lộn, khổng biết c&aacute;ch n&agrave;o điều khiển b&agrave;n tay bất lực của m&igrave;nh l&agrave;m theo đ&ograve;i hỏi của xơ Agathạ B&agrave; ta c&oacute; l&agrave;m g&igrave; đi nữa, b&agrave;n tay mặt của Meggie vẫn kh&ocirc;ng thể kẻ được chữ Ạ Thừa l&uacute;c xơ Agatha vừa quay sang chỗ kh&aacute;c, Meggie nhanh hơn d&ugrave;ng tay tr&aacute;i viết một chữ A thật đẹp.</h2>\r\n\r\n<h2>Nhưng cuối c&ugrave;ng xơ Agatha đ&atilde; thắng. Một buổi s&aacute;ng trong l&uacute;c c&aacute;c học sinh đang xếp h&agrave;ng, xơ Agatha đến lắm lấy tay của Meggie k&eacute;o ra sau lưng rồi d&ugrave;ng một sợi d&acirc;y thừng cột chặt lại. Xơ Agatha chỉ th&aacute;o d&acirc;y khi chu&ocirc;ng b&aacute;o tan học v&agrave;o l&uacute;c ba giờ chiều. Ngay giờ nghỉ buổi trưa, Meggie cũng phải ăn, đi dạo v&agrave; chơi với một b&ecirc;n tay tr&aacute;i ho&agrave;n to&agrave;n bất động. Ba th&aacute;ng sau, Meggie tập được viết bằng tay mặt kh&aacute; ngay ngắn, theo những qui định do xơ Agatha đề ra. Để đảm bảo chắc chắn c&ocirc; b&eacute; kh&ocirc;ng sử dụng trở lại tay tr&aacute;i, b&agrave; ta tiếp tục cột như thế th&ecirc;m hai th&aacute;ng. Sau đ&oacute; xơ Agatha tập hợp tất cả học sinh để đọc một tr&agrave;ng kinh, cảm ơn Đức Ch&uacute;a Trời với l&ograve;ng nh&acirc;n từ của Ng&agrave;i, đ&atilde; chứng minh sai lầm của Meggie - Những đứa con của Đức Ch&uacute;a Trời nh&acirc;n từ đều sử dụng tay mặt; những người sử dụng tay tr&aacute;i l&agrave; do quỷ sứ sinh ra, nhất l&agrave; khi ch&uacute;ng c&oacute; t&oacute;c m&agrave;u hung. Năm học sắp hết, th&aacute;ng 12 đến v&agrave; ng&agrave;y sinh nhật của Meggie cũng gần rồi. Như một th&ocirc;ng lệ của gia đ&igrave;nh khi sinh nhật của c&aacute;c con rơi v&agrave;o một ng&agrave;y đi học th&igrave; buổi lễ được dời qua ng&agrave;y thứ bảy.</h2>\r\n\r\n<h2>Qu&agrave; sinh nhật của Meggie năm nay l&agrave; một bộ t&aacute;ch dĩa với những h&igrave;nh vẽ đề t&agrave;i Trung Quốc m&agrave; c&ocirc; b&eacute; mong muốn từ l&acirc;u. Bộ t&aacute;ch dĩa ấy được đặt tr&ecirc;n một chiếc b&agrave;n nhỏ xinh sắn m&agrave;u xanh dương chung quanh c&oacute; những chiếc ghế nhỏ. Tất cả do Frank l&agrave;m cho em trong những l&uacute;c rỗi rảnh. Thế l&agrave; Agn&egrave;s mặc chiếc v&aacute;y m&agrave;u xanh mới, do ch&iacute;nh mẹ. Meggie cắt v&agrave; may, được đặt tr&ecirc;n một trong những chiếc ghế ấy.</h2>\r\n\r\n<h2>Hai ng&agrave;y trước Noel năm 1917, Pađy trở về nh&agrave; đặt l&ecirc;n b&agrave;n tờ tuần b&aacute;o v&agrave; một chồng s&aacute;ch mượn của thư viện lưu động. Lần thứ nhất, việc đọc b&aacute;o được chọn trước việc đọc s&aacute;ch. Tổng bi&ecirc;n tập vừa &aacute;p dụng một c&ocirc;ng thức mới cho tờ tuần b&aacute;o phỏng theo c&aacute;c tạp ch&iacute; Mỹ. Ở phần giữa tờ b&aacute;o được d&agrave;nh trọn để phản &aacute;nh t&igrave;nh h&igrave;nh chiến cuộc đang diễn ra.</h2>\r\n\r\n<h2>Frank chụp lấy tờ b&aacute;o v&agrave; đọc ngấu nghiến c&aacute;c b&agrave;i tường thuật.</h2>\r\n\r\n<h2>- Thưa ba, con muốn ghi t&ecirc;n v&agrave;o qu&acirc;n đội - Frank vừa n&oacute;i vừa đặt tờ b&aacute;o xuống b&agrave;n một c&aacute;ch lễ ph&eacute;p.</h2>\r\n\r\n<h2>Fiona quay phắt đầu lại, l&agrave;m đổ m&oacute;n ragu xuống bếp; Pađy giật nẩy m&igrave;nh trong ghế b&agrave;nh Windsor, ngưng đọc s&aacute;ch.</h2>\r\n\r\n<h2>- Con c&ograve;n qu&aacute; trẻ, Frank, &ocirc;ng đ&aacute;p lại.</h2>\r\n\r\n<h2>- Thưa ba, con đ&atilde; 17 tuổi. Con l&agrave; một người lớn! Tại sao bọn Đức v&agrave; bọn Thổ c&oacute; quyền t&agrave;n s&aacute;t qu&acirc;n l&iacute;nh của ch&uacute;ng ta như những con heo, trong khi đ&oacute; con lại ở nh&agrave; ngồi b&igrave;nh y&ecirc;n? Đ&atilde; đến l&uacute;c một thanh ni&ecirc;n của d&ograve;ng họ Cleary phải phụ vụ Tổ quốc.</h2>\r\n\r\n<h2>- Nhưng con chưa tới tuổi, Frank - người ta kh&ocirc;ng nhận con đ&acirc;u.</h2>\r\n\r\n<h2>- Nhận. Nếu ba kh&ocirc;ng ngăn trở - Frank n&oacute;i ngược lại một c&aacute;ch quyết liệt, mắt vẫn nh&igrave;n chạ</h2>\r\n\r\n<h2>- Nhưng ba kh&ocirc;ng đồng &yacute;. Con l&agrave; lao động duy nhất trong gia đ&igrave;nh hiện naỵ Gia đ&igrave;nh cần tiền do con l&agrave;m ra. Con biết điều đ&oacute;.</h2>\r\n\r\n<h2>- Nhưng trong qu&acirc;n đội con cũng được trả lương.</h2>\r\n\r\n<h2>- Lương l&iacute;nh hả? Pađy vừa hỏi vừa cười. Một thợ r&egrave;n ở Wahine c&oacute; thu nhập nhiều hơn một người đi l&iacute;nh ở ch&acirc;u &acirc;u.</h2>\r\n\r\n<h2>- Nhưng khi con ở đ&oacute;, con c&oacute; hy vọng đổi thay số phận thợ r&egrave;n của con! Đ&acirc;y l&agrave; lối tho&aacute;t duy nhất của con.</h2>\r\n\r\n<h2>- Tất cả điều đ&oacute; đều l&agrave; chuyện tầm ph&agrave;o! Trời ơi, con kh&ocirc;ng hiểu ch&uacute;t n&agrave;o về những điều con đang n&oacute;i. Ba sinh ra từ một đất nước trải qua một ng&agrave;n năm chiến tranh, do đ&oacute; ba biết ba đang n&oacute;i g&igrave;. Con c&oacute; bao giờ nghe, c&aacute;c cựu chiến binh kể lại cuộc chiến của những người n&ocirc;ng d&acirc;n g&oacute;c H&agrave; Lan ở Nam Phi chống lại qu&acirc;n Anh? Con thường ra thị trấn Wahine; vậy lần tới con h&atilde;y t&igrave;m hiểu. Ba nghĩ điều đ&oacute; sẽ rất bổ &iacute;ch cho con. Ngo&agrave;i ra ba c&oacute; cảm tưởng bọn Anh kh&ocirc;ng th&iacute;ch d&ugrave;ng người T&acirc;y T&acirc;y Lan; bọng ch&uacute;ng lu&ocirc;n bố tr&iacute; l&iacute;nh T&acirc;y T&acirc;y Lan ở những nơi dễ chết nhất để tr&aacute;nh nguy hiểm cho mạng sống qu&iacute; gi&aacute; của họ. Cứ xem c&aacute;i c&aacute;ch m&agrave; vi&ecirc;n tướng Chwichell đ&atilde; đưa qu&acirc;n l&iacute;nh của ch&uacute;ng ta đến một khu vực ho&agrave;n to&agrave;n kh&ocirc;ng cần thiết như ở Gallipili th&igrave; cũngbiết. Mười ng&agrave;n người bị thiệt mạng trong số năm mươi ng&agrave;n. Tại sao lại phải đi chiến đấu cho cuộc chiến của Anh? Nước Anh đ&atilde; gi&uacute;p &iacute;ch g&igrave; cho con, c&aacute;i xứ gọi l&agrave; Mẹ Tổ Quốc ấy, ngoại trừ việc h&uacute;t c&aacute;c thuộc địa đến giọt m&aacute;u cuối c&ugrave;ng. Nếu con đến nước Anh, con sẽ gặp ngay sự khinh bỉ v&igrave; con đến từ một xứ thuộc địa. T&acirc;y T&acirc;y Lan kh&ocirc;ng hề bị đe dọa, kể cả &uacute;c.</h2>\r\n\r\n<h2>- Nhưng con vẫn muốn v&agrave;o qu&acirc;n đội.</h2>\r\n\r\n<h2>- Con c&oacute; thể muốn bất cứ điều g&igrave; nhưng con sẽ kh&ocirc;ng rời khỏi nơi đ&acirc;y. Hay nhất l&agrave; con qu&ecirc;n đi tất cả những chuyện ấy. Hơn nữa con c&ograve;n qu&aacute; nhỏ để trở th&agrave;nh người l&iacute;nh.</h2>\r\n\r\n<h2>Gương mặt của Frank đỏ gay, hai m&ocirc;i m&iacute;m chặt lại, cậu ta cảm thấy đau khổ về chiều cao dưới trung b&igrave;nh của m&igrave;nh. Mới đ&acirc;y, một nỗi nghi ngờ đ&aacute;ng sợ lại x&acirc;m chiếm Frank. Mười bảy tuổi rồi m&agrave; Frank vẫn cao đ&uacute;ng một thước năm mươi ch&iacute;n, đ&oacute; l&agrave; chiều cao khi Frank được 14 tuổi; phải chăng m&igrave;nh kh&ocirc;ng c&ograve;n cao l&ecirc;n nữa.</h2>\r\n\r\n<h2>Thế nhưng, c&ocirc;ng việc ở l&ograve; r&egrave;n đ&atilde; mang lại cho anh một thể lực hơn hẳn v&oacute;c d&aacute;ng của anh. Tuy nhỏ con nhưng Frank c&oacute; một sức mạnh v&ocirc; địch. Ở tuổi 17; anh chưa bao giờ bị đ&aacute;nh bại trong bất cứ một cuộc thi đấu quyền Anh n&agrave;o. Frank được nhiều người khắp b&aacute;n đảo Taranaki biết đến.</h2>\r\n\r\n<h2>Pađy nh&igrave;n chăm ch&uacute; Frank, cố t&igrave;m hiểu đứa con trai lớn của m&igrave;nh nhưng &ocirc;ng cảm thấy bất lực, Frank l&agrave; đứa con &ldquo;xa con tim&rdquo; của &ocirc;ng nhất d&ugrave; cho &ocirc;ng đ&atilde; cố gắng kh&ocirc;ng thi&ecirc;n vị bất cứ đứa con n&agrave;o.</h2>\r\n\r\n<h2>Cuộc tranh luận tr&ecirc;n b&agrave;n ăn bỗng tắt ngang khi Pađy nhận x&eacute;t về chiều cao của Frank; cả gia đ&igrave;nh c&uacute;i đầu v&agrave; im lặng kh&aacute;c thường, họ ăn m&oacute;n ragu thỏ. Meggie kh&ocirc;ng ăn, mắt cứ nh&igrave;n Frank như lo sợ l&uacute;c n&agrave;o đ&oacute; anh m&igrave;nh c&oacute; thể biến đi mất. C&ograve;n Frank, sau khi ăn sạch, ngồi n&aacute;n lại một l&uacute;c cho đủ lễ, rồi đứng l&ecirc;n đi ra ngo&agrave;i. Một ph&uacute;t sau tiếng r&igrave;u bửa củi vang v&agrave;o tận trong nh&agrave;. Frank tấn c&ocirc;ng những kh&uacute;c gỗ cứng nhất m&agrave; Pađy đ&atilde; dự trữ để chuẩn bị đốt v&agrave;o m&ugrave;a đ&ocirc;ng, củi loại n&agrave;y ch&aacute;y chậm.</h2>\r\n\r\n<h2>Trong khi mọi người nghĩ rằng Meggie đ&atilde; ngủ, c&ocirc; b&eacute; lẻn ra khỏi ph&ograve;ng bằng ng&atilde; cửa sổ v&agrave; đi đến vựa củi, nơi đ&acirc;y được coi l&agrave; đ&aacute;ng kể nhất trong đời sống của cả nh&agrave;. Vựa củi chiếm khoảng đất rộng một trăm m&eacute;t vu&ocirc;ng, Frank đang đứng giữa b&atilde;i, chiếc r&igrave;u s&aacute;ng lo&aacute;ng giơ cao hạ xuống nghe r&iacute;t trong gi&oacute;, những mảnh gỗ nhỏ văng tứ tung, lưng trần của Frank như thoa mỡ, mồ h&ocirc;i chảy xuống từng giọt.</h2>\r\n\r\n<h2>Meggie r&oacute;n r&eacute;n đến im b&ecirc;n chiếc &aacute;o sơ mi v&agrave; &aacute;o l&oacute;t của Frank vứt ở một g&oacute;c, nh&igrave;n anh m&igrave;nh với chiếc r&igrave;u, Meggie kh&ocirc;ng khỏi ngạc nhi&ecirc;n. C&oacute; đến ba c&aacute;i r&igrave;u như thế sẵn s&agrave;ng để thay v&igrave; vỏ c&acirc;y bạch đ&agrave;n c&oacute; thể l&agrave;m lụt đi nhanh ch&oacute;ng những c&aacute;i b&eacute;n nhất.</h2>\r\n\r\n<h2>Frank tiếp tục l&agrave;m việc gần như theo bản năng dưới &aacute;nh s&aacute;ng ho&agrave;ng h&ocirc;n đang tắt lịm. Meggie n&eacute; tr&aacute;nh nhanh nhẹn những mảnh gỗ nhỏ v&agrave; chờ cho Frank kh&aacute;m ph&aacute; sự hiện diện của m&igrave;nh.</h2>\r\n\r\n<h2>Khi Frank quay lại để lấy một c&aacute;i r&igrave;u kh&aacute;c thi anh ta bắt gặp c&ocirc; em g&aacute;i, ngồi đ&oacute; im lặng trong chiếc &aacute;o sơ mi của Frank c&agrave;i n&uacute;t thật kỹ từ tr&ecirc;n xuống dưới. Frank bước đến gần Meggie ngồi xổm xuống, chiếc r&igrave;u vẫn để giữa hai đầu gối.</h2>\r\n\r\n<h2>- Em ra đ&acirc;y bằng c&aacute;ch n&agrave;o hỡi c&ocirc; b&eacute; l&eacute;m lỉnh?</h2>\r\n\r\n<h2>- Ng&atilde; cửa sổ. Em chờ cho Stuart ngủ mệ</h2>\r\n\r\n<h2>- Coi chừng đấy, em sẽ trở th&agrave;nh một thằng con trai hư hỏng mất.</h2>\r\n\r\n<h2>- Mặc kệ. Em vẫn th&iacute;ch chơi với bọn con trai hơn l&agrave; buồn hiu một m&igrave;nh.</h2>\r\n\r\n<h2>- Dĩ nhi&ecirc;n l&agrave; thế.</h2>\r\n\r\n<h2>- C&oacute; chuyện g&igrave; kh&ocirc;ng Meggiẻ</h2>\r\n\r\n<h2>- Frank, anh kh&ocirc;ng đi thật chớ?</h2>\r\n\r\n<h2>Meggie đặt hai b&agrave;n tay l&ecirc;n đ&ugrave;i anh m&igrave;nh v&agrave; ngước nh&igrave;n bằng mắt &acirc;u lo, miệng mở lớn, c&ograve;n nước mắt d&atilde; chảy xuống đầy lỗi mũi l&agrave;m cho c&ocirc; b&eacute; cảm thấy kh&oacute; thở.</h2>\r\n\r\n<h2>- V&acirc;ng c&oacute; lẽ anh sẽ đi Meggie ạ - Frank trả lời dịu d&agrave;ng.</h2>\r\n\r\n<h2>- Anh Frank ơi, kh&ocirc;ng n&ecirc;n! Mẹ v&agrave; em rất cần anh! Nh&agrave; n&agrave;y kh&ocirc;ng thể thiếu vắng anh được.</h2>\r\n\r\n<h2>Frank mỉm cười d&ugrave; biết khi nghe Meggie hồn nhi&ecirc;n lặp lại những lời n&oacute;i giống hệt mẹ.</h2>\r\n\r\n<h2>- Meggie, c&oacute; những việc xảy ra kh&ocirc;ng như mong muốn của ch&uacute;ng tạ Em phải biết điều đ&oacute;. Trong nh&agrave; n&agrave;y c&aacute;c th&agrave;nh vi&ecirc;n của gia đ&igrave;nh Cleary đ&atilde; được dạy phải l&agrave;m việc chung nhau v&igrave; lợi &iacute;ch của mọi người m&agrave; kh&ocirc;ng bao giờ nghĩ đến bản th&acirc;n m&igrave;nh. Anh muốn ra đi v&igrave; anh đ&atilde; 17 tuổi, đ&atilde; đến l&uacute;c phải tự lập. Nhưng cha kh&ocirc;ng đồng &yacute;. Người ta cần anh ở nh&agrave; v&igrave; lợi &iacute;ch chung. V&igrave; anh chưa đủ 21 tuổi n&ecirc;n anh phải nghe lời chạ</h2>\r\n\r\n<h2>Meggie gật đầu thật nhanh, cố gắng hiểu cho được những điều m&agrave; Frank giải th&iacute;ch.</h2>\r\n\r\n<h2>- Thế đấy Meggiẹ Anh đ&atilde; suy nghĩ rất kỹ v&agrave; anh sẽ ra đi, anh kh&ocirc;ng thay đổi quyết định. Anh biết mẹ v&agrave; em kh&ocirc;ng muốn thiếu anh, nhưng Bob lớn rất nhanh rồi ba v&agrave; c&aacute;c em sẽ kh&ocirc;ng nhận ra sự vắng mặt của anh đ&acirc;u. Chỉ c&oacute; đồng tiền của anh kiếm đuợc l&agrave; đ&aacute;ng kể đối với ba th&ocirc;i.</h2>\r\n\r\n<h2>- Thế l&agrave; anh kh&ocirc;ng thương ba mẹ v&agrave; tụi em nữa sao hở anh Frank?</h2>\r\n\r\n<h2>Frank quay lại &ocirc;m Meggie trong v&ograve;ng tay, siết mạnh v&agrave;o l&ograve;ng, vuốt ve c&ocirc; em g&aacute;i với nỗi sung sướng. H&igrave;nh như c&oacute; g&igrave; quyện chặt v&agrave;o như l&agrave; sự tra tấn, pha lẫn đau buồn, c&ugrave;ng x&oacute;t xa v&agrave; cả đ&oacute;i.</h2>\r\n\r\n<h2>- Kh&ocirc;ng đ&acirc;u Meggie! Anh thương em v&ocirc; c&ugrave;ng, thương mẹ v&agrave; em hơn tất cả mọi người cộng lại. Ch&uacute;a ơi, phải chi em lớn hơn th&igrave; anh c&oacute; thể giải th&iacute;ch cho em nghe. Nhưng c&oacute; lẽ cũng may khi em c&ograve;n b&eacute; bỏng như thế n&agrave;y... Đ&uacute;ng thế, như thế n&agrave;y vẫn tốt hơn...</h2>\r\n\r\n<h2>- Em van anh, anh đừng đi Frank ạ.</h2>\r\n\r\n<h2>- Meggie của anh, em kh&ocirc;ng hiểu những g&igrave;anh đ&atilde; n&oacute;i &agrave;? Nhưng th&ocirc;i, điều đ&oacute; kh&ocirc;ng quan trọng. C&aacute;i ch&iacute;nh l&agrave; em đừng n&oacute;i cho ai biết em đ&atilde; gặp anh. Em c&oacute; nghe kh&ocirc;ng? Anh kh&ocirc;ng muốn nguời kh&aacute;c biết em đ&atilde; ro chuyện n&agrave;y.</h2>\r\n\r\n<h2>Meggie đứng l&ecirc;n, r&aacute;ng nở nụ cười:</h2>\r\n\r\n<h2>- Anh thấy cần phải đi th&igrave; cứ đi, anh Frank.</h2>\r\n\r\n<h2>- Meggie, em n&ecirc;n trở về ph&ograve;ng v&agrave; l&ecirc;n giường trước khi mẹ biết được em kh&ocirc;ng c&oacute; ở đ&oacute;. Đi đi, chạy nhanh l&ecirc;n...!</h2>\r\n\r\n<h2>S&aacute;ng h&ocirc;m sau, Frank đ&atilde; đi khỏi nh&agrave;. Khi Fiona v&agrave;o đ&aacute;nh thức Meggie, n&eacute;t mặt của b&agrave; căng thẳng, nghi&ecirc;m nghị hơn l&uacute;c n&agrave;o hết. Meggie nhảy ra khỏi giường như con m&egrave;o bị phỏng nước s&ocirc;i v&agrave; tự mặc quần &aacute;o v&agrave;o kh&ocirc;ng cần nhờ mẹ c&agrave;i những chiếc n&uacute;t nhỏ.</h2>\r\n\r\n<h2>Ở bếp, mấy cậu con trai đ&atilde; ngồi chung quanh b&agrave;n, buồn b&atilde;. Ghế của Pađy trống. Ghế của Frank cũng trống. Meggie im lặng r&oacute;n r&eacute;n ngồi v&agrave;o chỗ của m&igrave;nh, răng cắn chặt v&agrave;o nhau lo sợ. Sau buổi ăn s&aacute;ng, Fiona ra lệnh cho c&aacute;c con dọn dẹp nh&agrave;. Ra ph&iacute;a sau nh&agrave; kho, Bob b&aacute;o tin với Meggiẹ</h2>\r\n\r\n<h2>- Frank đ&atilde; đi rồi - Bob n&oacute;i thật nhỏ.</h2>\r\n\r\n<h2>- C&oacute; lẽ anh ấy chỉ đi Wahine - Meggie đặt giả thiết.</h2>\r\n\r\n<h2>- Kh&ocirc;ng đ&acirc;u. Đồ ngu như b&ograve;. Anh ấy bỏ nh&agrave; ra đi để đăng v&agrave;o l&iacute;nh. Anh cũng muốn lớn nhanh l&ecirc;n để c&oacute; thể l&agrave;m như Frank! Anh ấy may mắn lắm.</h2>\r\n\r\n<h2>- C&ograve;n em th&igrave; th&iacute;c hanh ấy ở lại nh&agrave; hơn.</h2>\r\n\r\n<h2>- Đ&uacute;ng qu&aacute;, em chỉ l&agrave; một đứa con g&aacute;i.</h2>\r\n\r\n<h2>Khi Meggie trở v&agrave;o nh&agrave;, c&ocirc; b&eacute; hỏi mẹ.</h2>\r\n\r\n<h2>- Ba đ&acirc;u rồi?</h2>\r\n\r\n<h2>- Ba đi Wahinẹ</h2>\r\n\r\n<h2>- C&oacute; phải ba đi để đem Frank về?</h2>\r\n\r\n<h2>- Thật kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o giấu chuyện b&iacute; mật trong gia đ&igrave;nh n&agrave;y - Fiona cằn nhằn. - Kh&ocirc;ng, ba kh&ocirc;ng đi t&igrave;m Frank ở Wahinẹ Ba biết Frank đi đ&acirc;u. Ba đi đ&aacute;nh điện t&iacute;n cho cảnh s&aacute;t v&agrave; cho qu&acirc;n đội ở Wanganui. L&iacute;nh qu&acirc;n cảnh sẽ mang anh con về đ&acirc;y.</h2>\r\n\r\n<h2>- Mẹ ơi, con hy vọng rằng họ sẽ t&igrave;m ra Frank. Con kh&ocirc;ng muốn Frank đi lu&ocirc;n.</h2>\r\n\r\n<h2>- Kh&ocirc;ng một ai trong ch&uacute;ng ta muốn Frank ra đi. Ch&iacute;nh v&igrave; thế m&agrave; ba sẽ lo liệu những điều cần thiết... để người ta đưa Frank trở về đ&acirc;y. Tội nghiệp thằng Frank con t&ocirc;i! Tội nghiệp Frank! B&agrave; kh&ocirc;ng n&oacute;i với Meggie m&agrave; than v&atilde;n một m&igrave;nh. T&ocirc;i kh&ocirc;ng hiểu tại sao trẻ con phải g&aacute;nh chịu mọi tội lỗi của người lớn. Thằng Frank tội nghiệp của t&ocirc;i, n&oacute; kh&aacute;c tất cả...</h2>\r\n\r\n<h2>Ba h&ocirc;m sau, cảnh s&aacute;t mang Frank trở về! Cậu ta chống lại dữ dội như một con sư tử - theo lời của vi&ecirc;n trung sĩ ở Wanganui kể lại cho Pađỵ</h2>\r\n\r\n<h2>- Hắn ta đ&uacute;ng l&agrave; một t&ecirc;n v&otilde; sĩ. Khi anh ch&agrave;ng biết được rằng Văn ph&ograve;ng tuyển binh đ&atilde; được th&ocirc;ng b&aacute;o về trường hợp của hắn, hắn vọt chạy nhanh như một ngọn lao. Nếu hắn kh&ocirc;ng xui rủi đụng đầu một to&aacute;n l&iacute;nh tuần th&igrave; c&oacute; lẽ đ&atilde; tho&aacute;t th&acirc;n. Hắn chống lại dữ dội như một kẻ bị ma &aacute;m. Phải cần tới năm người mới c&ograve;ng hắn được. Anh ta quậy như l&agrave;m xiếc.</h2>\r\n\r\n<h2>Vừa kể lể một c&aacute;ch hấp dẫn, vi&ecirc;n cảnh s&aacute;t vừa th&aacute;o những d&acirc;y x&iacute;ch nặng nề trả lại tự do cho Frank rồi đẩy Frank qua ngưỡng cửa. Frank bị loạng choạng v&agrave; khi lấy lại thăng bằng anh đ&atilde; đứng ngay trước mặt Pađỵ Cậu ta co r&uacute;m người lại như thể sợ bị phỏng nếu chạm phải th&acirc;n thể của chạ</h2>\r\n\r\n<h2>C&aacute;c em của Frank đứng chung quanh nh&agrave; nh&igrave;n lại. Meggie &acirc;u lo kh&ocirc;ng biết người ta c&oacute; l&agrave;m g&igrave; hại Frank kh&ocirc;ng.</h2>\r\n\r\n<h2>Frank quay sang nh&igrave;n mẹ trước hết, hai mắt m&agrave;u đen v&agrave; m&agrave;u n&acirc;u pha trộn nhau trong một sự kết hợp chưa bao giờ được n&oacute;i ra v&agrave; cũng kh&ocirc;ng bao giờ n&ecirc;n n&oacute;i ra. &aacute;nh mắt khắt khe m&agrave;u xanh của Pađy nh&igrave;n thẳng Frank vừa khinh miệt, vừa chua cay, như ngầm n&oacute;i kh&ocirc;ng thể chờ đợi g&igrave; kh&aacute;c hơn ở một đức con như thế. Mắt Frank nh&igrave;n xuống đất, chấp nhận c&aacute;i quyền nổi giận của cha m&igrave;nh. Từ h&ocirc;m đ&oacute;, Pađy kh&ocirc;ng n&oacute;i chuyện với con trai ngoại trừ những c&acirc;u cần n&oacute;i.</h2>\r\n\r\n<h2>Gặp lại c&aacute;c em m&igrave;nh, đ&oacute; l&agrave; điều hết sức khổ t&acirc;m với Frank vừa hổ thẹn, vừa kh&oacute; chịu, như con chim lộng lẫy bay l&ecirc;n cao từ phương trời xa thẳm n&agrave;o đ&oacute;, rồi bỗng bị bắt phải quay về bị gẫy c&aacute;nh, tiếng h&oacute;t trở n&ecirc;n lặng lẽ.</h2>\r\n\r\n<h2>Buổi tối như thường lệ, Meggie chờ mẹ kiểm so&aacute;t xong c&aacute;c con đi ngủ, c&ocirc; b&eacute; mới chuồn ra cửa sổ v&agrave; băng qua s&acirc;n sau; Meggie biết r&otilde; Frank ở đ&acirc;u giờ n&agrave;y. Ở một g&oacute;c nh&agrave; khi, tr&aacute;nh mọi c&aacute;i nh&igrave;n soi nh&oacute;i, nhất l&agrave; của chạ</h2>\r\n\r\n<h2>- Frank, Frank, anh ở đ&acirc;u? C&ocirc; b&eacute; hạ thấp giọng hỏi. Meggie bước v&agrave;o nh&agrave; kho tối mịt, ch&acirc;n d&ograve; dẫm mặt đất với nỗi lo sợ chạm phải một con vật n&agrave;o đ&oacute;.</h2>\r\n\r\n<h2>- Lại đ&acirc;y, Meggiẹ</h2>\r\n\r\n<h2>C&ocirc; b&eacute; rất kh&oacute; khăn mới nhận ra được giọng n&oacute;i của Frank. Giọng n&oacute;i ấy trước đ&acirc;y rất quen thuộc với Meggie b&acirc;y giờ lại nghe đều đều, kh&ocirc;ng c&ograve;n sự nồng ấm trong đ&oacute;.</h2>\r\n\r\n<h2>Theo hướng gọi, Meggie đi lần đến chỗ Frank nằm rồi ng&atilde; v&agrave;o l&ograve;ng anh, hai tay &ocirc;m Frank, tay d&agrave;i được bao nhi&ecirc;u c&ocirc; b&eacute; &ocirc;m hết bấy nhi&ecirc;u.</h2>\r\n\r\n<h2>- Frank ơi, em sung sướng qu&aacute; v&igrave; anh đ&atilde; trở về đ&acirc;y.</h2>\r\n\r\n<h2>Frank t&igrave;m c&aacute;ch nằm s&aacute;t hơn nữa dưới đống cỏ kh&ocirc; để c&oacute; thể nh&igrave;n ngang mặt Meggiẹ Tay c&ocirc; b&eacute; luồn v&agrave;o t&oacute;c của Frank miệng k&ecirc;u l&ecirc;n gừ gừ như con m&egrave;o con. B&oacute;ng đen d&agrave;y đặc kh&ocirc;ng cho Frank nh&igrave;n r&otilde; mặt em, thế nhưng t&igrave;nh cảm của Meggie bỗng chốc ph&aacute; tung những g&uacute;t mắc trong l&ograve;ng anh. Frank bắt đầu kh&oacute;c, run rẩy to&agrave;n th&acirc;n một c&aacute;ch đau đớn, nước mắt l&agrave;m ướt &aacute;o c&ocirc; b&eacute;. Meggie th&igrave; kh&ocirc;ng kh&oacute;c. C&aacute;i g&igrave; đ&oacute; trong t&acirc;m hồn nhỏ b&eacute; của Meggie đ&atilde; sớm ch&iacute;n muồi v&agrave; c&ocirc; b&eacute; trở th&agrave;nh một người kh&aacute;c c&oacute; thể cảm nhận được niềm vui tr&agrave;n ngập, dữ dội, &yacute; thức r&otilde; về sự cần thiết của m&igrave;nh. Meggie ngồi tay lay nhẹ đấu t&oacute;c n&acirc;u th&acirc;n y&ecirc;u, cứ thế cho đến khi nỗi đau ở Frank dịu bớt v&agrave; tan v&agrave;o khoảng trống.</h2>', 0, 'tieng-chim-hot-trong-bui-man-gai-chuong-03-04');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(46, 25, 'Tiếng chim hót trong bụi mận gai - Chương 05-06', 'Tiếng chim hót trong bụi mận gai - Chương 05-06', '<h2>Con đường đi Drogheda chẳng gợi một ch&uacute;t kỷ niệm thời thơ ấu của m&igrave;nh - cha Ralph de Bricassart thầm nghĩ như thế. Mắt chỉ mở ph&acirc;n nửa đủ để chống chỏi lại &aacute;nh s&aacute;ng qu&aacute; ch&oacute;i chang giữa l&uacute;c chiếc &ocirc; t&ocirc; mới hiệu Daimler đang lăn b&aacute;nh tr&ecirc;n con đường đất gồ ghề bị đ&agrave;o xới bởi những chiếc xe b&ograve; qua lại.</h2>\r\n\r\n<h2>Kh&ocirc;ng c&oacute; ch&uacute;t g&igrave; &ecirc;m ả, xanh tươi v&agrave; mờ ảo của Ailen ở c&aacute;i xứ n&agrave;y. Drogheda! chẳng phải chiến trường m&agrave; cũng chẳng c&oacute; cuộc v&acirc;y h&atilde;m của cường quốc n&agrave;o trong lịch sử. N&oacute;i thế c&oacute; qu&aacute; lố kh&ocirc;ng? Trong con người cha Ralph t&iacute;nh h&agrave;i hước vẫn rất nhạy, n&oacute; đ&atilde; vẽ ra trong &ocirc;ng h&igrave;nh ảnh một Mary Carson &aacute;c cảm v&agrave; đầy quyền lực theo kiểu nh&acirc;n vật Cromwell.</h2>\r\n\r\n<h2>Cổng r&agrave;o cuối c&ugrave;ng hiện l&ecirc;n đ&agrave;ng sau một cụm c&acirc;y. Chiếc xe đỗ lại, tiếng m&aacute;y nghe kh&aacute; tọ Cha Ralph bước xuống, đầu đội chiếc n&oacute;n rộng v&agrave;nh m&agrave;u ghi đ&atilde; bạc. &ocirc;ng k&eacute;o c&aacute;i chốt ra rồi đẩy c&aacute;nh cổng bực bội. C&oacute; &iacute;t nhất hai mươi bảy chiếc cổng r&agrave;o như thế từ nh&agrave; của cha Ralph ở Gillanbone đến l&atilde;nh địa Drogheda; cứ đến mỗi cổng r&agrave;o cha Ralph phải dừng xe lại bước xuống, mở cổng rồi trở l&ecirc;n xe, cho xe chạy v&agrave;o b&ecirc;n kia cổng rồi lại dừng xe lại, bước xuống đ&oacute;ng cổng lại, rồi lại trở l&ecirc;n xe cho đến cổng kế tiếp. Nhiều l&uacute;c &ocirc;ng c&oacute; &yacute; định giảm bớt ph&acirc;n nửa c&aacute;i c&ocirc;ng việc đều đặn mất c&ocirc;ng ấy bằng c&aacute;ch bỏ lại ph&iacute;a sau những c&aacute;i cổng kh&ocirc;ng được đống lại. Nhưng d&ugrave; với cương vị thầy tu khiến cho nhiều người nễ trọng nhưng &ocirc;ng cũng biết rằng điều đ&oacute; vẫn kh&ocirc;ng ngăn cản c&aacute;c vị chủ của những cổng r&agrave;o sẽ buột &ocirc;ng trả một gi&aacute; đắt cho sự cẩu thả của m&igrave;nh. Cỡi ngựa th&igrave; kh&ocirc;ng đi nhanh v&agrave; tiện lợi bằng xe &ocirc; t&ocirc; nhưng đi ngựa lại c&oacute; thể mở v&agrave; đ&oacute;ng c&aacute;c cổng lại m&agrave; kh&ocirc;ng cần đặt ch&acirc;n xuống đất. &ldquo;Tấm huy chương n&agrave;o cũng c&oacute; bề tr&aacute;i của n&oacute;&rdquo; vừa n&oacute;i, tay cha Ralph vừa m&acirc;n m&ecirc; c&aacute;i bảng chỉ dẫn tốc độ. C&aacute;i cổng ở ph&iacute;a sau đ&atilde; được c&agrave;i chặt lại, cha cho &ocirc; t&ocirc; chạy nốt c&acirc;y số cuối c&ugrave;ng, tiến v&agrave;o ch&iacute;nh điện của khu vực Droghedạ</h2>\r\n\r\n<h2>Trang trại n&agrave;y ở &uacute;c khiến cho bất cứ ai cũng phải k&iacute;nh nể kể cả một người &aacute;i Nhĩ Lan quen thấy những l&acirc;u đ&agrave;i v&agrave; dinh thự qu&iacute; tộc. Drogheda l&agrave; trang trại xưa nhất v&agrave; rộng nhất trong v&ugrave;ng. &ocirc;ng chủ của n&oacute;, l&atilde;o Michael Carson, - đ&atilde; qua đời - x&acirc;y một t&ograve;a nh&agrave; rất h&agrave;i h&ograve;a với khung cảnh thi&ecirc;n nhi&ecirc;n, vật liệu x&acirc;y dựng l&agrave; sa thạch m&agrave;u v&agrave;ng lấy c&aacute;ch đ&acirc;y t&aacute;m c&acirc;y số; kiểu kiến tr&uacute;c của t&ograve;a nh&agrave; nghi&ecirc;m trang theo thế kỷ thứ 18 đời vua George đệ nhất ở Anh với một tầng v&agrave; những cửa sổ rộng chia th&agrave;nh &ocirc; vu&ocirc;ng nhỏ; một h&agrave;nh lang được chịu tr&ecirc;n những cột sắt chạy d&agrave;i theo tầng trệt. Những c&aacute;nh cửa sổ bằng gỗ m&agrave;u đen trang điểm cho ch&iacute;nh diện của t&ograve;a nh&agrave; cũng c&oacute; c&ocirc;ng dụng của n&oacute;: giữa c&aacute;i n&oacute;ng oi bức m&ugrave;a h&egrave;, c&aacute;c c&aacute;nh cửa đ&oacute;ng chặt để giữ c&aacute;i m&aacute;t mẻ b&ecirc;n trong.</h2>\r\n\r\n<h2>H&ocirc;m nay, trời v&agrave;o thu, c&aacute;c gốc nho mọc l&ecirc;n cao những tua xoắn v&agrave; l&aacute; rất xanh. Nhưng v&agrave;o m&ugrave;a xu&acirc;n, những d&acirc;y đậu t&iacute;a trồng v&agrave;o l&uacute;c t&ograve;a nh&agrave; vừa x&acirc;y - năm mươi năm trước - kết th&agrave;nh một khối chắc nịch với những ch&ugrave;m c&acirc;y tử đinh hương, c&agrave;nh t&uacute;a ra rậm rạp, đầy hoa b&aacute;m d&agrave;i theo tường v&agrave; tr&ecirc;n m&aacute;i hi&ecirc;n. Quanh ng&ocirc;i nh&agrave;, nhiều hecta cỏ trải rộng như một tấm thảm, được chăm s&oacute;c rất kỹ lưỡng, rải r&aacute;c đ&acirc;y đ&oacute; l&agrave; những h&igrave;nh khối đối xứng, d&ugrave; ở m&ugrave;a n&agrave;o, cũng rực rỡ m&agrave;u sắc; hoa hồng, thược dược, đinh tử v. v... chen nhau. Một cụm c&acirc;y bạch đ&agrave;n đẹp tuyệt m&agrave;u trắng x&aacute;m, với những chiếc l&aacute; thon d&agrave;i đong đưa c&aacute;ch mặt đất hai mươi thước, l&agrave;m cho ng&ocirc;i nh&agrave; tr&aacute;nh được sức n&oacute;ng dữ dội của mặt trời.</h2>\r\n\r\n<h2>Ngay cả những th&aacute;p chứa nước xấu x&iacute; cũng được c&aacute;i loại c&acirc;y c&oacute; hoa mọc đan len nhau che k&iacute;n. Nhờ say m&ecirc; trang trại, Michael Carson c&ograve;n sống đ&atilde; x&acirc;y kh&ocirc;ng biết bao nhi&ecirc;u l&agrave; th&aacute;p v&agrave; bồn nước. Người ta đồn rằng Drogheda c&oacute; thể giữ cho c&aacute;c thảm cỏ v&agrave; c&aacute;c vườn hoa tiếp tục xanh tươi d&ugrave; cho trời kh&ocirc;ng mưa trong mười năm.</h2>\r\n\r\n<h2>Cha Ralph rời &ocirc; t&ocirc;, đang băng qua b&atilde;i cỏ th&igrave; một đầy tớ g&aacute;i xuất hiện ở hi&ecirc;n trước nh&agrave;. Gương mặt c&ocirc; ta đầy t&agrave;n nhang, cười hết miệng.</h2>\r\n\r\n<h2>- Ch&agrave;o chị Minnie - cha Ralph l&ecirc;n tiếng trước.</h2>\r\n\r\n<h2>- Thưa cha, thật hoan hỉ được gặp cha v&agrave;o buổi s&aacute;ng đẹp n&agrave;y - Tiếng của chị ta vang l&ecirc;n vui vẻ, pha giọng Ailen.</h2>\r\n\r\n<h2>Một tay chị giữ c&aacute;nh cửa bật mở rộng, tay ia chị đưa ra đ&oacute;n lấy chiến n&oacute;n cũ h&igrave;nh d&aacute;ng kh&ocirc;ng c&oacute; vẻ g&igrave; l&agrave; đồ d&ugrave;ng của một tu sĩ.</h2>\r\n\r\n<h2>Mary Carson ngồi tr&ecirc;n chiếc ghế b&agrave;nh gần cửa sổ lớn mở rộng, dường như b&agrave; kh&ocirc;ng ch&uacute; &yacute; đến c&aacute;i lạnh đang tr&agrave;n v&agrave;o căn ph&ograve;ng n&agrave;y. M&aacute;i t&oacute;c m&agrave;u v&agrave;ng hung của b&agrave; vẫn rực rỡ kh&ocirc;ng k&eacute;m thời trẻ trung. Tr&ecirc;n mặt điểm những nốt t&agrave;n nhang m&agrave;u n&acirc;u v&igrave; tuổi t&aacute;c, nhưng kh&ocirc;ng thể che giấu được dưới những n&eacute;t nhăn. Dấu hiệu duy nhất về bản t&iacute;nh bướng bỉnh của người đ&agrave;n b&agrave; n&agrave;y ẩn chứa trong hai đường khắc s&acirc;u hai b&ecirc;n mũi chạy d&agrave;i xuống m&eacute;p m&ocirc;i v&agrave; trong đ&ocirc;i mắt m&agrave;u xanh nhợt lạnh l&ugrave;ng.</h2>\r\n\r\n<h2>Cha Ralph lặng lẽ đi ngang qua tấm thảm Aubusson v&agrave; h&ocirc;n tay l&ecirc;n b&agrave; chủ trang trại. Cử chỉ đ&oacute; ho&agrave;n to&agrave;n ph&ugrave; hợp với tầm v&oacute;c cao lớn, d&aacute;ng dấp qu&iacute; ph&aacute;i của vị linh mục. Hơn nữa chiếc &aacute;o d&ograve;ng m&agrave;u đen giản dị c&ograve;n mang lại &ocirc;ng c&aacute;i vẻ lịch thiệp của một nh&agrave; tụ Đ&ocirc;i mắt thản nhi&ecirc;n của b&agrave; Mary Carson bỗng s&aacute;ng l&ecirc;n v&agrave; h&igrave;nh như b&agrave; cố l&agrave;m duy&ecirc;n khi l&ecirc;n tiếng:</h2>\r\n\r\n<h2>- Cha d&ugrave;ng ch&uacute;t tr&agrave; nh&eacute;?</h2>\r\n\r\n<h2>- Rất sẵn s&agrave;ng, thưa b&agrave; Carson nếu trước hết b&agrave; y&ecirc;u cầu được xem lễ, &ocirc;ng đ&aacute;p.</h2>\r\n\r\n<h2>&ocirc;ng bu&ocirc;ng m&igrave;nh xuống chiếc ghế b&agrave;nh; chiếc &aacute;o d&ograve;ng bị giật l&ecirc;n để lộ ra ở b&ecirc;n trong chiếc quần cỡi ngựa m&agrave; hai ống nh&eacute;t v&agrave;o đ&ocirc;i ủng cao đến gần đầu gối.</h2>\r\n\r\n<h2>- T&ocirc;i c&oacute; mang theo b&aacute;nh th&aacute;nh cho b&agrave;. nhưng nếu b&agrave; muốn xem lễ, t&ocirc;i sẵn s&agrave;ng l&agrave;m lễ trong v&agrave;i ph&uacute;t. C&oacute; lẽ t&ocirc;i kh&ocirc;ng cần phải nhịn ăn l&acirc;u hơn.</h2>\r\n\r\n<h2>- Cha đ&atilde; đối xử qu&aacute; tối với ch&uacute;ng t&ocirc;i, thưa chạ B&agrave; n&oacute;i với một th&aacute;i độ kh&aacute; ki&ecirc;u kỳ. B&agrave; biết ch&iacute;nh x&aacute;c l&agrave; linh mục n&agrave;y, những như bao nhi&ecirc;u linh mục kh&aacute;c đến với b&agrave;, kh&ocirc;ng phải do l&ograve;ng k&iacute;nh trọng m&agrave; v&igrave; đồng tiền của b&agrave;.</h2>\r\n\r\n<h2>- Xin mời cha d&ugrave;ng tr&agrave; - b&agrave; n&oacute;i tiếp - T&ocirc;i thấy cha l&agrave;m lễ đ&atilde; đủ.</h2>\r\n\r\n<h2>Cha Ralph cố che giấu những &aacute;c cảm của &ocirc;ng về người phụ nữ lớn tuổi n&agrave;y. R&otilde; r&agrave;ng, gi&aacute;o khu n&agrave;y l&yacute; tưởng để cho &ocirc;ng r&egrave;n luyện lại. Nếu c&oacute; dịp tho&aacute;t khỏi thờ kỳ thất sủng m&agrave; &ocirc;ng đ&atilde; rơi v&agrave;o do t&aacute;nh t&igrave;nh kỳ kh&ocirc;i của m&igrave;nh, th&igrave; &ocirc;ng sẽ kh&ocirc;ng bao giờ lặp lại sai lầm cũ. V&agrave; nếu &ocirc;ng biết giữ g&igrave;n c&aacute;c lợi thế sẵn c&oacute;, b&agrave; gi&agrave; n&agrave;y c&oacute; thể thỏa m&atilde;n những ước nguyện của &ocirc;ng.</h2>\r\n\r\n<h2>- T&ocirc;i phải th&uacute; thật, thưa cha, năm vừa qua l&agrave; một năm đặc biệt dễ chịu - b&agrave; ta n&oacute;i. Cha l&agrave; người chăn dắt được hơn cha Kelly gi&agrave; rất nhiều, mong Ch&uacute;a trừng phạt t&acirc;m hồn mục n&aacute;t của &ocirc;ng ấy.</h2>\r\n\r\n<h2>B&agrave; n&oacute;i những tiếng sau c&ugrave;ng giọng đanh lại, th&ugrave; hằn.</h2>\r\n\r\n<h2>- B&agrave; Carson th&acirc;n mến! Đ&oacute; l&agrave; một t&igrave;nh cảm kh&ocirc;ng được nh&acirc;n từ lắm.</h2>\r\n\r\n<h2>- C&oacute; thể đ&uacute;ng, nhưng n&oacute; l&agrave; sự thật. &ocirc;ng ta chỉ l&agrave; một l&atilde;o gi&agrave; say rượu v&agrave; t&ocirc;i tin chắc rằng Ch&uacute;a sẽ khiến cho t&acirc;m hồn l&atilde;o ta bị thối rữa y như sự thối rữa th&acirc;n x&aacute;c của l&atilde;o tạ B&acirc;y giờ t&ocirc;i bắt đầu hiểu cha kh&aacute;c nhiều. T&ocirc;i nghĩ t&ocirc;i c&oacute; quyền đặt ra với cha v&agrave;i c&acirc;u hỏi chứ? N&oacute;i cho c&ugrave;ng, cha coi Drogheda như nơi nghỉ ngơi của chạ Trang trại n&agrave;y sẽ tạo điều kiền cho cha l&agrave;m quen với chăn nu&ocirc;i v&agrave; ho&agrave;n to&agrave;n chỉnh kỹ thuật cỡi ngựa của chạ, tr&aacute;nh những thăng trầm của đời sống ở Gillỵ Dĩ nhi&ecirc;n tất cả những chuyện đ&oacute; l&agrave; do sự mời mọc của t&ocirc;i, nhưng ngược lại t&ocirc;i c&oacute; quyền đặt ra một số y&ecirc;u cầu.</h2>\r\n\r\n<h2>Cha Ralph kh&ocirc;ng thể n&agrave;o chấp nhận được c&aacute;i c&aacute;ch m&agrave; b&agrave; ta nhắc nhở &ocirc;ng l&agrave; người chịu ơn b&agrave;. Nhưng &ocirc;ng đo&aacute;n biết sẽ c&oacute; ng&agrave;y b&agrave; ta đ&aacute;nh gi&aacute;, g&acirc;y ảnh hưởng, chi phối m&igrave;nh, đủ để c&oacute; thể đưa ra một số đ&ograve;i hỏi.</h2>\r\n\r\n<h2>- Đ&uacute;ng thế, b&agrave; Carson. T&ocirc;i c&oacute; cảm ơn b&agrave; thế n&agrave;o cũng kh&ocirc;ng đ&aacute;p lại được sự đ&oacute;n tiếp ở Drogheda v&agrave; tất cả những &acirc;n huệ ... n&agrave;o l&agrave; ngựa, xe &ocirc; t&ocirc;...</h2>\r\n\r\n<h2>- Cha bao nhi&ecirc;u tuổi? - b&agrave; ta cắt ngang.</h2>\r\n\r\n<h2>- Hai mươi t&aacute;m.</h2>\r\n\r\n<h2>- Cha trẻ hơn t&ocirc;i nghĩ. D&ugrave; sao người ta kh&ocirc;ng gởi một linh mục như cha v&agrave;o một nơi như Gillỵ Cha đ&atilde; l&agrave;m g&igrave; để bị người ta tống về đ&acirc;y, ở tận c&ugrave;ng thế giới?</h2>\r\n\r\n<h2>- T&ocirc;i đ&atilde; nhục mạ gi&aacute;m mục - &ocirc;ng trả lời một c&aacute;ch b&igrave;nh thản, nụ cười tr&ecirc;n m&ocirc;i.</h2>\r\n\r\n<h2>- &agrave;! Nhưng t&ocirc;i kh&ocirc;ng thể tưởng tượng một linh mục c&oacute; t&agrave;i năng như cha lại c&oacute; thể vui th&iacute;ch ở c&aacute;i h&oacute;c x&oacute; như Gillanbonẹ</h2>\r\n\r\n<h2>- Đ&oacute; l&agrave; &yacute; muốn của Ch&uacute;a.</h2>\r\n\r\n<h2>- Kh&ocirc;ng đ&uacute;ng. Cha đến đ&acirc;y do những sai lầm của con người..., của bản th&acirc;n cha, v&agrave; của vị gi&aacute;m mục của chạ Chỉ c&oacute; Đức Gi&aacute;o Ho&agrave;ng l&agrave; kh&ocirc;ng thể sai lầm m&agrave; th&ocirc;i. Cha ho&agrave;n to&agrave;n bị t&aacute;ch khỏi m&ocirc;i trường tự nhi&ecirc;n của m&igrave;nh ở Gilly; mọi người đều nhận thấy điều đ&oacute;, mặc d&ugrave; tất cả ch&uacute;ng t&ocirc;i rất sung sướng c&oacute; được một người như cha; sự kiện n&agrave;y đ&atilde; mang lại cho ch&uacute;ng t&ocirc;i một sự thay đổi dễ chịu v&igrave; cho đến nay người ta đ&atilde; gửi đến đ&acirc;y những t&ecirc;n kh&ocirc;ng ra g&igrave; lại mặc &aacute;o d&ograve;ng - Cũng kh&ocirc;ng cấm cản c&aacute;i m&ocirc;i trường tự nhi&ecirc;n của cha ở trong một ph&ograve;ng chờ n&agrave;o đ&oacute; đầy quyền lực gi&aacute;o hội; chớ kh&ocirc;ng phải ở đ&acirc;y giữa ngựa v&agrave; cừu. M&agrave;u đỏ t&iacute;a Hồng y thật hạp với chạ</h2>\r\n\r\n<h2>- T&ocirc;i e rằng đ&oacute; kh&ocirc;ng phải l&agrave; vấn đề. T&ocirc;i kh&ocirc;ng coi Gillanbone ở đ&uacute;ng ngay trung t&acirc;m l&atilde;nh thổ d&agrave;nh cho người đại diện của Gi&aacute;o Ho&agrave;ng. T&ocirc;i may mắn gặp được b&agrave; ở tại Drogheda n&agrave;y.</h2>\r\n\r\n<h2>B&agrave; Carson đ&oacute;n nhận lời nịnh h&oacute;t được t&ocirc; đậm kh&ocirc;ng kh&aacute;c lắm với sự dự t&iacute;nh của người đ&atilde; thốt ra. B&agrave; th&iacute;ch th&uacute; về vẻ đẹp trai, sự &acirc;n cần của vị linh mục c&oacute; đầu &oacute;c sắc b&eacute;n v&agrave; tế nhị. Thật ra &ocirc;ng c&oacute; thể l&agrave; một Hồng y tuyệt vời. Trong cả đời, b&agrave; kh&ocirc;ng nhớ được khi n&agrave;o đ&atilde; gặp được một người đ&agrave;n &ocirc;ng đẹp đến thế hoặc sử dụng c&aacute;i đẹp của m&igrave;nh một c&aacute;ch t&agrave;i t&igrave;nh như cha Ralph. &ocirc;ng ta biết r&otilde; sức l&ocirc;i cuốn của m&igrave;nh: d&aacute;ng dấp cao, th&acirc;n m&igrave;nh c&acirc;n đối, n&eacute;t thanh v&agrave; qu&yacute; ph&aacute;i. C&oacute; thể n&oacute;i mỗi yếu tố của c&aacute;i đẹp thể x&aacute;c ấy đều k&egrave;m theo sự chăm ch&uacute;t sực kỳ c&aacute;i bề ngo&agrave;i m&agrave; Ch&uacute;a chỉ trao cho một số rất &iacute;t người. Từ m&aacute;i t&oacute;c đen v&agrave; mịn, đến m&agrave;u xanh đ&aacute;ng kinh ngạc của đ&ocirc;i mắt, b&agrave;n tay, b&agrave;n ch&acirc;n thanh nh&atilde; v&agrave; c&acirc;n đối, &ocirc;ng ta thật ho&agrave;n to&agrave;n. Đ&uacute;ng thế, &ocirc;ng ta &yacute; thức rất r&otilde; về vẻ đẹp của m&igrave;nh. Thế nhưng &ocirc;ng ta l&agrave;m ra vẻ d&egrave; dặt như muốn chứng tỏ kh&ocirc;ng bao giờ &ocirc;ng để m&igrave;nh n&ocirc; lệ với c&aacute;i bề ngo&agrave;i đẹp trai ấy. Cũng c&oacute; thể &ocirc;ng ta sẵn s&agrave;ng sử dụng sức l&ocirc;i cuốn của m&igrave;nh để đạt được điều mong muốn kh&ocirc;ng ch&uacute;t ngại ng&ugrave;ng nếu &ocirc;ng x&eacute;t thấy cần thiết nhưng kh&ocirc;ng hề tự m&ecirc; hoặc. B&agrave; Mary Carson thiết tha muốn biết điều g&igrave; đ&atilde; đưa đẩy vị linh mục cư xử theo c&aacute;ch đ&oacute;.</h2>\r\n\r\n<h2>Thật lạ l&ugrave;ng. C&oacute; lắm linh mục đẹp như những ch&agrave;ng Adonis trong huyền thoại, cộng th&ecirc;m sức quyến rũ nhục dục của một Don Jucạ C&oacute; phải chăng họ chọ sự độc th&acirc;n để l&aacute;nh m&igrave;nh trốn tr&aacute;nh c&aacute;c hậu quả.</h2>\r\n\r\n<h2>- Tại sao cha lại chấp nhận cuộc sống ở Gillanbonẻ Tại sao cha kh&ocirc;ng cởi bỏ &aacute;o d&ograve;ng m&agrave; phải theo đuổi một cuộc sống như thế n&agrave;y? Những năng lực trời ph&uacute; cho ph&eacute;p cha trở n&ecirc;n gi&agrave;u c&oacute; v&agrave; uy quyền... Trước hết, cha đừng n&oacute;i l&agrave; cha dửng dưng với &yacute; niệm về quyền lực.</h2>\r\n\r\n<h2>- B&agrave; Carson th&acirc;n mến, b&agrave; l&agrave; một t&iacute;n đồ c&ocirc;ng gi&aacute;o - &ocirc;ng n&oacute;i, m&agrave;y nh&iacute;u lại - B&agrave; biết rằng ước nguyện của t&ocirc;i l&agrave; thi&ecirc;ng li&ecirc;ng; t&ocirc;i kh&ocirc;ng thể từ bỏ. T&ocirc;i sẽ m&atilde;i m&atilde;i l&agrave; linh mục cho đến chết.</h2>\r\n\r\n<h2>B&agrave; ta h&iacute;t mạnh v&agrave;o rồi cười ngặt nghẽo.</h2>\r\n\r\n<h2>- Th&ocirc;i đi, th&ocirc;i đi! Bộ cha thật sự tưởng rằng nếu cha từ bỏ ước nguyện của m&igrave;nh, cha sẽ bị một cơn sấm chớp, ch&oacute; v&agrave; s&uacute;ng đạn rượt đuổi?</h2>\r\n\r\n<h2>- Dĩ nhi&ecirc;n l&agrave; kh&ocirc;ng. Cũng như t&ocirc;i kh&ocirc;ng tin b&agrave; c&oacute; thể nghĩ rằng sự sợ h&atilde;i bị trừng phạt sẽ giữ t&ocirc;i lại con đường ngaỵ</h2>\r\n\r\n<h2>- Ồ! Cha đừng d&ugrave;ng những lời lẽ trịnh trọng như thế. Thế th&igrave; c&aacute;i g&igrave; r&agrave;ng buộc chả C&aacute;i g&igrave; đ&atilde; buột cha phải chịu đựng ở nơi bụi bặm, n&oacute;ng bức v&agrave; đầy mũi m&ograve;ng như c&aacute;i xứ Gilly n&agrave;y? V&agrave; nhất l&agrave; cha c&oacute; thể ở lại đ&acirc;y vĩnh viễn.</h2>\r\n\r\n<h2>Một tho&aacute;ng suy nghĩ xuất hiện trong đ&ocirc;i mắt m&agrave;u xanh của vị linh mục nhưng &ocirc;ng lại mỉm cười, nh&igrave;n b&agrave; Carson thương hại.</h2>\r\n\r\n<h2>- Quả thật b&agrave; c&ograve;n sức sống lắm - &ocirc;ng n&oacute;i như th&igrave; thầm. T&ocirc;i đ&atilde; chọn lựa trở th&agrave;nh linh mục khi c&ograve;n ở trong n&ocirc;i. L&agrave;m sao t&ocirc;i c&oacute; thể giải th&iacute;ch điều đ&oacute; với một phụ nữ? Phần n&agrave;o đ&oacute; t&ocirc;i l&agrave; nơi thu nhận, thưa b&agrave; Carson, v&agrave; nhiều l&uacute;c, t&ocirc;i cảm thấy ch&uacute;ng tr&agrave;n ngập trong t&ocirc;i. Nếu t&ocirc;i l&agrave; một linh mục xứng đ&aacute;ng hơn, sẽ kh&ocirc;ng cần một thời kỳ chuyển tiếp n&agrave;o. V&agrave; c&aacute;i trạng th&aacute;i trọn vẹn, sự đồng nhất với Ch&uacute;a kh&ocirc;ng lệ thuộc ở một nơi n&agrave;o nhất định, d&ugrave; t&ocirc;i ở Gillabone hay giữa c&aacute;c bức tường trong một t&ograve;a gi&aacute;m mục, ơn Ch&uacute;a vẫn đến như nhau. Dĩ nhi&ecirc;n rất kh&oacute; giải th&iacute;ch v&igrave; n&oacute; vẫn l&agrave; điều huyền b&iacute;, ngay cả với c&aacute;c linh mục. C&oacute; phải chăng ch&iacute;nh l&agrave; thế. Từ bỏ sự thi&ecirc;ng li&ecirc;ng ấy? T&ocirc;i kh&ocirc;ng thể từ bỏ được.</h2>\r\n\r\n<h2>- Như vậy, cha giải th&iacute;ch c&oacute; thể giống như một thứ quyền lực, phải kh&ocirc;ng? Tại sao n&oacute; chỉ ban cho c&aacute;c linh mục? Điều g&igrave; l&agrave;m cho cha tin rằng chỉ cần một lần xức dầu th&aacute;nh trong một buổi lễ d&agrave;i v&agrave; mệt mỏi l&agrave; đủ mang đến điều thi&ecirc;ng li&ecirc;ng ấy cho mọi người?</h2>\r\n\r\n<h2>&ocirc;ng lắc đầu.</h2>\r\n\r\n<h2>- Phải nhiều năm suy nghĩ trước khi được thụ phong, thưa b&agrave; Carson. Một tiến triển chậm đưa đến một trạng th&aacute;i t&acirc;m hồn mở ra cho Ch&uacute;a ngự. Việc thụ phong tự n&oacute; xứng đ&aacute;ng v&agrave; mỗi ng&agrave;y, ch&uacute;ng ta tỏ ra xứng đ&aacute;ng; đ&oacute; l&agrave; mục đ&iacute;ch của kh&aacute;nh nguyện, b&agrave; thấy kh&ocirc;ng? Kh&ocirc;ng một yếu tố thế tục n&agrave;o, xen v&agrave;o giữa người gi&aacute;o sĩ v&agrave; thi&ecirc;n hướng của &ocirc;ng ta... kể ca t&igrave;nh y&ecirc;u của người phụ nữ hay tiền bạc, hoặc sự gh&ecirc; tởm phải khuất phục trước những mệnh lệnh của những người kh&aacute;c. Sự ngh&egrave;o kh&oacute; kh&ocirc;ng phải mới lạ đối với t&ocirc;i, t&ocirc;i kh&ocirc;ng xuất th&acirc;n từ một gia đ&igrave;nh gi&agrave;u c&oacute;. T&ocirc;i chấp nhận sự thanh khiết m&agrave; kh&ocirc;ng thấy kh&oacute; khăn lắm. C&ograve;n sự phục t&ugrave;ng? Đối với ri&ecirc;ng t&ocirc;i, đ&oacute; l&agrave; điều kh&oacute; nhất. Nhưng t&ocirc;i t&ugrave;ng phục v&igrave; rằng nếu t&ocirc;i tự coi m&igrave;nh quan trọng hơn chức năng thừa sai của Ch&uacute;a th&igrave; t&ocirc;i đ&atilde; lạc hướng. T&ocirc;i phục t&ugrave;ng. V&agrave; nếu cần, t&ocirc;i sẵn s&agrave;ng chấp nhận Gillanbone suốt đời.</h2>\r\n\r\n<h2>- Như thế th&igrave; cha l&agrave; thằng ngốc - b&agrave; Carson vặn lại. T&ocirc;i cũng thế, t&ocirc;i cho rằng c&oacute; nhiều điều quan trọng hơn l&agrave; t&igrave;m cho m&igrave;nh một nh&acirc;n t&igrave;nh. Nhưng tự cho m&igrave;nh l&agrave; nơi Ch&uacute;a ngự, t&ocirc;i kh&ocirc;ng coi đ&oacute; l&agrave; một trong những điều quan trọng. T&ocirc;i kh&ocirc;ng bao giờ nhận ra được rằng cha lại tin ở Ch&uacute;a với một nhiệt t&acirc;m như thế. T&ocirc;i nghĩ ở cha cũng c&oacute; sự ho&agrave;i nghị</h2>\r\n\r\n<h2>- Ồ, t&ocirc;i cũng thế th&ocirc;i! C&oacute; con người l&yacute; tr&iacute; n&agrave;o lại kh&ocirc;ng ho&agrave;i nghi! Ch&iacute;nh v&igrave; thế m&agrave; c&oacute; những l&uacute;c t&ocirc;i trở n&ecirc;n trống rỗng. B&agrave; biết kh&ocirc;ng, t&ocirc;i nghĩ rằng t&ocirc;i c&oacute; thể từ bỏ mọi tham vọng, v&agrave; b&oacute;p chết mọi ham muốn để c&oacute; hy vọng trở th&agrave;nh một linh mục ho&agrave;n to&agrave;n.</h2>\r\n\r\n<h2>- Sự ho&agrave;n to&agrave;n d&ugrave; ở bất cứ lĩnh vực n&agrave;o cũng kh&ocirc;ng thể chịu được, một thứ buồn ch&aacute;n thảm hại - B&agrave; Carson nhận x&eacute;t - Ri&ecirc;ng t&ocirc;i, t&ocirc;i th&iacute;ch v&agrave; th&iacute;ch hơn nhiều một n&eacute;t n&agrave;o đ&oacute; kh&ocirc;ng ho&agrave;n to&agrave;n.</h2>\r\n\r\n<h2>Cha Ralph cười, nh&igrave;n b&agrave; Carson với sự ngưỡng mộ lẫn sự đố kỵ Một người đ&agrave;n b&agrave; đặc biệt.</h2>\r\n\r\n<h2>Sự g&oacute;a bụa đến với b&agrave; c&aacute;ch đ&acirc;y 30 năm v&agrave; đứa con duy nhất, đứa con trai, đ&atilde; chết l&uacute;c tuổi c&ograve;n nhỏ. Do ho&agrave;n cảnh đặc biết của b&agrave; ở giữa Gillanbone, b&agrave; đ&atilde; kh&ocirc;ng nhận lời bất cứ một người đ&agrave;n &ocirc;ng n&agrave;o hỏi cưới b&agrave; trong số những người nhiều cao vọng nhất m&agrave; b&agrave; c&oacute; quan hệ Với tư c&aacute;ch vợ g&oacute;a của Michael Carson, b&agrave; l&agrave; một b&agrave; ho&agrave;ng kh&ocirc;ng thể chối c&atilde;i. Nhưng với tư c&aacute;ch vợ của một g&atilde; n&agrave;o đ&oacute; b&agrave; đ&agrave;nh chuyển cả quyền lực của m&igrave;nh cho người m&igrave;nh chọn. Ch&iacute;nh v&igrave; thế m&agrave; b&agrave; từbỏ c&aacute;c kho&aacute;i lạc x&aacute;c thịt, th&iacute;ch cầm lấy c&acirc;y gậy quyền h&agrave;nh hơn. Thật kh&oacute; tưởng tượng được b&agrave; ta lấy chồng ở c&aacute;i xứ Gillanbone n&agrave;y vốn rất th&iacute;ch c&aacute;i m&oacute;n ngồi l&ecirc; đ&ocirc;i m&aacute;ch, n&oacute;i xấu. Th&igrave; đ&atilde; ra sao. Biết đ&acirc;u b&agrave; lại kh&ocirc;ng đi ngược lại c&aacute;ch xử thế l&acirc;u nay v&agrave; trở n&ecirc;n nh&acirc;n hậu, yếu đuối.</h2>\r\n\r\n<h2>Nhưng b&acirc;y giờ b&agrave; đ&atilde; kh&aacute; lớn tuổi v&agrave; người ta biết rằng b&agrave; ở ngo&agrave;i v&ograve;ng c&aacute;m dỗ x&aacute;c thịt. Khi vị linh mục trẻ tỏ ra cần mẫn trong c&aacute;c nhiệm vụ, b&agrave; c&oacute; thể trả ơn &ocirc;ng bằng những m&oacute;n qu&agrave; nhỏ, như chiếc &ocirc; tộ Cử chỉ đ&oacute; kh&ocirc;ng hề bị coi l&agrave; khiếm nh&atilde;. L&agrave; cột cụ kh&ocirc;ng hề lay chuyển của nh&agrave; thờ trong suốt cuộc đời, b&agrave; đ&atilde; gi&uacute;p cho xứ đạo v&agrave; người d&igrave;u dắt tinh thần đ&uacute;ng như điều n&ecirc;n l&agrave;m, ngay cả khi cha Kelly say m&egrave;m lảo đảo trong khi l&agrave;m lễ. B&agrave; kh&ocirc;ng phải l&agrave; người duy nhất quan t&acirc;m một c&aacute;ch đặc biệt người thay thế cha Kelly; cha Ralph de Bricassart xứng đ&aacute;ng được l&ograve;ng tất cả c&aacute;c con chi&ecirc;n ngh&egrave;o cũng như gi&agrave;u. Nếu gi&aacute;o d&acirc;n ở xa nhất kh&ocirc;ng c&oacute; khả năng đến gặp &ocirc;ng ở Gillanbone th&igrave; &ocirc;ng đến với họ v&agrave; cho tới khi Mary Carson tặng cho &ocirc;ng chiếc &ocirc; t&ocirc; Daimler, &ocirc;ng di chuyển bằng ngựa. Sự ki&ecirc;n nhẫn v&agrave; l&ograve;ng tố của &ocirc;ng khiến cho mọi người đều biết ơn. Một số người y&ecirc;u mến &ocirc;ng một c&aacute;ch ch&acirc;n th&agrave;nh; chẳng hạn như Martin King ở Bugela đ&atilde; trang ho&agrave;ng lại rất tốn k&eacute;m nh&agrave; xứ; Dominique &Oacute; Rourke ở Dibbanđibban trả tiền lương h&agrave;ng th&aacute;ng cho người quản gia giỏi giang.</h2>\r\n\r\n<h2>Do đ&oacute;, đứng tr&ecirc;n đ&agrave;i cao của tuổi t&aacute;c v&agrave; đơn vị, Mary Carson tự cho ph&eacute;p m&igrave;nh được hưởng mối quan hệ th&acirc;n mật với cha Ralph m&agrave; kh&ocirc;ng hề sợ tai tiếng. Bằng th&iacute;ch tấn c&ocirc;ng &ocirc;ng bằng tr&iacute; tuệ, con người m&agrave; b&agrave; đ&aacute;nh gi&aacute; ngang m&igrave;nh về mặt n&agrave;y. B&agrave; lấy l&agrave;m th&iacute;ch th&uacute; khi t&igrave;m c&aacute;ch xuy&ecirc;n thủng c&aacute;c &yacute; nghĩ thầm k&iacute;n của cha Ralph bởi v&igrave; b&agrave; chưa bao giờ tin rằng m&igrave;nh đ&atilde; hiểu r&otilde; con người n&agrave;y.</h2>\r\n\r\n<h2>Minnie đẩy chiếc b&agrave;n c&oacute; bốn b&aacute;nh xe nhỏ v&agrave;o ph&ograve;ng, tr&ecirc;n đ&oacute; đặt tr&agrave; v&agrave; b&aacute;nh ngọt.</h2>\r\n\r\n<h2>Mary Carson trao cho kh&aacute;ch b&aacute;nh gal&eacute;t v&agrave; l&aacute;t b&aacute;nh m&igrave; nướng.</h2>\r\n\r\n<h2>- Cha th&acirc;n mến - b&agrave; n&oacute;i sau một tiếng thở ra - t&ocirc;i mong cha sẽ cầu nguyện cho t&ocirc;i với một sự nhiệt t&igrave;nh đặc biệt như s&aacute;ng naỵ</h2>\r\n\r\n<h2>- B&agrave; h&atilde;y gọi t&ocirc;i l&agrave; Ralph. T&ocirc;i nghi ngờ về khả năng t&ocirc;i cầu nguyện cho b&agrave; với một nhiệt t&igrave;nh cao hơn mọi khị Tuy nhi&ecirc;n t&ocirc;i sẽ cố gắng.</h2>\r\n\r\n<h2>- Ồ, cha quả l&agrave; người quyến rũ! Nhưng nhận x&eacute;t của cha kh&ocirc;ng hồn nhi&ecirc;n như người ta tưởng. N&oacute;i chung, t&ocirc;i kh&ocirc;ng quan t&acirc;m đến c&aacute;i g&igrave; đương nhi&ecirc;n c&oacute;, nhưng ở cha t&ocirc;i kh&ocirc;ng d&aacute;m tin chắc rằng điều đập v&agrave;o mắt thật ra chỉ l&agrave; c&aacute;i phần ch&igrave;m dưới nước của tảng băng; phần n&agrave;o đ&oacute; giống như củ c&agrave; rốt m&agrave; người ta nhử trước con lừa. Thật sự cha nghĩ thế n&agrave;o về t&ocirc;i, cha De Bricassart? T&ocirc;i sẽ kh&ocirc;ng bao giờ biết được điều đ&oacute; v&igrave; rằng cha l&agrave; người qu&aacute; tế nhị phải kh&ocirc;ng? Thật quyến rũ, quyến rũ... Nhưng cha nhớ h&atilde;y cầu nguyện cho t&ocirc;i. T&ocirc;i gi&agrave; rồi v&agrave; phạm nhiều tội lỗi.</h2>\r\n\r\n<h2>Rồi b&agrave; Carson cười kh&uacute;c kh&iacute;ch.</h2>\r\n\r\n<h2>- T&ocirc;i rất muốn biết được cha đ&atilde; phạm tội như thế n&agrave;o? Đ&uacute;ng thế, t&ocirc;i rất muốn biết. Trong l&uacute;c n&agrave;y, t&ocirc;i đang thiếu một người quản l&yacute;.</h2>\r\n\r\n<h2>- Lại thiếu nữa?</h2>\r\n\r\n<h2>- C&oacute; tất cả năm người lần lượt l&agrave;m c&ocirc;ng việc ấy trong năm quạ Thật kh&oacute; m&agrave; t&igrave;m được một người tin cậy.</h2>\r\n\r\n<h2>- Nếu dư luận đồi đ&atilde;i m&agrave; đ&uacute;ng th&igrave; r&otilde; r&agrave;ng b&agrave; kh&ocirc;ng phải l&agrave; một người chủ quảng đại v&agrave; tốt bụng đối với những người l&agrave;m c&ocirc;ng.</h2>\r\n\r\n<h2>- Ồ sao lại ăn n&oacute;i tr&acirc;ng tr&aacute;o thế - Mary Carson vừa n&oacute;i vừa cười - Ai đ&atilde; tặng cho cha chiếc Daimler mới toanh để cho cha khỏi phải đi ngựa.</h2>\r\n\r\n<h2>- &agrave;! Nhưng b&agrave; cũng thấy với nhiệt t&igrave;nh của t&ocirc;i như thế n&agrave;o khi cầu nguyện cho b&agrave;.</h2>\r\n\r\n<h2>- Nếu Michael c&oacute; được một ch&uacute;t tr&iacute; tuệ v&agrave; c&aacute; t&iacute;nh của cha, t&ocirc;i tin rằng t&ocirc;i đ&atilde; y&ecirc;u anh ấy - B&agrave; ta bu&ocirc;ng c&acirc;u n&oacute;i đột ngột (sắc mặt của b&agrave; đổi ngay v&agrave; trở n&ecirc;n cau c&oacute;) c&oacute; lẽ cha nghĩ rằng t&ocirc;i kh&ocirc;ng c&ograve;n một người b&agrave; con n&agrave;o ở tr&ecirc;n c&otilde;i đời n&agrave;y v&agrave; t&ocirc;i buộc phải để lại tiền bạc, đất đai cho Mẹ của gi&aacute;o hội ch&uacute;ng tả</h2>\r\n\r\n<h2>- T&ocirc;i ho&agrave;n to&agrave;n kh&ocirc;ng biết một t&iacute; g&igrave; - cha Ralph đ&aacute;p lại một c&aacute;ch thản nhi&ecirc;n, tay vừa ch&acirc;m tr&agrave; v&agrave;o t&aacute;ch.</h2>\r\n\r\n<h2>- Đ&acirc;y nh&eacute;, t&ocirc;i vẫn c&ograve;n một người em gia đ&igrave;nh đ&ocirc;ng con phần nhiều l&agrave; con trai.</h2>\r\n\r\n<h2>- B&agrave; quả thật rất may mắn - giọng cha Ralph nghi&ecirc;m nghị.</h2>\r\n\r\n<h2>- Khi t&ocirc;i lấy chồng, t&ocirc;i ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; một t&agrave;i sản đất đai n&agrave;o. L&uacute;c đ&oacute; t&ocirc;i biết rằng t&ocirc;i kh&ocirc;ng thể kiếm một tắm chồng kh&aacute; giả ở Ailen nơi m&agrave; một c&ocirc; g&aacute;i muốn c&acirc;u được một &ocirc;ng chồng gi&agrave;u phải c&oacute; học v&agrave; thuộc d&ograve;ng d&otilde;i tiếng t&acirc;m. Cho n&ecirc;n t&ocirc;i đ&atilde; l&agrave;m ăn vất vả để d&agrave;nh dụm một số tiền đủ để đi đến một nước kh&aacute;c, ở đ&oacute; đ&agrave;n &ocirc;ng gi&agrave;u c&oacute; v&agrave; &iacute;t đ&ograve;i hỏi hơn. Khi đặt ch&acirc;n l&ecirc;n đất n&agrave;y, t&ocirc;i chỉ biết dựa v&agrave;o gương mặt dễ coi, v&oacute;c d&aacute;ng v&agrave; một bộ &oacute;c tr&ecirc;n trung b&igrave;nh của m&igrave;nh. Như thế t&ocirc;i đ&atilde; đ&oacute;ng đinh Michael Carson, một thằng gi&agrave;u ngu độn. &ocirc;ng ta y&ecirc;u t&ocirc;i cho đến hơi thở cuối c&ugrave;ng.</h2>\r\n\r\n<h2>- C&ograve;n người em của b&agrave;? - Cha Ralph vội v&agrave;ng hỏi với hy vọng k&eacute;o b&agrave; ta trở lại đề t&agrave;i ch&iacute;nh.</h2>\r\n\r\n<h2>- Em trai t&ocirc;i Padraic nhỏ hơn t&ocirc;i 11 tuổi. Vậy l&agrave; hắn đ&atilde; 54 rồi. Ch&uacute;ng t&ocirc;i l&agrave; hai kẻ duy nhất của gia đ&igrave;nh c&ograve;n sống s&oacute;t lại. T&ocirc;i biết rất &iacute;t về em t&ocirc;i v&igrave; khi t&ocirc;i rời Galway n&oacute; vẫn c&ograve;n qu&aacute; nhỏ. Hiện nay em t&ocirc;i sống ở T&acirc;y T&acirc;y Lan. N&oacute; đ&atilde; bỏ xứ ra đi mưu t&igrave;m sự nghiệp nhưng lại kh&ocirc;ng th&agrave;nh c&ocirc;ng. Tối h&ocirc;m kia l&uacute;c một trong những c&ocirc;ng nh&acirc;n của t&ocirc;i đến cho hay t&ecirc;n quản gia Arthur Teviot đ&atilde; chuồn với bọc quần &aacute;o của hắn, t&ocirc;i chợt nghĩ đến Padraic, một người sống chết với đất đai, nhiều kinh nghiệm nhưng kh&ocirc;ng c&oacute; điều kiện để trở th&agrave;nh &ocirc;ng chủ. Thế th&igrave; tại sao t&ocirc;i kh&ocirc;ng viết thư cho em t&ocirc;i v&agrave; y&ecirc;u cầu Padraic đưa con c&aacute;i đến đ&acirc;y l&agrave;m ăn sinh sống - T&ocirc;i đ&atilde; tự n&oacute;i với m&igrave;nh, khi t&ocirc;i chết, sẽ cho n&oacute; thừa kế Drogheda v&agrave; c&ocirc;ng ty Michael Limited v&igrave; n&oacute; l&agrave; b&agrave; con gần nhất của t&ocirc;i, kh&ocirc;ng kể v&agrave;i người anh em b&agrave; con xa x&ocirc;i c&ograve;n ở lại Ailen.</h2>\r\n\r\n<h2>B&agrave; cười v&agrave; n&oacute;i tiếp:</h2>\r\n\r\n<h2>- Tại sao lại phải chờ đợi? N&oacute; đến ngay b&acirc;y giờ vẫn tố hơn l&agrave; khi t&ocirc;i đ&atilde; chết. Ngay b&acirc;y giờ n&oacute; c&oacute; thể l&agrave;m quen với c&ocirc;ng việc nu&ocirc;i cừu tr&ecirc;n những c&aacute;nh đồng đất đen m&agrave; theo t&ocirc;i rất kh&aacute;c xa với c&aacute;c điều kiện ở T&acirc;y T&acirc;y Lan. Sau đ&oacute;, khi t&ocirc;i kh&ocirc;ng c&ograve;n nữa, n&oacute; đ&atilde; c&oacute; thể mang đ&ocirc;i ủng của t&ocirc;i một c&aacute;ch thoải m&aacute;i</h2>\r\n\r\n<h2>Đầu hơi nghi&ecirc;ng, b&agrave; quan s&aacute;t rất kỹ cha Ralph.</h2>\r\n\r\n<h2>- T&ocirc;i tự hỏi tại sao b&agrave; kh&ocirc;ng nghĩ đến chuyện n&agrave;y sớm hơn - Ralph chỉ n&oacute;i thế.</h2>\r\n\r\n<h2>- Ồ, t&ocirc;i c&oacute; nghĩ chứ! Tuy nhi&ecirc;n gần đ&acirc;y, t&ocirc;i muốn tr&aacute;nh bằng mọi gi&aacute; c&aacute;i đ&aacute;m k&ecirc;n k&ecirc;n chờ đợi một c&aacute;ch sốt ruột ng&agrave;y t&agrave;n của t&ocirc;i. T&ocirc;i c&oacute; cảm gi&aacute;c l&agrave; c&aacute;i ng&agrave;y của số mệnh gần kề v&agrave; t&ocirc;i tin rằng... Ồ! T&ocirc;i kh&ocirc;ng biết. G&iacute;a m&agrave; c&aacute;i ng&agrave;y ấy quanh t&ocirc;i l&agrave; những người ruột thịt chắc l&agrave; những người ruột thịt chắc l&agrave; t&ocirc;i sẽ dễ chịu hơn.</h2>\r\n\r\n<h2>- Chuyện g&igrave; đ&atilde; xảy ra, b&agrave; đang bệnh? Cha Ralph hỏi dồn đầy lo &acirc;u.</h2>\r\n\r\n<h2>- T&ocirc;i ho&agrave;n to&agrave;n khỏe mạnh - B&agrave; Mary Carson nh&uacute;n vai - Tuy nhi&ecirc;n, vượt qua c&aacute;i tuối 65 cũng c&oacute; c&aacute;i g&igrave; đ&aacute;ng lo ngại.</h2>\r\n\r\n<h2>- T&ocirc;i hiểu, v&agrave; b&agrave; rất c&oacute; l&yacute;. Chắc hẳn b&agrave; muốn được nghe những giọng n&oacute;i trẻ trung trong ng&ocirc;i nh&agrave; n&agrave;y.</h2>\r\n\r\n<h2>- Ồ, họ kh&ocirc;ng ở đ&acirc;y đ&acirc;u - B&agrave; Carson k&ecirc;u l&ecirc;n - Họ sẽ ở tại nh&agrave; d&agrave;nh cho quản l&yacute;, nằm b&ecirc;n con suối c&aacute;ch đ&acirc;y kh&ocirc;ng xạ Đặc biệt t&ocirc;i kh&ocirc;ng th&iacute;ch trẻ con, kể cả tiếng n&oacute;i của ch&uacute;ng.</h2>\r\n\r\n<h2>- Phải chăng l&agrave;m như thế l&agrave; keo kiệt với em trai duy nhất của b&agrave;?</h2>\r\n\r\n<h2>- N&oacute; sẽ c&oacute; gia t&agrave;i... vậy n&oacute; phải xứng đ&aacute;ng để được nhận c&aacute;i gia t&agrave;i đ&oacute;! B&agrave; Carson n&oacute;i một c&aacute;ch s&ograve;ng phẳng.</h2>\r\n\r\n<h2>Fiona lại sinh th&ecirc;m một đứa b&eacute; trai s&aacute;u ng&agrave;y trước khi Meggie kỷ niệm sinh nhật lần thứ ch&iacute;n. B&agrave; coi đ&oacute; la điều may mắn sau hai lần sẩy thai. Ch&iacute;n tuổi, Meggie c&oacute; thể gi&uacute;p đỡ cho mẹ. Fiona đ&atilde; bốn mươi - c&aacute;i tuổi kh&ocirc;ng c&ograve;n trẻ để sinh con m&agrave; kh&ocirc;ng thấy cực nhọc v&agrave; mất sức. Đứa con trai k&eacute;m sức khỏe được đặt t&ecirc;n l&agrave; Harold, v&agrave; đ&acirc;y l&agrave; lần đầu ti&ecirc;n b&agrave; phải nhờ b&aacute;c sĩ đến nh&agrave; chăm s&oacute;c thường xuy&ecirc;n.</h2>\r\n\r\n<h2>Đ&uacute;ng l&agrave; họa v&ocirc; đơn ch&iacute;, biết bao nhi&ecirc;u kh&oacute; khăn lại chồng chất l&ecirc;n gia đ&igrave;nh Clearỵ Sau chiến tranh thời kỳ ph&aacute;t triển vẫn c&ograve;n l&agrave; chuyện xa vời, tệ hơn nữa c&aacute;c miền qu&ecirc; đang bước v&agrave;o cơn khủng hoảng n&ecirc;n việc l&agrave;m c&agrave;ng ng&agrave;y c&agrave;ng kh&oacute; hơn.</h2>\r\n\r\n<h2>L&atilde;o Angus Mac Whirter một h&ocirc;m mang đến cho gia đ&igrave;nh bức điện l&uacute;c buổi cơm trưa vừa xong. Hay tay run run. Pađy mở phong thư ra: một bức thư gởi như thế thường kh&ocirc;ng phải l&agrave; những tin vui. Mấy đứa con trai tụ tập lại quanh &ocirc;ng, chỉ trừ Frank cầm t&aacute;ch tr&agrave; v&agrave; rời khỏi b&agrave;n ăn. Fiona nh&igrave;n theo Frank rồi quay nh&igrave;n &ocirc;ng chồng. Pađy lẩm bẩm g&igrave; đ&oacute;.</h2>\r\n\r\n<h2>- Chuyện g&igrave; đ&oacute; hở anh? B&agrave; hỏi chồng đang nh&igrave;n tờ giấy v&agrave; &ocirc;ng trả lời như một tin b&aacute;o tử.</h2>\r\n\r\n<h2>- Archibald kh&ocirc;ng cần cha con ch&uacute;ng ta - Bob đấm mạnh một c&aacute;ch tức giận xuống b&agrave;n. Anh đ&atilde; qu&aacute; hy vọng về việc theo tập sự l&agrave;m thợ cắt long cừu m&agrave; khởi đầu l&agrave; đ&agrave;n cừu của Archibald.</h2>\r\n\r\n<h2>- Tại sao hắn lại chơi m&igrave;nh một vố đau như vậy hở bả Đ&aacute;ng lẽ ng&agrave;y mai ch&uacute;ng ta đ&atilde; bắt đầu.</h2>\r\n\r\n<h2>- Hắn kh&ocirc;ng n&ecirc;u ra l&yacute; do n&agrave;o cả. Ba đo&aacute;n chắc một t&ecirc;n v&ocirc; loại n&agrave;o đ&oacute; đ&atilde; hạ gi&aacute; thấp để &ldquo;chặt co&rdquo; dưới ch&acirc;n ch&uacute;ng tạ</h2>\r\n\r\n<h2>- Ồ, Pađy! B&agrave; vợ thở ra.</h2>\r\n\r\n<h2>Đứa b&eacute; m&agrave; cả nh&agrave; gọi l&agrave; Hal bắt đầu kh&oacute;c trong chiếc n&ocirc;i đặt gần nh&agrave; bếp. Fiona chưa kịp quay lại th&igrave; Meggie đ&atilde; nhanh ch&acirc;n đến b&ecirc;n em. Frank vừa trở lại đứng gần cửa, vẫn t&aacute;ch tr&agrave; tr&ecirc;n tay, anh nh&igrave;n cha một c&aacute;ch chăm ch&uacute;.</h2>\r\n\r\n<h2>- Vậy th&igrave; bắt buộc t&ocirc;i phải đi gặp Archibald - Cuối c&ugrave;ng Pađy đ&atilde; n&oacute;i như thế - B&acirc;y giờ đ&atilde; qu&aacute; trễ để đi t&igrave;m một chỗ c&oacute; người thu&ecirc; cắt l&ocirc;ng cừu. T&ocirc;i cho rằng hắn phải giải th&iacute;ch với t&ocirc;i r&otilde; r&agrave;ng. Ch&uacute;ng ta chỉ c&ograve;n hy vọng kiếm được c&ocirc;ng việc vắt sữa cho đến khi đến Willoughly v&agrave;o th&aacute;ng bảy.</h2>\r\n\r\n<h2>Trong khi nh&igrave;n Meggie thay t&atilde; cho em, Frank tr&ecirc;u chọc:</h2>\r\n\r\n<h2>- Một người mẹ nhỏ...</h2>\r\n\r\n<h2>- Ồ, đừng n&oacute;i thế - Meggie bẻ lại, giận dỗi - Em chỉ gi&uacute;p mẹ th&ocirc;i m&agrave;...</h2>\r\n\r\n<h2>- Anh biết - Frank gật đầu dễ thương - Em l&agrave; một c&ocirc; g&aacute;i rất tốt, Meggie b&eacute; nhỏ của anh.</h2>\r\n\r\n<h2>Nếu kh&ocirc;ng v&igrave; Meggie v&agrave; mẹ, Frank đ&atilde; dứt kho&aacute;t rời khỏi ng&ocirc;i nh&agrave; n&agrave;y. Anh nh&igrave;n cay độc về ph&iacute;a cha, người tr&aacute;ch nhiệm ch&iacute;nh về cuộc sống mới c&oacute; lắm x&aacute;o trộn trong gia đ&igrave;nh.</h2>\r\n\r\n<h2>V&igrave; đ&atilde; lớn Frank kh&ocirc;ng bị đuổi ra khỏi nh&agrave; l&uacute;c mẹ sinh b&eacute; Hal, cho n&ecirc;n anh nghe tất cả những tiếng r&ecirc;n la khủng khiếp từ ph&ograve;ng mẹ h&ocirc;m Hal ra đời. Chưa c&oacute; một người phụ nữ n&agrave;o lại chịu một thử th&aacute;ch gh&ecirc; gớm như thế - anh đ&atilde; lặp lại c&acirc;u n&agrave;y cả ng&agrave;n lần. Một người đ&agrave;n &ocirc;ng &iacute;ch kỷ, kh&ocirc;ng chịu để cho vợ được y&ecirc;n.</h2>\r\n\r\n<h2>Meggie đặt Hal trở v&agrave;o n&ocirc;i v&agrave; đến đứng b&ecirc;n cạnh mẹ. Lại một c&ocirc; g&aacute;i m&agrave; cuộc đời sau n&agrave;y sẽ vứt đi - Frank chua x&oacute;t thầm nghĩ. Cũng gương mặt nghi&ecirc;ng nghi&ecirc;ng, sự nhạy cảm c&oacute; c&aacute;i g&igrave; đ&oacute; của mẹ trong đ&ocirc;i tay, trong th&acirc;n h&igrave;nh nhỏ b&eacute; của Meggiẹ C&ocirc; b&eacute; sẽ giống mẹ rất nhiều khi trở th&agrave;nh một phụ nữ. Meggie sẽ lấy ai l&agrave;m chồng? Một thằng bặm trợn n&agrave;o kh&aacute;c l&agrave;m nghề cắt l&ocirc;ng cừu người Ailen hay một t&ecirc;n n&agrave;o đ&oacute; th&ocirc; lỗ, dơ d&aacute;y, l&agrave;m mướn tại một h&atilde;ng sữa ở Wahinẻ Con b&eacute; xứng đ&aacute;ng c&oacute; một người chồng hơn thế nhưng gốc g&aacute;c gia đ&igrave;nh kh&ocirc;ng cho ph&eacute;p Meggie đ&ograve;i hỏi cao hơn. T&igrave;nh thế bế tắc, đ&oacute; l&agrave; điều m&agrave; ai cũng n&oacute;i đến v&agrave; mỗi năm như khẳng định th&ecirc;m lời tuy&ecirc;n &aacute;n n&agrave;y.</h2>\r\n\r\n<h2>Đo&aacute;n biết c&aacute;i nh&igrave;n chăm ch&uacute; của Frank; Fiona v&agrave; Meggie quay lại, c&ugrave;ng một l&uacute;c cả hai cười thật tr&igrave;u mến, nụ cười m&agrave; phụ nữ thường chỉ d&agrave;nh cho người m&igrave;nh y&ecirc;u thương nhất. Frank đặt t&aacute;ch tr&agrave; xuống rồi đi ra s&acirc;n cho ch&oacute; ăn, anh th&egrave;m được kh&oacute;c hay giết người miễn l&agrave; l&agrave;m sao vơi đi sự đau khổ.</h2>\r\n\r\n<h2>Ba ng&agrave;y sau Pađy nhận được tin buồn từ Archibald th&igrave; thư của Mary Carson đến. &ocirc;ng mở thư ngay tại trạm bưu điện Wahine v&agrave; trở về nh&agrave; vui mừng nhảy nh&oacute;t như một đứa trẻ con.</h2>\r\n\r\n<h2>- Ch&uacute;ng ta sẽ đi &uacute;c! &ocirc;ng h&eacute;t l&ecirc;n, tay giơ cao những tờ giấy trắng mịn đắt tiền trước sự ngơ ng&aacute;c của gia đ&igrave;nh.</h2>\r\n\r\n<h2>Im lặng. Mọi người nh&igrave;n &ocirc;ng. Sắc mặt của Fiona lộ n&eacute;t băn khoăn, Meggie cũng thế, nhưng &aacute;nh mắt của mấy đứa con trai th&igrave; đầy niềm vui. Mắt Frank s&aacute;ng ch&oacute;i.</h2>\r\n\r\n<h2>- Nhưng n&agrave;y Pađy, tại sao chị ấy bỗng nhi&ecirc;n nhớ đến anh sau bao nhi&ecirc;u năm xa c&aacute;ch? - Fiona hỏi chồng sau khi đọc l&aacute; thự Từ l&acirc;u nay chị ấy vẫn gi&agrave;u c&oacute; v&agrave; vẫn sống c&ocirc; độc. Thế nhưng c&oacute; l&uacute;c n&agrave;o b&agrave; ấy ngỏ lời gi&uacute;p đỡ ch&uacute;ng ta đ&acirc;u.</h2>\r\n\r\n<h2>- H&igrave;nh như chị ấy sợ chết một m&igrave;nh - Pađy n&oacute;i để vừa xua đuổi nỗi lo sợ của m&igrave;nh vừa l&agrave;m y&ecirc;n l&ograve;ng Fionạ Em c&oacute; thấy chị ấy n&oacute;i: chị kh&ocirc;ng c&ograve;n trẻ v&agrave; gia đ&igrave;nh c&aacute;c em l&agrave; những người thừa kế của chị. Chị nghĩ rằng ch&uacute;ng ta n&ecirc;n gặp nhau trước khi chị chết v&agrave; đ&atilde; đến l&uacute;c em tập l&agrave;m người quản l&yacute; của chị, đ&oacute; l&agrave; một c&aacute;ch đ&agrave;o tạo tốt. C&aacute;c con của em đ&atilde; đến tuổi l&agrave;m việc, cũng c&oacute; thể tham gia c&oacute; hiệu quả v&agrave;o việc chăn nu&ocirc;i. Drogheda sẽ trở th&agrave;nh một kiểu l&agrave;m ăn gia đ&igrave;nh, điều khiển bởi gia đ&igrave;nh, kh&ocirc;ng cần sự tiếp tay từ b&ecirc;n ngo&agrave;i.</h2>\r\n\r\n<h2>- Chị ấy kh&ocirc;ng n&oacute;i g&igrave; đến chuyện gởi tiền cho ch&uacute;ng ta đi &agrave;?</h2>\r\n\r\n<h2>Pađy ưỡn người l&ecirc;n v&agrave;dứt kho&aacute;t:</h2>\r\n\r\n<h2>- Kh&ocirc;ng bao giờ t&ocirc;i chịu hạ m&igrave;nh xuống xin xỏ chị ấy! Ch&uacute;ng ta c&oacute; thể đến &uacute;c m&agrave; kh&ocirc;ng cần ăn m&agrave;y tiền của chị ấy. T&ocirc;i c&oacute; một số tiền d&agrave;nh dụm l&acirc;u naỵ</h2>\r\n\r\n<h2>- T&ocirc;i nghĩ đ&uacute;ng ra chị ấy phải chịu c&aacute;i khoản chi ph&iacute; n&agrave;y chứ - Fiona vặn lại kh&ocirc;ng chịu bỏ quạ</h2>\r\n\r\n<h2>Sự bướng bỉnh của Fiona l&agrave;m ai cũng ngạc nhi&ecirc;n bở l&acirc;u nay &iacute;t khi Fiona n&oacute;i năng như vậy.</h2>\r\n\r\n<h2>- Tại sao anh lại từ bỏ cuộc sống của anh tại đ&acirc;y v&agrave; đi l&agrave;m việc cho chị ấy chỉ v&igrave; tin v&agrave;o một lời hứa trong thử Chị ấy chưa bao giờ nh&uacute;c nh&iacute;ch một ng&oacute;n tay để gi&uacute;p ch&uacute;ng ta, t&ocirc;i kh&ocirc;ng tin ở chị ấy. T&ocirc;i nhớ đ&atilde; nghe ch&iacute;nh anh n&oacute;i về chị ấy tựu chung chị ấy c&oacute; những ng&oacute;n tay cong quắp đ&aacute;ng tởm hơn cả bọn người b&ograve;n r&uacute;t từng xụ Anh Pađy &agrave;, d&ugrave; sao th&igrave; anh cũng kh&ocirc;ng biết r&otilde; chị ấy; anh v&agrave; chị ấy c&aacute;ch xa nhau về tuổi, chị ấy đi l&uacute;c trước khi anh bắt đầu đi học kia m&agrave;.</h2>\r\n\r\n<h2>- Anh thấy điều đ&oacute; chẳng ăn thua g&igrave;. V&agrave; nếu chị ấy thuộc loại người b&ograve;n r&uacute;t từng xu, lại c&agrave;ng tốt, v&igrave; như thế chẳng qua l&agrave; ch&uacute;ng ta sẽ được nhiều tiền hơn. Đừng k&egrave;o n&agrave;i nữa Fiona &agrave;. Ch&uacute;ng ta sẽ đi &uacute;c v&agrave; ch&uacute;ng ta sẽ tự lo cho chuyến đi ấy.</h2>\r\n\r\n<h2>Fiona kh&ocirc;ng n&oacute;i th&ecirc;m một lời. Tr&ecirc;n sắc mặt b&agrave; kh&ocirc;ng để lộ một n&eacute;t n&agrave;o để c&oacute; thể biết b&agrave; bị tổn thương hay kh&ocirc;ng.</h2>\r\n\r\n<h2>- Hoan h&ocirc;, ch&uacute;ng ta sẽ đi &uacute;c! Bob &ocirc;m lấy vai cha n&oacute;i lớn.</h2>\r\n\r\n<h2>Jack, Hughie v&agrave; Stuart nhảy m&uacute;a lung tung c&ograve;n Frank chỉ mỉm cười, mắt nh&igrave;n xa xăm. Chỉ c&oacute; Fiona v&agrave; Meggie l&agrave; buồn v&agrave; lo ngại. Cả hai hy vọng rằng mọi chuyện sẽ kh&ocirc;ng đến đ&acirc;u v&igrave; cuộc sống ở &uacute;c chưa chắc sẽ dễ d&agrave;ng hơn, b&ecirc;n ấy cũng sẽ y hệt như ở đ&acirc;y m&agrave; lại th&ecirc;m sự lạc l&otilde;ng v&igrave; lạ xứ lạ người.</h2>\r\n\r\n<h2>- Gillanbone ở đ&acirc;u bả - Stuart hỏi.</h2>\r\n\r\n<h2>Một tấm bản đồ thế giới cũ kỹ được đem ra. Mấy đứa con trai mở to mắt nh&igrave;n những tờ giấy ngả m&agrave;u v&agrave;ng ố cho đến khi ph&aacute;t hiện ra New South Wales, diện t&iacute;ch tương đương với Bắc đảo của T&acirc;y T&acirc;y Lan. V&agrave; k&igrave;a Gillabone ở ph&iacute;a tr&ecirc;n g&oacute;c.</h2>\r\n\r\n<h2>- Đ&acirc;y l&agrave; tấm bản đồ rất cũ, Pađy giải th&iacute;ch. &uacute;c ch&acirc;u giống như ch&acirc;u Mỹ, ph&aacute;t triển với những bước vĩ đại. T&ocirc;i tin chắc rằng cho tới nay đ&atilde; c&oacute; nhiều th&agrave;nh phố được x&acirc;y dựng.</h2>\r\n\r\n<h2>Pađy đi Wanganni để giữ t&aacute;m chỗ nằm tr&ecirc;n t&agrave;u thủy Wahinẹ Phải mang mấy con ch&oacute; cho h&agrave;ng x&oacute;m, b&aacute;n rẻ một số b&agrave;n ghế, chỉ giữ lại một v&agrave;i m&oacute;n m&agrave; Fiona cần thiết. Ch&eacute;n, dĩa, quần &aacute;o, s&aacute;ch vở v&agrave; dụng cụ nh&agrave; bếp đều được đ&oacute;ng th&ugrave;ng mang xuống t&agrave;u.</h2>\r\n\r\n<h2>Cuộc h&agrave;nh tr&igrave;nh đ&uacute;ng l&agrave; cơn &aacute;c mộng. Trước khi t&agrave;u Wahine rời cảng Wellington, tất cả đ&atilde; say s&oacute;ng v&agrave; cứ trong t&igrave;nh trạng đ&oacute; chịu đựng suốt một ng&agrave;n hai trăm hải l&yacute; tr&ecirc;n mặt biển động với những cơn gi&oacute; m&ugrave;a đ&ocirc;ng.</h2>\r\n\r\n<h2>Bốn tiếng đồng hồ sau khi rời Wellington, Frank v&agrave; Meggie vẫn lu&ocirc;n ở b&ecirc;n mẹ v&agrave; em b&eacute;, tưởng đ&acirc;u mẹ m&igrave;nh sắp chết. Người phục vụ tr&ecirc;n t&agrave;u mời một y sĩ từ những buồng hạng nhất xuống kh&aacute;m Fionạ &ocirc;ng ta đ&atilde; lắc đầu bi quan.</h2>\r\n\r\n<h2>- Mong sao cho cuộc h&agrave;nh tr&igrave;nh sớm kết th&uacute;c - &ocirc;ng ta chỉ n&oacute;i thế.</h2>\r\n\r\n<h2>Liền đ&oacute;, &ocirc;ng ra lệnh cho y t&aacute; t&igrave;m sữa cho đứa b&eacute;.</h2>\r\n\r\n<h2>Frank v&agrave; Meggie vẫn cố gắng cho b&eacute; Hal b&uacute; nhưng Hal kh&ocirc;ng thiết tha lắm khi b&igrave;nh sữa được đưa v&agrave;o miệng.</h2>\r\n\r\n<h2>C&ograve;n ba tiếng đồng hồ nữa đến Sydney, biển đột ngột phẳng lặng như mặt gương. Meggie bắt đầu tỉnh lại, tưởng tượng rằng chiếc t&agrave;u đang r&ecirc;n siết sau những đ&ograve;n nhừ tử chịu đựng s&oacute;ng gi&oacute;.</h2>\r\n\r\n<h2>Meggie kh&ocirc;ng bao giờ qu&ecirc;n những tiếng c&ograve;i buồn b&atilde; r&uacute; l&ecirc;n trong sương m&ugrave;, ấn tượng đầu ti&ecirc;n khi đến &uacute;c.</h2>\r\n\r\n<h2>Pađy đ&atilde; bồng Fiona tr&ecirc;n tay khi rời t&agrave;u Wahine, tiếp theo l&agrave; Frank với em b&eacute;, Meggie với một vali v&agrave; mỗi đứa con trai bước đi chệnh choạng, lưng c&ograve;ng dước sức nặng của một thứ n&agrave;o đ&oacute;. Họ đến Pyrmont - c&aacute;i t&ecirc;n ho&agrave;n to&agrave;n v&ocirc; nghĩa đối với họ - v&agrave;o một buổi s&aacute;ng m&ugrave;a đ&ocirc;ng đầy sương m&ugrave;, cuối th&aacute;ng t&aacute;m 1921. Một h&agrave;ng xe tắc-xi nối đu&ocirc;i nhau đậu trước bến t&agrave;u. Mắt Meggie mở to ra, miệng h&aacute; hốc, đứng nh&igrave;n; chưa bao giờ c&ocirc; b&eacute; lại thấy lắm &ocirc; t&ocirc; tập trung lại một nơi như thế. Pađy dồn được cả nh&agrave; l&ecirc;n một chiếc tắc-xị T&agrave;i xế gợi &yacute; đưa họ đến kh&aacute;ch sạn Cung nh&acirc;n nh&acirc;n.</h2>\r\n\r\n<h2>- Nơi n&agrave;y th&iacute;ch hợp nhất cho gia đ&igrave;nh &ocirc;ng bạn - hắn giải th&iacute;ch với Pađỵ Đ&acirc;y l&agrave; một kh&aacute;ch sạn d&agrave;nh cho c&ocirc;ng nh&acirc;n do những người thuộc Đạo qu&acirc;n cứu thế quản l&yacute;.</h2>\r\n\r\n<h2>C&aacute;c con đường rộng đầy xe, chạy đủ hướng. C&oacute; rất &iacute;t ngựa. Ch&oacute;a mắt, họ nh&igrave;n ngắm những t&ograve;a nh&agrave; cao bằng gạch, ngạc nhi&ecirc;n về những bước đi tất bật của kh&aacute;ch bộ h&agrave;nh ở Wellington. Thủ đ&ocirc; T&acirc;y T&acirc;y Lan đ&atilde; để lại dấu ấn đối với họ nhưng so với Sydney th&igrave; n&oacute; chỉ như một thị trấn.</h2>\r\n\r\n<h2>Trong khi Fiona nghỉ ngơi ở một trong v&ocirc; số những ph&ograve;ng của kh&aacute;ch sạn được gọi một c&aacute;ch tr&igrave;u mến Cung nh&acirc;n d&acirc;n, Pađy đi đến nh&agrave; ga trung t&acirc;m để hỏi giờ t&agrave;u lửa đi Gillanbonẹ</h2>\r\n\r\n<h2>Ngay tối h&ocirc;m đ&oacute; họ lại l&ecirc;n t&agrave;u. Nếu kh&ocirc;ng đi ngay chuyến n&agrave;y họ phải nằm kh&aacute;ch sạn th&ecirc;m một tuần nữa mới c&oacute; t&agrave;u đi Gillanbonẹ Trước khi l&ecirc;n t&agrave;u, Pađy đ&atilde; đ&aacute;nh điện cho Mary Carson b&aacute;o trước tin họ sẽ đến v&agrave;o chiều mai.</h2>\r\n\r\n<h2>Pađy t&igrave;m được một ngăn trống tr&ecirc;n toa t&agrave;u hạng ba cho cả gia đ&igrave;nh. Những người l&ecirc;n sau gh&eacute; đầu v&agrave;o t&igrave;m chỗ đều kinh h&atilde;i bỏ đi khi thấy l&uacute;c nh&uacute;c trẻ con.</h2>\r\n\r\n<h2>S&aacute;ng h&ocirc;m sau, mở cho&agrave;ng mắt ra, họ kinh ngạc v&agrave; lo &acirc;u trước cảnh vật hết sức lạ l&ugrave;ng ngo&agrave;i sự tưởng tượng. H&oacute;a ra c&oacute; một nơi như thế n&agrave;y tr&ecirc;n tr&aacute;i đất hay sao? Vẫn l&agrave; những thung lũng nhỏ c&oacute; vẻ dịu d&agrave;ng như ở T&acirc;y T&acirc;y Lan nhưng kh&ocirc;ng c&oacute; ch&uacute;t g&igrave; gợi đến miền đất vừa bỏ đi. Chỉ c&oacute; m&agrave;u n&acirc;u v&agrave; m&agrave;u x&aacute;m, kẻ cả c&acirc;y l&aacute;! L&uacute;a m&igrave; m&ugrave;a đ&ocirc;ng đ&atilde; ngả sang m&agrave;u bạc pha v&agrave;ng hung dưới &aacute;nh nắng dữ dội của mặt trời. Fiona nh&igrave;n cảnh vật kh&ocirc;ng để lộ x&uacute;c động c&ograve;n Meggie th&igrave; nước mắt r&agrave;n rụa. Thật gh&ecirc; tởm, m&ecirc;nh m&ocirc;ng v&agrave; kh&ocirc;ng c&oacute; một dấu vết của m&agrave;u.</h2>\r\n\r\n<h2>Gillanbone xuất hiện l&uacute;c &aacute;nh mặt trời sắp tắt. Một sự tập hợp đẹp mắt những căn nh&agrave; gỗ với t&ocirc;n dợn s&oacute;ng đ&atilde; cũ. Hai b&ecirc;n con đường ch&iacute;nh đầy bụi bặm, kh&ocirc;ng c&oacute; c&acirc;y v&agrave; buồn tẻ. Những tia s&aacute;ng cuối c&ugrave;ng của mặt trời phủ l&ecirc;n một m&agrave;u v&agrave;ng h&agrave;o nh&aacute;ng, đem lại cho thị trấn c&aacute;i uy thế ngắn ngủi mờ dần. Gia đ&igrave;nh Cleary đứng tại nh&agrave; ga mở to mắt nh&igrave;n. Nơi đ&acirc;y l&agrave; một tập hợp d&acirc;n cư trưng ở ngo&agrave;i r&igrave;a cũng những v&ugrave;ng đất đai kh&ocirc; cằn. Nơi cuối c&ugrave;ng của một v&ugrave;ng c&ograve;n nhận được những trận mưa kh&ocirc;ng xa lắm về hướng T&acirc;y, bắt đầu ba ng&agrave;n c&acirc;y số đất sa mạc.</h2>\r\n\r\n<h2>Một chiếc &ocirc; t&ocirc; lộng lẫy đậu ở s&acirc;n ga, một linh mục tiến đến gần họ bằng những bước d&agrave;i m&agrave; kh&ocirc;ng cần ch&uacute; &yacute; đến lớp bụi d&agrave;y. Chiếc &aacute;o d&ograve;ng d&agrave;i gợi nhớ h&igrave;nh ảnh của qu&aacute; khứ; h&igrave;nh như &ocirc;ng kh&ocirc;ng đi tr&ecirc;n hai ch&acirc;n như một người b&igrave;nh thường, m&agrave; lướt nhẹ như trong một giấc mơ; xung quanh &ocirc;ng bụi tung l&ecirc;n từng đợt.</h2>\r\n\r\n<h2>- Xin mừng tất cả đ&atilde; đến b&igrave;nh y&ecirc;n, t&ocirc;i l&agrave; cha De Bricassart, vừa n&oacute;i &ocirc;ng vừa đưa tay ra bắt tay Pađỵ T&ocirc;i kh&ocirc;ng thể n&agrave;o lầm, &ocirc;ng đ&uacute;ng l&agrave; em trai của b&agrave; Mary, &ocirc;ng giống b&agrave; ấy như hai giọt nước (&ocirc;ng quay sang nắm tay Fiona đưa l&ecirc;n m&ocirc;i với một nụ cước hết sức ch&acirc;n th&agrave;nh. Hơn ai hết, cha Ralph nhận ra ngay, đ&oacute; l&agrave; một người phụ nữ đ&aacute;ng k&iacute;nh). B&agrave; rất đẹp - &ocirc;ng n&oacute;i c&acirc;u ấy như thể nhận x&eacute;t vừa rồi của &ocirc;ng l&agrave; chuyện tự nhi&ecirc;n đối với một linh mục, rồi &ocirc;ng đưa mắt về ph&iacute;a anh em gia đ&igrave;nh Cleary đang tập trung ở một g&oacute;c.</h2>\r\n\r\n<h2>Mắt &ocirc;ng dừng một l&uacute;c, ngạc nhi&ecirc;n nh&igrave;n Frank bế Hal tr&ecirc;n taỵ Ph&iacute;a sau đ&aacute;m con trai, Meggie đứng nh&igrave;n cha Ralph, miệng h&eacute; mở, tần ngần một ch&uacute;t như vừa chứng kiến sự xuất hiện của Ch&uacute;a. Kh&ocirc;ng quan t&acirc;m đến chiếc &aacute;o d&ograve;ng bằng vải mịn phết xuống đất, &ocirc;ng vượt qua đ&aacute;m con trai đến ngồi trước mặt Meggie đặt hai b&agrave;n tay chắc rắn l&ecirc;n tay c&ocirc; b&eacute; như dịu d&agrave;ng che chở:</h2>\r\n\r\n<h2>- Thế n&agrave;o! Con t&ecirc;n g&igrave;? Cha Ralph hỏi với nụ cười.</h2>\r\n\r\n<h2>- Meggie - c&ocirc; b&eacute; trả lời.</h2>\r\n\r\n<h2>- Em t&ocirc;i, t&ecirc;n Meghann - Frank xen v&agrave;o hai m&agrave;y nh&iacute;u lại tỏ vẻ bực m&igrave;nh con người dẹp trai v&agrave; cao lớn ấy.</h2>\r\n\r\n<h2>- Meghann l&agrave; c&aacute;i t&ecirc;n t&ocirc;i ưa th&iacute;ch (&ocirc;ng đứng dậy nhưng vẫn giữ tay Meggie trong tay m&igrave;nh). Tốt nhất l&agrave; c&aacute;c bạn đến nghỉ đ&ecirc;m tại nh&agrave; xứ, &ocirc;ng vừa n&oacute;i vừa dẫn Meggie đến xe &ocirc; tộ T&ocirc;i sẽ đưa c&aacute;c bạn đến Droghela s&aacute;ng mai; trang trại qu&aacute; xa n&ecirc;n sau một cuộc h&agrave;nh tr&igrave;nh d&agrave;i bằng t&agrave;u hỏa thế n&agrave;y, ch&uacute;ng ta chưa thể đi ngay được chiều naỵ</h2>\r\n\r\n<h2>Kh&aacute;ch sạn Ho&agrave;ng Gia, nh&agrave; thờ c&ocirc;ng gi&aacute;o, trường học của nh&agrave; thờ, tu viện v&agrave; nh&agrave; xứ l&agrave; những ng&ocirc;i nh&agrave; bằng gạch hiếm hoi ở Gillanbone; tất cả c&aacute;c nh&agrave; kh&aacute;c, kể cả trường lớn của thị x&atilde;, đều bằng gỗ.</h2>\r\n\r\n<h2>B&acirc;y giờ b&oacute;ng tối đ&atilde; phủ tr&ugrave;m, trời trở lạnh kh&ocirc;ng thể tưởng tượng; nhưng ph&ograve;ng kh&aacute;ch nh&agrave; xứ, ngọn lửa của l&ograve; sưởi đang ch&aacute;y v&agrave; từ một gian kế đ&oacute; một m&ugrave;i thơm dịu d&agrave;ng hấp dẫn thoang thoảng bay quạ B&agrave; quản gia người Scotland da nhăn nheo, miệng lu&ocirc;n n&oacute;i. Đ&atilde; quen với sự lạnh nhạt của c&aacute;c linh mục ở Wahine, gia đ&igrave;nh Cleary vẫn xa lạ trước vẻ hiền l&agrave;nh chất ph&aacute;c nổi bật ở cha Ralph. Chỉ c&oacute; Pađy l&agrave; chậm, kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n v&igrave; &ocirc;ng li&ecirc;n tưởng đến l&ograve;ng nh&acirc;n hậu của c&aacute;c tu sĩ v&ugrave;ng Galway qu&ecirc; hương của &ocirc;ng vẫn thường c&oacute; c&aacute;ch đối xử tốt với người ngh&egrave;o kh&oacute;. Với &ocirc;ng, t&ocirc;n gi&aacute;o l&agrave; hơi ấm v&agrave; niềm an ủi; c&ograve;n với những người kh&aacute;c trong gia đ&igrave;nh &ocirc;ng th&igrave; t&ocirc;n gi&aacute;o l&agrave; một kh&aacute;i niệm ăn s&acirc;u đầy sợ h&atilde;i, l&agrave; sự kh&eacute;p m&igrave;nh v&agrave;o một c&aacute;ch xử thế chệch ra ngo&agrave;i nếu muốn tr&aacute;nh bị đ&agrave;y xuống địa ngục.</h2>\r\n\r\n<h2>Khi mọi người đ&atilde; về ph&ograve;ng, cha Ralph ngả người thoải m&aacute;i trong chiếc ghế b&agrave;nh, mải m&ecirc; nh&igrave;n ngọn lửa, đốt một điếu thuốc v&agrave; mỉm cười. Trong đầu &ocirc;ng hiện lại đầy đủ h&igrave;nh ảnh gia đ&igrave;nh Cleary, đ&uacute;ng như lần đầu &ocirc;ng đ&atilde; gặp ở s&acirc;n gạ Người đ&agrave;n &ocirc;ng thật giống Mary Carson, nhưng lưng c&ograve;ng v&igrave; l&agrave;m lụng vất vả v&agrave; r&otilde; r&agrave;ng kh&ocirc;ng hề c&oacute; n&eacute;t th&acirc;m hiểm của người chị - Vợ &ocirc;ng ta, đẹp, mỏi mệt, tưởng như vừa bước xuống từ tr&ecirc;n chiếc xe sang trọng được k&eacute;o bởi những con ngựa trắng. Frank, cau c&oacute;, mắt đen, thật đen; c&aacute;c đứa con trai kh&aacute;c giống cha, ngoại trừa đứa nhỏ nhất giống mẹ. Stuart, thằng n&agrave;y lớn l&ecirc;n sẽ rất đẹp. Đứa b&eacute; c&ograve;n bồng kh&ocirc;ng thể biết n&oacute; sẽ ra sao, v&agrave; cuối c&ugrave;ng l&agrave; Meggiẹ</h2>\r\n\r\n<h2>Đ&acirc;y l&agrave; đứa con g&aacute;i đẹp v&agrave; đ&aacute;ng y&ecirc;u nhất m&agrave; &ocirc;ng chưa từng gặp. M&agrave;u t&oacute;c kh&ocirc;ng thể n&agrave;o m&ocirc; tả được, kh&ocirc;ng n&acirc;u m&agrave; cũng kh&ocirc;ng v&agrave;ng một sự h&ograve;a hợp tuyệt vời của hai m&agrave;u. C&ocirc; b&eacute; đ&atilde; nh&igrave;n &ocirc;ng bằng đ&ocirc;i mắt x&aacute;m &aacute;nh l&ecirc;n m&agrave;u bạc, con người trong suốt &oacute;ng &aacute;nh như những vi&ecirc;n ngọc. Nh&uacute;n vai, &ocirc;ng n&eacute;m điếu thuốc v&agrave;o l&ograve; sưởi v&agrave; đứng dậy.</h2>\r\n\r\n<h2>S&aacute;ng h&ocirc;m sau, &ocirc;ng đưa những người kh&aacute;ch đi Droghedạ Quen thuộc với cảnh vật dọc đường, bằng những nhận x&eacute;t của m&igrave;nh, &ocirc;ng g&acirc;y th&iacute;ch th&uacute; cho những người ngồi tr&ecirc;n xe. H&ocirc;m nay trời n&oacute;ng bức, như h&ocirc;m quạ</h2>\r\n\r\n<h2>Tất cả ngồi chiếc Daimler tiện nghi gấp bội t&agrave;u hỏa.</h2>\r\n\r\n<h2>- C&aacute;c con cừu ở đ&acirc;y sẫm qu&aacute;! Meggie nhận x&eacute;t buồn b&atilde;; &aacute;nh mắt mất h&uacute;t theo những chấm đỏ li ti m&agrave; người ta c&oacute; thể đo&aacute;n l&agrave; những c&aacute;i mũi đang ch&uacute;i xuống cỏ.</h2>\r\n\r\n<h2>- &agrave;, t&ocirc;i thấy đ&uacute;ng ra n&ecirc;n chọn T&acirc;y T&acirc;y Lan, linh mục n&oacute;i. Đ&oacute; l&agrave; một xứ giống như &aacute;i Nhĩ Lan v&agrave; nơi c&aacute;c con cừu đều c&oacute; một m&agrave;u trắng sữa đẹp.</h2>\r\n\r\n<h2>- V&acirc;ng, ở đ&acirc;y giống T&acirc;y T&acirc;y Lan nhiều thứ - Pađy tiếp lời - Ở đ&oacute; cũng c&oacute; một thứ cỏ xanh mướt, nhưng hoang d&atilde; hơn.</h2>\r\n\r\n<h2>Pađy c&oacute; vẻ cảm t&igrave;nh s&acirc;u đậm đối với cha Ralph. Đ&uacute;ng l&uacute;c đ&oacute;, những con đ&agrave; điểu tập hợp th&agrave;nh một đ&agrave;n đứng phắt chệnh choạng rồi bắt đầu chạy nhanh như gi&oacute;, ch&acirc;n cao lỏng khỏng rất kh&oacute; nh&igrave;n r&otilde;, cổ d&agrave;i thẳng l&ecirc;n. Bọn trẻ con n&iacute;n thở rồi cười rộ l&ecirc;n, th&iacute;ch th&uacute; được xem những con chim to lớn chỉ chạy chớ kh&ocirc;ng baỵ</h2>\r\n\r\n<h2>Sau những x&uacute;c động m&agrave; &uacute;c ch&acirc;u nhanh ch&oacute;ng mang đến, trang trại Drogheda xuất hiện, gợi cho họ nhớ đến một phần T&acirc;y T&acirc;y Lan với tiền sảnh của t&ograve;a nh&agrave; theo kiểu thời vua George, những c&acirc;y đậu chổi um t&ugrave;m v&agrave; h&agrave;ng ng&agrave;n c&acirc;y hồng.</h2>\r\n\r\n<h2>- M&igrave;nh sẽ ở đ&acirc;y &agrave;? Meggie hỏi.</h2>\r\n\r\n<h2>- Kh&ocirc;ng ho&agrave;n to&agrave;n đ&uacute;ng như thế - Linh mục l&ecirc;n tiếng rất mau - Ng&ocirc;i nh&agrave; m&agrave; c&aacute;c bạn đến ở c&aacute;ch đ&acirc;y một kilomet rưỡi, ph&iacute;a dưới thấp một ch&uacute;t, cạnh b&ecirc;n con suối.</h2>\r\n\r\n<h2>Mary Carson chờ họ trong ph&ograve;ng kh&aacute;ch rộng th&ecirc;nh thang. B&agrave; kh&ocirc;ng c&oacute; một động t&aacute;c n&agrave;o để đ&oacute;n em trai m&igrave;nh; Pađy phải đến chỗ b&agrave; đang ngồi.</h2>\r\n\r\n<h2>- Thế n&agrave;o, Pađỷ B&agrave; hỏi nhỏ nhẹ nhưng mắt b&agrave; liếc nh&igrave;n qua ph&iacute;a cha Ralph đang bế Meggie tr&ecirc;n tay, hai c&aacute;nh tay c&ocirc; b&eacute; cho&agrave;ng lấy cổ chạ Mary Carson đứng dậy một c&aacute;ch nặng nề, kh&ocirc;ng ch&agrave;o Fiona hoặc bất cứ ai.</h2>\r\n\r\n<h2>- Ch&uacute;ng ta sẽ xem lễ tức thời, b&agrave; n&oacute;i. T&ocirc;i biết chắc cha Ralph rất n&oacute;ng l&ograve;ng đi ngaỵ</h2>\r\n\r\n<h2>- Kh&ocirc;ng đ&acirc;u b&agrave; Mary th&acirc;n mến (&ocirc;ng cười, mắt long lanh). T&ocirc;i sẽ l&agrave;m lễ, rồi tất cả ch&uacute;ng ta sẽ d&ugrave;ng chung một buổi cơm trưa ngon miệng v&agrave; sau đ&oacute; t&ocirc;i sẽ chỉ cho Meggie c&aacute;i nh&agrave; sẽ ở như t&ocirc;i đ&atilde; hứa với c&ocirc; b&eacute;.</h2>\r\n\r\n<h2>- Meggiẻ Mary Carson ngạc nhi&ecirc;n.</h2>\r\n\r\n<h2>- V&acirc;ng, đ&acirc;y l&agrave; Meggiẹ Nhưng ch&uacute;ng ta đ&atilde; l&agrave;m quen kh&ocirc;ng đ&uacute;ng tr&igrave;nh tự của n&oacute;, t&ocirc;i xin giới thiệu cho đ&uacute;ng. B&agrave; Mary, t&ocirc;i xin giới thiệu Fionạ</h2>\r\n\r\n<h2>Mary Carson b&agrave;y tỏ sự đồng &yacute; bằng một c&aacute;i gật đầu gọn lỏn v&agrave; sau đ&oacute; kh&ocirc;ng ch&uacute; &yacute; đến mấy cậu con trai m&agrave; cha Ralph lần lượt gọi t&ecirc;n. B&agrave; qu&aacute; chăm ch&uacute; quan s&aacute;t linh mục v&agrave; Meggiẹ</h2>\r\n\r\n<h2>Nơi ở d&agrave;nh cho người quản l&yacute; được x&acirc;y theo kiểu nh&agrave; s&agrave;n b&ecirc;n cạnh con suối hẹp khoảng mười m&eacute;t với h&agrave;ng bạch đ&agrave;n hai b&ecirc;n bờ. So với sự nguy nga của t&ograve;a nh&agrave; b&agrave; Mary Carson, ng&ocirc;i nh&agrave; c&oacute; vẻ trơ trọi nhưng n&oacute; cũng đủ những tiện nghi như căn nh&agrave; ở T&acirc;y T&acirc;y Lan. Đồ đạc b&agrave;y biện trong nh&agrave; theo kiểu thời Victoria, b&ecirc;n tr&ecirc;n phủ một lớp bụi mỏng.</h2>\r\n\r\n<h2>- C&aacute;c bạn rất may mắn, c&oacute; một ph&ograve;ng tắm trong nh&agrave;, linh mục dẫn đường ph&iacute;a trước vừa đi vừa giải th&iacute;ch. Nh&agrave; x&acirc;y tr&ecirc;n c&aacute;c cọc kh&aacute; cao v&igrave; v&agrave;o m&ugrave;a c&ograve;n suối lớn ra đột ngột, mực nước l&ecirc;n rất cao, t&ocirc;i nghe n&oacute;i, c&oacute; lần l&ecirc;n cao đến 17 m&eacute;t trong một đ&ecirc;m.</h2>\r\n\r\n<h2>- Người ở nh&agrave; n&agrave;y trước đ&acirc;y kh&ocirc;ng được sạch sẽ lắm - Fiona nhận x&eacute;t mấy ng&oacute;n tay chạm v&agrave;o lớp bụi phủ tr&ecirc;n tủ bu&yacute;t-phệ</h2>\r\n\r\n<h2>Cha Ralph cười to l&ecirc;n.</h2>\r\n\r\n<h2>- Đừng c&oacute; mong tẩy sạch lớp bụi n&agrave;y - &ocirc;ng giải th&iacute;ch - Nơi ch&uacute;ng ta ở thuộc v&ugrave;ng b&ecirc;n trong v&agrave; c&oacute; ba thứ m&agrave; b&agrave; sẽ phải chịu: sự n&oacute;ng bức, bụi v&agrave; ruồi. D&ugrave; b&agrave; c&oacute; l&agrave;m g&igrave; đi nữa cũng kh&ocirc;ng đổi kh&aacute;c được.</h2>\r\n\r\n<h2>- Cha rất tốt đối với ch&uacute;ng t&ocirc;i - Fiona nh&igrave;n linh mục n&oacute;i.</h2>\r\n\r\n<h2>- Cũng l&agrave; chuyện b&igrave;nh thường th&ocirc;i, hai &ocirc;ng b&agrave; l&agrave; những b&agrave; con duy nhất của người bạn tốt của t&ocirc;i, b&agrave; Mary Carson.</h2>\r\n\r\n<h2>Kh&ocirc;ng để x&uacute;c động, Fiona nh&uacute;n vai.</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng quen duy tr&igrave; những quan hệ th&acirc;n thuộc với c&aacute;c linh mục. C&aacute;c linh mục ở T&acirc;y T&acirc;y Lan kh&ocirc;ng can dự v&agrave;o việc ri&ecirc;ng của c&aacute;c con chi&ecirc;n.</h2>\r\n\r\n<h2>- B&agrave; kh&ocirc;ng phải l&agrave; người theo đạo c&ocirc;ng gi&aacute;o?</h2>\r\n\r\n<h2>- Pađy theo đạo c&ocirc;ng gi&aacute;o - Dĩ nhi&ecirc;n, con c&aacute;i cũng được dạy dỗ theo t&ocirc;n gi&aacute;o của anh ấy. Tất cả, cho đến đứa cuối c&ugrave;ng...</h2>\r\n\r\n<h2>- C&oacute; phải v&igrave; thế m&agrave; b&agrave; kh&ocirc;ng vừa l&ograve;ng?</h2>\r\n\r\n<h2>- Với t&ocirc;i ho&agrave;n to&agrave;n kh&ocirc;ng th&agrave;nh vấn đề.</h2>\r\n\r\n<h2>- B&agrave; đ&atilde; được.</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng phải l&agrave; kẻ đạo đức giả, thưa cha De Bricassart. T&ocirc;i đ&atilde; mất đức tin đối với ch&iacute;nh t&ocirc;n gi&aacute;o của t&ocirc;i v&agrave; t&ocirc;i kh&ocirc;ng mong muốn theo một t&ocirc;n gi&aacute;o kh&aacute;c cũng ho&agrave;n to&agrave;n v&ocirc; nghĩa đối với t&ocirc;i.</h2>\r\n\r\n<h2>- T&ocirc;i hiểu.</h2>\r\n\r\n<h2>Cha Ralph quan s&aacute;t Meggie đang đứng ngo&agrave;i hi&ecirc;n trước nh&agrave; v&agrave; nh&igrave;n ra con đường dẫn đến t&ograve;a nh&agrave; lớn Droghelạ</h2>\r\n\r\n<h2>- Con g&aacute;i của b&agrave; đẹp qu&aacute;. &ocirc;ng lại n&oacute;i. T&ocirc;i vẫn đặc biệt th&iacute;ch m&aacute;i t&oacute;c m&agrave;u v&agrave;n vơnidợ M&agrave;u t&oacute;c ấy của c&ocirc; b&eacute; sẽ khiến ngay cả danh họa Le Titien cũng chạy t&igrave;m b&uacute;t vẽ. T&ocirc;i chưa từng thấy m&agrave;u t&oacute;c như thế bao giờ, c&oacute; phải đ&oacute; l&agrave; con g&aacute;i duy nhất của b&agrave;?</h2>\r\n\r\n<h2>- V&acirc;ng. Cứ to&agrave;n l&agrave; con trai, trong gia đ&igrave;nh của Pađy v&agrave; của t&ocirc;i cũng thế. Con g&aacute;i rất hiếm.</h2>\r\n\r\n<h2>- Tội nghiệp c&ocirc; b&eacute;, &ocirc;ng n&oacute;i thầm, nhưng chẳng hiểu tại sao.</h2>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"height:260px; width:86%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top\">\r\n			<h2><strong>Chương 6</strong></h2>\r\n\r\n			<h2>&nbsp;</h2>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>L&uacute;c đầu, những con số l&agrave;m cho c&aacute;c th&agrave;nh vi&ecirc;n của gia đ&igrave;nh Cleary sững sờ. Drogheda c&oacute; một diện t&iacute;ch cả trăm ng&agrave;n hectạ Đường ranh d&agrave;i nhất c&oacute; tới 130 kilomet. S&aacute;u mươi lăm kilomet v&agrave; 27 c&aacute;i cổng r&agrave;o ngăn c&aacute;ch căn nh&agrave; ch&iacute;nh với Gillabone, điểm d&acirc;n cư duy nhất nằm trong v&ograve;ng 150 kilomet.</h2>\r\n\r\n<h2>Pađy v&agrave; c&aacute;c con trai như ở tr&ecirc;n m&acirc;y. C&oacute; khi họ ở tr&ecirc;n lưng ngựa nhiều ng&agrave;y liền c&aacute;ch xa nh&agrave; nhiều kilomet. Buổi tối, họ cắm trại dười bầu trời đầy sao khiến cho họ thỉnh thoảng tưởng như m&igrave;nh đ&atilde; h&ograve;a nhập c&ugrave;ng Ch&uacute;a.</h2>\r\n\r\n<h2>Nhưng phụ nữ ở c&aacute;i xứ n&agrave;y lại bị buộc ch&acirc;n ở nh&agrave;, họ thấy cuộc đời kh&ocirc;ng c&oacute; g&igrave; th&iacute;ch th&uacute;. Họ kh&ocirc;ng c&oacute; điều kiện giải tr&iacute; v&agrave; cả l&yacute; do để leo ngồi l&ecirc;n lưng ngựa. Vẫn l&agrave; những c&ocirc;ng việc nh&agrave;m ch&aacute;n, dai dẳng như nấu nướng, giặt đồ, qu&aacute;t lau, ủi quần &aacute;o, lo cho con nhỏ... Họ phải chịu đựng c&aacute;i n&oacute;ng bức, bụi bặm, lu&ocirc;n thiếu người đ&agrave;n &ocirc;ng để chở v&agrave; bửa củi, bơm nước, mổ g&agrave;. Trời n&oacute;ng bức đến nghẹt thở, d&ugrave; rằng c&ograve;n đang ở đầu m&ugrave;a xu&acirc;n, h&agrave;n thử biểu trong m&aacute;t l&ecirc;n đến 30 độ Trong nh&agrave; bếp nhiệt độ l&ecirc;n đến 47.</h2>\r\n\r\n<h2>Quần &aacute;o mặc nhiều lớp, may b&oacute; chặt, trước đ&acirc;y để sử dụng ở T&acirc;y T&acirc;y Lan, nơi m&agrave; nh&agrave; lu&ocirc;n m&aacute;t lạnh. Mary Carson đến thăm căn nh&agrave; của c&ocirc; em d&acirc;u m&igrave;nh với mục đ&iacute;ch quan s&aacute;t coi c&oacute; được vệ sinh kh&ocirc;ng. Chiếc &aacute;o d&agrave;i vải tr&uacute;c b&acirc;u m&agrave; Fiona mặc b&oacute; s&aacute;t l&ecirc;n đến cổ, đường viền ph&iacute;a sau phết dưới đất. Trong khi Mary Carson th&igrave; mặc theo mốt mới. Chiếc &aacute;o xoa m&agrave;u kem chỉ d&agrave;i đến bắp ch&acirc;n, tay rộng, th&acirc;n kh&ocirc;ng b&oacute; v&agrave; cổ hở thấp.</h2>\r\n\r\n<h2>- Fiona ơi, c&ocirc; thật l&agrave; cũ r&iacute;ch - Mary Carson nhận x&eacute;t.</h2>\r\n\r\n<h2>B&agrave; ta liếc nh&igrave;n ph&ograve;ng kh&aacute;ch, c&aacute;c bức tường được sơn lại m&agrave;u kem; s&agrave;n nh&agrave; l&oacute;t bằng những tấm thảm Ba Tư, đồ đạc b&agrave;y biện đắt tiền v&agrave; tinh tế, khiến cho b&agrave; phải ch&uacute; &yacute;.</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng c&oacute; th&igrave; giờ để c&oacute; thể l&agrave;m kh&aacute;c hơn - Fiona trả lời bằng giọng kh&ocirc; khan.</h2>\r\n\r\n<h2>- C&ocirc; c&oacute; nhiều thời giờ hơn ở đ&acirc;y, v&igrave; đ&agrave;n &ocirc;ng thường vắng mặt. H&atilde;y cắt ngắn chiếc &aacute;o lại, vất đi những c&aacute;i v&aacute;y v&agrave; cả coocx&ecirc;, nếu kh&ocirc;ng c&ocirc; sẽ chết ngạt thở khi m&ugrave;a h&egrave; đến. Nhiệt độ c&oacute; thể tăng l&ecirc;n khoảng một chục độ, c&ocirc; c&oacute; biết kh&ocirc;ng? Mắt b&agrave; Mary dừng lại bức ch&acirc;n dung của một phụ nữ đẹp, t&oacute;c v&agrave;ng, ăn mặc mốt của Nữ ho&agrave;ng Eng&eacute;nie - Ai đ&acirc;y? - B&agrave; vừa hỏi vừa đưa tay chỉ về bức vẽ.</h2>\r\n\r\n<h2>- B&agrave; ngoại t&ocirc;i.</h2>\r\n\r\n<h2>- Ồ, thật &agrave;? C&ograve;n b&agrave;n ghế, c&aacute;c tấm thảm n&agrave;y ở đ&acirc;u rả</h2>\r\n\r\n<h2>- Của b&agrave; ngoại t&ocirc;i.</h2>\r\n\r\n<h2>- Ồ, thật &agrave;? Fiona th&acirc;n mến của t&ocirc;i, h&igrave;nh như c&ocirc; vỡ mộng?</h2>\r\n\r\n<h2>Fiona vẫn b&igrave;nh tĩnh, hai m&ocirc;i m&iacute;m lại.</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng nghĩ như vậy. T&ocirc;i đ&atilde; lấy được một người chồng đ&ocirc;n hậu, chị cũng cần biết điều đ&oacute;.</h2>\r\n\r\n<h2>- Nhưng kh&ocirc;ng c&oacute; một đồng xụ Họ ri&ecirc;ng của em l&agrave; g&igrave;?</h2>\r\n\r\n<h2>- Armstrong.</h2>\r\n\r\n<h2>- Ồ, thật &agrave;? C&oacute; phải l&agrave; b&agrave; con ph&iacute;a Roderick Armstrong?</h2>\r\n\r\n<h2>- Roderick l&agrave; t&ecirc;n của anh cả t&ocirc;i.</h2>\r\n\r\n<h2>Mary Carson đứng dậy, tay chạm mạnh v&agrave;o chiếc n&oacute;n rộng v&agrave;nh để xua đuổi những con ruồi kh&ocirc;ng biết ki&ecirc;ng nể bất cứ ai.</h2>\r\n\r\n<h2>- Quả thật, c&ocirc; sinh ra trong một gia đ&igrave;nh kh&aacute; hơn d&ograve;ng họ Cleary, t&ocirc;i phải nh&igrave;n nhận điều n&agrave;y. C&ocirc; y&ecirc;u Pađy đến mức n&agrave;o m&agrave; bỏ tất cả như thế?</h2>\r\n\r\n<h2>- Điều đ&oacute; chỉ li&ecirc;n quan đến t&ocirc;i - giọng Fiona n&oacute;i đều đều - N&oacute;i kh&ocirc;ng li&ecirc;n quan đến chị. T&ocirc;i tr&aacute;nh n&oacute;i về chồng t&ocirc;i, kẻ cả với chị ruột anh ấy.</h2>\r\n\r\n<h2>Nếp nhăn hai b&ecirc;n mũi Mary Carson c&agrave;ng r&otilde; n&eacute;t, b&agrave; trố mắt.</h2>\r\n\r\n<h2>- C&ocirc; dễ bị tự &aacute;i qu&aacute;.</h2>\r\n\r\n<h2>Sau n&agrave;y Mary Carson kh&ocirc;ng đến nh&agrave; em d&acirc;u nữa nhưng b&agrave; Smith, quản gia của Mary Carson lại đến thường xuy&ecirc;n v&agrave; khuy&ecirc;n Fiona n&ecirc;n đổi thay c&aacute;ch ăn mặc.</h2>\r\n\r\n<h2>Trước khi chia tay, Mary Carson n&oacute;i:</h2>\r\n\r\n<h2>- Trong ph&ograve;ng t&ocirc;i c&oacute; một chiếc m&aacute;y may &iacute;t khi sử dụng. T&ocirc;i sẽ bảo hai người khi&ecirc;ng qua cho cộ Khi n&agrave;o t&ocirc;i cần, t&ocirc;i đến đ&acirc;y sử dụng (Mắt của b&agrave; nh&igrave;n về ph&iacute;a b&eacute; Hal đang nằm chơi tr&ecirc;n s&agrave;n nh&agrave;). T&ocirc;i rất th&iacute;ch nghe tiếng trẻ con l&iacute;u lo, b&agrave; Cleary ạ.</h2>\r\n\r\n<h2>Meggie sống kh&aacute; c&ocirc; độc. Bọn con trai chỉ c&ograve;n lại c&oacute; Stuart ở nh&agrave;. Jack v&agrave; Hughie theo cha để học nghề chăn cừu, Stuart kh&ocirc;ng c&oacute; nhiều th&igrave; giờ ở b&ecirc;n cạnh Meggie như những anh trai kh&aacute;c. Cậu ta sống với thế giới ri&ecirc;ng vốn kh&ocirc;ng ồn &agrave;o, th&iacute;ch ngồi h&agrave;ng giờ quan s&aacute;t đ&agrave;n kiến nối đu&ocirc;i nhau hơn l&agrave; leo tr&egrave;o tr&ecirc;n c&acirc;y, tr&ograve; chơi m&agrave; Meggie rất th&iacute;ch, nhất l&agrave; tr&egrave;o tr&ecirc;n những c&acirc;y bạch đ&agrave;n &uacute;c ch&acirc;u đẹp tuyệt.</h2>\r\n\r\n<h2>Tuy nhi&ecirc;n Meggie v&agrave; Stuart kh&ocirc;ng phải l&uacute;c n&agrave;o cũng c&oacute; th&igrave; giờ dư dả để nh&igrave;n kiến v&agrave; leo c&acirc;y, cả hai phải l&agrave;m việc rất dữ: bửa củi v&agrave; khi&ecirc;ng củi, đ&agrave;o hố r&aacute;c, trồng rau trong vườn, chăm s&oacute;c g&agrave; vịt v&agrave; heo. Cả hai cũng được học được biết c&aacute;ch giết rắn mặc d&ugrave; vẫn rất sợ ch&uacute;ng.</h2>\r\n\r\n<h2>Mấy năm gần đ&acirc;y mưa rất &iacute;t, suối cạn nước, những bồn chứa chỉ c&ograve;n ph&acirc;n nửa. Cỏ c&ograve;n đẹp nhưng kh&ocirc;ng thể so s&aacute;nh với l&uacute;c hoa l&aacute; xanh tươi.</h2>\r\n\r\n<h2>- C&oacute; lẽ t&igrave;nh h&igrave;nh sẽ tệ hơn - Mary Carson n&oacute;i với giọng ch&aacute;n nản.</h2>\r\n\r\n<h2>Những người d&acirc;n ở đ&acirc;y phải trải qua nạn lụt trước khi đối đầu với nạn hạn h&aacute;n dữ dội. V&agrave;o giữa th&aacute;ng gi&ecirc;ng, c&aacute;i gi&oacute; m&ugrave;a hướng Bắc - T&acirc;y thổi qua v&ugrave;ng n&agrave;y. Những ngọn gi&oacute; lớn đổi hướng bất thường mặc sức ho&agrave;nh h&agrave;nh. C&oacute; khi chỉ những điểm xa nhất ph&iacute;a Bắc của lục địa chịu những trận mưa h&egrave; như th&aacute;c; c&oacute; khi, những trận mưa ấy tr&agrave;n xuống ph&iacute;a dưới, l&agrave;m cho m&ugrave;a h&egrave; trở n&ecirc;n ẩm ướt. Năm đ&oacute;, v&agrave;o th&aacute;ng gi&ecirc;ng, m&acirc;y đen nghịt bị những cơn gi&oacute; x&eacute; toạc ra từng mảnh. Mưa bắt đầu đổ xuống, hồng thủy, trời đất gầm r&uacute; li&ecirc;n tục.</h2>\r\n\r\n<h2>- Gi&oacute; m&ugrave;a tới. Blue Williams, người chở mướn qua nhiều v&ugrave;ng gh&eacute; lại Droghela, vừa vấn thuốc h&uacute;t vừa n&oacute;i - S&ocirc;ng Cooper, s&ocirc;ng Barcoo, v&agrave; s&ocirc;ng Diamantia ph&igrave;nh to ra, c&ograve;n s&ocirc;ng Overflow vỡ bờ. Cả v&ugrave;ng b&ecirc;n trong Queensland sẽ ngập dưới 60 centimet nước; phải rất kh&oacute; khăn nếu muốn t&igrave;m ra một nơi cao để bảo vệ đ&agrave;n cừu.</h2>\r\n\r\n<h2>Bỗng chốc mưa đến. Pađy v&agrave; c&aacute;c con trai l&agrave;m việc thật vất vả để đưa c&aacute;c đ&agrave;n cừu rời khỏi những v&ograve;ng r&agrave;o ph&iacute;a dưới. Cha Ralph đến tiếp tay rất nhiệt t&igrave;nh. &ocirc;ng thắng y&ecirc;n ngựa v&agrave; đi c&ugrave;ng Frank, với những đ&agrave;n ch&oacute; săn giỏi nhất để di chuyển đ&agrave;n s&uacute;c vật về những khu đất r&agrave;o k&iacute;n gần r&igrave;a con s&ocirc;ng Barwon. Trong khi đ&oacute; Pađy v&agrave; hai người l&agrave;m c&ocirc;ng, mỗi người c&oacute; một cậu con trai đi theo lại chạy về hướng kh&aacute;c.</h2>\r\n\r\n<h2>Cha Ralph tr&ocirc;ng chẳng kh&aacute;c một nh&agrave; chăn nu&ocirc;i s&agrave;nh sỏi. &ocirc;ng cỡi con ngựa sắc hồng thuần chủng - qu&agrave; tặng của b&agrave; Mary Carson - mặc quần da, ch&acirc;n mang gi&agrave;y bốt b&oacute;ng lo&aacute;ng, th&acirc;n người chắc nịch b&oacute; s&aacute;t trong chiếc &aacute;o m&agrave;u trắng tinh, tay &aacute;o sắn l&ecirc;n cao, cổ &aacute;o mở rộng để lộ ra phần ngực r&aacute;m nắng. C&ograve;n Frank th&igrave; mặc quần d&agrave;i bạc m&agrave;u, ở ph&iacute;a dưới đầu gối được b&oacute; lại bằng những mảnh da cănguru, người anh mặc một chiếc &aacute;o ngắn s&aacute;t n&aacute;ch bằng nỉ mỏng m&agrave;u x&aacute;m, khiến Frank trở n&ecirc;n ngh&egrave;o n&agrave;n b&ecirc;n cạnh cha Ralph.</h2>\r\n\r\n<h2>Quả thật l&agrave; thế - Frank, nhếch m&eacute;p nghĩ thầm - nh&igrave;n theo c&aacute;i d&aacute;ng người thẳng của cha Ralph tr&ecirc;n lưng con ngựa c&aacute;i nhanh nhẹn xuy&ecirc;n qua c&acirc;y th&ocirc;ng v&agrave; ho&agrave;ng dương b&ecirc;n kia con suối nhỏ. Con ngựa của cha Ralph l&ocirc;ng đốm, một con vật t&iacute;nh kh&iacute; dữ dằn, bướng bỉnh, lu&ocirc;n tỏ ra căm gh&eacute;t đồng loại. Được k&iacute;ch động, bầy ch&oacute; chạy tứ tung, sủa loạn l&ecirc;n v&agrave; cắn lẫn nhau. Răng nanh của ch&uacute;ng nhe ra bắt buộc cha Ralph phải vung những đường roi để trấn &aacute;p. Kh&ocirc;ng c&oacute; g&igrave; l&agrave; kh&ocirc;ng thể l&agrave;m được ở con người n&agrave;y. &ocirc;ng biết cả hu&yacute;t gi&oacute; để ra lệnh đ&agrave;n ch&oacute; trở lại nhiệm vụ v&agrave; sử dụng ngọn roi kh&eacute;o l&eacute;o hơn cả Frank.</h2>\r\n\r\n<h2>Khi trời nh&aacute; nhem tối, cha Ralph v&agrave; bầy ch&oacute;, với sự tiếp sức kh&ocirc;ng c&oacute; hiệu quả mấy của Frank đ&atilde; đưa được đ&agrave;n cừu đến b&atilde;i đất thứ nhất, thường th&igrave; c&ocirc;ng việc ấy phải mất nhiều ng&agrave;y. Linh mục vừa th&aacute;o y&ecirc;n ngựa ở một l&ugrave;m c&acirc;y gần b&atilde;i đất thứ hai, h&agrave;o hứng cho rằng họ c&oacute; thể lập lại th&agrave;nh t&iacute;ch đưa đ&agrave;n cừu đến một b&atilde;i kh&aacute;c trước khi trận mưa &agrave;o tới. Mấy con ch&oacute; nằm tr&ecirc;n cỏ, lưỡi th&ograve;ng ra, t&igrave;m c&aacute;ch ve v&atilde;n cha Ralph. Frank k&eacute;o ra từ trong t&uacute;i tr&ecirc;n lưng ngựa những miếng thịt cănguru bầy nhầy v&agrave; quẳng cho n&oacute;. Ch&uacute;ng cắn nhau gi&agrave;nh giật.</h2>\r\n\r\n<h2>- Đồ s&uacute;c vật! Frank n&oacute;i lầm bầm. Ch&uacute;ng n&oacute; kh&ocirc;ng như lo&agrave;i ch&oacute; nh&agrave;. Đ&uacute;ng l&agrave; những con ch&oacute; rừng.</h2>\r\n\r\n<h2>- Theo t&ocirc;i nghĩ c&oacute; lẽ ch&uacute;ng gần gũi với điều Ch&uacute;a muốn hơn so với ch&oacute; nu&ocirc;i trong nh&agrave;. Cha Ralph n&oacute;i lại nhẹ nh&agrave;ng. Nhanh nhẹn, th&ocirc;ng minh, hung hăng, phần n&agrave;o thuần h&oacute;a. T&ocirc;i vẫn th&iacute;ch ch&uacute;ng hơn l&agrave; ch&oacute; nu&ocirc;i l&agrave;m bạn. M&egrave;o cũng thế - Anh c&oacute; nhận thấy, những con m&egrave;o lảng vảng xung quanh nh&agrave;? Hoang dại v&agrave; hung dữ kh&ocirc;ng thua những con beo; ch&uacute;ng kh&ocirc;ng cho người đến gần. Nhưng ch&uacute;ng săn chuột tuyệt vời v&agrave; kh&ocirc;ng cần ai nu&ocirc;i.</h2>\r\n\r\n<h2>Cha Ralph lấy ra từ trong t&uacute;i đặt tr&ecirc;n lưng ngựa một miếng thịt cừu đ&ocirc;ng lạnh v&agrave; một g&oacute;i b&aacute;nh m&igrave;, bợ Cắt một miếng thịt trao cho Frank, &ocirc;ng để b&aacute;nh m&igrave; v&agrave; bơ giữa hai người, tr&ecirc;n một kh&uacute;c c&acirc;y đốn ng&atilde;. &ocirc;ng cắn v&agrave;o miếng thịt ngon l&agrave;nh. Cả hai c&ugrave;ng uống nước trong một t&uacute;i da v&agrave; vấn thuốc h&uacute;t.</h2>\r\n\r\n<h2>- N&agrave;y Frank, h&igrave;nh như anh kh&ocirc;ng vui?</h2>\r\n\r\n<h2>Cha Ralph hỏi với một tiếng thở ra, tay vấn điếu thuốc thứ hai.</h2>\r\n\r\n<h2>Frank ngồi c&aacute;ch linh mục khoảng một m&eacute;t rưỡi; anh quay phắt đầu lại, nh&igrave;n cha Ralph với vẻ d&ograve; x&eacute;t.</h2>\r\n\r\n<h2>- Hạnh ph&uacute;c l&agrave; thế n&agrave;o?</h2>\r\n\r\n<h2>- Anh c&oacute; thể t&igrave;m ra một th&iacute; dụ. Cha v&agrave; c&aacute;c em của anh đều rất hạnh ph&uacute;c, &iacute;t ra l&uacute;c n&agrave;y. Nhưng anh th&igrave; lại kh&aacute;c. Mẹ anh cũng thế v&agrave; cả em g&aacute;i Meggie của anh đều kh&ocirc;ng th&iacute;ch &uacute;c ch&acirc;u?</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng th&iacute;ch v&ugrave;ng n&agrave;y. T&ocirc;i muốn đi Sydney; ở đ&oacute; t&ocirc;i hy vọng c&oacute; lối tho&aacute;t.</h2>\r\n\r\n<h2>- Sydneỵ Đ&oacute; l&agrave; một nơi dễ sa ng&atilde; - Cha Ralph cười n&oacute;i.</h2>\r\n\r\n<h2>- T&ocirc;i c&oacute;c cần! Ở đ&acirc;y t&ocirc;i cũng lại bị kẹt cứng như ở T&acirc;y T&acirc;y Lan. T&ocirc;i muốn trốn khỏi &ocirc;ng ấy.</h2>\r\n\r\n<h2>- &ocirc;ng ấy?</h2>\r\n\r\n<h2>Frank n&oacute;i một c&aacute;ch đột ngột v&agrave; từ chối n&oacute;i th&ecirc;m. Nằm ngửa, Frank ngước mắt nh&igrave;n l&ecirc;n t&aacute;n c&acirc;y.</h2>\r\n\r\n<h2>- Anh bao nhi&ecirc;u tuổi, Frank?</h2>\r\n\r\n<h2>- Hai mươi hai.</h2>\r\n\r\n<h2>- &agrave;, tức nhi&ecirc;n! C&oacute; lần n&agrave;o anh xa gia đ&igrave;nh chưa?</h2>\r\n\r\n<h2>- Chưa.</h2>\r\n\r\n<h2>- Anh c&oacute; đi khi&ecirc;u vũ lần n&agrave;o chưa? Chắc đ&atilde; c&oacute;?</h2>\r\n\r\n<h2>- Chưa.</h2>\r\n\r\n<h2>- Thế th&igrave; &ocirc;ng ấy sẽ kh&ocirc;ng giữ được anh l&acirc;u.</h2>\r\n\r\n<h2>- &ocirc;ng ta sẽ giữ t&ocirc;i đến chết.</h2>\r\n\r\n<h2>Cha Ralph ng&aacute;p d&agrave;i v&agrave; chuẩn bị ngủ.</h2>\r\n\r\n<h2>- Ch&uacute;c ngủ ngon - &ocirc;ng n&oacute;i trong miệng.</h2>\r\n\r\n<h2>H&ocirc;m sau d&ugrave; m&acirc;y vần vũ nhưng trời vẫn chưa mưa v&agrave; hai người đ&atilde; giải tỏa được b&atilde;i chăn thứ hai.</h2>\r\n\r\n<h2>Mưa bắt đầu đổ xuống v&agrave; buổi chiều l&uacute;c Frank v&agrave; linh mục phi ngựa đến chỗ cạn của đoạn suối gần nh&agrave;.</h2>\r\n\r\n<h2>Nhưng khi qua b&ecirc;n kia bờ th&igrave; người v&agrave; ngựa kh&ocirc;ng thể l&ecirc;n c&ugrave;ng l&uacute;c v&igrave; dốc đứng v&agrave; trơn trợt. Họ phải để ngựa l&ecirc;n trước, cả hai c&ograve;n lại leo l&ecirc;n tuột xuống mấy lượt.</h2>\r\n\r\n<h2>Nhờ thấy hai con ngựa kh&ocirc;ng c&oacute; chủ về nh&agrave;, Pađy đo&aacute;n ra chuyện kh&ocirc;ng hay của hai người. &ocirc;ng đến b&ecirc;n bờ suối với d&acirc;y thừng k&eacute;o cha Ralph v&agrave; Frank l&ecirc;n.</h2>\r\n\r\n<h2>Pađy mời cha v&agrave;o nh&agrave; thay quần &aacute;o ấm nhưng cha cười lắc đầu từ chối.</h2>\r\n\r\n<h2>- Mọi người đang chờ t&ocirc;i ở Nh&agrave; Lớn - &ocirc;ng n&oacute;i. Mary Carson nghe tiếng cha Ralph gọi cửa trước khi đ&aacute;m gia nh&acirc;n nghe thấy. &ocirc;ng đ&atilde; đi v&ograve;ng ra ph&iacute;a trước t&ograve;a nh&agrave; dự t&iacute;nh đi thẳng v&agrave;o ph&ograve;ng.</h2>\r\n\r\n<h2>- Cha kh&ocirc;ng thể v&agrave;o nh&agrave; trong t&igrave;nh trạng n&agrave;y, người lấm b&ecirc; lấm bết - B&agrave; Mary Carson đứng trong hi&ecirc;n n&oacute;i với Ralph.</h2>\r\n\r\n<h2>- Vậy xin b&agrave; đưa gi&uacute;p t&ocirc;i mấy c&aacute;i khăn v&agrave; chiếc valị</h2>\r\n\r\n<h2>Kh&ocirc;ng ch&uacute;t ngượng nghịu, b&agrave; quan s&aacute;t cha Ralph trong khi &ocirc;ng cởi &aacute;o, cởi gi&agrave;y bốt. &ocirc;ng tựa v&agrave;o cửa sổ ph&ograve;ng kh&aacute;ch d&ugrave;ng khăn để lau b&ugrave;n.</h2>\r\n\r\n<h2>- Cha l&agrave; người đ&agrave;n &ocirc;ng đẹp nhất m&agrave; t&ocirc;i được nh&igrave;n, Ralph ạ - Tại sao c&oacute; lắm linh mục đẹp như thế. Phải chăng v&igrave; c&oacute; d&ograve;ng m&aacute;u &aacute;i Nhĩ Lan. Tất nhi&ecirc;n, đ&oacute; l&agrave; một d&acirc;n tộc đẹp, những người &aacute;i Nhĩ Lan. C&oacute; phải những đ&agrave;n &ocirc;ng đẹp đi l&agrave;m linh mục để tho&aacute;t khỏi sự quyến rũ của ch&iacute;nh họ. T&ocirc;i đ&aacute;nh cuộc với cha rằng tất cả thiếu nữ ở Gilly đều ao ước chạ</h2>\r\n\r\n<h2>- Từ l&acirc;u t&ocirc;i c&oacute; th&oacute;i quen kh&ocirc;ng để &yacute; đến c&aacute;c c&ocirc; g&aacute;i si t&igrave;nh - &ocirc;ng trả lời vui vẻ - Bất cứ linh mục n&agrave;o dưới 50 tuổi đều co thể l&agrave; mục ti&ecirc;u của một số c&aacute;c c&ocirc; v&agrave; một linh mục dưới 35 th&igrave; dứt kho&aacute;t c&aacute;c c&ocirc; phải m&ecirc; rồi. Nhưng chỉ c&oacute; c&aacute;c c&ocirc; g&aacute;i theo đạo Tin l&agrave;nh l&agrave; muốn chinh phục t&ocirc;i một c&aacute;ch c&ocirc;ng khai.</h2>\r\n\r\n<h2>- Cha kh&ocirc;ng trả lời thẳng c&acirc;u hỏi của t&ocirc;i.</h2>\r\n\r\n<h2>Mary Carson đứng thẳng người l&ecirc;n, đến s&aacute;t cha Ralph đặt l&ograve;ng b&agrave;n tay của m&igrave;nh l&ecirc;n ngực linh mục v&agrave; giữ nguy&ecirc;n ở đ&oacute;.</h2>\r\n\r\n<h2>- Sự đ&ograve;i hỏi của phụ nữ c&oacute; l&agrave;m cha ray rứt kh&ocirc;ng, cha Ralph?</h2>\r\n\r\n<h2>- Kh&ocirc;ng, &ocirc;ng trả lời, đầu &ocirc;ng ngả về ph&iacute;a sau trong một động t&aacute;c b&agrave;y tỏ sự khinh miệt.</h2>\r\n\r\n<h2>- Với đ&agrave;n &ocirc;ng th&igrave; c&oacute; lẽ?</h2>\r\n\r\n<h2>- C&ograve;n tệ hơn phụ nữ. Kh&ocirc;ng, kh&ocirc;ng c&oacute; đ&ograve;i hỏi n&agrave;o gi&agrave;y v&ograve; t&ocirc;i. - Hay cha chỉ y&ecirc;u ch&iacute;nh cha m&agrave; th&ocirc;i?</h2>\r\n\r\n<h2>- C&agrave;ng &iacute;t hơn bất cứ ai.</h2>\r\n\r\n<h2>- Thật l&yacute; th&uacute;. B&agrave; đi v&ograve;ng quanh cha Ralph v&agrave; quan s&aacute;t linh mục. Ralph, Hồng Y De Bricassart - giọng Mary Carson mỉa mai.</h2>\r\n\r\n<h2>Đầu th&aacute;ng hai, cuộc sống thay đổi đột ngột với Meggie v&agrave; Stuart. Cả hai được gởi đi học nội tr&uacute; ở tu viện Gillabone v&igrave; kh&ocirc;ng c&oacute; trường n&agrave;o gần hơn. Pađy cho biết khi tới tuổi, Hal sẽ học theo phương ph&aacute;p h&agrave;m thụ của trường trung học Blacfriard ở Sydneỵ C&ograve;n b&acirc;y giờ v&igrave; Meggie v&agrave; Stuart quen học với thầy n&ecirc;n phải gởi đi học ở tu viện Saint Croix, tiền nội tr&uacute; được b&agrave; Mary Carson chi rộng r&atilde;i. C&ograve;n Jack v&agrave; Hughie sẽ kh&ocirc;ng đi học nữa, Drogheda cần ch&uacute;ng v&agrave; cả hai đều th&iacute;ch sống với đất đai.</h2>\r\n\r\n<h2>Meggie v&agrave; Stuart trải qua một cuộc sống &ecirc;m đềm ở tu viện Saint Croix, rất kh&aacute;c so với những ng&agrave;y ch&uacute;ng học Trường Th&aacute;nh T&acirc;m ở Wahinẹ Cha Ralph đ&atilde; tế nhị cho c&aacute;c nữ tu biết &ocirc;ng bảo trợ cho hai đứa b&eacute; v&agrave; kh&eacute;o l&eacute;o nhắc đến người c&ocirc; của ch&uacute;ng l&agrave; một phụ nữ gi&agrave;u nhất ở New South Wales. Do đ&oacute; m&agrave; sự rụt r&egrave; của Meggie được xem như một đức t&iacute;nh tốt v&agrave; cuộc sống t&aacute;ch rời, c&ocirc; độc của Stuart, th&oacute;i quen nh&igrave;n v&agrave;o khoảng kh&ocirc;ng, giờ n&agrave;y sang giờ kh&aacute;c, lại được coi l&agrave; th&aacute;nh thiện.</h2>\r\n\r\n<h2>Cha Ralph thường đến thăm Meggie v&agrave; Stuart, đ&oacute;n cả hai về nh&agrave; xứ đều đặn đến nỗi &ocirc;ng quyết định cho sơn căn ph&ograve;ng được d&agrave;nh cho Meggie - bằng m&agrave;u xanh dịu - &ocirc;ng cho treo m&agrave;n mới v&agrave; thay khăn trải giường. Stuart th&igrave; tiếp tục ngủ trong căn ph&ograve;ng trước đ&acirc;y sơn m&agrave;u kem b&acirc;y giờ ngả sang m&agrave;u n&acirc;u. Cha Ralph kh&ocirc;ng hề tỏ ra quan t&acirc;m Stuart ở đ&acirc;y c&oacute; vui kh&ocirc;ng. Sự hiện diện của Stuart l&agrave; phụ thuộc, cậu ta được mời về chỉ do sự tế nhị bắt buộc m&agrave; th&ocirc;i.</h2>\r\n\r\n<h2>Cha Ralph cũng kh&ocirc;ng biết tại sao &ocirc;ng lại mến Meggie đến thế, v&agrave; trong th&acirc;m t&acirc;m &ocirc;ng cũng kh&ocirc;ng khi n&agrave;o tự hỏi về điều n&agrave;y. Đầu ti&ecirc;n &ocirc;ng cảm thấy thương hại c&ocirc; b&eacute; khi nh&igrave;n thấy lần đầu ở s&acirc;n ga đầy bụi, Meggie đứng t&aacute;ch ri&ecirc;ng một m&igrave;nh, c&oacute; lẽ v&igrave; l&agrave; con g&aacute;i duy nhất trong gia đ&igrave;nh. &ocirc;ng kết luận như vậy. Nhưng ngược lại, linh mục kh&ocirc;ng lưu t&acirc;m đến Frank cũng đứng một m&igrave;nh ngo&agrave;i v&ograve;ng gia đ&igrave;nh; &ocirc;ng kh&ocirc;ng hề thương hại cậu thanh ni&ecirc;n ấy. C&ograve;n Meggiẻ C&ocirc; b&eacute; l&agrave;m cho linh mục x&uacute;c động s&acirc;u xa mặc d&ugrave; &ocirc;ng kh&ocirc;ng biết tại sao. M&agrave;u t&oacute;c của Meggie khiến cho &ocirc;ng bị m&ecirc; hoặc. M&agrave;u v&agrave; h&igrave;nh d&aacute;ng của đ&ocirc;i mắt l&agrave;m nhớ đến đ&ocirc;i mắt rất đẹp của Fiona, nhưng dịu d&agrave;ng gấp bội v&agrave; đ&aacute;ng y&ecirc;u hơn. V&agrave; t&iacute;nh t&igrave;nh của Meggie theo &ocirc;ng đ&oacute; l&agrave; c&aacute;i t&iacute;nh l&yacute; tưởng của người phụ nữ: thụ động, nhưng ki&ecirc;n quyết trước mọi thử th&aacute;ch. Kh&ocirc;ng c&oacute; sự nổi loạn ở Meggie, tr&aacute;i lại, cả đời Meggie sẽ khuất phục, chấp nhận sống b&ecirc;n trong những ranh giới của số phận người phụ nữ.</h2>\r\n\r\n<h2>Tuy nhi&ecirc;n, kh&ocirc;ng phải chỉ c&oacute; những điều đ&oacute; l&agrave;m Ralph qu&yacute; mến Meggie; m&agrave; trong s&acirc;u xa, t&igrave;nh cảm đối với c&ocirc; b&eacute; n&agrave;y xuất ph&aacute;t từ một pha trộn của thời gian, nơi chốn v&agrave; c&aacute; t&iacute;nh. Kh&ocirc;ng ai coi c&ocirc; b&eacute; ra g&igrave;, điều đ&oacute; g&acirc;y n&ecirc;n một khoảng trống trong cuộc đời của Meggie, c&aacute;i khoảng trống ấy &ocirc;ng c&oacute; thể l&agrave;m đầy v&agrave; chắc chắn sẽ nhận được t&igrave;nh thương của c&ocirc; b&eacute;. Meggie lại l&agrave; một đứa trẻ con v&agrave; như thế kh&ocirc;ng g&acirc;y n&ecirc;n lời ra tiếng v&agrave;o n&agrave;o cho lối sống v&agrave; tiếng tăm của linh mục. C&ocirc; b&eacute; rất đẹp v&agrave; &ocirc;ng say m&ecirc; với c&aacute;i đẹp ấy. Điều đ&oacute; kh&oacute; c&oacute; thể chấp nhận, c&ocirc; b&eacute; đ&atilde; lấp cai khoảng trống của cuộc đời &ocirc;ng, c&aacute;i khoảng trống m&agrave; Ch&uacute;a của &ocirc;ng kh&ocirc;ng thể lấp được, v&igrave; rằng c&ocirc; b&eacute; c&oacute; một sức hấp dẫn v&agrave; một nh&acirc;n c&aacute;ch r&otilde; n&eacute;t. Để kh&ocirc;ng g&acirc;y sự kh&oacute; chịu cho gia đ&igrave;nh Cleary khi &ocirc;ng thường xuy&ecirc;n tặng qu&agrave; cho Meggie, &ocirc;ng đ&atilde; d&agrave;nh tất cả thời gian m&agrave; &ocirc;ng c&oacute; thể c&oacute; được - thời giờ v&agrave; cả t&acirc;m tr&iacute; - để trang ho&agrave;ng căn ph&ograve;ng của Meggie ở trong nh&agrave; xứ, với mục đ&iacute;ch được thấy c&ocirc; b&eacute; vui, hơn l&agrave; để c&oacute; một nơi để đặt vi&ecirc;n ngọc qu&iacute; Meggie.</h2>', 0, 'tieng-chim-hot-trong-bui-man-gai-chuong-05-06');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(47, 25, 'Tiếng chim hót trong bụi mận gai - Chương 07-08', 'Tiếng chim hót trong bụi mận gai - Chương 07-08', '<h2>Frank kh&ocirc;ng về nh&agrave; từ mười lăm ng&agrave;y naỵ Anh ta theo người chăn nu&ocirc;i gi&agrave; t&ecirc;n Peter c&aacute;i th&ugrave;ng, với đ&agrave;n ch&oacute;, hai con ngựa, một chiếc xe nhỏ do một con ngựa tồi k&eacute;o d&ugrave;ng để chở đồ dự trữ, họ đến những v&ugrave;ng b&atilde;i chăn cừu xa nhất về hướng T&acirc;y đưa cừu về. C&ocirc;ng việc mất thời giờ, chậm chạp v&agrave; nhạt nhẽo kh&ocirc;ng như việc tập hợp cừu vội v&atilde; v&agrave;o thời điểm sắp sửa c&oacute; lụt. Nơi cắt l&ocirc;ng cừu chỉ c&oacute; thể nhận c&ugrave;ng một l&uacute;c mười ng&agrave;n con v&igrave; vậy kh&acirc;u tuyển lựa rất cần thiết.</h2>\r\n\r\n<h2>Khi Frank bước v&agrave;o nh&agrave; bếp, anh gặp ngay mẹ đứng b&ecirc;n bể rửa ch&eacute;n dĩa, vẫn l&uacute;i h&uacute;i v&agrave;o thứ c&ocirc;ng việc kh&ocirc;ng bao giờ kết th&uacute;c: gọt khoai.</h2>\r\n\r\n<h2>- Mẹ ơi, con về rồi! Anh vui mừng b&aacute;o tin.</h2>\r\n\r\n<h2>B&agrave; quay lại Frank, ngạc nhi&ecirc;n nh&igrave;n l&ecirc;n; hai tuần lễ xa nh&agrave; c&aacute;i nh&igrave;n của anh sắc hơn.</h2>\r\n\r\n<h2>- Trời ơi...! Frank lầm thầm trong miệng.</h2>\r\n\r\n<h2>Trong đ&ocirc;i mắt của Fiona kh&ocirc;ng thấy c&oacute; niềm vui gặp lại con, gương mặt đỏ l&ecirc;n xấu hổ, hai tay b&agrave; đặt l&ecirc;n tạp dề độn tr&ograve;n như để che giấu điều m&agrave; &aacute;o quần kh&ocirc;ng thể che được.</h2>\r\n\r\n<h2>- Con d&ecirc; gi&agrave; ấy thật gớm ghiếc! Frank k&ecirc;u l&ecirc;n, tay ch&acirc;n run rẩy.</h2>\r\n\r\n<h2>- Frank! Mẹ kh&ocirc;ng cho ph&eacute;p con được n&oacute;i như thế. Con lớn rồi. Con phải hiểu biết. Ch&iacute;nh con cũng ch&agrave;o đời bằng kết quả như thế. Điều n&agrave;y kh&ocirc;ng c&oacute; g&igrave; xấu xa; khi con chửi cha con, con đ&atilde; chửi cả mẹ rồi đấy!</h2>\r\n\r\n<h2>- &ocirc;ng ta kh&ocirc;ng c&oacute; quyền! Đ&aacute;ng ra &ocirc;ng ta phải để mẹ y&ecirc;n. Frank chống chế lại, miệng r&iacute;t l&ecirc;n s&ugrave;i bọt m&eacute;p.</h2>\r\n\r\n<h2>- Kh&ocirc;ng xấu xa đ&acirc;u, Frank - b&agrave; lặp lại bằng giọng mỏi mệt v&agrave; nh&igrave;n Frank bằng đ&ocirc;i mắt s&aacute;ng trong - kh&ocirc;ng xấu xa đ&acirc;u, Frank, kh&ocirc;ng xấu xa hơn h&agrave;nh động để cho một đứa trẻ sinh ra đời.</h2>\r\n\r\n<h2>Lần n&agrave;y th&igrave; Frank đỏ mặt l&ecirc;n. Anh kh&ocirc;ng d&aacute;m nh&igrave;n thẳng mẹ; anh nh&igrave;n đi nơi kh&aacute;c rồ về ph&ograve;ng chung với Bob, Jack v&agrave; Hughiẹ Mấy bức tường trống trơn, những chiếc giường chật như tr&ecirc;u chọc Frank, b&ecirc;u riếu sự c&oacute; mặt của anh; đ&acirc;y l&agrave; nơk ngủ buồn tẻ của Frank, kh&ocirc;ng c&oacute; người n&agrave;o kh&aacute;c để g&acirc;y hơi ấm v&agrave; cũng chẳng c&oacute; ch&uacute;t g&igrave; gọi l&agrave; thi&ecirc;ng li&ecirc;ng. V&agrave; l&uacute;c ấy trong anh hiện l&ecirc;n gương mặt của mẹ, n&eacute;t đẹp mệt mỏi, với quầng s&aacute;ng của m&aacute;i t&oacute;c v&agrave;ng &oacute;ng &aacute;nh tỏa ra như một vầng h&agrave;o quang, sung sướng v&igrave; cảm nhận trong bụng m&igrave;nh c&aacute;i kết quả đ&atilde; c&ugrave;ng con d&ecirc; gi&agrave; l&ocirc;ng l&aacute; cấu tạo giữa c&aacute;i n&oacute;ng oi bức của m&ugrave;a h&egrave;.</h2>\r\n\r\n<h2>Anh ta kh&ocirc;ng thể xua đuổi nỗi &aacute;m ảnh ấy, kh&ocirc;ng thể t&aacute;ch rời mẹ với những suy nghĩ đen tối đang v&acirc;y h&atilde;m m&igrave;nh. Rồi những đ&ograve;i hỏi tự nhi&ecirc;n của lứa tuổi v&agrave; sự sung m&atilde;n của người đ&agrave;n &ocirc;ng đ&atilde; dậy l&ecirc;n ở anh ra. B&igrave;nh thường, Frank xua đuổi được những &yacute; tưởng ấy khỏi đầu &oacute;c nhưng khi mẹ anh ta ph&ocirc; b&agrave;y ra trước anh sự hiển nhi&ecirc;n của chuyện d&acirc;m dục, v&agrave; c&aacute;i h&agrave;nh vi b&iacute; ẩn đ&atilde; thực hiện với con th&uacute; gi&agrave; đa d&acirc;m ấy th&igrave;... L&agrave;m sao Frank c&oacute; thể chấp nhận được, chịu đựng được? Frank mong muốn nghĩ về mẹ như một người hết sức th&aacute;nh thiện, trong trắng như Đức Mẹ đồng trinh, một người phụ nữ vượt l&ecirc;n tr&ecirc;n h&agrave;nh động tầm thường mặc d&ugrave; tất cả phụ nữ, ở khắp thế gian, đều phạm tội ấy. Cảm thấy mẹ đ&aacute;nh mất c&aacute;i &yacute; tưởng đẹp m&agrave; anh vốn tr&acirc;n trọng, Frank ph&aacute;t đi&ecirc;n l&ecirc;n. Để đầu &oacute;c được b&igrave;nh tỉnh, Frank cần phải tưởng tượng rằng mẹ m&igrave;nh nằm kế b&ecirc;n l&atilde;o gi&agrave; gh&ecirc; tởm ấy trong sự trong trắng ho&agrave;n to&agrave;n, chỉ để ngủ, v&agrave; trong đ&ecirc;m cả hai kh&ocirc;ng quay mặt lại nhau, cũng kh&ocirc;ng hề chạm nhau - Trời ơi!</h2>\r\n\r\n<h2>Tiếng lanh lảnh của kim loại khiến Frank nh&igrave;n xuống; anh nhận ra l&agrave; m&igrave;nh đang b&oacute;p vẹo c&aacute;i thanh sắt giường.</h2>\r\n\r\n<h2>- Tiếc qu&aacute;, m&agrave;y kh&ocirc;ng phải l&agrave; cha tao! Anh n&oacute;i với thanh sắt.</h2>\r\n\r\n<h2>- Frank, Fiona đứng ở cửa ph&ograve;ng gọi Frank. Anh ngước nh&igrave;n mẹ bằng đ&ocirc;i mắt đen, s&aacute;ng quắc, nhưng ướt đẫm như h&ograve;n than dưới mưa.</h2>\r\n\r\n<h2>- Trước sau g&igrave; con cũng giết &ocirc;ng ta - giọng Frank gầm gừ.</h2>\r\n\r\n<h2>- V&agrave; rồi con sẽ giết lu&ocirc;n mẹ chứ g&igrave;! Fiona vừa n&oacute;i vừa đến ngồi b&ecirc;n Frank.</h2>\r\n\r\n<h2>- Kh&ocirc;ng, con sẽ giải tho&aacute;t mẹ! Frank đ&aacute;p lại, giọng s&ocirc;i nổi, l&ograve;ng nung nấu bở một hy vọng đi&ecirc;n rồ.</h2>\r\n\r\n<h2>- Frank &agrave;, mẹ sẽ kh&ocirc;ng bao giờ đưƠ.c tự dọ Hơn nữa mẹ cũng kh&ocirc;ng muốn được tự dọ Mẹ muốn t&igrave;m hiểu sự m&ugrave; qu&aacute;ng của con từ đ&acirc;u m&agrave; ra, vậy m&agrave; mẹ kh&ocirc;ng thể hiểu được. Nhưng c&oacute; điều chắc l&agrave; kh&ocirc;ng phải từ mẹ, m&agrave; cũng kh&ocirc;ng từ cha con. Mẹ biết con kh&ocirc;ng c&oacute; hạnh ph&uacute;c, nhưng tại sao con tr&uacute;t tất cả l&ecirc;n mẹ v&agrave; chả (B&agrave; nh&igrave;n hai tay m&igrave;nh, rồi nh&igrave;n l&ecirc;n Frank). Đ&aacute;ng l&yacute; mẹ kh&ocirc;ng n&oacute;i điều n&agrave;y với con, nhưng mẹ nghĩ cần phải n&oacute;i. Frank &agrave;, đ&atilde; đến l&uacute;c con cần c&oacute; người bạn g&aacute;i, để sau n&agrave;y x&acirc;y dựng một m&aacute;i ấm gia đ&igrave;nh. Drogheda kh&ocirc;ng thiếu chỗ đ&acirc;u; về chuyện n&agrave;y mẹ kh&ocirc;ng &acirc;u lo cho c&aacute;c em con; c&aacute;c em con bản t&iacute;nh kh&ocirc;ng giống con. Nhưng c&ograve;n con, con cần một người vợ. Frank &agrave;. Khi c&oacute; vợ, con kh&ocirc;ng c&oacute; thời giờ để nghĩ đến mẹ nữa.</h2>\r\n\r\n<h2>Frank ngồi quay lưng lại mẹ, anh kh&ocirc;ng muốn phải nh&igrave;n thẳng v&agrave;o mẹ. Hơn năm ph&uacute;t đ&atilde; tr&ocirc;i qua, b&agrave; vẫn ngồi tr&ecirc;n giường với hy vọng Frank sẽ n&oacute;i điều g&igrave; đ&oacute;, nhưng rồi b&agrave; thở d&agrave;i, đứng l&ecirc;n v&agrave; rời khỏi ph&ograve;ng.</h2>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"height:260px; width:86%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top\">\r\n			<h2><strong>Chương 8</strong></h2>\r\n\r\n			<h2>&nbsp;</h2>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Sau khi c&aacute;c thợ cắt l&ocirc;ng cừu đ&atilde; đi, l&uacute;c m&agrave; cả v&ugrave;ng trở lại sự y&ecirc;n tĩnh b&igrave;nh thường của m&ugrave;a đ&ocirc;ng th&igrave; ng&agrave;y lễ h&agrave;ng năm của Gillanbone lại đến. Cuộc vui k&eacute;o d&agrave;i hai ng&agrave;y, Fiona cảm thấy kh&ocirc;ng khỏe lắm n&ecirc;n kh&ocirc;ng tham dự, c&ograve;n Pađy phải l&aacute;i xe Rolls Royce đưa Mary Carson l&ecirc;n tỉnh.</h2>\r\n\r\n<h2>Mọi người đều dự c&aacute;c cuộc vui. Mấy đứa con trai của Pađy, trước khi được đi theo Peter c&aacute;i th&ugrave;ng, đều bị hăm dọa đủ điều nếu ai đ&oacute; gặp ch&uacute;ng ph&aacute; ph&aacute;ch. Ri&ecirc;ng Frank đi thật sớm, một m&igrave;nh tr&ecirc;n chiếc &ocirc; t&ocirc; Ford. Tất cả người lớn đều ở lại đến ng&agrave;y mai để dự cuộc đua ngựa. B&agrave; Mary Carson, v&igrave; những l&yacute; do ri&ecirc;ng, đ&atilde; từ chối lời mời của cha Ralph đưa b&agrave; đến nghỉ đ&ecirc;m ở nh&agrave; xứ, nhưng ngược lại nằng nặc bảo Pađy v&agrave; Frank nhận lời cha Ralph.</h2>\r\n\r\n<h2>Sau khi đưa chị đến kh&aacute;ch sạn Ho&agrave;ng Gia, Pađy xuống quầy rượu v&agrave; gặp Frank, tay cầm một cốc bia.</h2>\r\n\r\n<h2>- Ba đ&atilde;i con sau - Pađy n&oacute;i nhỏ nhẹ với con - Ba phải đưa c&ocirc; Mary đi ăn cơm trưa ngo&agrave;i trời trước khi đi xem đua ngựa v&agrave; ba cần được thoải m&aacute;i trước khi phải chịu đựng một cuộc thử th&aacute;ch với c&ocirc; Marỵ</h2>\r\n\r\n<h2>Th&oacute;i quen v&agrave; sự sợ sệt kh&oacute; vượt qua hơn l&agrave; ta vẫn thường nghĩ khi định tho&aacute;t ra khỏi sự chi phối của n&oacute; - Frank n&oacute;i với m&igrave;nh. Anh ta tự thấy kh&ocirc;ng thể để h&agrave;nh động theo &yacute; m&igrave;nh, kh&ocirc;ng thể tr&uacute;t cả cốc bia v&agrave;o mặt cha, nhất l&agrave; trước mặt đ&ocirc;ng đảo những kh&aacute;ch tấp nập quanh quầy. Anh đ&agrave;nh nuốt ực phần bia c&ograve;n lại, mỉm cười v&agrave; lườm lườm n&oacute;i:</h2>\r\n\r\n<h2>- Rất tiếc ba; con c&oacute; hẹn với mấy thằng bạn ở chỗ lễ.</h2>\r\n\r\n<h2>Meggie với hai b&iacute;m t&oacute;c th&ograve;ng xuống qu&aacute; vai, mặc bộ đồng phục m&agrave;u xanh nữ sinh nội tr&uacute; trường Saint Croix, theo sau một b&agrave; xơ, đi băng qua b&atilde;i cỏ của tu viện để đến nh&agrave; xứ. Tại đ&acirc;y b&agrave; xơ giao Meggie cho b&agrave; quản gia của cha Ralph; b&agrave; n&agrave;y rất y&ecirc;u thương Meggiẹ</h2>\r\n\r\n<h2>- T&ocirc;i y&ecirc;u con b&eacute; n&agrave;y v&igrave; m&aacute;i t&oacute;c của n&oacute; ngọt ng&agrave;o như rượu ti&ecirc;n - ba ta giải th&iacute;ch Ralph ngạc nhi&ecirc;n về sự say m&ecirc; đ&oacute;. Annie - b&agrave; quản gia - kh&ocirc;ng ưa đ&aacute;m con g&aacute;i v&agrave; thường than phiền nh&agrave; xứ ở qu&aacute; gần trường học.</h2>\r\n\r\n<h2>- Th&ocirc;i đừng giải th&iacute;ch lung tung b&agrave; Annie ạ. T&oacute;c l&agrave; thứ v&ocirc; trị B&agrave; kh&ocirc;ng thể y&ecirc;u một con người duy nhất chỉ v&igrave; m&agrave;u t&oacute;c - Cha Ralph ghẹo b&agrave;.</h2>\r\n\r\n<h2>L&uacute;c đ&oacute; Frank v&agrave;o, cơn x&uacute;c động sau khi gặp cha m&igrave;nh tại quầy rượu, đầu &oacute;c rối loạn.</h2>\r\n\r\n<h2>- Meggie đi với anh, anh đưa em đi hội chợ - vừa n&oacute;i vừa đưa tay ra.</h2>\r\n\r\n<h2>- Hay l&agrave; cha sẽ đưa cả hai c&ugrave;ng đỉ Cha Ralph đề nghị. Hai b&agrave;n tay nhỏ b&eacute; của Meggie siết chặt hai tay của hai người đ&agrave;n &ocirc;ng m&agrave; c&ocirc; b&eacute; t&ocirc;n k&iacute;nh. Meggie thấy m&igrave;nh ở bảy tầng m&acirc;y.</h2>\r\n\r\n<h2>Hội chợ tổ chức ở b&ecirc;n bờ s&ocirc;ng Barson, b&ecirc;n cạnh trường đua ngựa.</h2>\r\n\r\n<h2>Th&aacute;i độ cha Ralph chăm s&oacute;c Meggie l&agrave;m nhiều thiếu nữ ghen gh&eacute;t, nhất l&agrave; hoa kh&ocirc;i Carmichael muốn chinh phục chạ Đi ngang một vũng nước, trong khi hoa kh&ocirc;i Carmichael l&uacute;ng t&uacute;ng với chiếc &aacute;o d&agrave;i phết đất, chờ đợi một cử chỉ galăng của cha Ralph, th&igrave; linh mục lại quay sang bế Meggie một c&aacute;ch &acirc;n cần.</h2>\r\n\r\n<h2>Ở đầu b&atilde;i đằng xa, một c&aacute;i lều to sừng sững, trước lều một s&agrave;n gỗ, sau lều treo một tấm vải với nhiều h&igrave;nh vẽ dữ tợn như muốn h&ugrave; dọa đ&aacute;m đ&ocirc;ng. Một đ&aacute;m người cầm m&aacute;y ph&aacute;t &acirc;m, n&oacute;i to với những người hiếu k&igrave; đang tụ tập lại.</h2>\r\n\r\n<h2>- Thưa c&aacute;c ng&agrave;i, đ&acirc;y l&agrave; đo&agrave;n v&otilde; sĩ nổi tiếng Jimmy Sharman! T&aacute;m nh&agrave; v&ocirc; địch ưu t&uacute; nhất thế giới, sẽ c&oacute; một khoản tiền thưởng cho bất cứ ai trong qu&iacute; ng&agrave;i muốn thử thời vận.</h2>\r\n\r\n<h2>C&aacute;c nh&agrave; v&ocirc; địch đứng tr&ecirc;n s&agrave;n gỗ cười n&oacute;i với nhau tự nhi&ecirc;n v&agrave; rất tự tin, xem chuyện n&agrave;y chẳng c&oacute; g&igrave; quan trọng.</h2>\r\n\r\n<h2>- Mau l&ecirc;n c&aacute;c bạn, ai muốn đeo găng v&agrave;o? C&aacute;i anh ch&agrave;ng cầm m&aacute;y ph&aacute;t &acirc;m vẫn la h&eacute;t. Ai muốn thử thời vận? Đeo găng v&agrave;o v&agrave; bạn sẽ l&atilde;nh ngay năm bảng! Xen lẫn tiếng h&eacute;t của anh ta l&agrave; những hồi trống th&uacute;c giục.</h2>\r\n\r\n<h2>- T&ocirc;i, Frank k&ecirc;u tọ T&ocirc;i đ&acirc;y!</h2>\r\n\r\n<h2>Frank r&uacute;t tay ra khỏi tay cha Ralph đang cố giữ anh lại. Đ&aacute;m đ&ocirc;ng cười rộ l&ecirc;n khi nh&igrave;n thấy v&oacute;c người thấp nhỏ của Frank. Bọn họ đẩy Frank l&ecirc;n s&agrave;n gỗ một c&aacute;ch vui vẻ. Nhưng anh ch&agrave;ng rao h&agrave;ng th&igrave; lại nghi&ecirc;m nghị khi một người trong những người của đo&agrave;n đưa tay th&acirc;n thiện gi&uacute;p Frank bước l&ecirc;n cầu thang đến chỗ của t&aacute;m v&otilde; sĩ.</h2>\r\n\r\n<h2>- Đừng c&oacute; vội cười thưa qu&iacute; ng&agrave;i! Ch&agrave;ng thanh ni&ecirc;n n&agrave;y kh&ocirc;ng to nhưng anh l&agrave; người đầu ti&ecirc;n t&igrave;nh nguyện l&ecirc;n đ&acirc;y! Trong một trận đấu, v&oacute;c d&aacute;ng người kh&ocirc;ng phải l&agrave; vấn đề quyết định m&agrave; l&agrave; những g&igrave; chứa trong bụng! V&agrave; đ&acirc;y l&agrave; một cậu b&eacute; can đảm l&ecirc;n thử thời vận. C&aacute;c anh c&ograve;n chờ đợi g&igrave;, những ch&agrave;ng to khỏe, hả? N&agrave;y, cậu trai h&atilde;y mang găng v&agrave;o v&agrave; l&atilde;nh năm bảng bằng c&aacute;ch đứng vững trước một trong c&aacute;c nh&agrave; v&ocirc; địch của Jimmy Sharman cho đến gi&acirc;y ph&uacute;t cuối c&ugrave;ng!</h2>\r\n\r\n<h2>Cha Ralph kh&ocirc;ng muốn Meggie chứng kiến cảnh Frank ăn đ&ograve;n n&ecirc;n bế xốc c&ocirc; b&eacute; l&ecirc;n v&agrave; định quay ra. Meggie h&eacute;t l&ecirc;n, cha Ralph c&agrave;ng đi ra xa, tiếng h&eacute;t c&ocirc; b&eacute; c&agrave;ng dữ dội. Người ta bắt đầu hướng mắt về họ. Uy t&iacute;n kh&ocirc;ng hề bị tai tiếng của cha Ralph trước đ&acirc;y c&oacute; nguy cơ bị cảnh hưởng v&igrave; chuyện n&agrave;y.</h2>\r\n\r\n<h2>- Nghe đ&acirc;y Meggie, cha kh&ocirc;ng thể đưa con v&agrave;o trong đ&oacute;! Ba con sẽ lột da cha v&igrave; &ocirc;ng ấy l&agrave;m như thế l&agrave; c&oacute; l&yacute;.</h2>\r\n\r\n<h2>- Con muốn ở lại với Frank! Con muốn ở lại với Frank! C&ocirc; b&eacute; cứ h&eacute;t l&ecirc;n, hai ch&acirc;n giẫy nẩy.</h2>\r\n\r\n<h2>- Thật l&agrave; rắc rối! linh mục bực dọc.</h2>\r\n\r\n<h2>Đ&agrave;nh phải chịu, kh&ocirc;ng thể n&agrave;o l&agrave;m kh&aacute;c hơn, cha Ralph cho tay v&agrave;o t&uacute;i lấy ra v&agrave;i đồng tiền v&agrave; đến gần cửa lều, mắt đảo quanh sợ một trong số c&aacute;c anh của Meggie bắt gặp.</h2>\r\n\r\n<h2>- Đ&acirc;y kh&ocirc;ng phải l&agrave; cuộc tr&igrave;nh diễn d&agrave;nh cho con b&eacute; n&agrave;y. Người b&aacute;n v&eacute; r&otilde; r&agrave;ng kh&oacute; chịu.</h2>\r\n\r\n<h2>Cha Ralph đưa mắt l&ecirc;n nh&igrave;n trời.</h2>\r\n\r\n<h2>- Nếu anh c&oacute; c&aacute;ch n&agrave;o đ&oacute; đưa c&ocirc; b&eacute; rời xa nơi đ&acirc;y m&agrave; kh&ocirc;ng g&acirc;y ồn &agrave;o để cảnh s&aacute;t c&oacute; thể bắt ch&uacute;ng ta về c&aacute;i tội h&agrave;nh hung trẻ con, th&igrave; t&ocirc;i sẵn s&agrave;ng đưa n&oacute; đi ngaỵ Anh c&ocirc; ấy chấp nhận l&ecirc;n đ&agrave;i so găng, c&ocirc; ấy kh&ocirc;ng muốn bỏ rơi anh m&igrave;nh.</h2>\r\n\r\n<h2>Người b&aacute;n v&eacute; nh&uacute;n vai.</h2>\r\n\r\n<h2>- Th&ocirc;i th&igrave; t&ocirc;i kh&ocirc;ng tranh luận với cha l&agrave;m g&igrave;. Xin mời v&agrave;o nhưng cố l&agrave;m sao cho n&oacute; đứng y&ecirc;n. Kh&ocirc;ng, kh&ocirc;ng, cha h&atilde;y giữ tiền lại, Jimmy Sharman kh&ocirc;ng nhận tiền của cha đ&acirc;u.</h2>\r\n\r\n<h2>Đầu ti&ecirc;n Frank so găng với một v&otilde; sĩ hạng l&ocirc;ng, anh hạ đo v&aacute;n v&otilde; sĩ n&agrave;y. Ở tiếng cồng thứ ba Frank đề nghị so găng với một đấu thủ kh&aacute;c. Khi anh thi đấu đến trận thứ ba th&igrave; tiếng đồn đ&atilde; lan ra nhanh như thuốc s&uacute;ng, trong lều chật n&iacute;ch người xem, kh&ocirc;ng c&ograve;n một chỗ trống.</h2>\r\n\r\n<h2>Đ&aacute;nh đấm l&agrave; c&aacute;ch duy nhất m&agrave; Frank biết để tr&uacute;t hận th&ugrave; v&agrave; sự phiền muộn. Ngay l&uacute;c anh tung ra c&uacute; đ&aacute;nh hạ đối thủ, h&igrave;nh như anh nghe một tiếng n&oacute;i xa xăm dội v&agrave;o tai anh: H&atilde;y giết &ocirc;ng ta! H&atilde;y giết &ocirc;ng ta!</h2>\r\n\r\n<h2>Sau đ&oacute; người ta đưa ra đối đầu với Frank một v&otilde; sĩ v&ocirc; địch đ&iacute;ch thực, một v&otilde; sĩ hạng nhẹ được chỉ thị giữ một cự ly c&aacute;ch xa Frank để d&ograve; xem Frank c&oacute; biết đấu quyền Anh kh&ocirc;ng hay chỉ l&agrave; liều lĩnh. Đ&ocirc;i mắt Jimmy Sharman s&aacute;ng l&ecirc;n. &ocirc;ng ta lu&ocirc;n đi t&igrave;m những nh&agrave; v&ocirc; địch, v&agrave;o c&aacute;c ng&agrave;y hội ở v&ugrave;ng qu&ecirc; thường gi&uacute;p cho &ocirc;ng ph&aacute;t hiện v&agrave;i người. Người v&otilde; sĩ hạng nhẹ theo đ&uacute;ng chỉ đạo của &ocirc;ng, trong khi đ&oacute; Frank, đi&ecirc;n l&ecirc;n muốn giết đối thủ, lao thẳng v&agrave;o c&aacute;i b&oacute;ng lu&ocirc;n nhảy qua nhảy lại kh&ocirc;ng ngớt n&eacute; tr&aacute;nh. Anh r&uacute;t ra b&agrave;i học, cứ mỗi lần đ&aacute;nh &aacute;p s&aacute;t, l&agrave; bị một trận mưa đ&ograve;n, v&igrave; rằng Frank thuộc v&agrave;o loại người kỳ lạ c&oacute; thể suy nghĩ ngay giữa l&uacute;c hung tợn đi&ecirc;n cuồng. V&agrave; Frank đ&atilde; đứng vững đến ph&uacute;t cuối c&ugrave;ng, trước những c&uacute; đấm dạn d&agrave;y kinh nghiệm của đối phương. Một con mắt của Frank sưng v&ugrave; l&ecirc;n, một đường t&eacute;t ở ch&acirc;n m&agrave;y v&agrave; một đường kh&aacute;c ở m&ocirc;i. Nhưng anh l&atilde;nh được hai mươi bảng v&agrave; được những người c&oacute; mặt th&aacute;n phục.</h2>\r\n\r\n<h2>- Con muốn ch&uacute;ng ta chờ Frank hay ch&uacute;ng ta đi ngay?</h2>\r\n\r\n<h2>C&ocirc; b&eacute; dựa v&agrave;o cha Ralph, đầy l&ograve;ng cảm ơn đối với sự y&ecirc;n lặng v&agrave; th&ocirc;ng cảm m&agrave; linh mục đ&atilde; d&agrave;nh chọ</h2>\r\n\r\n<h2>- Cha đ&atilde; phải tự hỏi tại sao con lại khiến tr&aacute;i tim v&ocirc; nghĩa của cha chịu một thử th&aacute;ch gay go như thế - linh mục n&oacute;i thầm (&ocirc;ng nghĩ rằng c&ocirc; b&eacute; sẽ rất buồn v&agrave; khổ m&agrave; nghe &ocirc;ng n&oacute;i nhưng &ocirc;ng c&oacute; nhu cầu phải b&agrave;y tỏ c&aacute;c suy nghĩ của m&igrave;nh th&agrave;nh tiếng, đấy l&agrave; trường hợp thường xảy ra ở những kẻ sống độc th&acirc;n). Con kh&ocirc;ng l&agrave;m cho cha nhớ đến mẹ v&agrave; cha cũng kh&ocirc;ng c&oacute; em, cha muốn biết y&ecirc;u thuật n&agrave;o ẩn n&uacute;p trong con v&agrave; trong gia đ&igrave;nh tội nghiệp của con cũng thế... Mọi việc c&oacute; qu&aacute; kh&oacute; khăn cho con kh&ocirc;ng, hỡi c&ocirc; b&eacute; Meggie của chả</h2>\r\n\r\n<h2>Frank bước ra khỏi lều, một miếng băng d&iacute;nh tr&ecirc;n ch&acirc;n m&agrave;y, tay chậm l&ecirc;n m&ocirc;i. Lần đầu ti&ecirc;n từ khi cha Ralph biết Frank, &ocirc;ng thấy anh ta c&oacute; vẻ vui l&ecirc;n. Tưởng như Frank vừa trải qua một đ&ecirc;m y&ecirc;u đương với một người phụ nữ - cha Ralph nghĩ thầm như thế.</h2>\r\n\r\n<h2>Khi Pađy bước v&agrave;o ph&ograve;ng kh&aacute;ch của nh&agrave; xứ để t&igrave;m Frank th&igrave; Meggie v&agrave; cha Ralph đang đứng quanh ngọn lửa, thoải m&aacute;i như trải qua một ng&agrave;y vui, tuyệt vời. Điều đ&oacute; khiến cho Pađy bực bội. Kh&ocirc;ng c&oacute; vợ b&ecirc;n cạnh, Pađy cảm thấy thiếu v&ocirc; c&ugrave;ng. Đối với chị Mary Carson, Pađy vẫn giữ sự &aacute;c cảm kh&ocirc;ng kh&aacute;c g&igrave; thời &ocirc;ng c&ograve;n l&agrave; một cậu b&eacute; ở &aacute;i Nhĩ Lan. Đ&uacute;ng l&uacute;c đ&oacute; &ocirc;ng ch&uacute; &yacute; đến c&aacute;i băng d&iacute;nh tr&ecirc;n ch&acirc;n m&agrave;y của Frank v&agrave; gương mặt sưng ph&ugrave;; một c&aacute;i cớ từ tr&ecirc;n m&ocirc;i rơi xuống để cho &ocirc;ng tr&uacute;t ra bao nhi&ecirc;u thứ bực bội.</h2>\r\n\r\n<h2>- Với c&aacute;i mặt thế n&agrave;y th&igrave; l&agrave;m sao m&agrave;y d&aacute;m về nh&igrave;n mặt mẹ mầy! &ocirc;ng rầy tọ Chỉ cần tao bu&ocirc;ng lơi một ch&uacute;t l&agrave; mầy lại sẵn s&agrave;ng vồ lấy thằng n&agrave;o đầu ti&ecirc;n lườm m&agrave;y.</h2>\r\n\r\n<h2>Sửng sốt, cha Ralph bật ngồi dậy v&agrave; n&oacute;i v&agrave;i tiếng để xoa dịu Pađy, nhưng Frank đ&atilde; phản ứng trước.</h2>\r\n\r\n<h2>- Con đ&atilde; kiếm tiền bằng c&aacute;ch n&agrave;y đ&acirc;y - Frank n&oacute;i rất khẽ, tay chỉ c&aacute;i băng d&iacute;nh - Hai mươi bảng trong v&agrave;i ph&uacute;t lao động, hơn cả số tiền c&ocirc; Mary trả cho ch&uacute;ng ta, ba v&agrave; con, trong một th&aacute;ng! Con đ&atilde; đ&aacute;nh ng&atilde; ba v&otilde; sĩ giỏi v&agrave; đứng vững trước một nh&agrave; v&ocirc; địch hạng nhẹ trong lều của Jimmy Sharman trưa n&agrave;y. Con đ&atilde; l&atilde;nh 20 bảng! Điều n&agrave;y n&oacute; kh&ocirc;ng d&iacute;nh dấp đến những g&igrave; ba thường nghĩ về con. Tất cả mọi người chứng kiến trận đấu đều nể nang con.</h2>\r\n\r\n<h2>- Thắng v&agrave;i thằng khốn khổ mệt mỏi v&agrave; bầm dập v&igrave; bị đấm, chỉ xuất hiện để l&ograve;e thi&ecirc;n hạ trong c&aacute;c ng&agrave;y hội ở nh&agrave; qu&ecirc; thế m&agrave; m&agrave;y lại ph&aacute;ch lối. Phải biết cư xử như một người đ&atilde; trưởng th&agrave;nh, Frank. Tao biết c&oacute; thể m&agrave;y đ&atilde; kh&ocirc;ng thể cao hơn nhưng h&atilde;y v&igrave; mẹ m&agrave;y, để c&aacute;i đầu của m&agrave;y lớn ra!</h2>\r\n\r\n<h2>Mặt Frank t&aacute;i nhợt đi! Như những miếng xương trắng phếu dưới mặt trời. Với anh, đ&acirc;y l&agrave; sự sỉ nhục kinh khủng nhất từ trước tới naỵ Hơi thở của Frank nghe r&iacute;t l&ecirc;n nhưng vẫn cố giữ hai tay ở y&ecirc;n hai b&ecirc;n sườn h&ocirc;ng.</h2>\r\n\r\n<h2>- Họ kh&ocirc;ng phải l&agrave; những t&ecirc;n khốn khổ bị bầm dập bởi những c&uacute; đấm, thưa bạ Ba đ&atilde; nghe danh Jimmy Sharman nhiều kh&ocirc;ng thua con v&agrave; ch&iacute;nh hắn ta n&oacute;i với con rằng con sinh ra l&agrave; một v&otilde; sĩ v&agrave; trước mặt con l&agrave; một tương lai s&aacute;ng. Hắn muốn mướn con v&agrave;o đo&agrave;n v&agrave; r&egrave;n luyện, v&agrave; sẽ trả lương cho con! C&oacute; thể con kh&ocirc;ng c&ograve;n lớn nữa nhưng con đ&atilde; đủ lớn để dạy cho bất cứ ai một b&agrave;i học... Lời n&oacute;i ấy c&oacute; gi&aacute; trị cho cả ba nữa, con d&ecirc; gi&agrave; thối tha!</h2>\r\n\r\n<h2>Những tiếng cuối c&ugrave;ng hạ thấp giọng vẫn lọt v&agrave;o tai của Pađỵ Mặt &ocirc;ng t&aacute;i nhợt kh&ocirc;ng thua mặt con khi &ocirc;ng biết Frank &aacute;m chỉ m&igrave;nh.</h2>\r\n\r\n<h2>- Sao m&agrave;y d&aacute;m n&oacute;i thế?</h2>\r\n\r\n<h2>- Ba kh&ocirc;ng l&agrave; c&aacute;i g&igrave; kh&aacute;c hơn. Ba đ&aacute;ng gh&ecirc; tởm. C&ograve;n tồi tệ hơn cừu đực động đực! Tại sao ba kh&ocirc;ng để mẹ con y&ecirc;n? Ba kh&ocirc;ng thể tự k&igrave;m chế việc d&ugrave;ng mẹ con để t&igrave;m kho&aacute;i lạc hả?</h2>\r\n\r\n<h2>- Đừng, đừng, đừng! Meggie h&eacute;t to l&ecirc;n. Mấy ng&oacute;n tay của cha Ralph cắm mạnh v&agrave;o hai vai Meggie như m&oacute;ng chim mồi để cố giữ c&ocirc; b&eacute; s&aacute;t v&agrave;o m&igrave;nh. Nước mắt r&agrave;n rụa tr&ecirc;n mặt, Meggie cố v&ugrave;ng vẫy hết sức m&igrave;nh nhưng vẫn kh&ocirc;ng thể tho&aacute;t ra. ...</h2>\r\n\r\n<h2>- Đừng ba, đừng! Frank ơi, em van anh! Em van anh! C&ocirc; b&eacute; la l&ecirc;n giọng hốt hoảng.</h2>\r\n\r\n<h2>Nhưng chỉ c&oacute; cha Ralph l&agrave; nghe c&ocirc; b&eacute;. Frank v&agrave; Pađy x&aacute;p lại đối diện nhau. L&ograve;ng căm hờn v&agrave; sự e sợ đều bị lột trần. Con đ&ecirc; trước kia ngăn chặn hai người - t&igrave;nh y&ecirc;u cả hai d&agrave;nh cho Fiona - đ&atilde; bị vỡ, giờ đ&acirc;y sự đối đầu ngấm ngầm đ&atilde; bộc lộ c&ocirc;ng khai.</h2>\r\n\r\n<h2>- Tao l&agrave; chồng. V&agrave; Ch&uacute;a đ&atilde; gi&aacute;ng ph&uacute;c cho cuộc kết hợp giữa hai vợ chồng tao bằng c&aacute;ch mang lại cho hai vợ chồng tao nhiều con c&aacute;i, Pađy n&oacute;i với giọng b&igrave;nh tĩnh hơn, cố gắng tự chủ.</h2>\r\n\r\n<h2>- Ba kh&ocirc;ng xứng đ&aacute;ng hơn một con ch&oacute; gi&agrave; dơ d&aacute;y chạy theo sau bất cứ con ch&oacute; c&aacute;i n&agrave;o để t&igrave;m c&aacute;ch nhảy đực!</h2>\r\n\r\n<h2>- C&ograve;n m&agrave;y, m&agrave;y kh&ocirc;ng hơn g&igrave; con ch&oacute; gi&agrave; dơ d&aacute;y đ&atilde; đẻ ra m&agrave;y m&agrave; tao kh&ocirc;ng hề biết đ&oacute; l&agrave; con ch&oacute; n&agrave;o! Pady h&eacute;t l&ecirc;n. Rất may, nhờ ơn Ch&uacute;a, tao kh&ocirc;ng c&oacute; d&iacute;nh dấp g&igrave; v&agrave;o chuyện ấy. Trời ơi, tại sao t&ocirc;i đ&atilde; n&oacute;i như thế...</h2>\r\n\r\n<h2>Pađy chợt im bặt; sự đi&ecirc;n tiết của &ocirc;ng tan biến nhanh như gi&oacute;. &ocirc;ng ta muốn ng&atilde; quỵ xuống, co r&uacute;m lại, hai tay đưa l&ecirc;n bụm miệng, tưởng như &ocirc;ng muốn rứt c&aacute;i lưỡi đ&atilde; thốt ra điều kh&ocirc;ng bao giờ c&oacute; quyền thốt ra.</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng muốn n&oacute;i điều đ&oacute;, Pađy than kh&oacute;c - T&ocirc;i kh&ocirc;ng muốn n&oacute;i điều đ&oacute;!</h2>\r\n\r\n<h2>Ngay l&uacute;c những lời ấy tho&aacute;t khỏi miệng Pađy, cha Ralph bu&ocirc;ng Meggie ra v&agrave; chạy ngay tới Frank. &ocirc;ng bẻ vặn c&aacute;nh tay Frank ra sau lưng, c&aacute;nh tay c&ograve;n lại &ocirc;ng kẹp cổ anh tạ &ocirc;ng rất to khỏe v&agrave; c&aacute;i kh&oacute;a ấy giữ chặt Frank; Frank v&ugrave;ng vẫy để tho&aacute;t ra nhưng rồi đột ngột sự chống trả của anh tan biến v&agrave; anh lắc đầu ra dấu khuất phục - Meggie quỵ xuống s&agrave;n nh&agrave;, quỳ gối v&agrave; kh&oacute;c, mắt hết nh&igrave;n anh rồi nh&igrave;n cha với sự van xin bất lực. C&ocirc; b&eacute; kh&ocirc;ng hiểu chuyện g&igrave; đ&atilde; xảy ra, nhưng biết rằng sau chuyện n&agrave;y th&igrave; m&igrave;nh sẽ kh&ocirc;ng c&ograve;n giữ đủ cả hai.</h2>\r\n\r\n<h2>- Đừng chối, thật sự &ocirc;ng muốn n&oacute;i ra điều ấy. Đ&aacute;ng l&yacute; t&ocirc;i đ&atilde; biết điều đ&oacute;. Cha h&atilde;y bu&ocirc;ng t&ocirc;i ra. T&ocirc;i kh&ocirc;ng đụng tới &ocirc;ng ta đ&acirc;u. Cầu xin Ch&uacute;a gi&uacute;p đỡ t&ocirc;i.</h2>\r\n\r\n<h2>- Cầu xin Ch&uacute;a gi&uacute;p đỡ c&aacute;c người? Cầu Ch&uacute;a khiến cả hai mục xương dưới địa ngục! Nếu c&aacute;c người l&agrave;m chấn thương t&acirc;m thần con b&eacute; n&agrave;y, t&ocirc;i sẽ giết c&aacute;c người. - Cha Ralph lớn tiếng, người duy nhất b&acirc;y giờ nổi giận - C&aacute;c người c&oacute; biết rằng t&ocirc;i đ&atilde; giữ con b&eacute; ở lại đ&acirc;y l&agrave; để ngăn cản cả hai cấu x&eacute; nhau chớ đ&acirc;u phải để nghe những điều tồi tệ ấy. Đ&aacute;ng l&yacute; t&ocirc;i cứ để hai người tha hồ giết nhau, đồ &iacute;ch kỷ, ngu đần v&agrave; khốn nạn.</h2>\r\n\r\n<h2>- Th&ocirc;i được rồi, t&ocirc;i đi đ&acirc;y - Frank n&oacute;i bằng một giọng xa lạ, trống rỗng. - T&ocirc;i sẽ sung v&agrave;o đo&agrave;n của Jimmy Sharman v&agrave; vĩnh viễn t&ocirc;i sẽ kh&ocirc;ng trở lại đ&acirc;y.</h2>\r\n\r\n<h2>- Con phải trở lại đ&acirc;y! Pađy n&oacute;i lớn - Ba sẽ ăn n&oacute;i sao đ&acirc;y với mẹ con? Mẹ con y&ecirc;u con hơn tất cả những người kh&aacute;c cộng lại. B&agrave; ấy sẽ kh&ocirc;ng bao giờ tha thứ cho bạ</h2>\r\n\r\n<h2>- &ocirc;ng cứ n&oacute;i l&agrave; t&ocirc;i đ&atilde; sung v&agrave;o đội Jimmy Sharman v&igrave; t&ocirc;i muốn trở th&agrave;nh một người c&oacute; t&ecirc;n tuổi. Đ&oacute; l&agrave; sự thật.</h2>\r\n\r\n<h2>- Điều m&agrave; ba đ&atilde; n&oacute;i... kh&ocirc;ng đ&uacute;ng sự thật đ&acirc;u Frank.</h2>\r\n\r\n<h2>Đ&ocirc;i mắt đen, đ&ocirc;i mắt xa lạ của Frank n&eacute;m một tia nh&igrave;n khinh miệt. Ch&iacute;nh đ&oacute; l&agrave; đ&ocirc;i mắt g&acirc;y ngạc nhi&ecirc;n cho cha Ralph ở lần đầu &ocirc;ng gặp ch&agrave;ng thanh ni&ecirc;n. Tại sao Fiona th&igrave; mắt n&acirc;u c&ograve;n Pađy mắt xanh lại đẻ ra đứa con trai mắt đen? Cha Ralph biết định luật di truyền Medel, do đ&oacute; bản th&acirc;n &ocirc;ng đ&atilde; r&uacute;t ra kết luận.</h2>\r\n\r\n<h2>Frank lấy &aacute;o măng t&ocirc; v&agrave; n&oacute;n.</h2>\r\n\r\n<h2>- Trời ơi, đ&uacute;ng như thế! T&ocirc;i vẫn nghi ngờ điều ấy. T&ocirc;i vẫn nhớ h&igrave;nh ảnh mẹ t&ocirc;i ngồi chơi đ&agrave;n Claxeven trong một ph&ograve;ng m&agrave; &ocirc;ng kh&ocirc;ng l&agrave;m sao c&oacute; được! T&ocirc;i vẫn linh cảm l&agrave; &ocirc;ng kh&ocirc;ng hề c&oacute; mặt ở đ&oacute;, &ocirc;ng đ&atilde; đến với mẹ t&ocirc;i sau t&ocirc;i. V&agrave; khi nhớ lại suốt bao năm th&aacute;ng t&ocirc;i th&ugrave; gh&eacute;t &ocirc;ng v&igrave; &ocirc;ng h&agrave;nh hạ mẹ t&ocirc;i. Ch&iacute;nh tại t&ocirc;i!</h2>\r\n\r\n<h2>- Kh&ocirc;ng tại ai hết, Frank ạ, kh&ocirc;ng do ai hết. - Cha Ralph cố xoa dịu Frank - Con kh&ocirc;ng hiểu hết những con đường b&iacute; ẩn của ch&uacute;a, con phải nh&igrave;n c&aacute;c sự việc theo c&aacute;ch đ&oacute;.</h2>\r\n\r\n<h2>Frank r&uacute;t tay ra khỏi b&agrave;n tay cha Ralph đang nắm chặt anh, rồi đi ra cửa bằng những bước nhẹ nh&agrave;ng, &ecirc;m ả - Kh&ocirc;ng thế chối c&atilde;i anh ta sinh ra đ&atilde; l&agrave; v&otilde; sĩ - Cha Ralph nghĩ thầm.</h2>\r\n\r\n<h2>- Những con đường b&iacute; ẩn của Ch&uacute;a! ch&agrave;ng thanh ni&ecirc;n mỉa mai, giọng n&oacute;i vang lại từ ngo&agrave;i cửa - Cha chỉ l&agrave; một sinh vật khi cha đ&oacute;ng vai linh mục, thưa cha De Bricassart! Ri&ecirc;ng t&ocirc;i, t&ocirc;i n&oacute;i Ch&uacute;a đ&atilde; đến cứu gi&uacute;p cha, cứu gi&uacute;p ri&ecirc;ng cha ma th&ocirc;i, v&igrave; rằng cha l&agrave; người duy nhất ở đ&acirc;y kh&ocirc;ng mảy may biết một ch&uacute;t n&agrave;o về con người thật ấy.</h2>\r\n\r\n<h2>Pađy ngồi sụp xuống tr&ecirc;n chiếc ghế b&agrave;nh, mặt x&aacute;m xịt, nhớn nh&aacute;c, mắt nh&igrave;n Meggie tr&acirc;n tr&acirc;n, trong khi đ&oacute; Meggie ngồi trước l&ograve; sưởi kh&oacute;c sướt mướt. Pađy định đứng dậy &ocirc;m con g&aacute;i m&igrave;nh nhưng cha Ralph chăn lại.</h2>\r\n\r\n<h2>- H&atilde;y để con b&eacute; y&ecirc;n. &ocirc;ng g&acirc;y cho n&oacute; bao nhi&ecirc;u đớn đau như thế đủ rồi. &ocirc;ng c&oacute; thể t&igrave;m rượu whisky trong tủ v&agrave; uống v&agrave;i hớp đi. T&ocirc;i đưa con b&eacute; v&agrave;o ph&ograve;ng cho n&oacute; nghỉ, nhưng t&ocirc;i sẽ trở lại n&oacute;i chuyện với &ocirc;ng. Vậy &ocirc;ng đừng rời khỏi đ&acirc;y. &ocirc;ng nghe t&ocirc;i n&oacute;i chứ?</h2>\r\n\r\n<h2>- T&ocirc;i chờ cha, thưa chạ Cha đặt Meggie l&ecirc;n giường d&ugrave;m t&ocirc;i.</h2>\r\n\r\n<h2>Tr&ecirc;n g&aacute;c, trong căn ph&ograve;ng sơn m&agrave;u xanh nhạt xinh xắn, linh mục cởi chiếc &aacute;o ngo&agrave;i v&agrave; bộ đồ b&oacute; s&aacute;t b&ecirc;n trong của Meggie v&agrave; đặt c&ocirc; b&eacute; ngồi tr&ecirc;n giường, để cởi gi&agrave;y vớ. Chiếc &aacute;o ngủ đ&atilde; được b&agrave; quản gia Annie để sẵn tr&ecirc;n gối! Linh mục tr&ograve;ng &aacute;o qua cổ v&agrave; k&eacute;o phủ xuống th&acirc;n người Meggiẹ Sau đ&oacute;, mới cở quần l&oacute;t cho Meggiẹ Trong thời gian n&agrave;y, cha Ralph kể đủ thứ chuyện đ&acirc;u đ&acirc;u cho c&ocirc; b&eacute; nghe, n&agrave;o l&agrave; chuyện tầm ph&agrave;o về những c&aacute;i n&uacute;t kh&oacute; cởi, những sợi d&acirc;y gi&agrave;y kh&oacute; bảo, những dải rubăng th&aacute;o m&atilde;i kh&ocirc;ng ra. Đ&ocirc;i mắt c&ocirc; b&eacute; c&ograve;n đầy n&eacute;t kinh ho&agrave;ng thơ ng&acirc;y kh&ocirc;ng giải th&iacute;ch được, tất cả rối ren v&agrave; đau đớn vượt sức chịu đựng của tuổi c&ocirc; b&eacute;. Meggie như rơi v&agrave;o khoảng kh&ocirc;ng - B&acirc;y giờ h&atilde;y nằm d&agrave;i ra, con y&ecirc;u qu&iacute; của cha v&agrave; r&aacute;ng nhắm mắt ngủ. Cha sẽ trở lại ngay để thăm con. Đừng lo sợ g&igrave;. Ch&uacute;ng ta sẽ trở lại tất cả những chuyện n&agrave;y v&agrave;o ng&agrave;y mai.</h2>\r\n\r\n<h2>Pađy hỏi cha Ralph khi linh mục trở lại ph&ograve;ng kh&aacute;ch:</h2>\r\n\r\n<h2>- Con b&eacute; đ&atilde; b&igrave;nh y&ecirc;n chưa?</h2>\r\n\r\n<h2>Linh mục đưa tay lấy chai rượu whisky tr&ecirc;n tủ v&agrave; r&oacute;t nửa lỵ</h2>\r\n\r\n<h2>- Th&agrave;nh thật m&agrave; n&oacute;i, t&ocirc;i chưa biết ra sao. C&oacute; Ch&uacute;a chứng gi&aacute;m, t&ocirc;i muốn hiểu sự bất hạnh tệ hại nhất của người Ailen l&agrave; từ đ&acirc;u: rượu hay c&aacute;i t&iacute;nh hay nổi giận bất thường... C&aacute;i g&igrave; đ&atilde; x&uacute;i giục &ocirc;ng n&oacute;i l&ecirc;n điều ấy? Th&ocirc;i, giải th&iacute;ch cũng bằng thừa. C&aacute;i t&iacute;nh kh&iacute; bất thường! Tất nhi&ecirc;n l&agrave; thế. T&ocirc;i đ&atilde; biết ngay Frank kh&ocirc;ng phải l&agrave; con của &ocirc;ng khi lần đầu t&ocirc;i gặp n&oacute;.</h2>\r\n\r\n<h2>- Kh&ocirc;ng c&oacute; g&igrave; tho&aacute;t khỏi cặp mắt của chạ</h2>\r\n\r\n<h2>- Kh&ocirc;ng phải thế. Nhưng chỉ cần c&oacute; ch&uacute;t &oacute;c quan s&aacute;t trung b&igrave;nh th&ocirc;i cũng đủ ph&aacute;t hiện những chuyện buồn phiền hay đau khổ m&agrave; một số con chi&ecirc;n của Ch&uacute;a phải g&aacute;nh chịu.</h2>\r\n\r\n<h2>- Phải chăng cha c&oacute; được nhiều người ở Gilly y&ecirc;u mến?</h2>\r\n\r\n<h2>- C&oacute; lẽ t&ocirc;i được như thế nhờ ở gương mặt v&agrave; v&oacute;c d&aacute;ng - linh mục Ralph trả lời một c&aacute;ch chua ch&aacute;t kh&ocirc;ng thể giữ giọng m&igrave;nh tự nhi&ecirc;n như &yacute; muốn.</h2>\r\n\r\n<h2>- Cha nghĩ như thế &agrave;? &yacute; kiến con lại kh&aacute;c, thưa chạ Ch&uacute;ng con y&ecirc;u mến cha v&igrave; cha l&agrave; một đấng chăn chi&ecirc;n tốt.</h2>\r\n\r\n<h2>đ&ugrave; sao th&igrave; t&ocirc;i d&iacute;nh l&iacute;u qu&aacute; s&acirc;u v&agrave;o những rắc rối của gia đ&igrave;nh &ocirc;ng - Cha Ralph n&oacute;i hơi l&uacute;ng t&uacute;ng - &ocirc;ng n&ecirc;n r&uacute;t ra hết đi những g&igrave; đ&egrave; nặng trong l&ograve;ng, &ocirc;ng bạn ạ.</h2>\r\n\r\n<h2>Pađy vẫn nh&igrave;n chăm chăm ngọn lửa đang ch&aacute;y rực. L&uacute;c n&atilde;y khi cha Ralph bế Meggie l&ecirc;n lầu v&agrave; đặt c&ocirc; b&eacute; l&ecirc;n giường &ocirc;ng đ&atilde; n&eacute;m v&agrave;o l&ograve; sưởi th&ecirc;m nhiều kh&uacute;c củi, l&ograve;ng bị gi&agrave;y x&eacute;o bởi sự ăn năn v&agrave; một đ&ograve;i hỏi dữ dội muốn l&agrave;m một điều g&igrave; đ&oacute;. C&aacute;i ly rượu như nhảy m&uacute;a tr&ecirc;n tay &ocirc;ng, bởi &ocirc;ng chưa qua cơn x&uacute;c động v&agrave; run bật l&ecirc;n từng chặp. Cha Ralph đứng l&ecirc;n, lấy chai rượu v&agrave; r&oacute;t đầy v&agrave;o ly của Pađỵ Sau khi nốc một hơi, Pađy thở d&agrave;i v&agrave; lau những giọt nước mắt tr&ecirc;n m&aacute; l&uacute;c n&agrave;o &ocirc;ng kh&ocirc;ng haỵ</h2>\r\n\r\n<h2>- Con kh&ocirc;ng hề biết cha của Frank l&agrave; ai. Chuyện xảy ra trước khi con gặp Fionạ Gia đ&igrave;nh của vợ con l&agrave; một gia đ&igrave;nh danh gi&aacute; ở T&acirc;y T&acirc;y Lan. Cha của Fiona c&oacute; một điền trang rộng lớn trồng l&uacute;a m&igrave; v&agrave; nu&ocirc;i cừu, ở gần Ashburton tr&ecirc;n Nam Đảo. Tiền nhiều kh&ocirc;ng kể xiết. Fiona l&agrave; con g&aacute;i duy nhất của &ocirc;ng ấy. Theo như con biết, &ocirc;ng ấy đ&atilde; lo trước cho Fiona đủ cả, n&agrave;o l&agrave; sang Anh, ra mắt ở triều đ&igrave;nh, v&agrave; lấy một người chồng xứng đ&aacute;ng. Dĩ nhi&ecirc;n Fiona kh&ocirc;ng động một m&oacute;ng tay v&agrave;o c&aacute;c c&ocirc;ng việc trong nh&agrave;. C&ocirc; ta c&oacute; nhiều người phục vụ... Gia đ&igrave;nh sống như vua ch&uacute;a.</h2>\r\n\r\n<h2>Con l&agrave;m c&ocirc;ng ở một h&atilde;ng sữa, đ&ocirc;i khi cũng thấy Fiona từ xạ C&ocirc; ấy đi dạo với một thằng b&eacute; khoảng 18 th&aacute;ng. Rồi một h&ocirc;m, l&atilde;o James Armstrong t&igrave;m con. &ocirc;ng cho con biết l&agrave; con g&aacute;i của &ocirc;ng đ&atilde; l&agrave;m nhục gia đ&igrave;nh định đưa c&ocirc; ấy đi đến một nơi xa th&igrave; b&agrave; ngoại của Fiona chống lại một c&aacute;ch ồn &agrave;o đến mức gia đ&igrave;nh buộc l&ograve;ng vẫn để Fiona ở đ&oacute; mặc d&ugrave; c&oacute; vẻ bất tiện. Nhưng sau n&agrave;y b&agrave; ngoại sắp qua đời, kh&ocirc;ng c&ograve;n ai ngăn cản việc đuổi Fiona v&agrave; đứa con trai ra khỏi nh&agrave;. Con c&ograve;n độc th&acirc;n v&agrave; James bảo con nếu chịu lấy con g&aacute;i của &ocirc;ng l&agrave;m vợ v&agrave; cam đoan chắc chắn với &ocirc;ng ta l&agrave; sẽ đưa con g&aacute;i đi khỏi Nam Đảo th&igrave; &ocirc;ng sẽ chịu mọi tổn ph&iacute; v&agrave; biếu th&ecirc;m một số tiền l&agrave; 500 bảng.</h2>\r\n\r\n<h2>Thưa cha, đ&oacute; l&agrave; cả một t&agrave;i sản đối với con, hơn nữa con đ&atilde; ch&aacute;n c&aacute;i cảnh độc th&acirc;n. Bao giờ con cũng nh&uacute;t nh&aacute;t, kh&ocirc;ng l&agrave;m được chuyện g&igrave; n&ecirc;n th&acirc;n trong quan hệ với ph&aacute;i nữ. &yacute; kiến ấy rất tốt đối với con v&agrave; thẳng thắng m&agrave; n&oacute;i, đứa b&eacute; kh&ocirc;ng g&acirc;y kh&oacute; ch&uacute;t n&agrave;o cho con. B&agrave; ngoại của Fiona cuối c&ugrave;ng đo&aacute;n ra được việc n&agrave;y v&agrave; cho người đi t&igrave;m con. L&uacute;c bấy giờ b&agrave; đ&atilde; bệnh nặng. B&agrave; l&agrave; một người c&oacute; uy quyền, t&iacute;nh t&igrave;nh rất kh&oacute; chịu, nhưng con quả quyết với cha, đ&oacute; l&agrave; một người phụ nữ rất đ&aacute;ng k&iacute;nh trọng. B&agrave; kể cho con nghe một ch&uacute;t về Fiona, nhưng vẫn kh&ocirc;ng n&oacute;i ai l&agrave; cha của thằng b&eacute;. Phần con cũng thấy kh&ocirc;ng cần thiết t&igrave;m hiểu xa hơn. B&agrave; buộc con hứa với b&agrave; ấy sẽ đối xử tối với ch&aacute;u g&aacute;i của b&agrave;... B&agrave; biết rằng Fiona sẽ rời khỏi nh&agrave; ngay khi b&agrave; nhắm mắt. V&agrave; ch&iacute;nh b&agrave; đ&atilde; đưa ra &yacute; kiến với James n&ecirc;n t&igrave;m cho Fiona một người chồng. Con rất tội nghiệp cho b&agrave; cụ; b&agrave; rất y&ecirc;u thương ch&aacute;u g&aacute;i của b&agrave;.</h2>\r\n\r\n<h2>Cha c&oacute; tin hay kh&ocirc;ng nếu con kể cho cha biết rằng lần đầu ti&ecirc;n con đến gần Fiona, ở khoảng c&aacute;ch nghe được tiếng n&oacute;i của c&ocirc; ấy, th&igrave; đ&oacute; l&agrave; ng&agrave;y cưới?</h2>\r\n\r\n<h2>- T&ocirc;i tin &ocirc;ng - cha Ralph n&oacute;i khẽ - Pađy, thế l&agrave; &ocirc;ng đ&atilde; cưới một người vợ c&oacute; điều kiện tr&ecirc;n &ocirc;ng rất nhiều.</h2>\r\n\r\n<h2>- V&acirc;ng. L&uacute;c ấy con rất sợ c&ocirc; ấy, sợ đến chết được. C&ocirc; ta đẹp qu&aacute;, thưa cha... V&agrave; cũng rất... xa x&ocirc;i, chắc cha hiểu con muốn n&oacute;i g&igrave;. Tưởng như c&ocirc; ta kh&ocirc;ng hiện diện trong nh&agrave; m&agrave; l&agrave; một người n&agrave;o kh&aacute;c ngồi đ&oacute;.</h2>\r\n\r\n<h2>- B&acirc;y giờ b&agrave; ấy vẫn c&ograve;n đẹp, &ocirc;ng Pađy ạ. Cha Ralph dịu d&agrave;ng n&oacute;i. T&ocirc;i c&oacute; thể nh&igrave;n thấy ở Meggie h&igrave;nh ảnh của Fiona trước kia.</h2>\r\n\r\n<h2>- Cuộc sống d&agrave;nh cho Fiona kh&ocirc;ng phải dễ d&agrave;ng thưa cha, như cha biết đấy, kh&ocirc;ng thể n&agrave;o kh&aacute;c hơn được. Với con, &iacute;t nhất c&ocirc; ấy được an to&agrave;n, kh&ocirc;ng sợ bị đối xử tệ. Phải chờ đến hai năm sau con mới d&aacute;m... n&oacute;i thẳng ra, mới d&aacute;m trở th&agrave;nh chồng thật sự của c&ocirc; ấy. Con buộc phải tập c&ocirc; ấy l&agrave;m bếp, qu&eacute;t nh&agrave;, giặt v&agrave; ủi quần &aacute;o. Trước kia c&ocirc; ấy kh&ocirc;ng biết l&agrave;m g&igrave; hết.</h2>\r\n\r\n<h2>Qua suốt bao năm th&aacute;ng d&agrave;i m&agrave; ch&uacute;ng con ở với nhau, chưa bao giờ c&ocirc; ấy h&eacute; m&ocirc;i một lần thở than nhưng đồng thời c&ocirc; ấy cũng kh&ocirc;ng bao giờ cười hoặc kh&oacute;c. Chỉ trong những l&uacute;c hết sức ri&ecirc;ng tư, trong t&igrave;nh vợ chồng, c&ocirc; ấy mới để lộ ra những x&uacute;c động của m&igrave;nh, nhưng ngay l&uacute;c đ&oacute; c&ocirc; ấy cũng kh&ocirc;ng n&oacute;i lời n&agrave;o. Con rất muốn nghe c&ocirc; ấy t&acirc;m t&igrave;nh v&agrave; đồng thời lại rất sợ v&igrave; con vẫn cảm gi&aacute;c rằng nếu c&ocirc; ấy t&acirc;m t&igrave;nh th&igrave; kh&ocirc;ng thể tr&aacute;nh nhắc tới người ấy. Kh&ocirc;ng! Con kh&ocirc;ng nghĩ rằng c&ocirc; ấy đối xử kh&ocirc;ng tốt với con v&agrave; mấy đứa nhỏ. Con đ&atilde; d&agrave;nh cho c&ocirc; ấy tất cả sự tr&igrave;u mến nhưng h&igrave;nh như t&igrave;nh y&ecirc;u đ&atilde; tắt trong l&ograve;ng c&ocirc; ấy tự bao giờ. Ngoại trừ đối với Frank. Con đo&aacute;n chắc rằng c&ocirc; ấy y&ecirc;u thương Frank hơn tất cả ch&uacute;ng con cộng lại. C&oacute; lẽ xưa kia c&ocirc; ấy rất y&ecirc;u cha của Frank. Nhưng con kh&ocirc;ng biết g&igrave; về con người ấy, hắn ta l&agrave; ai m&agrave; tại sao hắn kh&ocirc;ng thể cưới Fionạ</h2>\r\n\r\n<h2>Cha nh&igrave;n hai b&agrave;n tay m&igrave;nh, rồi nheo mắt.</h2>\r\n\r\n<h2>- Ồ, Pađy, cuộc đời quả l&agrave; khổ cực l&agrave;m sao! Nhờ ơn Ch&uacute;a m&agrave; t&ocirc;i đ&atilde; kh&ocirc;ng d&aacute;m đi gần hơn c&aacute;i r&igrave;a của cuộc sống. T&ocirc;i kh&ocirc;ng c&oacute; can đảm &ocirc;m lấy cuộc sống thật sự trong v&ograve;ng tay m&igrave;nh.</h2>\r\n\r\n<h2>Pađy đứng l&ecirc;n loạng choạng, hai ch&acirc;n kh&ocirc;ng vững.</h2>\r\n\r\n<h2>- Thế đ&oacute;, lần n&agrave;y th&igrave; con đ&atilde; l&agrave;m một điều dại dột. Con đ&atilde; &eacute;p buộc Frank đi khỏi nh&agrave;. Fiona sẽ kh&ocirc;ng bao giờ tha thứ con.</h2>\r\n\r\n<h2>- &ocirc;ng kh&ocirc;ng n&ecirc;n n&oacute;i với b&agrave; ấy, Pađy &agrave;. Kh&ocirc;ng, kh&ocirc;ng n&ecirc;n n&oacute;i. Cứ giải th&iacute;ch đơn giản với b&agrave; rằng Frank đ&atilde; đi theo đo&agrave;n v&otilde; sĩ, thế th&ocirc;i. B&agrave; ấy dư biết rằng từ l&acirc;u t&iacute;nh t&igrave;nh Frank đ&atilde; như thế v&agrave; b&agrave; ấy sẽ tin &ocirc;ng.</h2>\r\n\r\n<h2>- Con kh&ocirc;ng thể n&oacute;i dối như thế, thưa cha! Pađy k&ecirc;u l&ecirc;n, sững sờ.</h2>\r\n\r\n<h2>- Cần n&oacute;i như thế, Pađỵ B&agrave; ấy đ&atilde; đau khổ v&agrave; bị dằn vặt như thế chưa đủ sao? Đừng g&acirc;y th&ecirc;m bất hạnh cho b&agrave; ấy nữa.</h2>\r\n\r\n<h2>Trong l&ograve;ng m&igrave;nh, cha Ralph nghĩ: biết đ&acirc;u? - Rất c&oacute; thể cuối c&ugrave;ng b&agrave; ấy sẽ dồn t&igrave;nh y&ecirc;u d&agrave;nh cho Frank sang &ocirc;ng v&agrave; cho con người b&eacute; nhỏ đang nằm tr&ecirc;n lầu...</h2>\r\n\r\n<h2>- Cha tin như thế?</h2>\r\n\r\n<h2>- Rất c&oacute; thể: Nhưng chuyện xảy ra tối nay phải được giữ b&iacute; mật giữa hai ch&uacute;ng tạ</h2>\r\n\r\n<h2>- Nhưng c&ograve;n... Meggiẻ Con b&eacute; đ&atilde; nghe hết.</h2>\r\n\r\n<h2>- &ocirc;ng đừng lo về phần Meggiẹ T&ocirc;i sẽ liệu. T&ocirc;i kh&ocirc;ng nghĩ rằng Meggie hiểu được c&acirc;u chuyện xảy ra. N&oacute; sẽ nghĩ chỉ l&agrave; một cuộc c&atilde;i v&atilde; giữa &ocirc;ng v&agrave; Frank. T&ocirc;i sẽ t&igrave;m c&aacute;ch l&agrave;m cho Meggie hiểu rằng kể lại cho mẹ n&oacute; nghe chuyện c&atilde;i v&atilde; giữa cha v&agrave; anh chỉ l&agrave;m cho mẹ đau khổ th&ecirc;m. Hơn nữa theo t&ocirc;i nhận x&eacute;t, Meggie cũng &iacute;t khi n&oacute;i chuyện với mẹ n&oacute; (&ocirc;ng đứng l&ecirc;n). &ocirc;ng h&atilde;y đi ngủ đi Pađỵ Nhớ đừng qu&ecirc;n giữ cho sắc mặt của m&igrave;nh thật b&igrave;nh thường v&agrave;o s&aacute;ng mai khi &ocirc;ng trở lại l&agrave;m việc với nữ ho&agrave;ng Marỵ</h2>\r\n\r\n<h2>Meggie vẫn kh&ocirc;ng ngủ, nằm d&agrave;i, hai mắt mở to dưới &aacute;nh s&aacute;ng của ngọn đ&egrave;n đặt ở đầu giường. Linh mục ngồi ở m&eacute;p giường v&agrave; nhận ra rằng t&oacute;c của c&ocirc; b&eacute; vẫn c&ograve;n thắt b&iacute;m. Một c&aacute;ch thận trọng, &ocirc;ng th&aacute;o chiếc rubang m&agrave;u xanh nước biển v&agrave; nhẹ nh&agrave;ng gở từng lọn t&oacute;c cho đến khi cả m&aacute;i t&oacute;c dợn s&oacute;ng, v&agrave;ng &oacute;ng ả nằm trải ra gối.</h2>\r\n\r\n<h2>- Anh Frank đ&atilde; đi rồi, Meggie - &ocirc;ng n&oacute;i.</h2>\r\n\r\n<h2>- Con biết, thưa chạ</h2>\r\n\r\n<h2>- Con c&oacute; biết tại sao kh&ocirc;ng, con th&acirc;n y&ecirc;u?</h2>\r\n\r\n<h2>- C&oacute; chuyện ẩu đả với bạ</h2>\r\n\r\n<h2>- Con t&iacute;nh sao?</h2>\r\n\r\n<h2>- Con sẽ đi theo Frank. Anh ấy cần con.</h2>\r\n\r\n<h2>- Kh&ocirc;ng thể được, Meggie b&eacute; nhỏ của chạ</h2>\r\n\r\n<h2>- Được chứ thưa chạ Con muốn đi t&igrave;m anh ấy đ&ecirc;m nay, nhưng đ&ocirc;i ch&acirc;n của con r&atilde; rời v&agrave; con rất sợ b&oacute;ng đ&ecirc;m. Nhưng s&aacute;ng mai, con sẽ đi t&igrave;m anh ấy.</h2>\r\n\r\n<h2>- Kh&ocirc;ng n&ecirc;n Meggie ạ. Con phải biết, Frank c&oacute; cuộc sống của anh ấy, v&agrave; đ&atilde; đến l&uacute;c anh ấy phải ra đi. Cha biết l&agrave; con kh&ocirc;ng muốn cho anh ấy đi, nhưng đ&atilde; từ l&acirc;u, anh ấy muốn thế. Con kh&ocirc;ng n&ecirc;n &iacute;ch kỷ. Phải để cho anh ấy chọc cuộc sống theo &yacute; th&iacute;ch. Khi ch&uacute;ng ta lớn l&ecirc;n, &yacute; muốn được sống ở ngo&agrave;i c&aacute;i gia đ&igrave;nh ma ta đ&atilde; được nu&ocirc;i nấng l&agrave; chuyện b&igrave;nh thường v&agrave; đ&uacute;ng đắn. Frank đ&atilde; trưởng th&agrave;nh, con c&oacute; hiểu những lời cha n&oacute;i kh&ocirc;ng Meggiẹ</h2>\r\n\r\n<h2>Đ&ocirc;i mắt c&ocirc; b&eacute; nh&igrave;n thẳng v&agrave;o mặt linh mục. Một đ&ocirc;i mắt mỏi mệt, đau đớn v&agrave; kh&ocirc;ng c&ograve;n ch&uacute;t n&agrave;o l&agrave; trẻ con.</h2>\r\n\r\n<h2>- Con biết, c&ocirc; b&eacute; th&igrave; thầm. Frank đ&atilde; muốn đi từ l&acirc;u khi con c&ograve;n nhỏ x&iacute;u. Nhưng rồi anh ấy kh&ocirc;ng đi được. Ba đ&atilde; bắt anh đem trở về nh&agrave; v&agrave; buộc anh ấy tiếp tục sống với tụi con.</h2>\r\n\r\n<h2>- Nhưng lần n&agrave;y th&igrave; ba con kh&ocirc;ng thể đem anh ấy về nữa, Frank đ&atilde; đi thật sự rồi Meggiẹ Anh ấy sẽ kh&ocirc;ng trở về.</h2>\r\n\r\n<h2>- Con sẽ kh&ocirc;ng bao giờ gặp lại anh ấy nữa sao?</h2>\r\n\r\n<h2>- Cha kh&ocirc;ng biết được - cha Ralph trả lời th&agrave;nh thật - Cha vẫn muốn n&oacute;i với con để con y&ecirc;n l&ograve;ng l&agrave; anh ấy sẽ trở về, nhưng kh&ocirc;ng ai c&oacute; thể đo&aacute;n được tương lai, Meggie, kể cả c&aacute;c linh mục. Con kh&ocirc;ng n&ecirc;n n&oacute;i cho mẹ con biết chuyện c&atilde;i v&atilde; giữa ba v&agrave; anh Frank, nghe kh&ocirc;ng Meggiẻ Điều đ&oacute; sẽ l&agrave;m cho mẹ th&ecirc;m đau buồn; mẹ con hiện kh&ocirc;ng được khỏe.</h2>\r\n\r\n<h2>- Bởi v&igrave; mẹ sắp c&oacute; em b&eacute;?</h2>\r\n\r\n<h2>- Sao con biết.</h2>\r\n\r\n<h2>- Mẹ rất th&iacute;ch l&agrave;m nở ra c&aacute;c em b&eacute;. V&agrave; mẹ đ&atilde; cho nở nhiều em b&eacute; rất dễ thương, thưa chạ Ngay cả khi mẹ kh&ocirc;ng được khỏe. Con sẽ cho nở ra một em b&eacute; như Hal. Như thế con sẽ thấy bớt nhớ Frank hơn.</h2>\r\n\r\n<h2>- Sự sinh sản - cha Ralph n&oacute;i một m&igrave;nh. Ch&uacute;c con may mắn, Meggiẹ Nhưng chuyện g&igrave; xảy ra nếu con kh&ocirc;ng l&agrave;m nở ra được một em b&eacute;?</h2>\r\n\r\n<h2>- Con vẫn c&oacute; Hal, giọng n&oacute;i của Meggie đầy vẻ buồn ngủ, đầu n&eacute;p v&agrave;o gối, cha ơi, c&ograve;n cha sẽ kh&ocirc;ng đi chứ?</h2>\r\n\r\n<h2>- Một ng&agrave;y n&agrave;o đ&oacute;, rất c&oacute; thể; nhưng b&acirc;y giờ th&igrave; chưa, Meggie ạ, con đừng lo nghĩ. Cha c&oacute; cảm gi&aacute;c l&agrave; cha sẽ ở Gilly l&acirc;u, rất l&acirc;u - Cha Ralph trả lời, hai mắt vương buồn.</h2>', 0, 'tieng-chim-hot-trong-bui-man-gai-chuong-07-08');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(48, 25, 'Tiếng chim hót trong bụi mận gai - Chương 09-10', 'Tiếng chim hót trong bụi mận gai - Chương 09-10', '<h2>Kh&ocirc;ng thể kh&aacute;c hơn, Meggie phải trở về nh&agrave;. Fiona cần sự gi&uacute;p đỡ của c&ocirc; b&eacute;. Stuart c&ograve;n lại một m&igrave;nh ở tu viện Gilly, bắt đầu một cuộc tuyệt thực, cuối c&ugrave;ng cũng đ&atilde; trở về nh&agrave;.</h2>\r\n\r\n<h2>Khi gặp lại mẹ, c&ocirc; b&eacute; tưởng như bị một sức nặng khủng khiếp đ&egrave; nặng người m&igrave;nh; c&ocirc; c&oacute; biết đ&acirc;u đ&oacute; l&agrave; dấu hiệu của sự rời bỏ tuổi thơ v&agrave; l&agrave; sự cảm nhận rất sớm th&acirc;n phận người phụ nữ.</h2>\r\n\r\n<h2>Bề ngo&agrave;i, Fiona kh&ocirc;ng thay đổi, ngoại trừ c&aacute;i bụng, nhưng b&ecirc;n trong, tất cả đều chậm lại như một chiếc đồng hồ treo tường cũ kỹ với c&aacute;i quả lắc mỗi l&uacute;c một mỏi mệt cho đến khi ngừng hẳn.</h2>\r\n\r\n<h2>Ch&uacute; be con t&oacute;c m&agrave;u hung chạy lăng xăng khắp nh&agrave; đụng v&agrave;o b&agrave;n ghế, nhưng Fiona kh&ocirc;ng &eacute;p n&oacute; v&agrave;o một khu&ocirc;n khổ kỷ luật n&agrave;o. B&agrave;n vẫn tiếp tục c&ocirc;ng việc như l&acirc;u nay - đi lại giữa nh&agrave; bếp, nơi giặt quần &aacute;o v&agrave; bồn rửa ch&eacute;n - như tr&ecirc;n đời kh&ocirc;ng c&ograve;n g&igrave; kh&aacute;c hơn để l&agrave;m.</h2>\r\n\r\n<h2>Kh&ocirc;ng ai nhắc tới t&ecirc;n của Frank nhưng cứ s&aacute;u tuần một lần, Fiona ngước nh&igrave;n l&ecirc;n khi nghe tiếng k&egrave;n đồng b&aacute;o hiệu chiếc xe ph&aacute;t thư đến. B&agrave; vui l&ecirc;n một l&uacute;c. Nhưng khi b&agrave; Smith đ&atilde; trao thư từ cho Fiona v&agrave; kh&ocirc;ng thấy c&oacute; thư n&agrave;o của Frank th&igrave; nỗi x&oacute;t xa lại x&acirc;m chiếm lấy b&agrave; v&agrave; tắt lịm đi.</h2>\r\n\r\n<h2>C&oacute; th&ecirc;m hai người mới trong nh&agrave;. Fiona cho ra đời một l&uacute;c hai đứa con trai t&oacute;c hung. James v&agrave; Patrick m&agrave; trong nh&agrave; gọi một c&aacute;ch th&acirc;n y&ecirc;u l&agrave; Jimes v&agrave; Patsỵ</h2>\r\n\r\n<h2>B&eacute; Hal th&igrave; chỉ biết c&oacute; Meggie, n&oacute; kh&ocirc;ng muốn ai kh&aacute;c.</h2>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"height:260px; width:86%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top\">\r\n			<h2><strong>Chương 10</strong></h2>\r\n\r\n			<h2>&nbsp;</h2>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Cha Ralph quan s&aacute;t Meggie một c&aacute;ch tr&igrave;u mến. C&ocirc; b&eacute; đang chải m&aacute;i t&oacute;c m&agrave;u hung v&agrave; uốn lọn cho Patsy đứng kh&ocirc;ng vững tr&ecirc;n hai ch&acirc;n b&eacute; nhỏ, trong khi Jimes đứng kế b&ecirc;n chờ đến phi&ecirc;n m&igrave;nh. Cả hai nh&igrave;n chị bằng c&aacute;i nh&igrave;n đầy thương y&ecirc;u. Đ&uacute;ng l&agrave; một b&agrave; mẹ t&iacute; hon. Tấm l&ograve;ng ấy phải l&agrave; một đức t&iacute;nh bẩm sinh ở Meggie - cha Ralph nghĩ thầm.</h2>\r\n\r\n<h2>Trong một l&uacute;c cha Ralph bị thu h&uacute;t bởi cảnh th&acirc;n mật dễ thương n&agrave;y. &ocirc;ng thả lỏng chiếc roi da xuốn đ&ocirc;i gi&agrave;y bốt đầy bụi, rồi đưa mắt u buồn nh&igrave;n qua hi&ecirc;n, hướng về t&ograve;a nh&agrave; lớn khuất sau những c&acirc;y bạch đ&agrave;n v&agrave; c&aacute;c thứ c&acirc;y leo kh&aacute;c, biệt lập với căn nh&agrave; của người quản l&yacute; v&agrave; cuộc sống c&ograve;n lại của điền trang.</h2>\r\n\r\n<h2>Giữa c&aacute;i mạng lưới nhện rộng bao la ấy, con nhện gi&agrave; đang toan t&iacute;nh giăng ra một kế n&agrave;o nữa?</h2>\r\n\r\n<h2>- Cha ơi, cha kh&ocirc;ng nh&igrave;n đ&acirc;y! Meggie k&ecirc;u l&ecirc;n tr&aacute;ch m&oacute;c.</h2>\r\n\r\n<h2>- Cha xin lỗi con! Cha đang suy nghĩ.</h2>\r\n\r\n<h2>&ocirc;ng quay lại nh&igrave;n Meggie đ&uacute;ng l&uacute;c c&ocirc; b&eacute; vừa chải xong cho Jims. Cả ba đang theo d&otilde;i &ocirc;ng, chờ c&aacute;i l&uacute;c &ocirc;ng nghi&ecirc;ng m&igrave;nh xuống để bế hai ch&uacute; b&eacute; con sinh đ&ocirc;i, mỗi đứa một b&ecirc;n h&ocirc;ng.</h2>\r\n\r\n<h2>- Ch&uacute;ng ta h&atilde;y c&ugrave;ng nhau thăm b&agrave; Marỵ &ocirc;ng n&oacute;i.</h2>\r\n\r\n<h2>Meggie đi theo sau cha Ralph tr&ecirc;n con đường dẫn đến ng&ocirc;i nh&agrave; lớn, một tay cầm c&acirc;y roi da của linh mục v&agrave; một tay dắt cương con ngựa c&aacute;i l&ocirc;ng hai m&agrave;u. C&ograve;n linh mục bế hai đứa b&eacute; kh&ocirc;ng c&oacute; vẻ g&igrave; mệt nhọc mặc d&ugrave; từ đ&oacute; đến t&ograve;a nh&agrave; lớn xa hơn một c&acirc;y số rưỡi. V&agrave;o ph&iacute;a sau nh&agrave; bếp, cha Ralph lần lượt trao hai đứa b&eacute; cho b&agrave; Smith đ&oacute;n nhận ch&uacute;ng một c&aacute;ch sung sướng. Cha đi thẳng ra nh&agrave; trước, Meggie theo b&ecirc;n cạnh.</h2>\r\n\r\n<h2>Mary Carson đang ngồi tr&ecirc;n chiếc ghế b&agrave;nh đặc biệt cho m&igrave;nh. L&uacute;c n&agrave;y, b&agrave; &iacute;t khi rời khỏi ghế, hơn nữa, sự c&oacute; mặt của b&agrave; cũng kh&ocirc;ng c&ograve;n cần thiết v&igrave; Pađy lo liệu tất cả một c&aacute;ch giỏi giang. Khi cha Ralph bước v&agrave;o, cầm tay Meggie, th&igrave; c&aacute;i nh&igrave;n đầy &aacute; c cảm của b&agrave; gi&agrave; buộc ngay c&ocirc; b&eacute; phải nh&igrave;n xuống đất; cha Ralph nghe được tiếng tim đập nhanh của Meggie, c&agrave;ng nắm chặt cổ tay của c&ocirc; b&eacute; truyền sang n&oacute; niềm an ủi v&agrave; sự &acirc;u yếm. C&ocirc; b&eacute; nghi&ecirc;ng người xuống ch&agrave;o một c&aacute;ch vụng về, miệng ấp &uacute;ng mấy c&acirc;u thưa gửi.</h2>\r\n\r\n<h2>- C&ocirc; b&eacute; đi ngay ra ph&iacute;a sau nh&agrave; bếp chơi. Ch&aacute;u uống tr&agrave; với b&agrave; Smith - Mary Carson ra lệnh một c&aacute;ch lạnh l&ugrave;ng.</h2>\r\n\r\n<h2>- Tại sao b&agrave; kh&ocirc;ng thương n&oacute;? Cha Ralph vừa hỏi vừa bu&ocirc;ng m&igrave;nh xuống chiếc ghế m&agrave; &ocirc;ng hiểu rằng Mary Carson d&agrave;nh cho &ocirc;ng.</h2>\r\n\r\n<h2>- Bởi v&igrave; cha thương n&oacute; - b&agrave; trả lời.</h2>\r\n\r\n<h2>- Th&ocirc;i đi! Cha Ralph phản đối, lần thứ nhất thấy m&igrave;nh ở v&agrave;o thế yếu - Con b&eacute; chỉ l&agrave; đứa trẻ c&ocirc; độc, b&agrave; Mary ạ.</h2>\r\n\r\n<h2>- Nhưng cha đ&atilde; kh&ocirc;ng nh&igrave;n n&oacute; dước g&oacute;c cạnh đ&oacute;, ch&iacute;nh cha cũng biết điều ấy</h2>\r\n\r\n<h2>Cặp mắt xanh đẹp nh&igrave;n b&agrave; một c&aacute;ch mỉa mai; &ocirc;ng cảm thấy thoải m&aacute;i hơn.</h2>\r\n\r\n<h2>- B&agrave; nghĩ rằng t&ocirc;i c&oacute; những quan hệ tội lỗi với trẻ con? D&ugrave; sao t&ocirc;i cũng l&agrave; linh mục.</h2>\r\n\r\n<h2>- Trước hết cha l&agrave; một người đ&agrave;n &ocirc;ng, Ralph de Bricassart! L&agrave;m linh mục - điều đ&oacute; chỉ mang đến cho cha một cảm gi&aacute;c an to&agrave;n, c&oacute; thế th&ocirc;i.</h2>\r\n\r\n<h2>Bất ngờ v&agrave; bối rối, cha Ralph cười. Kh&ocirc;ng hiểu v&igrave; sao, &ocirc;ng cảm thấy thấy m&igrave;nh bất lực, kh&ocirc;ng c&oacute; khả năng so gươm với b&agrave; ngay h&ocirc;m đ&oacute;; h&igrave;nh như b&agrave; đ&atilde; kh&aacute;m ph&aacute; ra chỗ nhược của chiếc &aacute;o gi&aacute;p v&agrave; b&agrave; đ&atilde; khoan v&agrave;o đấy bằng nọc độc của con nhện. C&oacute; lẽ &ocirc;ng đ&atilde; thay đổi nhiều, đ&atilde; g&igrave;a đi, chấp nhận th&iacute;ch nghi với cuộc s&oacute;ng lu mờ ở Gillabonẹ Ngọn lửa tắt dần trong l&ograve;ng &ocirc;ng hay l&agrave; n&oacute; d&atilde; ch&aacute;y l&ecirc;n với một niềm say m&ecirc; kh&aacute;c?</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng phải l&agrave; một người đ&agrave;n &ocirc;ng - &ocirc;ng vặn lại - T&ocirc;i l&agrave; linh mục...</h2>\r\n\r\n<h2>- Ồ, Ralph, cha đ&atilde; thay đổi nhiều qu&aacute; - b&agrave; chế diễu - Phải chăng t&ocirc;i đang nghe ch&iacute;nh đức Hồng Y De Bricassart n&oacute;i?</h2>\r\n\r\n<h2>- Kh&ocirc;ng hề c&oacute; chuyện đ&oacute;, &ocirc;ng n&oacute;i, c&aacute;i nh&igrave;n c&oacute; một tho&aacute;ng tuyệt vọng che khuất. T&ocirc;i kh&ocirc;ng nghĩ rằng t&ocirc;i c&ograve;n muốn tiến l&ecirc;n tr&ecirc;n những tần cao ấy.</h2>\r\n\r\n<h2>B&agrave; Mary cười lớn, người nghi&ecirc;ng ra trước rồi ngả ra sau tr&ecirc;n chiếc ghế b&agrave;nh, nhưng kh&ocirc;ng một gi&acirc;y lơi quan s&aacute;t cha Ralph.</h2>\r\n\r\n<h2>- C&oacute; thật thế kh&ocirc;ng Ralph? Cha kh&ocirc;ng c&ograve;n muốn nữa? Thế th&igrave;, t&ocirc;i sẽ để cho cha mốc meo ở đ&acirc;y th&ecirc;m một thời gian, nhưng rồi c&aacute;i ng&agrave;y đền tội của cha sẽ đến, kh&ocirc;ng thể nghi ngờ. T&ocirc;i l&agrave; hiện th&acirc;n Quỷ Xa tăng đối với cha v&agrave; t&ocirc;i sẽ hiến d&acirc;ng cho cha... Th&ocirc;i, t&ocirc;i đ&atilde; n&oacute;i đủ rồi! Nhưng đừng c&oacute; nghi ngờ, t&ocirc;i sẽ bắt cha chịu sự nhục h&igrave;nh. Cha l&agrave; người đ&agrave;n &ocirc;ng hấp dẫn nhất m&agrave; t&ocirc;i gặp. Cha n&eacute;m c&aacute;i đẹp của cha l&ecirc;n đầu ch&uacute;ng t&ocirc;i, đồng thời lại khinh miệt những sự yếu đuối của ch&uacute;ng t&ocirc;i. Nhưng t&ocirc;i nhất định sẽ ghim cha l&ecirc;n tường v&agrave; bằng c&aacute;ch khai th&aacute;c ch&iacute;nh những chỗ yếu của cha, t&ocirc;i sẽ buộc cha tự b&aacute;n m&igrave;nh như một con điếm son phấn l&ograve;e loẹt. Cha c&oacute; tin như thế kh&ocirc;ng?</h2>\r\n\r\n<h2>&ocirc;ng bật ngửa ra sau, cười.</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng nghi ngờ l&agrave; b&agrave; sẽ thử l&agrave;m việc ấy nhưng t&ocirc;i kh&ocirc;ng tin b&agrave; hiểu t&ocirc;i như b&agrave; tưởng.</h2>\r\n\r\n<h2>- Kh&ocirc;ng hẳn. Thời gian sẽ trả lời cha, Ralph, chỉ c&oacute; thời gian th&ocirc;i. T&ocirc;i đ&atilde; gi&agrave;, với t&ocirc;i kh&ocirc;ng c&ograve;n g&igrave; kh&aacute;c hơn l&agrave; sự chờ đợi.</h2>\r\n\r\n<h2>- Thế th&igrave; b&agrave; nghĩ t&ocirc;i c&oacute; c&aacute;i g&igrave; kh&aacute;c hơn &agrave;? &ocirc;ng hỏi lại. Cũng l&agrave; thời gian m&agrave; th&ocirc;i, b&agrave; Mary, chỉ c&oacute; thời gian. Thời gian, c&aacute;t bụi v&agrave; muỗi m&ograve;ng.</h2>\r\n\r\n<h2>Một h&ocirc;m, b&eacute; Hal ho; hơi thở kh&oacute;, mệt nhọc từng l&uacute;c v&agrave; sức khỏe xuống rất nhanh. Pađy l&ecirc;n nh&agrave; lớn gọi điện thoại nhưng b&aacute;c sĩ lại ở c&aacute;ch đ&acirc;y hơn 60 c&acirc;y số v&agrave; &ocirc;ng ta đang đi thăm một bệnh nh&acirc;n kh&aacute;c.</h2>\r\n\r\n<h2>Meggie kh&ocirc;ng tin Hal sẽ chết, ngay cả khi Fiona v&agrave; Pađy quỳ b&ecirc;n giường, tuyệt vọng, đọc kinh cầu nguyện cho Hal. Nửa đ&ecirc;m, Pađy gỡ hai tay của Meggie đang &ocirc;m Hal rồi bế th&acirc;n x&aacute;c nhỏ b&eacute; cứng đơ của Hal đặt nhẹ nh&agrave;ng l&ecirc;n giường.</h2>\r\n\r\n<h2>Bấy giờ, Meggie cho&agrave;ng mở mắt. C&ocirc; b&eacute; đ&atilde; thiếp đi l&uacute;c Hal kh&ocirc;ng c&ograve;n gi&atilde;y giụa nữa.</h2>\r\n\r\n<h2>- Ba ơi, Hal c&oacute; đỡ kh&ocirc;ng bả</h2>\r\n\r\n<h2>Pađy lắc đầu.</h2>\r\n\r\n<h2>- Kh&ocirc;ng, Meggie ạ. Hal kh&ocirc;ng đỡ ch&uacute;t n&agrave;o như con mong, em đ&atilde; y&ecirc;n nghỉ rồi. Hal đ&atilde; trở về với Ch&uacute;a, thế l&agrave; kh&ocirc;ng c&ograve;n đau khổ nữa.</h2>\r\n\r\n<h2>- Kh&ocirc;ng, ba ơi, em kh&ocirc;ng chết. Kh&ocirc;ng thể thế được.</h2>\r\n\r\n<h2>... Meggie ngồi tr&ecirc;n ghế hai tay đặt l&ecirc;n đầu gối. Hal thuộc về c&ocirc; b&eacute; nhưng Hal đ&atilde; chết. Ch&uacute; Hal m&agrave; Meggie đ&atilde; thương y&ecirc;u, săn s&oacute;c, dỗ ngủ nay đ&atilde; kh&ocirc;ng c&ograve;n.</h2>\r\n\r\n<h2>Cha Ralph đ&atilde; bắt gặp Meggie như thế khi bước v&agrave;o nh&agrave; c&ugrave;ng với một b&aacute;c sĩ đi theo. Sau đ&oacute; kh&ocirc;ng l&acirc;u th&igrave; b&aacute;c sĩ đi ra. &ocirc;ng kh&ocirc;ng thể l&agrave;m g&igrave; kh&aacute;c hơn v&igrave; ở c&aacute;ch qu&aacute; xa bệnh viện, phương tiện y tế lại thiếu.</h2>\r\n\r\n<h2>Sau đ&oacute;, nhiệm vụ của cha Ralph cũng xong. Pađy t&igrave;m Fiona, c&ograve;n Bob v&agrave; c&aacute;c anh trai của Meggie th&igrave; xuống xưởng mộc để đ&oacute;ng c&aacute;i h&ograve;m.</h2>\r\n\r\n<h2>Qu&ecirc;n rằng m&igrave;nh đang cho&agrave;ng chiếc khăn lễ m&agrave;u t&iacute;a tr&ecirc;n cổ, cha Ralph c&uacute;i xuống thổi bếp b&ugrave;ng l&ecirc;n. &ocirc;ng vặn thấp ngọn đ&egrave;n đặt tr&ecirc;n b&agrave;n ph&iacute;a sau rồi ngồi tr&ecirc;n chiếc ghế d&agrave;i đặt trước mặt Meggie để c&oacute; thể quan s&aacute;t c&ocirc; b&eacute; r&otilde; hơn. Meggie đ&atilde; lớn; c&ocirc; b&eacute; đ&atilde; mang hia bảy dặm, c&oacute; khả năng bỏ lại cha Ralph đằng sau. &ocirc;ng cảm thấy m&igrave;nh ng&agrave;y c&agrave;ng rối loạn l&ecirc;n dữ dội khi nh&igrave;n lại cả chiều d&agrave;i của một cuộc đời đầy rẫy những ho&agrave;i nghi v&agrave; &aacute;m ảnh. Nhưng &ocirc;ng sợ c&aacute;i g&igrave;? C&aacute;i g&igrave; đ&atilde; l&agrave;m cho &ocirc;ng nghĩ rằng nếu n&oacute; xuất hiện th&igrave; &ocirc;ng kh&ocirc;ng thể đối đầu? &ocirc;ng c&oacute; thể vững v&agrave;ng với những người kh&aacute;c; &ocirc;ng kh&ocirc;ng sợ; nhưng nơi &ocirc;ng, sự chờ đợi c&aacute;i điều kh&ocirc;ng thể giải th&iacute;ch ấy đang len v&agrave;o trong &yacute; thức của &ocirc;ng, giữa l&uacute;c m&agrave; &ocirc;ng &iacute;t chờ đợi nhất th&igrave; &ocirc;ng biết sợ. Trong khi ấy Meggie, sinh ra sau &ocirc;ng 18 năm, đang lớn l&ecirc;n, bước trước &ocirc;ng rất xạ</h2>\r\n\r\n<h2>Meggie kh&ocirc;ng phải l&agrave; một nữ th&aacute;nh, c&agrave;ng kh&ocirc;ng phải l&agrave; một c&ocirc; b&eacute; g&aacute;i như mọi c&ocirc; b&eacute; g&aacute;i kh&aacute;c. Meggie kh&ocirc;ng bao gio than v&atilde;n, lại c&oacute; c&aacute;i đức t&iacute;nh bẩm sinh - hay đ&oacute; l&agrave; một bất hạnh - l&agrave; sự chịu đựng. Trước bất cứ chuyện g&igrave; hoặc xắp xảy ra c&ocirc; b&eacute; đều đối đầu v&agrave; chấp nhận hết để đốt ch&aacute;y th&ecirc;m ngọn lửa trong l&ograve;ng. Hay l&agrave; &yacute; tưởng m&agrave; &ocirc;ng đ&atilde; c&oacute; về Meggie chỉ l&agrave; tấm gương phản chiếu những ảo tưởng của ch&iacute;nh &ocirc;ng.</h2>\r\n\r\n<h2>- Ồ, Meggie - &ocirc;ng th&igrave; thầm với một tiếng thở d&agrave;i bất lực.</h2>\r\n\r\n<h2>Meggie quay lại nh&igrave;n cha Ralph đột ngột v&agrave; từ niềm đau khổ tột c&ugrave;ng c&ocirc; b&eacute; mỉm cười như gửi đến cha Ralph một nụ h&ocirc;n y&ecirc;u thương tuyệt đối, tr&agrave;n trề, kh&ocirc;ng ẩn giấu bất cứ điều g&igrave;; những điều cấm kỵ v&agrave; ức chế của một người phụ nữ chưa c&oacute; thế giới ri&ecirc;ng tư, c&ograve;n ng&acirc;y thơ của Meggiẹ</h2>\r\n\r\n<h2>Được y&ecirc;u như thế l&agrave;m cho cha Ralph b&agrave;ng ho&agrave;ng, như bị thi&ecirc;u đốt; &ocirc;ng đ&atilde; phải xin Thượng đế - d&ugrave; cho trước đ&acirc;y lắm l&uacute;c &ocirc;ng đ&atilde; nghi ngờ sự hiện hữu - h&atilde;y biến &ocirc;ng th&agrave;nh bất cứ một con người nao kh&aacute;c trong cuộc đời n&agrave;y, miễn l&agrave; kh&ocirc;ng phải Ralph de Bricassart. C&oacute; phải chăng ch&iacute;nh đ&oacute; la điều kh&ocirc;ng thể giải th&iacute;ch trong &ocirc;ng? Thượng đế ơi, tại sao t&ocirc;i lại y&ecirc;u thương c&ocirc; b&eacute; đến thế? Nhưng cũng như mọi khi, kh&ocirc;ng c&oacute; ai trả lời cho &ocirc;ng; Ngồi im trước mặt, Meggie vẫn tiếp tục mỉm cười với &ocirc;ng...</h2>\r\n\r\n<h2>Cuộc sống tiếp diễn theo c&aacute;i v&ograve;ng tuần ho&agrave;n vĩnh cửu, đều đặn của tr&aacute;i đất, m&ugrave;a h&egrave; kếp tiếp mang những cơn mưa đến l&agrave;m đầy con suối v&agrave; c&aacute;c bồn chứa, tưới m&aacute;t những rễ c&acirc;y kh&aacute;t nước, giữ chặt tr&ecirc;n mặt đất những cơn bụi đi&ecirc;n cuồng. Bọn đ&agrave;n &ocirc;ng mừng rỡ muốn kh&oacute;c khi l&agrave;m những c&ocirc;ng việc m&ugrave;a vụ, y&ecirc;n t&acirc;m kh&ocirc;ng phải mất c&ocirc;ng chở cỏ kh&ocirc; để nu&ocirc;i cừu.</h2>\r\n\r\n<h2>Drogheda biệt lập với thế giới c&ograve;n lại, chỉ c&oacute; đọc s&aacute;ch l&agrave; c&aacute;ch tiếp x&uacute;c duy nhất với b&ecirc;n ngo&agrave;i. Nhưng kh&ocirc;ng như Wahine c&oacute; thư viện lưu động, ở đ&acirc;y chỉ tr&ocirc;ng cậy v&agrave;o Ralph. &ocirc;ng cung cấp cho c&aacute;c gia đ&igrave;nh ham đọc ở Drogheda những quyển s&aacute;ch lấy từ tủ s&aacute;ch của ch&iacute;nh &ocirc;ng v&agrave; của tu viện.</h2>\r\n\r\n<h2>V&agrave;o thời đ&oacute;, cuốn s&aacute;ch ướt &aacute;t nhất cũng chỉ d&aacute;m tả một nụ h&ocirc;n trong s&aacute;ng, chứ kh&ocirc;ng đi xa hơn.</h2>\r\n\r\n<h2>Trong sự biệt lập ấy, Meggie ho&agrave;n to&agrave;n kh&ocirc;ng hiểu được cả c&aacute;i &yacute; nghĩa của kh&aacute;i niệm những thực tế của cuộc sống v&igrave; rằng điều kiện thực tế đ&atilde; ngăn chặn mọi ng&atilde; đường kh&aacute;m ph&aacute;. Cha của c&ocirc; dựng l&ecirc;n một r&agrave;o chắn ki&ecirc;n cố giữa đ&agrave;n &ocirc;ng phụ nữ trong gia đ&igrave;nh; c&aacute;c đề t&agrave;i như g&acirc;y giống hay giao phối của s&uacute;c vật kh&ocirc;ng bao giờ được đề cập trước mặt Meggie v&agrave; đ&agrave;n &ocirc;ng kh&ocirc;ng bao giờ xuất hiện trước mặt c&ocirc; b&eacute; m&agrave; kh&ocirc;ng ăn mặc đ&agrave;ng ho&agrave;ng. Loại s&aacute;ch c&oacute; khả năng soi s&aacute;ng một ch&uacute;t g&igrave; đ&oacute; cũng kh&ocirc;ng tới Droghedạ Meggie kh&ocirc;ng c&oacute; bạn g&aacute;i c&ugrave;ng lứa tuổi để tăng th&ecirc;m sự hiểu biết. Cuộc sống của c&ocirc; b&eacute; chỉ quanh quẩn v&agrave;o những c&ocirc;ng việc trong nh&agrave; v&agrave; những v&ugrave;ng l&acirc;n cận, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; một hoạt động n&agrave;o d&iacute;nh dấp đến giới t&iacute;nh. C&aacute;c sinh vật ở v&ugrave;ng đất n&agrave;y đều kh&ocirc;ng sinh đẻ. Mary Carson kh&ocirc;ng g&acirc;y giống ngựa; b&agrave; mua ngựa của Martin Jang ở Bugelạ Ở trang trại c&oacute; một con b&ograve; rừng, con vật hung dữ kh&ocirc;ng cho mọi người l&eacute;o h&aacute;nh tới. Mấy con ch&oacute; th&igrave; bị cột trong chuồng; lấy giống được coi như một c&ocirc;ng việc khoa học v&agrave; diễn ra dưới mắt l&agrave;nh nghề của Pađy v&agrave; Bob. Thời giờ kh&ocirc;ng phải để chăm s&oacute;c mấy con heo, vả lại Meggie cũng rất gh&ecirc; tởm ch&uacute;ng d&ugrave; phải nu&ocirc;i. N&oacute;i cho đ&uacute;ng l&agrave; Meggie kh&ocirc;ng c&oacute; thời giờ cần thiết để nghĩ đến bất cứ chuyện g&igrave; ngo&agrave;i hai đứa em trai. Sự dốt n&aacute;t n&agrave;y k&eacute;o theo sự dốt n&aacute;t kh&aacute;c; một cơ thể v&agrave; một bộ &oacute;c kh&ocirc;ng được k&eacute;o ra khỏi cơn ngủ th&igrave; cứ tiếp tục ngủ, mặc c&aacute;c sự kiện m&agrave; con người vừa mở mắt đ&atilde; ghi nhận được tất cả.</h2>\r\n\r\n<h2>V&agrave;i h&ocirc;m trước sinh nhật lần thứ 15 của Meggie, giữa c&aacute;i n&oacute;ng oi bức của m&ugrave;a h&egrave; l&ecirc;n đến gần cực điểm, Meggie bỗng ch&uacute; &yacute; thấy những vết n&acirc;u n&acirc;u từng vệt d&iacute;nh trong quần l&oacute;t. Sau đ&oacute; một hai h&ocirc;m kh&ocirc;ng thấy g&igrave; nữa nhưng s&aacute;u tuần sau, c&aacute;c vết ấy lại xuất hiện trở lại. Sự mắc cở ở Meggie biến th&agrave;nh sợ h&atilde;i. Lần đầu, c&ocirc; nghĩ l&agrave; do m&igrave;nh giặt quần &aacute;o kh&ocirc;ng sạch, thật l&agrave; nhục; nhưng qua lần xuất hiện thứ nh&igrave;, sự ho&agrave;i nghi kh&ocirc;ng cho ph&eacute;p nữa, chắc chắn kh&ocirc;ng thể chối c&atilde;i đ&acirc;y l&agrave; m&aacute;u. Meggie kh&ocirc;ng c&oacute; một ch&uacute;t kh&aacute;i niệm g&igrave; về nguy&ecirc;n nh&acirc;n sự chảy m&aacute;u ấy nhưng c&ocirc; tưởng tượng l&agrave; n&oacute; chảy ra ở ph&iacute;a sau. Sự chảy m&aacute;u rỉ rả ấy biến đi sau ba ng&agrave;y v&agrave; kh&ocirc;ng trở lại trước hai th&aacute;ng sau; Meggie l&eacute;n l&uacute;t giặt quần l&oacute;t, kh&ocirc;ng ai hay biết v&igrave; ng&agrave;y thường cũng ch&iacute;nh Meggie đảm tr&aacute;ch phần giặt giũ quần &aacute;o cho mọi người trong nh&agrave;.</h2>\r\n\r\n<h2>Hiện tượng chảy m&aacute;u lần kế tiếp k&egrave;m theo sự đau đớn, sự đau đớn nhiều nhất từ trước tới n&agrave;y, hơn cả những cơn đau dạ d&agrave;y. V&agrave; lần n&agrave;y, m&aacute;u chảy cũng nhiều, thật nhiều. C&ocirc; ăn cắp v&agrave;i miếng t&atilde; l&oacute;t cũ của hai em trai sinh đ&ocirc;i v&agrave; đặt ở trong quần l&oacute;t, với nỗi lo sợ kinh khủng m&aacute;u c&oacute; thể chảy ra tới b&ecirc;n ngo&agrave;i.</h2>\r\n\r\n<h2>C&aacute;i chết đ&atilde; mang Hal đi được Meggie hiểu như sự xuất hiện của một hiện tượng si&ecirc;u tự nhi&ecirc;n, nhưng c&ograve;n c&aacute;i g&igrave; đ&oacute; đang chảy &acirc;m ỉ từ trong người của Meggie th&igrave; lại g&acirc;y cho c&ocirc; một nỗi kinh ho&agrave;ng. Bằng mọi c&aacute;ch Meggie phải đi t&igrave;m mẹ hoặc cha để th&ocirc;ng b&aacute;o rằng m&igrave;nh sắp sửa chết do một bệnh tật nhục nh&atilde;, kh&oacute; n&oacute;i cho ai biết. Chỉ duy nhất với Frank, c&ocirc; mới c&oacute; thể thổ lộ nỗi lo &acirc;u của m&igrave;nh, nhưng giờ đ&acirc;y anh ấy đang ở qu&aacute; xa v&agrave; kh&ocirc;ng biết c&aacute;ch n&agrave;o m&agrave; t&igrave;m. Meggie c&oacute; nghe những người phụ nữ lớn tuổi giữa l&uacute;c uống tr&agrave; đ&atilde; n&oacute;i với nhau về những khối u v&agrave; ung thư, về những c&aacute;i chết khủng khiếp của những người bạn v&agrave; những người b&agrave; con sau những th&aacute;ng d&agrave;i đau đớn. V&agrave; Meggie tin rằng một hiện tượng tương tự cũng đang ngốn b&ecirc;n trong cơ thể ầm ỉ, tiến s&acirc;u v&agrave;o tr&aacute;i tim hoảng hốt của Meggiẹ &ocirc;i, c&ocirc; b&eacute; kh&ocirc;ng muốn chết ch&uacute;t n&agrave;o!</h2>\r\n\r\n<h2>Những hiểu biết tương đối về c&aacute;i chết lại rất mơ hồ đối với Meggie nhưng c&ocirc; rất sợ h&atilde;i v&agrave; kh&ocirc;ng bao giờ muốn chết. Meggie kh&ocirc;ng thể tưởng tượng được m&igrave;nh sẽ ra sao trong c&aacute;i thế giới b&ecirc;n kia. Với Meggie, t&ocirc;n gi&aacute;o chẳng kh&aacute;c g&igrave; một loạt luật lệ hơn l&agrave; cuộc sống tinh thần, do đ&oacute; t&ocirc;n gi&aacute;o kh&ocirc;ng gi&uacute;p g&igrave; được cho c&ocirc; cả.</h2>\r\n\r\n<h2>Sự lặng lẽ x&acirc;m chiếm Meggie, sự y&ecirc;n tĩnh ấy kh&ocirc;ng thể so s&aacute;nh với nỗi c&ocirc; độc &ecirc;m đềm v&agrave; mơ mộng của Stuart. Ở Meggie sự lặng lẽ đ&oacute; c&oacute; cả ch&uacute;t sợ h&atilde;i sửng sốt của một con th&uacute; bị rắn th&ocirc;i mi&ecirc;n. Khi c&oacute; ai hỏi đột ngột, Meggie giật nẩy người, nếu c&aacute;c đứa em trai gọi Meggie bằng c&aacute;ch h&eacute;t to l&ecirc;n, Meggie sẽ chạy hốt hoảng v&agrave; tự tr&aacute;ch m&igrave;nh sao sơ s&oacute;t. V&agrave; cứ mỗi khi lợi dụng được gi&acirc;y ph&uacute;t hiếm hoi ngơi nghỉ, Meggie liền chạy nhanh ra nghĩa trang để t&igrave;m lại Hal, c&aacute;i chết duy nhất m&agrave; c&ocirc; được biết.</h2>\r\n\r\n<h2>Mọi người đều nhận ra sự thay đổi ở Meggie nhưng đều coi đ&oacute; l&agrave; một hiện tượng ph&aacute;t triển b&igrave;nh thường của lứa tuổi. C&ocirc; b&eacute; giấu k&iacute;n một c&aacute;ch t&agrave;i t&igrave;nh nỗi c&ocirc; đơn tuyệt vọng. Kh&ocirc;ng ai c&oacute; thể hiểu những g&igrave; xảy ra b&ecirc;n trong Meggiẹ Kh&ocirc;ng c&oacute; một biểu lộ n&agrave;o, v&agrave; sự k&iacute;n đ&aacute;o đ&oacute; được giữ cho tới c&ugrave;ng. Từ Fiona đến Stuart, qua Frank, kh&ocirc;ng biết bao nhi&ecirc;u điều đ&atilde; xảy ra.</h2>\r\n\r\n<h2>Thế m&agrave;, trong những chuyến thăm Drogheda thường xuy&ecirc;n, cha Ralph đ&atilde; nhận ra sự thay đổi ở Meggie; sự thay đổi ấy tr&ugrave;ng hợp với diễn biến rất dễ thương ở người phụ nữ; n&oacute; l&agrave;m giảm đi c&aacute;c họat động v&igrave; lo lắng rồi &acirc;u sầu. Một sự sa s&uacute;t về thể x&aacute;c lẫn tinh thần đập v&agrave;o mắt cha Ralph. Meggie lẩn tr&aacute;nh mọi người. Cha Ralph kh&ocirc;ng muốn thấy Meggie biến th&agrave;nh một Fiona thứ hai. Gương mặt nhỏ b&eacute; thu lại chỉ c&ograve;n thấy r&otilde; đ&ocirc;i mắt, đ&ocirc;i mắt mở to như hướng về một viễn ảnh khủng khiếp n&agrave;o. Da m&agrave;u sữa, trước đ&acirc;y đầy đặn, kh&ocirc;ng hề bị r&aacute;m nắng, cũng kh&ocirc;ng c&oacute; vết t&agrave;n nhang, đang c&agrave;ng l&uacute;c c&agrave;ng tăng l&ecirc;n. Nếu c&aacute;i đ&agrave; n&agrave;y cứ tiếp tục, cha Ralph nghĩ thầm, một ng&agrave;y n&agrave;o đ&oacute; Meggie sẽ tự đ&acirc;m m&igrave;nh, vất vưởng trong tia s&aacute;ng hiu hắt m&agrave;u n&acirc;u, lờ đờ, rất kh&oacute; nhận ra bằng mắt, giữa những chiếc b&oacute;ng đan l&ecirc;n nhau v&agrave; những vệt đen đang lan dần l&ecirc;n bức tường trắng.</h2>\r\n\r\n<h2>Thế l&agrave; cha Ralph quyết kh&aacute;m ph&aacute; điều ấy ở Meggie, d&ugrave; phải d&ugrave;ng biện ph&aacute;p mạnh. V&agrave;o l&uacute;c đ&oacute;, Mary Carson tỏ ra kh&oacute; chịu v&agrave; ghen tương từng l&uacute;c mỗi khi cha Ralph ở lại tại nh&agrave; Pađỵ</h2>\r\n\r\n<h2>Một h&ocirc;m, &ocirc;ng tho&aacute;t ra được sự kềm kẹp của Mary Carson v&agrave; săn t&igrave;m cho được Meggie, cuối c&ugrave;ng gặp c&ocirc; trong nghĩa trang nhỏ, n&uacute;p nắng dưới một bức tượng th&aacute;nh - Meggie đang nh&igrave;n n&eacute;t mặt nhợt nhạt của bức tượng, trong khi đ&oacute; ch&iacute;nh gương mặt của c&ocirc; biểu lộ n&eacute;t sợ h&atilde;i, một sự tương phản giữa x&uacute;c cảm v&agrave; v&ocirc; cảm trơ trơ - cha Ralph nghĩ thầm như thế. Nhưng m&igrave;nh ở đ&acirc;y l&agrave;m g&igrave;, chạy theo đu&ocirc;i c&ocirc; ta như một con g&agrave; mẹ gi&agrave; nua, trong khi Fiona hoặc Pađy c&oacute; tr&aacute;ch nhiệm t&igrave;m hiểu những nguy&ecirc;n nh&acirc;n &acirc;u lo của con g&aacute;i.</h2>\r\n\r\n<h2>Nhưng cha mẹ của Meggie đ&atilde; kh&ocirc;ng nhận thấy g&igrave; bở họ &iacute;t quan t&acirc;m đến con g&aacute;i, trong khi cha Ralph lại d&agrave;nh cho Meggie thật nhiều thứ. Hơn thế, với cương vị một linh mục, cha Ralph c&ograve;n c&oacute; tr&aacute;ch nhiệm an ủi những t&acirc;m hồn đau khổ c&ocirc; đơn v&agrave; tuyệt vọng. &ocirc;ng kh&ocirc;ng thể chịu đựng nổi khi thấy Meggie đau khổ, nhưng tai hại l&agrave; &ocirc;ng đ&atilde; ng&agrave;y c&agrave;ng gắn bo với Meggie qua một loạt sự kiện rắc rối chồng l&ecirc;n nhau. Với Meggie, &ocirc;ng đ&atilde; hằn s&acirc;u biết bao sự việc v&agrave; kỷ niệm, do đ&oacute; &ocirc;ng cảm thấy sợ. T&igrave;nh thương của &ocirc;ng d&agrave;nh cho Meggie v&agrave; bản chất linh mục của &ocirc;ng, th&uacute;c đẩy &ocirc;ng phải sẵn s&agrave;ng mang lại mọi sự gi&uacute;p đỡ về mặt tinh thần m&agrave; đ&uacute;ng ra mọi người đều c&oacute; quyền chờ đợi ở &ocirc;ng. &ocirc;ng phải tự chiến đấu để thấy r&otilde; hơn l&agrave; m&igrave;nh c&oacute; tr&aacute;ch nhiệm với Meggie cũng như với bất cứ ai kh&aacute;c.</h2>\r\n\r\n<h2>Nghe tiếng ch&acirc;n đi tr&ecirc;n cỏ, Meggie quay lại, hai tay đặt ch&eacute;o tr&ecirc;n đ&ugrave;i, mắt nh&igrave;n xuống. Cha Ralph đến ngồi b&ecirc;n cạnh c&ocirc;, tay b&oacute; gối. Chiếc &aacute;o cho&agrave;ng kh&ocirc;ng được k&iacute;n đ&aacute;o l&agrave;m tăng vẻ đẹp c&aacute;i th&acirc;n thể b&ecirc;n trong của chạ Thối tho&aacute;t v&ocirc; &iacute;ch, &ocirc;ng nghĩ thầm. Nếu muốn, Meggie c&oacute; thể tr&aacute;nh n&eacute; &ocirc;ng.</h2>\r\n\r\n<h2>- C&oacute; chuyện g&igrave; xảy ra hở Meggiẻ</h2>\r\n\r\n<h2>- Kh&ocirc;ng c&oacute; chi thưa chạ</h2>\r\n\r\n<h2>- Cha kh&ocirc;ng tin lời con.</h2>\r\n\r\n<h2>- Con xin van cha, con xin van cha ma! Con kh&ocirc;ng thể n&oacute;i với cha chuyện n&agrave;y được.</h2>\r\n\r\n<h2>- Ồ, Meggie thương y&ecirc;u! C&ocirc; g&aacute;i thiếu đức t&iacute;n! Con c&oacute; thể n&oacute;i với cha tất cả. Ch&iacute;nh v&igrave;thế m&agrave; cha c&oacute; mặt tại đ&acirc;y. Ch&iacute;nh v&igrave; thế m&agrave; cha l&agrave; linh mục. Cha l&agrave; người đại diện đ&atilde; được Ch&uacute;a lựa chọn tr&ecirc;n trần gian. Cha nh&acirc;n danh Ch&uacute;a m&agrave; nghe con n&oacute;i, nh&acirc;n danh Người ta c&oacute; quyền tha thứ cho con. Nghe đ&acirc;y Meggie b&eacute; nhỏ của ta, kh&ocirc;ng c&oacute; điều g&igrave; ở tr&ecirc;n trần thế n&agrave;y m&agrave; Ch&uacute;a v&agrave; ta kh&ocirc;ng thể tha thứ. Con phải n&oacute;i cho ta biết điều g&igrave; l&agrave;m con băn khoăn, con thương y&ecirc;u. Ng&agrave;y n&agrave;o ta c&ograve;n tr&ecirc;n đời n&agrave;y, ta sẽ lu&ocirc;n t&igrave;m c&aacute;ch gi&uacute;p đỡ con, che chở con. Nếu con muốn th&igrave; cha sẽ như một thi&ecirc;n thần hộ mệnh, chắc chắn đ&aacute;ng tin cậy hơn so với bức tượng đ&aacute; cẩm thạch m&agrave; con nh&igrave;n thấy. Nếu con thương y&ecirc;u ta, h&atilde;y n&oacute;i cho ta biết l&agrave;m g&igrave; l&agrave;m con lo lắng.</h2>\r\n\r\n<h2>- Thưa cha, con sắp chết, c&ocirc; g&aacute;i vừa n&oacute;i vừa vặn hai tay - con bị ung thự</h2>\r\n\r\n<h2>Su&yacute;t nữa &ocirc;ng cười to l&ecirc;n v&igrave; g&aacute;nh nặng trong l&ograve;ng bỗng chốc tr&uacute;t bỏ hết; nhưng liền đ&oacute; nh&igrave;n kỹ l&agrave;n da mịn, hơi xanh, hai c&aacute;nh tay nhỏ gầy của Meggie, trong l&ograve;ng &ocirc;ng nổi l&ecirc;n một th&egrave;m muốn gh&ecirc; gớm được kh&oacute;c v&agrave; h&eacute;t to l&ecirc;n, o&aacute;n giận cả trời đất trước một bất c&ocirc;ng như thế. Kh&ocirc;ng, Meggie kh&ocirc;ng tự m&igrave;nh tưởng tượng ra chuyện khủng khiếp ấy m&agrave; kh&ocirc;ng dựa v&agrave;o một nguy&ecirc;n nh&acirc;n n&agrave;o đ&oacute;, c&oacute; thể c&oacute; một l&yacute; dọ</h2>\r\n\r\n<h2>- Điều g&igrave; khiến cho con tin như thế, quả tim b&eacute; nhỏ của chả</h2>\r\n\r\n<h2>Phải một l&uacute;c kh&aacute; l&acirc;u, c&ocirc; mới kể lại v&agrave; khi ấy, cha Ralph phải nghi&ecirc;ng đầu về ph&iacute;a gần m&ocirc;i Meggiẹ &ocirc;ng lặp lại một c&aacute;ch m&aacute;y m&oacute;c tư thế của một linh mục trước một con chi&ecirc;n đang xưng tội: lấy tay che mặt để mắt c&ocirc; g&aacute;i kh&ocirc;ng tr&ocirc;ng thấy m&igrave;nh, c&ograve;n tai sẵn s&agrave;ng nghe những lời xưng tội.</h2>\r\n\r\n<h2>- C&aacute;ch đ&acirc;y s&aacute;u th&aacute;ng thưa cha, chuyện ấy bắt đầu xảy ra. Con bị đau bụng dữ dội, nhưng kh&ocirc;ng phải đau bụng như khi ăn kh&ocirc;ng ti&ecirc;u đ&acirc;u... Rồi... Cha ơi... rất nhiều m&aacute;u chảy ra.</h2>\r\n\r\n<h2>Cha Ralph ngửa đầu ra sau, cử chỉ m&agrave; kh&ocirc;ng bao giờ c&oacute; trong những l&uacute;c nghe xưng tội; &ocirc;ng nh&igrave;n đầu Meggie cuối xuống v&igrave; xấu hổ. Biết bao x&uacute;c động dấy l&ecirc;n khiến cho &ocirc;ng kh&ocirc;ng thể lấy lại sự b&igrave;nh tĩnh. V&ocirc; l&yacute; thật. Sự nhẹ nh&otilde;m hết sức dễ chịu rồi sau đ&oacute; &ocirc;ng lại giận Fiona, mẹ của Meggie, &ocirc;ng th&aacute;n phục với sự kinh ngạc khi nghĩ đến c&ocirc; g&aacute;i b&eacute; bỏng ấy sao lại c&oacute; thể chịu đựng một cực h&igrave;nh dai dẳng, một sự kh&oacute; chịu như thế m&agrave; kh&ocirc;ng h&igrave;nh dung được l&agrave; c&aacute;i g&igrave;.</h2>\r\n\r\n<h2>&ocirc;ng đ&atilde; từng nghe những chuyện tương tự như chuyện Meggie kể trong suộc cuộc đời linh mục. Mỗi th&agrave;nh phố cha từng ở lại từ Dublin đến Gillabone, đều c&oacute; những c&ocirc; g&aacute;i hư hỏng len v&agrave;o ph&ograve;ng xưng tội kể lể những chuyện bịa đặt cứ như l&agrave; thật với mục đ&iacute;ch duy nhất l&agrave; khơi dậy c&aacute;i điều m&agrave; họ t&ograve; m&ograve; nhất ở một linh mục: đ&oacute; l&agrave; t&iacute;nh sung m&atilde;n của người đ&agrave;n &ocirc;ng. Tất cả đ&aacute;m con g&aacute;i đ&oacute; kh&ocirc;ng chịu nh&igrave;n nhận rằng họ ho&agrave;n to&agrave;n thất bại trong toan t&iacute;nh n&agrave;y. Họ th&igrave; thầm kể lại n&agrave;o l&agrave; họ đ&atilde; bị h&atilde;m hiếp như thế n&agrave;o, gợi l&ecirc;n những tr&ograve; cấm kỵ giữa họ với những đứa con g&aacute;i kh&aacute;c, những chuyện d&acirc;m đ&atilde;ng v&agrave; ngoại t&igrave;nh; một v&agrave;i c&ocirc; trong bọn, &oacute;c tưởng tượng phong ph&uacute; hơn, đến mức kể tỉ mỉ những chi tiết l&agrave;m t&igrave;nh với một linh mục. Thế m&agrave; cha Ralph vẫn nghe, ho&agrave;n to&agrave;n kh&ocirc;ng xao xuyến m&agrave; chỉ thấy kinh tởm v&agrave; x&oacute;t xa cho họ. Cha đ&atilde; trải qua cuộc sống khắc khổ ở tu viện.</h2>\r\n\r\n<h2>Hơn thế nữa với một con người thuộc tầm cỡ &ocirc;ng, b&agrave;i học ấy &ocirc;ng ti&ecirc;u h&oacute;a kh&ocirc;ng kh&oacute; khăn lắm. Nhưng trong tất cả c&aacute;c trường hợp đ&atilde; qua, chưa bao giờ c&aacute;c c&ocirc; g&aacute;i lại n&oacute;i về những chỗ yếu của th&acirc;n x&aacute;c, l&agrave;m hạ phẩm gi&aacute; của họ.</h2>\r\n\r\n<h2>D&ugrave; hết sức cố gắng nhưng cha Ralph kh&ocirc;ng thể ngăn chặn m&aacute;u trong người n&oacute;ng l&ecirc;n. Mặt ửng đỏ; cha Ralph quay đầu đi v&agrave; &uacute;p mặt trong tay, chống chỏi lại sự hổ thẹn lần đầu l&agrave;m đỏ mặt &ocirc;ng.</h2>\r\n\r\n<h2>Nhưng tất cả những chuyện ấy kh&ocirc;ng gi&uacute;p g&igrave; cho c&ocirc; g&aacute;i b&eacute; Meggiẹ Khi biết chắc rằng sắc mặt của m&igrave;nh đ&atilde; trở lại b&igrave;nh thường, cha Ralph đứng dậy, n&acirc;ng Meggie l&ecirc;n v&agrave; đặt c&ocirc; g&aacute;i ngồi tr&ecirc;n bệ đ&aacute; cẩm thạch để cho mặt của Meggie cao ngang với &ocirc;ng.</h2>\r\n\r\n<h2>- Meggie h&atilde;y nh&igrave;n chạ Đừng l&agrave;m thế, h&atilde;y nh&igrave;n thẳng chạ</h2>\r\n\r\n<h2>Meggie ngước l&ecirc;n với đ&ocirc;i mắt của con th&uacute; bị săn đuổi v&agrave; thấy linh mục mỉm cười; t&acirc;m hồn c&ocirc; cảm thấy nhẹ nh&agrave;ng một niềm an ủi v&ocirc; hạn. Cha sẽ kh&ocirc;ng mỉm cười như thế nếu c&ocirc; sắp chết. Meggie biết rất r&otilde; cha Ralph chẳng bao giờ giấu di&ecirc;m t&igrave;nh cảm của &ocirc;ng.</h2>\r\n\r\n<h2>- Meggie, con kh&ocirc;ng biết đ&acirc;u v&agrave; con cũng kh&ocirc;ng bị ung thự Đ&uacute;ng ra ở cương vị n&agrave;y cha kh&ocirc;ng thể giải th&iacute;ch chuyện g&igrave; đ&atilde; xảy ra nhưng cha nghĩ rằng cha phải l&agrave;m th&ocirc;i. Đ&aacute;ng l&yacute; mẹ con hải n&oacute;i cho con biết từ l&acirc;u, chuẩn bị cho con sẵn s&agrave;ng đ&oacute;n nhận hiện tượng ấy. Cha kh&ocirc;ng thể n&agrave;o hiểu được tại sao mẹ con kh&ocirc;ng l&agrave;m như vậy.</h2>\r\n\r\n<h2>Cha Ralph liếc nh&igrave;n bức tượng th&aacute;nh bằng cẩm thạch đứng sữn tr&ecirc;n cao, rồi cười như tắt nghẹn.</h2>\r\n\r\n<h2>- Lạy ch&uacute;a! Ch&uacute;a giao cho con một nhiệm vụ kh&ocirc;ng dễ d&agrave;ng. N&agrave;y Meggie th&acirc;n y&ecirc;u, với năm th&aacute;ng, khi con lớn l&ecirc;n, rồi gi&agrave; đi v&agrave; khi con biết được nhiều hơn về những thực tế của cuộc sống, con sẽ nhớ lại c&aacute;i ng&agrave;y n&agrave;y m&agrave; kh&ocirc;ng khỏi kh&oacute; chịu, hổ thẹn nữa l&agrave; kh&aacute;c. Nhưng đừng giữ kỷ niệm n&agrave;y l&agrave;m g&igrave;, Meggie ạ. Tất cả chuyện n&agrave;y chẳng c&oacute; g&igrave; lạ xấu hổ v&agrave; chẳng c&oacute; g&igrave; ngượng ngập cả. L&uacute;c n&agrave;y, cũng như mọi khi, cha chỉ l&agrave; c&ocirc;ng cụ ở trần thế, cha kh&ocirc;ng c&oacute; quyền nghĩ đến nhiệm vụ n&agrave;o kh&aacute;c. Con đ&atilde; qu&aacute; sợ h&atilde;i, con cần sự gi&uacute;p đỡ, v&agrave; Đức Ch&uacute;a Trời đ&atilde; ban cho con sự gi&uacute;p đ&oacute; qua đại diện của Người l&agrave; tạ Con n&ecirc;n nhớ điều ấy, Meggie ạ. Cha l&agrave; t&ocirc;i tớ của Ch&uacute;a v&agrave; những g&igrave; cha n&oacute;i đều nh&acirc;n danh Ch&uacute;a. Chuyện xảy ra với con l&agrave; chuyện đ&atilde; xảy ra với tất cả mọi phụ nữ, Meggie, mỗi th&aacute;ng một lần v&agrave; k&eacute;o d&agrave;i trong nhiều ng&agrave;y, con sẽ thấy ra m&aacute;u. Thường chuyện đ&oacute; xuất hiện ở tuổi 12, 13... Con đ&atilde; mấy tuổi?</h2>\r\n\r\n<h2>- Thưa con mười lăm!</h2>\r\n\r\n<h2>- Mười lăm &agrave;? Con đ&atilde; mười lăm? - Cha Ralph lắc đầu kh&ocirc;ng tin - Nhưng th&ocirc;i, con đ&atilde; n&oacute;i với cha th&igrave; cha kh&ocirc;ng nghi ngờ g&igrave;. Với c&aacute;i tuổi mười lăm chuyện ấy xảy ra như thế l&agrave; trễ. Hiện tượng n&agrave;y sẽ diễn mối th&aacute;ng cho đến khi con tr&ecirc;n 50 tuổi. Với một số người n&oacute; xuất hiện đều đặn theo chu kỳ tuần trăng, với một số kh&aacute;c c&oacute; thể kh&ocirc;ng đều đặn. Với người n&agrave;y kh&ocirc;ng bị đau đớn, với người kh&aacute;c th&igrave; ngược lại. Người ta kh&ocirc;ng biết được tại sao sự kh&aacute;c nhau ấy. Nhưng đ&oacute; l&agrave; dấu hiệu trưởng th&agrave;nh của con. Con c&oacute; hiểu trưởng th&agrave;nh l&agrave; g&igrave; kh&ocirc;ng?</h2>\r\n\r\n<h2>- Con biết chứ, thưa chạ Con đọc s&aacute;ch! Trưởng th&agrave;nh c&oacute; nghĩa l&agrave; con đ&atilde; lớn.</h2>\r\n\r\n<h2>- Tốt. Giải th&iacute;ch như thế cũng kh&aacute; đủ. Khi n&agrave;o con ra m&aacute;u l&agrave; người phụ nữ c&ograve;n c&oacute; thể c&oacute; con. Đ&oacute; l&agrave; sự kết tục n&ograve;i giống. V&agrave;o thời kỳ trước khi xảy ra tội tổ t&ocirc;ng, h&igrave;nh như Eva kh&ocirc;ng c&oacute; kinh nguyệt. Kinh nguyệt l&agrave; t&ecirc;n gọi hiện tượng đ&atilde; xảy đến với con, Meggiẹ Nhưng khi Adam v&agrave; Eva phạm tội, Ch&uacute;a Trời đ&atilde; trừng phạt người phụ nữ nặng hơn v&igrave; rằng trong sự sa ng&atilde; ấy, người phụ nữ c&oacute; tội nhiều hơn, ch&iacute;nh họ đ&atilde; c&aacute;m dỗ người đ&agrave;n &ocirc;ng. Con c&oacute; nhớ những lời giảng trong kinh th&aacute;nh kh&ocirc;ng? Người sẽ sinh đẻ trong đau đớn, c&oacute; nghĩa l&agrave; Ch&uacute;a muốn rằng đối với người phụ nữ, tất cả những g&igrave; li&ecirc;n quan đến việc sinh đẻ đều l&agrave; nguồn gốc của mọi tội lỗi. Đ&oacute; l&agrave; những niềm vui sướng thật lớn lao nhưng đồng thời cũng l&agrave; những đau đớn tận c&ugrave;ng. Đ&oacute; l&agrave; phần số của con người, Meggie ạ, con phải cam chịu như vậy.</h2>\r\n\r\n<h2>Meggie kh&ocirc;ng biết rằng cha Ralph cũng sẵn s&agrave;ng mang lại sự an ủi v&agrave; gi&uacute;p đỡ y như thế với bất cứ một nữ gi&aacute;o d&acirc;n n&agrave;o, mặc d&ugrave; c&oacute; khi cha kh&ocirc;ng thiết tha đến số phận của họ như đối với số phận của Meggiẹ Cha l&agrave; hiện th&acirc;n của l&ograve;ng tốt, nhưng kh&ocirc;ng bao giờ cha lại để bị l&ocirc;i cuốn v&agrave;o những nỗi lo &acirc;u. Sự an ủi v&agrave; gi&uacute;p đỡ ri&ecirc;ng c&agrave;ng c&oacute; &iacute;ch gấp bội đối với người đ&oacute;n nhận. Cha Ralph vượt l&ecirc;n tr&ecirc;n những điều tầm thường. Bất cứ ai cần sự gi&uacute;p đỡ của cha đều kh&ocirc;ng cảm thấy bị gh&eacute;t bỏ hay ch&ecirc; tr&aacute;ch. Nhiều linh mục đ&atilde; khiến cho con chi&ecirc;n mang mặc cảm tội lỗi, xấu xa, đ&ecirc; tiện khi ra về. C&ograve;n cha Ralph th&igrave; kh&ocirc;ng. Cha Ralph l&agrave;m cho họ hiểu rằng, ch&iacute;nh bản th&acirc;n &ocirc;ng, cũng c&oacute; những niềm đau v&agrave; những cuộc đấu tranh. Niềm đau th&igrave; xa lạ c&ograve;n cuộc đấu tranh c&oacute; thể kh&ocirc;ng thấy r&otilde; hết nhưng kh&ocirc;ng v&igrave; hiểu điều n&agrave;y, rằng phần lớn sức quyến rũ v&agrave; sức hấp dẫn của &ocirc;ng kh&ocirc;ng do con người x&aacute;c thịt của &ocirc;ng tạo n&ecirc;n, m&agrave; do ch&iacute;nh sự xa c&aacute;ch gần như thần th&aacute;nh, do một phần nhỏ rất con người thấm sầu v&agrave;o t&acirc;m hồn &ocirc;ng.</h2>\r\n\r\n<h2>Với Meggie, &ocirc;ng n&oacute;i chuyện như Frank đ&atilde; n&oacute;i chuyện với em g&aacute;i, mang h&agrave;ng nhau. Nhưng &ocirc;ng lớn tuổi hơn, s&aacute;ng suốt hơn v&agrave; hiểu biết hơn Frank nhiều, do đ&oacute; &ocirc;ng l&agrave; người m&agrave; Meggie c&oacute; thể t&acirc;m t&igrave;nh thoải m&aacute;i hơn. Giọng n&oacute;i của cha Ralph thanh tho&aacute;t mang &acirc;m điệu nhẹ nh&agrave;ng của người Ailen, c&oacute; khả năng l&agrave;m lắng xuống mọi sợ h&atilde;i v&agrave; &acirc;u lọ Meggie c&ograve;n trẻ, l&uacute;c n&agrave;o cũng t&ograve; m&ograve;, khao kh&aacute;t được hiểu tất cả những g&igrave; cần thiết; đầu &oacute;c chưa bao giờ bị rối loạn bởi những triết l&yacute; vốn thường g&acirc;y hoang mang. Cha Ralph l&agrave; người bạn của c&ocirc;, l&agrave; thần tượng y&ecirc;u thương của l&ograve;ng c&ocirc;, một mặt trời mới trong bầu trời ri&ecirc;ng của cộ</h2>\r\n\r\n<h2>- Meggie, b&acirc;y giờ con phải trở về nh&agrave; v&agrave; n&oacute;i cho mẹ con biết l&agrave; con bị ra m&aacute;u. Mẹ con sẽ chỉ c&aacute;ch l&agrave;m thế n&agrave;o để giữ cho con được sạch.</h2>\r\n\r\n<h2>- Mẹ con cũng bị như thế chứ?</h2>\r\n\r\n<h2>- Tất cả phụ nữ khỏe mạnh đều bị như thế. Nhưng khi mang thai, sẽ ngưng lại cho đến ng&agrave;y sinh em b&eacute;. Ch&iacute;nh khi tắt kinh, người phụ nữ biết rằng m&igrave;nh đang mang trong người một mầm sống mới.</h2>\r\n\r\n<h2>- Tại sao m&aacute;u ngưng chảy ra khi người phụ nữ chờ đợi sinh con?</h2>\r\n\r\n<h2>- Cha kh&ocirc;ng biết, thật sự kh&ocirc;ng biết. Xin lỗi con, Meggiẹ</h2>\r\n\r\n<h2>- Tại sao m&aacute;u lại chảy ở ph&iacute;a sau?</h2>\r\n\r\n<h2>Cha Ralph hướng mắt giận dữ về ph&iacute;a tượng th&aacute;nh đang b&igrave;nh thản ngắm nh&igrave;n &ocirc;ng, bất động trước những buồn phiền m&agrave; những người phụ nữ gặp phải. Mọi việc trở n&ecirc;n qu&aacute; h&oacute;c b&uacute;a đối với cha Ralph. &ocirc;ng kh&ocirc;ng ngờ Meggie lại đặt c&aacute;c c&acirc;u hỏi xa vời m&agrave; b&igrave;nh thường c&ocirc; hay bỏ lửng nửa chừng, điều ấy khiến cho cha kinh ngạc! Nhưng &ocirc;ng &yacute; thức rằng từ nay &ocirc;ng sẽ l&agrave; nguồn cung cấp hiểu biết cho Meggie, &ocirc;ng cố tr&aacute;nh bằng mọi gi&aacute; kh&ocirc;ng để c&ocirc; ph&aacute;t hiện sự bối rối v&agrave; kh&oacute; chịu của m&igrave;nh. Rất c&oacute; khả năng, c&ocirc; sẽ thu m&igrave;nh lại v&agrave; kh&ocirc;ng th&egrave;m đặt c&acirc;u hỏi với &ocirc;ng nữa.</h2>\r\n\r\n<h2>- M&aacute;u kh&ocirc;ng chảy ra từ ph&iacute;a sau của con, Meggie - &ocirc;ng trả lời, với tất cả sự ki&ecirc;n nhẫn. N&oacute; chảy ra từ một nơi ẩn giấu, ph&iacute;a trước th&acirc;n thể con, c&aacute;i nơi d&iacute;nh d&aacute;ng mật thiết với chuyện c&oacute; con.</h2>\r\n\r\n<h2>- Ồ, c&oacute; phải &yacute; cha muốn n&oacute;i m&aacute;u chảy ra từ chỗ ấy? Meggie reo l&ecirc;n. Nhiều lần con đ&atilde; tự hỏi c&aacute;c em b&eacute; ra đời như thế n&agrave;o?</h2>\r\n\r\n<h2>&ocirc;ng mỉm cười, nhấc bổng Meggie l&ecirc;n khỏi bệ đ&aacute;.</h2>\r\n\r\n<h2>- B&acirc;y giờ th&igrave; Meggie đ&atilde; biết tất cả rồi. Con c&oacute; biết ai tạo ra những đứa con kh&ocirc;ng, Meggiẻ</h2>\r\n\r\n<h2>- Ồ, biết chứ! - Meggie ưỡn ngực ra, vui sướng v&igrave; đ&atilde; hiểu biết ch&uacute;t &iacute;t - Người ta l&agrave;m cho c&aacute;c em b&eacute; mọc ra.</h2>\r\n\r\n<h2>- V&agrave; con c&oacute; biết ch&uacute;ng bắt đầu mọc ra như thế n&agrave;o kh&ocirc;ng?</h2>\r\n\r\n<h2>- Khi người ta muốn c&oacute; c&aacute;c em b&eacute; một c&aacute;ch m&atilde;nh liệt.</h2>\r\n\r\n<h2>- Ai n&oacute;i cho con nghe điều ấy?</h2>\r\n\r\n<h2>- Kh&ocirc;ng ai cả. Con tự hiểu như thế.</h2>\r\n\r\n<h2>Cha Ralph nhắm mắt lại v&agrave; thầm nghĩ kh&ocirc;ng ai c&oacute; thể kết tội &ocirc;ng h&egrave;n nh&aacute;t v&igrave; đ&atilde; bỏ lửng chuyện ấy ở đ&oacute;. D&ugrave; cho &ocirc;ng c&oacute; thương hại c&ocirc; g&aacute;i b&eacute; nhỏ, &ocirc;ng vẫn kh&ocirc;ng thể gi&uacute;p đỡ c&ocirc; ta xa hơn. Thử th&aacute;ch m&agrave; &ocirc;ng trải qua như thế qu&aacute; đủ rồi.</h2>\r\n\r\n<p>&nbsp;</p>', 0, 'tieng-chim-hot-trong-bui-man-gai-chuong-09-10');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(49, 25, 'Tiếng chim hót trong bụi mận gai - Chương 11-12', 'Tiếng chim hót trong bụi mận gai - Chương 11-12', '<h2>Mary Carson sắp 72 tuổi, b&agrave; chuẩn bị chu đ&aacute;o cho buổi tiếp t&acirc;n lớn, chưa từng c&oacute; ở Drogheda trong 50 năm quạ Ng&agrave;y kỷ niệm sinh nhật của b&agrave; đ&uacute;ng v&agrave;o thượng tuần th&aacute;ng 11, bấy giờ trời vẫn c&ograve;n n&oacute;ng dữ dội, nhất l&agrave; đối với người d&acirc;n ở Gillỵ</h2>\r\n\r\n<h2>- B&agrave; Smith h&atilde; nhớ nh&eacute; điều t&ocirc;i đ&atilde; n&oacute;i với b&agrave;. Minnie th&igrave; thầm. Đừng qu&ecirc;n nh&eacute;! B&agrave; ta sinh ng&agrave;y 3 th&aacute;ng 11?</h2>\r\n\r\n<h2>- Nhưng m&agrave;y muốn n&oacute;i g&igrave; nữa, Minniẻ B&agrave; quản gia hỏi.</h2>\r\n\r\n<h2>Th&aacute;i độ b&iacute; hiểm theo kiểu người Scotland của Minnie g&acirc;y bực bội cho b&agrave; gi&agrave; người Anh.</h2>\r\n\r\n<h2>- Sao, b&agrave; kh&ocirc;ng hiểu &agrave;? Điều đ&oacute; chứng tỏ ng&agrave;y sinh của b&agrave; ta l&agrave; con bọ cạp. Một con bọ cạp... B&agrave; c&oacute; thấy đ&uacute;ng thế kh&ocirc;ng?</h2>\r\n\r\n<h2>Tr&ecirc;n chiếc ghế b&agrave;nh, con nhện c&aacute;i gi&agrave; ở trung t&acirc;m mạng lưới, ph&aacute;t ra một loạt mệnh lệnh; phải l&agrave;m thế n&agrave;y, phải l&agrave;m thế kia, đem c&aacute;i n&agrave;y ra, dời c&aacute;i kia đi chỗ kh&aacute;c. Hai c&ocirc; g&aacute;i hầu ph&ograve;ng đang đ&aacute;nh b&oacute;ng những đồ bằng bạc v&agrave; rửa bộ ch&eacute;n đĩa bằng sứ Haviland, biến nh&agrave; nguyện nhỏ trong nh&agrave; th&agrave;nh nơi chi&ecirc;u đ&atilde;i v&agrave; xếp những b&agrave;n d&agrave;i đầy b&aacute;nh rượu ở mấy ph&ograve;ng b&ecirc;n cạnh.</h2>\r\n\r\n<h2>D&agrave;n nhạc Clarence &Oacute; Toole sẽ từ Sydney đến c&ugrave;ng một l&uacute;c với c&aacute;c m&oacute;n hải sản qu&iacute;. Cả một v&ugrave;ng rộng lớn, từ Rudna Hunish đến Inishmurray v&agrave; Bugela đến Narrengang rộn r&agrave;ng chuẩn bị tham dự buổi lễ n&agrave;y.</h2>\r\n\r\n<h2>Giữa l&uacute;c nền đ&aacute; cẩm thạch của đại sảnh vang l&ecirc;n những tiếng b&agrave;n ghế x&ecirc; dịch, những tiệng gọi nhau ơi ới của những người khu&acirc;n v&aacute;c đang b&agrave;y viện v. v... ph&ograve;ng tiếp t&acirc;n th&igrave; Mary Carson rời khỏi chiếc ghế b&agrave;nh, đến ngồi sau b&agrave;n viết. B&agrave; lấy ra một tờ giấy mịn, chấp b&uacute;t v&agrave;o b&igrave;nh mực rồi bắt đầu. B&agrave; viết li&ecirc;n tục kh&ocirc;ng do dự, ngay cả khi chấm phẩy. Năm năm gần đ&acirc;y, b&agrave; đ&atilde; suy nghĩ một c&aacute;ch chi li nội dung từng c&acirc;u phức tạp, sắp xếp thật chỉnh để từng chữ được sử dụng đ&uacute;ng, ch&iacute;nh x&aacute;c. Viết th&igrave; chẳng mất bao nhi&ecirc;u. B&agrave; Carson viết đầy hai tờ giấy, trong đ&oacute; một tờ chỉ hai phần bạ Khi viết xong d&ograve;ng cuối c&ugrave;ng, b&agrave; ngồi y&ecirc;n một l&uacute;c. B&agrave;n viết đặt b&ecirc;n cạnh cửa sổ lớn, chỉ cần quay đầu l&agrave; đ&atilde; nh&igrave;n thấy thảm cỏ trước nh&agrave;. Tiếng cười từ ngo&agrave;i vọng v&agrave;o l&agrave;m b&agrave; ch&uacute; &yacute;, l&uacute;c đầu b&agrave; chẳng nghĩ g&igrave; nhưng sau đ&oacute; lại khơi dậy trong b&agrave; sự bực bội tức tối ngấm ngầm. Cầu ch&uacute;a h&atilde;y đầy hắn xuống địa ngục, hắn v&agrave; con người &aacute;m ảnh hắn.</h2>\r\n\r\n<h2>Cha Ralph bỗng nẩy &yacute; kiến tập Meggie cỡi ngựa; d&ugrave; l&agrave; một c&ocirc; g&aacute;i ở đồng qu&ecirc;, chưa bao giờ Meggie c&oacute; dịp ngồi tr&ecirc;n y&ecirc;n ngựa. Thực tế lạ l&ugrave;ng l&agrave; c&aacute;c c&ocirc; g&aacute;i xuất th&acirc;n từ những gia đ&igrave;nh ngh&egrave;o rất &iacute;t khi được cỡi ngựa. Đ&acirc;y l&agrave; một tr&ograve; ti&ecirc;u khiển d&agrave;nh cho c&aacute;c phụ nữ trẻ gi&agrave;u c&oacute; - Ở n&ocirc;ng th&ocirc;n hay th&agrave;nh thị cũng thế.</h2>\r\n\r\n<h2>Một h&ocirc;m cha Ralph mang từ Gilly đến một đ&ocirc;i ủng ngắn, bộ quần &aacute;o cỡi ngựa v&agrave; n&eacute;m c&aacute;c thứ ấy l&ecirc;n chiếc b&agrave;n ở nh&agrave; bếp gia d&igrave;nh Clearỵ Pađy đang đọc s&aacute;ch sau buổi ăn trưa giật m&igrave;nh, nh&igrave;n l&ecirc;n ngạc nhi&ecirc;n.</h2>\r\n\r\n<h2>- Ủa, chuyện g&igrave; đ&oacute;, thưa chả &ocirc;ng ta hỏi.</h2>\r\n\r\n<h2>- Bộ đồ đi ngựa cho Meggiẹ</h2>\r\n\r\n<h2>- Sao? Pađy kinh ngạc.</h2>\r\n\r\n<h2>- Đồ đi ngựa cho Meggiẹ Pađy &agrave;, &ocirc;ng kh&ocirc;ng thể n&agrave;o vứt bỏ c&aacute;i lớp nh&agrave; qu&ecirc; của &ocirc;ng hay sao? &ocirc;ng l&agrave; người thừa kế của một điền trang rộng v&agrave; gi&agrave;u nhất xứ New South Wales, vậy m&agrave; chưa bao giờ &ocirc;ng cho ph&eacute;p c&ocirc; con g&aacute;i duy nhất của &ocirc;ng được cỡi ngựa! Thế th&igrave; l&agrave;m sao con g&aacute;i của &ocirc;ng giữ được vị tr&iacute; b&ecirc;n cạnh &aacute;i nữ Carmichael, &aacute;i nữ Hopeton v&agrave; phu nh&acirc;n Antony King, tất cả đều l&agrave; những kỵ sĩ t&agrave;i bạ Meggie phải tập cỡi ngựa ngồi một b&ecirc;n theo c&aacute;ch d&agrave;nh cho phụ nữ v&agrave; cả hai b&ecirc;n như đ&agrave;n &ocirc;ng. &ocirc;ng c&oacute; nghe t&ocirc;i kh&ocirc;ng &ocirc;ng Pađỷ T&ocirc;i biết &ocirc;ng rất bận, vậy t&ocirc;i sẽ dạy cho Meggie nghệ thuật cỡi ngựa, d&ugrave; &ocirc;ng c&oacute; th&iacute;ch hay kh&ocirc;ng th&iacute;ch. Nếu v&igrave; chuyện n&agrave;y Meggie c&oacute; xao l&atilde;ng việc nh&agrave; th&igrave; cũng chẳng sao. Một v&agrave;i giờ trong tuần Fiona sẽ kh&ocirc;ng c&oacute; Meggie b&ecirc;n cạnh để l&agrave;m việc nh&agrave;. Ch&uacute;ng ta kh&ocirc;ng trở lại vấn đề n&agrave;y, dứt kho&aacute;t l&agrave; như thế.</h2>\r\n\r\n<h2>Pađy kh&ocirc;ng c&oacute; khả năng đối đầu với một linh mục; thế l&agrave; từ đ&oacute;, Meggie bắt đầu tập cỡi ngựa. Nhiều năm trước đ&acirc;y c&ocirc; b&eacute; ao ước ph&uacute;t gi&acirc;y n&agrave;y. C&oacute; một lần, Meggie đ&atilde; b&agrave;y tỏ ước muốn đ&oacute; với cha nhưng Pađy lại qu&ecirc;n, v&agrave; c&ocirc; b&eacute; cũng kh&ocirc;ng nhắc lại coi như cha m&igrave;nh im lặng c&oacute; nghĩa l&agrave; từ chối. Được học cỡi ngựa dưới sự hướng dẫn của cha Ralph, Meggie sung sướng ph&aacute;t đi&ecirc;n l&ecirc;n, kh&ocirc;ng hề giấu giếm niềm vui của m&igrave;nh. Sự ngưỡng mộ của Meggie đối với cha Ralph giờ đ&acirc;y d&atilde; biến th&agrave;nh mối t&igrave;nh n&oacute;ng bỏng của một c&ocirc; g&aacute;i l&eacute;m lỉnh. Vẫn biết rằng mối t&igrave;nh ấy l&agrave; kh&ocirc;ng đi đến đ&acirc;u nhưng c&ocirc; b&eacute; vẫn tự cho ph&eacute;p m&igrave;nh mơ tưởng về con người ấy. Meggie tưởng tượng c&aacute;i cảm gi&aacute;c m&agrave; c&oacute; được khi ở trong v&ograve;ng tay của cha Ralph v&agrave; cả những nụ h&ocirc;n nồng n&agrave;n của chạ Những suy nghĩ thơ thẩn của Meggie kh&ocirc;ng đi xa hơn; c&ocirc; b&eacute; kh&ocirc;ng c&oacute; ch&uacute;t &yacute; tưởng g&igrave; về điều c&oacute; thể xảy ra sau đ&oacute;, thậm ch&iacute; kh&ocirc;ng hiểu những diễn biến tiếp theo sẽ như thế n&agrave;o.</h2>\r\n\r\n<h2>Nếu c&ocirc; b&eacute; biết rằng mơ tưởng đến một linh mục l&agrave; xấu th&igrave; ch&iacute;nh c&ocirc; cũng kh&ocirc;ng đủ sức để n&eacute; tr&aacute;nh. B&acirc;y giờ c&ocirc; chỉ c&ograve;n biết che giấu l&ograve;ng m&igrave;nh, kh&ocirc;ng để cho cha Ralph đo&aacute;n được những &yacute; nghĩ thầm k&iacute;n.</h2>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"height:260px; width:86%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top\">\r\n			<h2><strong>Chương 12</strong></h2>\r\n\r\n			<h2>&nbsp;</h2>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2>Trong khi Mary Carson đang đứng sau c&aacute;nh cửa sổ ph&ograve;ng kh&aacute;ch theo d&otilde;i cả hai người th&igrave; cha Ralph v&agrave; Meggie từ chuồng ngựa đang đi về tận b&ecirc;n kia đối diện với ng&ocirc;i nh&agrave; lớn, c&ugrave;ng một hướng với nh&agrave; của người quản l&yacute;. Những người l&agrave;m c&ocirc;ng ở trang trại chỉ được cỡi tr&ecirc;n những con ngựa chưa hề nu&ocirc;i trong chuồng m&agrave; thả ngo&agrave;i s&acirc;n hay lang thang đi ăn cỏ. Nhưng ở Drogheda vẫn c&oacute; những đ&agrave;n ngựa rất đẹp mặc d&ugrave; chỉ c&oacute; cha Ralph l&agrave; được cỡi tr&ecirc;n những con ngựa đ&oacute;. Mary Carson đ&atilde; giữ lại hai con ngựa thuần giống đặc biệt d&agrave;nh cho cha Ralph sử dụng, kh&ocirc;ng thể để những con ngựa gi&agrave; ốm nhom cho chạ Khi cha Ralph hỏi b&agrave; Mary Carson c&oacute; bằng l&ograve;ng cho Meggie cỡi tr&ecirc;n một trong hai con ngựa ấy kh&ocirc;ng, b&agrave; Mary kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o từ chối. C&ocirc; g&aacute;i l&agrave; ch&aacute;u của b&agrave; vả lại l&yacute; do của cha Ralph đưa ra l&agrave; ch&iacute; l&yacute;. C&ocirc; b&eacute; phải biết cỡi ngựa chứ, ch&aacute;u g&aacute;i của b&agrave; chủ kia m&agrave;.</h2>\r\n\r\n<h2>Trong th&acirc;m t&acirc;m, b&agrave; Mary Carson rất muốn từ chối hoặc &iacute;t ra n&ecirc;n c&ugrave;ng đi với họ. Nhưng b&agrave; kh&ocirc;ng thể cưỡng lại lời y&ecirc;u cầu của linh mục, cũng như với c&aacute;i tuổi n&agrave;y b&agrave; kh&ocirc;ng thể ngồi tr&ecirc;n lưng ngựa được nữa. Thế l&agrave; b&agrave; đ&agrave;nh chịu thua một c&aacute;ch bực tức khi nh&igrave;n thấy họ s&aacute;nh vai đi qua b&atilde;i cỏ. Người đ&agrave;n &ocirc;ng mặc quần ngắn, đ&ocirc;i ủng cao tới đầu gối, v&oacute;c người mảnh khảnh, n&eacute;t mặt c&oacute; ch&uacute;t liến thoắng của con trai. Cặp nam nữ n&agrave;y biểu lộ t&igrave;nh cảm th&acirc;n mật v&agrave; hết sức thanh thản. Đ&acirc;y l&agrave; lần thứ một ng&agrave;n, b&agrave; Mary Carson tự hỏi tại sao ngo&agrave;i b&agrave; ra, kh&ocirc;ng ai đặt vấn đề mối quan hệ ấy. Pađy th&igrave; tỏ ra hết sức bằng l&ograve;ng về mối quan hệ giữa con g&aacute;i m&igrave;nh v&agrave; cha Ralph, c&ograve;n Fiona l&uacute;c n&agrave;o cũng im lặng chẳng n&oacute;i lời n&agrave;o như kh&uacute;c gỗ. Phải chăng v&igrave; y&ecirc;u Ralph de Bricassart m&agrave; b&agrave; Mary Carson ph&aacute;t hiện ra c&aacute;i điều kh&ocirc;ng ai thấy được? Hoặc l&agrave; &oacute;c tưởng tượng của b&agrave; đ&atilde; hại b&agrave;? C&oacute; đ&uacute;ng đ&acirc;y l&agrave; t&igrave;nh bạn giữa một người đ&agrave;n &ocirc;ng gần 35 tuổi d&agrave;nh cho một c&ocirc; g&aacute;i chưa trưởng th&agrave;nh? Ngu ngốc! Kh&ocirc;ng một người đ&agrave;n &ocirc;ng n&agrave;o kể cả Ralph lại kh&ocirc;ng biết Meggie như b&ocirc;ng hồng đang nở sao? Với Ralph th&igrave; c&oacute; g&igrave; lọt qua được con mắt của &ocirc;ng ta đ&acirc;u?</h2>\r\n\r\n<h2>B&agrave;n tay của Mary Carson run l&ecirc;n, những giọt mực xanh đen rơi xuống l&agrave;m bẩn trang giấy trước mặt b&agrave; tạ Những ng&oacute;n tay khẳng khiu đưa v&agrave;o hộc tủ lấy ra một tờ giấy kh&aacute;c. B&agrave; lại cầm viết chấm v&agrave;o b&igrave;nh mực v&agrave; viết lại từng chữ, với sự tự tin v&agrave; viết su&ocirc;ng sẻ kh&ocirc;ng kh&aacute;c lần trước. Sau đ&oacute; b&agrave; đứng l&ecirc;n v&agrave; nặng nề đi về ph&iacute;a cửa.</h2>\r\n\r\n<h2>- Minnie, Minnie! B&agrave; gọi tọ</h2>\r\n\r\n<h2>- Ch&uacute;a ơi! Ch&iacute;nh b&agrave; ta gọi. - Chị hầu ph&ograve;ng k&ecirc;u l&ecirc;n bằng một giọng trong trẻo. Con c&oacute; thể gi&uacute;p g&igrave; cho b&agrave;, thưa b&agrave; Carson? Chị ta vừa hỏi vừa nghĩ thầm tại sao b&agrave; ta kh&ocirc;ng gọi b&agrave; Smith như mọi khị</h2>\r\n\r\n<h2>- M&agrave;y đi gọi ngay thằng l&agrave;m h&agrave;ng r&agrave;o v&agrave; Tom đến đ&acirc;y ngaỵ</h2>\r\n\r\n<h2>- Con c&oacute; cần n&oacute;i cho b&agrave; Smith biết kh&ocirc;ng?</h2>\r\n\r\n<h2>- Kh&ocirc;ng, m&agrave;y chỉ cần l&agrave;m theo như tao bảo.</h2>\r\n\r\n<h2>Khi cả hai đến, Mary Carson hỏi:</h2>\r\n\r\n<h2>- Hai anh c&oacute; biết viết kh&ocirc;ng?</h2>\r\n\r\n<h2>Cả hai vừa nuốt nước miếng để tự trấn an vừa gật đầu.</h2>\r\n\r\n<h2>- Vậy th&igrave; tốt. T&ocirc;i muốn hai anh nh&igrave;n t&ocirc;i k&yacute; t&ecirc;n tr&ecirc;n tờ giấy n&agrave;y, rồi hai anh k&yacute; t&ecirc;n v&agrave; viết địa chỉ của hai anh ở dưới chữ k&yacute; của t&ocirc;i. Hiểu chứ?</h2>\r\n\r\n<h2>Cả hai gật đầu.</h2>\r\n\r\n<h2>Mary Carson chăm ch&uacute; theo d&otilde;i họ v&agrave; khi hai người đ&atilde; viết xong, b&agrave; trao cho mỗi người một tờ bạc mười bảng với lời dặn phải biết giữ miệng.</h2>\r\n\r\n<h2>Meggie v&agrave; cha Ralph đ&atilde; biến mất tự bao giờ. Mary Carson nặng nề ngồi xuống b&agrave;n viết, lại lấy ra một tờ giấy kh&aacute;c v&agrave; lại bắt đầu viết. Lần n&agrave;y, c&oacute; lẽ do nội dung được viết kh&ocirc;ng đơn giản n&ecirc;n b&agrave; kh&ocirc;ng c&ograve;n khoan thai như trước. Rất nhiều lần, b&agrave; dừng lại để suy nghĩ, m&ocirc;i m&iacute;m chặt, mỉm cười nhưng kh&ocirc;ng vui, rồi b&agrave; lại tiếp tục. H&igrave;nh như b&agrave; c&oacute; nhiều điều phải n&oacute;i: c&aacute;c h&agrave;ng chữ san s&aacute;t nhau, thế m&agrave; b&agrave; vẫn cần một tờ thứ hai. Sau c&ugrave;ng b&agrave; đọc kỹ một lần nữa, b&agrave; xếp lại v&agrave; cho v&agrave;o một bao thư, b&ecirc;n ngo&agrave;i ni&ecirc;m phong bằng xi đỏ...</h2>\r\n\r\n<h2>Chỉ c&oacute; Pađy, Fiona, Bob, Jack v&agrave; Meggie phải đi dự buổi chi&ecirc;u đ&atilde;i; Hughie v&agrave; Stuart nhẹ nh&otilde;m v&igrave; được ở nh&agrave; để lo cho hai em b&eacute;. Thật l&agrave; một trường hợp hết sức đặc biết, Mary Carson đ&atilde; tr&uacute;t hầu bao kh&aacute; rộng r&atilde;i để cho mỗi người một bộ quần &aacute;o đẹp nhất c&oacute; thể c&oacute; được ở Gilly n&agrave;y.</h2>\r\n\r\n<h2>Khi Fiona v&agrave; Pađy ra khỏi ph&ograve;ng, mấy đứa con trai của hai &ocirc;ng b&agrave; hết sức ngạc nhi&ecirc;n chưa bao giờ ch&uacute;ng lại nh&igrave;n thấy cha mẹ ăn mặc đẹp một c&aacute;ch vương giả như thế.</h2>\r\n\r\n<h2>Tuy nhi&ecirc;n người g&acirc;y sự ch&uacute; &yacute; hơn hết trong căn ph&ograve;ng n&agrave;y ch&iacute;nh l&agrave; Meggiẹ C&oacute; lẽ v&igrave; nhớ lại thời nhỏ bị coi rẻ của m&igrave;nh v&agrave; đi&ecirc;n tiết v&igrave; c&aacute;c phụ nữ trẻ kh&aacute;c đều đặt may quần &aacute;o ở Sydney n&ecirc;n chị thợ may ở Gilly đ&atilde; đặt hết t&agrave;i năng v&agrave;o chiếc &aacute;o của Meggiẹ Đ&oacute; l&agrave; chiếc &aacute;o d&agrave;i kh&ocirc;ng c&oacute; tay, cổ hở kh&aacute; thấp. Fiona hơi ngại nhưng Meggie lại n&agrave;i nỉ mẹ ch&igrave;u m&igrave;nh. Chị thợ may cam đoan rằng tất cả c&aacute;c thiếu nữ b&acirc;y giờ đều ăn mặc như thế, lẽ n&agrave;o lại để Meggie ăn mặc như một đứa con g&aacute;i nh&agrave; qu&ecirc;, l&agrave;m tr&ograve; cười cho thi&ecirc;n hạ. Thế l&agrave; cuối c&ugrave;ng Fiona đ&atilde; nh&acirc;n nhượng.</h2>\r\n\r\n<h2>Pađy d&atilde; h&aacute; miệng khi đối diện với con g&aacute;i m&igrave;nh nhưng &ocirc;ng đ&atilde; kịp k&igrave;m chết lại v&agrave; kh&ocirc;ng để lọt m&ocirc;i lời n&agrave;o ra d&ugrave; cho cổ họng &ocirc;ng n&oacute;ng ra v&agrave; ngứa ng&aacute;y. B&agrave;i học cay đắng xảy ra ở nh&agrave; xứ với Frank tuy đ&atilde; l&acirc;u, nhưng Pađy vẫn c&ograve;n nhớ m&atilde;i.</h2>\r\n\r\n<h2>&ocirc;ng đưa tay về hướng con g&aacute;i v&agrave; mỉm cười dịu d&agrave;ng.</h2>\r\n\r\n<h2>- Ồ, Meggie, con đẹp qu&aacute;! Đến đ&acirc;y, ba muốn đ&oacute;ng vai ch&agrave;ng hiệp sĩ hộ vệ con. C&ograve;n Bob v&agrave; Jack lo cho mẹ con.</h2>\r\n\r\n<h2>Th&aacute;ng tới Meggie 17 tuổi v&agrave; lần đầu ti&ecirc;n trong cuộc đời, Pađy cảm thấy m&igrave;nh gi&agrave;. C&ograve;n Meggie, vẫn l&agrave; đứa con g&aacute;i y&ecirc;u qu&iacute; của &ocirc;ng. Kh&ocirc;ng thể để bất cứ chuyện g&igrave; l&agrave;m hỏng sự xuất hiện lần đầu của Meggie trước đ&aacute;m đ&ocirc;ng.</h2>\r\n\r\n<h2>Cha Ralph vẫn mặc chiếc &aacute;o thụng như thường lệ Kh&ocirc;ng c&oacute; một thứ thời trang n&agrave;o của đ&agrave;n &ocirc;ng lại th&iacute;ch hợp với cha Ralph hơn l&agrave; chiếc &aacute;o m&agrave;u đen theo một kiểu cắt rất nghi&ecirc;m trang: rộng ra ở ph&iacute;a dưới, v&ocirc; số n&uacute;t chạy d&agrave;i từ cổ xuống với chiếc thắt lưng rộng viền đỏ.</h2>\r\n\r\n<h2>Mary Carson mặc to&agrave;n một m&agrave;u trắng, xa tanh trắng, đăng ten trắng v&agrave; l&ocirc;ng đ&agrave; điểu trắng tr&ecirc;n chiếc &aacute;o của b&agrave;. Fiona nh&igrave;n b&agrave; sửng sốt v&agrave; kh&ocirc;ng c&ograve;n c&oacute; được th&aacute;i độ thản nhi&ecirc;n như mọi khị C&aacute;ch ăn mặc của Mary Carson thật l&agrave; lố bịch giống như c&ocirc; d&acirc;u trong ng&agrave;y cưới, kh&ocirc;ng thể n&agrave;o tưởng tượng được. - Tại sao b&agrave; ta lại đỏm d&aacute;ng như một c&ocirc; g&aacute;i gi&agrave; thất t&igrave;nh b&ocirc;i tr&eacute;t phấn son dị hợm.</h2>\r\n\r\n<h2>Thật ra b&agrave; ta giống từng n&eacute;t một bức ảnh nổi tiếng của nữ ho&agrave;ng Victoria chụp trước ng&agrave;y chết.</h2>\r\n\r\n<h2>Mary Carson cười với Pađy v&agrave; đặt tay l&ecirc;n vai em trai m&igrave;nh.</h2>\r\n\r\n<h2>- Em c&oacute; thể đưa tay ra d&igrave;u chị đến b&agrave;n ăn, Padraic. C&ograve;n cha Ralph sẽ đưa Fiona, mấy đứa con trai th&igrave; đi theo Meggie - Con c&oacute; khi&ecirc;u vũ tối nay kh&ocirc;ng, Meggiẻ</h2>\r\n\r\n<h2>Pađy nhanh miệng trả lời cho con:</h2>\r\n\r\n<h2>- N&oacute; c&ograve;n nhỏ qu&aacute;. N&oacute; mới mư&ograve;i bảy tuổi. N&oacute;i xong Pađy sực nhớ lại, một thiếu s&oacute;t kh&aacute;c của gia đ&igrave;nh m&igrave;nh l&agrave; c&aacute;c con đều kh&ocirc;ng hề được tập nhảy.</h2>\r\n\r\n<h2>- Rất tiếc! B&agrave; Mary Carson n&oacute;i bu&ocirc;ng th&otilde;ng.</h2>\r\n\r\n<h2>- Cuộc chi&ecirc;u đ&atilde; thật r&igrave;nh rang, một vũ hội rực rỡ, s&aacute;ng ch&oacute;i, kh&ocirc;ng thể n&agrave;o qu&ecirc;n. Đ&oacute; l&agrave; những lời b&igrave;nh phẩm nghe được qua c&aacute;c cuộc trao đổi tối h&ocirc;m đ&oacute;. Royal &Oacute; Mara, từ Inishmurray c&aacute;ch Drogheda 300 c&acirc;y số cũng c&oacute; mặt c&ugrave;ng vợ, c&aacute;c con trai v&agrave; con g&aacute;i duy nhất. D&acirc;n ch&uacute;ng ở trong v&ugrave;ng kh&ocirc;ng ngại đường xa 300 c&acirc;y số để đến dự một trận đấu Cricket, m&ocirc;n thể thao ưa th&iacute;ch ở &uacute;c, kh&ocirc;ng thể tưởng tượng l&agrave; họ đi một đo&agrave;n d&agrave;i như thể dự một... cuộc tiếp t&acirc;n. Duncan Gordon đến từ Each Uisgẹ Martin King với vợ, con trai Anthony v&agrave; c&ocirc; con d&acirc;u; Evan Pugh từ Braich Y Pull; Dominic &Oacute; Rourk từ Dibban Dibban, Horry Hoperton từ Beel - Beel v&agrave; hơn một chục nh&acirc;n vật tiếng t&acirc;m kh&aacute;c trong v&ugrave;ng. Hầu hết theo đạo C&ocirc;ng gi&aacute;o v&agrave; rất &iacute;t người trong số họ mang t&ecirc;n Anglo - Saxon; số người Irish, Scottish v&agrave; Welsh gần bằng nhau. C&oacute; người đồn rằng b&agrave; Mary c&oacute; đủ tiền để mua v&agrave; b&aacute;n cả vua nước Anh nước. B&agrave; ta đầu tư khai th&aacute;c th&eacute;p, bạc, ch&igrave;, thiếc, đồng v&agrave; v&agrave;ng, bỏ vốn v&agrave;o h&agrave;ng trăm x&iacute; nghiệp. Drogheda từ l&acirc;u kh&ocirc;ng c&ograve;n được coi l&agrave; nguồn sinh lợi ch&iacute;nh của b&agrave;; thu nhập ở điền trang n&agrave;y kh&ocirc;ng c&ograve;n c&oacute; nghĩa l&yacute; g&igrave; v&agrave; chỉ l&agrave; một c&ocirc;ng việc l&agrave;m chơi cho vui của Marỵ</h2>\r\n\r\n<h2>Cha Ralh kh&ocirc;ng n&oacute;i chuyện với Meggie trong buổi ăn v&agrave; cảu sau đ&oacute;. Suốt buổi chi&ecirc;u đ&atilde;i, &ocirc;ng qu&ecirc;n hẳn Meggiẹ Như bị x&uacute;c phạm, c&ocirc; g&aacute;i cứ theo d&otilde;i &ocirc;ng khắp ph&ograve;ng. Cảm nhận được sự xao xuyến ở Meggie, cha Ralph đến gần c&ocirc; b&eacute;, cắt nghĩa cho c&ocirc; r&otilde; nếu &ocirc;ng tỏ ra ch&uacute; &yacute; Meggie hơn c&aacute;c c&ocirc; ga&iacute; kh&aacute;c như Carmichael, Gordon hay &Oacute; Mara chẳng hạn th&igrave; tiếng tăm của hai người c&oacute; thể bị ảnh hương xấu. Cũng như Meggie, cha Ralph kh&ocirc;ng khi&ecirc;u vũ v&agrave; cũng như c&ocirc;, &ocirc;ng thu h&uacute;t sự ch&uacute; &yacute; của nhiều thiếu nữ; cả hai đẹp hơn hẳn tất cả những người c&oacute; mặt.</h2>\r\n\r\n<h2>Pađy v&agrave; Fiona vẫn ở lại chơi nhưng v&agrave;o nửa đ&ecirc;m, Bob v&agrave; Jack c&ugrave;ng Meggie rời khỏi cuộc vui. Cha mẹ họ kh&ocirc;ng hay họ đ&atilde; đi chơi nơi kh&aacute;c.</h2>\r\n\r\n<h2>Khi Meggie rời khỏi ph&ograve;ng, cha Ralph thấy m&igrave;nh trẻ hơn &iacute;t nhất mười tuổi. &ocirc;ng trở n&ecirc;n sinh động v&agrave; g&acirc;y ngạc nhi&ecirc;n cho c&aacute;c c&ocirc; g&aacute;i như Hopeton, Mackail, Gordon v&agrave; &Oacute; Mara về những bước nhảy với Carmichael. Sau đ&oacute;, cha Ralph mời lần lượt tất cả c&aacute;c thiếu nữ, lu&ocirc;n cả Pugh, xấu x&iacute; nhất. Kh&ocirc;ng một người kh&aacute;ch n&agrave;o c&oacute; thể than phiền rằng con g&aacute;i m&igrave;nh đ&atilde; mất cơ hội khi&ecirc;u vũ với cha De Bricassart.</h2>\r\n\r\n<h2>Đến ba giờ s&aacute;ng, Mary Carson đứng l&ecirc;n một c&aacute;ch nặng nề v&agrave; ng&aacute;p:</h2>\r\n\r\n<h2>- Kh&ocirc;ng, cuộc vui cứ tiếp tục! Nếu t&ocirc;i mệt - quả thật t&ocirc;i đ&atilde; mệt - t&ocirc;i sẽ đi nằm, đ&oacute; cũng l&agrave; &yacute; định của t&ocirc;i; nhưng thức ăn v&agrave; đồ uống kh&ocirc;ng thiếu, c&ograve;n d&agrave;n nhạc đ&atilde; được trả tiền để chơi cho đến khi kh&ocirc;ng c&ograve;n một ai muốn nhảy nữa mới th&ocirc;i. Hơn nữa c&oacute; một ch&uacute;t ồn &agrave;o chỉ dỗ giấc ngủ t&ocirc;i th&ecirc;m nhiều mộng đẹp. Thưa cha, cha c&oacute; thể gi&uacute;p đưa t&ocirc;i l&ecirc;n lầu được kh&ocirc;ng?</h2>\r\n\r\n<h2>Khi rời khỏi ph&ograve;ng chi&ecirc;u đ&atilde;i, b&agrave; kh&ocirc;ng đi đến cầu thang rộng uy nghi dẫn l&ecirc;n lầu, m&agrave; lại hướng cha Ralph c&ugrave;ng đến ph&ograve;ng kh&aacute;ch. Tr&ocirc;ng b&agrave; c&oacute; vẻ mỏi mệt, dựa cả người v&agrave;o c&aacute;nh tay của cha Ralph. Cửa ph&ograve;ng đ&oacute;ng k&iacute;n, b&agrave; chờ linh mục d&ugrave;ng ch&igrave;a kh&oacute;a do b&agrave; trao để mở cửa. B&agrave; đi v&agrave;o trước.</h2>\r\n\r\n<h2>- Cuộc chi&ecirc;u đ&atilde;i th&agrave;nh c&ocirc;ng đấy b&agrave; Mary ạ, &ocirc;ng n&oacute;i.</h2>\r\n\r\n<h2>- Đ&acirc;y l&agrave; lần cuối c&ugrave;ng t&ocirc;i tổ chức, thưa chạ</h2>\r\n\r\n<h2>- Kh&ocirc;ng n&ecirc;n n&oacute;i thế, b&agrave; Mary th&acirc;n mến.</h2>\r\n\r\n<h2>- Tại sao? T&ocirc;i đ&atilde; ch&aacute;n sống v&agrave; t&ocirc;i sẽ kết th&uacute;c cuộc sống ở đ&acirc;y. Cha kh&ocirc;ng tin &agrave;? Trong hơn 70 năm, t&ocirc;i lu&ocirc;n lu&ocirc;n l&agrave;m đ&uacute;ng những g&igrave; t&ocirc;i muốn. Cho n&ecirc;n nếu thần chết nghĩ rằng hắn c&oacute; quyền chọn lựa giờ ph&uacute;t ra đi của t&ocirc;i th&igrave; hắn sai lầm v&ocirc; c&ugrave;ng. T&ocirc;i sẽ chết v&agrave;o đ&uacute;ng c&aacute;i l&uacute;c t&ocirc;i chọn, m&agrave; tuyệt nhi&ecirc;n kh&ocirc;ng phải l&agrave; tự tử. Ch&iacute;nh l&ograve;ng ham sống gi&uacute;p cho ch&uacute;ng ta tồn tại; chấm dứt cuộc sống kh&ocirc;ng kh&oacute; khăn đ&acirc;u nếu ch&uacute;ng ta thật sự muốn. T&ocirc;i đ&atilde; mỏi mệt v&agrave; t&ocirc;i muốn chấm dứt. Rất đơn giản.</h2>\r\n\r\n<h2>Cha Ralph cũng thế, rất mỏi mệt, kh&ocirc;ng phải mỏi mệt với cuộc sống m&agrave; về c&aacute;i bề ngo&agrave;i kh&ocirc;ng thay đổi &ocirc;ng phải n&uacute;p ở ph&iacute;a sau, mỏi mệt v&igrave; kh&ocirc;ng kh&iacute; nh&agrave;m ch&aacute;n, sự thiếu vắng bạn tri kỷ v&agrave; mỏi mệt cả với ch&iacute;nh m&igrave;nh. Căn ph&ograve;ng lờ mờ dưới &aacute;nh s&aacute;ng của một c&acirc;y đ&egrave;n dầu bằng thủy tinh m&agrave;u hồng ngọc rất qu&iacute;, &aacute;nh đ&egrave;n tỏ ra những vệt s&aacute;ng m&agrave;u hồng l&ecirc;n mặt b&agrave; Mary Carson, l&agrave;m cho đường n&eacute;t cứng rắn của quai h&agrave;m b&agrave; c&agrave;ng trở n&ecirc;n ma qu&aacute;i. Cha Ralph thấy nhức ở ch&acirc;n, ở lưng, l&acirc;u rồi &ocirc;ng kh&ocirc;ng khi&ecirc;u vũ nhiều như thế mặc d&ugrave; &ocirc;ng vẫn tự h&agrave;o kh&ocirc;ng bị lạc hậu với những kiểu nhảy mới nhất. Ba mươi lăm tuổi rồi nhưng vẫn l&agrave; linh mục ở n&ocirc;ng th&ocirc;n... Vậy th&igrave; đến bao giờ mới trở th&agrave;nh một nh&acirc;n vật đầy quyền lực của Nh&agrave; thờ? Giấc một kết th&uacute;c trước khi bắt đầu - &ocirc; h&ocirc;! Những giấc mơ của thời trẻ! V&agrave; những lời n&oacute;i v&ocirc; t&acirc;m, sự s&ocirc;i nổi của tuổi trẻ. &ocirc;ng đ&atilde; thiếu bản lĩnh để chiến thắng thử th&aacute;ch. Nhưng kh&ocirc;ng bao giờ &ocirc;ng lặp lại sai lầm cũ, kh&ocirc;ng bao giờ, kh&ocirc;ng bao giờ...</h2>\r\n\r\n<h2>Kh&ocirc;ng giữ vững được chỗ đứng của m&igrave;nh, &ocirc;ng thở d&agrave;i. Đ&acirc;u c&ograve;n g&igrave;? Dịp may kh&ocirc;ng đến hai lần. Đ&atilde; đến l&uacute;c nh&igrave;n thẳng c&aacute;c sự việc, chấm dứt hy vọng v&agrave; mơ mộng.</h2>\r\n\r\n<h2>- Cha Ralph c&oacute; nhớ kh&ocirc;ng, t&ocirc;i đ&atilde; từng n&oacute;i với cha rằng t&ocirc;i sẽ đ&aacute;nh bại cha ngay tr&ecirc;n mảnh đất quen thuộc của cha, v&agrave; t&ocirc;i sẽ l&agrave;m cho cha bị sụp v&agrave;o ch&iacute;nh c&aacute;i bẫy của chả Tiếng n&oacute;i kh&ocirc;n khan k&eacute;o cha Ralph ra khỏi những &yacute; tưởng đang đeo đuổi trong l&uacute;c buồn ch&aacute;n. &ocirc;ng nh&igrave;n Mary Carson v&agrave; cười.</h2>\r\n\r\n<h2>- B&agrave; Mary th&acirc;n mến, t&ocirc;i kh&ocirc;ng qu&ecirc;n bất cứ điều g&igrave; m&agrave; b&agrave; đ&atilde; n&oacute;i. T&ocirc;i tự hỏi t&ocirc;i sẽ l&agrave;m g&igrave; nếu trong những năm qua kh&ocirc;ng c&oacute; sự gi&uacute;p đỡ của b&agrave;. Tr&iacute; tuệ của b&agrave;, sự kh&ocirc;n ngoan v&agrave; nhạy cảm của b&agrave; trước c&aacute;c sự việc...</h2>\r\n\r\n<h2>- Nếu t&ocirc;i trẻ hơn, t&ocirc;i đ&atilde; chinh phục &ocirc;ng bằng c&aacute;ch kh&aacute;c, Ralph. &ocirc;ng kh&ocirc;ng bao giờ biết t&ocirc;i kh&aacute;t khao đến mức n&agrave;o được l&ugrave;i lại ba mươi năm. Nếu c&oacute; con quỷ n&agrave;o xuất hiện tặng t&ocirc;i c&aacute;i xu&acirc;n sắc đổi lấy linh hồn t&ocirc;i, t&ocirc;i sẽ kh&ocirc;ng hề do dự mộ ph&uacute;t v&agrave; cũng kh&ocirc;ng bao giờ tiếc về cuộc thỏa thuận ấy như t&ecirc;n gi&agrave; ngu ngốc Faust đ&acirc;u. Nhưng th&ocirc;i đừng đi&ecirc;n rồ... T&ocirc;i kh&ocirc;ng thể tin v&agrave;o Ch&uacute;a lẫn quỉ, &ocirc;ng dư biết. T&ocirc;i kh&ocirc;ng thấy c&oacute; g&igrave; chứng tỏ cả hai đều tồn tại. C&ograve;n chả</h2>\r\n\r\n<h2>- Đ&uacute;ng l&agrave; l&yacute; luận hơi đơn giản.</h2>\r\n\r\n<h2>- C&oacute; thể. T&ocirc;i nghĩ rằng con người đ&atilde; mang sẵn đức tin trong m&igrave;nh ngay khi vừa sinh ra. Về phần t&ocirc;i, đ&oacute; l&agrave; cuộc đấu tranh kh&ocirc;ng ngừng với bản th&acirc;n, nhưng kh&ocirc;ng bao giờ t&ocirc;i bỏ cuộc.</h2>\r\n\r\n<h2>- Thế m&agrave; t&ocirc;i lại rất muốn l&agrave;m &ocirc;ng bỏ cuộc.</h2>\r\n\r\n<h2>- &ocirc;, b&agrave; Mary th&acirc;n mến! Điều đ&oacute; t&ocirc;i đ&atilde; biết! &ocirc;ng n&oacute;i với một tho&aacute;ng đ&ugrave;a, đ&ocirc;i mắt như nhuộm m&agrave;u n&acirc;u x&aacute;m dưới &aacute;nh đ&egrave;n.</h2>\r\n\r\n<h2>- Nhưng cha c&oacute; biết tại sao kh&ocirc;ng?</h2>\r\n\r\n<h2>Một cảm gi&aacute;c sợ h&atilde;i tr&agrave;n ngập cha Ralph. Bỗng chốc &ocirc;ng nhận ra con người đầy nhược điểm của m&igrave;nh nhưng &ocirc;ng liền l&ugrave;i &yacute; nghĩ đ&oacute;.</h2>\r\n\r\n<h2>- T&ocirc;i biết, b&agrave; Mary ạ. H&atilde;y tin t&ocirc;i, t&ocirc;i cảm thấy x&oacute;t xa về điều đ&oacute;.</h2>\r\n\r\n<h2>- Ngo&agrave;i mẹ của &ocirc;ng ra, bao nhi&ecirc;u phụ nữ đ&atilde; y&ecirc;u &ocirc;ng rồi?</h2>\r\n\r\n<h2>- Mẹ t&ocirc;i c&oacute; y&ecirc;u t&ocirc;i kh&ocirc;ng? T&ocirc;i tự hỏi điều đ&oacute;. D&ugrave; sao th&igrave; cuối c&ugrave;ng t&ocirc;i vẫn bị mẹ t&ocirc;i căm gh&eacute;t. Phần đ&ocirc;ng phụ nữ đều như thế cả. Đ&aacute;ng l&yacute; t&ecirc;n t&ocirc;i phải l&agrave; Hippolytẹ</h2>\r\n\r\n<h2>- Ồ! Điều đ&oacute; giải th&iacute;ch rất nhiều.</h2>\r\n\r\n<h2>- C&ograve;n những người phụ nữ kh&aacute;c, t&ocirc;i chỉ thấy c&oacute; Meggie... Nhưng đ&oacute; l&agrave; một c&ocirc; g&aacute;i c&ograve;n b&eacute;. Chắc kh&ocirc;ng qu&aacute; đ&aacute;ng ch&uacute;t n&agrave;o khi n&oacute;i rằng c&oacute; h&agrave;ng trăm phụ nữ muốn t&ocirc;i. Nhưng c&ograve;n y&ecirc;u t&ocirc;i? T&ocirc;i nghi ngờ điều n&agrave;y lắm.</h2>\r\n\r\n<h2>- C&ograve;n t&ocirc;i, t&ocirc;i đ&atilde; y&ecirc;u &ocirc;ng. - B&agrave; n&oacute;i giọng x&uacute;c động.</h2>\r\n\r\n<h2>- Kh&ocirc;ng đ&acirc;u, t&ocirc;i chỉ l&agrave; một chất k&iacute;ch th&iacute;ch tuổi gi&agrave; của b&agrave;, kh&ocirc;ng hơn kh&ocirc;ng k&eacute;m. Khi b&agrave; nh&igrave;n t&ocirc;i, t&ocirc;i l&agrave;m b&agrave; sực nhớ tất cả những ham muốn m&agrave; tuổi t&aacute;c đ&atilde; ngăn chặn b&agrave; lại.</h2>\r\n\r\n<h2>- &ocirc;ng lầm rồi. T&ocirc;i đ&atilde; y&ecirc;u &ocirc;ng. Y&ecirc;u thiết thạ &ocirc;ng nghĩ rằng tuổi gi&agrave; kh&ocirc;ng cho t&ocirc;i c&oacute; thể y&ecirc;u &agrave;? Thế th&igrave;, cha Ralph h&atilde;y nghe t&ocirc;i n&oacute;i điều sau đ&acirc;y. B&ecirc;n trong c&aacute;i th&acirc;n x&aacute;c qu&aacute;i đản n&agrave;y, t&ocirc;i vẫn c&ograve;n trẻ... T&ocirc;i c&ograve;n cảm gi&aacute;c, c&ograve;n kh&aacute;t khao, c&ograve;n mơ mộng, t&ocirc;i vẫn giậm ch&acirc;n v&agrave; bức tức trước những thua thiệt của th&acirc;n x&aacute;c gi&agrave; nua n&agrave;y. Tuổi gi&agrave; l&agrave; sự trừng phạt cay đắng nhất m&agrave; Ch&uacute;a bắt ch&uacute;ng ta phải chịu. Tại sao Người lại kh&ocirc;ng l&agrave;m cho t&acirc;m hồn gi&agrave; đi c&ugrave;ng một l&uacute;c với tuổi t&aacute;c? Tất nhi&ecirc;n t&ocirc;i sẽ xuống địa ngục. Nhưng trước đ&oacute; t&ocirc;i hy vọng sẽ c&oacute; dịp n&oacute;i với Đấng tối cao rằng ng&agrave;y l&agrave; kẻ tồi tệ, kh&ocirc; khan v&agrave; đ&aacute;ng khinh bỉ dường n&agrave;o.</h2>\r\n\r\n<h2>- B&agrave; g&oacute;a bụa qu&aacute; l&acirc;u, b&agrave; Marỵ Ch&uacute;a đ&atilde; ban cho b&agrave; sự tự do chọn lựa, đ&aacute;ng l&yacute; b&agrave; n&ecirc;n đi th&ecirc;m một bước nữa nhưng rồi b&agrave; vẫn quyết chọn cuộc sống độc th&acirc;n, vậy th&igrave; b&agrave; chỉ n&ecirc;n tr&aacute;ch m&igrave;nh, chứng kh&ocirc;ng thể tr&aacute;ch Ch&uacute;a được.</h2>\r\n\r\n<h2>Một l&uacute;c im lặng, b&agrave; bấu chặt lấy hai b&ecirc;n chiếc ghế b&agrave;nh, v&agrave; cảm thấy dễ chịu trở lại khi mở mắt ra. Dưới &aacute;nh đ&egrave;n dầu, đ&ocirc;i mắt ấy đỏ v&agrave; long lanh nhưng vẫn kh&ocirc;ng c&oacute; một giọt nước mắt, c&oacute; c&aacute;i g&igrave; đ&oacute; đau đớn hơn, trong suốt hơn những giọt nước mắt giấu k&iacute;n. Cha Ralph kh&ocirc;ng d&aacute;m thở mạnh, &ocirc;ng cảm thấy lo sợ v&agrave; nghĩ rằng b&agrave; ta chẳng kh&aacute;c g&igrave; con nhện c&aacute;i.</h2>\r\n\r\n<h2>- Cha Ralph, tr&ecirc;n b&agrave;n viết của t&ocirc;i c&oacute; một phong b&igrave;. Xin &ocirc;ng vui l&ograve;ng mang lại cho t&ocirc;i, cảm ơn &ocirc;ng nhiều lắm.</h2>\r\n\r\n<h2>Cha Ralph e d&egrave; đứng l&ecirc;n đi đến b&agrave;n v&agrave; cầm lấy phong b&igrave; với một ch&uacute;t t&ograve; m&ograve;. Phong b&igrave; trắng d&aacute;n k&iacute;n, nguy&ecirc;n ph&iacute;a sau đ&oacute;ng xi đỏ con dấu ri&ecirc;ng của Mary Carson h&igrave;nh đầu con cừu đực v&agrave; chữ D bao chung quanh. &ocirc;ng mang lại cho b&agrave; Carson nhưng liền đ&oacute; b&agrave; ra dấu mời &ocirc;ng ngồi xuống v&agrave; đưa phong b&igrave; ra ph&iacute;a trước.</h2>\r\n\r\n<h2>- Phong b&igrave; n&agrave;y của &ocirc;ng - b&agrave; nhếch miệng cười - v&agrave; số phận của &ocirc;ng ở trong đ&oacute;. &yacute; nghĩa của ph&ograve;ng b&igrave; n&agrave;y l&agrave; như thế. Thật tiếc v&ocirc; c&ugrave;ng, t&ocirc;i kh&ocirc;ng c&ograve;n c&oacute; mặt ở tr&ecirc;n cỏi đời n&agrave;y để chứng kiến những g&igrave; sẽ xảy ra, nhưng t&ocirc;i đo&aacute;n biết được chuyện ấy, v&igrave; rằng t&ocirc;i biết r&otilde; &ocirc;ng, biết &ocirc;ng nhiều hơn &ocirc;ng tưởng. Con người &ocirc;ng ki&ecirc;u h&atilde;nh kh&ocirc;ng thể chịu nổi! Phong b&igrave; chứa đựng số phận cuộc đời v&agrave; cả linh hồn &ocirc;ng đấy. T&ocirc;i đ&agrave;nh phải nhường &ocirc;ng cho Meggie nhưng t&ocirc;i an t&acirc;m v&igrave; đ&atilde; c&oacute; c&aacute;ch để con b&eacute; n&agrave;y cũng sẽ kh&ocirc;ng c&oacute; được &ocirc;ng.</h2>\r\n\r\n<h2>- Tại sao b&agrave; lại cay c&uacute; với Meggie đến thế?</h2>\r\n\r\n<h2>- T&ocirc;i đ&atilde; n&oacute;i với &ocirc;ng rồi. Tại v&igrave; &ocirc;ng y&ecirc;u n&oacute;.</h2>\r\n\r\n<h2>- Nhưng t&igrave;nh y&ecirc;u ấy kh&ocirc;ng như b&agrave; hiểu. Meggie đ&aacute;ng tuổi con t&ocirc;i, m&agrave; t&ocirc;i th&igrave; kh&ocirc;ng bao giờ c&oacute; con được, c&ocirc; ấy l&agrave; niềm vui của đời t&ocirc;i. B&agrave; Mary ạ, Meggie đối với t&ocirc;i l&agrave; một &yacute; niệm, đ&uacute;ng l&agrave; một &yacute; niệm!</h2>\r\n\r\n<h2>B&agrave; gi&agrave; cười cay độc:</h2>\r\n\r\n<h2>- T&ocirc;i kh&ocirc;ng muốn ch&uacute;ng ta n&oacute;i về Meggie qu&iacute; b&aacute;o của &ocirc;ng! T&ocirc;i sẽ m&atilde;i m&atilde;i kh&ocirc;ng gặp lại &ocirc;ng do đ&oacute; t&ocirc;i kh&ocirc;ng muốn mất th&igrave; giờ về chuyện của con b&eacute; ấy. Trở lại c&aacute;i thư, t&ocirc;i muốn rằng &ocirc;ng thề với t&ocirc;i, lời thề của một linh mục rằng &ocirc;ng sẽ kh&ocirc;ng mở phong b&igrave; trước khi ch&iacute;nh &ocirc;ng tận mắt nh&igrave;n thấy c&aacute;i chết của t&ocirc;i. &ocirc;ng chỉ mở thư ra trước khi t&ocirc;i được ch&ocirc;n cất. &ocirc;ng h&atilde;y thề đi.</h2>\r\n\r\n<h2>- Kh&ocirc;ng cần thế b&agrave; Mary ạ. T&ocirc;i sẽ l&agrave;m đ&uacute;ng như lời y&ecirc;u cầu của b&agrave;.</h2>\r\n\r\n<h2>- &ocirc;ng h&atilde;y thề đi, nếu kh&ocirc;ng t&ocirc;i lấy lại phong thư đấy.</h2>\r\n\r\n<h2>- Thế th&igrave;... t&ocirc;i đồng &yacute; - cha Ralph nh&uacute;n vai. Đ&acirc;y l&agrave; lời thề của một linh mục, t&ocirc;i xin thề, sẽ kh&ocirc;ng mở thư ra trước khi nh&igrave;n tận mắt b&agrave; đ&atilde; chết, v&agrave; sẽ đọc thư n&agrave;y trước khi b&agrave; được ch&ocirc;n cất.</h2>\r\n\r\n<h2>- Tốt lắm, tốt lắm.</h2>\r\n\r\n<h2>- B&agrave; Mary, xin b&agrave; đừng lo lắng! Chẳng qua đ&acirc;y cũng l&agrave; một &yacute; nghĩ ng&ocirc;ng cuồng đ&ugrave;a giỡn của b&agrave; thế th&ocirc;i. S&aacute;ng mai b&agrave; lại cười về chuyện n&agrave;y đấy.</h2>\r\n\r\n<h2>- T&ocirc;i sẽ kh&ocirc;ng thấy c&aacute;i s&aacute;ng mai ấy. T&ocirc;i sẽ chết đ&ecirc;m nay; t&ocirc;i kh&ocirc;ng qu&aacute; yếu đuối để n&aacute;n lại chờ gi&acirc;y ph&uacute;t sung sướng được nh&igrave;n thấy &ocirc;ng lần nữa. Kh&ocirc;ng lẽ t&ocirc;i lại tuột dốc đến thế sao! T&ocirc;i đi nghỉ đ&acirc;y. &ocirc;ng sẵn s&agrave;ng đưa t&ocirc;i l&ecirc;n đến đầu cầu thang chứ?</h2>\r\n\r\n<h2>Cha Ralph kh&ocirc;ng tin điều b&agrave; Mary Carson n&oacute;i nhưng biết rằng c&atilde;i lại cũng v&ocirc; &iacute;ch, vả lại b&agrave; ta đang trong tam trạng bất ổn kh&ocirc;ng n&ecirc;n l&agrave;m buồn l&ograve;ng b&agrave; bằng một chuyện b&ocirc;ng đ&ugrave;a. Chỉ c&oacute; Ch&uacute;a l&agrave; định c&aacute;i chết của một con người, ngoại trừ trường hợp theo &yacute; muốn ri&ecirc;ng m&agrave; ngay cả &yacute; muốn đ&oacute; cũng do Ch&uacute;a đ&atilde; ban cho ta c&oacute; thể tự kết liễu đời m&igrave;nh. Nhưng b&agrave; đ&atilde; n&oacute;i rằng b&agrave; sẽ kh&ocirc;ng tự tử m&agrave;. Cha Ralph d&igrave;u b&agrave; ta bước l&ecirc;n từng bậc thang, b&agrave; tỏ ra rất mệt v&agrave; cần được gi&uacute;p thật sự. Đến cuối cầu thang, &ocirc;ng cầm hai tay của b&agrave; l&ecirc;n, c&uacute;i xuống định h&ocirc;n nhưng b&agrave; ra r&uacute;t tay lại.</h2>\r\n\r\n<h2>- Ri&ecirc;ng đ&ecirc;m nay, t&ocirc;i kh&ocirc;ng muốn nhận một c&aacute;i h&ocirc;n như thế. H&ocirc;n tr&ecirc;n m&ocirc;i, Ralph. H&atilde;y &ocirc;m h&ocirc;n v&agrave;o m&ocirc;i t&ocirc;i như thể ch&uacute;ng ta l&agrave; đ&ocirc;i t&igrave;nh nh&acirc;n!</h2>\r\n\r\n<h2>Dưới &aacute;nh s&aacute;ng rực rỡ của c&acirc;y đ&egrave;n ch&ugrave;m lớn được thắp đến 400 ngọn nến, b&agrave; nhận ra gương mặt của linh mục thật kinh tởm, bất gi&aacute;c b&agrave; l&ugrave;i lại. Bấy giờ b&agrave; muốn chết đi, muốn một c&aacute;ch m&atilde;nh liệt kh&ocirc;ng thể n&agrave;o chần chờ được nữa.</h2>\r\n\r\n<h2>- B&agrave; Mary, t&ocirc;i l&agrave; linh mục! T&ocirc;i kh&ocirc;ng thể l&agrave;m chuyện đ&oacute;.</h2>\r\n\r\n<h2>Mary Carson cười lớn, tiếng cười ch&aacute;t ch&uacute;a, qu&aacute;i dị</h2>\r\n\r\n<h2>- Ồ, Ralph, &ocirc;ng đ&uacute;ng l&agrave; một kẻ lừa bịp! Lừa bịp với tư c&aacute;ch con người v&agrave; lừa bịp với tư c&aacute;ch một linh mục: &ocirc;ng chẳng l&agrave; g&igrave; hết, Ralph ạ. Một t&ecirc;n lừa bịp bất lực, v&ocirc; &iacute;ch! Một thứ đ&agrave;n &ocirc;ng bất lực v&agrave; một linh mục bất lực!...</h2>', 0, 'tieng-chim-hot-trong-bui-man-gai-chuong-11-12');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `tomtat`, `noidung`, `kichhoat`, `slug_chapter`) VALUES
(50, 25, 'Tiếng chim hót trong bụi mận gai - Chương 13-14', 'Tiếng chim hót trong bụi mận gai - Chương 13-14', '<h2>Ngo&agrave;i kia b&igrave;nh minh chưa r&otilde; n&eacute;t, kể cả vầng s&aacute;ng xuất hiện trước đ&oacute;. B&oacute;ng tối &ecirc;m &aacute;i, d&agrave;y đặc, trong kh&ocirc;ng kh&iacute; n&oacute;ng bức c&ograve;n phủ tr&ugrave;m l&ecirc;n cả Droghedạ Đi đến đầu b&ecirc;n kia b&atilde;i cỏ, cha Ralph đứng sững lại, ngước mắt nh&igrave;n l&ecirc;n trời, t&igrave;m kiếm Thượng đế bằng c&aacute;i ăn ten ho&agrave;n to&agrave;n bản năng của m&igrave;nh. Đ&uacute;ng rồi, ở một nơi n&agrave;o đ&oacute; tr&ecirc;n cao, giữa c&aacute;i chấm li ti ph&aacute;t ra thứ &aacute;nh s&aacute;ng tinh khiết v&agrave; vời vợi; c&oacute; g&igrave; ở giữa m&agrave;n đ&ecirc;m n&agrave;y? Chỉ cần nh&igrave;n v&ograve;m trời đầy sao cũng đ&atilde; thuyết phục con người tin v&agrave;o sự vĩnh cửu tồn tại của Ch&uacute;a. Tất nhi&ecirc;n b&agrave; ta c&oacute; l&yacute;. Một kẻ bịp. Đ&uacute;ng thật l&agrave; một kẻ bịp. Chẳng ra linh mục chẳng ra đ&agrave;n &ocirc;ng. Phải chi c&oacute; ai đ&oacute; thử t&igrave;m hiểu thế n&agrave;o l&agrave; người n&agrave;y hoặc người kia. Kh&ocirc;ng! Chẳng qua người n&agrave;y, chẳng phải người kia! Linh mục v&agrave; đ&agrave;n &ocirc;ng kh&ocirc;ng thể tồn tại chung với nhau - l&agrave; đ&agrave;n &ocirc;ng c&oacute; nghĩa l&agrave; kh&ocirc;ng l&agrave; linh mục. Tại sao t&ocirc;i lại để ch&acirc;n kẹt trong mạng nhện của b&agrave; tả Nọc độc của b&agrave; ta thật l&agrave; dữ dằn, nham hiểm nhiều hơn t&ocirc;i tưởng tượng. Trong thư chứa đựng điều g&igrave;? C&oacute; vẻ như b&agrave; ta muốn nhử ta! B&agrave; ta đ&atilde; biết g&igrave; đo&aacute;n g&igrave;? C&aacute;i g&igrave; ở ta m&agrave; b&agrave; muốn biết hay muốn đo&aacute;n? Chỉ c&oacute; sự trống rỗng v&agrave; sự c&ocirc; đơn. Ho&agrave;i nghi đau khổ. Lu&ocirc;n lu&ocirc;n đau khổ. Tuy nhi&ecirc;n, b&agrave; đ&atilde; lầm rồi b&agrave; Mary ạ. T&ocirc;i vẫn c&oacute; khả năng l&agrave;m người đ&agrave;n &ocirc;ng, t&ocirc;i kh&ocirc;ng bất lực. Chẳng qua l&agrave; t&ocirc;i đ&atilde; lựa chọn v&agrave; t&ocirc;i đ&atilde; trải qua nhiều năm tự m&igrave;nh chứng minh rằng c&aacute;i ph&uacute;t gi&acirc;y nổi dậy của thể x&aacute;c c&oacute; thể k&igrave;m chế được. C&oacute; ai đ&oacute; kh&oacute;c th&igrave; thầm trong nghĩa trang. Đ&uacute;ng l&agrave; Meggiẹ Cha Ralph k&eacute;o cao &aacute;o cho&agrave;ng l&ecirc;n, leo qua h&agrave;ng r&agrave;o sắt, kh&ocirc;ng cưỡng lại được l&ograve;ng h&aacute;o hức muốn gặp Meggie v&igrave; tối qua &ocirc;ng chưa kịp từ gi&atilde;. Bản t&iacute;nh dửng dưng đ&ugrave;a cợt trở lại với &ocirc;ng, b&agrave; ta kh&ocirc;ng thể n&agrave;o k&eacute;o &ocirc;ng ra khỏi c&aacute;i b&oacute;ng h&igrave;nh vẫn &aacute;m ảnh &ocirc;ng. Con nhện gi&agrave; nham hiểm! Cầu Ch&uacute;a trừng phạt b&agrave; ta bị rữa th&acirc;y ra. Bị rữa th&acirc;y ra. - Meggie thương y&ecirc;u, đừng kh&oacute;c nữa - &ocirc;ng ngồi xuống cạnh c&ocirc; g&aacute;i tr&ecirc;m thảm cỏ ướt đẫm sương. Cha đo&aacute;n chắc con kh&ocirc;ng mang theo khăn taỵ Cầm lấy khăn của cha, lau nước mắt đi, ngoan n&agrave;o. Meggie cầm mảnh vải v&agrave; v&acirc;ng lời lau nước mắt. - Con chưa thay cả chiếc &aacute;o đẹp n&agrave;y &agrave;. Con ngồi đ&acirc;y từ nửa đ&ecirc;m phải kh&ocirc;ng? - Con n&oacute;i với hai anh l&agrave; con đi ngủ. - Thế th&igrave; tại sao con ra đ&acirc;y. C&oacute; chuyện g&igrave; hả Meggiẻ - Hồi h&ocirc;m, cha kh&ocirc;ng n&oacute;i với con một lời. Xa xa về ph&iacute;a Đ&ocirc;ng trời ửng l&ecirc;n ch&uacute;t &aacute;nh s&aacute;ng m&agrave;u ngọc trai. Mấy con g&agrave; trống ở Drogheda đ&oacute;n hiệu b&igrave;nh minh bằng tiếng g&aacute;y r&aacute;t cả cổ họng. Bấy giờ cha Ralph nh&igrave;n r&otilde; dấu vết của những giọt nước mắt đ&ecirc;m qua kh&ocirc;ng l&agrave;m mờ được &aacute;nh mắt tuyệt đẹp của Meggiẹ - Meggie, trong buổi tiếp t&acirc;n đ&ecirc;m qua, con l&agrave; c&ocirc; g&aacute;i xinh đẹp nhất, kh&ocirc;ng c&oacute; ai s&aacute;nh bằng. Nhưng người ta cũng biết cha thường đến Drogheda nhiều hơn l&agrave; c&ocirc;ng việc thật sự đ&ograve;i hỏi. Cha l&agrave; linh mục, v&igrave; thế, cha đứng tr&ecirc;n mọi sự nghi ngờ. Tuy nhi&ecirc;n, cha e người ta kh&ocirc;ng nh&igrave;n c&aacute;c sự việc bằng con mắt ấy. Trong c&aacute;c linh mục, cha được coi l&agrave; người c&ograve;n trẻ v&agrave; dễ coi. Nếu cha ch&uacute; &yacute; v&agrave; con một ch&uacute;t th&ocirc;i th&igrave; cả Gilly n&agrave;y sẽ biết tức khắc. Rồi tất cả c&aacute;c đường d&acirc;y điện thoại sẽ loan truyền tin ấy. Con c&oacute; hiểu cha n&oacute;i g&igrave; kh&ocirc;ng? Meggie lắc đầu, những lọn t&oacute;c quăng &oacute;ng &aacute;nh c&agrave;ng l&uacute;c c&agrave;ng rực rỡ dưới &aacute;nh mặt trời. - Con c&ograve;n qu&aacute; trẻ để hiểu c&aacute;c chuyện rắc rối của đời n&agrave;y - &ocirc;ng n&oacute;i tiếp - nhưng cha phải cho con biết dần. V&agrave; h&igrave;nh như, cũng lại ch&iacute;nh cha l&agrave; người dạy dỗ con. Điều m&agrave; cha muốn con phải hiểu l&agrave; dư luận đồn đ&atilde;i rằng cha ch&uacute; &yacute; đến con với tư c&aacute;ch đ&agrave;n &ocirc;ng, chứ kh&ocirc;ng phải với tư c&aacute;ch linh mục. - Thưa cha! - Gh&ecirc; tởm, phải kh&ocirc;ng Meggiẻ &ocirc;ng mỉm cười - nhưng đ&oacute; l&agrave; điều m&agrave; họ n&oacute;i, cha đoan chắc với con thế. Con phải hiểu, Meggie, con kh&ocirc;ng c&ograve;n l&agrave; đứa trẻ con nữa, con l&agrave; một c&ocirc; g&aacute;i. Thế nhưng con lại chưa biết che giấu t&igrave;nh cảm m&agrave; cha d&agrave;nh cho con, nếu cha đến gần con, n&oacute;i chuyện với con trước nhiều người đang theo d&otilde;i th&igrave; &aacute;nh mắt của con sẽ tự th&uacute; con ngay, v&agrave; người ta kh&ocirc;ng thể kh&ocirc;ng hiểu sai tr&aacute;i th&aacute;i độ của con. Meggie nh&igrave;n cha Ralph một c&aacute;ch hơi kỳ lạ, kh&ocirc;ng thể n&agrave;o hiểu được. Meggie quay đi nơi kh&aacute;c v&agrave; cha Ralph chỉ nh&igrave;n thấy một nửa gương mặt. - V&acirc;ng, con đ&atilde; hiểu. Trước đ&acirc;y con thật l&agrave; ngu đần kh&ocirc;ng hiểu điều ấy. - Con thấy đ&atilde; đến l&uacute;c phải về nh&agrave; chưa? Hiện giờ hầu hết mọi người c&ograve;n ngủ nướng tr&ecirc;n giường, nhưng lỡ c&oacute; ai thức dậy sớm như mọi khi, con lại gặp rắc rối đấy. Con kh&ocirc;ng thể n&oacute;i con vừa đi với cha về m&agrave; cũng kh&ocirc;ng thể n&oacute;i con đ&atilde; c&oacute; mặt ở nh&agrave;, Meggie ạ. Meggie đứng l&ecirc;n, bất động một l&uacute;c, mắt nh&igrave;n xuống cha Ralph. - Con đi đ&acirc;y, thưa chạ Nhưng con cầu mong mọi người hiểu cha hơn; như thế sẽ kh&ocirc;ng ai đặt điều những chuyện như thế về chạ Nhưng c&oacute; phải cha kh&ocirc;ng hề vấn vương những &yacute; tưởng như vậy? Do một chuyện g&igrave; đ&oacute;, c&acirc;u hỏi của Meggie l&agrave;m tổn thương &ocirc;ng, tổn thương nặng nề, hậu quả của n&oacute; c&ograve;n hơn cả những lời ch&acirc;m chọc &aacute;c độc của Mary Carson. - Kh&ocirc;ng, Meggiẹ Con n&oacute;i đ&uacute;ng. Cha kh&ocirc;ng hề nu&ocirc;i những &yacute; tưởng như thế. Kh&ocirc;ng, cha ho&agrave;n to&agrave;n kh&ocirc;ng nghĩ đến. Con về đi, Meggiẹ Con về nh&agrave; đi! Cha Ralph nh&igrave;n theo Meggie đang chạy giữa c&aacute;c nấm mồ, rồi tr&egrave;o qua h&agrave;ng r&agrave;o thấp. Dưới lớp &aacute;o d&agrave;i th&ecirc;u những b&ocirc;ng hoa hồng, th&acirc;n h&igrave;nh của Meggie đẹp dịu d&agrave;ng, đầy nữ t&iacute;nh v&agrave; một ch&uacute;t g&igrave; đ&oacute; như kh&ocirc;ng thật. V&agrave;o xế chiều cha Ralph c&oacute; cảm gi&aacute;c một b&agrave;n tay chạm nhẹ v&agrave;o vai. &ocirc;ng giơ tay định nắm lấy nhưng vẫn kh&ocirc;ng đủ nghị lực để mở mắt ra. &ocirc;ng muốn giữ lại b&agrave;n tay ấy tr&ecirc;n m&aacute; m&igrave;nh. - Meggie, &ocirc;ng th&igrave; thầm. - Thưa cha, thưa cha! cha ơi h&atilde;y thức dậy! Giọng n&oacute;i của b&agrave; Smith k&eacute;o &ocirc;ng ra khỏi cơn m&ecirc; ngủ v&agrave; &ocirc;ng t&igrave;m lại ngay sự tỉnh t&aacute;o. - Chuyện g&igrave; đ&oacute; b&agrave; Smith? - B&agrave; Mary Carson... thưa chạ B&agrave; đ&atilde; chết. Đồng hồ đeo tay của &ocirc;ng chỉ s&aacute;u giờ hơn. C&ograve;n ng&aacute;i ngủ v&agrave; mỏi mệt bởi c&aacute;i n&oacute;ng nặng nề của ban ng&agrave;y, cha Ralph vội v&agrave;ng cở vộ đồ ngủ v&agrave; mặc v&agrave;o chiếc &aacute;o ch&ugrave;ng, cho&agrave;ng l&ecirc;n cổ chiếc khăn lễ, lấy dầu l&agrave;m lễ xức nước th&aacute;nh, c&acirc;y th&aacute;nh gi&aacute; lớn bằng bạc, tr&agrave;ng chuỗi hạt m&agrave;u đen. &ocirc;ng kh&ocirc;ng cho rằng b&agrave; Smith lầm lẫn. &ocirc;ng biết l&agrave; con nhện gi&agrave; đ&atilde; chết. B&agrave; ta c&oacute; cố t&igrave;nh th&uacute;c đẩy c&aacute;i chết sớm hơn? Nếu như thế, cầu Ch&uacute;a, b&agrave; kh&ocirc;ng để lại một chứng cớ n&agrave;o r&otilde; rệt v&agrave; c&aacute;i chết của b&agrave; kh&ocirc;ng l&agrave;m cho b&aacute;c sĩ nghi ngờ. L&agrave;m lễ xức dầu cho b&agrave; ta c&oacute; &iacute;t lợi g&igrave;, &ocirc;ng cũng kh&ocirc;ng biết, nhưng d&ugrave; sao vẫn phải l&agrave;m điều đ&oacute; cho b&agrave;. Nếu &ocirc;ng từ chối l&agrave;m lễ, th&igrave; sẽ c&oacute; cuộc điều tra, v&agrave; rồi sẽ đẻ ra đủ thứ chuyện rắc rối. &ocirc;ng cho rằng b&agrave; Mary chết v&igrave; tự tử; thế th&igrave; l&agrave;m lễ th&aacute;nh tr&ecirc;n thi thể của b&agrave; l&agrave; một sự x&uacute;c phạm đến những g&igrave; thi&ecirc;ng li&ecirc;ng nhất. B&agrave; ta đ&atilde; chết thật. C&oacute; lẽ đ&atilde; chết v&agrave;i ph&uacute;t sau khi cha Ralph từ gi&atilde;; như vậy l&agrave; c&aacute;ch đ&acirc;y khoảng mười tiếng đồng hồ. C&aacute;c cửa sổ đều đ&oacute;ng thật k&iacute;n, trong ph&ograve;ng c&oacute; m&ugrave;i ẩm của hơi nước chứa trong mấy chiếc chậu. Khi c&ograve;n sống b&agrave; vẫn l&agrave;m như thế để giữ cho da lu&ocirc;n m&aacute;t v&agrave; mịn. C&oacute; tiếng vo vo lạ tai trong kh&ocirc;ng kh&iacute;; sau một l&uacute;c ngơ ng&aacute;c, cha Ralph nhận ra đ&oacute; l&agrave; tiếng ruồi bay; từng đ&agrave;n lượn quanh, k&eacute;o nhau bu lại thưởng thức buổi tiệc thịnh soạn tr&ecirc;n thi thể của b&agrave; Marỵ Ch&uacute;ng bay qua lại, gh&eacute;p đ&ocirc;i v&agrave; đẻ trứng tr&ecirc;n cơ thể đ&atilde; lạnh của b&agrave;. - V&igrave; t&igrave;nh thương của Ch&uacute;a, b&agrave; Smith h&atilde;y mở cửa sổ ra. Cha Ralph k&ecirc;u l&ecirc;n, hơi thở dồn dập. Mặt t&aacute;i nhợt, &ocirc;ng đến gần giường. Một m&ugrave; h&ocirc;i thối kh&oacute; chịu hơn cả con ngựa chết ở ngo&agrave;i b&atilde;i. &ocirc;ng gh&ecirc; tởm kh&ocirc;ng d&aacute;m chạm v&agrave;o thi thể của b&agrave; cũng như &ocirc;ng đang gh&ecirc; tởm kh&ocirc;ng d&aacute;m chạm v&agrave;o người b&agrave; khi b&agrave; c&ograve;n sống, nhất l&agrave; tr&ecirc;n hai m&ocirc;i l&uacute;c nh&uacute;c đầy trứng ruồi. Cuối c&ugrave;ng th&igrave; mọi việc đều xong xu&ocirc;i. Cha Ralph đứng thẳng người l&ecirc;n: - B&agrave; Smith, chạy đi b&aacute;o cho &ocirc;ng Cleary hay, n&oacute;i với &ocirc;ng ấy ra lệnh cho c&aacute;c con trai đ&oacute;ng ngay một quan t&agrave;i. Ch&uacute;ng ta kh&ocirc;ng c&ograve;n thời giờ để chờ mang quan t&agrave;i từ Gilly đến, thi thể của b&agrave; đang thối ra. Lạy Ch&uacute;a! T&ocirc;i muốn n&ocirc;n ra v&agrave; phải đi tắm ngaỵ T&ocirc;i để quần &aacute;o ở ngo&agrave;i cửa, b&agrave; h&atilde;y đem đốt gi&ugrave;m t&ocirc;i. M&atilde;i m&atilde;i t&ocirc;i kh&ocirc;ng thể tẩy x&oacute;a được m&ugrave;i h&ocirc;i thối thấm v&agrave;o người t&ocirc;i. Trở về ph&ograve;ng ri&ecirc;ng, cha Ralph mặc chiếc quần đi ngựa v&agrave; &aacute;o sơ mi, bỗng &ocirc;ng nhớ lại phong thư v&agrave; lời thề của m&igrave;nh. Phong b&igrave; chứa bốn tờ giấy; &ocirc;ng mở từng tờ v&agrave; nhận ra ngay hai tờ sau l&agrave; di ch&uacute;c của Mary Carson. Hai tờ đầu dưới h&igrave;nh thức thư ri&ecirc;ng, viết cho &ocirc;ng.&nbsp;<em>Ralph th&acirc;n mến của t&ocirc;i, Như &ocirc;ng đ&atilde; nhận ra, văn bản thứ hai chứa đựng trong phong b&igrave; n&agrave;y l&agrave; di ch&uacute;c của t&ocirc;i. T&ocirc;i c&oacute; lập một di ch&uacute;c kh&aacute;c, k&yacute; t&ecirc;n v&agrave; đ&oacute;ng khằn lại ho&agrave;n to&agrave;n c&oacute; gi&aacute; trị, hiện nằm tại văn ph&ograve;ng của Harry Gough ở Gillỵ Di ch&uacute;c trong phong b&igrave; n&agrave;y được lập sau đ&oacute; rất l&acirc;u, v&agrave; tất nhi&ecirc;n n&oacute; c&oacute; gi&aacute; trị v&ocirc; hiệu h&oacute;a di ch&uacute;c m&agrave; Harry đang giữ. Thật ra, t&ocirc;i đ&atilde; lập di ch&uacute;c thứ hai chỉ c&aacute;ch đ&acirc;y mấy ng&agrave;y. T&ocirc;i đ&atilde; y&ecirc;u cầu Tom v&agrave; người thợ l&agrave;m h&agrave;ng r&agrave;o k&yacute; t&ecirc;n dưới di ch&uacute;c với tư c&aacute;ch người l&agrave;m chứng; v&igrave; t&ocirc;i hiểu biết rằng kh&ocirc;ng thể gọi người được thừa hưởng để l&agrave;m thủ tục n&agrave;y. Văn bản ho&agrave;n to&agrave;n hợp ph&aacute;p d&ugrave; rằng người lập kh&ocirc;ng phải l&agrave; Harrỵ Kh&ocirc;ng một t&ograve;a &aacute;n n&agrave;o trong cả nước, c&oacute; thể phủ nhận gi&aacute; trị của bản di ch&uacute;c sau, t&ocirc;i cam đoan với &ocirc;ng như thế. Nhưng tại sao t&ocirc;i kh&ocirc;ng y&ecirc;u cầu Harry lập di ch&uacute;c n&agrave;y nếu t&ocirc;i muốn sửa đổi nội dung của di ch&uacute;c thứ nhất? L&yacute; do thật đơn giản, Ralph th&acirc;n mến của t&ocirc;i. T&ocirc;i thật l&ograve;ng muốn kh&ocirc;ng một ai ngo&agrave;i t&ocirc;i v&agrave; &ocirc;ng sẽ biết l&agrave; c&oacute; tờ di ch&uacute;c n&agrave;y. Đ&acirc;y l&agrave; bản duy nhất v&agrave; bản n&agrave;y ở trong tay &ocirc;ng. Kh&ocirc;ng một người n&agrave;o biết được việc n&agrave;y cả, đ&acirc;y l&agrave; một phần rất quan trọng trong kế hoạch của t&ocirc;i. &ocirc;ng c&oacute; nhớ đoạn trong kinh th&aacute;nh kể lại Xatang đưa Ch&uacute;a Jesus Christ l&ecirc;n n&uacute;i v&agrave; c&aacute;m dỗ Người bằng c&aacute;ch hứa trao cho Người cả thế giới? Thật l&agrave; th&uacute; vị nếu t&ocirc;i biết được t&ocirc;i c&oacute; một quyền lực của Xatang v&agrave; t&ocirc;i c&oacute; kha năng c&aacute;m dỗ người t&ocirc;i y&ecirc;u (&ocirc;ng c&oacute; ho&agrave;i nghi về chuyện Xatang y&ecirc;u Ch&uacute;a? T&ocirc;i th&igrave; kh&ocirc;ng) bằng c&aacute;ch trao cho người ấy cả thế giới. Trường hợp của &ocirc;ng đứng trước hai con đường phải chọn đ&atilde; lu&ocirc;n k&iacute;ch th&iacute;ch suy nghĩ của t&ocirc;i trong suốt những năm gần đ&acirc;y v&agrave; khi t&ocirc;i c&agrave;ng đến gần c&aacute;i chết th&igrave; những g&igrave; t&ocirc;i h&igrave;nh dung trong đầu lại c&agrave;ng mang đến cho t&ocirc;i nhiều th&iacute;ch th&uacute;. Sau khi đọc tờ di ch&uacute;c, &ocirc;ng sẽ hiểu t&ocirc;i n&oacute;i với ngụ &yacute; g&igrave;. Trong khi t&ocirc;i bị hỏa thi&ecirc;u dưới địa ngục, bị loại ra khỏi c&aacute;i thế giới m&agrave; hiện giờ t&ocirc;i biết th&igrave; &ocirc;ng vẫn c&ograve;n sống, nhưng &ocirc;ng cũng sẽ bị thi&ecirc;u trong một địa ngục của những ngọn lửa dữ dội hơn c&aacute;c ngọn lửa m&agrave; kh&ocirc;ng một thần linh n&agrave;o c&oacute; thể tạo ra được. Ralph của t&ocirc;i! T&ocirc;i đ&atilde; t&igrave;m hiểu &ocirc;ng đến tận c&ugrave;ng! Rất c&oacute; thể t&ocirc;i chẳng l&agrave;m được việc g&igrave; kh&aacute;c nhưng t&ocirc;i lại rất biết c&aacute;ch h&agrave;nh hạ những người t&ocirc;i y&ecirc;u. V&agrave; &ocirc;ng l&agrave; một con mồi v&ocirc; c&ugrave;ng hấp dẫn so với người chồng qu&aacute; cố Michael th&acirc;n y&ecirc;u của t&ocirc;i. Khi t&ocirc;i mới biết &ocirc;ng, &ocirc;ng c&oacute; &yacute; muốn chiếm đoạt Drogheda v&agrave; tiền bạc của t&ocirc;i, phải kh&ocirc;ng Ralph? &ocirc;ng coi cả hai l&agrave; một phương tiện gi&uacute;p &ocirc;ng mua c&aacute;i quyền đạt tới gi&aacute; trị m&agrave; &ocirc;ng mong muốn. Nhưng l&uacute;c đ&oacute;, Meggie xuất hiện v&agrave; &ocirc;ng đ&atilde; loại ngay trong đầu &ocirc;ng mọi &yacute; nghĩ mua chuộc t&ocirc;i, phải thế kh&ocirc;ng? T&ocirc;i đ&atilde; trở th&agrave;nh c&aacute;i b&igrave;nh phong cho c&aacute;c cuộc lui tới của &ocirc;ng ở Drogheda, tạo cơ hội cho &ocirc;ng gặp c&ocirc; b&eacute; ấy. T&ocirc;i tự hỏi &ocirc;ng c&oacute; dễ d&agrave;ng thay đổi sự trung th&agrave;nh của &ocirc;ng hay kh&ocirc;ng nếu &ocirc;ng biết đ&iacute;ch x&aacute;c t&ocirc;i c&oacute; một t&agrave;i sản lớn như thế n&agrave;o. &ocirc;ng ho&agrave;i nghi phải kh&ocirc;ng Ralph? T&ocirc;i nghĩ rằng &ocirc;ng kh&ocirc;ng tin đi&ecirc;u đ&oacute;. Đ&uacute;ng l&agrave; rất kh&oacute; coi về ph&iacute;a người để lại di ch&uacute;c khi phải k&ecirc; khai gi&aacute; trị ch&iacute;nh x&aacute;c của di sản, nhưng theo đ&aacute;nh gi&aacute; của t&ocirc;i, t&ocirc;i thấy tốt hơn cứ n&ecirc;u ra đ&acirc;y gi&uacute;p &ocirc;ng an t&acirc;m biết rằng &ocirc;ng c&oacute; đủ c&aacute;c dữ kiện để lấy một quyết định khi thời điểm đến. Nếu c&oacute; sai lệch cũng chỉ khoảng v&agrave;i trăm ng&agrave;n bản, t&agrave;i sản của t&ocirc;i l&ecirc;n tới tr&ecirc;n 13 triệu đồng sterling. T&ocirc;i đang viết cuối trang hai v&agrave; t&ocirc;i kh&ocirc;ng c&oacute; &yacute; định n&aacute;n lại l&acirc;u hơn nữa với l&aacute; thư n&agrave;y. H&atilde;y đọc di ch&uacute;c t&ocirc;i, Ralph. V&agrave; khi đ&atilde; đọc xong, &ocirc;ng cứ quyết định lấy &ocirc;ng sẽ l&agrave;m g&igrave;. Hoặc &ocirc;ng sẽ trao cho Harry Gough để ch&iacute;nh thức h&oacute;a, hoặc &ocirc;ng đốt n&oacute; đi v&agrave; kh&ocirc;ng n&oacute;i một lời với bất cứ ai về sự tồn tại của n&oacute;? Đ&acirc;y l&agrave; một quyết định ho&agrave;n to&agrave;n thuộc quyền của &ocirc;ng. T&ocirc;i thấy cần n&oacute;i th&ecirc;m rằng di ch&uacute;c nằm ở văn ph&ograve;ng của Harry đ&atilde; được t&ocirc;i thảo ra một năm sau khi Pađy đến đ&acirc;y. Trong di ch&uacute;c đ&oacute;, t&ocirc;i để lại ho&agrave;n to&agrave;n t&agrave;i sản cho em trai t&ocirc;i. B&acirc;y giờ th&igrave; &ocirc;ng biết ch&iacute;nh x&aacute;c chuyện g&igrave; được đặt ra với &ocirc;ng. Ralph, t&ocirc;i y&ecirc;u &ocirc;ng đến mức t&ocirc;i c&oacute; thể giết &ocirc;ng v&igrave; &ocirc;ng đ&atilde; xua đuổi t&ocirc;i, nếu t&ocirc;i kh&ocirc;ng kịp suy nghĩ lại c&aacute;ch trả th&ugrave; đ&oacute; l&agrave; qu&aacute; nhẹ nh&agrave;ng. T&ocirc;i kh&ocirc;ng thuộc loại người cao thượng; t&ocirc;i y&ecirc;u &ocirc;ng, nhưng t&ocirc;i muốn &ocirc;ng g&agrave;o th&eacute;t l&ecirc;n v&igrave; kinh ho&agrave;ng. V&igrave; rằng, t&ocirc;i biết rất r&otilde; quyết định m&agrave; &ocirc;ng sẽ chọn. T&ocirc;i biết một c&aacute;ch chắc chắn như l&agrave; t&ocirc;i ở đ&oacute; đang nh&igrave;n &ocirc;ng. &ocirc;ng sẽ g&agrave;o th&eacute;t l&ecirc;n, Ralph, v&agrave; &ocirc;ng sẽ biết thế n&agrave;o l&agrave; nổi kinh ho&agrave;ng. Thế th&igrave; cứ đọc đi, ch&agrave;ng linh mục đẹp trai đầy tham vọng của t&ocirc;i! H&atilde;y đọc đi di ch&uacute;c của t&ocirc;i v&agrave; h&atilde;y quyết định số phận của m&igrave;nh.&nbsp;</em>L&aacute; thư kh&ocirc;ng c&oacute; chữ k&yacute;, kể cả chữ k&yacute; tắt. Cha Ralph cảm thấy mồ h&ocirc;i ướt đẫm tr&aacute;n m&igrave;nh, nhỏ giọt từ g&aacute;y xuống lưng. V&agrave; &ocirc;ng muốn đứng dậy ngay, đem đốt cả hai văn bản, kh&ocirc;ng cần xem nội dung của tờ di ch&uacute;c. Nhưng quả thật b&agrave; ta hiểu qu&aacute; nhiều con mồi của m&igrave;nh, c&aacute;i con nhện gi&agrave;, phục phịch ấy. Tất nhi&ecirc;n rồi linh mục lại tiếp tục đọc văn bản thứ hai. &ocirc;ng qu&aacute; t&ograve; m&ograve; để c&oacute; thể chống lại sự c&aacute;m dỗ. Ch&uacute;a ơi! &ocirc;ng đ&atilde; l&agrave;m g&igrave; m&agrave; để cho b&agrave; ta nhận ch&igrave; v&agrave;o một cơn xo&aacute;y kinh khủng như thế? Tại sao phụ nữ lại mang đến cho &ocirc;ng lắm khổ lụy? Tại sao th&acirc;n x&aacute;c &ocirc;ng kh&ocirc;ng l&ugrave;n thấp, tật nguyền, xấu xả Nếu được như thế, biết đ&acirc;u &ocirc;ng sẽ hạnh ph&uacute;c? Hai gờ giấy kia của di ch&uacute;c cũng c&ugrave;ng một tuồng chữ nhỏ nhắn v&agrave; chi lị Cũng ti tiện v&agrave; hằn th&ugrave; như l&ograve;ng dạ đ&agrave;n b&agrave;.&nbsp;<em>T&ocirc;i k&yacute; t&ecirc;n dưới đ&acirc;y l&agrave; Mary Carson, khỏe mạnh v&agrave; s&aacute;ng suốt, tuy&ecirc;n bố đ&acirc;y l&agrave; di ch&uacute;c của t&ocirc;i, c&oacute; gi&aacute; trị v&ocirc; hiệu h&oacute;a v&agrave; ti&ecirc;u hủy c&aacute;c di ch&uacute;c cũng do t&ocirc;i lập ra trước đ&acirc;y. Ngoại trừ c&aacute;c vật di tặng n&ecirc;u ra dưới đ&acirc;y, t&ocirc;i để lại to&agrave;n bộ động sản v&agrave; bất động sản cho T&ograve;a th&aacute;nh La M&atilde;, với những điều kiện sau đ&acirc;y: Thứ nhất, T&ograve;a th&aacute;nh La M&atilde; đ&atilde; n&ecirc;u tr&ecirc;n (dưới đ&acirc;y sẽ gọi tắt l&agrave; T&ograve;a th&aacute;nh) phải hiểu rằng t&ocirc;i đ&atilde; k&iacute;nh trọng v&agrave; thương y&ecirc;u như thế ng&agrave;o người đại diện của Ch&uacute;a, cha Ralph. Duy nhất v&igrave; long nh&acirc;n đức của cha, do sự biết ơn ở người đ&atilde; hướng dẫn linh hồn t&ocirc;i, do sự gi&uacute;p đỡ m&agrave; cha mang đến cho t&ocirc;i v&agrave; kh&ocirc;ng bao giờ từ chối, ch&iacute;nh v&igrave; những l&yacute; do ấy m&agrave; t&ocirc;i đ&atilde; để lại to&agrave;n bộ t&agrave;i sản của t&ocirc;i cho T&ograve;a th&aacute;nh. Thứ hai, T&ograve;a th&aacute;nh tiếp tục hưởng c&aacute;c quyền lợi của di sản n&agrave;y cho đến khi T&ograve;a Th&aacute;nh c&ograve;n thừa nhận phẩm gi&aacute; v&agrave; khả năng của cha Ralph n&oacute;i tr&ecirc;n. Thứ ba, ch&iacute;nh cha Ralph đ&atilde; n&ecirc;u c&oacute; tr&aacute;ch nhiệm quản l&yacute; v&agrave; điều h&agrave;nh tất cả động sản v&agrave; bất động sản với tư c&aacute;ch l&agrave; người quản l&yacute; ch&iacute;nh của di sản n&agrave;y. Thứ tư, nếu cha Ralph được n&ecirc;u tr&ecirc;n qua đời th&igrave; tờ di ch&uacute;c của cha sẽ được nh&igrave;n nhận về mặt ph&aacute;p l&yacute; li&ecirc;n quan đến sự quản l&yacute; tiếp tục t&agrave;i sản của t&ocirc;i. C&oacute; nghĩa l&agrave; T&ograve;a th&aacute;nh vẫn tiếp tục l&agrave;m chủ t&agrave;i sản n&agrave;y nhưng linh mục Ralph l&agrave; người duy nhất c&oacute; thẩm quyền chỉ định người thay thế cha để quản l&yacute;. Kh&ocirc;ng bắt buộc người thay thế cha Ralph phải được chọn trong gi&aacute;o sĩ hay người ngo&agrave;i gi&aacute;o hội, theo đạo C&ocirc;ng gi&aacute;o. Thứ năm, điền trang Drogheda kh&ocirc;ng được b&aacute;n hay ph&acirc;n chia. Thứ s&aacute;u, em t&ocirc;i Padraic Cleary, phải được giữ lại l&agrave;m người quản l&yacute; điền trang Drogheda, được quyền ở lại t&ograve;a nh&agrave; của t&ocirc;i v&agrave; được trả h&agrave;ng th&aacute;ng một số tiền lương m&agrave; chỉ c&oacute; cha Ralph biết được m&agrave; th&ocirc;i. Thứ bảy, trong trường hợp em t&ocirc;i Padraic Cleary qua đời, th&igrave; vợ em t&ocirc;i v&agrave; c&aacute;c con em t&ocirc;i được quyền tiếp tục ở t&ograve;a nh&agrave; đ&oacute; v&agrave; cương vị quản l&yacute; điền trang sẽ trao cho c&aacute;c con của em t&ocirc;i theo thứ tự Rober, John, Hugh, Stuart, James va Patrick; danh s&aacute;ch n&agrave;y loại Francis. Thứ t&aacute;m, khi Padraic qua đời v&agrave; đứa con cuối c&ugrave;ng của em t&ocirc;i cũng qua đời th&igrave; c&aacute;c quyền lợi d&agrave;nh cho Padraic Cleary sẽ được chuyển sang c&aacute;c ch&aacute;u của em t&ocirc;i. Những di tặng ri&ecirc;ng. Để lại cho Padraic Cleary tất cả đồ đạc trong c&aacute;c ng&ocirc;i nh&agrave; nằm tr&ecirc;n đất của điền trang Droghedạ B&agrave; Eunice, Smith, quản gia của t&ocirc;i, được quyền ở lại cương vị hiện nay của b&agrave; với số lương xứng đ&aacute;ng cho đến khi n&agrave;o t&ugrave;y &yacute; muốn của b&agrave;, ngo&agrave;i ra trao ngay cho b&agrave; số tiền năm ng&agrave;n bảng Anh. Khi b&agrave; về hưu, trả cho b&agrave; một trợ cấp đầy đủ. Với Minerva &Oacute; Brien v&agrave; Catherine Donnelly, cả hai được quyền ở lại cương vị m&igrave;nh cho đến khi n&agrave;o t&ugrave;y &yacute;. Trao cho mỗi người một ng&agrave;n bảng Anh ngay b&acirc;y giờ v&agrave; khi về hưu cả hai nhận một số tiền trợ cấp đầy đủ. Với cha Ralph, h&agrave;ng năm hưởng một số tiền l&agrave; mười ng&agrave;n bảng Anh, như thế suốt cả đời, để cho cha sử dụng việc ri&ecirc;ng kh&ocirc;ng cần b&aacute;o c&aacute;o.&nbsp;</em>Tờ di ch&uacute;c c&oacute; chữ k&yacute; hợp lệ, ghi r&otilde; ng&agrave;y th&aacute;ng v&agrave; c&oacute; chữ k&yacute; người chứng. Ph&ograve;ng của cha Ralph nh&igrave;n ra hướng T&acirc;y. Mặt trời đang lặn. M&agrave;n bụi mỏng xuất hiện v&agrave;o mỗi m&ugrave;a h&egrave; phủ l&ecirc;n cả kh&ocirc;ng gian đang ch&igrave;m trong y&ecirc;n lặng. C&aacute;c tia mặt trời xuy&ecirc;n qua m&agrave;n bụi li ti ấy kh&ocirc;ng kh&aacute;c g&igrave; những ng&oacute;n tay, tất cả s&aacute;ng rực l&ecirc;n m&agrave;u v&agrave;ng &aacute;nh v&agrave; mau t&iacute;a. - Hoan h&ocirc; Mary! &ocirc;ng n&oacute;i một m&igrave;nh. B&agrave; Mary ơi, t&ocirc;i phải nh&igrave;n nhận l&agrave; b&agrave; đ&atilde; thắng t&ocirc;i. Một đ&ograve;n của bậc sư, t&ocirc;i l&agrave; người bị lừa, chứ kh&ocirc;ng phải b&agrave;. Nước mắt rơi xuống từ l&uacute;c n&agrave;o l&agrave;m cha Ralph kh&ocirc;ng c&ograve;n nh&igrave;n thấy r&otilde; c&aacute;c tờ giấy đang cầm tr&ecirc;n tay; &ocirc;ng vội v&atilde; đưa ra xa để tr&aacute;nh khỏi bị nh&ograve;a. Mười ba triệu đồng bảng Anh. Mười ba triệu đồng bảng Anh. Đ&uacute;ng lắm, đ&oacute; l&agrave; mục ti&ecirc;u m&agrave; &ocirc;ng nhắm đến trước thời gian &ocirc;ng gặp Meggiẹ Với sự xuất hiện của Meggie, &ocirc;ng đ&atilde; bỏ rơi kế hoạch v&igrave; rằng &ocirc;ng kh&ocirc;ng thể lạnh l&ugrave;ng tiến h&agrave;nh một chiến dịch m&agrave; &ocirc;ng biết cuối c&ugrave;ng truất quyền thừa kế của Meggiẹ Nhưng nếu &ocirc;ng biết trước ch&iacute;nh x&aacute;c t&agrave;i sản của con nhện gi&agrave; kếch s&ugrave; như thế th&igrave; &ocirc;ng đ&atilde; như thế n&agrave;o? Phản ứng của &ocirc;ng sẽ ra sao? &ocirc;ng kh&ocirc;ng bao giờ tưởng tượng rằng Mary Carson c&oacute; thể để lại cho &ocirc;ng một phần mười số tiền n&agrave;y, n&oacute;i chi mười ba triệu đồng bảng Anh? Suốt bảy năm, Pađy v&agrave; gia đ&igrave;nh sống &acirc;m thầm trong ng&ocirc;i nh&agrave; d&agrave;nh cho vi&ecirc;n quản l&yacute;. Mọi người trong gia đ&igrave;nh l&agrave;m việc cật lực cho Mary Carson? V&igrave; c&aacute;i g&igrave;? V&igrave; khoản tiền c&ocirc;ng bủn xỉn của b&agrave; ta sao? Chưa một lần cha Ralph chứng kiến Pađy than phiền sự đối xử keo kiệt của người chị c&oacute; lẽ Pađy nghĩ rằng &ocirc;ng ta sẽ được đền b&ugrave; lại khi Mary Carson qua đời. Pađy chịu đựng đến mức quen dần v&agrave; cuối c&ugrave;ng y&ecirc;u thương Drogheda như điền trang n&agrave;y cũng sẽ thuộc về &ocirc;ng th&ocirc;i. - Mary, b&agrave; thật l&agrave; tinh qu&aacute;i, cha Ralph lặp lại một m&igrave;nh. Lần đầu ti&ecirc;n kể từ thuở ấu thơ, những giọt nước mắt rơi đầm đ&igrave;a tr&ecirc;n tay &ocirc;ng nhưng kh&ocirc;ng hề rơi l&ecirc;n tờ di ch&uacute;c. Mười ba triệu bảng Anh; v&agrave; c&ograve;n c&oacute; cả khả năng trở th&agrave;nh Hồng Y Bricassart. Dĩ nhi&ecirc;n l&agrave; bằng c&aacute;ch tước đoạt quyền thừa kế của Pađy, vợ &ocirc;ng ta, c&aacute;c con trai &ocirc;ng ta - v&agrave; Meggiẹ Trời ơi h&igrave;nh như b&agrave; ta đ&atilde; hết sức qui? qu&aacute;i thấy được cả ruột gan của m&igrave;nh! Nếu Mary tước đoạt c&ocirc;ng sức Pađy, một c&aacute;ch trần trụi, th&igrave; con đường của cha Ralph được vạch ra qu&aacute; r&otilde; r&agrave;ng: &ocirc;ng ta sẽ cầm ngay tờ di ch&uacute;c, đứng l&ecirc;n đi xuống bếp, v&agrave; n&eacute;m n&oacute; v&agrave;o l&ograve; lửa kh&ocirc;ng một ch&uacute;t do dự. Nhưng kh&ocirc;ng, Mary Carson kh&ocirc;ng l&agrave;m thế, b&agrave; lại bảo đảm cho Pađy kh&ocirc;ng t&uacute;ng thiếu, lại c&oacute; được một cuộc sống dễ thở hơn ở Drogheda sau c&aacute;i chết của b&agrave; ta, mặt kh&aacute;c điền trang n&agrave;y cũng kh&ocirc;ng hẳn truất khỏi tay &ocirc;ng. Thu nhập c&aacute;c loại của điền trang v&agrave; quyền sở hữu &ocirc;ng kh&ocirc;ng được hưởng, đ&uacute;ng thế, nhưng &ocirc;ng vẫn ở tr&ecirc;n đất đ&oacute;. V&acirc;ng, &ocirc;ng kh&ocirc;ng l&agrave;m chủ c&aacute;i t&agrave;i sản kếch s&ugrave; 13 triệu bảng Anh, nhưng &ocirc;ng vẫn được k&iacute;nh trọng v&agrave; sống một c&aacute;ch đầy đủ. Meggie kh&ocirc;ng bao giờ đ&oacute;i, kh&ocirc;ng bị tước đoạt tất cả. C&oacute; điều l&agrave; Mary Carson kh&ocirc;ng tạo điều kiện cho Meggie s&aacute;nh ngang vai với tiểu thư Carmichael v&agrave; giới thượng lưu thuộc địa. Ho&agrave;n to&agrave;n được k&iacute;nh trọng, được chấp nhận trong giới gi&agrave;u sang nhưng kh&ocirc;ng bao giờ thuộc hẳn v&agrave;o tầng lớp b&ecirc;n tr&ecirc;n. Kh&ocirc;ng bao giờ. Mười ba triệu bảng Anh. Đủ điều kiện để rời bỏ Gillanbone v&agrave; b&oacute;ng tối vĩnh cửu, tạo được một vị tr&iacute; trong h&agrave;ng chức sắc của Nh&agrave; thờ, được l&ograve;ng những người ngang h&agrave;ng v&agrave; cả với c&aacute;c đấng bề tr&ecirc;n. Tất cả những điều thuận lợi ấy đến giữa l&uacute;c &ocirc;ng c&ograve;n trẻ, c&ograve;n c&oacute; thể tranh thủ những g&igrave; bị bỏ dở l&acirc;u naỵ Mary Carson đ&atilde; biến Gillanbone th&agrave;nh trung t&acirc;m l&atilde;nh địa d&agrave;nh ri&ecirc;ng cho Đức Tổng gi&aacute;m mục đại diện của Gi&aacute;o ho&agrave;ng. Dư &acirc;m của sự việc n&agrave;y sẽ vang dội đến Vatican, đ&oacute; l&agrave; c&aacute;ch trả th&ugrave; của b&agrave;. D&ugrave; cho gi&aacute;o hội gi&agrave;u c&oacute; đến đ&acirc;u, mười ba triệu bảng Anh vẫn l&agrave; mười ba triệu bảng Anh. Đ&oacute; kh&ocirc;ng phải l&agrave; một số tiền c&oacute; thể coi nhẹ, d&ugrave; với gi&aacute;o hội đi nữa. V&agrave; ch&iacute;nh cha Ralph l&agrave; người duy nhất c&oacute; thể đưa t&agrave;i sản kếch s&ugrave; ấy về cho Nh&agrave; thờ, &ocirc;ng l&agrave; người đ&atilde; được nh&igrave;n nhận tờ di ch&uacute;c; ch&iacute;nh b&agrave; Mary Carson đo&aacute;n trước điều đ&oacute;, cầu Ch&uacute;a đ&agrave;y mụ gi&agrave; n&agrave;y mục xương dưới địa ngục! Nhưng chắc chắn Pađy sẽ giận dữ, sẽ từ chối nh&igrave;n mặt v&agrave; từ nay chẳng th&egrave;m n&oacute;i một lời n&agrave;o với &ocirc;ng. Nhưng d&ugrave; c&oacute; giận dữ, &ocirc;ng ấy cũng sẽ kh&ocirc;ng kiện được ra t&ograve;a. Thật sự c&oacute; cần lấy một quyết định kh&ocirc;ng? Quyết định ấy, cha Ralph đ&atilde; biết rồi. Ngay l&uacute;c vừa đọc tờ di ch&uacute;c &ocirc;ng đ&atilde; biết sẽ phải phản ứng như thế n&agrave;o. Nước mắt đ&atilde; kh&ocirc; r&aacute;o. Cha Ralph đứng l&ecirc;n, nhẹ nh&agrave;ng v&agrave; gọn g&agrave;ng như mọi khi, &ocirc;ng xem lại chiếc &aacute;o sơm mi bỏ v&agrave;o quần đ&atilde; gọn g&agrave;ng chưa, rồi đến gần cửa. &ocirc;ng phải đi ngay Gilly, để lấy chiếc &aacute;o d&ograve;ng v&agrave; c&aacute;c thứ kh&aacute;c để l&agrave;m lễ tang. Nhưng trước hết, &ocirc;ng phải nh&igrave;n mặt Mary lần cuối. C&aacute;c cửa sổ vẫn mở toang nhưng m&ugrave;i h&ocirc;i thối vẫn x&ocirc;ng l&ecirc;n, kh&ocirc;ng c&oacute; một ngọn gi&oacute; nhẹ. Đi những bước dứt kho&aacute;t, &ocirc;ng đến gần giường, nh&igrave;n xuống. Những trứng ruồi đẻ đ&atilde; bắt đầu nở th&agrave;nh d&ograve;i, lổn ngổn tr&ecirc;n gương mặt c&oacute; nhiều nơi ẩm ướt. B&agrave;n tay v&agrave; c&aacute;nh tay Mary đ&atilde; s&igrave;nh l&ecirc;n, da nứt ra với những đốm m&agrave;u xanh t&aacute;i. &ocirc;i lại Ch&uacute;a! Sao con nhện gi&agrave; lại gớm ghiếc như thế. Nhưng mụ sẽ kh&ocirc;ng bao giờ thắng được Meggie của ta, kh&ocirc;ng thể n&agrave;o tước đoạt ở Meggie những g&igrave; kh&ocirc;ng thuộc về mụ. Rất c&oacute; thể ta cũng bị thi&ecirc;u ở hỏa ngục kế b&ecirc;n mụ nhưng ta biết trước hỏa ngục d&agrave;nh cho mụ như thế n&agrave;o: mụ mong muốn v&ocirc; c&ugrave;ng hai ch&uacute;ng ta sẽ c&ugrave;ng nhau rục xương trong vĩnh cửu nhưng mụ sẽ vẫn thấy ta hết sức dửng dưng trước mụ. ... Cha Ralph trầm ng&acirc;m kh&aacute; l&acirc;u, l&acirc;u hơn thời gian đi về nh&agrave; xứ v&agrave; trở lại. Trước khi về nh&agrave;, &ocirc;ng đ&atilde; gh&eacute; lại nh&agrave; của Harry Gough ở Gillanbonẹ Harry Gough vừa ngồi v&agrave;o b&agrave;n ăn, đứng dậy v&agrave; đi thẳng v&agrave;o ph&ograve;ng kh&aacute;ch khi được chị gi&uacute;p v